INSERT INTO `posts` (`id`, `title`, `slug`, `excerpt`, `body`, `user_id`, `category_id`, `views`, `approved`, `created_at`, `updated_at`) VALUES
(1, 'EVN kiến nghị gỡ khó cho điện mặt trời mái nhà tự dùng', 'EVN-ien-nghi-go-ho-cho-dien-thoai-mat-troi-mai-nha-tu-dung', 'EVN kiến nghị các bộ, ngành sớm có quy định, cơ chế ràng buộc trách nhiệm, thủ tục đấu nối cũng như quy định về an toàn xây dựng với điện mặt trời mái nhà tự dùng.', '<p class=\"description\">EVN kiến nghị c&aacute;c bộ, ng&agrave;nh sớm c&oacute; quy định, cơ chế r&agrave;ng buộc tr&aacute;ch nhiệm, thủ tục đấu nối cũng như quy định về an to&agrave;n x&acirc;y dựng với điện mặt trời m&aacute;i nh&agrave; tự d&ugrave;ng.</p>\r\n<article class=\"fck_detail \">\r\n<p class=\"Normal\">Đề xuất n&agrave;y được Tập đo&agrave;n Điện lực Việt Nam (EVN) đưa ra trước những vướng mắc của c&aacute;c chủ đầu tư điện mặt trời m&aacute;i nh&agrave; tự d&ugrave;ng, kh&ocirc;ng đấu nối v&agrave; ph&aacute;t điện l&ecirc;n hệ thống quốc gia.</p>\r\n<p class=\"Normal\">Tập đo&agrave;n n&agrave;y cho biết vừa qua nhận được nhiều kiến nghị của một số chủ đầu tư v&agrave; địa phương đề nghị được đấu nối hệ thống điện mặt trời m&aacute;i nh&agrave; để tự ti&ecirc;u d&ugrave;ng tại chỗ, kh&ocirc;ng ph&aacute;t l&ecirc;n lưới điện.</p>\r\n<p class=\"Normal\">Chẳng hạn, C&ocirc;ng ty TNHH Norsk Solar Việt Nam muốn đầu tư hệ thống điện mặt trời m&aacute;i nh&agrave; cho c&aacute;c khu c&ocirc;ng nghiệp, nh&agrave; m&aacute;y, trung t&acirc;m thương mại. C&ocirc;ng ty TNHH Maruha Chemical Việt Nam đầu tư điện mặt trời m&aacute;i nh&agrave; tại hệ thống nh&agrave; m&aacute;y để tự d&ugrave;ng nội bộ. Hay Sở C&ocirc;ng Thương Tiền Giang lắp đặt điện mặt trời m&aacute;i nh&agrave; tại trụ sở l&agrave;m việc 8 sở, ng&agrave;nh v&agrave; trung t&acirc;m h&agrave;nh ch&iacute;nh c&ocirc;ng tỉnh Tiền Giang...</p>\r\n<p class=\"Normal\">Do chưa c&oacute; cơ chế, quy định ph&aacute;p l&yacute; cụ thể n&ecirc;n hiện EVN tạm thời kh&ocirc;ng thực hiện thoả thuận đấu nối c&aacute;c hệ thống điện mặt trời m&aacute;i nh&agrave; tự d&ugrave;ng n&agrave;y v&agrave; chờ hướng dẫn từ Bộ C&ocirc;ng Thương.</p>\r\n<p class=\"Normal\">Theo tập đo&agrave;n n&agrave;y, do c&aacute;c hệ thống điện mặt trời m&aacute;i nh&agrave; lắp đặt trong phạm vi quản l&yacute; của chủ đầu tư, kh&ocirc;ng thuộc quản l&yacute; của c&aacute;c tổng c&ocirc;ng ty, c&ocirc;ng ty điện lực n&ecirc;n nếu kh&ocirc;ng chấp thuận để c&aacute;c chủ đầu tư ph&aacute;t triển điện mặt trời m&aacute;i nh&agrave; tự d&ugrave;ng c&oacute; thể g&acirc;y phản ứng ti&ecirc;u cực từ họ.</p>\r\n<p class=\"Normal\">Ngược lại, nếu EVN chấp thuận cho c&aacute;c chủ đầu tư th&igrave; lại c&oacute; rủi ro, do chưa c&oacute; cơ sở ph&aacute;p l&yacute; r&otilde; r&agrave;ng. Mặt kh&aacute;c, do kh&ocirc;ng kiểm so&aacute;t c&aacute;c hệ thống điện mặt trời m&aacute;i nh&agrave; tự d&ugrave;ng c&oacute; thể g&acirc;y kh&oacute; khăn đến lập kế hoạch huy động c&aacute;c nguồn điện hiện hữu, đảm bảo c&acirc;n bằng cung cầu trong vận h&agrave;nh hệ thống điện quốc gia, nhất l&agrave; tại khu vực miền Trung, miền Nam.</p>\r\n<p class=\"Normal\">Ngo&agrave;i ra, khi hệ thống điện mặt trời m&aacute;i nh&agrave; của kh&aacute;ch h&agrave;ng bị sự cố, kh&ocirc;ng đ&aacute;p ứng đủ nhu cầu sử dụng tại chỗ như dự kiến, cần c&oacute; cơ chế r&agrave;ng buộc về tr&aacute;ch nhiệm tự chịu rủi ro của kh&aacute;ch h&agrave;ng.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-kinhdoanh.vnecdn.net/2022/06/18/dien-mat-troi-mai-nha-8548-1655517837.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=M2-3x_1wZfRkvHTdKJr-gg 1x, https://i1-kinhdoanh.vnecdn.net/2022/06/18/dien-mat-troi-mai-nha-8548-1655517837.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=r7e-xV4BFkfr2-aB1GH5ew 1.5x, https://i1-kinhdoanh.vnecdn.net/2022/06/18/dien-mat-troi-mai-nha-8548-1655517837.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=1L-g0IOSR5-watquGlW82g 2x\" data-srcset=\"https://i1-kinhdoanh.vnecdn.net/2022/06/18/dien-mat-troi-mai-nha-8548-1655517837.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=M2-3x_1wZfRkvHTdKJr-gg 1x, https://i1-kinhdoanh.vnecdn.net/2022/06/18/dien-mat-troi-mai-nha-8548-1655517837.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=r7e-xV4BFkfr2-aB1GH5ew 1.5x, https://i1-kinhdoanh.vnecdn.net/2022/06/18/dien-mat-troi-mai-nha-8548-1655517837.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=1L-g0IOSR5-watquGlW82g 2x\"><img class=\"lazy lazied\" src=\"https://i1-kinhdoanh.vnecdn.net/2022/06/18/dien-mat-troi-mai-nha-8548-1655517837.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=M2-3x_1wZfRkvHTdKJr-gg\" alt=\"Một c&ocirc;ng tr&igrave;nh điện mặt trời m&aacute;i nh&agrave; tại TP HCM. Ảnh: EVNHCM\" loading=\"lazy\" data-src=\"https://i1-kinhdoanh.vnecdn.net/2022/06/18/dien-mat-troi-mai-nha-8548-1655517837.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=M2-3x_1wZfRkvHTdKJr-gg\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Một c&ocirc;ng tr&igrave;nh điện mặt trời m&aacute;i nh&agrave; tại TP HCM.&nbsp;<em>Ảnh: EVNHCM</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Trong bối cảnh đ&oacute;, c&aacute;c nh&agrave; đầu tư li&ecirc;n tục kiến nghị v&agrave; để gỡ vướng mắc, tập đo&agrave;n n&agrave;y đề nghị cơ quan quản l&yacute; sớm c&oacute; quy định cụ thể về cơ chế r&agrave;ng buộc tr&aacute;ch nhiệm tự chịu rủi ro, điều kiện, tr&igrave;nh tự thủ tục đấu nối, ti&ecirc;u chuẩn lắp đặt... với hệ thống điện mặt trời m&aacute;i nh&agrave; tự d&ugrave;ng. Trước mắt, EVN đề nghị xem x&eacute;t ưu ti&ecirc;n lắp đặt tại khu vực miền Bắc.</p>\r\n<p class=\"Normal\"><strong>Li&ecirc;n quan đến thủ tục đăng k&yacute; kinh doanh với c&aacute;c nh&agrave; đầu tư, c&aacute;c gia đ&igrave;nh c&oacute; điện mặt trời m&aacute;i nh&agrave; ph&aacute;t l&ecirc;n lưới,&nbsp;</strong>EVN cũng c&oacute; những kiến nghị gỡ vướng do hiện mỗi tỉnh y&ecirc;u cầu thủ tục kh&aacute;c nhau.</p>\r\n<p class=\"Normal\">Như tỉnh Long An y&ecirc;u cầu tất cả chủ đầu tư điện mặt trời m&aacute;i nh&agrave; phải đăng k&yacute; kinh doanh, trong đ&oacute; Ph&uacute; Y&ecirc;n, TP HCM, Đ&agrave; Nẵng... lại kh&ocirc;ng y&ecirc;u cầu thủ tục n&agrave;y với hệ thống điện mặt trời m&aacute;i nh&agrave; c&oacute; doanh thu dưới 100 triệu đồng một năm.</p>\r\n<p class=\"Normal\">Đến nay đ&atilde; c&oacute; 104.294 dự &aacute;n điện mặt trời m&aacute;i nh&agrave; được lắp đặt, với tổng c&ocirc;ng suất đặt 9.581 MW.</p>\r\n<p class=\"Normal\">Trước thực tế n&agrave;y, EVN tr&igrave;nh Hội đồng th&agrave;nh vi&ecirc;n ph&ecirc; duyệt hướng dẫn thủ tục đăng k&yacute; kinh doanh điện mặt trời m&aacute;i nh&agrave; đối với hộ gia đ&igrave;nh, c&aacute; nh&acirc;n. Theo đ&oacute;, tập đo&agrave;n n&agrave;y sẽ thanh to&aacute;n tiền mua điện từ hệ thống điện mặt trời m&aacute;i nh&agrave; cho c&aacute;c hộ gia đ&igrave;nh, c&aacute; nh&acirc;n thuộc đối tượng kh&ocirc;ng phải đăng k&yacute;, bổ sung ng&agrave;nh, nghề kinh doanh điện mặt trời m&aacute;i nh&agrave;.</p>\r\n<p class=\"Normal\">Với chủ đầu tư điện mặt trời m&aacute;i nh&agrave; thuộc đối tượng phải đăng k&yacute;, bổ sung ng&agrave;nh, nghề kinh doanh cung cấp th&ocirc;ng tin về việc đ&atilde; ho&agrave;n th&agrave;nh thủ tục n&agrave;y theo quy định.</p>\r\n</article>', 218, 4, 16, 1, '2022-11-22 07:13:27', '2023-02-08 01:23:16'),
(3, 'Phim Em và Trịnh \'cháy vé\'', 'phim-em-va-trinh-chay-ve', '\"Em và Trịnh\" - phim về Trịnh Công Sơn và các mỹ nhân - tăng sức \"nóng\" sau một tuần công chiếu, được nhà rạp mở thêm suất 0h.', '<div id=\"divfirst\">\r\n<p class=\"Normal\">Theo đại diện Galaxy - đơn vị ph&aacute;t h&agrave;nh, một tuần qua, phim ghi nhận hiện tượng tăng dần về lượt kh&aacute;n giả lẫn suất chiếu trong ng&agrave;y.</p>\r\n<p class=\"Normal\">\"Th&ocirc;ng thường, c&aacute;c phim đạt doanh thu cao nhất v&agrave;o những ng&agrave;y chiếu đầu ti&ecirc;n, giảm dần sau đ&oacute;. Với&nbsp;<em><a href=\"https://vnexpress.net/giai-tri/phim/thu-vien-phim/em-va-trinh-446\" rel=\"dofollow\">Em v&agrave; Trịnh</a></em>, phim c&oacute; đ&agrave; tăng li&ecirc;n tục, ng&agrave;y sau doanh thu cao hơn ng&agrave;y trước\", người đại diện cho biết. Dịp cuối tuần, nhiều rạp ở H&agrave; Nội bố tr&iacute; th&ecirc;m suất chiếu&nbsp;<em>Em v&agrave; Trịnh</em>&nbsp;l&uacute;c 0h để phục vụ kh&aacute;n giả.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-giaitri.vnecdn.net/2022/06/19/em-va-trinh-1-jpeg-4697-1655629584.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=VBvXiWFT4yrlyiAk3_4h4w 1x, https://i1-giaitri.vnecdn.net/2022/06/19/em-va-trinh-1-jpeg-4697-1655629584.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=SrDCuSnOBWSnsKP1n87oEw 1.5x, https://i1-giaitri.vnecdn.net/2022/06/19/em-va-trinh-1-jpeg-4697-1655629584.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=qIHPzXkG6cFtT8-t3tZ2xw 2x\" data-srcset=\"https://i1-giaitri.vnecdn.net/2022/06/19/em-va-trinh-1-jpeg-4697-1655629584.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=VBvXiWFT4yrlyiAk3_4h4w 1x, https://i1-giaitri.vnecdn.net/2022/06/19/em-va-trinh-1-jpeg-4697-1655629584.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=SrDCuSnOBWSnsKP1n87oEw 1.5x, https://i1-giaitri.vnecdn.net/2022/06/19/em-va-trinh-1-jpeg-4697-1655629584.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=qIHPzXkG6cFtT8-t3tZ2xw 2x\"><img class=\"lazy lazied\" src=\"https://i1-giaitri.vnecdn.net/2022/06/19/em-va-trinh-1-jpeg-4697-1655629584.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=VBvXiWFT4yrlyiAk3_4h4w\" alt=\"Kh&aacute;n giả Cần Thơ giao lưu c&ugrave;ng &ecirc;k&iacute;p Em v&agrave; Trịnh h&ocirc;m 17/6. Ảnh: Thanh Huyền\" loading=\"lazy\" data-src=\"https://i1-giaitri.vnecdn.net/2022/06/19/em-va-trinh-1-jpeg-4697-1655629584.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=VBvXiWFT4yrlyiAk3_4h4w\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Kh&aacute;n giả Cần Thơ giao lưu c&ugrave;ng &ecirc;k&iacute;p \"Em v&agrave; Trịnh\" h&ocirc;m 17/6. Ảnh:<em>&nbsp;Thanh Huyền</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Tr&ecirc;n Box Office - đơn vị quan s&aacute;t ph&ograve;ng v&eacute; độc lập, h&ocirc;m 19/6, phim đạt 4,2 tỷ đồng (t&iacute;nh trong ng&agrave;y), gấp ba lần&nbsp;<em><a href=\"https://vnexpress.net/giai-tri/phim/thu-vien-phim/jurassic-world-dominion-447?fbclid=IwAR3TdYjr40I7o-_MAwlLZ9BCP8wbJZekNnoB-4iYAwuglLlyrp4WgU1-AAE\" rel=\"dofollow\">Jurassic World: Dominion</a></em>&nbsp;- bom tấn Hollywood về thế giới khủng long. Sau gần 10 ng&agrave;y c&ocirc;ng chiếu, t&aacute;c phẩm thu về hơn 60 tỷ đồng, kinh ph&iacute; l&agrave;m phim được c&ocirc;ng bố l&agrave; 50 tỷ. Theo đại diện CGV, phim đang c&oacute; lượng suất chiếu &aacute;p đảo c&aacute;c phim ra mắt c&ugrave;ng dịp, như&nbsp;<em>Jurassic World: Dominion,&nbsp;<a href=\"https://vnexpress.net/giai-tri/phim/thu-vien-phim/lightyear-450?fbclid=IwAR1e9KU9inYq-DAVAWkHbHqWZC-gbgnXIAm6ZJ6SuroRvAfHvR28djPQVfM\" rel=\"dofollow\">Lightyear</a>...</em></p>\r\n<p class=\"Normal\"><em>*&nbsp;</em><a href=\"https://vnexpress.net/giai-tri/phim/thu-vien-phim/em-va-trinh-446\" target=\"_blank\" rel=\"dofollow noopener\">Review phim \"Em v&agrave; Trịnh\"</a></p>\r\n<p class=\"Normal\">Tr&ecirc;n c&aacute;c nền tảng mạng x&atilde; hội, phim cũng nhận được quan t&acirc;m. T&ecirc;n bộ phim lọt v&agrave;o danh s&aacute;ch c&aacute;c từ kh&oacute;a nổi bật tr&ecirc;n Goolge. Ở TikTok, hashtag về phim hiện thu h&uacute;t hơn 245 triệu lượt xem.</p>\r\n</div>\r\n<div id=\"admbackground\" data-set=\"dfp\"></div>\r\n<div id=\"divend\">\r\n<figure class=\"item_slide_show clearfix\">\r\n<div id=\"video_parent_356829\" class=\"box_embed_video_parent embed_video_new\" data-vcate=\"1003834\" data-vscate=\"1003007\" data-vscaten=\"Giải tr&iacute;\" data-vid=\"356829\" data-ratio=\"1\" data-articleoriginal=\"4475570\" data-ads=\"1\" data-license=\"0\" data-duration=\"163\" data-brandsafe=\"0\" data-type=\"0\" data-play=\"1\" data-frame=\"\" data-aot=\"Hậu trường cảnh thập ni&ecirc;n 1960 của phim \'Em v&agrave; Trịnh\'\" data-videoclassify=\"None\" data-initdom=\"1\" data-view=\"inview\" data-auto=\"1\" data-status=\"play\">\r\n<div id=\"embed_video_356829\" class=\"box_embed_video\"></div>\r\n</div>\r\n</figure>\r\n<p class=\"Normal\">Chiếu sớm từ ng&agrave;y 10/6, t&aacute;c phẩm của <a href=\"https://vnexpress.net/tag/phan-gia-nhat-linh-353213\" target=\"_blank\" rel=\"dofollow noopener\">Phan Gia Nhật Linh</a>&nbsp;thu h&uacute;t sự ch&uacute; &yacute; của c&ocirc;ng ch&uacute;ng v&agrave; giới văn nghệ sĩ.&nbsp;<em><a href=\"https://vnexpress.net/giai-tri/phim/thu-vien-phim/em-va-trinh-446\" rel=\"dofollow\">Em v&agrave; Trịnh</a>&nbsp;</em>bắt đầu với sự kiện Trịnh C&ocirc;ng Sơn lần đầu gặp nữ sinh Nhật Michiko Yoshii (Nakatani Akari đ&oacute;ng) cuối thập ni&ecirc;n 1980. Theo đuổi luận văn cao học đề t&agrave;i &acirc;m nhạc phản chiến, c&ocirc; sang Việt Nam để t&igrave;m hiểu về cuộc đời v&agrave; con người nhạc sĩ t&agrave;i hoa. Từ d&ograve;ng hồi tưởng của Trịnh C&ocirc;ng Sơn, từng lớp k&yacute; ức được lần giở, l&agrave;m sống dậy một thời hoa mộng b&ecirc;n những n&agrave;ng thơ. Ở hiện tại, &ocirc;ng v&agrave; Michiko cũng nảy sinh những rung động nhờ mối giao cảm trong &acirc;m nhạc.</p>\r\n<p class=\"Normal\">*&nbsp;<a href=\"https://vnexpress.net/em-va-trinh-gay-tranh-cai-ve-hinh-tuong-trinh-cong-son-4476078.html\" target=\"_blank\" rel=\"dofollow noopener\">\'Em v&agrave; Trịnh\' g&acirc;y tranh c&atilde;i về h&igrave;nh tượng Trịnh C&ocirc;ng Sơn</a></p>\r\n<p class=\"Normal\">Phim ghi điểm ở phần &acirc;m nhạc, h&igrave;nh ảnh song nhận nhiều lời ch&ecirc; trong kh&acirc;u kịch bản. T&aacute;c phẩm cũng&nbsp;<a href=\"https://vnexpress.net/em-va-trinh-gay-tranh-cai-ve-hinh-tuong-trinh-cong-son-4476078.html\" rel=\"dofollow\">g&acirc;y tranh c&atilde;i</a>&nbsp;về h&igrave;nh tượng Trịnh C&ocirc;ng Sơn, khi nhiều kh&aacute;n giả cho rằng phim ph&aacute;c thảo ch&acirc;n dung nhạc sĩ kh&ocirc;ng như họ biết qua tư liệu, s&aacute;ch b&aacute;o. C&acirc;u chuyện Trịnh C&ocirc;ng Sơn b&ecirc;n c&aacute;c \"n&agrave;ng thơ\" bị cho khắc họa kh&ocirc;ng đ&uacute;ng đời thực, biến nhạc sĩ th&agrave;nh người hời hợt khi y&ecirc;u. Mảng s&aacute;ng t&aacute;c của Trịnh C&ocirc;ng Sơn cũng chưa được khai th&aacute;c kỹ. Trong phim, trừ ca kh&uacute;c&nbsp;<em>Diễm Xưa&nbsp;</em>được giới thiệu chi tiết ho&agrave;n cảnh s&aacute;ng t&aacute;c, c&aacute;c nhạc phẩm c&ograve;n lại chỉ vang l&ecirc;n nhằm tăng hiệu ứng cảm x&uacute;c.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-giaitri.vnecdn.net/2022/06/19/em-va-trinh-3-5077-1655632582.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=k01N6Mzw891JQMagR9TnXA 1x, https://i1-giaitri.vnecdn.net/2022/06/19/em-va-trinh-3-5077-1655632582.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=93WtqA3wMJBO03yY8VPiPA 1.5x, https://i1-giaitri.vnecdn.net/2022/06/19/em-va-trinh-3-5077-1655632582.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=47PHMd4lXKbUmtPVRS0JAg 2x\" data-srcset=\"https://i1-giaitri.vnecdn.net/2022/06/19/em-va-trinh-3-5077-1655632582.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=k01N6Mzw891JQMagR9TnXA 1x, https://i1-giaitri.vnecdn.net/2022/06/19/em-va-trinh-3-5077-1655632582.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=93WtqA3wMJBO03yY8VPiPA 1.5x, https://i1-giaitri.vnecdn.net/2022/06/19/em-va-trinh-3-5077-1655632582.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=47PHMd4lXKbUmtPVRS0JAg 2x\"><img class=\"lazy lazied\" src=\"https://i1-giaitri.vnecdn.net/2022/06/19/em-va-trinh-3-5077-1655632582.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=k01N6Mzw891JQMagR9TnXA\" alt=\"Kh&aacute;n giả xếp h&agrave;ng mua v&eacute; xem Em v&agrave; Trịnh ở một rạp tại quận 10, TP HCM giữa tuần qua. Ảnh: Đỗ Thanh\" loading=\"lazy\" data-src=\"https://i1-giaitri.vnecdn.net/2022/06/19/em-va-trinh-3-5077-1655632582.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=k01N6Mzw891JQMagR9TnXA\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Nhiều kh&aacute;n giả mua v&eacute; suất chiếu \"Em v&agrave; Trịnh\" ở một rạp tại quận 10, TP HCM, giữa tuần qua. Ảnh:&nbsp;<em>Đỗ Thanh</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">H&ocirc;m 16/6, trong buổi gặp gỡ truyền th&ocirc;ng khi về nước thực hiện show kỷ niệm 60 năm ca h&aacute;t, danh ca&nbsp;<a href=\"https://vnexpress.net/chu-de/khanh-ly-2878\" rel=\"dofollow\">Kh&aacute;nh Ly&nbsp;</a>cho biết sẽ kh&ocirc;ng đi xem&nbsp;<em>Em v&agrave; Trịnh</em>&nbsp;v&igrave; kh&ocirc;ng hứng th&uacute; thưởng thức h&igrave;nh tượng hư cấu về Trịnh C&ocirc;ng Sơn. B&agrave; cho rằng kh&aacute;n giả nếu c&oacute; thể n&ecirc;n đi xem để cổ vũ điện ảnh nước nh&agrave;.</p>\r\n<p class=\"Normal\">Từ ng&agrave;y 17/6, nh&agrave; ph&aacute;t h&agrave;nh r&uacute;t khỏi rạp phim&nbsp;<em><a href=\"https://vnexpress.net/giai-tri/phim-trinh-cong-son-ngung-chieu-4476267.html\" rel=\"dofollow\">Trịnh C&ocirc;ng Sơn</a></em>&nbsp;- bản d&agrave;i 95 ph&uacute;t, xoay quanh thời thanh xu&acirc;n của nhạc sĩ. Đơn vị cho biết muốn tập trung quảng b&aacute;&nbsp;<em>Em v&agrave; Trịnh -</em>&nbsp;bản d&agrave;i 136 ph&uacute;t, do phi&ecirc;n bản n&agrave;y được kh&aacute;n giả đ&oacute;n nhận hơn. Trước khi ngừng chiếu, bản&nbsp;<em>Trịnh C&ocirc;ng Sơn</em>&nbsp;đạt doanh thu khoảng 1,6 tỷ đồng.</p>\r\n<figure class=\"item_slide_show clearfix\">\r\n<div id=\"video_parent_353564\" class=\"box_embed_video_parent embed_video_new\" data-vcate=\"1003834\" data-vscate=\"1003007\" data-vscaten=\"Giải tr&iacute;\" data-vid=\"353564\" data-ratio=\"1\" data-articleoriginal=\"4458146\" data-ads=\"1\" data-license=\"0\" data-duration=\"133\" data-brandsafe=\"0\" data-type=\"0\" data-play=\"0\" data-frame=\"\" data-aot=\"Phim \'Em v&agrave; Trịnh\' h&eacute; lộ cảnh Trịnh C&ocirc;ng Sơn gặp Kh&aacute;nh Ly\" data-videoclassify=\"None\" data-initdom=\"1\">\r\n<div class=\"box_img_video embed-container\" data-vid=\"353564\"><img src=\"https://iv1.vnecdn.net/giaitri/images/web/2022/04/30/trailer-chinh-cua-phim-em-va-trinh-1651326324.jpg?w=750&amp;h=450&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=imwVZl5h12mfmt72xXd4Yw\" alt=\"Trailer ch&iacute;nh của phim \'Em v&agrave; Trịnh\'\">\r\n<div class=\"icon_blockvideo\">\r\n<div class=\"img_icon\">&nbsp;</div>\r\n<div class=\"image_icon_center\">&nbsp;</div>\r\n</div>\r\n</div>\r\n</div>\r\n</figure>\r\n</div>', 218, 8, 21, 1, '2022-11-18 19:35:41', '2023-02-08 01:11:21'),
(4, 'Siêu tàu bệnh viện của Hải quân Mỹ cập cảng Vũng Rô', 'sieu-tau-benh-vien-cua-hai-quan-my-cap-cang-vung-ro', 'Tàu bệnh viện USNS Mercy thuộc Bộ tư lệnh Hải vận Quân sự Mỹ cập cảng Vũng Rô tham gia các hoạt động chương trình Đối tác Thái Bình Dương.', '<p>Ng&agrave;y 19/6, t&agrave;u bệnh viện USNS Mercy (T-AH19) với gần 1.000 giường bệnh thuộc Bộ Tư lệnh Hải vận Qu&acirc;n sự Hoa Kỳ đ&atilde; cập Cảng Vũng R&ocirc; (Ph&uacute; Y&ecirc;n), đ&aacute;nh dấu điểm dừng ch&acirc;n đầu ti&ecirc;n trong khu&ocirc;n khổ chương tr&igrave;nh Đối t&aacute;c Th&aacute;i B&igrave;nh Dương 2022 (PP22).</p>\r\n<p>PP22 l&agrave; một chương tr&igrave;nh nhằm tăng cường khả năng sẵn s&agrave;ng ứng ph&oacute; thảm họa v&agrave; viện trợ nh&acirc;n đạo đa quốc gia h&agrave;ng năm c&oacute; quy m&ocirc; nhất được thực hiện tại khu vực Ấn Độ Dương - Th&aacute;i B&igrave;nh Dương.</p>\r\n<div>\r\n<p class=\"image\"><img class=\"width100 initial loaded\" src=\"https://cdn.baogiaothong.vn/upload/images/2022-2/article_img/2022-06-19/img-bgt-2021-usns-mercy-19622n-1655638583-width665height499.jpg\" alt=\"Si&ecirc;u t&agrave;u bệnh viện của Hải qu&acirc;n Mỹ cập cảng Vũng R&ocirc; 1\" data-original=\"https://cdn.baogiaothong.vn/upload/images/2022-2/article_img/2022-06-19/img-bgt-2021-usns-mercy-19622n-1655638583-width665height499.jpg\" data-was-processed=\"true\"></p>\r\n<p class=\"img_chu_thich\">T&agrave;u bệnh viện USNS Mercy thuộc Bộ tư lệnh Hải vận Qu&acirc;n sự Mỹ. Ảnh: US Navy</p>\r\n<section class=\"bnrBt txtCent\"></section>\r\n</div>\r\n<p>Theo Ban tổ chức PP22, dịp n&agrave;y, nh&oacute;m PP22 bao gồm đại diện đến từ c&aacute;c nước: Australia, Nhật Bản, Vương quốc Anh v&agrave; Hoa Kỳ, sẽ phối hợp với c&aacute;c đối t&aacute;c tại Việt Nam để thực hiện c&aacute;c hoạt động, dự &aacute;n nhằm mang lại lợi &iacute;ch cho người d&acirc;n Ph&uacute; Y&ecirc;n v&agrave; khu vực.</p>\r\n<p>Đ&acirc;y l&agrave; năm hoạt động thứ 17 của Chương tr&igrave;nh Đối t&aacute;c Th&aacute;i B&igrave;nh Dương, nhằm tăng cường khả năng sẵn s&agrave;ng ứng ph&oacute; thảm họa v&agrave; viện trợ nh&acirc;n đạo đa quốc gia h&agrave;ng năm c&oacute; quy m&ocirc; nhất được thực hiện tại Khu vực Ấn Độ Dương - Th&aacute;i B&igrave;nh Dương.</p>\r\n<p>Trong thời gian triển khai ở Ph&uacute; Y&ecirc;n, Chương tr&igrave;nh PP22 sẽ cung cấp c&aacute;c hoạt động chăm s&oacute;c&nbsp;<a class=\"seokwl\" title=\"Y tế\" href=\"https://www.baogiaothong.vn/y-te/\">y tế</a>&nbsp;li&ecirc;n quan đến c&aacute;c lĩnh vực nội khoa v&agrave; nha khoa; c&aacute;c hoạt động x&acirc;y ph&ograve;ng học mới tại điểm trường Lam Sơn thuộc Trường Tiểu học An Thọ, Trường Tiểu học An Ninh Đ&ocirc;ng 1, điểm trường Phước Hậu thuộc trường Tiểu học Xu&acirc;n Đ&agrave;i; c&aacute;c hoạt động trao đổi chuy&ecirc;n m&ocirc;n li&ecirc;n quan đến quy tr&igrave;nh ứng ph&oacute; thảm họa. Ngo&agrave;i ra, Ban nhạc Hạm đội Th&aacute;i B&igrave;nh Dương của Hoa Kỳ cũng sẽ biểu diễn c&ugrave;ng c&aacute;c nghệ sỹ của Việt Nam trong c&aacute;c chương tr&igrave;nh giao lưu cộng đồng.</p>\r\n<div>\r\n<p class=\"image\"><img class=\"width100 loaded\" src=\"https://cdn.baogiaothong.vn/upload/images/2022-2/article_img/2022-06-19/img-bgt-2021-3-mygz-1655638759-width560height284.jpg\" alt=\"Si&ecirc;u t&agrave;u bệnh viện của Hải qu&acirc;n Mỹ cập cảng Vũng R&ocirc; 2\" data-original=\"https://cdn.baogiaothong.vn/upload/images/2022-2/article_img/2022-06-19/img-bgt-2021-3-mygz-1655638759-width560height284.jpg\" data-was-processed=\"true\"></p>\r\n<p class=\"img_chu_thich\">Một số kỹ thuật điều trị y tế tr&ecirc;n t&agrave;u</p>\r\n</div>\r\n<p>Đại t&aacute; Hank Kim, Chỉ huy trưởng PP22 cho biết, đ&acirc;y l&agrave; sự hợp t&aacute;c thể hiện sự đo&agrave;n kết, gi&uacute;p x&acirc;y dựng l&ograve;ng tin giữa c&aacute;c quốc gia v&agrave; gi&uacute;p ch&uacute;ng ta lu&ocirc;n sẵn s&agrave;ng phối hợp hiệu quả trong c&aacute;c trường hợp c&ugrave;ng tham gia ứng ph&oacute; thi&ecirc;n tai, hay bất kỳ sự cố thảm họa n&agrave;o.</p>\r\n<p>&Ocirc;ng Hank Kim mong muốn được chia sẻ kinh nghiệm v&agrave; chuy&ecirc;n m&ocirc;n của đội ngũ tới c&aacute;c cơ quan tại Việt Nam v&agrave; c&aacute;c quốc gia đối t&aacute;c để x&acirc;y dựng th&ecirc;m nhiều kỹ năng, tiếp tục duy tr&igrave; c&aacute;c chương tr&igrave;nh nh&acirc;n đạo.</p>\r\n<p>T&agrave;u USNS Mercy (T-AH-19) l&agrave; con t&agrave;u đầu ti&ecirc;n trong lớp t&agrave;u bệnh viện Mercy của Hải qu&acirc;n Mỹ. Theo quy định của c&ocirc;ng ước Gen&egrave;ve, USNS Mercy v&agrave; thủy thủ đo&agrave;n kh&ocirc;ng được mang theo bất kỳ thứ vũ kh&iacute; tấn c&ocirc;ng n&agrave;o, ngoại trừ vũ kh&iacute; ph&ograve;ng vệ. Bất cứ h&agrave;nh động n&agrave;o tấn c&ocirc;ng t&agrave;u Mercy đều được xem như tội &aacute;c chiến tranh.</p>\r\n<p>Nhiệm vụ ch&iacute;nh của t&agrave;u USNS Mercy l&agrave; cung cấp c&aacute;c dịch vụ phẫu thuật v&agrave; y tế di động sắc b&eacute;n, linh hoạt v&agrave; nhanh ch&oacute;ng để hỗ trợ thủy qu&acirc;n lục chiến, c&aacute;c lực lượng đặc nhiệm mặt đất v&agrave; tr&ecirc;n kh&ocirc;ng, c&aacute;c đơn vị kh&ocirc;ng qu&acirc;n v&agrave; lục qu&acirc;n triển khai tr&ecirc;n bờ, c&aacute;c lực lượng đặc nhiệm đổ bộ đường biển v&agrave; c&aacute;c lực lượng chiến đấu tr&ecirc;n mặt nước.</p>\r\n<p>USNS Mercy lu&ocirc;n được n&acirc;ng cấp đổi mới những trang thiết bị y khoa hiện đại, đ&aacute;p ứng nhu cầu ng&agrave;y một tốt hơn cho bệnh nh&acirc;n.</p>\r\n<p>Với đội ngũ b&aacute;c sĩ hơn 300 người, 12 ph&ograve;ng mổ ti&ecirc;u chuẩn kỹ thuật cao với khả năng thực hiện phẫu thuật đa khoa, chỉnh h&igrave;nh&hellip; c&oacute; thể chữa trị c&ugrave;ng l&uacute;c cho hơn 1.000 bệnh nh&acirc;n. T&agrave;u bệnh viện của Mỹ được thiết kế với nhiệm vụ kh&aacute;m v&agrave; chữa bệnh cho c&aacute;c binh sĩ hoạt động tr&ecirc;n c&aacute;c t&agrave;u chiến, b&ecirc;n cạnh đ&oacute; t&agrave;u bệnh viện cũng l&agrave;m c&aacute;c nhiệm vụ nh&acirc;n đạo quốc tế tại những nơi chiến sự hoặc gặp thi&ecirc;n tai tr&ecirc;n khắp&nbsp;<a class=\"seokwl\" title=\"thế giới\" href=\"https://www.baogiaothong.vn/the-gioi/\">thế giới</a>. Đồng thời t&agrave;u c&ograve;n cung cấp dịch vụ kh&aacute;m chữa bệnh, phẫu thuật di động trong c&aacute;c nhiệm vụ cứu trợ quốc tế tại những v&ugrave;ng thi&ecirc;n tai hay chiến sự.</p>', 218, 1, 52, 1, '2022-11-18 19:35:41', '2023-02-07 07:31:52'),
(6, 'Tuần lễ \"điểm nhấn\" tại Festival Huế 2022 - Festival bốn mùa có gì đặc sắc?', 'tuan-le-diem-nhan-tai-festival-hue-2022---festival-bon-mua-co-gi-dac-sac', 'Điểm nhấn của Festival Huế 2022 là Tuần lễ Festival văn hóa- nghệ thuật, với nhiều chương trình nghệ thuật, lễ hội quy mô, độc đáo, hoành tráng.', '<p>Chiều 20/6, tỉnh Thừa Thi&ecirc;n Huế tổ chức họp b&aacute;o Tuần lễ Festival Huế 2022. Đ&acirc;y l&agrave; năm đầu ti&ecirc;n Festival Huế diễn ra li&ecirc;n tục với chuỗi c&aacute;c hoạt động văn h&oacute;a, lễ hội quanh năm được ph&acirc;n bố theo chủ đề từng m&ugrave;a- Festival bốn m&ugrave;a.</p>\r\n<p>Festival Huế 2022 cũng l&agrave; năm đầu ti&ecirc;n TP Huế mở rộng địa giới h&agrave;nh ch&iacute;nh, to&agrave;n tỉnh phấn đấu đến 2025 x&acirc;y dựng v&agrave; ph&aacute;t triển Thừa Thi&ecirc;n Huế trở th&agrave;nh TP trực thuộc Trung ương tr&ecirc;n nền tảng bảo tồn, ph&aacute;t huy gi&aacute; trị di sản cố đ&ocirc; v&agrave; bản sắc văn h&oacute;a Huế, với đặc trưng văn h&oacute;a, di sản, sinh th&aacute;i, cảnh quan, th&acirc;n thiện m&ocirc;i trường v&agrave; th&ocirc;ng minh.</p>\r\n<section class=\"bnrBt txtCent\"></section>\r\n<p>Đ&acirc;y cũng l&agrave; năm gắn với Kỷ niệm 335 năm ch&uacute;a Nguyễn Ph&uacute;c Th&aacute;i chọn Ph&uacute; Xu&acirc;n l&agrave;m thủ phủ Xứ Đ&agrave;ng Trong, từ đ&oacute; trở th&agrave;nh Kinh đ&ocirc; của nh&agrave; T&acirc;y Sơn v&agrave; kinh đ&ocirc; nước Việt Nam thời nh&agrave; Nguyễn.</p>\r\n<div>\r\n<p class=\"image\"><img class=\"width100 loaded\" src=\"https://cdn.baogiaothong.vn/upload/images/2022-2/article_img/2022-06-20/img-bgt-2021-tuan-le-festival-hue-2022-co-gi-dac-sac-1-1655723791-width1280height720.jpg\" alt=\"Tuần lễ \" data-original=\"https://cdn.baogiaothong.vn/upload/images/2022-2/article_img/2022-06-20/img-bgt-2021-tuan-le-festival-hue-2022-co-gi-dac-sac-1-1655723791-width1280height720.jpg\" data-was-processed=\"true\"></p>\r\n<p class=\"img_chu_thich\">Ph&oacute; Chủ tịch UBND tỉnh Thừa Thi&ecirc;n Huế Nguyễn Thanh B&igrave;nh ph&aacute;t biểu tại buổi họp b&aacute;o Tuần lễ Festival Huế 2022</p>\r\n</div>\r\n<p>Điểm nhấn của Festival Huế 2022 l&agrave; Tuần lễ Festival văn h&oacute;a - nghệ thuật với chủ đề &ldquo;Di sản văn h&oacute;a với hội nhập v&agrave; ph&aacute;t triển&rdquo; diễn ra từ ng&agrave;y 25- 30/6.</p>\r\n<p>Đ&acirc;y l&agrave; sự khẳng định cho nỗ lực của Thừa Thi&ecirc;n Huế n&oacute;i ri&ecirc;ng v&agrave; cả nước n&oacute;i chung đưa cuộc sống trở lại b&igrave;nh thường sau đại dịch v&agrave; l&agrave; sự kiện g&oacute;p phần k&iacute;ch cầu du lịch với mục ti&ecirc;u ph&aacute;t triển du lịch &ldquo;chủ động &ndash; bền vững &ndash; an to&agrave;n&rdquo;.</p>\r\n<p>Tuần lễ Festival Huế 2022 với nhiều chương tr&igrave;nh nghệ thuật, lễ hội quy m&ocirc;, độc đ&aacute;o, ho&agrave;nh tr&aacute;ng.</p>\r\n<p>Trong đ&oacute; chương tr&igrave;nh Nghệ thuật Khai m&agrave;n (20h ng&agrave;y 25/6) tại Quảng trường Ngọ M&ocirc;n- l&agrave; chương tr&igrave;nh biểu diễn nghệ thuật kết hợp tr&igrave;nh diễn thời trang &aacute;o d&agrave;i mở m&agrave;n cho chuỗi hoạt động s&ocirc;i nổi của Tuần lễ Festival Huế 2022.</p>\r\n<p>&ldquo;Chương tr&igrave;nh được d&agrave;n dựng c&ocirc;ng phu, mới lạ, kết hợp hiệu ứng &acirc;m thanh, c&ocirc;ng nghệ chiếu s&aacute;ng ti&ecirc;n tiến v&agrave; ph&aacute;o hoa nghệ thuật được kỳ vọng đem đến cho kh&aacute;n giả những cảm x&uacute;c kh&oacute; qu&ecirc;n.</p>\r\n<p>C&aacute;c tiết mục sẽ t&aacute;i hiện TP Huế đẹp như một &ldquo;B&agrave;i thơ đ&ocirc; thị&rdquo;, ngợi ca v&ugrave;ng đất di sản, cổ k&iacute;nh c&oacute; bề d&agrave;y truyền thống nơi &ldquo;Tinh hoa hội tụ&rdquo;, đồng thời t&ocirc;n vinh một &ldquo;Th&agrave;nh phố xanh&rdquo; ph&aacute;t triển bền vững, thể hiện những &ldquo;Ước vọng về Huế&rdquo; - TP Festival của ch&acirc;u &Aacute; c&ugrave;ng đất nước Việt Nam tỏa s&aacute;ng ch&agrave;o đ&oacute;n tương lai rạng ngời&rdquo;, l&atilde;nh đạo BTC Festival Huế 2022 chia sẻ.</p>\r\n<p>Tuần lễ Festival Huế 2022 c&ograve;n c&oacute; chương tr&igrave;nh biểu diễn h&agrave;ng đ&ecirc;m của c&aacute;c đo&agrave;n nghệ thuật trong nước v&agrave; quốc tế (từ 19h30- 21h30 c&aacute;c ng&agrave;y 26- 29/6) tại c&aacute;c s&acirc;n khấu Quốc Tử Gi&aacute;m, Bia Quốc Học, Cồn D&atilde; Vi&ecirc;n, C&ocirc;ng vi&ecirc;n 3/2, phố đi bộ Nguyễn Đ&igrave;nh Chiểu v&agrave; cầu Gỗ Lim.</p>\r\n<div>\r\n<p class=\"image\"><img class=\"width100 loaded\" src=\"https://cdn.baogiaothong.vn/upload/images/2022-2/article_img/2022-06-20/img-bgt-2021-tuan-le-festival-hue-2022-co-gi-dac-sac-3-1655723886-width1280height720.jpg\" alt=\"Tuần lễ \" data-original=\"https://cdn.baogiaothong.vn/upload/images/2022-2/article_img/2022-06-20/img-bgt-2021-tuan-le-festival-hue-2022-co-gi-dac-sac-3-1655723886-width1280height720.jpg\" data-was-processed=\"true\"></p>\r\n<p class=\"img_chu_thich\">Một trong những hoạt động quảng diễn đường phố tại Festival Huế - Ảnh tư liệu</p>\r\n</div>\r\n<p>Lễ hội đường phố &ldquo;Sắc m&agrave;u văn h&oacute;a&rdquo; (16h từ ng&agrave;y 26- 28/6), với h&igrave;nh thức quảng diễn đường phố s&ocirc;i động của c&aacute;c đo&agrave;n nghệ thuật trong nước v&agrave; quốc tế như: tr&igrave;nh diễn ca m&uacute;a nhạc, tạp kỹ đường phố trong nước v&agrave; quốc tế (26/6); diễu h&agrave;nh xe cổ &ldquo;Huế bốn m&ugrave;a hoa&rdquo; (27/6); quảng diễn nghệ thuật c&aacute;c loại h&igrave;nh lễ hội v&agrave; tr&ograve; diễn d&acirc;n gian với sự tham gia của c&aacute;c địa phương trong tỉnh (28/6).</p>\r\n<p>Chương tr&igrave;nh &Acirc;m nhạc Trịnh C&ocirc;ng Sơn (19h30 ng&agrave;y 27/6) tại s&acirc;n khấu Ngọ M&ocirc;n, với sự tham gia của nhiều thế hệ nghệ sĩ, ca sĩ nổi tiếng, c&ugrave;ng sự g&oacute;p mặt của c&aacute;c diễn vi&ecirc;n trong phim &ldquo;Em v&agrave; Trịnh.</p>\r\n<p>Chương tr&igrave;nh quảng diễn &ldquo;Tuồng Huế, Ng&agrave;n xưa &acirc;m vọng&rdquo; (7h ng&agrave;y 28/6), với lễ rước mặt nạ Tuồng v&agrave; tr&igrave;nh diễn Tuồng Cung đ&igrave;nh trải d&agrave;i từ Thanh B&igrave;nh Từ Đường đến Nghinh Lương Đ&igrave;nh- Đ&acirc;y cũng l&agrave; sự kiện nhằm tri &acirc;n tổ nghề Tuồng, t&ocirc;n vinh di sản Tuồng cung đ&igrave;nh Huế, ph&ocirc; diễn vẻ đẹp của trang phục, nghệ thuật vẽ mặt nạ Tuồng.</p>\r\n<p>Chương tr&igrave;nh &ldquo;Ho&agrave;ng cung giao h&ograve;a&rdquo; (20h ng&agrave;y 29/6) tại Đại Nội, l&agrave; chương tr&igrave;nh nghệ thuật kết hợp giữa truyền thống v&agrave; hiện đại trong kh&ocirc;ng gian Ho&agrave;ng cung, thể hiện &acirc;m sắc cung đ&igrave;nh v&agrave; những ca kh&uacute;c về Huế xưa, được kết cấu bằng nghệ thuật sắp đặt cảnh tr&iacute; v&agrave; &aacute;nh s&aacute;ng tại kh&ocirc;ng gian từ Ngọ M&ocirc;n đến điện Th&aacute;i H&ograve;a.</p>\r\n<div>\r\n<p class=\"image\"><img class=\"width100 loaded\" src=\"https://cdn.baogiaothong.vn/upload/images/2022-2/article_img/2022-06-20/img-bgt-2021-tuan-le-festival-hue-2022-co-gi-dac-sac-2-1655723954-width900height600.jpg\" alt=\"Tuần lễ \" data-original=\"https://cdn.baogiaothong.vn/upload/images/2022-2/article_img/2022-06-20/img-bgt-2021-tuan-le-festival-hue-2022-co-gi-dac-sac-2-1655723954-width900height600.jpg\" data-was-processed=\"true\"></p>\r\n<p class=\"img_chu_thich\">Đi c&agrave; kheo tại Festival Huế - Ảnh tư liệu</p>\r\n</div>\r\n<p>Tuần lễ Festival Huế 2022 kh&eacute;p lại với Đ&ecirc;m Gala Gi&atilde; bạn (19h30 ng&agrave;y 30/6) tại cồn D&atilde; Vi&ecirc;n, với những tiết mục giao lưu giữa c&aacute;c đo&agrave;n nghệ thuật Việt Nam v&agrave; quốc tế - l&agrave; &ldquo;bữa tiệc nghệ thuật&rdquo; mở, nơi nghệ sĩ biểu diễn v&agrave; kh&aacute;n giả h&ograve;a v&agrave;o kh&ocirc;ng kh&iacute; lễ hội, x&oacute;a nh&ograve;a khoảng c&aacute;ch, tất cả c&ugrave;ng thăng hoa với cảm x&uacute;c nghệ thuật trong kh&ocirc;ng gian trữ t&igrave;nh cạnh d&ograve;ng s&ocirc;ng Hương thơ mộng.</p>\r\n<p>Ngo&agrave;i ra, trong khu&ocirc;n khổ Festival Huế 2022 &ndash; Festival bốn m&ugrave;a c&ograve;n c&oacute; nhiều hoạt động đồng h&agrave;nh, hưởng ứng, c&aacute;c chương tr&igrave;nh x&atilde; hội h&oacute;a v&agrave; rất nhiều hoạt động văn h&oacute;a cộng đồng, trưng b&agrave;y, triển l&atilde;m...</p>\r\n<p>&ldquo;Festival Huế 2022 l&agrave; cơ hội để du kh&aacute;ch thưởng thức sự giao thoa sống động giữa truyền thống v&agrave; hiện đại, trải nghiệm những sắc m&agrave;u văn h&oacute;a, nghệ thuật hấp dẫn của văn h&oacute;a Huế, văn h&oacute;a Việt Nam v&agrave; c&aacute;c quốc gia tr&ecirc;n&nbsp;<a class=\"seokwl\" title=\"thế giới\" href=\"https://www.baogiaothong.vn/the-gioi/\">thế giới</a>, đắm m&igrave;nh trong kh&ocirc;ng gian cổ k&iacute;nh của một cố đ&ocirc; gi&agrave;u bản sắc truyền thống, nhưng vẫn s&aacute;ng bừng sức sống của một đ&ocirc; thị xanh, th&acirc;n thiện với m&ocirc;i trường v&agrave; ph&aacute;t triển bền vững&rdquo;, BTC Festival Huế 2022 cho hay.</p>', 218, 3, 33, 1, '2022-11-18 19:35:41', '2023-02-08 01:11:25'),
(7, 'Bitcoin mất mốc 20.000 USD', 'bitcoin-mat-moc-20000-usd', 'Lần đầu tiên kể từ tháng 12/2020, Bitcoin xuống dưới 20.000 USD khi nhà đầu tư tiếp tục tháo chạy khỏi các tài sản rủi ro.', '<p class=\"description\">Lần đầu ti&ecirc;n kể từ th&aacute;ng 12/2020, Bitcoin xuống dưới 20.000 USD khi nh&agrave; đầu tư tiếp tục th&aacute;o chạy khỏi c&aacute;c t&agrave;i sản rủi ro.</p>\r\n<article class=\"fck_detail \">\r\n<div id=\"divfirst\">\r\n<p class=\"Normal\">Đầu giờ chiều nay,&nbsp;<a href=\"https://vnexpress.net/chu-de/bitcoin-3112\" rel=\"dofollow\">Bitcoin</a>&nbsp;giảm 6% xuống quanh 19.300 USD một đồng. Tiền số lớn nhất thế giới về vốn h&oacute;a đ&atilde; giảm 12 ng&agrave;y li&ecirc;n tiếp. Ethereum c&oacute; thời điểm cũng về dưới 1.000 USD, mức thấp nhất kể từ th&aacute;ng 1/2021.</p>\r\n<p class=\"Normal\">\"Những lo ngại về suy tho&aacute;i đang tăng, khiến nh&agrave; đầu tư th&aacute;o chạy khỏi t&agrave;i sản rủi ro. Điều đ&oacute; cũng khiến c&aacute;c nh&agrave; giao dịch tiền điện tử thận trọng trong việc bắt đ&aacute;y, d&ugrave; Bitcoin đ&atilde; giảm về mức thấp\", Edward Moya, nh&agrave; ph&acirc;n t&iacute;ch thị trường cấp cao tại Oanda, nhận x&eacute;t.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture data-inimage=\"done\"><source srcset=\"https://i1-kinhdoanh.vnecdn.net/2022/06/18/1x-1-png-3270-1655540608.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=BSMbeQ7se9sLkLVFuJh3Nw 1x, https://i1-kinhdoanh.vnecdn.net/2022/06/18/1x-1-png-3270-1655540608.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ATKoChTSlP0DD9RXTc_S0w 1.5x, https://i1-kinhdoanh.vnecdn.net/2022/06/18/1x-1-png-3270-1655540608.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=11IAV5uGF14MKZA6K4jM-g 2x\" data-srcset=\"https://i1-kinhdoanh.vnecdn.net/2022/06/18/1x-1-png-3270-1655540608.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=BSMbeQ7se9sLkLVFuJh3Nw 1x, https://i1-kinhdoanh.vnecdn.net/2022/06/18/1x-1-png-3270-1655540608.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ATKoChTSlP0DD9RXTc_S0w 1.5x, https://i1-kinhdoanh.vnecdn.net/2022/06/18/1x-1-png-3270-1655540608.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=11IAV5uGF14MKZA6K4jM-g 2x\"><img class=\"lazy lazied\" src=\"https://i1-kinhdoanh.vnecdn.net/2022/06/18/1x-1-png-3270-1655540608.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=BSMbeQ7se9sLkLVFuJh3Nw\" alt=\"Bitcoin xuống dưới 20.000 USD, lần đầu ti&ecirc;n kể từ th&aacute;ng 12/2020. Ảnh: Bloomberg\" loading=\"lazy\" data-src=\"https://i1-kinhdoanh.vnecdn.net/2022/06/18/1x-1-png-3270-1655540608.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=BSMbeQ7se9sLkLVFuJh3Nw\" data-ll-status=\"loaded\"></picture>\r\n<div class=\"embed-container-ads\">&nbsp;</div>\r\n</div>\r\n<figcaption>\r\n<p class=\"Image\">Bitcoin xuống dưới 20.000 USD, lần đầu ti&ecirc;n kể từ th&aacute;ng 12/2020. Ảnh:&nbsp;<em>Bloomberg</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Cục Dự trữ Li&ecirc;n bang Mỹ (Fed) tuần n&agrave;y&nbsp;<a href=\"https://vnexpress.net/fed-tang-lai-suat-manh-nhat-gan-30-nam-4476573.html\" rel=\"dofollow\">tăng l&atilde;i suất</a>&nbsp;th&ecirc;m 0,75% - mức tăng lớn nhất kể từ năm 1994. C&aacute;c ng&acirc;n h&agrave;ng trung ương kh&aacute;c cũng ra t&iacute;n hiệu sẽ tiếp tục tăng l&atilde;i suất trong năm nay để kiềm chế lạm ph&aacute;t. M&ocirc;i trường l&atilde;i suất cao, theo giới ph&acirc;n t&iacute;ch, l&agrave; t&iacute;n hiệu ti&ecirc;u cực cho c&aacute;c t&agrave;i sản rủi ro như tiền điện tử. Việc n&agrave;y g&oacute;p phần khiến Bitcoin giảm 70% so với mức cao nhất mọi thời đại v&agrave;o th&aacute;ng 11.</p>\r\n<p class=\"Normal\">Thị trường bắt đầu trượt dốc từ cuối năm ngo&aacute;i do lo ngại về khả năng thắt chặt tiền tệ của Fed, sau đ&oacute; l&agrave; sự sụp đổ của mạng lưới Terra v&agrave;o th&aacute;ng trước v&agrave; khủng hoảng gần đ&acirc;y của c&ocirc;ng ty cho vay tiền điện tử Celsius Network. &Aacute;p lực l&ecirc;n thị trường tiếp tục tăng khi Three Arrows Capital cho biết đ&atilde; chịu tổn thất lớn v&agrave; đang xem x&eacute;t việc b&aacute;n t&agrave;i sản hoặc t&igrave;m một g&oacute;i cứu trợ. Theo c&ocirc;ng ty nghi&ecirc;n cứu Glassnode, ngay cả những người nắm giữ tiền điện tử cho mục đ&iacute;ch d&agrave;i hạn cũng đang chịu &aacute;p lực.</p>\r\n</div>\r\n<div id=\"admbackground\" data-set=\"dfp\"></div>\r\n<div id=\"divend\">\r\n<p class=\"Normal\">Mike McGlone, nh&agrave; ph&acirc;n t&iacute;ch của&nbsp;<em>Bloomberg Intelligence</em>, cho rằng ngay cả khi thị trường lao dốc, dữ liệu lịch sử cho thấy Bitcoin c&oacute; thể t&igrave;m thấy ngưỡng hỗ trợ quan trọng quanh 20.000 USD. Những đợt b&aacute;n th&aacute;o trước đ&oacute; chứng minh ngưỡng gi&aacute; n&agrave;y c&oacute; khả năng phục hồi.</p>\r\n<p class=\"Normal\">Bitcoin c&oacute; thể \"x&acirc;y dựng ngưỡng hỗ trợ trong khoảng 20.000 USD như c&aacute;ch thị trường giữ được mốc 5.000 USD trong giai đoạn 2018-2019 v&agrave; v&ugrave;ng 300 USD năm 2014-2015\", Mike nhận x&eacute;t.</p>\r\n<p class=\"Normal\">Quy m&ocirc; thị trường tiền điện tử đ&atilde; thu hẹp đ&aacute;ng kể so với mức đỉnh x&aacute;c lập cuối năm 2021, khi Bitcoin được giao dịch gần 69.000 USD v&agrave; c&aacute;c nh&agrave; giao dịch đổ tiền v&agrave;o c&aacute;c khoản đầu tư mang t&iacute;nh đầu cơ. Theo&nbsp;<em>CoinGecko</em>, tổng vốn h&oacute;a thị trường của tiền điện tử hiện chỉ c&ograve;n khoảng 900 tỷ USD, giảm từ 3.000 tỷ USD v&agrave;o th&aacute;ng 11 năm trước.</p>\r\n<p class=\"Normal\">\"T&acirc;m l&yacute; tr&ecirc;n thị trường tiền điện tử cho thấy những ẩn số l&agrave; vấn đề quan trọng nhất tại thời điểm n&agrave;y\", Ainsley To, Noelle Acheson v&agrave; Konrad Laesser của Genesis Trading đ&aacute;nh gi&aacute;, \"Sự trỗi dậy của rủi ro đối t&aacute;c l&agrave; một lời nhắc nhở rằng kh&ocirc;ng phải mọi yếu tố quan trọng trong quản l&yacute; rủi ro đều c&oacute; thể được định lượng một c&aacute;ch ch&iacute;nh x&aacute;c. Rủi ro l&agrave; những g&igrave; c&ograve;n s&oacute;t lại sau khi bạn nghĩ rằng bạn kiểm so&aacute;t được mọi thứ\".</p>\r\n</div>\r\n</article>', 218, 4, 29, 1, '2022-11-21 19:35:41', '2023-02-08 01:11:31'),
(12, 'Các tỷ phú tiền số mất 80% tài sản', 'cac-ty-phu-tien-so-mat-80%-tai-san', 'Tổng tài sản của 7 tỷ phú tiền số giàu nhất thế giới cuối năm ngoái đạt 145 tỷ USD, nhưng giờ chỉ còn hơn 30 tỷ USD', '<p class=\"description\">Tổng t&agrave;i sản của 7 tỷ ph&uacute; tiền số gi&agrave;u nhất thế giới cuối năm ngo&aacute;i đạt 145 tỷ USD, nhưng giờ chỉ c&ograve;n hơn 30 tỷ USD.</p>\r\n<article class=\"fck_detail \">\r\n<p class=\"Normal\">Cơn sốt tiền số đ&atilde; biến Changpeng Zhao, Sam Bankman-Fried, Mike Novogratz v&agrave; nhiều người kh&aacute;c th&agrave;nh tỷ ph&uacute; với khối t&agrave;i sản tăng theo cấp số nh&acirc;n. Nhưng kh&ocirc;ng l&acirc;u sau khi gia nhập nh&oacute;m gi&agrave;u nhất thế giới, vận may của họ lại đột ngột biến mất.</p>\r\n<p class=\"Normal\">Ng&agrave;y 9/11/2021, tổng t&agrave;i sản của 7 tỷ ph&uacute; tiền số gi&agrave;u nhất thế giới đạt 145 tỷ USD, khi gi&aacute; Bitcoin lập đỉnh tại gần 69.000 USD. Nhưng đến nay, họ đ&atilde; mất tổng cộng 114 tỷ USD, theo&nbsp;<em>Bloomberg Billionaires Index,&nbsp;</em>tương đương mức giảm gần 80%.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture data-inimage=\"done\"><source srcset=\"https://i1-kinhdoanh.vnecdn.net/2022/06/14/A-nh-chu-p-Ma-n-hi-nh-2022-06-1489-5988-1655205772.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=fDuHATzK_D10v44DrZoSNg 1x, https://i1-kinhdoanh.vnecdn.net/2022/06/14/A-nh-chu-p-Ma-n-hi-nh-2022-06-1489-5988-1655205772.png?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=m4u67sH5v2ykarbqqiB9Mg 1.5x, https://i1-kinhdoanh.vnecdn.net/2022/06/14/A-nh-chu-p-Ma-n-hi-nh-2022-06-1489-5988-1655205772.png?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=qF-Sdsur8C447Ar93tOQUg 2x\" data-srcset=\"https://i1-kinhdoanh.vnecdn.net/2022/06/14/A-nh-chu-p-Ma-n-hi-nh-2022-06-1489-5988-1655205772.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=fDuHATzK_D10v44DrZoSNg 1x, https://i1-kinhdoanh.vnecdn.net/2022/06/14/A-nh-chu-p-Ma-n-hi-nh-2022-06-1489-5988-1655205772.png?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=m4u67sH5v2ykarbqqiB9Mg 1.5x, https://i1-kinhdoanh.vnecdn.net/2022/06/14/A-nh-chu-p-Ma-n-hi-nh-2022-06-1489-5988-1655205772.png?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=qF-Sdsur8C447Ar93tOQUg 2x\"><img class=\"lazy lazied\" src=\"https://i1-kinhdoanh.vnecdn.net/2022/06/14/A-nh-chu-p-Ma-n-hi-nh-2022-06-1489-5988-1655205772.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=fDuHATzK_D10v44DrZoSNg\" alt=\"Khối t&agrave;i sản của 7 tỷ ph&uacute; tiền số giảm mạnh theo đ&agrave; lao dốc gần đ&acirc;y của thị trường. Ảnh: Bloomberg\" loading=\"lazy\" data-src=\"https://i1-kinhdoanh.vnecdn.net/2022/06/14/A-nh-chu-p-Ma-n-hi-nh-2022-06-1489-5988-1655205772.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=fDuHATzK_D10v44DrZoSNg\" data-ll-status=\"loaded\"></picture>\r\n<div class=\"embed-container-ads\">&nbsp;</div>\r\n</div>\r\n<figcaption>\r\n<p class=\"Image\">Khối t&agrave;i sản của 7 tỷ ph&uacute; tiền số giảm mạnh theo đ&agrave; lao dốc gần đ&acirc;y của thị trường. Ảnh:&nbsp;<em>Bloomberg</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\"><a href=\"https://vnexpress.net/chu-de/cz-3828\" rel=\"dofollow\">Changpeng Zhao</a>, nh&agrave; s&aacute;ng lập Binance - s&agrave;n giao dịch tiền điện tử lớn nhất thế giới, từng sở hữu&nbsp;<a href=\"https://vnexpress.net/ceo-binance-so-huu-tai-san-tien-so-lon-nhat-the-gioi-4414344.html\" rel=\"dofollow\">khối t&agrave;i sản lớn thứ 11 thế giới</a>, theo&nbsp;<em>Bloomberg Billionaires Index.&nbsp;</em>Tuy nhi&ecirc;n, con số n&agrave;y hiện giảm 89%, chỉ c&ograve;n 10,2 tỷ USD. Binance cũng trở th&agrave;nh t&acirc;m điểm ch&uacute; &yacute; cho c&aacute;c nh&agrave; lập ph&aacute;p tại Mỹ, vốn đang t&igrave;m c&aacute;ch kiềm chế ng&agrave;nh c&ocirc;ng nghiệp tiền số.</p>\r\n<p class=\"Normal\">Bankman-Fried - CEO 30 tuổi của s&agrave;n giao dịch FTX, cũng ghi nhận t&agrave;i sản giảm 66% kể từ khi đạt đỉnh 26 tỷ USD. Theo&nbsp;<em>Bloomberg</em>, điều n&agrave;y c&oacute; thể l&agrave;m ảnh hưởng đến kế hoạch đầu tư v&agrave;o ch&iacute;nh trị của anh. Bankman-Fried đ&atilde; r&oacute;t 16 triệu USD v&agrave;o c&aacute;c Si&ecirc;u ủy ban hoạt động ch&iacute;nh trị (Super PAC) v&agrave;o th&aacute;ng 4. Tỷ ph&uacute; cũng cho biết dự kiến đ&oacute;ng g&oacute;p hơn 100 triệu USD trong cuộc bầu cử tổng thống tiếp theo để hỗ trợ đảng D&acirc;n chủ.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-kinhdoanh.vnecdn.net/2022/06/14/changpeng-7562-1641797459-9162-1655213080.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=5qN7xYj9K1L0pK_EpnLDmA 1x, https://i1-kinhdoanh.vnecdn.net/2022/06/14/changpeng-7562-1641797459-9162-1655213080.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=luHVHUU7cejfnvarp9wekg 1.5x, https://i1-kinhdoanh.vnecdn.net/2022/06/14/changpeng-7562-1641797459-9162-1655213080.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=MSDcibM7Qv4VqGe_4OdPIg 2x\" data-srcset=\"https://i1-kinhdoanh.vnecdn.net/2022/06/14/changpeng-7562-1641797459-9162-1655213080.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=5qN7xYj9K1L0pK_EpnLDmA 1x, https://i1-kinhdoanh.vnecdn.net/2022/06/14/changpeng-7562-1641797459-9162-1655213080.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=luHVHUU7cejfnvarp9wekg 1.5x, https://i1-kinhdoanh.vnecdn.net/2022/06/14/changpeng-7562-1641797459-9162-1655213080.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=MSDcibM7Qv4VqGe_4OdPIg 2x\"><img class=\"lazy lazied\" src=\"https://i1-kinhdoanh.vnecdn.net/2022/06/14/changpeng-7562-1641797459-9162-1655213080.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=5qN7xYj9K1L0pK_EpnLDmA\" alt=\"CEO Binance Chengpeng Zhao trong một sự kiện về tiền số tại Malta năm 2018. Ảnh: Reuters\" loading=\"lazy\" data-src=\"https://i1-kinhdoanh.vnecdn.net/2022/06/14/changpeng-7562-1641797459-9162-1655213080.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=5qN7xYj9K1L0pK_EpnLDmA\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">CEO Binance Chengpeng Zhao trong một sự kiện về tiền số tại Malta năm 2018. Ảnh:&nbsp;<em>Reuters</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Novogratz (57 tuổi) người đứng đầu Fortress Investment Group, l&agrave; một trong những c&aacute;i t&ecirc;n nổi bật tr&ecirc;n thị trường tiền điện tử. T&agrave;i sản của tỷ ph&uacute; n&agrave;y đ&atilde; giảm xuống c&ograve;n 2,1 tỷ USD, thấp hơn cả khi lần đầu xuất hiện trong bảng xếp hạng&nbsp;<em>Bloomberg Billionaires Index&nbsp;</em>v&agrave;o th&aacute;ng 12/2020. Thời điểm đ&oacute;, Bitcoin giao dịch quanh 29.000 USD.</p>\r\n<p class=\"Normal\">Trong khi đ&oacute;, Cameron v&agrave; Tyler Winklevoss ghi nhận t&agrave;i sản chỉ c&ograve;n 3 tỷ USD mỗi người - giảm nửa từ mức cao nhất l&agrave; 5,9 tỷ USD. Hai người s&aacute;ng lập s&agrave;n giao dịch tiền điện tử Gemini th&aacute;ng n&agrave;y cũng th&ocirc;ng b&aacute;o sẽ cắt giảm khoảng 10% nh&acirc;n sự.</p>\r\n<p class=\"Normal\">C&aacute;c nh&agrave; s&aacute;ng lập Coinbase Brian Armstrong (39 tuổi) v&agrave; Fred Ehrsam (34 tuổi) từng ghi nhận khối t&agrave;i sản trị gi&aacute; 18,1 tỷ USD. Nhưng hiện tại, họ chỉ c&ograve;n 2,1 tỷ USD mỗi người. Nguy&ecirc;n nh&acirc;n l&agrave; do cổ phiếu của c&ocirc;ng ty n&agrave;y giảm 79% kể từ khi IPO.</p>\r\n<p class=\"Normal\">Những người đặt cược lớn v&agrave;o Bitcoin, như CEO Microstrategy Michael Saylor hay Tổng thống El Salvador Nayib Bukele cũng đang cảm thấy kh&oacute; khăn khi gi&aacute; tiền số lớn nhất thế giới xuống 21.000 USD một đồng - mức thấp nhất kể từ th&aacute;ng 12/2020.</p>\r\n<p class=\"Normal\">Từng được coi l&agrave; ph&aacute;t kiến mở ra kỷ nguy&ecirc;n mới của t&agrave;i ch&iacute;nh phi tập trung, tiền số đang trải qua giai đoạn kh&oacute; khăn do một loạt biến cố. Hồi th&aacute;ng 5, hệ sinh th&aacute;i Luna v&agrave; tiền số ổn định (stablecoin) TerraUSD sụp đổ. Mới đ&acirc;y, Celsius - một trong những nền tảng cho vay tiền số lớn nhất hiện nay - đ&atilde; th&ocirc;ng b&aacute;o đ&oacute;ng băng tất cả c&aacute;c giao dịch. Động th&aacute;i n&agrave;y được đưa ra sau tin đồn Celsius kh&ocirc;ng thể đ&aacute;p ứng lợi nhuận như đ&atilde; hứa hẹn tr&ecirc;n một số sản phẩm.</p>\r\n<p class=\"Normal\">Thị trường t&agrave;i ch&iacute;nh to&agrave;n cầu th&igrave; trở n&ecirc;n hỗn loạn khi Cục Dự trữ Li&ecirc;n bang Mỹ (Fed) v&agrave; c&aacute;c ng&acirc;n h&agrave;ng trung ương kh&aacute;c c&oacute; kế hoạch tăng mạnh l&atilde;i suất để kiềm chế lạm ph&aacute;t cao nhất nhiều thập kỷ. Việc n&agrave;y c&agrave;ng khiến đ&agrave; giảm của tiền số tăng tốc.</p>\r\n<p class=\"Normal\">Saylor cho biết vẫn đặt niềm tin v&agrave;o tiền số. Tuy nhi&ecirc;n, c&ocirc;ng ty phần mềm do &ocirc;ng th&agrave;nh lập đang ghi nhận khoản lỗ kỷ lục. Microstrategy bắt đầu mua Bitcoin từ năm 2020, đến nay đang lỗ hơn 1 tỷ USD theo gi&aacute; giao dịch gần nhất của đồng tiền số n&agrave;y.</p>\r\n</article>', 218, 4, 79, 1, '2022-11-18 19:35:41', '2023-02-08 01:26:10');
INSERT INTO `posts` (`id`, `title`, `slug`, `excerpt`, `body`, `user_id`, `category_id`, `views`, `approved`, `created_at`, `updated_at`) VALUES
(20, 'Cam kết của ASEAN - Ấn Độ trong bối cảnh đầy rẫy bất đồng, tranh chấp', 'cam-ket-cua-asean---an-do-trong-boi-canh-day-ray-bat-dong-tranh-chap', 'Hội nghị đặc biệt các Bộ trưởng Ngoại giao ASEAN-Ấn Độ đã đạt được nhiều kết quả quan trọng khác nhau.', '<p><strong>Bốn kết quả quan trọng</strong></p>\r\n<p>Đại sứ Vũ Hồ, Quyền Trưởng SOM ASEAN Việt Nam, Vụ trưởng Vụ ASEAN, Bộ Ngoại giao cho biết: Hội nghị đặc biệt c&aacute;c Bộ trưởng Ngoại giao ASEAN-Ấn Độ lần n&agrave;y mang rất nhiều &yacute; nghĩa đặc biệt.</p>\r\n<p>Trước hết, đ&acirc;y l&agrave; hội nghị đ&aacute;nh dấu kỷ niệm 30 năm th&agrave;nh lập quan hệ đối t&aacute;c ASEAN-Ấn Độ, l&agrave; hội nghị đầu ti&ecirc;n giữa ASEAN v&agrave; Ấn Độ được tổ chức trực tiếp sau qu&atilde;ng thời gian gi&aacute;n đoạn do dịch bệnh&nbsp;<a class=\"seokwl\" title=\"Covid-19\" href=\"https://www.baogiaothong.vn/tin-tuc-covid-19-moi-nhat-ngay-hom-nay-channel405/\">Covid-19</a>.</p>\r\n<p>Hội nghị l&agrave; dịp để c&aacute;c b&ecirc;n đ&aacute;nh gi&aacute; t&igrave;nh h&igrave;nh quốc tế, khu vực v&agrave; c&aacute;c biến động phức tạp hiện nay, để c&ugrave;ng đưa ra c&aacute;c định hướng tương lai cho hợp t&aacute;c giữa ASEAN v&agrave; Ấn Độ thời gian tới.</p>\r\n<section class=\"bnrBt txtCent\"></section>\r\n<p>Với &yacute; nghĩa hết sức quan trọng, th&ocirc;ng qua hội nghị lần n&agrave;y, Việt Nam đ&atilde; gửi đi th&ocirc;ng điệp về h&ograve;a b&igrave;nh, ổn định, hợp t&aacute;c, đối thoại, tới tất cả c&aacute;c nước trong động đồng quốc tế v&agrave; khu vực.</p>\r\n<div>\r\n<p class=\"image\"><img class=\"width100 loaded\" src=\"https://cdn.baogiaothong.vn/upload/images/2022-2/article_img/2022-06-18/img-bgt-2021-screen-shot-2022-06-18-at-09-1655518381-width1474height778.jpg\" alt=\"cam kết của&nbsp;asean -&nbsp;Ấn Độ trong bối cảnh đầy rẫy bất đồng, tranh chấp&nbsp;\" data-original=\"https://cdn.baogiaothong.vn/upload/images/2022-2/article_img/2022-06-18/img-bgt-2021-screen-shot-2022-06-18-at-09-1655518381-width1474height778.jpg\" data-was-processed=\"true\"></p>\r\n<p class=\"img_chu_thich\">Đại sứ Vũ Hồ, Quyền Trưởng SOM ASEAN Việt Nam, Vụ trưởng Vụ ASEAN, Bộ Ngoại giao</p>\r\n</div>\r\n<p>Với những &yacute; nghĩa đ&oacute;, Hội nghị đặc biệt c&aacute;c Bộ trưởng Ngoại giao ASEAN-Ấn Độ đ&atilde; đạt được nhiều kết quả quan trọng kh&aacute;c nhau.</p>\r\n<p>Một trong những kết quả đầu ti&ecirc;n ch&iacute;nh l&agrave; hai b&ecirc;n đều thấy r&otilde; c&aacute;c gi&aacute; trị của h&ograve;a b&igrave;nh v&agrave; ổn định, gi&aacute; trị của hợp t&aacute;c, gi&aacute; trị của đối thoại, phấn đấu chung v&igrave; h&ograve;a b&igrave;nh, ổn định v&agrave; phồn vinh ở khu vực.</p>\r\n<p>Dựa tr&ecirc;n truyền thống l&acirc;u đời giữa quan hệ giữa Đ&ocirc;ng Nam &Aacute; v&agrave; Ấn Độ, c&oacute; thể thấy hai b&ecirc;n đều mong muốn n&acirc;ng mối quan hệ ASEAN-Ấn Độ l&ecirc;n một tầm cao mới.</p>\r\n<p>Hiện ASEAN v&agrave; Ấn Độ đang l&agrave; Đối t&aacute;c chiến lược của nhau. Trong bối cảnh t&igrave;nh h&igrave;nh quốc tế v&agrave; khu vực diễn biến phức tạp trong khu vực v&agrave; tr&ecirc;n&nbsp;<a class=\"seokwl\" title=\"thế giới\" href=\"https://www.baogiaothong.vn/the-gioi/\">thế giới</a>&nbsp;hiện nay, tinh thần đối thoại v&agrave; hợp t&aacute;c phải được duy tr&igrave;. Đ&acirc;y ch&iacute;nh l&agrave; th&ocirc;ng điệp được gửi đi qua hội nghị lần n&agrave;y.</p>\r\n<p>B&ecirc;n cạnh đ&oacute;, c&aacute;c b&ecirc;n đ&atilde; đ&aacute;nh gi&aacute; lại tất cả những hoạt động hợp t&aacute;c thời gian qua, đặc biệt l&agrave; giai đoạn đứt g&atilde;y do dịch bệnh Covid-19. C&aacute;c định hướng của hợp t&aacute;c ASEAN-Ấn Độ trong thời gian tới sẽ tập trung v&agrave;o tận dụng tất cả những th&agrave;nh quả đ&atilde; đạt được cũng như tiềm năng to lớn của cả hai b&ecirc;n.</p>\r\n<p>V&iacute; dụ như thị trường của ASEAN-Ấn Độ gần 2 tỷ người d&acirc;n, với tổng GDP gần 6.000 tỷ. Đ&acirc;y l&agrave; một trong những tiềm năng lớn trong hợp t&aacute;c kinh tế v&agrave; thương mại giữa ASEAN v&agrave; Ấn Độ.</p>\r\n<p>Kết quả thứ tư l&agrave; quyết t&acirc;m của c&aacute;c nước duy tr&igrave; h&ograve;a b&igrave;nh v&agrave; ổn định trong khu vực th&ocirc;ng qua đối thoại, đ&agrave;m ph&aacute;n v&agrave; x&acirc;y dựng l&ograve;ng tin.</p>\r\n<p>C&oacute; thể thấy tất cả những tranh chấp, những bất đồng trong khu vực vẫn thường xuy&ecirc;n diễn ra, kể cả những vận động giữa c&aacute;c nước lớn hay những tranh chấp về l&atilde;nh thổ, l&atilde;nh hải giữa c&aacute;c quốc gia. Trong bối cảnh đ&oacute;, ASEAN cũng như Ấn Độ đều cam kết sẽ dựa tr&ecirc;n đối thoại, hợp t&aacute;c để giảm bớt bất đồng, m&acirc;u thuẫn trong quan hệ giữa c&aacute;c nước, c&aacute;c quốc gia.</p>\r\n<p>T&oacute;m lại, Hội nghị đặc biệt c&aacute;c Bộ trưởng Ngoại giao ASEAN-Ấn Độ lần n&agrave;y chuyển đi th&ocirc;ng điệp về đối thoại thẳng thắn, hợp t&aacute;c ch&acirc;n th&agrave;nh, v&agrave; h&agrave;i h&ograve;a trong tất cả c&aacute;c lợi &iacute;ch.</p>\r\n<p><strong>Mang tinh thần Việt Nam đến hội nghị</strong></p>\r\n<p>Bộ trưởng Ngoại giao B&ugrave;i Thanh Sơn đ&atilde; tham gia hết sức t&iacute;ch cực v&agrave; chủ động trong tất cả c&aacute;c hoạt động của hội nghị, mang đến hội nghị tinh thần của Việt Nam, v&agrave; mang đến cho Ấn Độ tinh thần của 50 năm quan hệ Việt Nam-Ấn Độ.</p>\r\n<p>Ph&aacute;t biểu của Bộ trưởng B&ugrave;i Thanh Sơn tập trung nhiều tới th&uacute;c đẩy mối quan hệ gắn kết giữa Ấn Độ v&agrave; ASEAN, cũng như với Việt Nam trong tất cả c&aacute;c vấn đề của khu vực, trong tận dụng những kết quả đ&atilde; c&oacute; được, ph&aacute;t huy tiềm năng giữa ASEAN n&oacute;i chung, Việt Nam n&oacute;i ri&ecirc;ng với Ấn Độ để th&uacute;c đẩy mối quan hệ n&agrave;y l&ecirc;n một tầm cao mới.</p>\r\n<p>Tại Hội nghị, Bộ trưởng B&ugrave;i Thanh Sơn đ&atilde; khẳng định rất r&otilde; tất cả những quan điểm, lập trường của Việt Nam về c&aacute;c vấn đề quốc tế v&agrave; khu vực, về c&aacute;c tranh chấp, cạnh tranh giữa c&aacute;c nước lớn, cũng như c&aacute;c vấn đề đang nảy sinh trong khu vực như vấn đề biển Đ&ocirc;ng, vấn đề Myanmar, hay thậm ch&iacute; cả những vấn đề xung đột ở c&aacute;c khu vực kh&aacute;c tr&ecirc;n thế giới cũng được đề cập tới.</p>\r\n<p>T&ocirc;i cho rằng đ&oacute;ng g&oacute;p của Việt Nam, ngo&agrave;i đ&oacute;ng g&oacute;p cho th&agrave;nh c&ocirc;ng của hội nghị, c&ograve;n l&agrave; sự truyền tải th&ocirc;ng điệp của Đảng v&agrave; Nh&agrave; nước ta sau th&agrave;nh c&ocirc;ng của&nbsp;<a class=\"seokwl\" title=\"Đại hội Đảng\" href=\"https://www.baogiaothong.vn/tin-moi-nhat-dai-hoi-dang-lan-thu-xiii-channel467/\">Đại hội Đảng</a>&nbsp;lần thứ XIII đến c&aacute;c giới Ấn Độ, nhất l&agrave; khi Bộ trưởng B&ugrave;i Thanh Sơn tham gia Đối thoại Delhi lần thứ 12, l&agrave; Đối thoại quan trọng của khu vực Nam &Aacute; về c&aacute;c vấn đề ch&iacute;nh s&aacute;ch, b&ecirc;n cạnh Hội nghị đặc biệt c&aacute;c Bộ trưởng Ngoại giao ASEAN-Ấn Độ.</p>\r\n<p>T&ocirc;i tin rằng l&agrave; những đ&oacute;ng g&oacute;p của Việt Nam cho th&agrave;nh c&ocirc;ng của hội nghị lần n&agrave;y sẽ tiếp tục được ph&aacute;t huy, th&uacute;c đẩy trong thời gian tới. Những đề cập, những đề xuất của Việt Nam n&ecirc;u ra tại Hội nghị đặc biệt c&aacute;c Bộ trưởng Ngoại giao ASEAN-Ấn Độ sẽ th&agrave;nh trở th&agrave;nh t&agrave;i sản chung trong hợp t&aacute;c giữa Việt Nam v&agrave; Ấn Độ thời gian sau n&agrave;y.</p>', 218, 2, 36, 1, '2022-11-18 19:35:41', '2022-11-22 04:06:10'),
(27, 'Chi tiết Toyota Corolla Cross 2020 mới ra mắt tại triển lãm Bangkok Motor Show 2020, ngày về Việt Nam không xa', 'chi-tiet-toyota-corolla-cross-2020-moi-ra-mat-tai-trien-lam-bangkok-motor-show-2020-ngay-ve-viet-nam-khong-xa', 'Toyota Corolla Cross 2020 ra mắt tại Thái Lan với 2 phiên bản chính là động cơ xăng và hệ truyền động hybrid. Giá bán ra từ 723 triệu đồng đến 877 triệu đồng.', '<p>Sau gần 1 tuần được ra mắt tại Th&aacute;i Lan, mẫu xe SUV ho&agrave;n to&agrave;n mới mang t&ecirc;n gọi Corolla Cross đ&atilde; được h&atilde;ng Toyota đem đến&nbsp;<a href=\"https://tinxe.vn/trien-lam-bangkok-motor-show-2020-cd944\"><strong>triển l&atilde;m Bangkok Motor Show 2020</strong></a>&nbsp;giới thiệu với kh&aacute;ch tham quan. Kh&aacute; th&uacute; vị l&agrave; ngay từ b&acirc;y giờ, c&aacute;c đại l&yacute; Toyota trong nước cũng đ&atilde; cho mở cọc Toyota Corolla Cross.</p>\r\n<div class=\"width-100 inline\">\r\n<div id=\"ads-detail-below-sapo-320x180\" class=\"box-ads-anymind\"></div>\r\n</div>\r\n<p><em>Toyota Corolla Cross 2020 mới ra mắt tại triển l&atilde;m Bangkok Motor Show 2020, ng&agrave;y về Việt Nam kh&ocirc;ng xa</em></p>\r\n<p><a href=\"https://tinxe.vn/gia-xe-toyota-corolla-cross\"><strong>Toyota Corolla Cross</strong></a>&nbsp;2020 ra mắt tại Th&aacute;i Lan với 2 phi&ecirc;n bản động cơ ch&iacute;nh l&agrave; xăng v&agrave; hệ truyền động hybrid. Gi&aacute; b&aacute;n ra cho Toyota Corolla Cross 2020 tại Th&aacute;i Lan từ 989.000 Baht đến 1.199.000 Baht, tương đương 723 triệu đồng đến 877 triệu đồng. Ri&ecirc;ng phi&ecirc;n bản Toyota Corolla Cross 2020 xăng được cho c&oacute; gi&aacute; b&aacute;n ưu đ&atilde;i chỉ 701 triệu đồng đến hết th&aacute;ng 9 nhưng số lượng giới hạn.</p>\r\n<p>Toyota Corolla Cross 2020 được mang đến giới thiệu tại triển l&atilde;m Bangkok Motor Show 2020 với c&aacute;c m&agrave;u sơn ch&iacute;nh như xanh, x&aacute;m, bạc, trắng v&agrave; đỏ. Ở 2 phi&ecirc;n bản Toyota Corolla Cross 2020 tại Th&aacute;i Lan c&oacute; th&ecirc;m 2 biến thể l&agrave; Sport Urban v&agrave; Adventure với những thay đổi ở m&aacute;i nh&agrave; của xe cũng như phần đu&ocirc;i xe thiết kế kh&aacute;c biệt.</p>\r\n<div class=\"dvg_photo_box\">\r\n<figure><span id=\"imageGalleryId-4\" class=\"lozadload-content imageGalleryId\" data-src=\"https://img.tinxe.vn/2020/07/18/95ywCUPr/tinxe-6-0ddd.jpg\" data-desc=\"Những chiếc Toyota Corolla Cross 2020 với 3 biến thể\"><img class=\"lozad\" title=\"Những chiếc Toyota Corolla Cross 2020 với 3 biến thể\" src=\"https://img.tinxe.vn/resize/1000x-/2020/07/18/95ywCUPr/tinxe-6-0ddd.jpg\" alt=\"Những chiếc Toyota Corolla Cross 2020 với 3 biến thể\" width=\"800\" height=\"1431\" data-height=\"1431\" data-width=\"800\" data-src=\"https://img.tinxe.vn/resize/1000x-/2020/07/18/95ywCUPr/tinxe-6-0ddd.jpg\" data-loaded=\"true\"></span></figure>\r\n<p><em>Những chiếc Toyota Corolla Cross 2020 với 3 biến thể</em></p>\r\n<div class=\"width-100 inline\">\r\n<div id=\"ads-detail-below-320x250\" class=\"box-ads-anymind\"></div>\r\n</div>\r\n</div>\r\n<div class=\"dvg_photo_box\">\r\n<figure><span id=\"imageGalleryId-5\" class=\"lozadload-content imageGalleryId\" data-src=\"https://img.tinxe.vn/2020/07/18/95ywCUPr/tinxe-13-b818.jpg\" data-desc=\"Phần đu&ocirc;i xe c&oacute; thiết kế kh&aacute;c biệt nhau\"><img class=\"lozad\" title=\"Phần đu&ocirc;i xe c&oacute; thiết kế kh&aacute;c biệt nhau\" src=\"https://img.tinxe.vn/resize/1000x-/2020/07/18/95ywCUPr/tinxe-13-b818.jpg\" alt=\"Phần đu&ocirc;i xe c&oacute; thiết kế kh&aacute;c biệt nhau\" width=\"800\" height=\"1572\" data-height=\"1572\" data-width=\"800\" data-src=\"https://img.tinxe.vn/resize/1000x-/2020/07/18/95ywCUPr/tinxe-13-b818.jpg\" data-loaded=\"true\"></span></figure>\r\n<p><em>Phần đu&ocirc;i xe c&oacute; thiết kế kh&aacute;c biệt nhau</em></p>\r\n</div>\r\n<p>Dễ d&agrave;ng nhận thấy, Toyota Corolla Cross 2020 phi&ecirc;n bản Adventure c&oacute; cản va sau thiết kế kh&aacute;c biệt so với bản ti&ecirc;u chuẩn, 2 đ&egrave;n hậu được nối với nhau bằng một thanh nẹp mạ cr&ocirc;m b&oacute;ng lo&aacute;ng. C&ograve;n với Toyota Corolla Cross 2020 phi&ecirc;n bản Sport Urban chất hơn với cản va sau thể thao sơn m&agrave;u đen.</p>\r\n<div class=\"dvg_photo_box\">\r\n<figure><span id=\"imageGalleryId-6\" class=\"lozadload-content imageGalleryId\" data-src=\"https://img.tinxe.vn/2020/07/18/95ywCUPr/tinxe-12-3663.jpg\" data-desc=\"Toyota Corolla Cross 2020 động cơ xăng\"><img class=\"lozad\" title=\"Toyota Corolla Cross 2020 động cơ xăng\" src=\"https://img.tinxe.vn/resize/1000x-/2020/07/18/95ywCUPr/tinxe-12-3663.jpg\" alt=\"Toyota Corolla Cross 2020 động cơ xăng\" width=\"800\" height=\"476\" data-height=\"476\" data-width=\"800\" data-src=\"https://img.tinxe.vn/resize/1000x-/2020/07/18/95ywCUPr/tinxe-12-3663.jpg\" data-loaded=\"true\"></span></figure>\r\n<p><em>Toyota Corolla Cross 2020 động cơ xăng</em></p>\r\n</div>\r\n<p>Toyota Corolla Cross 2020 sử dụng động cơ xăng 4 xi-lanh, h&uacute;t kh&iacute; tự nhi&ecirc;n, dung t&iacute;ch 1.8 l&iacute;t, sản sinh c&ocirc;ng suất tối đa 140 m&atilde; lực tại v&ograve;ng tua m&aacute;y 6.000 v&ograve;ng/ph&uacute;t v&agrave; m&ocirc;-men xoắn cực đại 177 Nm tại v&ograve;ng tua m&aacute;y 4.000 v&ograve;ng/ph&uacute;t. Động cơ kết hợp với hộp số biến thi&ecirc;n v&ocirc; cấp Super CVT-i c&ugrave;ng việc hỗ trợ xăng E85.</p>\r\n<div class=\"dvg_photo_box\">\r\n<figure><span id=\"imageGalleryId-7\" class=\"lozadload-content imageGalleryId\" data-src=\"https://img.tinxe.vn/2020/07/18/95ywCUPr/dong-co-1a65.jpg\" data-desc=\"C&ograve;n đ&acirc;y l&agrave; Toyota Corolla Cross 2020 sử dụng hệ truyền động Hybrid\"><img class=\"lozad\" title=\"C&ograve;n đ&acirc;y l&agrave; Toyota Corolla Cross 2020 sử dụng hệ truyền động Hybrid\" src=\"https://img.tinxe.vn/resize/1000x-/2020/07/18/95ywCUPr/dong-co-1a65.jpg\" alt=\"C&ograve;n đ&acirc;y l&agrave; Toyota Corolla Cross 2020 sử dụng hệ truyền động Hybrid\" width=\"800\" height=\"427\" data-height=\"427\" data-width=\"800\" data-src=\"https://img.tinxe.vn/resize/1000x-/2020/07/18/95ywCUPr/dong-co-1a65.jpg\" data-loaded=\"true\"></span></figure>\r\n<p><em>C&ograve;n đ&acirc;y l&agrave; Toyota Corolla Cross 2020 sử dụng hệ truyền động Hybrid</em></p>\r\n</div>\r\n<p>Toyota Corolla Cross 2020 sử dụng hệ truyền động Hybrid bao gồm động cơ 4 xi-lanh, h&uacute;t kh&iacute; tự nhi&ecirc;n, dung t&iacute;ch 1.8 l&iacute;t c&oacute; c&ocirc;ng suất tối đa 98 m&atilde; lực tại tua m&aacute;y 5.200 v&ograve;ng/ph&uacute;t v&agrave; m&ocirc;-men xoắn cực đại 142 Nm tại tua m&aacute;y 3.600 v&ograve;ng/ph&uacute;t. Động cơ n&agrave;y đồng h&agrave;nh với hộp số e-CVT v&agrave; m&ocirc;-tơ điện c&oacute; c&ocirc;ng suất 72 m&atilde; lực c&ugrave;ng m&ocirc;-men xoắn cực đại 163 Nm. Tổng cộng, hệ truyền động hybrid n&agrave;y tạo ra c&ocirc;ng suất 122 m&atilde; lực cho mẫu xe Toyota Corolla Cross 2020.</p>\r\n<div class=\"dvg_photo_box\">\r\n<figure><span id=\"imageGalleryId-8\" class=\"lozadload-content imageGalleryId\" data-src=\"https://img.tinxe.vn/2020/07/18/95ywCUPr/tinxe-7-5d57.jpg\" data-desc=\"Diện mạo của Toyota Corolla Cross \"><img class=\"lozad\" title=\"Diện mạo của Toyota Corolla Cross \" src=\"https://img.tinxe.vn/resize/1000x-/2020/07/18/95ywCUPr/tinxe-7-5d57.jpg\" alt=\"Diện mạo của Toyota Corolla Cross \" width=\"1434\" height=\"956\" data-height=\"956\" data-width=\"1434\" data-src=\"https://img.tinxe.vn/resize/1000x-/2020/07/18/95ywCUPr/tinxe-7-5d57.jpg\" data-loaded=\"true\"></span></figure>\r\n<p><em>Diện mạo của Toyota Corolla Cross</em></p>\r\n</div>\r\n<div class=\"dvg_photo_box\">\r\n<figure><span id=\"imageGalleryId-9\" class=\"lozadload-content imageGalleryId\" data-src=\"https://img.tinxe.vn/2020/07/18/95ywCUPr/tinxe-14-0257.jpg\" data-desc=\"Đ&egrave;n pha của Toyota Corolla Cross \"><img class=\"lozad\" title=\"Đ&egrave;n pha của Toyota Corolla Cross \" src=\"https://img.tinxe.vn/resize/1000x-/2020/07/18/95ywCUPr/tinxe-14-0257.jpg\" alt=\"Đ&egrave;n pha của Toyota Corolla Cross \" width=\"800\" height=\"450\" data-height=\"450\" data-width=\"800\" data-src=\"https://img.tinxe.vn/resize/1000x-/2020/07/18/95ywCUPr/tinxe-14-0257.jpg\" data-loaded=\"true\"></span></figure>\r\n<p><em>Đ&egrave;n pha của Toyota Corolla Cross</em></p>\r\n</div>\r\n<div class=\"dvg_photo_box\">\r\n<figure><span id=\"imageGalleryId-10\" class=\"lozadload-content imageGalleryId\" data-src=\"https://img.tinxe.vn/2020/07/18/95ywCUPr/tinxe-4-6f9d.jpg\" data-desc=\"Lưới tản nhiệt tr&ecirc;n Toyota Corolla Cross \"><img class=\"lozad\" title=\"Lưới tản nhiệt tr&ecirc;n Toyota Corolla Cross \" src=\"https://img.tinxe.vn/resize/1000x-/2020/07/18/95ywCUPr/tinxe-4-6f9d.jpg\" alt=\"Lưới tản nhiệt tr&ecirc;n Toyota Corolla Cross \" width=\"800\" height=\"534\" data-height=\"534\" data-width=\"800\" data-src=\"https://img.tinxe.vn/resize/1000x-/2020/07/18/95ywCUPr/tinxe-4-6f9d.jpg\" data-loaded=\"true\"></span></figure>\r\n<p><em>Lưới tản nhiệt tr&ecirc;n Toyota Corolla Cross</em></p>\r\n</div>\r\n<p>Ở thiết kế, Toyota Corolla Cross được ph&aacute;t triển dựa tr&ecirc;n cơ sở gầm bệ của Corolla. Ngoại h&igrave;nh xe g&acirc;y ấn tượng với kiểu d&aacute;ng kết hợp đường cong v&agrave; đường g&acirc;n cơ bắp. Xe sử dụng đ&egrave;n LED thiết kế đẹp mắt, dải đ&egrave;n LED v&agrave; đ&egrave;n sương m&ugrave; đều c&oacute; tr&ecirc;n Toyota Corolla Cross.</p>\r\n<div class=\"dvg_photo_box\">\r\n<figure><span id=\"imageGalleryId-11\" class=\"lozadload-content imageGalleryId\" data-src=\"https://img.tinxe.vn/2020/07/18/95ywCUPr/tinxe-2-8ffa.jpg\" data-desc=\"Nội thất của Toyota Corolla Cross \"><img class=\"lozad\" title=\"Nội thất của Toyota Corolla Cross \" src=\"https://img.tinxe.vn/resize/1000x-/2020/07/18/95ywCUPr/tinxe-2-8ffa.jpg\" alt=\"Nội thất của Toyota Corolla Cross \" width=\"800\" height=\"534\" data-height=\"534\" data-width=\"800\" data-src=\"https://img.tinxe.vn/resize/1000x-/2020/07/18/95ywCUPr/tinxe-2-8ffa.jpg\" data-loaded=\"true\"></span></figure>\r\n<p><em>Nội thất của Toyota Corolla Cross</em></p>\r\n</div>\r\n<p>B&ecirc;n trong khoang l&aacute;i, Toyota Corolla Cross c&oacute; m&agrave;n h&igrave;nh cảm ứng 9 inch, kết nối Apple CarPlay v&agrave; dịch vụ T-Connect của Toyota. Hệ thống &acirc;m thanh 6 loa.</p>\r\n<p>C&aacute;c tiện nghi tr&ecirc;n xe gồm ghế l&aacute;i chỉnh điện 8 hướng, m&agrave;n h&igrave;nh MID 7 inch sau v&ocirc;-lăng, điều h&ograve;a tự động hai v&ugrave;ng, bệ tỳ tay ở h&agrave;ng ghế sau c&oacute; hộc để cốc v&agrave; cổng USB, cửa gi&oacute; cho h&agrave;ng ghế sau, tựa lưng h&agrave;ng ghế sau chỉnh g&oacute;c 6 độ. Theo h&atilde;ng Toyota, mẫu SUV mới&nbsp;n&agrave;y cung cấp kh&ocirc;ng gian rộng r&atilde;i cho 5 người ngồi v&agrave; c&oacute; thể t&iacute;ch khoang h&agrave;nh l&yacute; 487 l&iacute;t.</p>\r\n<div class=\"dvg_photo_box\">\r\n<figure><span id=\"imageGalleryId-12\" class=\"lozadload-content imageGalleryId\" data-src=\"https://img.tinxe.vn/2020/07/18/95ywCUPr/tinxe-1-a770.jpg\" data-desc=\"Cốp xe của Toyota Corolla Cross \"><img class=\"lozad playing\" title=\"Cốp xe của Toyota Corolla Cross \" src=\"https://img.tinxe.vn/resize/1000x-/2020/07/18/95ywCUPr/tinxe-1-a770.jpg\" alt=\"Cốp xe của Toyota Corolla Cross \" width=\"1000\" height=\"668\" data-height=\"668\" data-width=\"1000\" data-src=\"https://img.tinxe.vn/resize/1000x-/2020/07/18/95ywCUPr/tinxe-1-a770.jpg\" data-loaded=\"true\"></span></figure>\r\n<p><em>Cốp xe của Toyota Corolla Cross</em></p>\r\n</div>\r\n<p>Toyota Corolla Cross c&oacute; c&aacute;c trang bị c&ocirc;ng nghệ an to&agrave;n như cảnh b&aacute;o va chạm ph&iacute;a trước, cảnh b&aacute;o chệch l&agrave;n, đ&egrave;n pha tự động, gương chiếu hậu tự động chỉnh g&oacute;c thấp khi l&ugrave;i, camera 360 độ, 7 t&uacute;i kh&iacute;.</p>', 218, 13, 39, 1, '2022-11-18 19:35:41', '2023-02-07 08:30:31'),
(31, 'Hơn 100 công ty châu Âu vẫn ở lại Nga', 'hon-100-cong-ty-chau-au-van-o-lai-nga', 'Sau gần 4 tháng diễn ra khủng hoảng Ukraine, hơn 100 công ty từ Liên minh châu Âu (EU) vẫn hoạt động tại Nga, bất chấp các lệnh trừng phạt.', '<p class=\"Normal\">Nghi&ecirc;n cứu của Viện L&atilde;nh đạo thuộc Đại học Yale cho thấy 247 c&ocirc;ng ty đa quốc gia vẫn đang hoạt động tại Nga. Trong đ&oacute;, 116 doanh nghiệp l&agrave; đến từ EU, bất chấp việc sức &eacute;p trừng phạt l&ecirc;n ch&iacute;nh quyền của Tổng thống Nga Vladimir Putin ng&agrave;y c&agrave;ng lớn.</p>\r\n<p class=\"Normal\">C&aacute;c c&ocirc;ng ty n&agrave;y thuộc nhiều lĩnh vực, từ thời trang (Gorgio Armani, Lacoste,...) đến thực phẩm (Nestle, Ritter Sport,...) v&agrave; ng&acirc;n h&agrave;ng (HSBC, ING Bank,...). Một b&aacute;o c&aacute;o th&aacute;ng trước của tổ chức n&agrave;y th&igrave; cho thấy với phần lớn c&ocirc;ng ty rời Nga, lợi nhuận từ thị trường chứng kho&aacute;n tăng điểm đ&atilde; đủ b&ugrave; chi ph&iacute; từ việc bỏ lại thị trường n&agrave;y.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture data-inimage=\"done\"><source srcset=\"https://i1-kinhdoanh.vnecdn.net/2022/06/20/russia-5-4238-1655716674.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=DANQGgZn3RRBT7VQ0tyRQA 1x, https://i1-kinhdoanh.vnecdn.net/2022/06/20/russia-5-4238-1655716674.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=0AFkYHCbVm7Ua7c9A2RPzw 1.5x, https://i1-kinhdoanh.vnecdn.net/2022/06/20/russia-5-4238-1655716674.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=gc8MMTvYccZWDmzoX9Q14A 2x\" data-srcset=\"https://i1-kinhdoanh.vnecdn.net/2022/06/20/russia-5-4238-1655716674.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=DANQGgZn3RRBT7VQ0tyRQA 1x, https://i1-kinhdoanh.vnecdn.net/2022/06/20/russia-5-4238-1655716674.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=0AFkYHCbVm7Ua7c9A2RPzw 1.5x, https://i1-kinhdoanh.vnecdn.net/2022/06/20/russia-5-4238-1655716674.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=gc8MMTvYccZWDmzoX9Q14A 2x\"><img class=\"lazy lazied\" src=\"https://i1-kinhdoanh.vnecdn.net/2022/06/20/russia-5-4238-1655716674.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=DANQGgZn3RRBT7VQ0tyRQA\" alt=\"Trung t&acirc;m Kinh doanh Quốc tế tại Moscow (Nga). Ảnh: Bloomberg\" loading=\"lazy\" data-src=\"https://i1-kinhdoanh.vnecdn.net/2022/06/20/russia-5-4238-1655716674.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=DANQGgZn3RRBT7VQ0tyRQA\" data-ll-status=\"loaded\"></picture>\r\n<div class=\"embed-container-ads\">&nbsp;</div>\r\n</div>\r\n<figcaption>\r\n<p class=\"Image\">Trung t&acirc;m Kinh doanh Quốc tế tại Moscow (Nga). Ảnh:&nbsp;<em>Bloomberg</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Sau khi Nga mở chiến dịch qu&acirc;n sự tại Ukraine cuối th&aacute;ng 2, nước n&agrave;y đ&atilde; bị phương T&acirc;y &aacute;p đặt c&aacute;c lệnh trừng phạt chưa từng c&oacute;. Việc n&agrave;y khiến nhiều doanh nghiệp phải thu hẹp hoạt động hoặc r&uacute;t ho&agrave;n to&agrave;n khỏi Nga v&igrave; sợ vi phạm lệnh trừng phạt. Đến nay, nghi&ecirc;n cứu của Đại học Yale cho biết đ&atilde; c&oacute; khoảng 1.000 c&ocirc;ng ty c&ocirc;ng bố kế hoạch giảm quy m&ocirc; hoạt động tại Nga.</p>\r\n<p class=\"Normal\">Tuần trước, b&aacute;o c&aacute;o của một tổ chức tư vấn tại Nga cho biết hơn một nửa trong số 5.000 c&ocirc;ng ty nước ngo&agrave;i đang hoạt động ở đ&acirc;y khẳng định kh&ocirc;ng giới hạn hoạt động v&agrave; sẽ tiếp tục tuyển dụng 2 triệu lao động. Chỉ khoảng 90 c&ocirc;ng ty, chủ yếu hoạt động trong lĩnh vực tư vấn v&agrave; c&ocirc;ng nghệ, đ&atilde; b&aacute;n mảng kinh doanh v&agrave; chuyển nh&acirc;n vi&ecirc;n sang nước kh&aacute;c.</p>', 218, 4, 40, 1, '2022-11-18 19:35:41', '2022-11-22 05:33:35'),
(33, 'Tàu cá bị cháy nghi do chập điện tại bình ắc quy, thiệt hại hàng trăm triệu', 'tau-ca-bi-chay-nghi-do-chap-dien-tai-binh-ac-quy-thiet-hai-hang-tram-trieu', 'Theo Cảnh sát PCCC Quảng Trị, nguyên nhân xác định ban đầu tàu cá bị cháy rụi ca bin ở Cửa Việt do chập điện gây cháy tại bình ắc quy.', '<p>Chiều 15/6, UBND thị trấn Cửa Việt, huyện Gio Linh, tỉnh Quảng Trị cho biết, tr&ecirc;n địa b&agrave;n vừa xảy ra vụ hỏa ho&agrave;n khiến cabin của t&agrave;u c&aacute; mang số hiệu QT.19270.TS bị ch&aacute;y rụi, thiệt hại ước t&iacute;nh h&agrave;ng trăm triệu đồng.</p>\r\n<p>Vụ ch&aacute;y t&agrave;u c&aacute; xảy ra tại địa b&agrave;n khu phố 3, thị trấn Cửa Việt.</p>\r\n<div>\r\n<p class=\"image\"><img class=\"width100 initial loaded\" src=\"https://cdn.baogiaothong.vn/upload/images/2022-2/article_img/2022-06-15/img-bgt-2021-tau-ca-bi-chay-nghi-do-chap-dien-tai-binh-ac-quy-thiet-hai-hang-tram-trieu-1-1655292695-width1280height720.jpg\" alt=\"t&agrave;u c&aacute; bị ch&aacute;y nghi do chập điện tại b&igrave;nh ắc quy, thiệt hại h&agrave;ng trăm triệu\" data-original=\"https://cdn.baogiaothong.vn/upload/images/2022-2/article_img/2022-06-15/img-bgt-2021-tau-ca-bi-chay-nghi-do-chap-dien-tai-binh-ac-quy-thiet-hai-hang-tram-trieu-1-1655292695-width1280height720.jpg\" data-was-processed=\"true\"></p>\r\n<p class=\"img_chu_thich\">Vụ ch&aacute;y khiến c&aacute;c thiết bị tr&ecirc;n cabin t&agrave;u c&aacute; bị hư hỏng nghi&ecirc;m trọng. Ảnh: Ph&ograve;ng Cảnh s&aacute;t PCCC v&agrave; CNCH C&ocirc;ng an tỉnh Quảng Trị</p>\r\n</div>\r\n<p>Người d&acirc;n khi ph&aacute;t hiện sự việc lập tức sử dụng c&aacute;c phương tiện tại chỗ để chữa ch&aacute;y, đồng thời điện b&aacute;o sự việc đến Ph&ograve;ng Cảnh s&aacute;t PCCC v&agrave; Cứu nạn cứu hộ C&ocirc;ng an tỉnh Quảng Trị.</p>\r\n<p>Ngay sau khi tiếp nhận th&ocirc;ng tin, Ph&ograve;ng Cảnh s&aacute;t PCCC v&agrave; Cứu nạn cứu hộ đ&atilde; điều động lực lượng c&ugrave;ng phương tiện khẩn trương c&oacute; mặt tại hiện trường vụ ch&aacute;y.</p>\r\n<div>\r\n<p class=\"image\"><img class=\"width100 loaded\" src=\"https://cdn.baogiaothong.vn/upload/images/2022-2/article_img/2022-06-15/img-bgt-2021-tau-ca-bi-chay-nghi-do-chap-dien-tai-binh-ac-quy-thiet-hai-hang-tram-trieu-2-1655292757-width1280height720.jpg\" alt=\"t&agrave;u c&aacute; bị ch&aacute;y nghi do chập điện tại b&igrave;nh ắc quy, thiệt hại h&agrave;ng trăm triệu\" data-original=\"https://cdn.baogiaothong.vn/upload/images/2022-2/article_img/2022-06-15/img-bgt-2021-tau-ca-bi-chay-nghi-do-chap-dien-tai-binh-ac-quy-thiet-hai-hang-tram-trieu-2-1655292757-width1280height720.jpg\" data-was-processed=\"true\"></p>\r\n<p class=\"img_chu_thich\">C&aacute;c thiết bị tr&ecirc;n cabin t&agrave;u c&aacute; bị ch&aacute;y. Ảnh: Ph&ograve;ng Cảnh s&aacute;t PCCC v&agrave; CNCH C&ocirc;ng an tỉnh Quảng Trị</p>\r\n<section class=\"bnrBt txtCent\"></section>\r\n</div>\r\n<p>Tại hiện trường, đ&aacute;m ch&aacute;y đ&atilde; được người d&acirc;n sử dụng phương tiện tại chỗ dập tắt, to&agrave;n bộ cabin của t&agrave;u c&aacute; mang số hiệu QT.19270.TS đ&atilde; bị ch&aacute;y ho&agrave;n to&agrave;n.</p>\r\n<p>Nguy&ecirc;n nh&acirc;n x&aacute;c định ban đầu do chập điện g&acirc;y ch&aacute;y tại b&igrave;nh ắc quy.</p>\r\n<p>Theo l&atilde;nh đạo UBND thị trấn Cửa Việt, t&agrave;u c&aacute; tr&ecirc;n của ngư d&acirc;n tại địa phương, trước đ&oacute; đ&atilde; l&ecirc;n đ&agrave; sửa chữa nhưng s&aacute;ng 16/5 kh&ocirc;ng may xảy ra ch&aacute;y, thiệt hại ước t&iacute;nh ban đầu khoảng 800 triệu đến cả tỷ đồng.</p>\r\n<div>\r\n<p class=\"image\"><img class=\"width100 loaded\" src=\"https://cdn.baogiaothong.vn/upload/images/2022-2/article_img/2022-06-15/img-bgt-2021-tau-ca-bi-chay-nghi-do-chap-dien-tai-binh-ac-quy-thiet-hai-hang-tram-trieu-4-1655292792-width1280height720.jpg\" alt=\"t&agrave;u c&aacute; bị ch&aacute;y nghi do chập điện tại b&igrave;nh ắc quy, thiệt hại h&agrave;ng trăm triệu\" data-original=\"https://cdn.baogiaothong.vn/upload/images/2022-2/article_img/2022-06-15/img-bgt-2021-tau-ca-bi-chay-nghi-do-chap-dien-tai-binh-ac-quy-thiet-hai-hang-tram-trieu-4-1655292792-width1280height720.jpg\" data-was-processed=\"true\"></p>\r\n<p class=\"img_chu_thich\">Nhiều thiết bị tr&ecirc;n cabin t&agrave;u c&aacute; bị ch&aacute;y... \"th&agrave;nh tro\". Ảnh: Ph&ograve;ng Cảnh s&aacute;t PCCC v&agrave; CNCH C&ocirc;ng an tỉnh Quảng Trị</p>\r\n<div>\r\n<p class=\"image\"><img class=\"width100 loaded\" src=\"https://cdn.baogiaothong.vn/upload/images/2022-2/article_img/2022-06-15/img-bgt-2021-tau-ca-bi-chay-nghi-do-chap-dien-tai-binh-ac-quy-thiet-hai-hang-tram-trieu-3-1655292850-width1280height720.jpg\" alt=\"t&agrave;u c&aacute; bị ch&aacute;y nghi do chập điện tại b&igrave;nh ắc quy, thiệt hại h&agrave;ng trăm triệu\" data-original=\"https://cdn.baogiaothong.vn/upload/images/2022-2/article_img/2022-06-15/img-bgt-2021-tau-ca-bi-chay-nghi-do-chap-dien-tai-binh-ac-quy-thiet-hai-hang-tram-trieu-3-1655292850-width1280height720.jpg\" data-was-processed=\"true\"></p>\r\n<p class=\"img_chu_thich\">Khu vực b&aacute;nh l&aacute;i cũng bị ch&aacute;y... Ảnh: Ph&ograve;ng Cảnh s&aacute;t PCCC v&agrave; CNCH C&ocirc;ng an tỉnh Quảng Trị</p>\r\n</div>\r\n</div>', 218, 12, 47, 1, '2022-11-18 19:35:41', '2023-02-07 08:34:45'),
(34, 'HomeResort: Sự hợp nhất giữa BĐS nhà ở và BĐS du lịch – nghỉ dưỡng', 'homeresort-su-hop-nhat-giua-bds-nha-o-va-bds-du-lich-nghi-duong', 'HomeResort: Sự hợp nhất giữa BĐS nhà ở và BĐS du lịch – nghỉ dưỡng', '<p>Theo từng đặc t&iacute;nh, BĐS tại Việt Nam được xếp v&agrave;o 4 nh&oacute;m ch&iacute;nh, bao gồm BĐS nh&agrave; ở, BĐS thương mại dịch vụ, BĐS du lịch nghỉ dưỡng v&agrave; BĐS c&ocirc;ng nghiệp.</p>\r\n<p>Trong đ&oacute;, BĐS nh&agrave; ở được hiểu l&agrave; loại h&igrave;nh nh&agrave; đất được cấp quyền sở hữu l&acirc;u d&agrave;i phục vụ cho nhu cầu an cư, lưu tr&uacute; l&acirc;u d&agrave;i của cư d&acirc;n. BĐS c&ocirc;ng nghiệp gắn liền với c&aacute;c h&igrave;nh thức nh&agrave; xưởng sản xuất. BĐS thương mại dịch vụ bao gồm trung t&acirc;m thương mại, văn ph&ograve;ng, dịch vụ y tế, gi&aacute;o dục, kh&aacute;ch sạn,&hellip; mang t&iacute;nh chất kinh doanh.</p>\r\n<p align=\"center\"><img class=\"news-image initial loaded\" src=\"https://icdn.24h.com.vn/upload/2-2022/images/2022-06-17/HomeResort-Su-hop-nhat-giua-BdS-nha-o-va-BdS-du-lich--nghi-duong-11-1655453985-211-width660height428.jpg\" alt=\"HomeResort: Sự hợp nhất giữa BĐS nh&agrave; ở v&agrave; BĐS du lịch &ndash; nghỉ dưỡng - 1\" data-was-processed=\"true\"></p>\r\n<p class=\"img_chu_thich_0407\">Đ&ocirc; thị biển l&agrave; nh&agrave; ở phục vụ nhu cầu an cư l&acirc;u d&agrave;i đi k&egrave;m t&iacute;nh năng khai th&aacute;c kinh doanh du lịch</p>\r\n<p>BĐS du lịch nghỉ dưỡng được xem l&agrave; một phần của BĐS thương mại dịch vụ. Theo tổ chức du lịch thế giới, du lịch bao gồm tất cả mọi hoạt động của những người du h&agrave;nh, tạm tr&uacute;, trong mục đ&iacute;ch tham quan, kh&aacute;m ph&aacute; v&agrave; t&igrave;m hiểu, trải nghiệm hoặc trong mục đ&iacute;ch nghỉ ngơi, giải tr&iacute;, thư gi&atilde;n. Do đ&oacute;, bất động sản du lịch nghỉ dưỡng l&agrave; c&aacute;c c&ocirc;ng tr&igrave;nh phục vụ cho chuyến du lịch của con người, lưu tr&uacute; ngắn hạn. V&igrave; nằm trong BĐS thương mại, dịch vụ, n&ecirc;n đại đa số c&aacute;c BĐS thuần cho du lịch nghỉ dưỡng tại Việt Nam giới hạn thời gian sở hữu.</p>\r\n<p>Từ đ&acirc;y đ&ocirc; thị biển ra đời l&agrave; sự kết hợp giữa bất động sản nh&agrave; ở nhưng c&oacute; t&iacute;nh năng như một bất động sản du lịch nghỉ dưỡng. Việc hợp nhất giữa hai nh&oacute;m bất động sản gi&uacute;p đ&ocirc; thị biển vừa c&oacute; ưu thế lớn nhất của BĐS nh&agrave; ở l&agrave; đặc quyền sở hữu l&acirc;u d&agrave;i, đồng thời được thừa hưởng khả năng khai th&aacute;c cho thu&ecirc;/ kinh doanh c&aacute;c dịch vụ du lịch của BĐS du lịch.</p>\r\n<p><strong>HomeResort &ndash; Phi&ecirc;n bản n&acirc;ng cấp tối cao của đ&ocirc; thị biển</strong></p>\r\n<p>Kh&ocirc;ng dừng lại ở một đ&ocirc; thị biển th&ocirc;ng thường, HomeResort &ndash; xu hướng mới được nh&agrave; ph&aacute;t triển Hưng Vương Developer n&acirc;ng cấp th&ecirc;m nhiều gi&aacute; trị tối ưu.</p>\r\n<p align=\"center\"><img class=\"news-image loaded\" src=\"https://icdn.24h.com.vn/upload/2-2022/images/2022-06-17/HomeResort-Su-hop-nhat-giua-BdS-nha-o-va-BdS-du-lich--nghi-duong-22-1655453985-740-width660height441.jpg\" alt=\"HomeResort: Sự hợp nhất giữa BĐS nh&agrave; ở v&agrave; BĐS du lịch &ndash; nghỉ dưỡng - 2\" data-original=\"https://icdn.24h.com.vn/upload/2-2022/images/2022-06-17/HomeResort-Su-hop-nhat-giua-BdS-nha-o-va-BdS-du-lich--nghi-duong-22-1655453985-740-width660height441.jpg\" data-was-processed=\"true\"></p>\r\n<p>Điều n&agrave;y được thể hiện ở 10 h&igrave;nh th&aacute;i wellness luxury bao gồm 6 gi&aacute; trị wellness living kết hợp với địa thế thủy bao duy nhất tr&ecirc;n thị trường BĐS biển khi hội tụ 4 h&igrave;nh th&aacute;i mặt nước 1km biển &ndash; 1,1 km s&ocirc;ng, 4km k&ecirc;nh hồ. Đồng thời c&oacute; sự g&oacute;p mặt bởi cả hai thương hiệu số 1 to&agrave;n cầu l&agrave; Marriott International (7000 chi nh&aacute;nh tại 132 quốc gia) v&agrave; Best Western Premier (hơn 4000 chi nh&aacute;nh tại hơn 100 quốc gia) quản l&yacute; vận h&agrave;nh.</p>\r\n<p><strong>Chuỗi đầu tư đa năng</strong></p>\r\n<p>Lẽ đương nhi&ecirc;n việc kết hợp giữa BĐS nh&agrave; ở v&agrave; du lịch cộng th&ecirc;m c&aacute;c t&iacute;nh năng n&acirc;ng cấp, HomeResort sẽ mở ra b&agrave;i to&aacute;n đầu tư đa năng, trong đ&oacute; tập trung ở ba gi&aacute; trị: an cư nghỉ dưỡng để tận hưởng cuộc sống hạnh ph&uacute;c &ndash; gia tăng gi&aacute; trị BĐS bền vững &ndash; khai th&aacute;c cho thu&ecirc; tối ưu.</p>\r\n<p>Trong đ&oacute;, b&agrave;i to&aacute;n gia tăng gi&aacute; trị được &ldquo;bảo h&agrave;nh&rdquo; an t&acirc;m trọn đời, miễn nhiễm với biến động thị trường nhờ t&acirc;m l&yacute; ăn chắc mặc bền của người Việt &ndash; ưu ti&ecirc;n quyền sở hữu l&acirc;u d&agrave;i. Trong hội thảo &ldquo;Xu thế sở hữu bất động sản: Kinh nghiệm quốc tế v&agrave; thực tiễn Việt Nam&rdquo;, theo Realtimes, khi khảo s&aacute;t mức gi&aacute; b&aacute;n v&agrave; tỷ lệ hấp thụ của một dự &aacute;n BĐS biển trong c&ugrave;ng một khu vực, chất lượng tương đương th&igrave; loại h&igrave;nh c&oacute; ph&aacute;p l&yacute; l&acirc;u d&agrave;i lu&ocirc;n cao hơn hữu hạn.</p>\r\n<p align=\"center\"><img class=\"news-image loaded\" src=\"https://icdn.24h.com.vn/upload/2-2022/images/2022-06-17/HomeResort-Su-hop-nhat-giua-BdS-nha-o-va-BdS-du-lich--nghi-duong-33-1655453985-761-width660height441.jpg\" alt=\"HomeResort: Sự hợp nhất giữa BĐS nh&agrave; ở v&agrave; BĐS du lịch &ndash; nghỉ dưỡng - 3\" data-original=\"https://icdn.24h.com.vn/upload/2-2022/images/2022-06-17/HomeResort-Su-hop-nhat-giua-BdS-nha-o-va-BdS-du-lich--nghi-duong-33-1655453985-761-width660height441.jpg\" data-was-processed=\"true\"></p>\r\n<p class=\"img_chu_thich_0407\">HomeResort mang đến quỹ đầu tư đa năng</p>\r\n<p>Tại thời kỳ h&agrave;ng loạt dự &aacute;n hạ tầng t&aacute;c động mạnh đến B&igrave;nh Ch&acirc;u &ndash; H&agrave;m T&acirc;n như đường ven biển Vũng T&agrave;u &ndash; B&igrave;nh Ch&acirc;u, cao tốc Bi&ecirc;n H&ograve;a &ndash; Vũng T&agrave;u đều khởi c&ocirc;ng năm 2023, s&acirc;n bay Long Th&agrave;nh ho&agrave;n thiện năm 2025 th&igrave; năm 2022 l&agrave; thời điểm th&iacute;ch hợp để đầu tư khi gi&aacute; BĐS tại đ&acirc;y c&ograve;n hợp l&yacute;. Theo chuy&ecirc;n gia Trần Kh&aacute;nh Quang, trong 2 &ndash; 3 năm tới, B&igrave;nh Ch&acirc;u &ndash; Hồ Tr&agrave;m dự kiến tăng gi&aacute; từ 50-100%. V&agrave; khi s&acirc;n bay Long Th&agrave;nh ho&agrave;n thiện, sẽ tạo ra đợt tăng gi&aacute; thứ 2.</p>\r\n<p>B&aacute;o c&aacute;o thịnh vượng 2022 của Knight Frank dự b&aacute;o số người si&ecirc;u gi&agrave;u tại Việt Nam sẽ tăng 26% mỗi năm từ nay đến năm 2026. Trong đ&oacute; với vai tr&ograve; l&agrave; điểm du lịch gần nhất của TP.HCM &ndash; trung t&acirc;m kinh tế số 1 cả nước khi chỉ c&aacute;ch 90 ph&uacute;t di chuyển, nhu cầu sở hữu t&agrave;i sản biển l&acirc;u d&agrave;i như Venezia Beach sẽ gia tăng mạnh tỷ lệ thuận với tốc độ tăng trưởng của giới gi&agrave;u v&agrave; si&ecirc;u gi&agrave;u Việt.</p>\r\n<p>Giữa bối cảnh nhu cầu sở hữu t&agrave;i sản biển l&acirc;u d&agrave;i ng&agrave;y c&agrave;ng cao trong khi nguồn cung sản phẩm n&agrave;y khan hiếm, những m&ocirc; h&igrave;nh đ&aacute;p ứng được ti&ecirc;u ch&iacute; như Venezia Beach dự b&aacute;o sẽ c&oacute; dư địa tăng gi&aacute; cao.</p>\r\n<p>Về b&agrave;i to&aacute;n cho thu&ecirc; lưu tr&uacute; ngắn hạn, kinh doanh c&aacute;c hoạt động du lịch, Venezia Beach được đ&aacute;nh gi&aacute; cao nhờ nằm tại vị tr&iacute; gi&aacute;p ranh của hai thị trường du lịch lớn nhất ph&iacute;a Nam khi điểm đầu tiếp gi&aacute;p B&igrave;nh Ch&acirc;u (BRVT) v&agrave; điểm cuối tiếp gi&aacute;p H&agrave;m T&acirc;n (B&igrave;nh Thuận), do đ&oacute; đ&ocirc; thị biển tiếp cận được lượng du kh&aacute;ch l&ecirc;n đến 22 triệu lượt.</p>\r\n<p align=\"center\"><img class=\"news-image loaded\" src=\"https://icdn.24h.com.vn/upload/2-2022/images/2022-06-17/HomeResort-Su-hop-nhat-giua-BdS-nha-o-va-BdS-du-lich--nghi-duong-44-1655453985-453-width660height339.jpg\" alt=\"HomeResort: Sự hợp nhất giữa BĐS nh&agrave; ở v&agrave; BĐS du lịch &ndash; nghỉ dưỡng - 4\" data-original=\"https://icdn.24h.com.vn/upload/2-2022/images/2022-06-17/HomeResort-Su-hop-nhat-giua-BdS-nha-o-va-BdS-du-lich--nghi-duong-44-1655453985-453-width660height339.jpg\" data-was-processed=\"true\"></p>\r\n<p class=\"img_chu_thich_0407\">16 cụm tiện &iacute;ch &ldquo;all-in-one&rdquo;, Venezia Beach sẵn s&agrave;ng trở th&agrave;nh &ldquo;hub&rdquo; du lịch khu vực</p>\r\n<p>16 cụm tiện &iacute;ch đẳng cấp quốc tế t&iacute;ch hợp h&agrave;ng trăm tiện &iacute;ch th&agrave;nh phần chạm đến mọi nhu cầu vui chơi &ndash; giải tr&iacute; &ndash; mua sắm &ndash; nghỉ dưỡng &ndash; thể thao biển &ndash; kh&aacute;m ph&aacute; &ndash; trung t&acirc;m hội nghị - bến du thuyền &ndash; wellness,&hellip; l&agrave; nền tảng để giữ ch&acirc;n, thu h&uacute;t du kh&aacute;ch. Đặc biệt trong bối cảnh B&igrave;nh Ch&acirc;u v&agrave; H&agrave;m T&acirc;n (d&agrave;i 22km đường bờ biển) đều chưa c&oacute; ph&ograve;ng lưu tr&uacute; v&agrave; d&ograve;ng BĐS biển 5 sao th&igrave; hệ tiện &iacute;ch &ldquo;all-in-one&rdquo; tr&ecirc;n quy m&ocirc; 72 ha như Venezia Beach nhanh ch&oacute;ng trở th&agrave;nh t&acirc;m điểm, &ldquo;hub&rdquo; du lịch to&agrave;n khu vực.</p>\r\n<p>Ngo&agrave;i thừa hưởng nguồn kh&aacute;ch quốc tế từ hai đơn vị vận h&agrave;nh danh tiếng, chiến lược hợp t&aacute;c c&ugrave;ng Crystal Bay &ndash; tập đo&agrave;n lữ h&agrave;nh khai th&aacute;c mảng quốc tế mở ra cho đ&ocirc; thị biển th&ecirc;m một k&ecirc;nh chủ động đ&oacute;n kh&aacute;ch hiệu quả. Crystal Bay đang thực hiện 1,800 chuyến bay với hơn nửa triệu lượt du kh&aacute;ch từ ch&acirc;u &Acirc;u, ch&acirc;u &Aacute; mỗi năm.</p>\r\n<p>Hiện nay, theo b&aacute;o c&aacute;o của Savill, Hồ Tr&agrave;m dẫn đầu về tỷ trọng kh&aacute;ch sạn v&agrave; khu nghỉ dưỡng hoạt động trở lại sau dịch. Tỷ suất lấp đầy lu&ocirc;n đạt tr&ecirc;n 80%, ri&ecirc;ng loại h&igrave;nh lưu tr&uacute; cao cấp đạt 100% c&ocirc;ng suất thu&ecirc; cuối tuần.</p>\r\n<p>Về mức gi&aacute; khai th&aacute;c ph&ograve;ng khảo s&aacute;t tr&ecirc;n c&aacute;c nền tảng booking online, một view biển diện t&iacute;ch 30-40 m2 tại Hồ Tr&agrave;m c&oacute; gi&aacute; 8-10 triệu/ đ&ecirc;m cho cuối tuần v&agrave; 3-4 triệu/ đ&ecirc;m cho ng&agrave;y thường. C&ograve;n gi&aacute; villa 3-4 ph&ograve;ng ngủ dao động 18 &ndash; 20 triệu ng&agrave;y thường, cao hơn gi&aacute; thu&ecirc; ở thị trường truyền thống từ 50-150%. Tr&ecirc;n c&ugrave;ng một cung đường biển, gi&aacute; thu&ecirc; ph&ograve;ng của Venezia Beach được đ&aacute;nh gi&aacute; sẽ tương đương với khu vực Hồ Tr&agrave;m. Đồng thời c&ocirc;ng suất cho thu&ecirc; dự b&aacute;o ở mức cao do nguồn cung 5 sao gần như vắng b&oacute;ng.</p>', 218, 14, 15, 1, '2022-11-18 19:35:41', '2023-02-07 08:25:32'),
(36, 'Cảnh sát vật lộn với “giặc lửa” khống chế vụ cháy lớn tại nhà máy Scavi Huế', 'canh-sat-vat-lon-voi-giac-lua-khong-che-vu-chay-lon-tai-nha-may-scavi-hue', 'Sau nhiều giờ vật lộn với “giặc lửa”, Cảnh sát PCCC và lực lượng chức năng đã khống chế vụ cháy lớn tại Công ty Scavi Huế trong KCN Phong Điền.', '<p>Vụ ch&aacute;y xảy ra v&agrave;o khoảng hơn 2h s&aacute;ng 13/6 ở một xưởng sản xuất của C&ocirc;ng ty Scavi Huế tại Khu c&ocirc;ng nghiệp Phong Điền (huyện Phong Điền, tỉnh Thừa Thi&ecirc;n Huế).</p>\r\n<p>Sau khi nhận tin b&aacute;o, lực lượng Cảnh s&aacute;t PCCC v&agrave; Cứu nạn cứu hộ C&ocirc;ng an tỉnh Thừa Thi&ecirc;n Huế c&ugrave;ng nhiều xe chữa ch&aacute;y chuy&ecirc;n dụng v&agrave; c&aacute;c cơ quan chức năng nhanh ch&oacute;ng c&oacute; mặt tại hiện trường triển khai c&ocirc;ng t&aacute;c dập lửa.</p>\r\n<div>\r\n<p class=\"image\"><img class=\"width100 initial loaded\" src=\"https://cdn.baogiaothong.vn/upload/images/2022-2/article_img/2022-06-13/img-bgt-2021-canh-sat-vat-lon-voi-giac-lua-khong-che-vu-chay-lon-tai-nha-may-scavi-hue-1-1655092394-width1280height720.jpg\" alt=\"cảnh s&aacute;t vật lộn với &ldquo;giặc lửa&rdquo; khống chế vụ ch&aacute;y lớn tại nh&agrave; m&aacute;y scavi huế\" data-original=\"https://cdn.baogiaothong.vn/upload/images/2022-2/article_img/2022-06-13/img-bgt-2021-canh-sat-vat-lon-voi-giac-lua-khong-che-vu-chay-lon-tai-nha-may-scavi-hue-1-1655092394-width1280height720.jpg\" data-was-processed=\"true\"></p>\r\n<p class=\"img_chu_thich\">Đ&aacute;m ch&aacute;y lớn lan nhanh khiến c&ocirc;ng t&aacute;c dập lửa gặp rất nhiều kh&oacute; khăn. Ảnh: T.H</p>\r\n</div>\r\n<p>Tuy nhi&ecirc;n, đ&aacute;m ch&aacute;y lớn ở khu vực c&oacute; nhiều vật dụng dễ ch&aacute;y n&ecirc;n c&ocirc;ng t&aacute;c dập lửa gặp rất nhiều kh&oacute; khăn. Từ b&ecirc;n ngo&agrave;i, nhiều người hoảng hốt khi thấy cột kh&oacute;i đen lớn v&agrave; bốc cao h&agrave;ng chục m&eacute;t.</p>\r\n<section class=\"bnrBt txtCent\"></section>\r\n<p>Sau nhiều giờ vật lộn với &ldquo;giặc lửa&rdquo;, đến khoảng hơn 8h s&aacute;ng c&ugrave;ng ng&agrave;y vụ ch&aacute;y cơ bản được khống chế v&agrave; lực lượng chức năng tập trung phun nước l&agrave;m nguội.</p>\r\n<p>Vụ ch&aacute;y kh&ocirc;ng g&acirc;y thiệt hại về người. Nguy&ecirc;n nh&acirc;n vụ ch&aacute;y hiện đang được cơ quan chức năng điều tra, l&agrave;m r&otilde;.</p>\r\n<p>C&ocirc;ng ty Scavi Huế l&agrave; c&ocirc;ng ty con của Tập đo&agrave;n Financi&egrave;re B\'Lao (Ph&aacute;p), doanh nghiệp FDI chuy&ecirc;n về dệt may c&aacute;c sản phẩm nội y, đồ tắm, &aacute;o quần&nbsp;<a class=\"seokwl\" title=\"thể thao\" href=\"https://www.baogiaothong.vn/the-thao/\">thể thao</a>, đồ bảo hộ tại Việt Nam.</p>\r\n<p>Tại C&ocirc;ng ty Scavi Huế hiện c&oacute; khoảng 7.000 c&ocirc;ng nh&acirc;n đang l&agrave;m việc.</p>\r\n<div id=\"v-24hContainer_video_player_3943292\" class=\"v-24hContainer_video_player_3943292_loaded\">\r\n<div id=\"my-video-multiple1_1655653505818\" class=\"video-js vjs-default-skin vjs-16-9 vjs-controls-enabled vjs-workinghover vjs-v7 vjs-controlbar-always-show vjs-viewportautoplay vjs-contextmenu-ui vjs-onviewport vjs-has-started my-video-multiple1_1655653505818-dimensions vjs-ad-playing vjs-ad-loading vjs-paused vjs-user-inactive\" lang=\"vn\" tabindex=\"-1\" role=\"region\" aria-label=\"Video Player\">\r\n<div id=\"my-video-multiple1_1655653505818_ima-ad-container\" class=\"my-video-multiple1_1655653505818_ima-ad-container ima-ad-container\">\r\n<div>ng&agrave;y 10/11/2021, C&ocirc;ng ty Scavi Huế đ&atilde; được UBND tỉnh Quảng Trị quyết định chấp thuận chủ trương đầu tư Dự &aacute;n nh&agrave; m&aacute;y sản xuất trang phục l&oacute;t v&agrave; h&agrave;ng thể thao tại Khu c&ocirc;ng nghiệp T&acirc;y Bắc Hồ X&aacute; (huyện Vĩnh Linh).</div>\r\n</div>\r\n</div>\r\n</div>\r\n<p>Dự &aacute;n c&oacute; c&ocirc;ng suất thiết kế khoảng 90 triệu sản phẩm/ năm, chia th&agrave;nh 2 giai đoạn. Diện t&iacute;ch đất dự kiến 18,5ha. Tổng vốn đầu tư của dự &aacute;n 575 tỷ đồng.</p>\r\n<p>Li&ecirc;n quan đến vụ ch&aacute;y lớn tại C&ocirc;ng ty Scavi Huế trong Khu c&ocirc;ng nghiệp Phong Điền (huyện Phong Điền, tỉnh Thừa Thi&ecirc;n Huế), trưa 13/6, Ph&ograve;ng Cảnh s&aacute;t PCCC v&agrave; cứu nạn cứu hộ C&ocirc;ng an tỉnh Quảng Trị x&aacute;c nhận, đơn vị đ&atilde; điều động 2 xe chữa ch&aacute;y c&ugrave;ng nhiều c&aacute;n bộ, chiến sĩ v&agrave;o chi viện, phối hợp triển khai khống chế vụ ch&aacute;y tr&ecirc;n.</p>\r\n<p>Đ&acirc;y l&agrave; lần đầu ti&ecirc;n lực lượng Cảnh s&aacute;t PCCC v&agrave; Cứu nạn cứu hộ C&ocirc;ng an tỉnh Quảng Trị chi viện, tham gia phối hợp với lực lượng C&ocirc;ng an tỉnh Thừa Thi&ecirc;n Huế trong c&ocirc;ng t&aacute;c chữa ch&aacute;y.</p>\r\n<div>\r\n<p class=\"image\"><img class=\"width100 loaded\" src=\"https://cdn.baogiaothong.vn/upload/images/2022-2/article_img/2022-06-13/img-bgt-2021-canh-sat-quang-tri-phoi-hop-khong-che-vu-chay-lon-tai-nha-may-scavi-hue-2-1655103171-width1280height720.jpg\" alt=\"cảnh s&aacute;t vật lộn với &ldquo;giặc lửa&rdquo; khống chế vụ ch&aacute;y lớn tại nh&agrave; m&aacute;y scavi huế\" data-original=\"https://cdn.baogiaothong.vn/upload/images/2022-2/article_img/2022-06-13/img-bgt-2021-canh-sat-quang-tri-phoi-hop-khong-che-vu-chay-lon-tai-nha-may-scavi-hue-2-1655103171-width1280height720.jpg\" data-was-processed=\"true\"></p>\r\n<p class=\"img_chu_thich\">Lực lượng chữa ch&aacute;y triển khai theo 4 mũi: 1 mũi trực diện v&agrave;o khu vực nh&agrave; xưởng; 1 mũi ngăn ch&aacute;y lan v&agrave;o khu vực nh&agrave; kho; 1 mũi ngăn ch&aacute;y lan v&agrave;o khu vực nh&agrave; xưởng; 1 mũi l&agrave;m m&aacute;t, bảo vệ kh&ocirc;ng để lửa lan sang kho chứa c&oacute; diện t&iacute;ch khoảng 4000m2 v&agrave; nh&agrave; xưởng c&oacute; diện t&iacute;ch khoảng 3000m2.</p>\r\n</div>\r\n<p>Theo Ph&ograve;ng Cảnh s&aacute;t PCCC v&agrave; Cứu nạn cứu hộ C&ocirc;ng an tỉnh Quảng Trị, khoảng 5h30\' s&aacute;ng ng&agrave;y 13/6, sau khi nhận được đề nghị của l&atilde;nh đạo C&ocirc;ng an tỉnh Thừa Thi&ecirc;n Huế về việc chi viện lực lượng, phương tiện tham gia chữa ch&aacute;y tại C&ocirc;ng ty Scavi Huế, l&atilde;nh đạo C&ocirc;ng an tỉnh Quảng Trị đ&atilde; trực tiếp chỉ đạo điều động 2 xe chữa ch&aacute;y c&ugrave;ng nhiều c&aacute;n bộ, chiến sĩ nhanh ch&oacute;ng l&ecirc;n đường chi viện.</p>\r\n<p>Đến khoảng 11h, lực lượng tham gia chi viện của C&ocirc;ng an tỉnh Quảng Trị đ&atilde; về đơn vị.</p>', 218, 12, 17, 1, '2022-11-18 19:35:41', '2023-02-07 08:35:31'),
(40, 'Choáng với tiền thưởng cầu thủ Ả Rập Xê Út nhận được khi vô địch U23 châu Á', 'choang-voi-tien-thuong-cau-thu-a-rap-xe-ut-nhan-duoc-khi-vo-dich-u23-chau-a', 'Mỗi cầu thủ Ả Rập Xê Út ước tính nhận thưởng tới khoảng 6 tỷ đồng sau chức vô địch U23 châu Á 2022.', '<p>Tối 19/6, U23 Ả Rập X&ecirc; &Uacute;t đ&atilde; đ&aacute;nh bại U23 Uzbekistan trong trận chung kết U23 ch&acirc;u &Aacute; 2022, qua đ&oacute; lần đầu l&ecirc;n ng&ocirc;i v&ocirc; địch tại giải đấu số 1 ch&acirc;u lục d&agrave;nh cho lứa U23.</p>\r\n<div>\r\n<p class=\"image\"><img class=\"width100 initial loaded\" src=\"https://cdn.baogiaothong.vn/upload/images/2022-2/article_img/2022-06-20/img-bgt-2021-u23-a-rap-xe-ut-1655717164-width1280height720.jpg\" alt=\"cho&aacute;ng với tiền thưởng cầu thủ Ả rập x&ecirc; &Uacute;t nhận được khi v&ocirc; địch u23 ch&acirc;u &Aacute;\" data-original=\"https://cdn.baogiaothong.vn/upload/images/2022-2/article_img/2022-06-20/img-bgt-2021-u23-a-rap-xe-ut-1655717164-width1280height720.jpg\" data-was-processed=\"true\"></p>\r\n<p class=\"img_chu_thich\">U23 Ả Rập X&ecirc; &Uacute;t v&ocirc; địch giải U23 ch&acirc;u &Aacute; 2022</p>\r\n</div>\r\n<p>Chức v&ocirc; địch lịch sử n&agrave;y gi&uacute;p c&aacute;c cầu thủ Ả Rập X&ecirc; &Uacute;t nhận được mức thưởng hậu hĩnh.</p>\r\n<p>Cụ thể, Bộ&nbsp;<a class=\"seokwl\" title=\"thể thao\" href=\"https://www.baogiaothong.vn/the-thao/\">Thể thao</a>&nbsp;Ả Rập X&ecirc; &Uacute;t quyết định thưởng mỗi cầu thủ v&agrave; th&agrave;nh vi&ecirc;n ban huấn luyện 1 triệu riyal (266.000 USD), tương đương hơn 6 tỷ đồng.</p>\r\n<p>Bộ trưởng Thể thao Ả Rập X&ecirc; &Uacute;t Abdulaziz bin Turki Al Saud cũng gửi những lời ch&uacute;c tới Firas v&agrave; đồng đội th&ocirc;ng qua Twitter.</p>\r\n<p>&ldquo;T&ocirc;i gửi lời ch&uacute;c mừng ch&acirc;n th&agrave;nh tới c&aacute;c th&agrave;nh vi&ecirc;n U23 Ả Rập X&ecirc; &Uacute;t v&igrave; những g&igrave; mọi người đ&atilde; đạt được.</p>\r\n<p>Xin ch&uacute;c mừng những người h&ugrave;ng, c&aacute;c th&agrave;nh vi&ecirc;n kỹ thuật v&agrave; ban điều h&agrave;nh. Ch&uacute;ng t&ocirc;i rất tự h&agrave;o v&igrave; c&aacute;c bạn&rdquo;, &ocirc;ng Abdulaziz viết.</p>\r\n<p>Trở lại với h&agrave;nh tr&igrave;nh v&ocirc; địch của U23 Ả Rập X&ecirc; &Uacute;t, họ kh&ocirc;ng để thủng lưới b&agrave;n n&agrave;o sau 6 trận, ghi 13 b&agrave;n, l&agrave; đội b&oacute;ng sở hữu h&agrave;ng c&ocirc;ng xuất sắc nhất v&agrave; h&agrave;ng thủ chắc chắn nhất.</p>\r\n<p>Tại v&ograve;ng bảng, Ả Rập X&ecirc; &Uacute;t thắng 1 h&ograve;a 2 trận. Ở tứ kết, họ đ&aacute;nh bại Việt Nam trước khi vượt qua Australia ở b&aacute;n kết v&agrave; thắng nốt Uzbekistan trong trận đấu cuối c&ugrave;ng.</p>', 218, 1, 25, 1, '2022-11-18 19:35:41', '2022-11-22 08:27:50');
INSERT INTO `posts` (`id`, `title`, `slug`, `excerpt`, `body`, `user_id`, `category_id`, `views`, `approved`, `created_at`, `updated_at`) VALUES
(43, '7 phát minh y tế giúp nền y học có bước tiến vượt bậc', '7-phat-minh-y-te-giup-nen-y-hoc-co-buoc-tien-vuot-bac', 'Sự đổi mới nhanh chóng trong lĩnh vực y tế đã có tác động cực tốt trong việc phát hiện và điều trị bệnh.', '<p><strong>1. Ống nghe (ph&aacute;t minh năm 1815)</strong></p>\r\n<p align=\"center\">&nbsp;</p>\r\n<div id=\"container-24h-banner-in-image\">\r\n<div>\r\n<div><img class=\"news-image initial loading\" src=\"https://icdn.24h.com.vn/upload/1-2022/images/2022-02-08/7-phat-minh-y-te-giup-nen-y-hoc-co-buoc-tien-vuot-bac-1-1644304797-652-width660height440.jpg\" alt=\"7 ph&aacute;t minh y tế gi&uacute;p nền y học c&oacute; bước tiến vượt bậc - 1\" data-was-processed=\"true\"></div>\r\n<div id=\"24h-banner-in-image\">\r\n<div id=\"ySslEbzCxv_139_15s\" class=\"txtCent\">\r\n<div class=\"text_adver_right\">&nbsp;</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n<p>&nbsp;</p>\r\n<p>Trước khi ph&aacute;t minh ra ống nghe, c&aacute;c b&aacute;c sĩ chỉ c&oacute; thể đặt tai v&agrave;o ngực bệnh nh&acirc;n để nghe nhịp tim v&agrave; phổi. Tuy nhi&ecirc;n, đ&acirc;y l&agrave; phương ph&aacute;p kh&ocirc;ng mấy hiệu quả.</p>\r\n<p>Trong một lần thăm kh&aacute;m theo phương ph&aacute;p tr&ecirc;n, b&aacute;c sĩ người Ph&aacute;p Ren&eacute; La&euml;nnec đ&atilde; cố gắng nghe nhịp tim của một bệnh nh&acirc;n c&oacute; lớp mỡ kh&aacute; d&agrave;y v&agrave; kh&ocirc;ng thể nghe thấy nhịp tim của anh ấy. Điều n&agrave;y đ&atilde; th&ocirc;i th&uacute;c &ocirc;ng ph&aacute;t triển ra chiếc ống nghe đầu ti&ecirc;n, một ống gỗ h&igrave;nh chiếc k&eacute;n, c&oacute; t&aacute;c dụng khuếch đại &acirc;m thanh của tim v&agrave; phổi. S&aacute;ng kiến khuếch đại &acirc;m thanh n&agrave;y đ&atilde; dẫn đến phi&ecirc;n bản ống nghe m&agrave; ch&uacute;ng ta biết ng&agrave;y nay.</p>\r\n<p><strong>2. Thuốc m&ecirc; (ph&aacute;t minh năm 1846)</strong></p>\r\n<p align=\"center\"><img class=\"news-image loaded\" src=\"https://icdn.24h.com.vn/upload/1-2022/images/2022-02-08/7-phat-minh-y-te-giup-nen-y-hoc-co-buoc-tien-vuot-bac-2-1644304816-519-width748height500.jpg\" alt=\"7 ph&aacute;t minh y tế gi&uacute;p nền y học c&oacute; bước tiến vượt bậc - 3\" data-original=\"https://icdn.24h.com.vn/upload/1-2022/images/2022-02-08/7-phat-minh-y-te-giup-nen-y-hoc-co-buoc-tien-vuot-bac-2-1644304816-519-width748height500.jpg\" data-was-processed=\"true\"></p>\r\n<p>Từ xa xưa đ&atilde; c&oacute; mu&ocirc;n v&agrave;n h&igrave;nh thức g&acirc;y m&ecirc; được thử nghiệm. Thế nhưng v&agrave;o năm 1846, một nha sĩ t&ecirc;n William TG Morton l&agrave; người đầu ti&ecirc;n chứng minh việc sử dụng Ether g&acirc;y m&ecirc; trong phẫu thuật. Sự kiện n&agrave;y đ&aacute;nh dấu cho phương ph&aacute;p g&acirc;y m&ecirc; hiện đại, cho ph&eacute;p điều trị kh&ocirc;ng g&acirc;y đau đớn cho bệnh nh&acirc;n, ngay cả khi b&aacute;c sĩ phải thực hiện những ca phẫu thuật phức tạp.</p>\r\n<p><strong>3. Tia X (ph&aacute;t minh năm 1895)</strong></p>\r\n<p align=\"center\"><img class=\"news-image loaded\" src=\"https://icdn.24h.com.vn/upload/1-2022/images/2022-02-08/7-phat-minh-y-te-giup-nen-y-hoc-co-buoc-tien-vuot-bac-3-1644304835-56-width628height418.jpg\" alt=\"7 ph&aacute;t minh y tế gi&uacute;p nền y học c&oacute; bước tiến vượt bậc - 4\" data-original=\"https://icdn.24h.com.vn/upload/1-2022/images/2022-02-08/7-phat-minh-y-te-giup-nen-y-hoc-co-buoc-tien-vuot-bac-3-1644304835-56-width628height418.jpg\" data-was-processed=\"true\"></p>\r\n<p>Trước khi c&oacute; m&aacute;y chụp X-quang, việc chuẩn đo&aacute;n v&agrave; điều trị chấn thương l&agrave; v&ocirc; c&ugrave;ng kh&oacute; khăn đối với c&aacute;c b&aacute;c sĩ. Phải đến năm 1895, nh&agrave; vật l&yacute; người Đức Wilhelm Conrad R&ouml;ntgen ph&aacute;t hiện ra tia X, mọi vấn đề phức tạp mới được giải quyết một c&aacute;ch đơn giản. V&agrave;o thời điểm đ&oacute;, tia X k&igrave; diệu đến mức nhiều người kh&ocirc;ng tin v&agrave;o những b&aacute;o c&aacute;o ban đầu của nh&agrave; khoa học. Ng&agrave;y nay, X-quang l&agrave; một kỹ thuật h&igrave;nh ảnh mạnh mẽ, được sử dụng trong việc thăm kh&aacute;m, điều trị v&agrave; d&ugrave;ng trong c&aacute;c cuộc phẫu thuật. CT, xạ trị v&agrave; soi huỳnh quang đều dựa tr&ecirc;n tia X.</p>\r\n<p><strong>4. Thuốc kh&aacute;ng sinh (ph&aacute;t minh năm 1907)</strong></p>\r\n<p align=\"center\"><img class=\"news-image loaded\" src=\"https://icdn.24h.com.vn/upload/1-2022/images/2022-02-08/7-phat-minh-y-te-giup-nen-y-hoc-co-buoc-tien-vuot-bac-4-1644304851-516-width800height536.jpg\" alt=\"7 ph&aacute;t minh y tế gi&uacute;p nền y học c&oacute; bước tiến vượt bậc - 5\" data-original=\"https://icdn.24h.com.vn/upload/1-2022/images/2022-02-08/7-phat-minh-y-te-giup-nen-y-hoc-co-buoc-tien-vuot-bac-4-1644304851-516-width800height536.jpg\" data-was-processed=\"true\"></p>\r\n<p>Kỷ nguy&ecirc;n của thuốc kh&aacute;ng sinh bắt đầu với sự tổng hợp Salvarsan (nay được gọi l&agrave; Arsphenamine) của Alfred Bertheim v&agrave; Paul Ehrlich v&agrave;o năm 1907. Thuốc được sử dụng như một phương ph&aacute;p điều trị hiệu quả đầu ti&ecirc;n cho bệnh giang mai, đ&aacute;nh dấu sự khởi đầu của điều trị kh&aacute;ng khuẩn. Năm 1928, Alexander Fleming ghi nhận t&aacute;c dụng kh&aacute;ng khuẩn m&agrave; nấm Penicillium g&acirc;y ra (ti&ecirc;u diệt vi khuẩn trong đĩa Petri), đ&aacute;nh dấu sự khởi đầu của Penicillin. Thuốc kh&aacute;ng sinh chỉ bắt đầu được sử dụng phổ biến sau năm 1945 khi c&aacute;c phương ph&aacute;p sản xuất h&agrave;ng loạt để sản xuất Penicillin trở n&ecirc;n phổ biến.</p>\r\n<p>Thuốc kh&aacute;ng sinh đ&atilde; tạo ra một cuộc c&aacute;ch mạng trong y học hiện đại, v&igrave; ch&uacute;ng cho ph&eacute;p điều trị v&agrave; phục hồi c&aacute;c bệnh thường g&acirc;y tử vong trước đ&acirc;y. C&ugrave;ng với ti&ecirc;m chủng, thuốc kh&aacute;ng sinh đ&atilde; tạo điều kiện cho việc ti&ecirc;u diệt gần như x&oacute;a sổ c&aacute;c bệnh dịch như bệnh lao. Thật kh&oacute; để tưởng tượng thế giới của ch&uacute;ng ta m&agrave; kh&ocirc;ng c&oacute; kh&aacute;ng sinh trong đ&oacute;. Ng&agrave;y nay, cộng đồng y tế đang xem x&eacute;t nghi&ecirc;n cứu c&aacute;c vi khuẩn kh&aacute;ng thuốc đ&atilde; ph&aacute;t triển do lạm dụng thuốc kh&aacute;ng sinh.</p>\r\n<p><strong>5. Ống th&ocirc;ng d&ugrave;ng 1 lần (ph&aacute;t minh năm 1944)</strong></p>\r\n<p align=\"center\"><img class=\"news-image loaded\" src=\"https://icdn.24h.com.vn/upload/1-2022/images/2022-02-08/7-phat-minh-y-te-giup-nen-y-hoc-co-buoc-tien-vuot-bac-5-1644304872-634-width507height340.jpg\" alt=\"7 ph&aacute;t minh y tế gi&uacute;p nền y học c&oacute; bước tiến vượt bậc - 6\" data-original=\"https://icdn.24h.com.vn/upload/1-2022/images/2022-02-08/7-phat-minh-y-te-giup-nen-y-hoc-co-buoc-tien-vuot-bac-5-1644304872-634-width507height340.jpg\" data-was-processed=\"true\"></p>\r\n<p>Hầu hết c&aacute;c đổi mới y học được ti&ecirc;n phong bởi những người c&oacute; học vấn cao, nhưng trong trường hợp n&agrave;y, điều ngược lại đ&atilde; xảy ra. Ống th&ocirc;ng d&ugrave;ng một lần được ph&aacute;t minh bởi David S. Sheridan, người chỉ mới học lớp 8 v&agrave; đang l&agrave;m việc trong một doanh nghiệp sửa chữa s&agrave;n nh&agrave; v&agrave;o thời điểm đ&oacute;.</p>\r\n<p>Sheridan đ&atilde; tạo ra một ống nhựa rỗng được thiết kế để bỏ đi sau một lần sử dụng. Sau đ&oacute;, &ocirc;ng đ&atilde; nắm giữ hơn 50 bằng s&aacute;ng chế trước khi qua đời v&agrave;o năm 2004 ở tuổi 95.</p>\r\n<p><strong>6. M&aacute;y khử rung tim (ph&aacute;t minh năm 1947)</strong></p>\r\n<p align=\"center\"><img class=\"news-image loaded\" src=\"https://icdn.24h.com.vn/upload/1-2022/images/2022-02-08/7-phat-minh-y-te-giup-nen-y-hoc-co-buoc-tien-vuot-bac-6-1644304894-65-width1920height1080.jpg\" alt=\"7 ph&aacute;t minh y tế gi&uacute;p nền y học c&oacute; bước tiến vượt bậc - 7\" data-original=\"https://icdn.24h.com.vn/upload/1-2022/images/2022-02-08/7-phat-minh-y-te-giup-nen-y-hoc-co-buoc-tien-vuot-bac-6-1644304894-65-width1920height1080.jpg\" data-was-processed=\"true\"></p>\r\n<p>M&aacute;y khử rung tim l&agrave; một trong những ph&aacute;t minh kỳ diệu nhất v&agrave; kh&aacute;i niệm &yacute; tưởng về khử rung tim đ&atilde; được biết đến trong nhiều thập kỷ. Tuy nhi&ecirc;n, phải đến khi Claude Beck, một b&aacute;c sĩ phẫu thuật tim mạch ở Cleveland, khử rung tim th&agrave;nh c&ocirc;ng cho một bệnh nh&acirc;n cận kề c&aacute;i chết th&igrave; loại m&aacute;y n&agrave;y mới được đưa v&agrave;o sử dụng rộng r&atilde;i.</p>\r\n<p>Ng&agrave;y nay, m&aacute;y khử rung tim được sử dụng h&agrave;ng ng&agrave;y tr&ecirc;n khắp thế giới v&agrave; đ&atilde; cứu h&agrave;ng triệu người tho&aacute;t khỏi tay &ldquo;Thần chết&rdquo;.</p>\r\n<p><strong>7. CT Scanner (ph&aacute;t minh v&agrave;o năm 1971) v&agrave; MRI (ph&aacute;t minh v&agrave;o năm 1978)</strong></p>\r\n<p>Việc ph&aacute;t minh ra tia X đ&atilde; tạo ra một cuộc c&aacute;ch mạng về chi tiết của một cuộc kiểm tra, c&aacute;c nh&agrave; y tế muốn tiếp cận với chi tiết hơn nữa - dẫn đến việc ph&aacute;t minh ra m&aacute;y qu&eacute;t CT, hiển thị c&aacute;c lớp kh&aacute;c nhau trong nhiều h&igrave;nh ảnh X-quang.</p>\r\n<p align=\"center\"><img class=\"news-image loaded\" src=\"https://icdn.24h.com.vn/upload/1-2022/images/2022-02-08/7-phat-minh-y-te-giup-nen-y-hoc-co-buoc-tien-vuot-bac-4238-ct-scan-222-1644304928-212-width1678height1119.jpg\" alt=\"7 ph&aacute;t minh y tế gi&uacute;p nền y học c&oacute; bước tiến vượt bậc - 8\" data-original=\"https://icdn.24h.com.vn/upload/1-2022/images/2022-02-08/7-phat-minh-y-te-giup-nen-y-hoc-co-buoc-tien-vuot-bac-4238-ct-scan-222-1644304928-212-width1678height1119.jpg\" data-was-processed=\"true\"></p>\r\n<p>Tiến sĩ Godfrey Hounsfield đ&atilde; ph&aacute;t triển m&aacute;y qu&eacute;t CT đầu ti&ecirc;n được thử nghiệm tr&ecirc;n một bệnh nh&acirc;n ở London. Sau đ&oacute;, &ocirc;ng đ&atilde; nhận được giải Nobel Sinh l&yacute; học hoặc Y học năm 1979 cho s&aacute;ng chế n&agrave;y.</p>\r\n<p>Sau khi ph&aacute;t minh ra m&aacute;y qu&eacute;t CT, Tiến sĩ Raymond V. Damadian đ&atilde; cấp bằng s&aacute;ng chế cho kỹ thuật sử dụng cộng hưởng từ hạt nh&acirc;n để ph&acirc;n biệt giữa m&ocirc; b&igrave;nh thường v&agrave; m&ocirc; ung thư. Điều n&agrave;y sau đ&oacute; đ&atilde; ph&aacute;t triển th&agrave;nh MRI v&agrave; cải thiện ho&agrave;n to&agrave;n việc chăm s&oacute;c sức khỏe cho h&agrave;ng triệu bệnh nh&acirc;n tr&ecirc;n khắp thế giới.</p>', 218, 5, 35, 1, '2022-11-18 19:35:41', '2022-11-22 05:39:43'),
(45, 'Đây là chủ nhân của Aston Martin Vantage F1 Edition đầu tiên về Việt Nam, 1 nhà sưu tập trứ danh', 'day-la-chu-nhan-cua-aston-martin-vantage-f1-edition-dau-tien-ve-viet-nam-1-nha-suu-tap-tru-danh', 'Chiếc Aston Martin Vantage F1 Edition mới lộ ảnh về nước được cho của ông Đặng Lê Nguyên Vũ. Như vậy đại gia ngành cà phê đang sở hữu 2 chiếc xe thể thao tốc độ Aston Martin Vantage phiên bản giới hạn là F1 Edition và 007 Edition.', '<p>C&aacute;ch đ&acirc;y &iacute;t ng&agrave;y, h&igrave;nh ảnh về chiếc xe thể thao tốc độ Aston Martin Vantage phi&ecirc;n bản giới hạn F1 Edition bị bắt gặp vận chuyển tại H&agrave; Nội tr&ecirc;n xe cứu hộ đ&atilde; thu h&uacute;t sự quan t&acirc;m của giới m&ecirc; xe trong nước.</p>\r\n<div class=\"width-100 inline\">\r\n<div id=\"ads-detail-below-sapo-320x180\" class=\"box-ads-anymind\"></div>\r\n</div>\r\n<p>Đ&acirc;y cũng l&agrave; lần đầu ti&ecirc;n một chiếc xe đua đường phố Aston Martin Vantage F1 Edition xuất hiện tại Việt Nam v&agrave; tất nhi&ecirc;n l&agrave; kh&ocirc;ng &iacute;t người đ&atilde; t&ograve; m&ograve; chủ nh&acirc;n của chiếc xe thể thao Aston Martin Vantage F1 Edition l&agrave; người y&ecirc;u xe n&agrave;o, đến nay, c&acirc;u trả lời đ&atilde; c&oacute;.</p>\r\n<div class=\"dvg_photo_box\">\r\n<figure><span id=\"imageGalleryId-0\" class=\"lozadload-content imageGalleryId\" data-src=\"https://img.tinxe.vn/2022/06/17/95ywCUPr/286594830-1394902120977446-4312996240857753419-n-57d6.jpg\" data-desc=\"\"><img class=\"lozad\" title=\"\" src=\"https://img.tinxe.vn/resize/1000x-/2022/06/17/95ywCUPr/286594830-1394902120977446-4312996240857753419-n-57d6.jpg\" alt=\"\" width=\"1600\" height=\"890\" data-height=\"890\" data-width=\"1600\" data-src=\"https://img.tinxe.vn/resize/1000x-/2022/06/17/95ywCUPr/286594830-1394902120977446-4312996240857753419-n-57d6.jpg\" data-loaded=\"true\"></span></figure>\r\n</div>\r\n<p><a href=\"http://tinxe.vn/tin-xe/ro-tin-don-dang-le-nguyen-vu-la-chu-nhan-cua-aston-martin-vantage-f1-edition-dau-tien-ve-viet-nam-id20220617150657494\"><strong><em>Rộ tin đồn Đặng L&ecirc; Nguy&ecirc;n Vũ l&agrave; chủ nh&acirc;n của Aston Martin Vantage F1 Edition đầu ti&ecirc;n về Việt Nam</em></strong></a></p>\r\n<div class=\"dvg_photo_box\">\r\n<figure><span id=\"imageGalleryId-1\" class=\"lozadload-content imageGalleryId\" data-src=\"https://img.tinxe.vn/2022/06/15/95ywCUPr/277101928-2146993852143033-662296926148588856-n-fe-90e3.jpg\" data-desc=\"Aston Martin Vantage 007 Edition của &ocirc;ng Đặng L&ecirc; Nguy&ecirc;n Vũ đ&atilde; nhận b&agrave;n giao trước đ&oacute;\"><img class=\"lozad\" title=\"Aston Martin Vantage 007 Edition của &ocirc;ng Đặng L&ecirc; Nguy&ecirc;n Vũ đ&atilde; nhận b&agrave;n giao trước đ&oacute;\" src=\"https://img.tinxe.vn/resize/1000x-/2022/06/15/95ywCUPr/277101928-2146993852143033-662296926148588856-n-fe-90e3.jpg\" alt=\"Aston Martin Vantage 007 Edition của &ocirc;ng Đặng L&ecirc; Nguy&ecirc;n Vũ đ&atilde; nhận b&agrave;n giao trước đ&oacute;\" width=\"1000\" height=\"750\" data-height=\"750\" data-width=\"1000\" data-src=\"https://img.tinxe.vn/resize/1000x-/2022/06/15/95ywCUPr/277101928-2146993852143033-662296926148588856-n-fe-90e3.jpg\" data-loaded=\"true\"></span></figure>\r\n<p><em>Aston Martin Vantage 007 Edition của &ocirc;ng Đặng L&ecirc; Nguy&ecirc;n Vũ đ&atilde; nhận b&agrave;n giao trước đ&oacute;</em></p>\r\n</div>\r\n<p>Đơn vị chuy&ecirc;n nhập khẩu si&ecirc;u xe cũng như t&igrave;m c&aacute;c mẫu xe độc lạ cho doanh nh&acirc;n Đặng L&ecirc; Nguy&ecirc;n Vũ sưu tầm đ&atilde; h&eacute; lộ đ&iacute;ch đến của chiếc xe Aston Martin Vantage F1 Edition mới về nước ch&iacute;nh l&agrave; garage của Chủ tịch Trung Nguy&ecirc;n.</p>\r\n<div class=\"width-100 inline\">\r\n<div id=\"ads-detail-below-320x250\" class=\"box-ads-anymind\"></div>\r\n</div>\r\n<p>Hiện chưa r&otilde;&nbsp;Aston Martin Vantage F1 Edition mới lộ ảnh về nước đ&atilde; b&agrave;n giao cho &ocirc;ng Đặng L&ecirc; Nguy&ecirc;n Vũ hay chưa, nếu h&agrave;ng độc n&agrave;y đ&atilde; về garage xe của \"Qua\" Vũ, vị đại gia ng&agrave;nh c&agrave; ph&ecirc; n&agrave;y sẽ sở hữu c&ugrave;ng l&uacute;c 2 chiếc xe thể thao tốc độ Aston Martin Vantage phi&ecirc;n bản giới hạn l&agrave; F1 Edition v&agrave; 007 Edition.</p>\r\n<div class=\"dvg_photo_box\">\r\n<figure><span id=\"imageGalleryId-2\" class=\"lozadload-content imageGalleryId\" data-src=\"https://img.tinxe.vn/2022/06/17/95ywCUPr/289009968-2215937485248669-2675524919859139670-n-6063.jpg\" data-desc=\"Aston Martin Vantage F1 Edition của Đặng L&ecirc; Nguy&ecirc;n Vũ c&oacute; m&agrave;u xanh Racing\"><img class=\"lozad\" title=\"Aston Martin Vantage F1 Edition của Đặng L&ecirc; Nguy&ecirc;n Vũ c&oacute; m&agrave;u xanh Racing\" src=\"https://img.tinxe.vn/resize/1000x-/2022/06/17/95ywCUPr/289009968-2215937485248669-2675524919859139670-n-6063.jpg\" alt=\"Aston Martin Vantage F1 Edition của Đặng L&ecirc; Nguy&ecirc;n Vũ c&oacute; m&agrave;u xanh Racing\" width=\"1600\" height=\"895\" data-height=\"895\" data-width=\"1600\" data-src=\"https://img.tinxe.vn/resize/1000x-/2022/06/17/95ywCUPr/289009968-2215937485248669-2675524919859139670-n-6063.jpg\" data-loaded=\"true\"></span></figure>\r\n<p><em>Aston Martin Vantage F1 Edition của Đặng L&ecirc; Nguy&ecirc;n Vũ c&oacute; m&agrave;u xanh Racing</em></p>\r\n</div>\r\n<p>Sự c&oacute; mặt của chiếc xe thể thao tốc độ Vantage F1 Edition trong bộ sưu tập xe của Chủ tịch Trung Nguy&ecirc;n sẽ gi&uacute;p doanh nh&acirc;n n&agrave;y tăng số lượng d&agrave;n xe Aston Martin l&ecirc;n hơn 7 chiếc khi trước đ&oacute; đ&atilde; sưu tầm 1 chiếc DB9 Volante, 3 xe Aston Martin Vanquish Coupe v&agrave; một chiếc Aston Martin Vanquish Volante.</p>\r\n<p>Trong bộ sưu tập xe Aston Martin Vantage đời 2018 trở về sau n&agrave;y, ngo&agrave;i bản ti&ecirc;u chuẩn, F1 Edition v&agrave; 007 Edition ra, phi&ecirc;n bản Aston Martin Vantage AMR cũng rất đ&aacute;ng để đại gia Đặng L&ecirc; Nguy&ecirc;n Vũ để mắt đến nhằm bổ sung bộ sưu tập xe của m&igrave;nh.</p>\r\n<p>Chưa hết, một phi&ecirc;n bản Aston Martin Vantage mui trần cũng l&agrave; mảnh gh&eacute;p ho&agrave;n hảo cho bộ sưu tập xe thể thao tốc độ Vantage của \"Qua\" Vũ.</p>', 218, 13, 36, 1, '2022-11-18 19:35:41', '2023-02-07 08:30:56'),
(52, 'Kê đặt bàn học, giá sách theo phong thủy', 'ke-dat-ban-hoc-gia-sach-theo-phong-thuy', 'Kê bàn học, đặt giá sách đúng nguyên tắc phong thủy và phù hợp với điều kiện thực tế góp phần nâng cao kết quả học tập, giữ gìn sức khỏe.', '<div id=\"divfirst\">\r\n<p class=\"Normal\">C&oacute; hai quan điểm kh&aacute;c nhau trong c&aacute;ch bố tr&iacute; ph&ograve;ng đọc, nơi l&agrave;m việc. C&aacute;c nh&agrave;&nbsp;<a href=\"https://vnexpress.net/topic/phong-thuy-17232\" target=\"_blank\" rel=\"dofollow noopener\">phong thủy</a>&nbsp;xưa ch&uacute; trọng đến việc lựa chọn hướng của ph&ograve;ng đọc, ph&ograve;ng l&agrave;m việc (thư ph&ograve;ng). Cụ thể, thư ph&ograve;ng n&ecirc;n bố tr&iacute; ở hướng Nam hoặc Đ&ocirc;ng Nam của ng&ocirc;i nh&agrave; hay gian ph&ograve;ng. B&agrave;n ghế v&agrave; gi&aacute; s&aacute;ch cũng n&ecirc;n được k&ecirc; đặt ở phương vị tương ứng hoặc ở vị tr&iacute; thuận tiện cho người đọc.</p>\r\n<p class=\"Normal\">Nhiều nh&agrave; nghi&ecirc;n cứu phong thủy hiện đại cho rằng, quan điểm n&agrave;y xuất ph&aacute;t từ việc người xưa c&oacute; điều kiện để lựa chọn. Trong bối cảnh đất rộng người thưa, tầng lớp \"c&oacute; chữ\" trong x&atilde; hội chủ yếu l&agrave; tr&iacute; thức phong kiến v&agrave; nho sỹ, ai cũng c&oacute; thể chọn hướng Nam để bố tr&iacute; ph&ograve;ng đọc.</p>\r\n<p class=\"Normal\">Thực tế kh&ocirc;ng ho&agrave;n to&agrave;n như vậy. Ngay thời đ&oacute;, một số trường ph&aacute;i phong thủy, giới nghệ sỹ (thi nh&acirc;n, thư ph&aacute;p gia, họa sỹ) v&agrave; những người theo đạo đ&atilde; ch&uacute; trọng đến từng chi tiết trong bố tr&iacute; ph&ograve;ng đọc, nhất l&agrave; việc k&ecirc; đặt b&agrave;n ghế, gi&aacute; s&aacute;ch, nghi&ecirc;n b&uacute;t... Thậm ch&iacute; nhiều quan lại, chức sắc t&ocirc;n gi&aacute;o thời Tống ở Trung Quốc c&ograve;n xếp sắp giấy mực, ống b&uacute;t, ấn triện ở những vị tr&iacute; kh&aacute;c nhau t&ugrave;y theo ng&agrave;y hoặc giờ l&agrave;m việc.</p>\r\n<p class=\"Normal\">Đến đời nh&agrave; Thanh, sau khi Ung Ch&iacute;nh v&agrave; C&agrave;n Long cho s&agrave;ng lọc, nghi&ecirc;n cứu hiệu chỉnh, bi&ecirc;n soạn lại to&agrave;n bộ hệ thống thư tịch li&ecirc;n quan đến trạch c&aacute;t, b&oacute;i to&aacute;n, phong thủy, hai trường ph&aacute;i đ&atilde; được kết hợp với nhau th&agrave;nh một hệ thống nguy&ecirc;n tắc chung về việc bố tr&iacute; ph&ograve;ng, k&ecirc; đặt b&agrave;n ghế v&agrave; gi&aacute; s&aacute;ch trong ph&ograve;ng đọc.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture data-inimage=\"done\"><source srcset=\"https://i1-giadinh.vnecdn.net/2022/06/18/anh-minh-hoa-1-1-8914-1655537446.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=mDR1b9qWVa5ui0Q2MRdmjA 1x, https://i1-giadinh.vnecdn.net/2022/06/18/anh-minh-hoa-1-1-8914-1655537446.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=-FcXQ1rPTKFCNxMmL_FS1Q 1.5x, https://i1-giadinh.vnecdn.net/2022/06/18/anh-minh-hoa-1-1-8914-1655537446.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=hPO-KJ9xw0Xv92Q2lmxStw 2x\" data-srcset=\"https://i1-giadinh.vnecdn.net/2022/06/18/anh-minh-hoa-1-1-8914-1655537446.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=mDR1b9qWVa5ui0Q2MRdmjA 1x, https://i1-giadinh.vnecdn.net/2022/06/18/anh-minh-hoa-1-1-8914-1655537446.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=-FcXQ1rPTKFCNxMmL_FS1Q 1.5x, https://i1-giadinh.vnecdn.net/2022/06/18/anh-minh-hoa-1-1-8914-1655537446.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=hPO-KJ9xw0Xv92Q2lmxStw 2x\"><img class=\"lazy lazied\" src=\"https://i1-giadinh.vnecdn.net/2022/06/18/anh-minh-hoa-1-1-8914-1655537446.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=mDR1b9qWVa5ui0Q2MRdmjA\" alt=\"Theo phong thủy, ph&ograve;ng đọc v&agrave; ph&ograve;ng l&agrave;m việc bố tr&iacute; ở phương hướng n&agrave;o cũng phải tu&acirc;n thủ nguy&ecirc;n tắc kỵ bối m&ocirc;n, nghĩa l&agrave; k&ecirc; b&agrave;n sao cho người ngồi l&agrave;m việc, đọc hoặc viết kh&ocirc;ng quay lưng ra ph&iacute;a cửa, gồm cả cửa ch&iacute;nh v&agrave; cửa sổ. Ảnh minh họa: jpkcnet.com\" loading=\"lazy\" data-src=\"https://i1-giadinh.vnecdn.net/2022/06/18/anh-minh-hoa-1-1-8914-1655537446.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=mDR1b9qWVa5ui0Q2MRdmjA\" data-ll-status=\"loaded\"></picture>\r\n<div class=\"embed-container-ads\">\r\n<div id=\"sis_inimage\" data-google-query-id=\"CKqUxcTuufgCFRQFXAodhtIPbA\">\r\n<div id=\"google_ads_iframe_/27973503/Vnexpress/Desktop/Inimage/Doisong/Doisong.nha.detail_0__container__\"><iframe id=\"google_ads_iframe_/27973503/Vnexpress/Desktop/Inimage/Doisong/Doisong.nha.detail_0\" tabindex=\"0\" title=\"3rd party ad content\" role=\"region\" src=\"https://3c83b913dbae9df605d2f77e608e181f.safeframe.googlesyndication.com/safeframe/1-0-38/html/container.html\" name=\"\" width=\"0\" height=\"0\" frameborder=\"0\" marginwidth=\"0\" marginheight=\"0\" scrolling=\"no\" sandbox=\"allow-forms allow-popups allow-popups-to-escape-sandbox allow-same-origin allow-scripts allow-top-navigation-by-user-activation\" data-is-safeframe=\"true\" aria-label=\"Advertisement\" data-google-container-id=\"4\" data-load-complete=\"true\"></iframe></div>\r\n</div>\r\n</div>\r\n</div>\r\n<figcaption>\r\n<p class=\"Image\">Theo phong thủy, ph&ograve;ng đọc v&agrave; ph&ograve;ng l&agrave;m việc bố tr&iacute; ở phương hướng n&agrave;o cũng phải tu&acirc;n thủ nguy&ecirc;n tắc \"kỵ bối m&ocirc;n\", nghĩa l&agrave; k&ecirc; b&agrave;n sao cho người ngồi l&agrave;m việc, đọc hoặc viết kh&ocirc;ng quay lưng ra ph&iacute;a cửa, gồm cả cửa ch&iacute;nh v&agrave; cửa sổ. Ảnh minh họa:&nbsp;<em>jpkcnet.com</em></p>\r\n<div id=\"iFrameUnderImageContainer\" class=\"embed-container\"><iframe id=\"iFrameUnderImage\" src=\"https://static.eclick.vn/html5/vs_001/ads/n/novaworld/2022/05/30/00000/inimagepc/dfp/pc/std/fixed/carousel/index.html?link=https%3A%2F%2Fadclick.g.doubleclick.net%2Fpcs%2Fclick%3Fxai%3DAKAOjsv-z6-WD4IYTEZYEYZ8Ptm2nTVNywLTP_mmnYv5vE1H7IQTyCcqIsWy7DP8rGEREns1crW0zrLztSbcKCiFUNDCR-rPmtShCAHoQyyMfmNiDyCdC9szirRnY-Mju-JlBy1NmxHJfDbHofaJRbNtAbfmMr2asQ2o4F9gl3zKP_E88O60fKzEZqQAkHXSfrVlPoF-qcCZwBI-u_gZV9ZLdDyX-5gVKQA7G9KaCTcO9C8bNZpGs2a5w5qLBkBn1zUYr11kYucsTCla5wrfDU2_YauJKFEPVFBpuBR-YktSHrpN6oEYQZNhSpfxtp6YU09bGgLDMCNm5gSn-uWwN2rY1dBnf1PLFLOBMY4A914%26sai%3DAMfl-YSb20rJBslX3paCbCnyaTJZXUZIUS6BXiTFkyp8JKFFptiztLCeGIcgxhjH1TPHD6jde_6r4bwfNApl5Y5nHsPG4dG1PAF8D9fAAGP-q7SzcR9IKUbOX9qZUFBVQPYW70NZ%26sig%3DCg0ArKJSzJln8oC8Jg4BEAE%26fbs_aeid%3D%5Bgw_fbsaeid%5D%26urlfix%3D1%26adurl%3Dhttps%3A%2F%2Fnovaworldphanthiet.com.vn%2Fvi%2Fphan-ky%2Fshop-villa-golf%2F%253Futm_source%253Dvne_banner%2526utm_medium%253Dvne_banner_in-image-banner%2526utm_campaign%253Dvne_banner_in-image-banner_nvwptt5-t6%2526utm_term%253Dvne_banner_in-image-banner_nvwptt5-t6_shop-villa-golf%2526utm_content%253Dvne_banner_in-image-banner_nvwptt5-t6_shop-villa-golf_nvwpt&amp;otherlink=&amp;campaign_name=Shop%20Villa&amp;index_brand=LB1001.3_Novaland&amp;index_industrial=L1001_B%E1%BA%A5t%20%C4%91%E1%BB%99ng%20s%E1%BA%A3n\" frameborder=\"0\" marginwidth=\"0\" marginheight=\"0\" scrolling=\"no\"></iframe></div>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Theo đ&oacute;, muốn đạt hiệu quả cao trong&nbsp;<a href=\"https://vnexpress.net/tag/hoc-tap-10169\" target=\"_blank\" rel=\"dofollow noopener\">học tập</a>, c&ocirc;ng t&aacute;c cần ch&uacute; &yacute; k&ecirc; b&agrave;n ghế, đặt gi&aacute; s&aacute;ch theo c&aacute;c nguy&ecirc;n tắc sau đ&acirc;y:</p>\r\n<p class=\"Normal\">Ph&ograve;ng đọc, ph&ograve;ng l&agrave;m việc bố tr&iacute; ở phương hướng n&agrave;o cũng phải tu&acirc;n thủ nguy&ecirc;n tắc \"kỵ bối m&ocirc;n\", nghĩa l&agrave; k&ecirc; b&agrave;n sao cho người ngồi l&agrave;m việc, đọc hoặc viết kh&ocirc;ng quay lưng ra ph&iacute;a cửa, gồm cả cửa ch&iacute;nh v&agrave; cửa sổ.</p>\r\n<p class=\"Normal\">Trường hợp k&ecirc; b&agrave;n nh&igrave;n thẳng ra cửa, đ&ograve;i hỏi khoảng c&aacute;ch từ cửa ch&iacute;nh (hoặc cửa sổ) đến cạnh gần nhất của b&agrave;n phải lớn hơn 3 m. Khoảng trống từ cửa đến b&agrave;n học, l&agrave;m việc n&ecirc;n k&ecirc; đặt b&agrave;n uống nước hoặc chậu cảnh để h&oacute;a giải s&aacute;t kh&iacute;.</p>\r\n<p class=\"Normal\">B&agrave;n đọc s&aacute;ch, l&agrave;m việc kh&ocirc;ng được k&ecirc; ngay dưới x&agrave;, dầm nh&agrave; hoặc đ&egrave;n trần nhằm gi&uacute;p người đọc tập trung hơn, đồng thời bớt mệt mỏi, kh&ocirc;ng bị đau đầu, ch&oacute;ng mặt.</p>\r\n</div>\r\n<div id=\"admbackground\" data-set=\"dfp\"></div>\r\n<div id=\"divend\">\r\n<p class=\"Normal\">Nếu kh&ocirc;ng c&oacute; đ&egrave;n b&agrave;n, sử dụng đ&egrave;n trần hoặc đ&egrave;n tường, b&agrave;n đọc phải k&ecirc; sao cho b&oacute;ng đ&egrave;n kh&ocirc;ng vu&ocirc;ng g&oacute;c với khu vực đỉnh đầu, vai v&agrave; g&aacute;y của người ngồi đọc. Tương tự, b&agrave;n l&agrave;m việc kh&ocirc;ng được k&ecirc; ngay dưới v&agrave; vu&ocirc;ng g&oacute;c với quạt trần hoặc điều h&ograve;a nhiệt độ.</p>\r\n<p class=\"Normal\">Phạm cấm kỵ n&agrave;y thường khiến người ngồi đọc đau vai mỏi g&aacute;y, ngủ dậy mệt mỏi, sắc mặt nhợt nhạt. L&acirc;u dần sẽ mắc c&aacute;c bệnh về thần kinh v&agrave; thương h&agrave;n.</p>\r\n<p class=\"Normal\">Kh&ocirc;ng k&ecirc; đặt b&agrave;n l&agrave;m việc hoặc chỗ ngồi l&agrave;m việc ngay cửa ra v&agrave;o. Cửa ph&ograve;ng đọc n&ecirc;n mở ở một g&oacute;c nhất định, hạn chế mở cửa ch&iacute;nh giữa. C&oacute; thể sử dụng cửa sổ, cửa k&iacute;nh để lấy &aacute;nh s&aacute;ng, nhưng phải đảm bảo người ngồi đọc, l&agrave;m việc kh&ocirc;ng nh&igrave;n thấy cảnh vật b&ecirc;n ngo&agrave;i.</p>\r\n<p class=\"Normal\">Tr&ecirc;n b&agrave;n l&agrave;m việc, s&aacute;ch vở c&oacute; thể b&agrave;y cả hai b&ecirc;n tr&aacute;i v&agrave; phải người ngồi đọc, ri&ecirc;ng ống b&uacute;t nhất thiết phải đặt ở ph&iacute;a tay tr&aacute;i v&agrave; phải cao hơn những đồ vật kh&aacute;c b&ecirc;n phải.</p>\r\n<p class=\"Normal\">B&uacute;t l&ocirc;ng, b&uacute;t vẽ cắm trong ống b&uacute;t phải x&ograve;e ng&ograve;i như b&ocirc;ng hoa, kh&ocirc;ng được chụm lại như ngọn gi&aacute;o; hoặc sử dụng gi&aacute; để treo ng&ograve;i b&uacute;t hướng xuống dưới.</p>\r\n<p class=\"Normal\">Nghi&ecirc;n mực, lọ mực, ấn triện... đặt b&ecirc;n tay phải người ngồi l&agrave;m việc, b&agrave;y theo thứ tự từ cao đến thấp, từ trong ra ngo&agrave;i.</p>\r\n<p class=\"Normal\">Gi&aacute; s&aacute;ch c&oacute; thể k&ecirc; ở cả 3 ph&iacute;a, trước mặt, b&ecirc;n phải hoặc b&ecirc;n tr&aacute;i b&agrave;n đọc. Nhưng nếu điều kiện cho ph&eacute;p, k&ecirc; gi&aacute; s&aacute;ch b&ecirc;n ph&iacute;a tay tr&aacute;i của người ngồi đọc l&agrave; tốt nhất.</p>\r\n<p class=\"Normal\">Kh&ocirc;ng n&ecirc;n k&ecirc; gi&aacute; s&aacute;ch sau lưng người ngồi đọc. Nếu v&igrave; l&yacute; do n&agrave;o đ&oacute; phải k&ecirc; gi&aacute; s&aacute;ch ph&iacute;a sau lưng, phải đảm bảo sự chắc chắn, an to&agrave;n của gi&aacute; v&agrave; s&aacute;ch b&aacute;o b&agrave;y tr&ecirc;n đ&oacute;.</p>\r\n<p class=\"Normal\">Những ph&ograve;ng chuy&ecirc;n để đọc s&aacute;ch, s&aacute;ng t&aacute;c, ph&ograve;ng học của con c&aacute;i trong gia đ&igrave;nh, ngo&agrave;i việc tu&acirc;n thủ c&aacute;c nguy&ecirc;n tắc n&ecirc;u tr&ecirc;n, tốt nhất n&ecirc;n bố tr&iacute; linh hoạt, h&agrave;ng năm c&oacute; thể thay đổi vị tr&iacute; b&agrave;n ghế...</p>\r\n<p class=\"Normal\">Phong thủy Huyền kh&ocirc;ng đ&atilde; chứng nghiệm thực tế v&agrave; rất coi trọng việc k&ecirc; đặt b&agrave;n học ở vị tr&iacute; của sao Văn kh&uacute;c - chủ về văn học, tr&iacute; tuệ, quan vận. Văn kh&uacute;c quản hướng Đ&ocirc;ng Nam nhưng mỗi năm lại lưu chiếu v&agrave; cai quản một trong 9 cung thi&ecirc;n b&agrave;n, do đ&oacute; c&oacute; thể k&ecirc; đặt b&agrave;n học theo cung vị lưu chiếu h&agrave;ng năm của sao.</p>\r\n<p class=\"Normal\">Trường hợp kh&ocirc;ng thay đổi vị tr&iacute; ngồi theo quỹ đạo của sao Văn kh&uacute;c th&igrave; khi k&ecirc; đặt b&agrave;n học, b&agrave;n l&agrave;m việc tại c&aacute;c hướng tương sinh với Văn kh&uacute;c: Đ&ocirc;ng, Đ&ocirc;ng Nam, ch&iacute;nh Nam hoặc ch&iacute;nh Bắc.</p>\r\n<p class=\"Normal\">Trong v&ograve;ng 10 năm tới sao Văn kh&uacute;c lần lượt lưu chiếu (quản) ở c&aacute;c cung (hướng) sau: Năm 2022 tại hướng Đ&ocirc;ng Nam, 2023 tại Trung cung, 2024 tại hướng T&acirc;y Bắc, 2025 tại hướng T&acirc;y, 2026 tại hướng Đ&ocirc;ng Bắc, 2027 tại hướng Nam, 2028 tại Bắc, 2029 tại hướng T&acirc;y Nam, 2030 tại hướng Đ&ocirc;ng, 2031 tại hướng Đ&ocirc;ng Nam.</p>\r\n</div>', 218, 12, 37, 1, '2022-11-18 19:35:41', '2023-02-07 08:36:17'),
(57, 'Chủ tịch Vietravel: Đổi mới toàn diện là con đường duy nhất sau dịch', 'chu-tich-vietravel-doi-moi-toan-dien-la-con-duong-duy-nhat-sau-dich', 'Chủ tịch Vietravel Nguyễn Quốc Kỳ nhận định sau Covid-19, các doanh nghiệp du lịch dù lớn hay nhỏ đều đứng trước một vạch xuất phát, ai chậm đổi mới sẽ mất thị trường.', '<div id=\"divfirst\">\r\n<p class=\"Normal\">Từng v&iacute; von những mất m&aacute;t của doanh nghiệp trong Covid-19 như mỗi ng&agrave;y đẩy một chiếc Camry xuống s&ocirc;ng, &ocirc;ng Nguyễn Quốc Kỳ- Chủ tịch HĐQT Vietravel cho biết, ngay khi dịch ổn định, doanh nghiệp x&aacute;c định bắt đầu một \"trận đ&aacute;nh mới\".</p>\r\n<p class=\"Normal\">Cụ thể, to&agrave;n bộ m&aacute;y nhanh ch&oacute;ng bước ra khỏi t&igrave;nh trạng ngủ đ&ocirc;ng để c&oacute; thể \"r&atilde; đ&ocirc;ng t&iacute;ch cực\", ph&aacute;t triển v&agrave; giữ vững chỗ đứng tr&ecirc;n thị trường.</p>\r\n<p class=\"Normal\">\"Trong đại dịch l&agrave; ngủ đ&ocirc;ng t&iacute;ch cực, đến l&uacute;c r&atilde; đ&ocirc;ng cũng phải t&iacute;ch cực. Nếu kh&ocirc;ng, chuyển đổi chậm, trạng th&aacute;i chậm sẽ l&agrave;m doanh nghiệp hụt hơi v&agrave; kh&ocirc;ng theo kịp y&ecirc;u cầu thị trường\", &ocirc;ng nhấn mạnh trong talkshow The Next Power k&eacute;o d&agrave;i 45 ph&uacute;t ph&aacute;t s&oacute;ng tuần rồi tr&ecirc;n&nbsp;<em>VnExpress</em>.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-kinhdoanh.vnecdn.net/2022/06/20/Thumbnail-tap-6-1655-1655701609.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=85SvPYJxoF6rIA5DfBUTXQ 1x, https://i1-kinhdoanh.vnecdn.net/2022/06/20/Thumbnail-tap-6-1655-1655701609.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=aSID4zCbcE9RMVtFLXsClA 1.5x, https://i1-kinhdoanh.vnecdn.net/2022/06/20/Thumbnail-tap-6-1655-1655701609.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=oQ6PD-InbLJwjOb2jFRlBw 2x\" data-srcset=\"https://i1-kinhdoanh.vnecdn.net/2022/06/20/Thumbnail-tap-6-1655-1655701609.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=85SvPYJxoF6rIA5DfBUTXQ 1x, https://i1-kinhdoanh.vnecdn.net/2022/06/20/Thumbnail-tap-6-1655-1655701609.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=aSID4zCbcE9RMVtFLXsClA 1.5x, https://i1-kinhdoanh.vnecdn.net/2022/06/20/Thumbnail-tap-6-1655-1655701609.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=oQ6PD-InbLJwjOb2jFRlBw 2x\"><img class=\"lazy lazied\" src=\"https://i1-kinhdoanh.vnecdn.net/2022/06/20/Thumbnail-tap-6-1655-1655701609.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=85SvPYJxoF6rIA5DfBUTXQ\" alt=\"&Ocirc;ng Nguyễn Quốc Kỳ- Chủ tịch HĐQT Vietravel.\" loading=\"lazy\" data-src=\"https://i1-kinhdoanh.vnecdn.net/2022/06/20/Thumbnail-tap-6-1655-1655701609.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=85SvPYJxoF6rIA5DfBUTXQ\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">&Ocirc;ng Nguyễn Quốc Kỳ- Chủ tịch HĐQT Vietravel.</p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\"><strong>R&atilde; đ&ocirc;ng bằng đổi mới to&agrave;n diện</strong></p>\r\n<p class=\"Normal\">Chiến lược đổi mới của doanh nghiệp n&agrave;y được thực hiện nhanh ch&oacute;ng, tr&ecirc;n bề rộng với ba hướng ch&iacute;nh l&agrave; thay đổi cấu tr&uacute;c vận h&agrave;nh, nhận thức v&agrave; c&ocirc;ng nghệ. Đơn vị chuyển m&igrave;nh th&agrave;nh một tập đo&agrave;n du lịch, h&agrave;ng kh&ocirc;ng, tạo th&agrave;nh hệ sinh th&aacute;i tuần ho&agrave;n. Th&ecirc;m v&agrave;o đ&oacute;, doanh nghiệp tăng cường li&ecirc;n kết với nhiều tập đo&agrave;n, đơn vị để c&ugrave;ng vượt qua kh&oacute; khăn, r&atilde; đ&ocirc;ng nhanh v&agrave; hồi phục trở lại.</p>\r\n</div>\r\n<div id=\"admbackground\" data-set=\"dfp\"></div>\r\n<div id=\"divend\">\r\n<p class=\"Normal\">Trong đ&oacute;, c&ocirc;ng nghệ l&agrave; một trong những mũi nhọn được coi trọng. C&ocirc;ng nghệ hiện diện trong tất cả c&aacute;c kh&acirc;u, từ điều h&agrave;nh hệ thống, đến tạo sản phẩm, x&uacute;c tiến quảng b&aacute;, x&acirc;y dựng hệ thống b&aacute;n h&agrave;ng v&agrave; chăm s&oacute;c kh&aacute;ch h&agrave;ng.</p>\r\n<p class=\"Normal\">Sau dịch, Vietravel đ&oacute;ng bớt hệ thống offline, đẩy mạnh online, hoạt động tr&ecirc;n 3 s&agrave;n thương mại điện tử: Tiki, Shopee, Lazada. \"Đ&acirc;y l&agrave; sự thay đổi rất lớn về hệ b&aacute;n h&agrave;ng v&agrave; nhận thức trong qu&aacute; tr&igrave;nh chuyển đổi m&ocirc;i trường, cơ cấu kinh doanh\", chủ tịch doanh nghiệp nhận định.</p>\r\n<p class=\"Normal\">Thực hiện h&agrave;ng loạt đổi mới trong doanh nghiệp, &ocirc;ng Nguyễn Quốc Kỳ nhận định đổi tư duy l&agrave; đặc biệt kh&oacute;, nhất l&agrave; khi tư duy đ&oacute; từng đưa m&igrave;nh tới th&agrave;nh c&ocirc;ng. Từ g&oacute;c nh&igrave;n của người l&atilde;nh đạo &ocirc;ng chia sẻ: \"T&ocirc;i biết chấp nhận, bu&ocirc;ng bỏ những thứ m&igrave;nh nghĩ l&agrave; nguy&ecirc;n tắc trước dịch, phải bước ra khỏi v&ugrave;ng an to&agrave;n để kh&ocirc;ng trở n&ecirc;n nh&agrave;m ch&aacute;n, bị động, bảo thủ, từ đ&oacute; dễ d&agrave;ng tiếp nhận c&aacute;i mới\".</p>\r\n<p class=\"Normal\">Kh&ocirc;ng chỉ thay đổi nhận thức của bản th&acirc;n người l&agrave;m l&atilde;nh đạo, nhận thức của to&agrave;n bộ nh&acirc;n vi&ecirc;n cũng phải đổi mới để th&iacute;ch nghi. Theo &ocirc;ng Quốc Kỳ, đ&acirc;y l&agrave; một trong những việc kh&oacute; nhất. \"Tuy rằng m&igrave;nh c&oacute; &yacute; tưởng nhưng phải n&oacute;i cho mọi người c&ugrave;ng hiểu tại sao phải thay đổi, đ&ocirc;i l&uacute;c h&agrave;nh động n&agrave;y ảnh hưởng đến vị tr&iacute; c&ocirc;ng việc, quyền lợi của những c&aacute;n bộ, con người, bộ m&aacute;y đ&atilde; được định vị trước dịch. Từ đ&oacute; tạo th&agrave;nh những tranh c&atilde;i nội bộ. Ch&uacute;ng t&ocirc;i đ&atilde; phải l&agrave;m kỹ v&agrave; trao đổi với nhau, c&ugrave;ng th&ocirc;ng qua, thống nhất c&ugrave;ng l&agrave;m\".</p>\r\n<p class=\"Normal\">Sự đổi mới quyết liệt của &ocirc;ng lớn du lịch cũng thể hiện trong hoạt động t&aacute;i cơ cấu nguồn lực t&agrave;i ch&iacute;nh. Vietravel ưu ti&ecirc;n tập trung v&agrave;o những kh&acirc;u ch&iacute;nh, giữ vững c&aacute;c bộ phận cốt yếu. Trước dịch, doanh nghiệp c&oacute; 64 văn ph&ograve;ng đại diện tr&ecirc;n to&agrave;n quốc, sau dịch chỉ c&ograve;n lại 29. Theo &ocirc;ng Quốc Kỳ, đ&acirc;y l&agrave; bước đi bắt buộc để giảm bớt chi ph&iacute;, d&ugrave;ng nguồn lực đ&oacute; để phục vụ t&aacute;i cấu tr&uacute;c</p>\r\n<figure class=\"item_slide_show clearfix\">\r\n<div id=\"video_parent_357002\" class=\"box_embed_video_parent embed_video_new\" data-vcate=\"1003834\" data-vscate=\"1003006\" data-vscaten=\"Kinh doanh\" data-vid=\"357002\" data-ratio=\"1\" data-articleoriginal=\"4476641\" data-ads=\"1\" data-license=\"1\" data-duration=\"2406\" data-brandsafe=\"0\" data-type=\"0\" data-play=\"1\" data-frame=\"\" data-aot=\"Chủ tịch Vietravel: Sau \'ngủ đ&ocirc;ng\' l&agrave; \'r&atilde; đ&ocirc;ng\'\" data-videoclassify=\"None\" data-initdom=\"1\" data-view=\"inview\" data-auto=\"1\" data-status=\"play\">\r\n<div id=\"embed_video_357002\" class=\"box_embed_video\"></div>\r\n</div>\r\n</figure>\r\n<p class=\"Normal\">Kh&aacute;c với nhiều đại diện doanh nghiệp từng tham gia The Next Power v&agrave; chia sẻ quan điểm đổi mới từng phần, tiết kiệm, d&egrave; sẻn..., &ocirc;ng Nguyễn Quốc Kỳ cho rằng với một doanh nghiệp du lịch, đổi mới to&agrave;n diện l&agrave; con đường duy nhất. \"Sau dịch l&agrave; một x&atilde; hội mới, h&igrave;nh th&agrave;nh thị trường ti&ecirc;u thụ mới với những nhu cầu mới, nếu kh&ocirc;ng thay đổi nhanh m&agrave; vẫn lưu luyến c&aacute;i cũ th&igrave; sẽ bị bỏ lại. Đ&acirc;y l&agrave; thời điểm kh&ocirc;ng c&oacute; doanh nghiệp lớn, kh&ocirc;ng c&oacute; danh nghiệp nhỏ, tất cả đều đứng tr&ecirc;n một vạch xuất ph&aacute;t, nếu ai nhanh ch&acirc;n, chuẩn bị tốt sẽ nắm được thị trường\".</p>\r\n<p class=\"Normal\">Nh&igrave;n nhận về tương lai của thị trường, &ocirc;ng Nguyễn Quốc Kỳ dự đo&aacute;n ng&agrave;nh du lịch cần khoảng 2 -3 năm để chữa l&agrave;nh vết thương v&agrave; c&oacute; thể phần n&agrave;o hồi phục v&agrave;o năm 2024 - 2025. Đại dịch đ&atilde; để lại một vết sẹo kh&oacute; l&agrave;nh với ng&agrave;nh du lịch, kh&ocirc;ng một c&ocirc;ng ty n&agrave;o l&agrave; kh&ocirc;ng tổn thương, thậm ch&iacute;, nhiều đơn vị phải đ&oacute;ng cửa. Tuy nhi&ecirc;n những mất m&aacute;t h&ocirc;m nay sẽ để lại gi&aacute; trị, kinh nghiệm, tạo dựng điểm tựa cho tương lai.</p>\r\n</div>', 218, 4, 40, 1, '2022-11-18 19:35:41', '2023-02-06 20:30:45');
INSERT INTO `posts` (`id`, `title`, `slug`, `excerpt`, `body`, `user_id`, `category_id`, `views`, `approved`, `created_at`, `updated_at`) VALUES
(58, 'Ngắm nhìn nhan sắc 2 chiếc siêu xe hypercar McLaren Senna của đại gia Việt', 'ngam-nhin-nhan-sac-2-chiec-sieu-xe-hypercar-mclaren-senna-cua-dai-gia-viet', 'Trong khi chiếc siêu xe triệu đô McLaren Senna của Hoàng Kim Khánh sở hữu màu sơn Cerberus Pearl do bộ phận cá nhân hóa MSO tạo ra chỉ đúng cho 2 xe Senna trên thế giới', '<p>V&agrave;o chiều ng&agrave;y 16 th&aacute;ng 6 năm 2022, một showroom si&ecirc;u xe ở Th&agrave;nh phố Hồ Ch&iacute; Minh đ&atilde; cho chiếc hypercar McLaren Senna của m&igrave;nh nhập về Việt Nam hơn 3 th&aacute;ng qua xuất hiện c&ocirc;ng khai tr&ecirc;n đường phố S&agrave;i th&agrave;nh đ&atilde; khiến giới m&ecirc; xe trong nước kh&aacute; vui mừng. Như vậy, c&aacute;c đại gia Việt đ&atilde; sở hữu 2 chiếc xe McLaren Senna tr&ecirc;n tổng số 500 xe được sản xuất to&agrave;n cầu.</p>\r\n<div class=\"width-100 inline\">\r\n<div id=\"ads-detail-below-sapo-320x180\" class=\"box-ads-anymind\"></div>\r\n</div>\r\n<p>Trong khi chiếc si&ecirc;u xe triệu đ&ocirc; McLaren Senna đầu ti&ecirc;n xuất hiện tại Việt Nam của Ho&agrave;ng Kim Kh&aacute;nh sở hữu m&agrave;u sơn Cerberus Pearl do bộ phận c&aacute; nh&acirc;n h&oacute;a MSO tạo ra chỉ đ&uacute;ng cho 2 xe Senna tr&ecirc;n thế giới, th&igrave; chiếc McLaren Senna thứ 2 xuất hiện tại Việt Nam lại sở hữu bộ &aacute;o lấy cảm hứng từ chiếc xe F1 của tay đua huyền thoại Ayrton Senna nhưng lại được d&aacute;n bằng đề-can. M&agrave;u sơn gốc của xe l&agrave; trắng.</p>\r\n<p>H&atilde;y c&ugrave;ng&nbsp;<a href=\"http://tinxe.vn/tin-xe/ngam-nhin-nhan-sac-2-chiec-sieu-xe-hypercar-mclaren-senna-cua-dai-gia-viet-id20220617093317735\"><strong>ngắm nh&igrave;n nhan sắc 2 chiếc si&ecirc;u xe hypercar McLaren Senna của đại gia Việt</strong></a></p>\r\n<div class=\"dvg_photo_box\">\r\n<figure><span class=\"lozadload-content\"><img class=\"lozad\" title=\"Chiếc McLaren Senna m&agrave;u Cerberus Pearl mang biển số t&ecirc;n vợ của Ho&agrave;ng Kim Kh&aacute;nh\" src=\"https://img.tinxe.vn/resize/1000x-/2022/06/17/95ywCUPr/278707488-2266447873498249-161612568782906739-n-6bb9.jpg\" alt=\"Chiếc McLaren Senna m&agrave;u Cerberus Pearl mang biển số t&ecirc;n vợ của Ho&agrave;ng Kim Kh&aacute;nh\" width=\"1600\" height=\"1599\" data-height=\"1599\" data-width=\"1600\" data-src=\"https://img.tinxe.vn/resize/1000x-/2022/06/17/95ywCUPr/278707488-2266447873498249-161612568782906739-n-6bb9.jpg\" data-loaded=\"true\"></span></figure>\r\n<p><em>Chiếc McLaren Senna m&agrave;u Cerberus Pearl mang biển số t&ecirc;n vợ của Ho&agrave;ng Kim Kh&aacute;nh</em></p>\r\n</div>\r\n<p>Chiều ng&agrave;y 18 th&aacute;ng 4 năm 2020, h&igrave;nh ảnh khui c&ocirc;ng chiếc si&ecirc;u xe McLaren Senna m&agrave;u Cerberus Pearl đ&atilde; được nhiều trang về si&ecirc;u xe của Campuchia chia sẻ v&agrave; kh&ocirc;ng qu&aacute; kh&oacute; để giới m&ecirc; xe Việt nhận biết đ&acirc;y l&agrave; si&ecirc;u phẩm do Ho&agrave;ng Kim Kh&aacute;nh nhập về nước bạn để l&agrave;m c&aacute;c thủ tục đăng k&yacute; biển số theo t&ecirc;n m&igrave;nh y&ecirc;u th&iacute;ch.</p>\r\n<div class=\"width-100 inline\">\r\n<div id=\"ads-detail-below-320x250\" class=\"box-ads-anymind\"></div>\r\n</div>\r\n<p>Chiếc xe McLaren Senna n&agrave;y sau đ&oacute; được 2 người Việt điều khiển tr&ecirc;n đường phố Campuchia v&agrave; mất 3 th&aacute;ng sau đ&oacute;, si&ecirc;u phẩm McLaren Senna m&agrave;u Cerberus Pearl n&agrave;y mới được vận chuyển qua cửa khẩu Việt Nam ở tỉnh B&igrave;nh Phước trước khi được xe cứu hộ chở thẳng về nh&agrave; của Ho&agrave;ng Kim Kh&aacute;nh.</p>\r\n<div class=\"dvg_photo_box\">\r\n<figure><span class=\"lozadload-content\"><img class=\"lozad\" title=\"Chỉ c&oacute; đ&uacute;ng 2 xe McLaren Senna mang m&agrave;u Cerberus Pearl được sản xuất\" src=\"https://img.tinxe.vn/resize/1000x-/2022/06/17/95ywCUPr/278843828-2265250980284605-1453967506813895518-n-0566.jpg\" alt=\"Chỉ c&oacute; đ&uacute;ng 2 xe McLaren Senna mang m&agrave;u Cerberus Pearl được sản xuất\" width=\"1284\" height=\"801\" data-height=\"801\" data-width=\"1284\" data-src=\"https://img.tinxe.vn/resize/1000x-/2022/06/17/95ywCUPr/278843828-2265250980284605-1453967506813895518-n-0566.jpg\" data-loaded=\"true\"></span></figure>\r\n<p><em>Chỉ c&oacute; đ&uacute;ng 2 xe McLaren Senna mang m&agrave;u Cerberus Pearl được sản xuất</em></p>\r\n</div>\r\n<p>McLaren Senna cũng l&agrave; si&ecirc;u xe đầu ti&ecirc;n được Ho&agrave;ng Kim Kh&aacute;nh chọn chơi theo biển số Campuchia, điểm th&uacute; vị của xe ch&iacute;nh l&agrave; m&agrave;u sơn t&aacute;n sắc Cerberus Pearl chỉ c&oacute; đ&uacute;ng 2 xe Senna tr&ecirc;n thế giới sở hữu, chiếc c&ograve;n lại đang định cư tại Nhật Bản.</p>\r\n<p>Sau 2 năm c&oacute; mặt tại Việt Nam, chiếc si&ecirc;u xe triệu đ&ocirc; McLaren Senna của Ho&agrave;ng Kim Kh&aacute;nh vẫn thường xuy&ecirc;n được chủ nh&acirc;n mang ra chạy tr&ecirc;n đường phố tại S&agrave;i th&agrave;nh hay gần đ&acirc;y nhất l&agrave; \"phượt\" đi Đắk N&ocirc;ng cũng như chạy từ H&agrave; Nội đi Quảng Ninh v&agrave; Hải Dương.</p>\r\n<div class=\"dvg_photo_box\">\r\n<figure><span class=\"lozadload-content\"><img class=\"lozad\" title=\"McLaren Senna v&agrave; Koenigsegg Regera của Ho&agrave;ng Kim Kh&aacute;nh được đăng k&yacute; biển số Campuchia, d&agrave;n si&ecirc;u xe c&ograve;n lại đều ra biển trắng của Việt Nam\" src=\"https://img.tinxe.vn/resize/1000x-/2022/06/17/95ywCUPr/276228262-2240885099387860-1754243828152925011-n-b74d.jpg\" alt=\"McLaren Senna v&agrave; Koenigsegg Regera của Ho&agrave;ng Kim Kh&aacute;nh được đăng k&yacute; biển số Campuchia, d&agrave;n si&ecirc;u xe c&ograve;n lại đều ra biển trắng của Việt Nam\" width=\"1284\" height=\"711\" data-height=\"711\" data-width=\"1284\" data-src=\"https://img.tinxe.vn/resize/1000x-/2022/06/17/95ywCUPr/276228262-2240885099387860-1754243828152925011-n-b74d.jpg\" data-loaded=\"true\"></span></figure>\r\n<p><em>McLaren Senna v&agrave; Koenigsegg Regera của Ho&agrave;ng Kim Kh&aacute;nh được đăng k&yacute; biển số Campuchia, d&agrave;n si&ecirc;u xe c&ograve;n lại đều ra biển trắng của Việt Nam</em></p>\r\n</div>\r\n<p><a href=\"https://tinxe.vn/gia-xe-mclaren-senna\"><strong>Gi&aacute; lăn b&aacute;nh xe McLaren Senna</strong></a>&nbsp;của Ho&agrave;ng Kim Kh&aacute;nh khi l&agrave;m xong c&aacute;c thủ tục đăng k&yacute; biển số tại đất nước Campuchia được tiết lộ 40 tỷ đồng, bằng gi&aacute; xe Ferrari 488 Pista Spider v&agrave; Lamborghini Aventador S LP740-4 m&agrave; đại gia n&agrave;y cho ra biển trắng.</p>\r\n<p>Ch&iacute;nh v&igrave; điều n&agrave;y m&agrave; chủ nh&acirc;n của Koenigsegg Regera từng chia sẻ chơi xe b&acirc;y giờ t&ugrave;y v&agrave;o ho&agrave;n cảnh v&agrave; t&agrave;i ch&iacute;nh, việc ra biển số n&agrave;o kh&ocirc;ng quan trọng lắm, miễn sao c&oacute; xe sớm nhất để trải nghiệm.</p>\r\n<div class=\"dvg_photo_box\">\r\n<figure><span class=\"lozadload-content\"><img class=\"lozad\" title=\"McLaren Senna thứ 2 mới lộ ảnh c&ocirc;ng khai c&oacute; mặt tại Việt Nam\" src=\"https://img.tinxe.vn/resize/1000x-/2022/06/17/95ywCUPr/-d37d.jpg\" alt=\"McLaren Senna thứ 2 mới lộ ảnh c&ocirc;ng khai c&oacute; mặt tại Việt Nam\" width=\"1600\" height=\"1049\" data-height=\"1049\" data-width=\"1600\" data-src=\"https://img.tinxe.vn/resize/1000x-/2022/06/17/95ywCUPr/-d37d.jpg\" data-loaded=\"true\"></span></figure>\r\n<p><em>McLaren Senna thứ 2 mới lộ ảnh c&ocirc;ng khai c&oacute; mặt tại Việt Nam</em></p>\r\n</div>\r\n<p>Hơn 1 năm chiếc si&ecirc;u xe McLaren Senna của Ho&agrave;ng Kim Kh&aacute;nh xuất hiện tại Việt Nam, giới m&ecirc; xe trong nước lại dậy s&oacute;ng khi th&ocirc;ng tin một chiếc xe McLaren Senna mang m&agrave;u &aacute;o lấy cảm hứng từ chiếc xe F1 của tay đua huyền thoại Ayrton Senna sắp c&oacute; mặt trong nước.</p>\r\n<p>Nhưng phải đến th&aacute;ng 3 đầu năm nay, tin tức của&nbsp;<a href=\"https://tinxe.vn/\"><strong>Tinxe.vn</strong></a>&nbsp;mới x&aacute;c nhận chiếc McLaren Senna thứ 2 tại Việt Nam đ&atilde; l&agrave;m xong c&aacute;c thủ tục th&ocirc;ng quan v&agrave; được mang về 1 showroom b&aacute;n xe tại huyện B&igrave;nh Ch&aacute;nh để giấu, sau n&agrave;y, chiếc McLaren Senna được mang đến 1 showroom si&ecirc;u xe ở quận 1 nhưng vẫn được tr&ugrave;m bạt. V&agrave; đến chiều h&ocirc;m qua, chiếc si&ecirc;u xe McLaren Senna đầu ti&ecirc;n nhập về Việt Nam mới ch&iacute;nh thức lộ diện.</p>\r\n<div class=\"dvg_photo_box\">\r\n<figure><span class=\"lozadload-content\"><img class=\"lozad\" title=\"McLaren Senna thứ 2 xuất hiện tại Việt Nam nguy&ecirc;n bản c&oacute; m&agrave;u trắng\" src=\"https://img.tinxe.vn/resize/1000x-/2022/06/17/95ywCUPr/z3497489972000-412f9c7cf6ece6634dfbad2f0011c082-4188.jpg\" alt=\"McLaren Senna thứ 2 xuất hiện tại Việt Nam nguy&ecirc;n bản c&oacute; m&agrave;u trắng\" width=\"1600\" height=\"1740\" data-height=\"1740\" data-width=\"1600\" data-src=\"https://img.tinxe.vn/resize/1000x-/2022/06/17/95ywCUPr/z3497489972000-412f9c7cf6ece6634dfbad2f0011c082-4188.jpg\" data-loaded=\"true\"></span></figure>\r\n<p><em>McLaren Senna thứ 2 xuất hiện tại Việt Nam nguy&ecirc;n bản c&oacute; m&agrave;u trắng</em></p>\r\n</div>\r\n<div class=\"dvg_photo_box\">\r\n<figure><span class=\"lozadload-content\"><img class=\"lozad\" title=\"Nhưng sau đ&oacute; vị chủ nh&acirc;n cũ của xe đ&atilde; tưởng nhớ Ayrton Senna n&ecirc;n đ&atilde; cho d&aacute;n lại bộ &aacute;o gợi nhớ chiếc xe McLaren MP4/4 m&agrave; tay đua Brasil đ&atilde; từng cầm l&aacute;i d&agrave;nh chức v&ocirc; địch v&agrave;o năm 1988\" src=\"https://img.tinxe.vn/resize/1000x-/2022/06/17/95ywCUPr/z3497490110969-e573a3d4f0fb2a1276bd24301b34aa9f-095b.jpg\" alt=\"Nhưng sau đ&oacute; vị chủ nh&acirc;n cũ của xe đ&atilde; tưởng nhớ Ayrton Senna n&ecirc;n đ&atilde; cho d&aacute;n lại bộ &aacute;o gợi nhớ chiếc xe McLaren MP4/4 m&agrave; tay đua Brasil đ&atilde; từng cầm l&aacute;i d&agrave;nh chức v&ocirc; địch v&agrave;o năm 1988\" width=\"1600\" height=\"1566\" data-height=\"1566\" data-width=\"1600\" data-src=\"https://img.tinxe.vn/resize/1000x-/2022/06/17/95ywCUPr/z3497490110969-e573a3d4f0fb2a1276bd24301b34aa9f-095b.jpg\" data-loaded=\"true\"></span></figure>\r\n<p><em>Nhưng sau đ&oacute; vị chủ nh&acirc;n cũ của xe đ&atilde; tưởng nhớ Ayrton Senna n&ecirc;n đ&atilde; cho d&aacute;n lại bộ &aacute;o gợi nhớ chiếc xe McLaren MP4/4 m&agrave; tay đua Brasil đ&atilde; từng cầm l&aacute;i d&agrave;nh chức v&ocirc; địch v&agrave;o năm 1988</em></p>\r\n</div>\r\n<div class=\"dvg_photo_box\">\r\n<figure><span class=\"lozadload-content\"><img class=\"lozad\" title=\"C&ugrave;ng với đ&oacute; l&agrave; c&aacute;c th&ocirc;ng tin về 41 chiến thắng tr&ecirc;n 17 trường đua kh&aacute;c nhau của tay đua Ayrton Senna cũng xuất hiện tr&ecirc;n bộ &aacute;o n&agrave;y\" src=\"https://img.tinxe.vn/resize/1000x-/2022/06/17/95ywCUPr/z3497489971793-4d418431872fb127b9c1772e9ec0f6b8-ca2f.jpg\" alt=\"C&ugrave;ng với đ&oacute; l&agrave; c&aacute;c th&ocirc;ng tin về 41 chiến thắng tr&ecirc;n 17 trường đua kh&aacute;c nhau của tay đua Ayrton Senna cũng xuất hiện tr&ecirc;n bộ &aacute;o n&agrave;y\" width=\"1600\" height=\"1519\" data-height=\"1519\" data-width=\"1600\" data-src=\"https://img.tinxe.vn/resize/1000x-/2022/06/17/95ywCUPr/z3497489971793-4d418431872fb127b9c1772e9ec0f6b8-ca2f.jpg\" data-loaded=\"true\"></span></figure>\r\n<p><em>C&ugrave;ng với đ&oacute; l&agrave; c&aacute;c th&ocirc;ng tin về 41 chiến thắng tr&ecirc;n 17 trường đua kh&aacute;c nhau của tay đua Ayrton Senna cũng xuất hiện tr&ecirc;n bộ &aacute;o n&agrave;y</em></p>\r\n</div>\r\n<p>Ngoại thất của chiếc si&ecirc;u xe McLaren Senna thứ 2 tại Việt Nam nguy&ecirc;n bản c&oacute; m&agrave;u trắng nhưng đ&atilde; được chủ nh&acirc;n cũ cho l&ecirc;n bộ &aacute;o mới&nbsp;theo phương ph&aacute;p d&aacute;n đề-can, &yacute; tưởng đến từ m&agrave;u sơn của chiếc xe C&ocirc;ng thức 1 McLaren MP4/4 m&agrave; Ayrton Senna đ&atilde; cầm l&aacute;i gi&agrave;nh chức v&ocirc; địch v&agrave;o năm 1988.</p>\r\n<p>Điểm nhấn ch&iacute;nh l&agrave; 41 chiến thắng tr&ecirc;n 17 trường đua kh&aacute;c nhau của tay đua người Brasil n&agrave;y cũng xuất hiện tr&ecirc;n ngoại thất xe Senna v&agrave; cản va sau c&ograve;n c&oacute; d&ograve;ng chữ thể hiện c&acirc;u n&oacute;i kinh điển của Ayrton Senna ch&iacute;nh l&agrave;&nbsp;<em>\"T&ocirc;i sinh ra kh&ocirc;ng phải đứng thứ 2 hoặc thứ 3. T&ocirc;i sinh ra để chiến thắng\".</em></p>\r\n<div class=\"dvg_photo_box\">\r\n<figure><span class=\"lozadload-content\"><img class=\"lozad\" title=\"Bộ &aacute;o n&agrave;y từng thu h&uacute;t sự quan t&acirc;m của giới truyền th&ocirc;ng quốc tế. T&ugrave;y chọn tr&ecirc;n xe McLaren Senna n&agrave;y bao gồm ốp c&aacute;ch nhiệt động cơ bằng l&aacute; v&agrave;ng v&agrave; t&iacute;ch hợp phần mềm Track Telemetry, gi&uacute;p người l&aacute;i ph&acirc;n t&iacute;ch v&agrave; ghi lại h&agrave;nh tr&igrave;nh của xe tr&ecirc;n đường đua. \" src=\"https://img.tinxe.vn/resize/1000x-/2022/06/17/95ywCUPr/z3497490007673-d5599024b5d284e3e4d61501c3fc22fa-2959.jpg\" alt=\"Bộ &aacute;o n&agrave;y từng thu h&uacute;t sự quan t&acirc;m của giới truyền th&ocirc;ng quốc tế. T&ugrave;y chọn tr&ecirc;n xe McLaren Senna n&agrave;y bao gồm ốp c&aacute;ch nhiệt động cơ bằng l&aacute; v&agrave;ng v&agrave; t&iacute;ch hợp phần mềm Track Telemetry, gi&uacute;p người l&aacute;i ph&acirc;n t&iacute;ch v&agrave; ghi lại h&agrave;nh tr&igrave;nh của xe tr&ecirc;n đường đua. \" width=\"1600\" height=\"1056\" data-height=\"1056\" data-width=\"1600\" data-src=\"https://img.tinxe.vn/resize/1000x-/2022/06/17/95ywCUPr/z3497490007673-d5599024b5d284e3e4d61501c3fc22fa-2959.jpg\" data-loaded=\"true\"></span></figure>\r\n<p><em>Bộ &aacute;o n&agrave;y từng thu h&uacute;t sự quan t&acirc;m của giới truyền th&ocirc;ng quốc tế. T&ugrave;y chọn tr&ecirc;n xe McLaren Senna n&agrave;y bao gồm ốp c&aacute;ch nhiệt động cơ bằng l&aacute; v&agrave;ng v&agrave; t&iacute;ch hợp phần mềm Track Telemetry, gi&uacute;p người l&aacute;i ph&acirc;n t&iacute;ch v&agrave; ghi lại h&agrave;nh tr&igrave;nh của xe tr&ecirc;n đường đua.</em></p>\r\n</div>\r\n<div class=\"dvg_photo_box\">\r\n<figure><span class=\"lozadload-content\"><img class=\"lozad\" title=\"Một phần cửa xe McLaren Senna được l&agrave;m từ k&iacute;nh trong suốt\" src=\"https://img.tinxe.vn/resize/1000x-/2022/06/17/95ywCUPr/z3497490118055-24378decd39ad66120a242223de70054-77dc.jpg\" alt=\"Một phần cửa xe McLaren Senna được l&agrave;m từ k&iacute;nh trong suốt\" width=\"1600\" height=\"1115\" data-height=\"1115\" data-width=\"1600\" data-src=\"https://img.tinxe.vn/resize/1000x-/2022/06/17/95ywCUPr/z3497490118055-24378decd39ad66120a242223de70054-77dc.jpg\" data-loaded=\"true\"></span></figure>\r\n<p><em>Một phần cửa xe McLaren Senna được l&agrave;m từ k&iacute;nh trong suốt</em></p>\r\n</div>\r\n<div class=\"dvg_photo_box\">\r\n<figure><span class=\"lozadload-content\"><img class=\"lozad\" title=\"Gi&aacute; lăn b&aacute;nh của xe McLaren Senna n&agrave;y c&oacute; thể tr&ecirc;n 80 tỷ đồng nếu đăng k&yacute; biển trắng, c&ograve;n c&aacute;c loại biển số kh&aacute;c tại Việt Nam, chi ph&iacute; c&oacute; thể kh&ocirc;ng tới 42 tỷ đồng\" src=\"https://img.tinxe.vn/resize/1000x-/2022/06/17/95ywCUPr/z3497490101136-e34b84861cf153a54f5ca4aed48dad50-1be7.jpg\" alt=\"Gi&aacute; lăn b&aacute;nh của xe McLaren Senna n&agrave;y c&oacute; thể tr&ecirc;n 80 tỷ đồng nếu đăng k&yacute; biển trắng, c&ograve;n c&aacute;c loại biển số kh&aacute;c tại Việt Nam, chi ph&iacute; c&oacute; thể kh&ocirc;ng tới 42 tỷ đồng\" width=\"1600\" height=\"1280\" data-height=\"1280\" data-width=\"1600\" data-src=\"https://img.tinxe.vn/resize/1000x-/2022/06/17/95ywCUPr/z3497490101136-e34b84861cf153a54f5ca4aed48dad50-1be7.jpg\" data-loaded=\"true\"></span></figure>\r\n<p><em>Gi&aacute; lăn b&aacute;nh của xe McLaren Senna n&agrave;y c&oacute; thể tr&ecirc;n 80 tỷ đồng nếu đăng k&yacute; biển trắng, c&ograve;n c&aacute;c loại biển số kh&aacute;c tại Việt Nam, chi ph&iacute; c&oacute; thể kh&ocirc;ng tới 42 tỷ đồng</em></p>\r\n</div>\r\n<p>D&ugrave; c&oacute; kh&aacute;c biệt ở bộ &aacute;o cũng như trang bị trong nội thất, nhưng cả 2 xe McLaren Senna tại Việt Nam đều c&oacute; trang bị ống xả k&eacute;p, mang đến &acirc;m thanh phấn k&iacute;ch hơn so với nhiều xe Senna trang bị 3 ống xả, ngo&agrave;i ra, hệ truyền động của xe vẫn sử dụng khối động cơ V8, dung t&iacute;ch 4.0 l&iacute;t, tăng &aacute;p k&eacute;p sản sinh ra c&ocirc;ng suất tối đa 789 m&atilde; lực v&agrave; m&ocirc;-men xoắn cực đại 800 Nm.</p>\r\n<p>Sức mạnh của si&ecirc;u phẩm McLaren Senna đầu ti&ecirc;n v&agrave; thứ 2 xuất hiện tại Việt Nam được truyền về trục b&aacute;nh sau th&ocirc;ng qua hộp số ly hợp k&eacute;p 7 cấp, nhờ đ&oacute;, mẫu xe hypercar của Anh quốc chỉ cần thời gian 2,8 gi&acirc;y để tăng tốc từ vị tr&iacute; xuất ph&aacute;t l&ecirc;n 100 km/h trước khi đạt vận tốc tối đa 341 km/h.</p>\r\n<p>Một th&ocirc;ng tin quan trọng, giới đại gia Việt vẫn c&ograve;n sở hữu 1 chiếc xe McLaren Senna kh&aacute;c nhưng chưa được chia sẻ, chỉ biết rằng, si&ecirc;u phẩm n&agrave;y c&oacute; gi&aacute; tr&ecirc;n 2,6 triệu đ&ocirc; la tại nước ngo&agrave;i v&agrave; rất hiếm được nh&igrave;n thấy lăn b&aacute;nh ngo&agrave;i đường phố tr&ecirc;n thế giới.</p>', 218, 13, 22, 1, '2022-11-18 19:35:41', '2023-02-07 08:31:22'),
(61, 'Honda tiếp tục hé lộ thiết kế môtô Hornet mới, ngoại hình như KTM Super Duke R', 'honda-tiep-tuc-he-lo-thiet-ke-moto-hornet-moi-ngoai-hinh-nhu-ktm-super-duke-r', 'Honda New Hornet có thiết kế Naked Roadster với yên xe thấp, chiều dài cơ sở ngắn khá chiến.', '<p>Sau khi c&ocirc;ng bố th&ocirc;ng tin về mẫu&nbsp;Honda New Hornet&nbsp;v&agrave;o cuối năm 2021, mới đ&acirc;y Honda vừa đăng tải một số h&igrave;nh ảnh đầu ti&ecirc;n về &yacute; tưởng thiết kế cho mẫu xe Naked bike mới. Đ&aacute;ng ch&uacute; &yacute; ch&iacute;nh l&agrave; t&ecirc;n xe Hornet từng được sử dụng cho mẫu CB600F hạng trung đ&atilde; được hồi sinh tr&ecirc;n mẫu xe mới.</p>\r\n<div class=\"width-100 inline\">\r\n<div id=\"ads-detail-below-sapo-320x180\" class=\"box-ads-anymind\"></div>\r\n</div>\r\n<div class=\"dvg_photo_box\">\r\n<figure><span class=\"lozadload-content\"><img class=\"lozad\" title=\"Honda New Hornet\" src=\"https://img.tinxe.vn/resize/1000x-/2022/06/08/XForF7yt/honda-hornet-concept-02-03c0.jpg\" alt=\"Honda New Hornet\" width=\"600\" height=\"400\" data-height=\"400\" data-width=\"600\" data-src=\"https://img.tinxe.vn/resize/1000x-/2022/06/08/XForF7yt/honda-hornet-concept-02-03c0.jpg\" data-loaded=\"true\"></span></figure>\r\n<p class=\"dvg_photo_caption\">Thiết kế ph&aacute;c thảo của Honda New Hornet</p>\r\n</div>\r\n<p>Nh&igrave;n v&agrave;o h&igrave;nh tr&ecirc;n, c&oacute; thể thấy mẫu Honda New Hornet mới sẽ mang ngoại h&igrave;nh của một chiếc naked roadster với đặc trưng l&agrave; y&ecirc;n xe thấp, chiều d&agrave;i cơ sở ngắn, b&igrave;nh xăng thu&ocirc;n nhọn v&agrave; d&egrave;n pha dạng đơn. Nhưng điều đ&aacute;ng n&oacute;i ở đ&acirc;y l&agrave; Honda New Hornet c&oacute; ngoại h&igrave;nh khiến nhiều người li&ecirc;n tưởng ngay đến mẫu KTM Super Duke R với đường n&eacute;t thiết kế sắc sảo tương tự v&agrave; trang bị&nbsp;khung ống th&eacute;p dạng lưới mắt c&aacute;o.</p>\r\n<div class=\"dvg_photo_box\">\r\n<figure><span class=\"lozadload-content\"><img class=\"lozad\" title=\"Honda New Hornet\" src=\"https://img.tinxe.vn/resize/1000x-/2022/06/08/XForF7yt/honda-hornet-concept-01-c120.jpg\" alt=\"Honda New Hornet\" width=\"600\" height=\"400\" data-height=\"400\" data-width=\"600\" data-src=\"https://img.tinxe.vn/resize/1000x-/2022/06/08/XForF7yt/honda-hornet-concept-01-c120.jpg\" data-loaded=\"true\"></span></figure>\r\n<p class=\"dvg_photo_caption\">Thiết kế của Honda New Hornet khiến nhiều người li&ecirc;n tưởng đến mẫu KTM Super Duke R hay KTM 890 Duke R</p>\r\n</div>\r\n<p>C&aacute;c điểm nhấn về thiết kế kh&aacute;c c&oacute; thể kể đến phuộc trước h&agrave;nh tr&igrave;nh ngược được bố tr&iacute; kh&aacute; cho&atilde;i, ghi đ&ocirc;ng c&oacute; thể xem l&agrave; thấp tạo n&ecirc;n tư thế hơi chồm. Phần đu&ocirc;i xe c&oacute; thiết kế kh&aacute; ngắn, trang bị gắp đ&ocirc;i ph&iacute;a sau v&agrave; ống xả vuốt thẳng.</p>\r\n<div class=\"width-100 inline\">\r\n<div id=\"ads-detail-below-320x250\" class=\"box-ads-anymind\"></div>\r\n</div>\r\n<div class=\"dvg_photo_box\">\r\n<figure><span class=\"lozadload-content\"><img class=\"lozad\" title=\"Honda New Hornet\" src=\"https://img.tinxe.vn/resize/1000x-/2022/06/08/XForF7yt/honda-hornet-concept-03-5891.jpg\" alt=\"Honda New Hornet\" width=\"600\" height=\"400\" data-height=\"400\" data-width=\"600\" data-src=\"https://img.tinxe.vn/resize/1000x-/2022/06/08/XForF7yt/honda-hornet-concept-03-5891.jpg\" data-loaded=\"true\"></span></figure>\r\n<p class=\"dvg_photo_caption\">Tạo h&igrave;nh b&igrave;nh xăng Honda New Hornet</p>\r\n</div>\r\n<p>Ngo&agrave;i sự tương đồng về thiết kế, đương nhi&ecirc;n Honda New Hornet kh&ocirc;ng hề giống với mẫu m&ocirc;t&ocirc; của &Aacute;o về mặt trang bị, đặc biệt l&agrave; động cơ. V&agrave;o cuối năm ngo&aacute;i, Honda đ&atilde; đăng đoạn video ngắn tiết lộ rằng Honda New Hornet sẽ trang bị động cơ xylanh đ&ocirc;i với tua m&aacute;y kh&aacute; cao. Đ&aacute;ng tiếc l&agrave; th&ocirc;ng số kỹ thuật chi tiết của xe vẫn chưa được h&eacute; lộ.</p>\r\n<p>Việc Honda trang bị động cơ xylanh đ&ocirc;i cho mẫu New Hornet kh&aacute; dễ hiểu, hiện nay ti&ecirc;u chuẩn kh&iacute; thải đang ng&agrave;y c&agrave;ng khắt khe hơn, việc ph&aacute;t triển động cơ xylanh đ&ocirc;i cũng sẽ đẽ d&agrave;ng hơn, trọng lượng nhẹ&nbsp;so với động cơ 4 xylanh.&nbsp;Vậy n&ecirc;n động cơ hai xylanh song song sẽ l&agrave; lựa chọn ho&agrave;n hảo cho mẫu naked bike hạng trung cố gi&aacute; th&agrave;nh hấp dẫn.</p>\r\n<p>Hiện tại, d&ograve;ng&nbsp;naked bike hạng trung với gi&aacute; th&agrave;nh hợp l&yacute; đang kh&aacute; được ưa chuộng, điển h&igrave;nh l&agrave; mẫu MT-07 của Yamaha với trang bị động cơ CP2 2 xylanh&nbsp;đang \"l&agrave;m mưa l&agrave;m gi&oacute;\" ở nhiều thị trường. Ngo&agrave;i ra, d&ograve;ng động cơ xylanh đ&ocirc;i mới của Yamaha cũng sẽ được trang bị cho nhiều mẫu xe kh&aacute;c c&ugrave;ng ph&acirc;n kh&uacute;c, v&iacute; dụ như mẫu Transalp với thiết kế Adventure hạng trung ho&agrave;n to&agrave;n mới.</p>\r\n<p>Được biết, Honda New Hornet vẫn đang trong qu&aacute; tr&igrave;nh ph&aacute;t triển tại trung t&acirc;m nghi&ecirc;n cứu v&agrave; ph&aacute;t triển sản phẩm của Honda tại Rome, do nh&agrave; thiết kế Giovanni Dovis thực hiện. Giovanni Dovis cũng ch&iacute;nh l&agrave; nh&agrave; thiết kế đứng sau sản phẩm Honda ADV 350 của h&atilde;ng. Dự kiến Honda New Hornet sẽ ch&iacute;nh thức ra mắt thị trường v&agrave;o cuối năm nay.</p>', 218, 13, 32, 1, '2022-11-18 19:35:41', '2023-02-07 08:31:47'),
(67, 'Hà Hồ - Kim Lý tái hiện MV \'Cả một trời thương nhớ\'', 'ha-ho---kim-ly-tai-hien-mv-ca-mot-troi-thuong-nho', 'Hồ Ngọc Hà chụp bộ ảnh bên Kim Lý cảm hứng từ \"Cả một trời thương nhớ\" - MV xe duyên họ - dịp cô đón tuổi 36.', '<div id=\"divfirst\">\r\n<p class=\"Normal\">Nữ ca sĩ h&oacute;a qu&yacute; c&ocirc; với đầm xẻ ngực &ocirc;m s&aacute;t, cho&agrave;ng &aacute;o l&ocirc;ng.&nbsp;<a href=\"https://vnexpress.net/chu-de/kim-ly-2428\" rel=\"dofollow\">Kim L&yacute;</a>&nbsp;thể hiện vẻ lịch l&atilde;m c&ugrave;ng vest, kho&aacute;c th&ecirc;m &aacute;o măng t&ocirc;. Bộ ảnh do đạo diễn Trần Th&agrave;nh Trung l&ecirc;n &yacute; tường, gi&aacute;m đốc s&aacute;ng tạo Kye Nguyễn, Đinh H&agrave; Uy&ecirc;n Thư.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-giaitri.vnecdn.net/2021/11/24/HO-NGOC-HA-KIM-LY-2-JPG-9623-1637747685.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=X5SmJ6Vfktp5RM-li_T55Q 1x, https://i1-giaitri.vnecdn.net/2021/11/24/HO-NGOC-HA-KIM-LY-2-JPG-9623-1637747685.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=6zbpOQm9dG_1viyzAb5fXQ 1.5x, https://i1-giaitri.vnecdn.net/2021/11/24/HO-NGOC-HA-KIM-LY-2-JPG-9623-1637747685.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=Zd986pVKiU0k3IJU2Vh-vw 2x\" data-srcset=\"https://i1-giaitri.vnecdn.net/2021/11/24/HO-NGOC-HA-KIM-LY-2-JPG-9623-1637747685.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=X5SmJ6Vfktp5RM-li_T55Q 1x, https://i1-giaitri.vnecdn.net/2021/11/24/HO-NGOC-HA-KIM-LY-2-JPG-9623-1637747685.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=6zbpOQm9dG_1viyzAb5fXQ 1.5x, https://i1-giaitri.vnecdn.net/2021/11/24/HO-NGOC-HA-KIM-LY-2-JPG-9623-1637747685.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=Zd986pVKiU0k3IJU2Vh-vw 2x\"><img class=\"lazy lazied\" src=\"https://i1-giaitri.vnecdn.net/2021/11/24/HO-NGOC-HA-KIM-LY-2-JPG-9623-1637747685.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=X5SmJ6Vfktp5RM-li_T55Q\" alt=\"Hồ Ngọc H&agrave; - Kim L&yacute; t&aacute;i hiện MV Cả một trời thương nhớ\" loading=\"lazy\" data-src=\"https://i1-giaitri.vnecdn.net/2021/11/24/HO-NGOC-HA-KIM-LY-2-JPG-9623-1637747685.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=X5SmJ6Vfktp5RM-li_T55Q\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Hồ Ngọc H&agrave; - Kim L&yacute; t&aacute;i hiện h&igrave;nh ảnh trong MV \"Cả một trời thương nhớ\" - MV họ thực hiện bốn năm trước - dịp sinh nhật c&ocirc;. Ảnh:&nbsp;<em>Mr.AT</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Stylist Kye Nguyễn cho biết: \"Bộ ảnh c&oacute; phong c&aacute;ch thời trang hơi hướng thập ni&ecirc;n 1930-1940, lấy cảm hứng từ c&acirc;u chuyện&nbsp;<a href=\"https://vnexpress.net/ca-mot-troi-thuong-nho-bai-hat-xe-duyen-ha-ho-kim-ly-4105163.html\" target=\"_blank\" rel=\"dofollow noopener\"><em>Cả một trời thương nhớ</em></a>&nbsp;- MV đ&aacute;nh dấu sự n&ecirc;n duy&ecirc;n của hai anh chị. Thay v&igrave; sự bỏ ngỏ với c&acirc;u hỏi họ c&oacute; gặp lại nhau kh&ocirc;ng, lần n&agrave;y t&ocirc;i viết th&ecirc;m c&aacute;i kết hạnh ph&uacute;c cho họ\".</p>\r\n<figure class=\"item_slide_show clearfix\">\r\n<div id=\"video_parent_160708\" class=\"box_embed_video_parent embed_video_new\" data-vcate=\"1003834\" data-vscate=\"1003007\" data-vscaten=\"Giải tr&iacute;\" data-vid=\"160708\" data-ratio=\"1\" data-articleoriginal=\"3745680\" data-ads=\"1\" data-license=\"0\" data-duration=\"435\" data-brandsafe=\"0\" data-type=\"0\" data-play=\"1\" data-frame=\"\" data-aot=\"Kim L&yacute; đưa H&agrave; Hồ đi diễn ở Cần Thơ\" data-videoclassify=\"None\" data-initdom=\"1\" data-view=\"inview\" data-auto=\"1\" data-status=\"play\">\r\n<div id=\"embed_video_160708\" class=\"box_embed_video\">\r\n<div class=\"bg_overlay_small_unpin\">&nbsp;</div>\r\n<div class=\"bg_overlay_small\">&nbsp;</div>\r\n<div class=\"embed-container\">\r\n<div id=\"video-160708\">\r\n<div id=\"parser_player_160708\" class=\"media_content\">\r\n<div id=\"videoContainter_160708\" class=\"videoContainter\">\r\n<div id=\"media-video-160708\" class=\"video-js vjs-default-skin vjs-workinghover vjs-v7 media-video-160708-dimensions vjs-has-started vjs-controls-enabled vjs-playing vjs-user-inactive\" lang=\"vi\" role=\"region\" data-ex=\"st=0&amp;bs=0&amp;pt=0\" data-subtitle=\"0\" data-mode=\"360\" aria-label=\"Video Player\">\r\n<div class=\"vjs-text-track-display\" aria-live=\"off\" aria-atomic=\"true\">&nbsp;</div>\r\n<div class=\"vjs-loading-spinner\" dir=\"ltr\"><span class=\"vjs-control-text\">Video Player is loading.</span></div>\r\n<button class=\"vjs-big-play-button\" title=\"\" type=\"button\" aria-disabled=\"false\"></button>\r\n<div class=\"vjs-control-bar\" dir=\"ltr\"><button class=\"vjs-play-control vjs-control vjs-button vjs-playing\" title=\"Dừng\" type=\"button\" aria-disabled=\"false\"><span class=\"vjs-control-text\" aria-live=\"polite\">Dừng</span></button>\r\n<div class=\"vjs-current-time vjs-time-control vjs-control\"><span class=\"vjs-control-text\">Hiện tại&nbsp;</span><span class=\"vjs-current-time-display\" aria-live=\"off\">0:12</span></div>\r\n<div class=\"vjs-time-control vjs-time-divider\">\r\n<div>/</div>\r\n</div>\r\n<div class=\"vjs-duration vjs-time-control vjs-control\"><span class=\"vjs-control-text\">Thời lượng&nbsp;</span><span class=\"vjs-duration-display\" aria-live=\"off\">7:15</span></div>\r\n<div class=\"vjs-progress-control vjs-control\">\r\n<div class=\"vjs-progress-holder vjs-slider vjs-slider-horizontal\" tabindex=\"0\" role=\"slider\" aria-valuenow=\"2.97\" aria-valuemin=\"0\" aria-valuemax=\"100\" aria-label=\"Progress Bar\" aria-valuetext=\"0:12 of 7:15\">\r\n<div class=\"vjs-load-progress\"><span class=\"vjs-control-text\">Đ&atilde; tải: 0%</span>\r\n<div>&nbsp;</div>\r\n</div>\r\n<div class=\"vjs-play-progress vjs-slider-bar\">\r\n<div class=\"vjs-time-tooltip\">&nbsp;</div>\r\n<span class=\"vjs-control-text\">Tiến tr&igrave;nh: 0%</span></div>\r\n</div>\r\n</div>\r\n<div class=\"vjs-volume-panel vjs-control vjs-volume-panel-vertical\"><button class=\"vjs-mute-control vjs-control vjs-button vjs-vol-0\" title=\"Bỏ tắt tiếng\" type=\"button\" aria-disabled=\"false\"><span class=\"vjs-control-text\" aria-live=\"polite\">Bỏ tắt tiếng</span></button>\r\n<div class=\"vjs-volume-control vjs-control vjs-volume-vertical\">\r\n<div class=\"vjs-volume-bar vjs-slider-bar vjs-slider vjs-slider-vertical\" tabindex=\"0\" role=\"slider\" aria-valuenow=\"0\" aria-valuemin=\"0\" aria-valuemax=\"100\" aria-label=\"Volume Level\" aria-live=\"polite\" aria-valuetext=\"0%\">\r\n<div class=\"vjs-volume-level\">&nbsp;</div>\r\n</div>\r\n</div>\r\n</div>\r\n<button class=\"vjs-fullscreen-control vjs-control vjs-button\" title=\"To&agrave;n m&agrave;n h&igrave;nh\" type=\"button\" aria-disabled=\"false\"><span class=\"vjs-control-text\" aria-live=\"polite\">To&agrave;n m&agrave;n h&igrave;nh</span></button></div>\r\n<div id=\"160708_soundTopRight\" class=\"fp-sound-status-container\"></div>\r\n<div class=\"share-hover\">&nbsp;</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n<figcaption class=\"desc_cation\">\r\n<div class=\"inner_caption\">\r\n<p class=\"Image\">MV \"Cả một trời thương nhớ\" được Hồ Ngọc H&agrave; ph&aacute;t h&agrave;nh năm 2017, đang thu h&uacute;t hơn 30 triệu lượt xem. Video:&nbsp;<em>Hồ Ngọc H&agrave;</em></p>\r\n</div>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Ở tuổi 36,&nbsp;<a href=\"https://vnexpress.net/chu-de/ho-ngoc-ha-1003\" rel=\"dofollow\">Hồ Ngọc H&agrave;</a>&nbsp;mong bản th&acirc;n sẽ nhẹ nh&agrave;ng hơn, biết kiềm chế cảm x&uacute;c n&oacute;ng giận, c&acirc;n bằng được c&ocirc;ng việc v&agrave; cuộc sống ri&ecirc;ng. Khi &ecirc;k&iacute;p b&agrave;n về &yacute; tưởng, Hồ Ngọc H&agrave; bất ngờ lẫn th&iacute;ch th&uacute;. C&ocirc; n&oacute;i: \"Cảm ơn mọi người đ&atilde; lu&ocirc;n d&agrave;nh thời gian, c&ocirc;ng sức để tạo n&ecirc;n những m&oacute;n qu&agrave; &yacute; nghĩa cho t&ocirc;i. L&acirc;u rồi mới chụp bộ ảnh t&igrave;nh cảm, mang tạo h&igrave;nh đặc biệt, lại l&agrave; c&acirc;u chuyện tiếp theo của&nbsp;<em>Cả một trời thương nhớ</em>&nbsp;n&ecirc;n t&ocirc;i v&agrave; Kim L&yacute; đều cảm x&uacute;c\".</p>\r\n</div>\r\n<div id=\"admbackground\" data-set=\"dfp\"></div>\r\n<div id=\"divend\">\r\n<div>\r\n<figure class=\"item_slide_show clearfix\">\r\n<div id=\"video_parent_342227\" class=\"box_embed_video_parent embed_video_new\" data-vcate=\"1003834\" data-vscate=\"1003007\" data-vscaten=\"Giải tr&iacute;\" data-vid=\"342227\" data-ratio=\"1\" data-articleoriginal=\"4394012\" data-ads=\"1\" data-license=\"0\" data-duration=\"412\" data-brandsafe=\"0\" data-type=\"0\" data-play=\"0\" data-frame=\"\" data-aot=\"\" data-videoclassify=\"None\" data-initdom=\"1\">\r\n<div class=\"box_img_video embed-container\" data-vid=\"342227\"><img src=\"https://iv1.vnecdn.net/giaitri/images/web/2021/11/24/ho-ngoc-ha-tiet-lo-mong-muon-o-tuoi-moi-1637747222.jpg?w=750&amp;h=450&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=bDdKbHsDLFh9NWF7oLemWw\" alt=\"Hồ Ngọc H&agrave; tiết lộ mong muốn ở tuổi mới\">\r\n<div class=\"icon_blockvideo\">\r\n<div class=\"img_icon\">&nbsp;</div>\r\n<div class=\"image_icon_center\">&nbsp;</div>\r\n</div>\r\n</div>\r\n</div>\r\n<figcaption class=\"desc_cation\">\r\n<div class=\"inner_caption\">\r\n<p class=\"Image\">Hồ Ngọc H&agrave; n&oacute;i về những mong ước ở tuổi mới. Video:&nbsp;<em>T Production</em></p>\r\n</div>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Nhạc phẩm do Nguyễn Minh Cường s&aacute;ng t&aacute;c, được Hồ Ngọc H&agrave; chọn quay MV, ph&aacute;t h&agrave;nh dưới dạng phim ngắn d&agrave;i hơn bảy ph&uacute;t hồi th&aacute;ng 7/2017. Bản ballad n&oacute;i về niềm hy vọng của đ&ocirc;i t&igrave;nh nh&acirc;n c&oacute; thể \"c&ugrave;ng đi đến nơi gọi l&agrave; hạnh ph&uacute;c\", kh&ocirc;ng giống với tinh thần buồn b&atilde; ở c&aacute;c ca kh&uacute;c c&ocirc; từng thể hiện. Nội dung kể về mối t&igrave;nh tiền kiếp giữa một giai nh&acirc;n ph&ograve;ng tr&agrave; v&agrave; một sĩ quan qu&acirc;n đội (Kim L&yacute; thể hiện).</p>\r\n<p class=\"Normal\">Hồ Ngọc H&agrave; từng cho biết phải uống ba chai bia để bớt hồi hộp khi quay cảnh t&igrave;nh cảm với Kim L&yacute;. MV nhận phản hồi t&iacute;ch cực từ kh&aacute;n giả, đến nay c&oacute; hơn 34 triệu lượt xem tr&ecirc;n Youtube. Ca kh&uacute;c được Hồ Ngọc H&agrave; lựa chọn biểu diễn cuối ở những đ&ecirc;m nhạc như&nbsp;<em>Love songs&nbsp;</em>hồi th&aacute;ng 8/2017, khiến Kim L&yacute; kh&oacute;c khi xem.</p>\r\n</div>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-giaitri.vnecdn.net/2021/11/24/HO-NGOC-HA-KIM-LY-6-JPG-1868-1637747685.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=YbCiaCDkDTpTN_f_JUoNSQ 1x, https://i1-giaitri.vnecdn.net/2021/11/24/HO-NGOC-HA-KIM-LY-6-JPG-1868-1637747685.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=I5-H9nBxrwTIErataC4zEw 1.5x, https://i1-giaitri.vnecdn.net/2021/11/24/HO-NGOC-HA-KIM-LY-6-JPG-1868-1637747685.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=ZwnYxo4ZNfb5QtOKMv3OGw 2x\" data-srcset=\"https://i1-giaitri.vnecdn.net/2021/11/24/HO-NGOC-HA-KIM-LY-6-JPG-1868-1637747685.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=YbCiaCDkDTpTN_f_JUoNSQ 1x, https://i1-giaitri.vnecdn.net/2021/11/24/HO-NGOC-HA-KIM-LY-6-JPG-1868-1637747685.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=I5-H9nBxrwTIErataC4zEw 1.5x, https://i1-giaitri.vnecdn.net/2021/11/24/HO-NGOC-HA-KIM-LY-6-JPG-1868-1637747685.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=ZwnYxo4ZNfb5QtOKMv3OGw 2x\"><img class=\"lazy lazied\" src=\"https://i1-giaitri.vnecdn.net/2021/11/24/HO-NGOC-HA-KIM-LY-6-JPG-1868-1637747685.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=YbCiaCDkDTpTN_f_JUoNSQ\" alt=\"Hồ Ngọc H&agrave; - Kim L&yacute; t&aacute;i hiện MV Cả một trời thương nhớ - 1\" loading=\"lazy\" data-src=\"https://i1-giaitri.vnecdn.net/2021/11/24/HO-NGOC-HA-KIM-LY-6-JPG-1868-1637747685.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=YbCiaCDkDTpTN_f_JUoNSQ\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Hồ Ngọc H&agrave; n&ecirc;n duy&ecirc;n Kim L&yacute; khi cả hai đ&oacute;ng chung MV, hiện sống hạnh ph&uacute;c b&ecirc;n hai nh&oacute;c tỳ đ&aacute;ng y&ecirc;u. Ảnh:&nbsp;<em>Mr.AT</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Hồ Ngọc H&agrave; - Kim L&yacute; y&ecirc;u nhau hơn hai năm trước khi c&ocirc;ng khai mối quan hệ. Họ lu&ocirc;n d&agrave;nh thời gian chăm s&oacute;c nhau, đi&nbsp;<a href=\"https://vnexpress.net/ha-ho-nghi-duong-cung-kim-ly-va-con-trai-4063533.html\" rel=\"dofollow\">du lịch</a>&nbsp;chung. Kim L&yacute; c&ograve;n th&acirc;n thiết với con trai ri&ecirc;ng của vợ anh - b&eacute; Su Beo. Th&aacute;ng 5/2020, Hồ Ngọc H&agrave; x&aacute;c nhận&nbsp;<a href=\"https://vnexpress.net/ho-ngoc-ha-mang-thai-4104272.html\" rel=\"dofollow\">mang thai đ&ocirc;i</a>&nbsp;với Kim L&yacute;. Ng&agrave;y 4/11, con song sinh của họ l&agrave; b&eacute;&nbsp;<a href=\"https://vnexpress.net/cap-song-sinh-mot-tuoi-nha-ha-ho-4381079.html\" rel=\"dofollow\">Lisa v&agrave; Leon</a>&nbsp;tr&ograve;n một tuổi.</p>\r\n<p class=\"Normal\"><a href=\"https://vnexpress.net/ha-ho-tang-them-300-trieu-dong-chong-dich-4071035.html\" rel=\"dofollow\">Hồ Ngọc H&agrave;</a>&nbsp;sinh năm 1984, qu&ecirc; Quảng B&igrave;nh. C&ocirc; khởi đầu nghệ thuật với c&ocirc;ng việc người mẫu, sau đ&oacute; b&eacute;n duy&ecirc;n qua ca h&aacute;t. Trong gần 20 năm đi h&aacute;t, c&ocirc; c&oacute; nhiều hit như&nbsp;<em>Giấc mơ chỉ l&agrave; giấc mơ, Gửi người y&ecirc;u cũ, T&igrave;m lại giấc mơ, Y&ecirc;u thương nhạt nh&ograve;a...</em>&nbsp;C&ocirc; c&ograve;n l&agrave;m huấn luyện vi&ecirc;n tại c&aacute;c chương tr&igrave;nh Giọng h&aacute;t Việt,&nbsp;<a href=\"https://vnexpress.net/dau-an-ho-ngoc-ha-tai-the-face-3464522.html\" rel=\"dofollow\">The Face</a>...</p>\r\n<p class=\"Normal\"><a href=\"https://vnexpress.net/kim-ly-mung-sinh-nhat-ha-ho-4017282.html\" rel=\"dofollow\">Kim L&yacute;</a>&nbsp;sinh năm 1983, c&oacute; bố người Việt v&agrave; mẹ người Thụy Điển. Nam diễn vi&ecirc;n tự lập từ năm 14 tuổi, bươn chải để theo đuổi nghề diễn xuất. Anh từng g&acirc;y ch&uacute; &yacute; với kh&aacute;n giả trong nước với phim&nbsp;<a href=\"https://vnexpress.net/huong-ga-cau-chuyen-hay-nhung-cach-ke-chua-toi-3100168.html\" rel=\"dofollow\"><em>Hương Ga</em></a>.</p>\r\n</div>', 218, 8, 42, 1, '2022-11-18 19:35:41', '2023-02-06 20:34:58');
INSERT INTO `posts` (`id`, `title`, `slug`, `excerpt`, `body`, `user_id`, `category_id`, `views`, `approved`, `created_at`, `updated_at`) VALUES
(69, 'BTV Anh Tuấn: Tôi tìm cách để “chân chạm đất, đầu vẫn có thể bay”!', 'btv-anh-tuan-toi-tim-cach-de-chan-cham-dat-dau-van-co-the-bay', 'Anh Tuấn xuất hiện tại cuộc hẹn cùng Báo Giao thông với hình ảnh giản dị, khác hẳn khi lên hình.', '<p>Trước khi tr&ograve; chuyện, anh giao hẹn: &ldquo;T&ocirc;i kh&ocirc;ng trả lời chuyện gia đ&igrave;nh đ&acirc;u nh&eacute;!&rdquo;. K&iacute;n tiếng về đời tư, song, BTV nổi tiếng xởi lởi kể đủ chuyện khi n&oacute;i về nghề b&aacute;o.</p>\r\n<div>\r\n<p class=\"image\"><img class=\"width100 initial loaded\" src=\"https://cdn.baogiaothong.vn/upload/images/2022-2/article_img/2022-06-20/img-bgt-2021-anhtuan2-1655694504-width777height1080.jpg\" alt=\"BTV Anh Tuấn: T&ocirc;i t&igrave;m c&aacute;ch để &ldquo;ch&acirc;n chạm đất, đầu vẫn c&oacute; thể bay&rdquo;! 1\" data-original=\"https://cdn.baogiaothong.vn/upload/images/2022-2/article_img/2022-06-20/img-bgt-2021-anhtuan2-1655694504-width777height1080.jpg\" data-was-processed=\"true\"></p>\r\n<p class=\"img_chu_thich\">Bi&ecirc;n tập vi&ecirc;n Anh Tuấn</p>\r\n</div>\r\n<p><strong>&ldquo;Tim đập, ch&acirc;n run&rdquo; khi lần đầu dẫn s&acirc;n khấu</strong></p>\r\n<p><em><strong>Học nghệ thuật từ b&eacute;, lại bỏ tất cả để theo truyền h&igrave;nh, đ&atilde; bao giờ anh hối hận?</strong></em></p>\r\n<p>Tất cả quyết định trong cuộc đời, &iacute;t khi t&ocirc;i hối hận. Mỗi quyết định đều mang đến cho m&igrave;nh một ng&atilde; rẽ c&oacute; thể biết trước hoặc kh&ocirc;ng, việc của m&igrave;nh l&agrave; phải quyết t&acirc;m đi hết.</p>\r\n<section class=\"bnrBt txtCent\"></section>\r\n<p>Một số người bạn nước ngo&agrave;i khi biết t&ocirc;i gi&agrave;nh học bổng 4 năm tại trường Berklee College of Music đều rất kinh ngạc. Biết t&ocirc;i từ bỏ, họ n&oacute;i t&ocirc;i đi&ecirc;n. Thực ra, họ kh&ocirc;ng đứng ở g&oacute;c độ của m&igrave;nh để nh&igrave;n vấn đề.</p>\r\n<p>Đương nhi&ecirc;n, nếu đi học l&uacute;c đ&oacute;, c&oacute; thể l&uacute;c n&agrave;y t&ocirc;i đang l&agrave; một nghệ sĩ thuần t&uacute;y hoặc rất nổi tiếng. Nhưng t&ocirc;i kh&ocirc;ng nh&igrave;n lại đằng sau v&agrave; kh&ocirc;ng bao giờ nghĩ &ldquo;nếu như&rdquo;.</p>\r\n<p><strong><em>L&agrave;m truyền h&igrave;nh kiểu &ldquo;tay tr&aacute;i&rdquo; như thế, c&oacute; khi n&agrave;o anh cảm thấy kh&oacute; khăn?</em></strong></p>\r\n<p>Ng&agrave;y xưa, t&ocirc;i học văn t&agrave;m tạm, th&iacute;ch nghe nhạc, xem phim v&agrave; cũng c&oacute; năng khiếu &acirc;m nhạc. May mắn sau khi tốt nghiệp, t&ocirc;i l&agrave;m BTV chuy&ecirc;n về &acirc;m nhạc n&ecirc;n được l&agrave;m mọi thứ theo thế mạnh của bản th&acirc;n. Gần như kh&ocirc;ng c&oacute; kh&oacute; khăn, c&oacute; thể v&igrave; t&ocirc;i th&iacute;ch nghi tốt với m&ocirc;i trường b&aacute;o ch&iacute;.</p>\r\n<p>Nhưng c&ocirc;ng việc MC lại ho&agrave;n to&agrave;n kh&aacute;c. T&ocirc;i từng chơi nhạc, chỉ nhắm mắt tập trung lắng nghe tiếng nhạc, trong khi MC chủ yếu l&agrave; n&oacute;i. T&ocirc;i đ&atilde; rất bỡ ngỡ. Đến giờ, t&ocirc;i vẫn thấy khả năng ăn n&oacute;i của m&igrave;nh kh&ocirc;ng bằng nhiều người.</p>\r\n<p>Thời gian đầu v&agrave;o đ&agrave;i, được giao nhiệm vụ dẫn s&acirc;n khấu, t&ocirc;i &ldquo;tim đập ch&acirc;n run&rdquo;, sợ n&oacute;i sai. T&ocirc;i cố gắng t&igrave;m hiểu, học hỏi thật nhiều. Cũng may mắn, t&ocirc;i được tham gia c&aacute;c lớp đ&agrave;o tạo MC, từ c&aacute;ch ứng biến sự cố, t&ocirc;ng giọng, biểu cảm&hellip; với c&aacute;c chuy&ecirc;n gia nước ngo&agrave;i.</p>\r\n<p><strong><em>Tr&ograve;n 25 năm l&agrave;m nghề, đ&atilde; bao giờ anh gặp &ldquo;tai nạn nghề nghiệp&rdquo;?</em></strong></p>\r\n<p>Những tai nạn đến giờ thường chỉ mang t&iacute;nh h&agrave;i hước. V&iacute; dụ khi dẫn, MC phải đeo tai nghe để nghe hiệu lệnh từ đạo diễn. Đ&ocirc;i l&uacute;c m&igrave;nh mất tập trung, lại n&oacute;i lu&ocirc;n theo đạo diễn.</p>\r\n<p>T&ocirc;i c&oacute; 11 năm dẫn chương tr&igrave;nh &ldquo;Nối v&ograve;ng tay lớn&rdquo; ph&aacute;t s&oacute;ng trực tiếp, c&oacute; nhiều l&atilde;nh đạo Đảng, Nh&agrave; nước đến dự, những sai s&oacute;t, kể cả một dấu phẩy cũng rất nguy hiểm. Với những chương tr&igrave;nh như vậy th&igrave; phải tập trung cao độ, kh&ocirc;ng được ph&eacute;p sai s&oacute;t.</p>\r\n<p>T&iacute;nh t&ocirc;i cầu to&agrave;n, lỗi n&agrave;o sai sẽ rất &aacute;y n&aacute;y. T&ocirc;i nhớ c&oacute; lần ở một chương tr&igrave;nh, đ&atilde; v&ocirc; thức n&oacute;i theo kịch bản m&agrave; kh&ocirc;ng biết kịch bản sai t&ecirc;n nhạc sĩ. Sau đ&oacute;, t&ocirc;i t&igrave;m c&aacute;ch li&ecirc;n lạc để xin lỗi v&agrave; nhận thiếu s&oacute;t. B&aacute;c ấy đ&atilde; rất mở l&ograve;ng chấp nhận v&agrave; đến giờ vẫn y&ecirc;u qu&yacute; t&ocirc;i.</p>\r\n<p><strong>Gia đ&igrave;nh l&agrave; số 1</strong></p>\r\n<p><em><strong>G&aacute;c lại c&ocirc;ng việc của nh&agrave; b&aacute;o, nghệ sĩ Anh Tuấn l&agrave; người như thế n&agrave;o?</strong></em></p>\r\n<p>Vẫn l&agrave; Anh Tuấn, nhưng t&ocirc;i ph&acirc;n ra những con người m&igrave;nh được ph&eacute;p, theo từng chuẩn trong mỗi trường hợp.</p>\r\n<p>T&ocirc;i c&oacute; thể ch&igrave;m đắm v&agrave;o những giấc mơ bay bổng, cho m&igrave;nh thi thoảng mơ mộng. T&ocirc;i c&oacute; thể chạy xe về qu&ecirc; ngắm những c&aacute;nh đồng trải d&agrave;i, hay v&agrave;o l&acirc;u đ&agrave;i lại tưởng tượng m&igrave;nh l&agrave; ho&agrave;ng tử (cười). M&igrave;nh được ph&eacute;p m&agrave;, đ&uacute;ng kh&ocirc;ng?</p>\r\n<p>Nhưng một nh&agrave; sản xuất, đạo diễn Anh Tuấn lại cực kỳ kh&oacute; t&iacute;nh v&agrave; ki&ecirc;n quyết. C&oacute; lẽ, t&ocirc;i thừa hưởng t&iacute;nh c&aacute;ch n&agrave;y của bố (PGS. NGND Vũ Hướng - PV). T&iacute;nh t&ocirc;i thẳng thắn, c&oacute; g&igrave; n&oacute;i đ&oacute; n&ecirc;n dễ l&agrave;m người kh&aacute;c buồn. Bởi thế, t&ocirc;i lu&ocirc;n phải nhắc mọi người l&agrave;m việc với m&igrave;nh l&agrave; đừng c&oacute; tự &aacute;i.</p>\r\n<div class=\"sp-anhright sp-quotedep5\" data-block-quote-type=\"{#block-quote-type#}\">\r\n<div class=\"sp-quotemarktop3\">&ldquo;</div>\r\n<p>Nghề b&aacute;o gi&uacute;p t&ocirc;i được tiếp x&uacute;c với nhiều người, mở mang nhiều tri thức về x&atilde; hội, c&aacute;c mối quan hệ.<br>Bản th&acirc;n t&ocirc;i l&agrave;m nhiều vai tr&ograve; như nghệ sĩ, nh&agrave; sản xuất, nh&agrave; b&aacute;o... Nghệ sĩ c&oacute; thể &ldquo;bay&rdquo;, nhưng nh&agrave; b&aacute;o ch&acirc;n phải chạm đất.<br>T&ocirc;i t&igrave;m c&aacute;ch dung h&ograve;a hai t&iacute;nh c&aacute;ch đ&oacute;, l&agrave;m sao ch&acirc;n chạm đất nhưng t&acirc;m hồn vẫn c&oacute; thể bay bổng theo c&aacute;ch của m&igrave;nh.</p>\r\n<p><em><strong>BTV Anh Tuấn</strong></em></p>\r\n<div class=\"sp-quotemarkbottom3\">&rdquo;</div>\r\n</div>\r\n<p><br><strong><em>Kh&ocirc;ng chỉ thẳng thắn, anh c&ograve;n rất &ldquo;đi&ecirc;n&rdquo;?</em></strong></p>\r\n<p>Đi&ecirc;n chứ! T&ocirc;i từng chạy xe m&ocirc; t&ocirc; 230km/h trong một s&acirc;n bay cũ, thế c&oacute; đi&ecirc;n kh&ocirc;ng? (cười). Nhưng trong kinh doanh, t&ocirc;i rất nghi&ecirc;m t&uacute;c v&agrave; b&agrave;i bản, để mọi người khi l&agrave;m việc với m&igrave;nh sẽ cảm thấy an to&agrave;n.</p>\r\n<p>T&ocirc;i lu&ocirc;n x&aacute;c định m&igrave;nh l&agrave; người c&oacute; thể đi&ecirc;n nhưng cũng l&yacute; tr&iacute;. T&ocirc;i từng sản xuất nhiều chương tr&igrave;nh đưa nghệ sĩ nước ngo&agrave;i về Việt Nam. C&ocirc;ng việc đ&oacute; phải tuyệt đối ch&iacute;nh x&aacute;c, kh&ocirc;ng d&agrave;nh cho một c&aacute;i đầu bay bổng.</p>\r\n<p><strong><em>Anh cũng rất l&yacute; tr&iacute; để kh&ocirc;ng bị cuốn theo sự nổi tiếng, đ&aacute;nh mất sự ri&ecirc;ng tư?</em></strong></p>\r\n<p>Điều n&agrave;y c&oacute; lẽ do t&iacute;nh c&aacute;ch mỗi người. Hồi b&eacute;, t&ocirc;i hơi rụt r&egrave;. Bố mẹ hay đi c&ocirc;ng t&aacute;c n&ecirc;n t&ocirc;i hay phải ở một m&igrave;nh. V&igrave; thế, m&igrave;nh kh&ocirc;ng hay t&acirc;m sự với ai v&agrave; th&iacute;ch những điều g&igrave; thuộc về ri&ecirc;ng tư.</p>\r\n<p>Lớn l&ecirc;n, t&ocirc;i c&agrave;ng muốn giữ điều đ&oacute;. Gia đ&igrave;nh, cuộc sống c&aacute; nh&acirc;n l&agrave; của m&igrave;nh. Với t&ocirc;i, gia đ&igrave;nh rất thi&ecirc;ng li&ecirc;ng. Bất cứ điều g&igrave; động đến gia đ&igrave;nh theo hướng ti&ecirc;u cực, t&ocirc;i buồn v&agrave; nghĩ m&igrave;nh sẽ l&agrave;m mọi người buồn.</p>\r\n<p>Thế n&ecirc;n với c&ocirc;ng ch&uacute;ng, mọi người h&atilde;y cứ biết một MC Anh Tuấn, l&agrave;m đạo diễn, nh&agrave; sản xuất, kinh doanh v&agrave; lu&ocirc;n coi gia đ&igrave;nh l&agrave; số 1. Thế th&ocirc;i l&agrave; đủ rồi!</p>\r\n<p><em><strong>Nhưng anh l&agrave; người nổi tiếng, việc giữ g&oacute;c ri&ecirc;ng tư cho m&igrave;nh hẳn kh&ocirc;ng dễ d&agrave;ng?</strong></em></p>\r\n<p>Ai biết cứ biết, c&ograve;n t&ocirc;i sẽ kh&ocirc;ng tự n&oacute;i ra. Ng&agrave;y xưa đi l&agrave;m về, t&ocirc;i hay ch&agrave;o mấy chị lao c&ocirc;ng buổi đ&ecirc;m. C&oacute; chị thắc mắc &ldquo;Người nổi tiếng m&agrave; đi đ&acirc;u cũng ch&agrave;o hỏi?&rdquo;. T&ocirc;i đ&aacute;p: &ldquo;Bỏ sự nổi tiếng, em cũng chỉ l&agrave; thằng người th&ocirc;i&rdquo;.</p>\r\n<p>T&ocirc;i nghĩ, m&igrave;nh may mắn nhưng kh&ocirc;ng c&oacute; nghĩa kh&aacute;c người ta. B&igrave;nh thường, t&ocirc;i c&oacute; thể mặc quần so&oacute;c, &aacute;o ph&ocirc;ng, ngồi l&ecirc; la ngo&agrave;i đường m&agrave; kh&ocirc;ng ai biết l&agrave; ai. Nhiều người n&oacute;i t&ocirc;i, người nổi tiếng m&agrave; kh&ocirc;ng giữ h&igrave;nh ảnh. Kh&ocirc;ng sao! T&ocirc;i kh&ocirc;ng phải người giữ h&igrave;nh ảnh.</p>\r\n<p>Bởi thế đến giờ, t&ocirc;i vẫn kh&ocirc;ng bao giờ trang điểm khi l&ecirc;n h&igrave;nh. Gương mặt thế n&agrave;o, t&ocirc;i sẽ l&ecirc;n h&igrave;nh như thế v&agrave; cả khi ốm hay mệt, t&ocirc;i cũng để kh&aacute;n giả thấy điều đ&oacute;. C&oacute; lẽ điều n&agrave;y kh&ocirc;ng đ&uacute;ng với truyền h&igrave;nh, nhưng t&ocirc;i kh&ocirc;ng th&iacute;ch c&aacute;i g&igrave; đ&oacute; giả.</p>\r\n<p><strong>Nhiều bạn mới l&agrave; gi&agrave;u c&oacute;!</strong></p>\r\n<div>\r\n<p class=\"image\"><img class=\"width100 loaded\" src=\"https://cdn.baogiaothong.vn/upload/images/2022-2/article_img/2022-06-20/img-bgt-2021-khong-chi-la-nha-bao-anh-tuan-cung-la-mot-nghe-si-nha-san-xuat-1655694554-width1280height852.jpg\" alt=\"BTV Anh Tuấn: T&ocirc;i t&igrave;m c&aacute;ch để &ldquo;ch&acirc;n chạm đất, đầu vẫn c&oacute; thể bay&rdquo;! 2\" data-original=\"https://cdn.baogiaothong.vn/upload/images/2022-2/article_img/2022-06-20/img-bgt-2021-khong-chi-la-nha-bao-anh-tuan-cung-la-mot-nghe-si-nha-san-xuat-1655694554-width1280height852.jpg\" data-was-processed=\"true\"></p>\r\n<p class=\"img_chu_thich\">Kh&ocirc;ng chỉ l&agrave; nh&agrave; b&aacute;o, Anh Tuấn cũng l&agrave; một nghệ sĩ, nh&agrave; sản xuất</p>\r\n</div>\r\n<p><strong><em>C&oacute; bao giờ, anh x&aacute;c định m&igrave;nh muốn l&agrave;m nghệ sĩ hay nh&agrave; b&aacute;o hơn?</em></strong></p>\r\n<p>T&ocirc;i vẫn x&aacute;c định m&igrave;nh l&agrave; nh&agrave; b&aacute;o l&agrave;m văn h&oacute;a. 25 năm cầm tấm thẻ nh&agrave; b&aacute;o l&agrave; qu&atilde;ng thời gian d&agrave;i. Kh&ocirc;ng y&ecirc;u nghề, kh&oacute; c&oacute; thể duy tr&igrave; tới tận l&uacute;c n&agrave;y.</p>\r\n<p>T&ocirc;i may mắn hoạt động trong lĩnh vực b&aacute;o h&igrave;nh mảng văn h&oacute;a, n&ecirc;n l&agrave;m nghề b&aacute;o nhưng cũng được l&agrave;m nghề m&igrave;nh học. T&ocirc;i lu&ocirc;n cảm ơn số phận đ&atilde; đưa m&igrave;nh tới ch&acirc;n trời mới.</p>\r\n<p><strong><em>L&agrave;m b&aacute;o c&oacute; đủ để anh gi&agrave;u?</em></strong></p>\r\n<p>Định nghĩa gi&agrave;u, ngh&egrave;o kh&aacute; v&ocirc; c&ugrave;ng. C&oacute; người n&oacute;i đủ sống l&agrave; ổn, c&oacute; người thấy m&igrave;nh gi&agrave;u tri thức, cũng c&oacute; người định lượng bằng tiền. T&ocirc;i kh&ocirc;ng c&oacute; định nghĩa thế n&agrave;o l&agrave; gi&agrave;u m&agrave; chỉ thấy, nhiều bạn đ&atilde; l&agrave; gi&agrave;u. Khi gặp kh&oacute; khăn hay l&uacute;c hạnh ph&uacute;c, m&igrave;nh c&oacute; người để chia sẻ đ&atilde; l&agrave; sung sướng.</p>\r\n<p>C&oacute; thể, mọi người n&oacute;i t&ocirc;i s&aacute;o rỗng, h&igrave;nh thức, nhưng t&ocirc;i quan niệm như thế. Tất nhi&ecirc;n, t&ocirc;i l&agrave; người th&iacute;ch l&agrave;m kinh tế v&agrave; giờ vẫn l&agrave;m th&ecirc;m kinh tế.</p>\r\n<p><em><strong>V&agrave; sự nổi tiếng nhờ nghề b&aacute;o cho anh nhiều thuận lợi khi l&agrave;m kinh tế?</strong></em></p>\r\n<p>L&agrave;m b&aacute;o h&igrave;nh, được nổi tiếng l&agrave; sự may mắn. Được mọi người biết đến v&agrave; y&ecirc;u qu&yacute; th&igrave; khi l&agrave;m kinh tế li&ecirc;n quan tới họ, m&igrave;nh c&oacute; thể được ưu &aacute;i hơn. Ngược lại, việc xuất hiện l&ecirc;n truyền h&igrave;nh ở lĩnh vực nghệ thuật nhiều, cũng c&oacute; người nghi ngờ khả năng l&agrave;m kinh tế của t&ocirc;i. C&oacute; thể, họ cho rằng m&igrave;nh l&agrave; người chỉ l&agrave;m giải tr&iacute; v&agrave; chỉ để chơi c&ugrave;ng th&ocirc;i.</p>\r\n<p>Nhưng với t&ocirc;i, điều đ&oacute; kh&ocirc;ng th&agrave;nh vấn đề. Trong kinh doanh, t&ocirc;i th&iacute;ch sự thẳng thắn v&agrave; kh&ocirc;ng muốn để t&igrave;nh cảm xen v&agrave;o. T&ocirc;i chơi với nhiều người bạn rất giỏi l&agrave;m kinh tế, nhưng kh&ocirc;ng nhờ họ điều g&igrave;.</p>', 218, 1, 52, 1, '2022-11-18 19:35:41', '2023-02-07 07:34:28'),
(71, 'Lựa chọn và sử dụng cây xanh để tăng vượng khí', 'lua-chon-va-su-dung-cay-xanh-de-tang-vuong-khi', 'Sử dụng cây xanh, bồn cảnh để làm vượng hiên trước, ban công, cửa sổ là giải pháp phổ biến và hiệu quả trong phong thủy hiện đại.', '<div id=\"divfirst\">\r\n<p class=\"Normal\">Trong kiến tr&uacute;c cổ, mỗi ng&ocirc;i nh&agrave; thường c&oacute; hi&ecirc;n trước hi&ecirc;n sau, hai ch&aacute;i nh&agrave; v&agrave; hệ thống cửa sổ. Trong đ&oacute; hi&ecirc;n trước gắn liền với cửa ch&iacute;nh v&agrave; s&acirc;n (hoặc đường đi, ng&otilde; phố), l&agrave; \"k&ecirc;nh\" tiến kh&iacute; chủ đạo, c&oacute; vai tr&ograve; h&oacute;a giải, lọc sinh kh&iacute; trước khi dẫn v&agrave;o nội thất.</p>\r\n<p class=\"Normal\">Cửa ch&iacute;nh v&agrave; hi&ecirc;n trước kết hợp với nhau tạo th&agrave;nh \"kh&iacute; khẩu\" (cửa tiến kh&iacute;) của một gia trạch, ch&uacute;ng cần được đặt trong mối quan hệ tương sinh v&agrave; sinh nhập, nếu kh&ocirc;ng sẽ khiến t&agrave;i vận của gia đ&igrave;nh suy bại.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-giadinh.vnecdn.net/2022/05/31/cay-xanh-1-5281-1653991865.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=EvjMv3k22TBTWdevaL4fmg 1x, https://i1-giadinh.vnecdn.net/2022/05/31/cay-xanh-1-5281-1653991865.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=hwupdDG4HS1X9M7HmLA98A 1.5x, https://i1-giadinh.vnecdn.net/2022/05/31/cay-xanh-1-5281-1653991865.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=wQJH3gPvKyQkwNaJk3XBcQ 2x\" data-srcset=\"https://i1-giadinh.vnecdn.net/2022/05/31/cay-xanh-1-5281-1653991865.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=EvjMv3k22TBTWdevaL4fmg 1x, https://i1-giadinh.vnecdn.net/2022/05/31/cay-xanh-1-5281-1653991865.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=hwupdDG4HS1X9M7HmLA98A 1.5x, https://i1-giadinh.vnecdn.net/2022/05/31/cay-xanh-1-5281-1653991865.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=wQJH3gPvKyQkwNaJk3XBcQ 2x\"><img class=\"lazy lazied\" src=\"https://i1-giadinh.vnecdn.net/2022/05/31/cay-xanh-1-5281-1653991865.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=EvjMv3k22TBTWdevaL4fmg\" alt=\"Sử dụng c&acirc;y xanh, bồn cảnh để l&agrave;m vượng hi&ecirc;n trước, ban c&ocirc;ng, cửa sổ l&agrave; giải ph&aacute;p phổ biến v&agrave; hiệu quả trong phong thủy hiện đại. Ảnh minh họa: life.ima.ir\" loading=\"lazy\" data-src=\"https://i1-giadinh.vnecdn.net/2022/05/31/cay-xanh-1-5281-1653991865.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=EvjMv3k22TBTWdevaL4fmg\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Sử dụng c&acirc;y xanh, bồn cảnh để l&agrave;m vượng hi&ecirc;n trước, ban c&ocirc;ng, cửa sổ l&agrave; giải ph&aacute;p phổ biến v&agrave; hiệu quả trong phong thủy hiện đại.&nbsp;<em>Ảnh minh họa: life.ima.ir</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Trong bối cảnh hiện nay, nhất l&agrave; đối với nh&agrave; ở đ&ocirc; thị, căn hộ chung cư, trước khi l&agrave;m tăng vượng kh&iacute; cần đảm bảo hi&ecirc;n trước, ban c&ocirc;ng v&agrave; cửa sổ sạch sẽ tho&aacute;ng đ&atilde;ng.</p>\r\n<p class=\"Normal\">Kh&ocirc;ng n&ecirc;n sử dụng hệ thống n&agrave;y l&agrave;m nơi phơi ph&oacute;ng hoặc cất trữ đồ vật. Uất kh&iacute; do ẩm ướt hoặc cất trữ đồ cũ sản sinh ra sẽ x&acirc;m chiếm to&agrave;n bộ nội kh&iacute; ng&ocirc;i nh&agrave;, khiến sinh kh&iacute; kh&ocirc;ng thể tiến nhập; kh&ocirc;ng kh&aacute;c n&agrave;o con người sống v&agrave; l&agrave;m việc trong m&ocirc;i trường &ocirc; nhiễm, l&acirc;u dần th&agrave;nh bệnh. Sử dụng hoa, c&acirc;y cảnh l&agrave;m vượng sinh kh&iacute; theo nguy&ecirc;n tắc sau:</p>\r\n<p class=\"Normal\">- Kh&ocirc;ng d&ugrave;ng những loại hoa, c&acirc;y cảnh c&oacute; gai nhọn hoặc d&acirc;y leo nhiều l&aacute; rậm rạp; l&aacute; c&acirc;y hấp dẫn nhiều loại s&acirc;u bọ cư tr&uacute;, nhất l&agrave; s&acirc;u r&oacute;m.</p>\r\n<p class=\"Normal\">- Kh&ocirc;ng d&ugrave;ng c&aacute;c loại c&acirc;y rụng l&aacute; theo m&ugrave;a, c&acirc;y c&oacute; rễ cọc lớn đ&ograve;i hỏi phải tưới tắm nhiều, thường xuy&ecirc;n.</p>\r\n<p class=\"Normal\">- Kh&ocirc;ng trồng c&acirc;y trực tiếp tr&ecirc;n nền s&acirc;n ngay s&aacute;t m&aacute;i hi&ecirc;n. Hạn chế tối đa sử dụng c&aacute;c loại c&acirc;y thủy sinh hoặc d&acirc;y leo nhiều rễ phụ.</p>\r\n</div>\r\n<div id=\"admbackground\" data-set=\"dfp\"></div>\r\n<div id=\"divend\">\r\n<p class=\"Normal\">- N&ecirc;n sử dụng bồn (chậu) cảnh, với c&aacute;c loại hoa-c&acirc;y cảnh c&oacute; m&agrave;u sắc tươi tắn, c&oacute; hương thơm, &iacute;t rụng l&aacute; v&agrave; xanh tốt quanh năm. Nếu c&oacute; điều kiện, bồn hoa- c&acirc;y cảnh n&ecirc;n lu&acirc;n lưu đặt trước hi&ecirc;n, ban c&ocirc;ng theo m&ugrave;a l&agrave; tốt nhất, cụ thể:</p>\r\n<p class=\"Normal\">+ M&ugrave;a xu&acirc;n n&ecirc;n d&ugrave;ng c&aacute;c loại hoa như mai, đ&agrave;o, uất kim hương, lan, đỗ quy&ecirc;n...</p>\r\n<p class=\"Normal\">+ M&ugrave;a hạ n&ecirc;n đặt tường vi, hoa hồng, th&uacute;y c&uacute;c, thi&ecirc;n ngưu, thược dược, h&agrave;m tiếu, thạch tr&uacute;c...</p>\r\n<p class=\"Normal\">+ M&ugrave;a thu n&ecirc;n d&ugrave;ng hoa c&uacute;c, qu&acirc;n tử lan, dạ hương, dạ l&yacute;; v&agrave; m&ugrave;a đ&ocirc;ng n&ecirc;n d&ugrave;ng thủy ti&ecirc;n, sơn tr&agrave;, lạp mai...</p>\r\n<p class=\"Normal\">- C&aacute;c loại bonsai, c&acirc;y cảnh c&oacute; thể d&ugrave;ng quanh năm để h&oacute;a s&aacute;t, l&agrave;m vượng hi&ecirc;n trước, ban c&ocirc;ng... T&ugrave;y theo diện t&iacute;ch thực tế, c&oacute; thể chọn d&ugrave;ng t&ugrave;ng La H&aacute;n, tr&uacute;c v&agrave;ng, sung, sanh, si đ&aacute;, ngũ sắc sơn tr&agrave;, ngọc lan, trắc b&aacute;ch diệp, vạn ni&ecirc;n thanh... B&igrave;nh dị v&agrave; hiệu quả hơn, c&oacute; thể d&ugrave;ng c&aacute;c loại c&acirc;y cảnh dễ t&iacute;nh như khế (bonsai), đinh lăng, sung ăn tr&aacute;i (bonsai), ổi t&agrave;u, ngũ gia b&igrave;, hoa ng&acirc;u, r&acirc;m bụt, ch&egrave; mạn hảo...</p>\r\n<p class=\"Normal\">Sử dụng hoa, c&acirc;y cảnh đ&uacute;ng phương ph&aacute;p, chủng loại c&oacute; thể tăng cường vượng kh&iacute; cho kh&ocirc;ng gian sống, l&agrave;m việc của gia đ&igrave;nh hoặc cơ quan, đơn vị. Cho d&ugrave; hệ thống \"kh&iacute; khẩu\" quan trọng n&agrave;y c&oacute; phạm xung s&aacute;t cũng sẽ được h&oacute;a giải cơ bản; đồng thời g&oacute;p phần quan trọng l&agrave;m đẹp, xanh h&oacute;a m&ocirc;i trường.</p>\r\n<p class=\"Normal\">Cửa ch&iacute;nh (gồm cả hi&ecirc;n trước) thường phạm s&aacute;t Ho&agrave;ng tuyền trong quan hệ với cổng, lối ra v&agrave;o, cửa h&agrave;nh lang, cửa thang m&aacute;y... Cửa sổ, cửa ph&ograve;ng, h&agrave;nh lang thường phạm B&aacute;t s&aacute;t trong tương quan với kh&iacute; khẩu.</p>\r\n<p class=\"Normal\">Ho&agrave;ng tuyền s&aacute;t thường g&acirc;y bệnh ở lục phủ ngũ tạng, tổn hại ph&uacute;c đức, tại vận suy bại dần. B&aacute;t s&aacute;t thường khiến người nh&agrave; gặp tai họa bất thường, bị đạo tặc trộm cắp, tai nạn huyết quang...</p>\r\n<p class=\"Normal\">Những ng&ocirc;i nh&agrave; hoặc khu&ocirc;n vi&ecirc;n c&oacute; cửa (cổng) ch&iacute;nh, cửa phụ v&agrave; h&agrave;nh lang nội bộ tạo với nhau một g&oacute;c t&ugrave; theo h&igrave;nh chữ L hoặc chữa T thường hay phạm cấm kỵ của B&aacute;t s&aacute;t.</p>\r\n<p class=\"Normal\">Với những l&yacute; do n&ecirc;u tr&ecirc;n, c&aacute;c nh&agrave;&nbsp;<a href=\"https://vnexpress.net/tag/phong-thuy-82979\" target=\"_blank\" rel=\"dofollow noopener\">phong thủy</a>&nbsp;hết sức ch&uacute; &yacute; đến việc gia tăng vượng kh&iacute;, h&oacute;a s&aacute;t cho hi&ecirc;n trước, ban c&ocirc;ng v&agrave; cửa sổ. Ngo&agrave;i việc thiết kế, tu sửa đảm bảo hệ thống n&agrave;y tr&aacute;nh được tương quan B&aacute;t s&aacute;t v&agrave; Ho&agrave;ng tuyền, sử dụng c&acirc;y xanh, bồn cảnh để l&agrave;m vượng hi&ecirc;n trước, ban c&ocirc;ng v&agrave; cửa sổ l&agrave; giải ph&aacute;p phổ biến v&agrave; hiệu quả nhiều mặt.</p>\r\n</div>', 218, 12, 57, 1, '2022-11-18 19:35:41', '2023-02-07 07:30:48'),
(72, '4 thức uống giải nhiệt mùa hè, giúp làn da tươi sáng', '4-thuc-uong-giai-nhiet-mua-he-giup-lan-da-tuoi-sang', 'Nước rất quan trọng với sức khỏe trong đó có làn da và đặc biệt trong mùa hè cơ thể dễ bị mất nước. Khi cơ thể không đủ nước từ bên trong thì sức khỏe của làn da cũng bị ảnh hưởng rõ rệt…', '<p>Tuy nhi&ecirc;n nếu uống nhiều nước bạn sẽ cảm thấy nh&agrave;m ch&aacute;n, dưới đ&acirc;y l&agrave; 4 loại thức uống ngon m&agrave; bạn c&oacute; thể nh&acirc;m nhi để c&oacute; được l&agrave;n da s&aacute;ng đẹp trong m&ugrave;a h&egrave; n&agrave;y ...</p>\r\n<p align=\"center\"><img class=\"news-image initial loading\" src=\"https://icdn.24h.com.vn/upload/2-2022/images/2022-06-15/1655257633-90d8eae0df2ff09e68ddcecef3538f56-width600height350.jpg\" alt=\"4 thức uống giải nhiệt m&ugrave;a h&egrave;, gi&uacute;p l&agrave;n da tươi s&aacute;ng - 1\" width=\"660\" data-was-processed=\"true\"></p>\r\n<p class=\"img_chu_thich_0407\">Thức uống cho l&agrave;m da tươi s&aacute;ng m&ugrave;a h&egrave;.</p>\r\n<p><strong>1.&nbsp;Nước chanh gi&uacute;p s&aacute;ng da</strong></p>\r\n<p>L&agrave; một nguồn gi&agrave;u vitamin C v&agrave; chất chống oxy h&oacute;a chống lại c&aacute;c gốc tự do, gi&uacute;p l&agrave;m cho l&agrave;n da của bạn s&aacute;ng hơn. Nước chanh c&oacute; thể gi&uacute;p chống lại mụn trứng c&aacute; v&agrave; c&aacute;c vấn đề da nhạy cảm như mẩn đỏ, ph&aacute;t ban. Uống nước chanh c&oacute; thể gi&uacute;p ngăn ngừa l&atilde;o h&oacute;a, nếp nhăn...</p>\r\n<p>C&aacute;ch chế biến: Lấy một cốc nước lọc, cho đường (độ ngọt t&ugrave;y &yacute;), rồi vắt chanh v&agrave;o thưởng thức.</p>\r\n<p align=\"center\"><img class=\"news-image loaded\" src=\"https://icdn.24h.com.vn/upload/2-2022/images/2022-06-15/1655257633-c8932f6921714438f7ef259bf95360a8-width540height303.jpg\" alt=\"4 thức uống giải nhiệt m&ugrave;a h&egrave;, gi&uacute;p l&agrave;n da tươi s&aacute;ng - 2\" width=\"660\" data-original=\"https://icdn.24h.com.vn/upload/2-2022/images/2022-06-15/1655257633-c8932f6921714438f7ef259bf95360a8-width540height303.jpg\" data-was-processed=\"true\"></p>\r\n<p class=\"img_chu_thich_0407\">Nước chanh.</p>\r\n<p><strong>2. Hỗn hợp nước chanh tr&agrave; xanh bạc h&agrave; v&agrave; gừng</strong></p>\r\n<p>Đ&acirc;y l&agrave; thức uống l&yacute; tưởng để hạ nhiệt cơ thể v&agrave; tạo cảm gi&aacute;c sảng kho&aacute;i. Gừng c&oacute; đặc t&iacute;nh chống vi&ecirc;m. Khi kết hợp với nhau c&aacute;c th&agrave;nh phần n&agrave;y sẽ l&agrave;m cho l&agrave;n da tươi s&aacute;ng.</p>\r\n<p>C&aacute;ch chế biến: Đun s&ocirc;i khoảng 500 ml nước v&agrave; cho v&agrave;o đ&oacute; 4 t&uacute;i tr&agrave; xanh, 1/2 cốc l&aacute; bạc h&agrave; tươi cắt nhỏ v&agrave; 1-2 l&aacute;t gừng. Ủ trong 30 ph&uacute;t sau đ&oacute; đổ ra b&igrave;nh lớn, để nguội, vắt th&ecirc;m chanh rồi thưởng thức&hellip;</p>\r\n<p align=\"center\"><img class=\"news-image loaded\" src=\"https://icdn.24h.com.vn/upload/2-2022/images/2022-06-15/1655257634-a07abbc59e067cd60475696e1db033d2-width600height400.jpg\" alt=\"4 thức uống giải nhiệt m&ugrave;a h&egrave;, gi&uacute;p l&agrave;n da tươi s&aacute;ng - 3\" width=\"660\" data-original=\"https://icdn.24h.com.vn/upload/2-2022/images/2022-06-15/1655257634-a07abbc59e067cd60475696e1db033d2-width600height400.jpg\" data-was-processed=\"true\"></p>\r\n<p class=\"img_chu_thich_0407\">Nước chanh gừng, bạc h&agrave;&hellip;</p>\r\n<p><strong>3. Sinh tố quả mọng</strong></p>\r\n<p>C&aacute;c loại quả mọng như việt quất, d&acirc;u t&acirc;y, d&acirc;u tằm, m&acirc;m x&ocirc;i&hellip; kh&ocirc;ng chỉ si&ecirc;u ngon m&agrave; c&ograve;n rất c&oacute; lợi cho việc l&agrave;m s&aacute;ng da.</p>\r\n<p>C&aacute;ch chế biến: Cho c&aacute;c loại quả mọng như việt quất, d&acirc;u t&acirc;y, d&acirc;u tằm, m&acirc;m x&ocirc;i&hellip; v&agrave;o m&aacute;y xay sinh tố. Th&ecirc;m một th&igrave;a sữa chua hoặc sữa với một th&igrave;a mật ong v&agrave;o xay nhuyễn rồi thưởng thức.</p>\r\n<p align=\"center\"><img class=\"news-image loaded\" src=\"https://icdn.24h.com.vn/upload/2-2022/images/2022-06-15/1655257634-65a51a7af171ee01f461dfae129902e5-width600height400.jpg\" alt=\"4 thức uống giải nhiệt m&ugrave;a h&egrave;, gi&uacute;p l&agrave;n da tươi s&aacute;ng - 4\" width=\"660\" data-original=\"https://icdn.24h.com.vn/upload/2-2022/images/2022-06-15/1655257634-65a51a7af171ee01f461dfae129902e5-width600height400.jpg\" data-was-processed=\"true\"></p>\r\n<p class=\"img_chu_thich_0407\">Sinh tố quả mọng.</p>\r\n<p><strong>4. Nước dừa v&agrave; tr&aacute;i c&acirc;y</strong></p>\r\n<p>Thời tiết m&ugrave;a h&egrave; n&oacute;ng nực, một cốc nước dừa tươi sẽ gi&uacute;p bạn giải nhanh cơn kh&aacute;t. Nước dừa kh&ocirc;ng chỉ cung cấp nước, kho&aacute;ng chất, chất điện giải cho cơ thể m&agrave; c&ograve;n gi&uacute;p l&agrave;m s&aacute;ng da, chữa r&aacute;m nắng&hellip;</p>\r\n<p>C&aacute;ch chế biến: Cho nửa cốc nước dừa tươi, th&ecirc;m 2 quả chuối ch&iacute;n, 1 cốc dứa v&agrave; xo&agrave;i cắt nhỏ, v&agrave; 1/2 quả bơ cắt l&aacute;t v&agrave; trộn đều tất cả c&aacute;c th&agrave;nh phần n&agrave;y trong m&aacute;y trộn v&agrave; lọc nước &eacute;p. Nếu dung dịch đặc, h&atilde;y th&ecirc;m nước dừa. Bạn cũng c&oacute; thể th&ecirc;m mật ong v&agrave; hạt lanh v&agrave;o rồi thưởng thức.</p>\r\n<p align=\"center\"><img class=\"news-image loaded\" src=\"https://icdn.24h.com.vn/upload/2-2022/images/2022-06-15/1655257634-b3ceb65d2215b6a577d7a6936a853e08-width600height400.jpg\" alt=\"4 thức uống giải nhiệt m&ugrave;a h&egrave;, gi&uacute;p l&agrave;n da tươi s&aacute;ng - 5\" width=\"660\" data-original=\"https://icdn.24h.com.vn/upload/2-2022/images/2022-06-15/1655257634-b3ceb65d2215b6a577d7a6936a853e08-width600height400.jpg\" data-was-processed=\"true\"></p>\r\n<p class=\"img_chu_thich_0407\">Nước dừa vừa cung cấp nước, vừa gi&uacute;p cho l&agrave;n da tươi s&aacute;ng.</p>', 218, 5, 38, 1, '2022-11-18 19:35:41', '2022-11-22 05:40:13'),
(73, 'Bình Dương: Tân Uyên tập trung hoàn thiện quy hoạch, sẵn sàng lên thành phố năm 2023', 'binh-duong-tan-uyen-tap-trung-hoan-thien-quy-hoach-san-sang-len-thanh-pho-nam-2023', 'Từ chuẩn đô thị loại III, thị xã Tân Uyên đang tích cực điều chỉnh quy hoạch, khai thác nguồn lực công nghiệp - dịch vụ chất lượng cao để chuẩn bị lên thành phố năm 2023.', '<p>Hiện T&acirc;n Uy&ecirc;n đạt 5/5 ti&ecirc;u ch&iacute; về quy m&ocirc; d&acirc;n số, diện t&iacute;ch tự nhi&ecirc;n, số đơn vị h&agrave;nh ch&iacute;nh trực thuộc, ph&acirc;n loại đ&ocirc; thị, cơ cấu v&agrave; tr&igrave;nh độ ph&aacute;t triển kinh tế - x&atilde; hội. D&acirc;n số to&agrave;n thị x&atilde; c&oacute; hơn 421.000 người, tương đương TP Dĩ An v&agrave; vượt xa TP Thủ Dầu Một, khoảng 341.830 người.</p>\r\n<p><strong>Đủ điều kiện l&ecirc;n th&agrave;nh phố</strong></p>\r\n<p>Theo l&atilde;nh đạo T&acirc;n Uy&ecirc;n, thời gian qua, việc đầu tư x&acirc;y dựng c&aacute;c khu chức năng, hạ tầng kỹ thuật đ&ocirc; thị cơ bản ph&ugrave; hợp quy hoạch. C&aacute;c dự &aacute;n đầu tư x&acirc;y dựng đ&ocirc; thị triển khai thuận lợi đ&atilde; từng bước thay đổi diện mạo kh&ocirc;ng gian cảnh quan, thu h&uacute;t lượng lớn lao động đến sinh sống v&agrave; l&agrave;m việc, g&oacute;p phần th&uacute;c đẩy kinh tế - x&atilde; hội ph&aacute;t triển.</p>\r\n<p align=\"center\"><img class=\"news-image initial loading\" src=\"https://icdn.24h.com.vn/upload/2-2022/images/2022-06-16/Tan-Uyen-tap-trung-hoan-thien-quy-hoach-san-sang-len-thanh-pho-nam-2023-11-1655365766-730-width660height371.jpg\" alt=\"B&igrave;nh Dương: T&acirc;n Uy&ecirc;n tập trung ho&agrave;n thiện quy hoạch, sẵn s&agrave;ng l&ecirc;n th&agrave;nh phố năm 2023 - 1\" data-was-processed=\"true\"></p>\r\n<p class=\"img_chu_thich_0407\">Hạ tầng ho&agrave;n thiện tạo c&uacute; h&iacute;ch để T&acirc;n Uy&ecirc;n l&ecirc;n th&agrave;nh phố</p>\r\n<p>Tr&ecirc;n thực tế, c&aacute;c yếu tố mới ph&aacute;t sinh so với quy hoạch được duyệt trước đ&acirc;y đ&ograve;i hỏi địa phương phải điều chỉnh lại mục đ&iacute;ch sử dụng đất c&aacute;c v&ugrave;ng l&acirc;n cận khu c&ocirc;ng nghiệp, khu trung t&acirc;m h&agrave;nh ch&iacute;nh thị x&atilde;, điều chỉnh chức năng, quy m&ocirc; ranh giới của c&aacute;c khu đ&ocirc; thị.</p>\r\n<p>B&aacute;o c&aacute;o Kế hoạch Ph&aacute;t triển Nh&agrave; ở B&igrave;nh Dương giai đoạn 2021-2025, to&agrave;n tỉnh sẽ d&agrave;nh ra 1.600 ha đất cho c&aacute;c dự &aacute;n, tổng vốn đầu tư dự kiến 130.000 tỷ đồng. Trong đ&oacute;, T&acirc;n Uy&ecirc;n đứng đầu khi cần đến 274 ha, vốn đầu tư hơn 22.000 tỷ đồng để ph&aacute;t triển nh&agrave; ở mới.</p>\r\n<p>Mặt kh&aacute;c, cơ cấu kinh tế địa phương vẫn tiếp tục ph&aacute;t triển c&ocirc;ng nghiệp theo quy hoạch chung tỉnh B&igrave;nh Dương, trở th&agrave;nh trung t&acirc;m c&ocirc;ng nghiệp c&ocirc;ng nghệ cao. Cụ thể, T&acirc;n Uy&ecirc;n tiếp tục ho&agrave;n thiện c&aacute;c cụm, khu c&ocirc;ng nghiệp hiện hữu (Nam T&acirc;n Uy&ecirc;n, Nam T&acirc;n Uy&ecirc;n mở rộng, VSIP II, cụm c&ocirc;ng nghiệp Uy&ecirc;n Hưng, Th&aacute;i H&ograve;a, Ph&uacute; Ch&aacute;nh) v&agrave; đầu tư x&acirc;y dựng c&aacute;c khu c&ocirc;ng nghiệp dọc trục đường V&agrave;nh đai 4 như VSIP III, Nam T&acirc;n Uy&ecirc;n mở rộng giai đoạn 2...</p>\r\n<p>Đối với c&aacute;c khu sản xuất tập trung v&agrave; c&aacute;c cơ sở sản xuất nhỏ lẻ xen lẫn trong khu d&acirc;n cư, thị x&atilde; từng bước thực hiện việc di dời. Đồng thời, đẩy mạnh tỷ trọng ng&agrave;nh thương mại - dịch vụ, logistics, tập trung ph&aacute;t triển c&aacute;c lĩnh vực t&agrave;i ch&iacute;nh ng&acirc;n h&agrave;ng tại c&aacute;c khu vực trung t&acirc;m như Uy&ecirc;n Hưng, T&acirc;n Phước Kh&aacute;nh&hellip;</p>\r\n<p align=\"center\"><img class=\"news-image loaded\" src=\"https://icdn.24h.com.vn/upload/2-2022/images/2022-06-16/Tan-Uyen-tap-trung-hoan-thien-quy-hoach-san-sang-len-thanh-pho-nam-2023-22-1655365766-888-width660height373.jpg\" alt=\"B&igrave;nh Dương: T&acirc;n Uy&ecirc;n tập trung ho&agrave;n thiện quy hoạch, sẵn s&agrave;ng l&ecirc;n th&agrave;nh phố năm 2023 - 2\" data-original=\"https://icdn.24h.com.vn/upload/2-2022/images/2022-06-16/Tan-Uyen-tap-trung-hoan-thien-quy-hoach-san-sang-len-thanh-pho-nam-2023-22-1655365766-888-width660height373.jpg\" data-was-processed=\"true\"></p>\r\n<p class=\"img_chu_thich_0407\">T&acirc;n Uy&ecirc;n sở hữu nhiều cụm, khu c&ocirc;ng nghiệp lớn</p>\r\n<p>Đặc biệt, để n&acirc;ng loại đ&ocirc; thị T&acirc;n Uy&ecirc;n đạt ti&ecirc;u ch&iacute; loại II năm 2023, ch&iacute;nh quyền địa phương x&uacute;c tiến đầu tư loạt dự &aacute;n giao th&ocirc;ng trọng điểm như V&agrave;nh đai 3, V&agrave;nh Đai 4, đại lộ Uy&ecirc;n Hưng - Thủ Dầu Một, đại lộ Nam T&acirc;n Uy&ecirc;n&hellip;</p>\r\n<p>Hiện thị x&atilde; T&acirc;n Uy&ecirc;n đang ho&agrave;n thiện c&aacute;c thủ tục để n&acirc;ng cấp l&ecirc;n th&agrave;nh phố. Dự kiến, giữa năm nay, T&acirc;n Uy&ecirc;n sẽ ho&agrave;n th&agrave;nh đề &aacute;n gửi Sở Nội vụ, HĐND, UBND tỉnh B&igrave;nh Dương, sau đ&oacute; tr&igrave;nh Trung ương theo quy định.</p>\r\n<p><strong>B&agrave;n đạp từ v&ugrave;ng l&otilde;i trung t&acirc;m</strong></p>\r\n<p>Trong lộ tr&igrave;nh ph&aacute;t triển của T&acirc;n Uy&ecirc;n, nếu Uy&ecirc;n Hưng l&agrave; trung t&acirc;m h&agrave;nh ch&iacute;nh sẵn c&oacute; th&igrave; T&acirc;n Phước Kh&aacute;nh đ&oacute;ng vai tr&ograve; hạt nh&acirc;n, trở th&agrave;nh khu trung t&acirc;m trong mạng lưới kết nối c&aacute;c th&agrave;nh phố, thị x&atilde; lớn nhất tỉnh l&agrave; Thủ Dầu Một, Dĩ An, Thuận An, T&acirc;n Uy&ecirc;n, Bến C&aacute;t. Theo đ&oacute;, T&acirc;n Phước Kh&aacute;nh được quy hoạch để trở th&agrave;nh nơi ph&aacute;t triển c&aacute;c c&ocirc;ng tr&igrave;nh gi&aacute;o dục, dịch vụ c&ocirc;ng nghiệp, cảng s&ocirc;ng đi c&ugrave;ng với c&aacute;c dự &aacute;n nh&agrave; ở, khu đ&ocirc; thị hiện đại.</p>\r\n<p>Chưa kể, khu vực n&agrave;y đ&atilde; v&agrave; đang tập trung mọi nguồn lực để đẩy mạnh n&acirc;ng cấp c&aacute;c tuyến đường ĐT 747B với lộ giới quy hoạch 74m, đường ĐT 746 lộ giới 42m v&agrave; x&uacute;c tiến đầu tư c&aacute;c trục đường đối nội quan trọng như T&acirc;n Phước Kh&aacute;nh 10, đường LKV13 với lộ giới 28m.</p>\r\n<p>Theo đại diện Tập đo&agrave;n BĐS An Gia, nhiều yếu tố cộng hưởng từ quy hoạch ph&aacute;t triển chiến lược đi c&ugrave;ng với hệ thống c&aacute;c tiện &iacute;ch x&atilde; hội ng&agrave;y c&agrave;ng đầy đủ đ&atilde; đưa T&acirc;n Phước Kh&aacute;nh trở th&agrave;nh t&acirc;m điểm của d&ograve;ng vốn đầu tư bất động sản (BĐS). Đ&acirc;y cũng l&agrave; khu vực c&oacute; nền kinh tế ph&aacute;t triển năng động bậc nhất B&igrave;nh Dương, thu h&uacute;t lượng lớn tri thức, c&aacute;c chuy&ecirc;n gia về an cư lạc nghiệp.</p>\r\n<p align=\"center\"><img class=\"news-image loaded\" src=\"https://icdn.24h.com.vn/upload/2-2022/images/2022-06-16/Tan-Uyen-tap-trung-hoan-thien-quy-hoach-san-sang-len-thanh-pho-nam-2023-33-1655365766-924-width660height439.jpg\" alt=\"B&igrave;nh Dương: T&acirc;n Uy&ecirc;n tập trung ho&agrave;n thiện quy hoạch, sẵn s&agrave;ng l&ecirc;n th&agrave;nh phố năm 2023 - 3\" data-original=\"https://icdn.24h.com.vn/upload/2-2022/images/2022-06-16/Tan-Uyen-tap-trung-hoan-thien-quy-hoach-san-sang-len-thanh-pho-nam-2023-33-1655365766-924-width660height439.jpg\" data-was-processed=\"true\"></p>\r\n<p class=\"img_chu_thich_0407\">Khu biệt lập The Standard được đầu tư hơn 1.200 tỷ đồng</p>\r\n<p>Đ&aacute;ng n&oacute;i, tại T&acirc;n Phước Kh&aacute;nh hiện chỉ c&oacute; duy nhất dự &aacute;n The Standard của An Gia được x&acirc;y dựng để phục vụ giới chuy&ecirc;n gia, kỹ sư cấp cao. Dự &aacute;n sở hữu lợi thế lớn về kết nối, liền kề với tuyến V&agrave;nh Đai 3, V&agrave;nh Đai 4 gi&uacute;p cư d&acirc;n dễ d&agrave;ng di chuyển đến c&aacute;c th&agrave;nh phố, thị x&atilde;, c&aacute;c khu c&ocirc;ng nghiệp, trung t&acirc;m thương mại, kho b&atilde;i logistics&hellip;</p>\r\n<p>Mặt kh&aacute;c, nhờ lối ph&aacute;t triển sản phẩm mới mẻ c&ugrave;ng số lượng hạn chế, c&aacute;c sản phẩm villa, nh&agrave; phố biệt lập tại The Standard ghi nhận lực cầu tốt ở cả thị trường sơ cấp lẫn thứ cấp.</p>\r\n<p>&ldquo;<em>Đặc biệt, trong bối cảnh thị trường B&igrave;nh Dương c&oacute; nguy cơ bội thực nguồn cung căn hộ, d&ograve;ng sản phẩm nh&agrave; phố biệt lập mang đến một cơ hội sinh lời từ sự kh&aacute;c biệt. Nhiều người mua t&igrave;m kiếm lợi nhuận bền vững từ cho thu&ecirc;, hướng đến nh&oacute;m chuy&ecirc;n gia trong v&agrave; ngo&agrave;i nước đ&ograve;i hỏi cao về chất lượng sống. Đ&acirc;y cũng được xem như t&agrave;i sản t&iacute;ch lũy về l&acirc;u d&agrave;i cho thế hệ tương lai&rdquo;</em>, đại diện An Gia n&oacute;i.</p>\r\n<p>Theo chuy&ecirc;n gia, sự thiếu vắng d&ograve;ng bất động sản cao cấp n&agrave;y đang tạo \"khoảng trống\" dư địa đầy hấp dẫn đối với c&aacute;c doanh nghiệp cả trong v&agrave; ngo&agrave;i nước. Sau thời gian ph&aacute;t triển mạnh ph&acirc;n kh&uacute;c căn hộ vừa t&uacute;i tiền, thị trường T&acirc;n Uy&ecirc;n hứa hẹn sẽ tiếp tục trưởng th&agrave;nh với sự xuất hiện của những d&ograve;ng sản phẩm cao cấp hơn. Đ&acirc;y sẽ l&agrave; những sản phẩm được ph&aacute;t triển theo ti&ecirc;u chuẩn chất lượng sống mới, tương đương với trải nghiệm sống tiện nghi, hiện đại tại TP.HCM.</p>', 218, 14, 41, 1, '2022-11-18 19:35:41', '2023-02-07 08:26:21'),
(74, 'Đáp án đề thi môn Toán vào lớp 10 Hà Nội năm 2022 cập nhật nhanh nhất', 'dap-an-de-thi-mon-toan-vao-lop-10-ha-noi-nam-2022-cap-nhat-nhanh-nhat', 'Đề thi và đáp án đề thi môn Toán vào lớp 10 TP Hà Nội năm 2022 được cập nhật nhanh và chính xác nhất trên Tinmoi.vn.', '<p>Cập nhật đề thi, đ&aacute;p &aacute;n&nbsp; gợi &yacute; đề thi v&agrave;o 10 m&ocirc;n To&aacute;n tại H&agrave; Nội năm 2022 ch&iacute;nh x&aacute;c nhất tr&ecirc;n Tinmoi.vn.</p>\r\n<figure class=\"expNoEdit\"><img title=\"Đ&aacute;p &aacute;n đề thi m&ocirc;n To&aacute;n v&agrave;o lớp 10 H&agrave; Nội năm 2022 cập nhật nhanh nhất 1\" src=\"https://cdnmedia.tinmoi.vn/upload/nguyenxuan/2022/06/19/dap-an-de-thi-mon-toan-vao-lop-10-ha-noi-nam-2022-cap-nhat-nhanh-nhat-1655611648-1.jpg\" alt=\"Đ&aacute;p &aacute;n đề thi m&ocirc;n To&aacute;n v&agrave;o lớp 10 H&agrave; Nội năm 2022 cập nhật nhanh nhất 1\" width=\"675\" height=\"4096\">\r\n<figcaption>Đ&aacute;p &aacute;n đề thi m&ocirc;n To&aacute;n v&agrave;o lớp 10 H&agrave; Nội năm 2022 cập nhật nhanh nhất</figcaption>\r\n</figure>\r\n<figure class=\"expNoEdit\"><img title=\"Đ&aacute;p &aacute;n đề thi m&ocirc;n To&aacute;n v&agrave;o lớp 10 H&agrave; Nội năm 2022 cập nhật nhanh nhất 2\" src=\"https://cdnmedia.tinmoi.vn/upload/nguyenxuan/2022/06/19/dap-an-de-thi-mon-toan-vao-lop-10-ha-noi-nam-2022-cap-nhat-nhanh-nhat-1655608799-1.jpg\" alt=\"Đ&aacute;p &aacute;n đề thi m&ocirc;n To&aacute;n v&agrave;o lớp 10 H&agrave; Nội năm 2022 cập nhật nhanh nhất 2\" width=\"1536\" height=\"2048\">\r\n<figcaption>Đề thi m&ocirc;n To&aacute;n tuyển sinh lớp 10 H&agrave; Nội năm học 2022-2023.</figcaption>\r\n</figure>\r\n<p>S&aacute;ng nay c&aacute;c học sinh tại H&agrave; Nội tiếp tục ho&agrave;n th&agrave;nh b&agrave;i thi m&ocirc;n To&aacute;n của kỳ thi tuyển sinh v&agrave;o lớp 10 THPT c&ocirc;ng lập năm học 2022 - 2023. Sau 2 m&ocirc;n thi đầu ti&ecirc;n l&agrave; m&ocirc;n Ngữ văn, Tiếng Anh được đ&aacute;nh gi&aacute; vừa sức th&igrave; c&aacute;c th&iacute; sinh bước v&agrave;o m&ocirc;n thi cuối với t&acirc;m thế rất hứng khởi.</p>\r\n<p>M&ocirc;n To&aacute;n với thời gian l&agrave;m b&agrave;i 120 ph&uacute;t, bắt đầu từ 8h. T&iacute;nh đến buổi thi m&ocirc;n To&aacute;n s&aacute;ng nay, c&oacute; 41 th&iacute; sinh l&agrave; F0 tham gia thi lớp 10 tại H&agrave; Nội.</p>\r\n<figure class=\"expNoEdit\"><img title=\"Đ&aacute;p &aacute;n đề thi m&ocirc;n To&aacute;n v&agrave;o lớp 10 H&agrave; Nội năm 2022 cập nhật nhanh nhất 3\" src=\"https://cdnmedia.tinmoi.vn/upload/nguyenxuan/2022/06/19/dap-an-de-thi-mon-toan-vao-lop-10-ha-noi-nam-2022-cap-nhat-nhanh-nhat-1655607238-1.jpg\" alt=\"Đ&aacute;p &aacute;n đề thi m&ocirc;n To&aacute;n v&agrave;o lớp 10 H&agrave; Nội năm 2022 cập nhật nhanh nhất 3\" width=\"1200\" height=\"675\">\r\n<figcaption>Đ&aacute;p &aacute;n đề thi m&ocirc;n To&aacute;n v&agrave;o lớp 10 H&agrave; Nội năm 2022 cập nhật nhanh nhất</figcaption>\r\n</figure>\r\n<p>Trưởng ph&ograve;ng Quản l&yacute; thi v&agrave; Kiểm định chất lượng (Sở GD-ĐT H&agrave; Nội) Phạm Quốc Toản cho biết trong kỳ thi tuyển sinh lớp 10 năm nay to&agrave;n th&agrave;nh phố c&oacute; 186 th&iacute; sinh l&agrave; F0. Với 41 th&iacute; sinh F0 đăng k&yacute; dự thi, H&agrave; Nội đ&atilde; bố tr&iacute; 37 ph&ograve;ng thi được đảm bảo y&ecirc;u cầu ph&ograve;ng chống dịch theo hướng dẫn của Sở Y tế v&agrave; Sở GD-ĐT th&agrave;nh phố. Tại những ph&ograve;ng thi n&agrave;y, c&aacute;n bộ coi thi sẽ mặc đồ bảo hộ. Sau mỗi b&agrave;i thi ph&ograve;ng sẽ được phun y tế khử khuẩn để chuẩn bị cho b&agrave;i thi tiếp theo. 145 th&iacute; sinh c&ograve;n lại c&oacute; nguyện vọng x&eacute;t tuyển theo điểm học bạ.</p>\r\n<p>Ng&agrave;y mai, c&aacute;c th&iacute; sinh thi v&agrave;o trường chuy&ecirc;n sẽ tiếp tục l&agrave;m b&agrave;i thi m&ocirc;n chuy&ecirc;n.</p>\r\n<p>Năm nay, tổng số th&iacute; sinh đăng k&yacute; dự thi l&agrave; 106.609 (trong đ&oacute; c&oacute; 321 th&iacute; sinh tự do), số th&iacute; sinh đề nghị x&eacute;t tuyển thẳng l&agrave; 516. Th&agrave;nh phố tổ chức số ph&ograve;ng thi l&agrave; 4.550 tại khoảng 203 điểm thi. Số c&aacute;n bộ trực tiếp tham gia coi thi l&agrave; khoảng 14.000 người; số c&aacute;n bộ tham gia phục vụ, bảo vệ điểm thi l&agrave; khoảng 3.000 người; số c&aacute;n bộ tham gia chấm thi l&agrave; khoảng 2.500 người.</p>', 218, 6, 74, 1, '2022-11-18 19:35:41', '2022-11-22 05:46:16');
INSERT INTO `posts` (`id`, `title`, `slug`, `excerpt`, `body`, `user_id`, `category_id`, `views`, `approved`, `created_at`, `updated_at`) VALUES
(75, 'Quan họ Bắc Ninh thay lời tạm biệt SEA Games 31', 'quan-ho-bac-ninh-thay-loi-tam-biet-sea-games-31', 'HÀ NỘI-Các liền anh, liền chị hát quan họ, mời nghệ sĩ nước bạn trầu têm cánh phượng, trong lễ bế mạc SEA Games 31, tối 23/5.', '<div id=\"divfirst\">\r\n<p class=\"Normal\">Sau tiết mục của đo&agrave;n Campuchia, hơn 100 nghệ sĩ - nam mặc &aacute;o the, khăn xếp, nữ mặc &aacute;o tứ th&acirc;n, đeo n&oacute;n quai thao - bước ra s&acirc;n khấu trong nền nhạc giai điệu&nbsp;<em>Mời trầu</em>. C&aacute;c liền anh, liền chị cầm khay trầu t&ecirc;m c&aacute;nh phượng mời nghệ sĩ nước bạn, thể hiện tinh thần hiếu kh&aacute;ch, th&acirc;n thiện v&agrave; n&eacute;t đẹp văn h&oacute;a d&acirc;n tộc.</p>\r\n<p class=\"Normal\">Sau đ&oacute;, họ tiếp tục thể hiện li&ecirc;n kh&uacute;c&nbsp;<em>Gi&atilde; bạn, Người ở đừng về.</em>&nbsp;Phần cuối, c&aacute;c nghệ sĩ đứng tr&ecirc;n s&acirc;n khấu, vẫy tay ch&agrave;o kh&aacute;n giả, kh&eacute;p lại SEA Games 31.</p>\r\n<div>\r\n<figure class=\"item_slide_show clearfix\">\r\n<div id=\"video_parent_355380\" class=\"box_embed_video_parent embed_video_new\" data-vcate=\"1003834\" data-vscate=\"1003007\" data-vscaten=\"Giải tr&iacute;\" data-vid=\"355380\" data-ratio=\"1\" data-articleoriginal=\"4467180\" data-ads=\"1\" data-license=\"0\" data-duration=\"271\" data-brandsafe=\"0\" data-type=\"0\" data-play=\"1\" data-frame=\"\" data-aot=\"\" data-videoclassify=\"None\" data-initdom=\"1\" data-view=\"inview\" data-auto=\"1\" data-status=\"play\">\r\n<div id=\"embed_video_355380\" class=\"box_embed_video\"></div>\r\n</div>\r\n<figcaption class=\"desc_cation\">\r\n<div class=\"inner_caption\">&nbsp;</div>\r\n</figcaption>\r\n</figure>\r\n</div>\r\n<p class=\"Normal\">Đạo diễn Ho&agrave;ng C&ocirc;ng Cường cho biết &ecirc;k&iacute;p chọn quan họ - di sản văn h&oacute;a phi vật thể của nh&acirc;n loại - nhằm giới thiệu n&eacute;t nghệ thuật đặc sắc của d&acirc;n tộc đến bạn b&egrave; khu vực v&agrave; thế giới. \"Việt Nam tự h&agrave;o v&igrave; c&oacute; những di sản, ch&uacute;ng ta phải khoe ra với năm ch&acirc;u. Hơn nữa, lời ca tiếng h&aacute;t đầy &yacute; nghĩa như lời ch&agrave;o cởi mở, th&acirc;n thiện với những h&igrave;nh ảnh đẹp\", đạo diễn n&oacute;i.</p>\r\n<p class=\"Normal\"><strong>Với chủ đề \"Hội tụ để tỏa s&aacute;ng\", lễ bế mạc kết hợp giữa nghệ thuật truyền thống v&agrave; &acirc;m nhạc s&ocirc;i động</strong>.&nbsp;Mở m&agrave;n l&agrave; li&ecirc;n kh&uacute;c&nbsp;<em>Nồng n&agrave;n H&agrave; Nội, Th&aacute;ng 10 H&agrave; Nội, H&agrave; Nội niềm tin v&agrave; hy vọng</em>... qua sự thể hiện Đ&ocirc;ng H&ugrave;ng, Kh&aacute;nh Linh, Bảo Tr&acirc;m, Phạm Anh Duy. H&igrave;nh ảnh Thăng Long ngh&igrave;n năm văn hiến - nơi tổ chức ch&iacute;nh SEA Games 31 - hiện l&ecirc;n qua hoạt cảnh đạp x&iacute;ch l&ocirc; tr&ecirc;n phố, phố cổ r&ecirc;u phong v&agrave; những con đường l&aacute; v&agrave;ng rơi được tr&igrave;nh chiếu tr&ecirc;n m&agrave;n h&igrave;nh led 580 m2.</p>\r\n<p class=\"Normal\">Ca sĩ Đ&ocirc;ng H&ugrave;ng - g&oacute;p mặt trong li&ecirc;n kh&uacute;c về H&agrave; Nội - n&oacute;i vinh dự khi được tham gia chương tr&igrave;nh. Ca sĩ c&ugrave;ng c&aacute;c th&agrave;nh vi&ecirc;n trong &ecirc;k&iacute;p tập luyện li&ecirc;n tục trong nhiều ng&agrave;y để kết hợp mượt m&agrave;. \"Tiết mục l&agrave; m&oacute;n qu&agrave; của nghệ sĩ thủ đ&ocirc; gửi tới bạn b&egrave; quốc tế. T&ocirc;i h&agrave;i l&ograve;ng với phần biểu diễn của bản th&acirc;n v&agrave; c&aacute;c đồng nghiệp\", Đ&ocirc;ng H&ugrave;ng cho biết.</p>\r\n</div>\r\n<div id=\"admbackground\" data-set=\"dfp\"></div>\r\n<div id=\"divend\">\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture data-inimage=\"done\"><source srcset=\"https://i1-giaitri.vnecdn.net/2022/05/24/xich-lo-1320-1653325403.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=7Akpyij-FafMtq5NjQqt_g 1x, https://i1-giaitri.vnecdn.net/2022/05/24/xich-lo-1320-1653325403.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=-saNLlAqjsLnv_Ieg19Rjg 1.5x, https://i1-giaitri.vnecdn.net/2022/05/24/xich-lo-1320-1653325403.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=0YZZexXuFAFEBuk3xE2Qdg 2x\" data-srcset=\"https://i1-giaitri.vnecdn.net/2022/05/24/xich-lo-1320-1653325403.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=7Akpyij-FafMtq5NjQqt_g 1x, https://i1-giaitri.vnecdn.net/2022/05/24/xich-lo-1320-1653325403.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=-saNLlAqjsLnv_Ieg19Rjg 1.5x, https://i1-giaitri.vnecdn.net/2022/05/24/xich-lo-1320-1653325403.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=0YZZexXuFAFEBuk3xE2Qdg 2x\"><img class=\"lazy lazied\" src=\"https://i1-giaitri.vnecdn.net/2022/05/24/xich-lo-1320-1653325403.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=7Akpyij-FafMtq5NjQqt_g\" alt=\"X&iacute;ch l&ocirc; - h&igrave;nh ảnh vốn quen thuộc tại H&agrave; Nội. Ảnh: Giang Huy\" loading=\"lazy\" data-src=\"https://i1-giaitri.vnecdn.net/2022/05/24/xich-lo-1320-1653325403.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=7Akpyij-FafMtq5NjQqt_g\" data-ll-status=\"loaded\"></picture>\r\n<div class=\"embed-container-ads\">&nbsp;</div>\r\n</div>\r\n<figcaption>\r\n<p class=\"Image\">X&iacute;ch l&ocirc; - h&igrave;nh ảnh quen thuộc tại H&agrave; Nội. Ảnh:&nbsp;<em>Giang Huy</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">T&igrave;nh y&ecirc;u đất nước, con người được khắc họa qua li&ecirc;n kh&uacute;c<em>&nbsp;Diệu kỳ Việt Nam, Ng&agrave;n ước mơ Việt Nam, Việt Nam tươi đẹp, Việt Nam những chuyến đi, Những tr&aacute;i tim Việt Nam, Việt Nam h&ograve;a thanh c&ugrave;ng năm ch&acirc;u,&nbsp;</em>do ca sĩ Văn Mai Hương, Uy&ecirc;n Linh, Dương Ho&agrave;ng Yến v&agrave; H&agrave; Nhi tr&igrave;nh b&agrave;y.</p>\r\n<p class=\"Normal\">Tiết mục nhảy đ&egrave;n led tr&ecirc;n nền nhạc b&agrave;i&nbsp;<em>Trống cơm,&nbsp;</em>được phối theo phong c&aacute;ch mới, gửi gắm th&ocirc;ng điệp về tinh thần hội nhập của Việt Nam. C&aacute;c nghệ sĩ đội n&oacute;n l&aacute;, mặc trang phục truyền thống nhưng nhảy hiphop v&agrave; thực hiện v&agrave;i thế v&otilde;.</p>\r\n<p class=\"Normal\">Campuchia - chủ nh&agrave; SEA Games 32 - gửi lời ch&agrave;o đến Li&ecirc;n đo&agrave;n thể thao c&aacute;c nước bằng loạt tiết mục: m&uacute;a hoa hồng Phn&ocirc;m P&ecirc;nh do cựu quốc vương Norodom Sihanouk s&aacute;ng t&aacute;c, m&uacute;a đương đại Linh vật Bồ c&acirc;u trắng, h&aacute;t ca kh&uacute;c&nbsp;<em>Cambodia Welcoming and Blessing (Campuchia ch&agrave;o đ&oacute;n v&agrave; ch&uacute;c ph&uacute;c)</em>,&nbsp;<em>Tinh thần chiến đấu</em>... Nghệ sĩ Thanh Hiền - gi&aacute;m đốc sản xuất lễ bế mạc - cho biết: \"Sự xuất hiện của c&aacute;c nghệ sĩ Campuchia thể hiện th&ocirc;ng điệp hội nhập, gắn kết bạn b&egrave; quốc tế, đồng thời hướng tới kỳ SEA Games tiếp theo\".</p>\r\n<div>\r\n<figure class=\"item_slide_show clearfix\">\r\n<div id=\"video_parent_355381\" class=\"box_embed_video_parent embed_video_new\" data-vcate=\"1003834\" data-vscate=\"1003007\" data-vscaten=\"Giải tr&iacute;\" data-vid=\"355381\" data-ratio=\"1\" data-articleoriginal=\"4467180\" data-ads=\"1\" data-license=\"0\" data-duration=\"142\" data-brandsafe=\"0\" data-type=\"0\" data-play=\"0\" data-frame=\"\" data-aot=\"\" data-videoclassify=\"None\" data-initdom=\"1\">\r\n<div class=\"box_img_video embed-container\" data-vid=\"355381\"><img src=\"https://iv1.vnecdn.net/giaitri/images/web/2022/05/23/campuchia-bieu-dien-tai-be-mac-sea-games-31-1653325187.jpg?w=750&amp;h=450&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=PeXiXtEf305Ty907trwhbw\" alt=\"Campuchia biểu diễn tại bế mạc SEA Games 31\">\r\n<div class=\"icon_blockvideo\">\r\n<div class=\"img_icon\">&nbsp;</div>\r\n<div class=\"image_icon_center\">&nbsp;</div>\r\n</div>\r\n</div>\r\n</div>\r\n<figcaption class=\"desc_cation\">\r\n<div class=\"inner_caption\">\r\n<p class=\"Image\">Tiết mục m&uacute;a của nghệ sĩ Campuchia. Video:&nbsp;<em>VTV6</em></p>\r\n</div>\r\n</figcaption>\r\n</figure>\r\n</div>\r\n<p class=\"Normal\">Sự kiện kh&ocirc;ng &aacute;p dụng nhiều c&ocirc;ng nghệ tr&igrave;nh diễn, tạo sự ho&agrave;nh tr&aacute;ng như khai mạc m&agrave; nhẹ nh&agrave;ng, ấm &aacute;p v&agrave; mang m&agrave;u sắc trẻ trung, hiện đại hơn. &Ecirc;k&iacute;p với hơn 400 người chỉ c&oacute; 10 ng&agrave;y chuẩn bị. Bế mạc tổ chức tại Cung Điền kinh trong nh&agrave;, phải chờ c&aacute;c nội dung thi đấu kết th&uacute;c n&ecirc;n &ecirc;k&iacute;p chỉ c&oacute; hai ng&agrave;y lắp r&aacute;p s&acirc;n khấu, &acirc;m thanh, &aacute;nh s&aacute;ng trước khi tổng duyệt. Trước đ&oacute;, lễ khai mạc giới thiệu văn h&oacute;a, con người Việt Nam với những đại cảnh mang hồn cốt d&acirc;n tộc, thể hiện t&igrave;nh cảm, sự th&acirc;n thiện, hiếu kh&aacute;ch.</p>\r\n<p class=\"Normal\"><a rel=\"follow\">SEA Games 31</a>&nbsp;diễn ra tại Việt Nam từ ng&agrave;y 12 đến 23/5 với 40 m&ocirc;n thể thao. Lần thứ hai Việt Nam đăng cai đại hội, sau năm 2003. H&agrave; Nội l&agrave; địa điểm thi đấu ch&iacute;nh, c&ugrave;ng 11 địa phương l&acirc;n cận gồm: Bắc Ninh, Hải Ph&ograve;ng, Hải Dương, Quảng Ninh, H&agrave; Nam, Nam Định, Ninh B&igrave;nh, H&ograve;a B&igrave;nh, Vĩnh Ph&uacute;c, Bắc Giang, Ph&uacute; Thọ. Việt Nam dẫn đầu với 205 huy chương v&agrave;ng, 125 huy chương bạc v&agrave; 116 huy chương đồng.</p>\r\n</div>', 218, 8, 0, 1, '2022-11-18 19:35:41', '2023-02-06 20:36:28'),
(76, 'Naked bike cỡ nhỏ Yamaha MT-15 phiên bản mới 2022 ra mắt với diện mạo đẹp hơn, giá từ 67,9 triệu đồng', 'naked-bike-co-nho-yamaha-mt-15-phien-ban-moi-2022-ra-mat-voi-dien-mao-dep-hon-gia-tu-679-trieu-dong', 'Yamaha MT-15 2022 tại Thái Lan sẽ có 3 tùy chọn màu gồm Xám Xanh neon, Xanh Đen, và Đen nhám.', '<p>Yamaha vừa ch&iacute;nh thức giới thiệu phi&ecirc;n bản mới 2022 cho mẫu MT-15 tại thị trường Th&aacute;i Lan&nbsp;với n&acirc;ng cấp ở m&agrave;u sắc v&agrave; tem xe, mang đến cho mẫu naked bike cỡ nhỏ diện mạo mới bắt mắt v&agrave; trẻ trung hơn.</p>\r\n<div class=\"width-100 inline\">\r\n<div id=\"ads-detail-below-sapo-320x180\" class=\"box-ads-anymind\"></div>\r\n</div>\r\n<p>Theo đ&oacute;,&nbsp;<a title=\"Gi&aacute; xe Yamaha MT-15 mới nhất h&ocirc;m nay\" href=\"https://tinxe.vn/gia-xe-may-yamaha-mt-15\" target=\"_blank\" rel=\"noopener\"><strong>Yamaha MT-15</strong></a>&nbsp;tại Th&aacute;i Lan sẽ được b&aacute;n ra thị trường với 3 t&ugrave;y chọn m&agrave;u sắc gồm X&aacute;m Xanh neon, Xanh Đen,&nbsp;v&agrave; Đen nh&aacute;m. Ngo&agrave;i t&ugrave;y chọn m&agrave;u sắc mới, Yamaha MT-15 2022 vẫn giữ nguy&ecirc;n thiết kế v&agrave; trang bị của mẫu MT-15 hiện đang c&oacute; tr&ecirc;n thị trường.</p>\r\n<div class=\"dvg_photo_box\">\r\n<figure><span class=\"lozadload-content\"><img class=\"lozad\" title=\"Yamaha MT-15 2022\" src=\"https://img.tinxe.vn/resize/1000x-/2022/06/08/XForF7yt/yamaha-mt-15-2022-1-1ae8.jpg\" alt=\"Yamaha MT-15 2022\" width=\"1600\" height=\"1067\" data-height=\"1067\" data-width=\"1600\" data-src=\"https://img.tinxe.vn/resize/1000x-/2022/06/08/XForF7yt/yamaha-mt-15-2022-1-1ae8.jpg\" data-loaded=\"true\"></span></figure>\r\n<p class=\"dvg_photo_caption\">Yamaha MT-15 2022 m&agrave;u X&aacute;m Xanh neon</p>\r\n</div>\r\n<p>Cụ thể, Yamaha MT-15 2022 vẫn mang thiết kế naked bike với ng&ocirc;n ngữ chung của d&ograve;ng MT-Series, đặc trưng bởi c&aacute;c đường n&eacute;t cơ bắp, t&aacute;o bạo v&agrave; đầu đ&egrave;n pha dạng projector. Ph&iacute;a tr&ecirc;n đ&egrave;n pha l&agrave; cặp đ&egrave;n định vị dạng LED được tạo h&igrave;nh như đ&ocirc;i mắt c&aacute;ch điệu.</p>\r\n<div class=\"dvg_photo_box\">\r\n<figure><span class=\"lozadload-content\"><img class=\"lozad\" title=\"Yamaha MT-15 2022\" src=\"https://img.tinxe.vn/resize/1000x-/2022/06/08/XForF7yt/yamaha-mt-15-2022-2-b330.jpg\" alt=\"Yamaha MT-15 2022\" width=\"1600\" height=\"1067\" data-height=\"1067\" data-width=\"1600\" data-src=\"https://img.tinxe.vn/resize/1000x-/2022/06/08/XForF7yt/yamaha-mt-15-2022-2-b330.jpg\" data-loaded=\"true\"></span></figure>\r\n<p class=\"dvg_photo_caption\">Yamaha MT-15 2022 m&agrave;u Xanh Đen</p>\r\n<div class=\"width-100 inline\">\r\n<div id=\"ads-detail-below-320x250\" class=\"box-ads-anymind\"></div>\r\n</div>\r\n</div>\r\n<p>Phần đu&ocirc;i xe được vuốt nhọn, đi k&egrave;m l&agrave; y&ecirc;n xe một mảnh ph&acirc;n th&agrave;nh hai tầng, tạo cho xe d&aacute;ng vẻ thể thao, linh hoạt, ăn nhập với thiết kế tổng thể của xe. B&igrave;nh xăng v&agrave; c&aacute;nh gi&oacute; kh&aacute; nổi bật với thiết kế sắc sảo, đi k&egrave;m l&agrave; logo Yamaha v&agrave; biểu tượng của d&ograve;ng naked bike MT-Series.</p>\r\n<div class=\"dvg_photo_box\">\r\n<figure><span class=\"lozadload-content\"><img class=\"lozad\" title=\"Yamaha MT-15 2022\" src=\"https://img.tinxe.vn/resize/1000x-/2022/06/08/XForF7yt/yamaha-mt-15-2022-3-1261.jpg\" alt=\"Yamaha MT-15 2022\" width=\"1600\" height=\"1067\" data-height=\"1067\" data-width=\"1600\" data-src=\"https://img.tinxe.vn/resize/1000x-/2022/06/08/XForF7yt/yamaha-mt-15-2022-3-1261.jpg\" data-loaded=\"true\"></span></figure>\r\n<p class=\"dvg_photo_caption\">Yamaha MT-15 2022 m&agrave;u Đen Nh&aacute;m</p>\r\n</div>\r\n<p>Về trang bị, Yamaha MT-15 2022 sở hữu hệ thống chiếu s&aacute;ng LED, phuộc trước h&agrave;nh tr&igrave;nh ngược, giảm x&oacute;c đơn sau, v&agrave;nh đ&uacute;c 17 inch v&agrave; phanh đĩa ti&ecirc;u chuẩn. Đồng hồ của xe l&agrave; m&agrave;n h&igrave;nh kỹ thuật số ho&agrave;n to&agrave;n, hiện thị c&aacute;c th&ocirc;ng tin vận h&agrave;nh của xe như v&ograve;ng tua m&aacute;y, tốc độ, cấp số, lượng xăng v&agrave; một số th&ocirc;ng tin kh&aacute;c.</p>\r\n<p>Cung cấp sức mạnh cho xe vẫn l&agrave; động cơ xylanh đơn dung t&iacute;ch 155cc, l&agrave;m m&aacute;t bằng dung dịch, mang đến cho xe c&ocirc;ng suất 19 m&atilde; lực tại tua m&aacute;y 10.000 v&ograve;ng/ph&uacute;t v&agrave; m&ocirc;men xoắn cực đại 14,7 Nm tại tua m&aacute;y 8.500 v&ograve;ng/ph&uacute;t. Đi k&egrave;m với động cơ n&agrave;y l&agrave; hộp số c&ocirc;n tay 6 cấp, hệ thống van biến thi&ecirc;n VVA, bộ ly hợp hỗ trợ v&agrave; l&aacute; trượt gi&uacute;p sang số mượt hơn.</p>\r\n<p>Tại thị trường l&aacute;ng giềng Th&aacute;i Lan, gi&aacute; xe MT-15 được c&ocirc;ng bố ở mức 101.000 baht, tương đương 67,9 triệu đồng. Trong khi đ&oacute; gi&aacute; xe Yamaha MT-15 tại thị trường Việt Nam đang được ni&ecirc;m yết ở mức 69 triệu đồng.</p>', 218, 13, 0, 1, '2022-11-18 19:35:41', '2023-02-07 08:32:58'),
(81, 'Siêu xe mui trần Ferrari 458 Spider từng của Hải Phòng về tay doanh nhân chơi xe bí ẩn Sài thành', 'sieu-xe-mui-tran-ferrari-458-spider-tung-cua-hai-phong-ve-tay-doanh-nhan-choi-xe-bi-an-sai-thanh', 'Đây chính là 1 trong những người chơi siêu xe đầu tiên tại Việt Nam cùng với Cường \"Đô-la\". Sau nhiều năm kín tiếng, đến nay, vị doanh nhân này đã thu mua không ít siêu xe và mới nhất là Ferrari 458 Spider chỉ có 2 chiếc tại Việt Nam.', '<p>Số lượng d&ograve;ng si&ecirc;u xe Ferrari mui trần tại Việt Nam ước t&iacute;nh tr&ecirc;n 10 chiếc nhưng chỉ c&oacute; 2 xe thuộc phi&ecirc;n bản 458 Spider. Trong đ&oacute;, ngo&agrave;i chiếc si&ecirc;u xe&nbsp;<a href=\"https://tinxe.vn/ferrari-458-spider-cd421\"><strong>Ferrari 458 Spider</strong></a>&nbsp;m&agrave;u đỏ của &ocirc;ng Đặng L&ecirc; Nguy&ecirc;n Vũ đ&atilde; b&aacute;n lại cho một c&ocirc;ng ty nhập khẩu ở quận 5 c&ograve;n c&oacute; một chiếc Ferrari 458 Spider m&agrave;u trắng được người chơi xe tại Hải Ph&ograve;ng đem về nước kh&aacute; k&iacute;n tiếng v&agrave; chỉ &iacute;t l&acirc;u sau đ&oacute; đ&atilde; b&aacute;n lại v&agrave;o Nam.</p>\r\n<div class=\"dvg_photo_box\">\r\n<figure><span id=\"imageGalleryId-1\" class=\"lozadload-content imageGalleryId\" data-src=\"https://img.tinxe.vn/2021/02/15/95ywCUPr/z2330870228221-81e13b59ebe3769ff4b84c8846cad3d5-6c2d.jpg\" data-desc=\"Xe đ&atilde; được đăng k&yacute; lại biển S&agrave;i th&agrave;nh. Ước t&iacute;nh doanh nh&acirc;n n&agrave;y sở hữu tr&ecirc;n 6 chiếc Ferrari tại Việt Nam\"><img class=\"lozad\" title=\"Xe đ&atilde; được đăng k&yacute; lại biển S&agrave;i th&agrave;nh. Ước t&iacute;nh doanh nh&acirc;n n&agrave;y sở hữu tr&ecirc;n 6 chiếc Ferrari tại Việt Nam\" src=\"https://img.tinxe.vn/resize/1000x-/2021/02/15/95ywCUPr/z2330870228221-81e13b59ebe3769ff4b84c8846cad3d5-6c2d.jpg\" alt=\"Xe đ&atilde; được đăng k&yacute; lại biển S&agrave;i th&agrave;nh. Ước t&iacute;nh doanh nh&acirc;n n&agrave;y sở hữu tr&ecirc;n 6 chiếc Ferrari tại Việt Nam\" width=\"1600\" height=\"1088\" data-height=\"1088\" data-width=\"1600\" data-src=\"https://img.tinxe.vn/resize/1000x-/2021/02/15/95ywCUPr/z2330870228221-81e13b59ebe3769ff4b84c8846cad3d5-6c2d.jpg\" data-loaded=\"true\"></span></figure>\r\n<p><em>Xe đ&atilde; được đăng k&yacute; lại biển S&agrave;i th&agrave;nh. Ước t&iacute;nh doanh nh&acirc;n n&agrave;y sở hữu tr&ecirc;n 6 chiếc Ferrari tại Việt Nam</em></p>\r\n</div>\r\n<p>Hiện chiếc si&ecirc;u xe mui trần Ferrari 458 Spider m&agrave;u trắng c&oacute; một kh&ocirc;ng hai tại Việt Nam n&agrave;y đ&atilde; về tay doanh nh&acirc;n k&iacute;n tiếng ở quận 2, Th&agrave;nh phố Hồ Ch&iacute; Minh.</p>\r\n<p>Được biết, người n&agrave;y ch&iacute;nh l&agrave; 1 trong những người chơi si&ecirc;u xe đầu ti&ecirc;n tại Việt Nam c&ugrave;ng với Cường \"Đ&ocirc;-la\" v&agrave; sau gần 15 năm vẫn duy tr&igrave; niềm đam m&ecirc; với những chiếc si&ecirc;u xe h&agrave;ng đầu của thương hiệu &Yacute;.</p>\r\n<div class=\"width-100 inline\">\r\n<div id=\"ads-detail-below-320x250\" class=\"box-ads-anymind\"></div>\r\n</div>\r\n<p>Chiếc si&ecirc;u xe Ferrari 458 Spider ngay sau khi được doanh nh&acirc;n n&agrave;y mua về đ&atilde; đổi biển số sang đầu 51H thay v&igrave; 15A như trước. Chiếc si&ecirc;u xe mui trần Ferrari 458 Spider thứ 2 ra biển số trắng ở Việt Nam c&oacute; ngoại thất sơn m&agrave;u trắng Bianco Avus.</p>\r\n<p>B&ecirc;n cạnh đ&oacute; xe c&ograve;n c&oacute; nhiều chi tiết sơn đen tạo điểm nhấn. Ngoại h&igrave;nh xe c&oacute; bộ m&acirc;m 5 chấu đơn sơn s&aacute;ng b&oacute;ng kếp hợp c&ugrave;ng kẹp phanh m&agrave;u v&agrave;ng nổi bật.</p>\r\n<p>So với phi&ecirc;n bản Ferrari 458 Italia, si&ecirc;u xe mui trần Ferrari 458 Spider sử dụng loại mui xếp cứng c&oacute; thể đ&oacute;ng hoặc mở mui chỉ trong thời gian từ 14&nbsp;đến 16 gi&acirc;y.</p>\r\n<p>Ngay cả khi xe đang chạy, người điều khiển cũng c&oacute; thể thao t&aacute;c đ&oacute;ng hoặc mở mui xe nhưng tốc độ của xe được h&atilde;ng Ferrari khuyến c&aacute;o dưới 50 km/h.</p>\r\n<div class=\"dvg_photo_box\">\r\n<figure><span id=\"imageGalleryId-13\" class=\"lozadload-content imageGalleryId\" data-src=\"https://img.tinxe.vn/2021/02/15/95ywCUPr/dsc02946-620a.jpg\" data-desc=\" Ferrari 458 Spider sử dụng mui xếp cứng\"><img class=\"lozad\" title=\" Ferrari 458 Spider sử dụng mui xếp cứng\" src=\"https://img.tinxe.vn/resize/1000x-/2021/02/15/95ywCUPr/dsc02946-620a.jpg\" alt=\" Ferrari 458 Spider sử dụng mui xếp cứng\" width=\"1600\" height=\"1066\" data-height=\"1066\" data-width=\"1600\" data-src=\"https://img.tinxe.vn/resize/1000x-/2021/02/15/95ywCUPr/dsc02946-620a.jpg\" data-loaded=\"true\"></span></figure>\r\n<p><em>Ferrari 458 Spider sử dụng mui xếp cứng</em></p>\r\n</div>\r\n<div class=\"dvg_photo_box\">\r\n<figure><span id=\"imageGalleryId-14\" class=\"lozadload-content imageGalleryId\" data-src=\"https://img.tinxe.vn/2021/02/15/95ywCUPr/dsc02943-695c.jpg\" data-desc=\"Xe c&oacute; bộ m&acirc;m 5 chấu đơn đi k&egrave;m kẹp phanh m&agrave;u v&agrave;ng\"><img class=\"lozad\" title=\"Xe c&oacute; bộ m&acirc;m 5 chấu đơn đi k&egrave;m kẹp phanh m&agrave;u v&agrave;ng\" src=\"https://img.tinxe.vn/resize/1000x-/2021/02/15/95ywCUPr/dsc02943-695c.jpg\" alt=\"Xe c&oacute; bộ m&acirc;m 5 chấu đơn đi k&egrave;m kẹp phanh m&agrave;u v&agrave;ng\" width=\"1600\" height=\"1066\" data-height=\"1066\" data-width=\"1600\" data-src=\"https://img.tinxe.vn/resize/1000x-/2021/02/15/95ywCUPr/dsc02943-695c.jpg\" data-loaded=\"true\"></span></figure>\r\n<p><em>Xe c&oacute; bộ m&acirc;m 5 chấu đơn đi k&egrave;m kẹp phanh m&agrave;u v&agrave;ng</em></p>\r\n</div>\r\n<div class=\"dvg_photo_box\">\r\n<figure><span id=\"imageGalleryId-15\" class=\"lozadload-content imageGalleryId\" data-src=\"https://img.tinxe.vn/2021/02/15/95ywCUPr/dsc02953-d1c7.jpg\" data-desc=\"Ferrari 458 Spider c&oacute; tốc độ tối đa 320 km/h\"><img class=\"lozad playing\" title=\"Ferrari 458 Spider c&oacute; tốc độ tối đa 320 km/h\" src=\"https://img.tinxe.vn/resize/1000x-/2021/02/15/95ywCUPr/dsc02953-d1c7.jpg\" alt=\"Ferrari 458 Spider c&oacute; tốc độ tối đa 320 km/h\" width=\"1600\" height=\"1066\" data-height=\"1066\" data-width=\"1600\" data-src=\"https://img.tinxe.vn/resize/1000x-/2021/02/15/95ywCUPr/dsc02953-d1c7.jpg\" data-loaded=\"true\"></span></figure>\r\n<p><em>Ferrari 458 Spider c&oacute; tốc độ tối đa 320 km/h</em></p>\r\n</div>\r\n<p>Phi&ecirc;n bản mui trần của Ferrari 458 Italia sử dụng động cơ V8, dung t&iacute;ch 4.5 l&iacute;t, sản sinh c&ocirc;ng suất tối đa 562 m&atilde; lực tại v&ograve;ng tua 9.000 v&ograve;ng/ph&uacute;t v&agrave; m&ocirc;-men xoắn cực đại 540 Nm.</p>\r\n<p>Động cơ kết hợp với hộp số ly hợp k&eacute;p 7 cấp, nhờ&nbsp;đ&oacute;, si&ecirc;u xe mui trần&nbsp;Ferrari 458 Spider mất 3,4 gi&acirc;y để tăng tốc l&ecirc;n 100 km/h từ vị tr&iacute; xuất ph&aacute;t trước khi đạt tốc độ tối đa 320 km/h.</p>', 218, 13, 0, 1, '2022-11-18 19:35:41', '2023-02-07 08:33:47'),
(98, 'Tay trắng khởi nghiệp, chàng trai sinh năm 1987 có thu nhập cả tỷ đồng mỗi năm', 'tay-trang-khoi-nghiep-chang-trai-sinh-nam-1987-co-thu-nhap-ca-ty-dong-moi-nam', 'Tay trắng khởi nghiệp, chàng trai sinh năm 1987 có thu nhập cả tỷ đồng mỗi năm', '<p>Từng đối mặt với h&agrave;ng loạt thử th&aacute;ch, kh&oacute; khăn trong những ng&agrave;y đầu khởi nghiệp nhưng đến nay sau hơn 10 năm anh Nguyễn Long Hải quản l&yacute; một doanh nghiệp về kỹ thuật v&agrave; điều h&agrave;nh một doanh nghiệp về ph&acirc;n phối v&agrave; xuất khẩu tại TP HCM cho biết đang bắt đầu gặt h&aacute;i th&agrave;nh quả từ những nỗ lực của m&igrave;nh.</p>\r\n<p>Ch&agrave;ng trai sinh năm 1987 cho biết thu nhập sau thuế mỗi năm của anh tăng li&ecirc;n tục từ 400 triệu đồng (2019) rồi đến 600 triệu đồng v&agrave; l&ecirc;n mức 1 tỷ đồng trong năm 2021 d&ugrave; nhiều hoạt động kinh tế bị ảnh hưởng nặng nề bởi t&aacute;c động ti&ecirc;u cực của dịch Covid-19.</p>\r\n<p align=\"center\">&nbsp;</p>\r\n<div id=\"container-24h-banner-in-image\">\r\n<div>\r\n<div><img class=\"news-image initial loading\" src=\"https://icdn.24h.com.vn/upload/1-2022/images/2022-01-19/Tay-trang-khoi-nghiep-chang-trai-1987-nguoi-TP-HCM-co-thu-nhap-ca-ty-dong-moi-nam-anh-1-1642600279-544-width740height493.jpg\" alt=\"Tay trắng khởi nghiệp, ch&agrave;ng trai sinh năm 1987 c&oacute; thu nhập cả tỷ đồng mỗi năm - 1\" data-was-processed=\"true\"></div>\r\n<div id=\"24h-banner-in-image\">\r\n<div id=\"EIALsdfbgU_139_15s\" class=\"txtCent\">\r\n<div class=\"text_adver_right\">&nbsp;</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n<p>&nbsp;</p>\r\n<p class=\"img_chu_thich_0407\" align=\"center\">Ch&agrave;ng trai sinh năm 1987 cho biết đang bắt đầu gặt h&aacute;i những th&agrave;nh c&ocirc;ng từ những nỗ lực trong những ng&agrave;y đầu khởi nghiệp</p>\r\n<p>Anh Hải&nbsp;học về lĩnh vực kỹ thuật v&agrave; c&ocirc;ng nghệ, sau khi ra trường từng c&oacute; thời gian l&agrave;m trong những tập đo&agrave;n lớn về sản xuất vi mạch của Nhật v&agrave; Mỹ tại Việt Nam. Tuy nhi&ecirc;n, x&aacute;c định sự đ&agrave;o thải khốc liệt trong lĩnh vực n&agrave;y n&ecirc;n ngay những năm 2010-2011, Hải đ&atilde; l&ecirc;n kế hoạch khởi nghiệp.</p>\r\n<p>Với việc từng chứng kiến rất nhiều bạn học kỹ thuật sau đ&oacute; bỏ nghề l&agrave;m kinh doanh, l&agrave;m thất bại v&agrave;i lần hết vốn rồi lại quay về nghề kỹ thuật, một số th&igrave; tiếp tục b&aacute;m trụ nhưng cũng lay lắt qua ng&agrave;y n&ecirc;n ngay từ đầu Hải đ&atilde; nỗ lực tạo ra sự kh&aacute;c biệt trong qu&aacute; tr&igrave;nh khởi nghiệp của m&igrave;nh.</p>\r\n<p>Theo đ&oacute;, ch&agrave;ng trai sinh năm 1987 quyết định vẫn theo đuổi c&ocirc;ng việc chuy&ecirc;n m&ocirc;n v&agrave;o giờ h&agrave;nh ch&iacute;nh, v&agrave; ngo&agrave;i giờ l&agrave;m việc th&igrave; theo đuổi ho&agrave;i b&atilde;o x&acirc;y dựng nghiệp kinh doanh.</p>\r\n<p>Chia sẻ về c&aacute;ch l&agrave;m của m&igrave;nh, anh Hải cho biết đầu ti&ecirc;n l&agrave; chọn định hướng ph&ugrave; hợp với chi ph&iacute; khởi nghiệp thấp bởi l&uacute;c bắt tay v&agrave;o khởi nghiệp số vốn trong tay anh gần như bằng kh&ocirc;ng. Sau đ&oacute; d&ugrave;ng phần thu nhập c&oacute; được từ việc l&agrave;m c&ocirc;ng ăn lương sau khi trừ c&aacute;c khoản chi ph&iacute; sinh hoạt để đầu tư v&agrave;o việc kinh doanh của m&igrave;nh h&agrave;ng th&aacute;ng, đến khi kinh doanh sinh lời, anh tiếp tục t&aacute;i đầu tư tiền lời n&agrave;y để mở rộng doanh nghiệp của m&igrave;nh.</p>\r\n<p>Nhờ v&agrave;o chiến thuật n&agrave;y, hầu hết những quyết định của anh kh&ocirc;ng bị chi phối bởi &aacute;p lực kiếm tiền để duy tr&igrave; doanh nghiệp, từ đ&oacute; gi&uacute;p anh c&oacute; được những quyết định s&aacute;ng suốt hơn để gi&uacute;p sự nghiệp v&agrave; doanh nghiệp của m&igrave;nh đi l&ecirc;n.</p>\r\n<p>Tuy nhi&ecirc;n, ch&agrave;ng trai trẻ cũng thừa nhận lựa chọn n&agrave;y đ&ograve;i hỏi bản th&acirc;n phải chấp nhận đi chậm hơn so với bạn b&egrave; c&ugrave;ng trang lứa trong những năm đầu bởi phải hy sinh một số cơ hội thăng tiến ở c&ocirc;ng việc chuy&ecirc;n m&ocirc;n, trong khi đ&oacute; việc kinh doanh cũng chỉ đang trong giai đoạn vừa học vừa l&agrave;m.</p>\r\n<p>Sau 5 năm đầu ti&ecirc;n đối mặt nhiều kh&oacute; khăn, đến những năm 2015-2016 th&igrave;&nbsp;mọi thứ bắt đầu trở n&ecirc;n dễ d&agrave;ng hơn, hoạt động kinh doanh của Hải v&agrave;o giai đoạn tự vận h&agrave;nh, anh cũng t&igrave;m được những đối t&aacute;c tin cậy để c&ugrave;ng x&acirc;y dựng sự nghiệp chung. &nbsp;</p>\r\n<p>Đến đầu năm 2018, doanh nghiệp ri&ecirc;ng của Hải tiếp tục c&oacute; sự thay đổi khi thực hiện chuyển đổi cơ cấu l&ecirc;n nền tảng online, b&ecirc;n cạnh đ&oacute; l&agrave; ho&agrave;n thiện cơ sở vật chất v&agrave; hạ tầng đ&atilde; đầu tư từ năm 2014.</p>\r\n<p>Nhiều hoạt động kinh doanh bị ảnh hưởng nặng nề bởi dịch Covid-19 trong năm 2020 v&agrave; 2021 nhưng doanh nghiệp của ch&agrave;ng trai trẻ kh&ocirc;ng bị ảnh hưởng qu&aacute; nhiều, thậm ch&iacute; bắt kịp với xu hướng kinh doanh mới trong bối cảnh dịch bệnh.</p>\r\n<p>Nhờ đ&oacute;, doanh thu vẫn nghi nhận tăng đều theo kế hoạch từng năm. Thậm ch&iacute;, c&ograve;n mở rộng được thị trường kinh doanh trong khi đội ngũ l&agrave;m việc kh&ocirc;ng bị giới hạn bởi kh&ocirc;ng gian v&agrave; thời gian.</p>\r\n<p align=\"center\"><img class=\"news-image loaded\" src=\"https://icdn.24h.com.vn/upload/1-2022/images/2022-01-19/Tay-trang-khoi-nghiep-chang-trai-1987-nguoi-TP-HCM-co-thu-nhap-ca-ty-dong-moi-nam-anh-2-1642600279-156-width740height493.jpg\" alt=\"Tay trắng khởi nghiệp, ch&agrave;ng trai sinh năm 1987 c&oacute; thu nhập cả tỷ đồng mỗi năm - 3\" data-original=\"https://icdn.24h.com.vn/upload/1-2022/images/2022-01-19/Tay-trang-khoi-nghiep-chang-trai-1987-nguoi-TP-HCM-co-thu-nhap-ca-ty-dong-moi-nam-anh-2-1642600279-156-width740height493.jpg\" data-was-processed=\"true\"></p>\r\n<p class=\"img_chu_thich_0407\" align=\"center\">Nguyễn Long Hải cho biết kh&ocirc;ng ngại chia sẻ những kinh nghiệm r&uacute;t ra sau qu&atilde;ng thời gian 10 năm khởi nghiệp của m&igrave;nh</p>\r\n<p>Ch&agrave;ng trai sinh năm 1987 cho biết th&ecirc;m năm 2021 vừa qua, ngo&agrave;i những th&agrave;nh tựu đạt được trong sự nghiệp, c&oacute; 2 điều m&agrave; anh cảm thấy tự h&agrave;o l&agrave; th&ocirc;ng qua việc ki&ecirc;n tr&igrave; với những lựa chọn&nbsp;của m&igrave;nh từ những năm 2012, năm vừa qua anh c&oacute; th&ecirc;m thời gian d&agrave;nh cho gia đ&igrave;nh&nbsp;th&ocirc;ng qua việc vận h&agrave;nh c&ocirc;ng việc tại nh&agrave;, cũng như x&acirc;y dựng được đội ngũ cộng sự cốt l&otilde;i để c&ugrave;ng chia sẻ tr&aacute;ch nhiệm. Hai l&agrave; anh cũng ho&agrave;n thiện những quy tr&igrave;nh tuyển chọn v&agrave; đ&agrave;o tạo b&agrave;i bản, kết hợp với những c&ocirc;ng cụ tự động h&oacute;a doanh nghiệp để gi&uacute;p anh em cộng sự đi sau c&oacute; thể l&agrave;m việc một c&aacute;ch dễ d&agrave;ng v&agrave; hiệu quả hơn.</p>\r\n<p>Sau hơn 10 năm khởi nghiệp, Nguyễn Long Hải cho biết đến nay bản th&acirc;n anh đ&atilde; giải quyết được vấn đề cơm &aacute;o gạo tiền để được l&agrave;m việc dựa tr&ecirc;n những g&igrave; m&igrave;nh đam m&ecirc; v&agrave; c&oacute; thế mạnh. Sau đ&oacute;, th&ocirc;ng qua việc mở rộng quy m&ocirc; doanh nghiệp, anh c&oacute; cơ hội được truyền tải lại những trải nghiệm, b&iacute; quyết kinh doanh của m&igrave;nh đến với những thế hệ sau. V&agrave; cuối c&ugrave;ng, anh tạo dựng được sự nghiệp bền vững&nbsp;để cho con c&aacute;i nối nghiệp, thậm ch&iacute; nếu thế hệ con ch&aacute;u kh&ocirc;ng muốn theo nghiệp kinh doanh, th&igrave; những quan điểm sống v&agrave; mối quan hệ m&agrave; anh để lại c&oacute; thể gi&uacute;p ch&uacute;ng gặt h&aacute;i được th&agrave;nh tựu ở bất kỳ lĩnh vực chuy&ecirc;n m&ocirc;n n&agrave;o.</p>', 218, 14, 0, 1, '2022-11-18 19:35:41', '2023-02-07 08:27:17'),
(100, 'Shark Tank Việt Nam mùa 5 - Tập 3: MC lên sóng nhà Đài VTV gọi vốn', 'shark-tank-viet-nam-mua-5---tap-3-mc-len-song-nha-dai-vtv-goi-von', 'Shark Tank Việt Nam mùa 5 - Tập 3: MC lên sóng nhà Đài VTV gọi vốn', '<p>Shark Tank Việt Nam m&ugrave;a 5 - Tập 3 c&oacute; sự xuất hiện của MC Jolie Đặng (Đặng Thị Ho&agrave;i Trinh) - Nh&agrave; s&aacute;ng l&acirc;p v&agrave; điều h&agrave;nh của anyLEARN.</p>\r\n<p>Ho&agrave;i Trinh từng l&agrave; MC truyền h&igrave;nh, c&oacute; hơn 15 năm kinh nghiệm trong lĩnh vực truyền th&ocirc;ng, 11 năm kinh nghiệm l&agrave;m mẹ v&agrave; 6 năm l&agrave;m việc trong lĩnh vực gi&aacute;o dục.</p>\r\n<div>\r\n<p class=\"image\"><img class=\"width100 initial loaded\" src=\"https://cdn.baogiaothong.vn/upload/images/2022-2/article_img/2022-06-19/img-bgt-2021-968a1352-1655655218-width1280height720.jpg\" alt=\"Shark Tank Việt Nam m&ugrave;a 5 - Tập 3: MC l&ecirc;n s&oacute;ng nh&agrave; Đ&agrave;i VTV gọi vốn 1\" data-original=\"https://cdn.baogiaothong.vn/upload/images/2022-2/article_img/2022-06-19/img-bgt-2021-968a1352-1655655218-width1280height720.jpg\" data-was-processed=\"true\"></p>\r\n<p><em>Ho&agrave;i Trinh từng l&agrave; MC truyền h&igrave;nh</em></p>\r\n</div>\r\n<p>Trong suốt h&agrave;nh tr&igrave;nh đ&oacute;, chị đ&atilde; tiếp x&uacute;c rất nhiều với cộng đồng phụ huynh v&agrave; chị nhận thấy rằng, một nỗi lo lớn nhất của c&aacute;c bậc cha mẹ l&agrave; l&agrave;m sao c&oacute; thể t&igrave;m được cho con m&igrave;nh một m&ocirc;i trường học tập an to&agrave;n về cơ sở vật chất, về t&acirc;m l&yacute; v&agrave; tinh thần, về chất lượng gi&aacute;o dục v&agrave; về sự đầu tư t&agrave;i ch&iacute;nh cho con ở h&agrave;nh tr&igrave;nh học tập d&agrave;i hạn.</p>\r\n<section class=\"bnrBt txtCent\"></section>\r\n<p>Theo lời giới thiệu của Ho&agrave;i Trinh, anyLEARN l&agrave; nền tảng kết nối gi&aacute;o dục h&agrave;ng đầu với slogan &ldquo;học kh&ocirc;ng giới hạn&rdquo;. Định vị l&agrave; nền tảng kết nối gi&aacute;o dục h&agrave;ng đầu, c&ocirc;ng ty đang l&agrave;m việc với những đối t&aacute;c l&agrave; những tập đo&agrave;n gi&aacute;o dục lớn.</p>\r\n<p>Chị chia sẻ, Việt Nam c&oacute; 23 triệu học sinh sinh vi&ecirc;n, tương đương mỗi năm phải chi ra khoảng 23 tỷ USD để đầu tư cho gi&aacute;o dục, chị c&ugrave;ng đội ngũ đ&atilde; quyết định ph&aacute;t triển dự &aacute;n nền tảng kết nối gi&aacute;o dục anyLEARN gi&uacute;p học sinh c&oacute; một lộ tr&igrave;nh ph&aacute;t triển trong sự vui vẻ, hạnh ph&uacute;c, học đ&uacute;ng được sở trường, đam m&ecirc;.</p>\r\n<p>Theo đ&oacute;, chị đến Shark Tank để gọi vốn 200.000 USD đổi lấy 10% cổ phần c&ocirc;ng ty v&agrave; cho biết nếu c&aacute;c Shark đầu tư cho anyLEARN, c&ocirc;ng ty sẽ ghi t&ecirc;n c&aacute;c Shark v&agrave;o danh s&aacute;ch cổ đ&ocirc;ng danh dự.</p>\r\n<p>&ldquo;C&oacute; 3 điều m&agrave; anyLEARN đang cam kết, thứ nhất l&agrave; đảm bảo chất lượng của đối t&aacute;c, bởi v&igrave; l&agrave; mỗi đối t&aacute;c của anyLEARN đều qua 4 bước chọn lọc, bao gồm truyền th&ocirc;ng, ph&aacute;p l&yacute;, t&agrave;i ch&iacute;nh v&agrave; thương hiệu; thứ 2 nữa l&agrave; c&aacute;c kh&oacute;a học cũng đều sẽ được đ&aacute;nh gi&aacute; chất lượng, khi c&aacute;c lớp học v&agrave; kh&oacute;a học hiển thị th&igrave; sẽ c&oacute; 4 bước đ&aacute;nh gi&aacute;, bước đầu ti&ecirc;n l&agrave; đội ngũ của anyLEARN, sau khi thẩm định xong th&igrave; họ sẽ c&oacute; một đ&aacute;nh gi&aacute; để lại, bước thứ 2 l&agrave; đội ngũ chuy&ecirc;n gia l&agrave; những hiệu trưởng, l&agrave; những giảng vi&ecirc;n, l&agrave; những chủ trường, họ đ&aacute;nh gi&aacute;, bước thứ 3 nữa l&agrave; chỉ c&oacute; những người đ&atilde; đăng k&yacute;, đ&atilde; booking v&agrave; học th&igrave; sau đ&oacute; mới để lại đ&aacute;nh gi&aacute;, bước cuối c&ugrave;ng l&agrave; ch&uacute;ng ta sẽ đo lường th&ocirc;ng qua độ y&ecirc;u th&iacute;ch, tương t&aacute;c của cộng đồng&rdquo; &ndash; Ho&agrave;i Trinh giải th&iacute;ch. Chị cũng cho biết anyLEARN thu tiền từ ng&acirc;n s&aacute;ch m&agrave; c&aacute;c trường đang chi ra cho việc truyền th&ocirc;ng v&agrave; tuyển sinh của họ.</p>\r\n<div>\r\n<p class=\"image\">&nbsp;</p>\r\n<div>\r\n<p class=\"image\"><img class=\"width100 loaded\" src=\"https://cdn.baogiaothong.vn/upload/images/2022-2/article_img/2022-06-19/img-bgt-2021-qt2-1059-1655655285-width1280height720.jpg\" alt=\"Shark Tank Việt Nam m&ugrave;a 5 - Tập 3: MC l&ecirc;n s&oacute;ng nh&agrave; Đ&agrave;i VTV gọi vốn 2\" data-original=\"https://cdn.baogiaothong.vn/upload/images/2022-2/article_img/2022-06-19/img-bgt-2021-qt2-1059-1655655285-width1280height720.jpg\" data-was-processed=\"true\"></p>\r\n<p><em>Ho&agrave;i Trinh đến Shark Tank để gọi vốn 200.000 USD đổi lấy 10% cổ phần c&ocirc;ng ty</em></p>\r\n</div>\r\n</div>\r\n<p>trong nền tảng n&agrave;y. Bởi c&oacute; thể c&aacute;c trường k&yacute; hợp t&aacute;c v&agrave; trả tiền sẽ được l&ecirc;n hạng cao hơn trong app (ứng dụng) so với c&aacute;c trường kh&ocirc;ng trả tiền.</p>\r\n<p>Ho&agrave;i Trinh cho biết, anyLEARN vẫn tư vấn cho phụ huynh c&aacute;c trường c&ocirc;ng lập v&agrave; sẽ tư vấn bổ trợ th&ecirc;m nhiều m&ocirc;n phụ kh&aacute;c ph&ugrave; hợp theo từng giai đoạn.</p>\r\n<p>Shark Hưng nhận x&eacute;t th&ecirc;m, m&ocirc; h&igrave;nh kinh doanh của startup đang c&oacute; Conflict of Interest (xung đột lợi &iacute;ch). &ldquo;Bạn sẽ kh&ocirc;ng giữ vị tr&iacute; trung lập khi tư vấn trường đ&oacute; cho phụ huynh học sinh m&agrave; n&oacute; bị xung đột lợi &iacute;ch bởi chuyện nh&agrave; trường đ&oacute; trả hoa hồng cho bạn. Bạn đang l&agrave; Sellers Agent - tức l&agrave; đại diện cho b&ecirc;n b&aacute;n chứ kh&ocirc;ng phải đại diện cho b&ecirc;n mua, bạn kh&ocirc;ng phải l&agrave; Buyers Agent bảo vệ v&agrave; mang lại quyền lợi cho người mua l&agrave; cha mẹ học sinh. V&igrave; vậy n&oacute; sẽ c&oacute; sự xung đột&rdquo; - Shark Hưng nhận x&eacute;t. Shark Hưng cũng khuy&ecirc;n startup n&ecirc;n tạo một c&aacute;i phễu đ&aacute;nh gi&aacute; trung lập v&agrave; kh&aacute;ch quan để khiến cho phụ huynh tin tưởng v&agrave; truy cập v&agrave;o, c&ograve;n &ocirc;ng quyết định kh&ocirc;ng đầu tư cho startup.</p>\r\n<p>Shark B&igrave;nh nhận x&eacute;t, bản chất của startup l&agrave; đi m&ocirc;i giới cho c&aacute;c cơ sở gi&aacute;o dục, n&ecirc;n &ldquo;long mạch&rdquo; l&agrave; đặt ở b&aacute;n h&agrave;ng nhưng Ho&agrave;i Trinh vẫn chưa n&oacute;i được m&igrave;nh giỏi b&aacute;n h&agrave;ng ở chỗ n&agrave;o v&agrave; đội ngũ sales (b&aacute;n h&agrave;ng) ra sao.</p>\r\n<p>Ho&agrave;i Trinh chia sẻ, anyLEARN kh&ocirc;ng tuyển đội ngũ sales về để b&aacute;n h&agrave;ng m&agrave; sẽ c&oacute; c&aacute;c chuy&ecirc;n gia tư vấn v&agrave; định hướng gi&aacute;o dục. Sau khi chuy&ecirc;n gia đ&atilde; vẽ ra một sơ đồ l&agrave; học sinh n&ecirc;n học g&igrave;, như thế n&agrave;o th&igrave; c&aacute;c admin sẽ hướng dẫn kh&aacute;ch đăng k&yacute;. B&ecirc;n cạnh đ&oacute;, chị cũng marketing offline bằng c&aacute;ch tiếp cận c&aacute;c cộng đồng doanh nh&acirc;n &ndash; những kh&aacute;ch h&agrave;ng tiềm năng của anyLEARN v&agrave; th&ecirc;m một c&aacute;ch nữa l&agrave; chia sẻ tệp kh&aacute;ch h&agrave;ng với c&aacute;c trường.</p>\r\n<p>Shark B&igrave;nh nhận x&eacute;t, startup đ&atilde; l&ecirc;n Shark Tank qu&aacute; sớm. Shark cũng bất ngờ khi nh&igrave;n thấy kế hoạch tăng trưởng m&agrave; startup đ&atilde; đặt ra l&agrave; năm 2021 l&agrave; 3 tỷ doanh thu, 2022 l&ecirc;n đến 180 tỷ, 2023 l&agrave; 1.800 tỷ, 2024 l&agrave; 7.200 tỷ, 2025 l&agrave; 21.600 tỷ doanh thu. Cuối c&ugrave;ng Shark B&igrave;nh cũng như nhiều Shark kh&aacute;c từ chối đầu tư.</p>', 218, 1, 0, 1, '2022-11-18 19:35:41', '2022-11-22 08:33:13'),
(101, 'Hiểu đúng về gương bát quái trong phong thủy', 'hieu-dung-ve-guong-bat-quai-trong-phong-thuy', 'Lấy lý do \"theo phong thủy\", không ít người đưa ra những quan điểm mê tín dị đoan trong việc treo gương bát quái.', '<div id=\"divfirst\">\r\n<p class=\"Normal\">Họ cho rằng gương b&aacute;t qu&aacute;i l&agrave; một ph&aacute;p kh&iacute; phong thủy hội tụ năm nguy&ecirc;n tố phong thủy, sơ đồ Lạc Thư, sơ đồ b&aacute;t qu&aacute;i n&ecirc;n được sử dụng để khắc phục, bảo vệ hoặc h&oacute;a giải xung s&aacute;t cho những ng&ocirc;i nh&agrave; hướng xấu, như nh&agrave; ở gần nghĩa địa, đường xe lửa, cầu vượt, ng&atilde; tư hoặc nơi c&oacute; c&acirc;y lớn (g&acirc;y ảnh hưởng về sức khỏe, tiền t&agrave;i, sự nghiệp).</p>\r\n<p class=\"Normal\">Thực tế, gương b&aacute;t qu&aacute;i cũng như c&aacute;c loại tượng con gi&aacute;p, tỳ hưu, tiền ngũ đế, hồ l&ocirc; đồng, thủy tinh cầu, thất tinh trận, ngọc như &yacute;, th&aacute;p văn xương, phong thủy lu&acirc;n, ngh&ecirc; đồng, c&oacute;c thiềm thừ, muối phong thủy, bột trừ t&agrave;, bột tẩy uế kh&ocirc;ng phải l&agrave; ph&aacute;p kh&iacute; phong thủy.</p>\r\n<p class=\"Normal\">Kh&ocirc;ng c&oacute; một ghi ch&eacute;p ch&iacute;nh thống n&agrave;o về nguồn gốc của gương b&aacute;t qu&aacute;i. D&acirc;n gian cho rằng nhiều đạo sĩ thời cổ đại thường sử dụng một miếng gỗ th&acirc;n c&acirc;y đ&agrave;o c&oacute; t&aacute;m cạnh, b&agrave;o nhẵn hai mặt, tr&ecirc;n đ&oacute; khắc 8 quẻ dịch đại diện cho 8 hướng của vũ trụ (hoặc d&ugrave;ng kiếm bằng gỗ c&acirc;y đ&agrave;o) trong khi tu luyện.</p>\r\n<p class=\"Normal\">Đạo sĩ đắc đạo thường d&ugrave;ng hai loại \"vũ kh&iacute;\" n&agrave;y hoặc c&acirc;y phất trần để h&agrave;ng phục y&ecirc;u ma, cầu đảo. Miếng gỗ th&acirc;n c&acirc;y đ&agrave;o nhờ \"thần lực\" của đạo sĩ m&agrave; trở n&ecirc;n nổi tiếng. Gọi l&agrave; b&aacute;t qu&aacute;i do tr&ecirc;n mặt miếng gỗ c&oacute; khắc h&igrave;nh t&aacute;m quẻ dịch (gọi l&agrave; gương th&aacute;i cực khi tr&ecirc;n mặt khắc h&igrave;nh nhật nguyệt). Sức mạnh của gương b&aacute;t qu&aacute;i thực chất l&agrave; ph&aacute;p lực do đạo sĩ tu h&agrave;nh m&agrave; c&oacute;.</p>\r\n</div>\r\n<div id=\"admbackground\" data-set=\"dfp\"></div>\r\n<div id=\"divend\">\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-giadinh.vnecdn.net/2022/04/13/71snso6e79l-ac-sl1000-16498368-4711-6256-1649836921.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=0rdl65Rh1grAeYbe47sdcw 1x, https://i1-giadinh.vnecdn.net/2022/04/13/71snso6e79l-ac-sl1000-16498368-4711-6256-1649836921.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=j7Rzgz_q8RlOPI8XNpAeWw 1.5x, https://i1-giadinh.vnecdn.net/2022/04/13/71snso6e79l-ac-sl1000-16498368-4711-6256-1649836921.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=XpgS63bRnh8q9AiPKQHJ5Q 2x\" data-srcset=\"https://i1-giadinh.vnecdn.net/2022/04/13/71snso6e79l-ac-sl1000-16498368-4711-6256-1649836921.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=0rdl65Rh1grAeYbe47sdcw 1x, https://i1-giadinh.vnecdn.net/2022/04/13/71snso6e79l-ac-sl1000-16498368-4711-6256-1649836921.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=j7Rzgz_q8RlOPI8XNpAeWw 1.5x, https://i1-giadinh.vnecdn.net/2022/04/13/71snso6e79l-ac-sl1000-16498368-4711-6256-1649836921.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=XpgS63bRnh8q9AiPKQHJ5Q 2x\"><img class=\"lazy lazied\" src=\"https://i1-giadinh.vnecdn.net/2022/04/13/71snso6e79l-ac-sl1000-16498368-4711-6256-1649836921.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=0rdl65Rh1grAeYbe47sdcw\" alt=\"Gương b&aacute;t qu&aacute;i kh&ocirc;ng phải ph&aacute;p kh&iacute; phong thủy. Ảnh: Amazon\" loading=\"lazy\" data-src=\"https://i1-giadinh.vnecdn.net/2022/04/13/71snso6e79l-ac-sl1000-16498368-4711-6256-1649836921.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=0rdl65Rh1grAeYbe47sdcw\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Gương b&aacute;t qu&aacute;i kh&ocirc;ng phải ph&aacute;p kh&iacute; phong thủy. Ảnh:&nbsp;<em>Amazon</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Ở thời hiện đại, gương b&aacute;t qu&aacute;i được l&agrave;m bằng thủy tinh hoặc kim loại, trang tr&iacute; theo một số quan niệm d&acirc;n gian, kiểu d&aacute;ng cũng kh&aacute; phong ph&uacute; đa dạng, c&oacute; gi&aacute; trị nghệ thuật - trang tr&iacute; cao.</p>\r\n<p class=\"Normal\">Gương b&aacute;t qu&aacute;i ng&agrave;y nay chỉ l&agrave; một loại đồ vật c&oacute; khả năng phản quang hoặc tụ quang (nếu được sản xuất như một thấu k&iacute;nh hội tụ), v&igrave; vậy cần sử dụng như gương (k&iacute;nh) trong trang tr&iacute; nội thất, ngoại thất.</p>\r\n<p class=\"Normal\">Gương b&aacute;t qu&aacute;i kh&ocirc;ng c&oacute; khả năng h&oacute;a s&aacute;t, trừ t&agrave;, trấn trạch, sinh vượng; cũng kh&ocirc;ng c&oacute; khả năng g&acirc;y tổn hại cho h&agrave;ng x&oacute;m. Trường hợp cần giải quyết yếu tố t&acirc;m l&yacute; cũng c&oacute; thể vận dụng. Chẳng hạn, đối với những dương trạch phạm xuy&ecirc;n t&acirc;m s&aacute;t, trực xung s&aacute;t, thi&ecirc;n nhận s&aacute;t, thi&ecirc;n trảm s&aacute;t, quang s&aacute;t, xạ tiễn s&aacute;t... sau khi đ&atilde; xử l&yacute; h&oacute;a s&aacute;t theo nguy&ecirc;n l&yacute; phong thủy, c&oacute; thể treo một gương b&aacute;t qu&aacute;i để \"l&agrave;m ph&eacute;p\".</p>\r\n<p class=\"Normal\">Khi mua v&agrave; treo cần lưu &yacute; rằng gương b&aacute;t qu&aacute;i \"chuẩn\" bao giờ cũng khắc quẻ C&agrave;n ở ph&iacute;a tr&ecirc;n, tức l&agrave; ở đầu của con c&aacute; trắng trong h&igrave;nh th&aacute;i cực; quẻ Kh&ocirc;n khắc ở ph&iacute;a dưới, tức l&agrave; ở đầu con c&aacute; đen. Nh&agrave;, cửa ở bất kỳ hướng n&agrave;o cũng phải treo gương đ&uacute;ng trời tr&ecirc;n đất dưới, tức l&agrave; quẻ C&agrave;n (ở vị tr&iacute; mắt con c&aacute; trắng) v&agrave; quẻ Kh&ocirc;n (ở vị tr&iacute; mắt con c&aacute; đen) c&ugrave;ng nằm tr&ecirc;n một đường thẳng. Mọi chế t&aacute;c mới về gương b&aacute;t qu&aacute;i, c&aacute;c quẻ v&agrave; h&igrave;nh vẽ kh&ocirc;ng đ&uacute;ng vị tr&iacute; đều kh&ocirc;ng n&ecirc;n d&ugrave;ng.</p>\r\n</div>', 218, 12, 0, 1, '2022-11-18 19:35:41', '2023-02-07 08:36:46');
INSERT INTO `posts` (`id`, `title`, `slug`, `excerpt`, `body`, `user_id`, `category_id`, `views`, `approved`, `created_at`, `updated_at`) VALUES
(105, 'Làm không hết việc, 8x Thái Nguyên thu 20 triệu mỗi ngày dịp cận tết', 'lam-khong-het-viec-8x-thai-nguyen-thu-20-trieu-moi-ngay-dip-can-tet', 'Trước nhu cầu mua cây và hoa trang trí nhà cửa của người dân những ngày cận Tết nguyên đán Nhâm Dần 2022, chàng trai sinh năm 1989 tại Thái Nguyên cho biết doanh thu bán hàng của anh cũng tăng vọt.', '<p>Anh Nguyễn Văn Hậu sinh năm 1989 tại Th&aacute;i Nguy&ecirc;n cho biết những ng&agrave;y cận&nbsp;<a class=\"TextlinkBaiviet\" title=\"Tết Nh&acirc;m Dần\" href=\"https://www.24h.com.vn/sac-tet-nham-dan-c46e6691.html\">Tết Nh&acirc;m Dần</a>&nbsp;2022, c&ocirc;ng việc tại vườn hoa v&agrave; cửa h&agrave;ng hoa của anh&nbsp;lu&ocirc;n&nbsp;tất bật từ s&aacute;ng sớm đến tối mịt.</p>\r\n<p align=\"center\">&nbsp;</p>\r\n<div id=\"container-24h-banner-in-image\">\r\n<div>\r\n<div><img class=\"news-image initial loaded\" src=\"https://icdn.24h.com.vn/upload/1-2022/images/2022-01-17/Lam-khong-het-viec-chang-trai-31-tuoi-nguoi-Thai-Nguyen-thu-20-trieu-moi-ngay-dip-can-tet-anh-1-1642402469-967-width740height552.jpg\" alt=\"L&agrave;m kh&ocirc;ng hết việc, 8x Th&aacute;i Nguy&ecirc;n thu 20 triệu mỗi ng&agrave;y dịp cận tết - 1\" data-was-processed=\"true\"></div>\r\n<div id=\"24h-banner-in-image\">\r\n<div id=\"hqejBmTLHP_139_15s\" class=\"txtCent\">\r\n<div class=\"text_adver_right\">&nbsp;</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n<p>&nbsp;</p>\r\n<p class=\"img_chu_thich_0407\" align=\"center\">Ch&agrave;ng trai sinh năm 1989 tại Th&aacute;i Nguy&ecirc;n sở hữu vườn hồng h&agrave;ng ngh&igrave;n gốc</p>\r\n<p>Ch&agrave;ng trai sinh năm 1989&nbsp;bắt đầu kinh doanh c&aacute;c giống hồng từ năm 2017. Đến nay, anh đang sở hữu vườn hồng hơn 2.000 m&eacute;t vu&ocirc;ng với h&agrave;ng ngh&igrave;n gốc hồng c&aacute;c loại. Anh chia sẻ trong những năm qua đ&atilde; sưu tầm&nbsp;được gần như đầy đủ những giống hoa hồng cổ bản địa của Việt Nam.</p>\r\n<p>C&ugrave;ng với đ&oacute;, trong vườn cũng c&oacute; gần trăm giống hoa hồng nhập ngoại ti&ecirc;u chuẩn, được những người y&ecirc;u hoa y&ecirc;u th&iacute;ch, cũng như hợp kh&iacute; hậu Việt Nam. Theo chủ vườn n&agrave;y, gốc hồng nhiều tiền nhất trong vườn được anh mua với gi&aacute; 20 triệu đồng. Những c&acirc;y tầm trung cũng c&oacute; gi&aacute; từ 5 đến 7 triệu đồng,... Anh Hậu chia sẻ đến đầu năm 2021, số vốn đầu tư v&agrave;o vườn c&acirc;y của anh đ&atilde; l&ecirc;n tới 1 tỷ đồng.</p>\r\n<p>Theo ch&agrave;ng trai trẻ, những th&aacute;ng b&igrave;nh thường doanh thu từ b&aacute;n giống hoa v&agrave; c&aacute;c loại c&acirc;y cảnh của vườn dao động từ 20 &ndash; 25 triệu đồng/th&aacute;ng. Số tiền n&agrave;y sau khi trừ đi một phần chi ph&iacute; sinh hoạt, tiếp tục được anh quay v&ograve;ng v&agrave;o đầu tư sản xuất, từng bước ho&agrave;n thiện khu vườn theo &yacute; tưởng của m&igrave;nh.</p>\r\n<p>Ch&agrave;ng trai sinh năm 1989 cũng chia sẻ doanh thu v&agrave; lợi nhuận với những chủ vườn c&acirc;y hoa giống như anh chủ yếu đến v&agrave;o những dịp cuối năm khi nhu cầu mua c&acirc;y, hoa cảnh của người d&acirc;n để trang tr&iacute; nh&agrave; cửa đ&oacute;n Tết tăng mạnh.</p>\r\n<p align=\"center\"><img class=\"news-image loaded\" src=\"https://icdn.24h.com.vn/upload/1-2022/images/2022-01-17/Lam-khong-het-viec-chang-trai-31-tuoi-nguoi-Thai-Nguyen-thu-20-trieu-moi-ngay-dip-can-tet-anh-3-1642402469-779-width740height659.jpg\" alt=\"L&agrave;m kh&ocirc;ng hết việc, 8x Th&aacute;i Nguy&ecirc;n thu 20 triệu mỗi ng&agrave;y dịp cận tết - 3\" data-original=\"https://icdn.24h.com.vn/upload/1-2022/images/2022-01-17/Lam-khong-het-viec-chang-trai-31-tuoi-nguoi-Thai-Nguyen-thu-20-trieu-moi-ngay-dip-can-tet-anh-3-1642402469-779-width740height659.jpg\" data-was-processed=\"true\"></p>\r\n<p class=\"img_chu_thich_0407\" align=\"center\">Anh Hậu chia sẻ nhu cầu mua c&acirc;y, hoa trang tr&iacute; nh&agrave; cửa của người d&acirc;n tăng mạnh những ng&agrave;y cận Tết</p>\r\n<p>Để chuẩn bị cho đợt b&aacute;n h&agrave;ng Tết Nh&acirc;m Dần 2022, ngay từ th&aacute;ng 9, th&aacute;ng 10 &acirc;m lịch anh đ&atilde; chuẩn bị th&ecirc;m số tiền mặt đến 300 &ndash; 400 triệu đồng để nhập th&ecirc;m c&aacute;c loại c&acirc;y hoa cảnh.</p>\r\n<p>B&ecirc;n cạnh đ&oacute;, anh cũng mở rộng cửa h&agrave;ng l&ecirc;n cả ngh&igrave;n m&eacute;t vu&ocirc;ng nhằm đ&aacute;p ứng nhu cầu lựa chọn đa dạng về c&acirc;y v&agrave; hoa trang tr&iacute; nh&agrave; cửa của người d&acirc;n trong v&ugrave;ng.</p>\r\n<p>Ch&agrave;ng trai 31 tuổi cho biết trong đợt cao điểm b&aacute;n hoa, c&acirc;y cảnh Tết &acirc;m lịch năm 2022 hiện nay, b&ecirc;n cạnh hoa hồng l&agrave; giống c&acirc;y chủ lực, vườn c&acirc;y v&agrave; cửa h&agrave;ng hoa của anh c&ograve;n b&aacute;n th&ecirc;m c&aacute;c loại c&acirc;y hoa cảnh kh&aacute;c như quất bonsai, quất thế, lan, đ&agrave;o, mẫu đơn, c&uacute;c, hải đường, đỗ quy&ecirc;n, hoa giấy, hoa tr&agrave;,&hellip; B&ecirc;n cạnh đ&oacute;, anh cũng phục vụ b&aacute;n th&ecirc;m c&aacute;c loại chậu trồng c&acirc;y cảnh để đ&aacute;p ứng nhu cầu đa dạng của người d&acirc;n tr&ecirc;n địa b&agrave;n.</p>\r\n<p>Để đ&aacute;p ứng nhu cầu mua c&acirc;y v&agrave; hoa trang tr&iacute; của người d&acirc;n những ng&agrave;y qua, anh Hậu&nbsp;đ&atilde; huy động th&ecirc;m 4 người trong gia đ&igrave;nh phụ gi&uacute;p vừa b&aacute;n, vừa chăm s&oacute;c c&acirc;y, giao h&agrave;ng cho kh&aacute;ch.</p>\r\n<p align=\"center\"><img class=\"news-image loaded\" src=\"https://icdn.24h.com.vn/upload/1-2022/images/2022-01-17/Lam-khong-het-viec-chang-trai-31-tuoi-nguoi-Thai-Nguyen-thu-20-trieu-moi-ngay-dip-can-tet-anh-2-1642402469-948-width740height582.jpg\" alt=\"L&agrave;m kh&ocirc;ng hết việc, 8x Th&aacute;i Nguy&ecirc;n thu 20 triệu mỗi ng&agrave;y dịp cận tết - 4\" data-original=\"https://icdn.24h.com.vn/upload/1-2022/images/2022-01-17/Lam-khong-het-viec-chang-trai-31-tuoi-nguoi-Thai-Nguyen-thu-20-trieu-moi-ngay-dip-can-tet-anh-2-1642402469-948-width740height582.jpg\" data-was-processed=\"true\"></p>\r\n<p class=\"img_chu_thich_0407\" align=\"center\">Ch&agrave;ng trai 31 tuổi thừa nhận những ng&agrave;y cận Tết c&ocirc;ng việc rất bận rộn</p>\r\n<p>&ldquo;L&agrave;m nghề b&aacute;n c&acirc;y hoa cảnh dịp tết bận như con mọn, l&agrave;m kh&ocirc;ng hết việc, ngay cả việc ăn uống của những người l&agrave;m tại vườn v&agrave; cửa h&agrave;ng cũng phải tranh thủ&rdquo;, ch&agrave;ng trai sinh năm 1989 chia sẻ.</p>\r\n<p>Theo anh Hậu hiện doanh thu mỗi ng&agrave;y từ cửa h&agrave;ng hoa v&agrave; c&acirc;y cảnh của anh dao động trong khoảng 20 triệu đồng, trong đ&oacute; những giỏ lan c&oacute; gi&aacute; từ v&agrave;i trăm ngh&igrave;n đến v&agrave;i triệu đồng được nhiều người lựa chọn để trang tr&iacute; nh&agrave; hoặc qu&agrave; tặng tết &acirc;m lịch cho người th&acirc;n, bạn b&egrave;.</p>\r\n<p>Nhờ đ&oacute;, sau khi trừ đi c&aacute;c khoản chi ph&iacute; v&agrave; giống,&nbsp;anh cũng thu được một khoản l&atilde;i đ&aacute;ng kể để c&oacute; thể tiếp tục t&aacute;i đầu tư v&agrave; mở rộng vườn c&acirc;y, hoa cảnh của m&igrave;nh trong năm mới.</p>\r\n<p>Ch&agrave;ng trai sinh năm 1989 dự kiến trong năm 2022 n&agrave;y sẽ mở rộng vườn hồng&nbsp;l&ecirc;n khoảng 1ha. Do đ&oacute;, ngo&agrave;i ho&agrave;n thiện khu vườn trở th&agrave;nh điểm tham quan với du kh&aacute;ch, anh c&ograve;n c&oacute; kế hoạch mở th&ecirc;m khu ăn uống, sản xuất th&ecirc;m những sản phẩm từ hoa hồng như tinh dầu, tr&agrave;,... để đ&aacute;p ứng nhu cầu ng&agrave;y c&agrave;ng lớn của người d&acirc;n v&agrave; du kh&aacute;ch thập phương đến với vườn.</p>', 218, 14, 0, 1, '2022-11-18 19:35:41', '2023-02-07 08:28:25'),
(106, 'Khoảnh khắc ấm áp của nữ sinh bên cha trong ngày chụp ảnh kỷ yếu', 'khoanh-khac-am-ap-cua-nu-sinh-ben-cha-trong-ngay-chup-anh-ky-yeu', 'Hình ảnh nữ sinh hồn nhiên được bố chỉnh mũ trong ngày chụp kỷ yếu tốt nghiệp bất ngờ gây sốt mạng bởi khoảnh khắc ý nghĩa nhân Ngày của cha 19/6.', '<p>Chụp ảnh kỷ yếu vốn l&agrave; một hoạt động quen thuộc của học sinh, sinh vi&ecirc;n cuối cấp để ghi lại những khoảnh khắc ấm &aacute;p b&ecirc;n bạn b&egrave;, thầy c&ocirc;, gia đ&igrave;nh. Một trong những h&igrave;nh ảnh ấn tượng nhất m&ugrave;a kỷ yếu năm nay đ&oacute; ch&iacute;nh l&agrave; khoảnh khắc một nữ sinh cười tươi, hồn nhi&ecirc;n khi được bố đội mũ cử nh&acirc;n tại trường THPT Phạm Văn Đồng, Quảng Ng&atilde;i.</p>\r\n<p><img class=\"medium-zoom-image\" src=\"https://ttol.vietnamnetjsc.vn/images/2022/06/20/16/12/a3f4b7095c4bb515ec5a.jpg\" alt=\"Khoảnh khắc ấm &aacute;p của nữ sinh b&ecirc;n cha trong ng&agrave;y chụp ảnh kỷ yếu-1\"></p>\r\n<p><img class=\"medium-zoom-image\" src=\"https://ttol.vietnamnetjsc.vn/images/2022/06/20/16/12/f9e9f6141d56f408ad47.jpg\" alt=\"Khoảnh khắc ấm &aacute;p của nữ sinh b&ecirc;n cha trong ng&agrave;y chụp ảnh kỷ yếu-2\"></p>\r\n<p><img class=\"medium-zoom-image\" src=\"https://ttol.vietnamnetjsc.vn/images/2022/06/20/16/12/61346dc9868b6fd5369a.jpg\" alt=\"Khoảnh khắc ấm &aacute;p của nữ sinh b&ecirc;n cha trong ng&agrave;y chụp ảnh kỷ yếu-3\"></p>\r\n<p>Sau khi xem được bức ảnh 2 cha con đang lan truyền rộng r&atilde;i tr&ecirc;n mạng x&atilde; hội, cư d&acirc;n mạng b&agrave;y tỏ:</p>\r\n<p>Nickname Lương Ngọc Th&ocirc;ng viết: \"Lễ tốt nghiệp m&agrave; được chụp h&igrave;nh với cha, mẹ th&igrave; chắc chắn sẽ l&agrave; tấm h&igrave;nh kỷ yếu đ&aacute;ng nhớ nhất. Nh&acirc;n ng&agrave;y của cha, xin ch&uacute;c những người cha lu&ocirc;n lu&ocirc;n vui vẻ, khỏe mạnh v&agrave; hạnh ph&uacute;c!\".</p>\r\n<div id=\"vtzone63\" class=\"vmcadszone\">\r\n<div id=\"yomedia-passback-c9f647d5c88e46c39004b88badfad27c-1655733883303\"></div>\r\n</div>\r\n<p>Bạn Ngọc Nữ b&agrave;y tỏ: \"Ch&uacute;c mừng em v&agrave; ch&uacute;c b&aacute;c thật nhiều sức khỏe. Hy vọng, em g&aacute;i sẽ lu&ocirc;n nở nụ cười tr&ecirc;n m&ocirc;i, học tập thật tốt để xứng đ&aacute;ng với c&ocirc;ng lao bố mẹ nu&ocirc;i nấng. Mong em lu&ocirc;n th&agrave;nh c&ocirc;ng v&agrave; đừng bao giờ gục ng&atilde; em nh&eacute;!\"</p>\r\n<p>\"H&atilde;y lu&ocirc;n l&agrave; c&ocirc; g&aacute;i b&eacute; nhỏ v&agrave; đ&aacute;ng y&ecirc;u trong mắt của ba, ch&uacute;c em c&oacute; một m&ugrave;a thi thật &yacute; nghĩa v&agrave; đạt được kết quả cao\", bạn Thu Phương gửi lời ch&uacute;c.</p>\r\n<p>T&agrave;i khoản t&ecirc;n Lương Sỹ Quyết Thắng bộc bạch: \"Được sinh ra đời l&agrave; một điều hạnh ph&uacute;c, được l&agrave;m con của ba mẹ ch&iacute;nh l&agrave; một ph&eacute;p m&agrave;u. Ng&ocirc;i nh&agrave; nhỏ của ch&uacute;ng ta l&agrave; một t&ograve;a l&acirc;u đ&agrave;i tuy kh&ocirc;ng nguy nga tr&aacute;ng lệ m&agrave; v&ocirc; v&agrave;n t&igrave;nh y&ecirc;u v&agrave; hạnh ph&uacute;c\".</p>\r\n<p>\"Xem những bức ảnh n&agrave;y m&igrave;nh chỉ muốn kh&oacute;c th&ocirc;i. Con xin tr&acirc;n trọng cảm ơn những người l&agrave;m cha, l&agrave;m mẹ, cảm ơn đ&atilde; cho c&aacute;c con cuộc đời n&agrave;y...\", Trần Phương Hậu viết d&ograve;ng trạng th&aacute;i đầy cảm x&uacute;c.</p>', 218, 6, 3, 1, '2022-11-18 19:35:41', '2023-02-05 19:51:46'),
(107, 'Vải thiều, thanh long chen chân vào thị trường 2,6 tỉ USD của Thái Lan', 'vai-thieu-thanh-long-chen-chan-vao-thi-truong-26-ti-usd-cua-thai-lan', 'Để khai thác được tiềm năng thị trường Thái Lan, doanh nghiệp Việt quyết tâm đưa vải thiều, thanh long loại 1 để chinh phục', '<p>Ng&agrave;y 19-6, Đại diện Hiệp hội Rau quả Việt Nam cho biết một doanh nghiệp hội vi&ecirc;n đang x&uacute;c tiến đưa vải thiều loại 1 sang Th&aacute;i Lan để chuẩn bị cho tuần lễ \"Vải thiều Việt Nam\" tại hệ thống b&aacute;n lẻ Central Group của Th&aacute;i Lan, bắt đầu từ ng&agrave;y 23-6 tới.</p>\r\n<p>\"Vừa qua, một số doanh nghiệp xuất khẩu tr&aacute;i c&acirc;y đ&atilde; khảo s&aacute;t thị trường Th&aacute;i Lan v&agrave; nhận thấy cơ hội thị trường rất lớn. D&ugrave; tr&aacute;i c&acirc;y Việt đ&atilde; được b&aacute;n tại Th&aacute;i Lan nhưng đa số đi ph&acirc;n kh&uacute;c thị trường gi&aacute; rẻ n&ecirc;n chất lượng thấp, c&ocirc;ng nghệ bảo quản kh&ocirc;ng tốt n&ecirc;n h&agrave;ng b&agrave;y b&aacute;n k&eacute;m tươi ngon. Mặc d&ugrave; Th&aacute;i Lan cũng c&oacute; quả vải nhưng hiện kh&ocirc;ng phải thời điểm thu hoạch n&ecirc;n vải thiều Việt Nam c&oacute; nhiều cơ hội\" - đại diện Hiệp hội Rau quả Việt Nam nhận x&eacute;t.</p>\r\n<p align=\"center\">&nbsp;</p>\r\n<div id=\"container-24h-banner-in-image\">\r\n<div>\r\n<div><img class=\"news-image initial loading\" src=\"https://icdn.24h.com.vn/upload/2-2022/images/2022-06-19//1655628812-248eed7725f6851ab30fb6c46ff75334-width1280height1116.jpg\" alt=\"Vải thiều, thanh long chen ch&acirc;n v&agrave;o thị trường 2,6 tỉ USD của Th&aacute;i Lan - 1\" width=\"660\" data-was-processed=\"true\"></div>\r\n<div id=\"24h-banner-in-image\">\r\n<div id=\"povYNPAuBX_139_15s\" class=\"txtCent\">\r\n<div class=\"text_adver_right\">&nbsp;</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n<p>&nbsp;</p>\r\n<p align=\"center\"><img class=\"news-image loaded\" src=\"https://icdn.24h.com.vn/upload/2-2022/images/2022-06-19//1655628812-0f809e79ce2c0083fe29c59cbb130d6d-width684height596.jpg\" alt=\"Vải thiều, thanh long chen ch&acirc;n v&agrave;o thị trường 2,6 tỉ USD của Th&aacute;i Lan - 3\" width=\"660\" data-original=\"https://icdn.24h.com.vn/upload/2-2022/images/2022-06-19//1655628812-0f809e79ce2c0083fe29c59cbb130d6d-width684height596.jpg\" data-was-processed=\"true\"></p>\r\n<p class=\"img_chu_thich_0407\">Vải thiều vận chuyển bằng đường h&agrave;ng kh&ocirc;ng b&aacute;n tại một cửa h&agrave;ng TP HCM</p>\r\n<p>Theo Cục X&uacute;c tiến thương mại (Bộ C&ocirc;ng Thương), nhu cầu nhập khẩu c&aacute;c loại tr&aacute;i c&acirc;y, rau củ tươi của Th&aacute;i Lan l&agrave; rất lớn với kim ngạch nhập khẩu đạt 2,6 tỉ&nbsp;<a class=\"TextlinkBaiviet\" title=\"USD\" href=\"https://www.24h.com.vn/ty-gia-ngoai-te-ttcb-c426.html\">USD</a>&nbsp;v&agrave;o năm 2020. Trong khi đ&oacute;, Việt Nam c&oacute; nhiều sản phẩm thế mạnh m&agrave; Th&aacute;i Lan kh&ocirc;ng cạnh tranh được, v&iacute; dụ như: vải v&agrave; thanh long.</p>\r\n<p>Năm 2020, Việt Nam đ&atilde; xuất khẩu 157,1 triệu USD rau quả sang Th&aacute;i Lan nhưng&nbsp; năm 2021 gi&aacute; trị giảm c&ograve;n 147,3 triệu USD do ảnh hưởng của Covid-19.</p>\r\n<p>Dịch Covid-19 đang được kiểm so&aacute;t l&agrave; cơ hội để Việt Nam đẩy mạnh xuất khẩu tr&aacute;i c&acirc;y n&oacute;i ri&ecirc;ng v&agrave; n&ocirc;ng sản sang thị trường tiềm năng n&agrave;y.</p>', 218, 14, 0, 1, '2022-11-18 19:35:41', '2023-02-07 08:28:54'),
(108, 'Bộ ảnh kỷ yếu \"hiếm có khó đạo nhái\" của học sinh Nghệ An', 'bo-anh-ky-yeu-hiem-co-kho-dao-nhai-cua-hoc-sinh-nghe-an', 'Loạt ảnh kỷ yếu giá trị như thế này không phải lớp học nào cũng có cơ hội thực hiện!', '<p>Cứ đến độ th&aacute;ng 5 h&agrave;ng năm, c&aacute;c bạn học sinh cuối cấp lại rục rịch chuẩn bị chụp ảnh kỷ yếu, đ&aacute;nh dấu h&agrave;nh tr&igrave;nh trưởng th&agrave;nh của m&igrave;nh. Từ &yacute; tưởng chụp như thế n&agrave;o, chuẩn bị những g&igrave;, thu&ecirc; trang phục ra sao&hellip; đều được c&aacute;c th&agrave;nh vi&ecirc;n trong lớp nghi&ecirc;n cứu v&agrave; đưa ra phương &aacute;n tốt nhất. V&agrave; đương nhi&ecirc;n, lớp học n&agrave;o cũng mong muốn bộ ảnh kỷ yếu của m&igrave;nh l&agrave; độc đ&aacute;o nhất, ấn tượng nhất. Thế nhưng, trong l&uacute;c thực hiện sẽ c&oacute; những t&igrave;nh huống xảy đến bất ngờ như học sinh lớp 12D, trường THPT Đ&ocirc; Lương 4, Nghệ An.</p>\r\n<p>Mới đ&acirc;y, tr&ecirc;n mạng x&atilde; hội c&oacute; chia sẻ loạt ảnh kỷ yếu của&nbsp;lớp 12D&nbsp;khiến cộng đồng mạng v&ocirc; c&ugrave;ng th&iacute;ch th&uacute;. Trong ảnh, c&aacute;c bạn học sinh mặc trang phục &aacute;o cử nh&acirc;n đang vội v&agrave;ng c&agrave;o th&oacute;c được phơi dưới l&ograve;ng đường gi&uacute;p người d&acirc;n.</p>\r\n<p><img id=\"img_456294108046569472\" class=\"medium-zoom-image\" title=\"Bộ ảnh kỷ yếu hiếm c&oacute; kh&oacute; đạo nh&aacute;i của học sinh Nghệ An - Ảnh 1.\" src=\"https://ttol.vietnamnetjsc.vn/images/2022/06/01/16/23/photo-1-16540529883751624878288.jpg\" alt=\"Bộ ảnh kỷ yếu hiếm c&oacute; kh&oacute; đạo nh&aacute;i của học sinh Nghệ An-1\"></p>\r\n<table>\r\n<tbody>\r\n<tr>\r\n<td>\r\n<p><img id=\"img_456294144628105216\" class=\"medium-zoom-image\" title=\"Bộ ảnh kỷ yếu hiếm c&oacute; kh&oacute; đạo nh&aacute;i của học sinh Nghệ An - Ảnh 2.\" src=\"https://ttol.vietnamnetjsc.vn/images/2022/06/01/16/23/photo-1-16540529940561356572536.jpg\" alt=\"Bộ ảnh kỷ yếu hiếm c&oacute; kh&oacute; đạo nh&aacute;i của học sinh Nghệ An-2\"></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>Anh&nbsp;Thế Trường, người chụp bộ ảnh n&agrave;y cho biết, ban đầu lớp chụp bộ ảnh kh&aacute;c nhưng l&uacute;c n&agrave;y bầu trời bắt đầu xuất hiện cơn mưa n&ecirc;n tất cả đ&atilde; c&ugrave;ng nhau thu gom th&oacute;c hộ b&agrave; con quanh đ&oacute;. Ai cũng hối hả, người x&uacute;c, người c&agrave;o&hellip; d&ugrave; mệt nhưng c&aacute;c bạn đều v&ocirc; c&ugrave;ng vui vẻ v&agrave; cố gắng hết sức.</p>\r\n<p>\"L&uacute;c đ&oacute;, m&igrave;nh đang chụp ảnh th&igrave; m&acirc;y đen k&eacute;o đến, trời lắc rắc đ&ocirc;i hạt mưa, bỗng một nh&oacute;m nam sinh chạy &ugrave;a ra, d&ugrave;ng vật dụng sẵn c&oacute; ở đ&oacute; vun l&uacute;a lại. Phần lớn c&aacute;c bạn l&agrave; con nh&agrave; n&ocirc;ng, t&ocirc;i nghĩ t&igrave;nh huống ngo&agrave;i kịch bản n&agrave;y cũng kh&aacute; hay, n&oacute; nhắc c&aacute;c em biết qu&yacute; trọng th&agrave;nh quả lao động của bố mẹ n&ecirc;n bấm v&agrave;i kiểu ghi lại khoảnh khắc ấy\", anh Trường cho hay.</p>\r\n<p>&nbsp;</p>\r\n<table>\r\n<tbody>\r\n<tr>\r\n<td>\r\n<p><img id=\"img_456294177249132544\" class=\"medium-zoom-image\" title=\"Bộ ảnh kỷ yếu hiếm c&oacute; kh&oacute; đạo nh&aacute;i của học sinh Nghệ An - Ảnh 4.\" src=\"https://ttol.vietnamnetjsc.vn/images/2022/06/01/16/23/photo-3-1654052995091106674763.jpg\" alt=\"Bộ ảnh kỷ yếu hiếm c&oacute; kh&oacute; đạo nh&aacute;i của học sinh Nghệ An-3\"></p>\r\n<div id=\"vtzone63\" class=\"vmcadszone\"></div>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table>\r\n<tbody>\r\n<tr>\r\n<td>\r\n<p><img id=\"img_456294168207818752\" class=\"medium-zoom-image\" title=\"Bộ ảnh kỷ yếu hiếm c&oacute; kh&oacute; đạo nh&aacute;i của học sinh Nghệ An - Ảnh 5.\" src=\"https://ttol.vietnamnetjsc.vn/images/2022/06/01/16/23/photo-4-1654052994661597084268.jpg\" alt=\"Bộ ảnh kỷ yếu hiếm c&oacute; kh&oacute; đạo nh&aacute;i của học sinh Nghệ An-4\"></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table>\r\n<tbody>\r\n<tr>\r\n<td>\r\n<p><img id=\"img_456294185840140288\" class=\"medium-zoom-image\" title=\"Bộ ảnh kỷ yếu hiếm c&oacute; kh&oacute; đạo nh&aacute;i của học sinh Nghệ An - Ảnh 6.\" src=\"https://ttol.vietnamnetjsc.vn/images/2022/06/01/16/23/photo-5-16540529955841521522354.jpg\" alt=\"Bộ ảnh kỷ yếu hiếm c&oacute; kh&oacute; đạo nh&aacute;i của học sinh Nghệ An-5\"></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>Kh&ocirc;ng qu&aacute; cầu kỳ nhưng bộ ảnh kỷ yếu c&agrave;o th&oacute;c &ldquo;chạy mưa&rdquo; gi&uacute;p d&acirc;n n&agrave;y đ&atilde; nhận về&nbsp;rất nhiều&nbsp;lời khen của cộng đồng mạng bởi sự độc đ&aacute;o, ấn tượng v&agrave; v&ocirc; c&ugrave;ng đặc biệt. Từ một khoảnh khắc rất thật với h&agrave;nh động &yacute; nghĩa, c&aacute;c bạn học sinh đ&atilde; c&oacute; những bức ảnh v&ocirc; c&ugrave;ng gi&aacute; trị m&agrave; kh&ocirc;ng phải lớp học n&agrave;o cũng c&oacute; cơ hội thực hiện.</p>\r\n<p>Sau khi đăng tải, loạt ảnh n&agrave;y đ&atilde; thu h&uacute;t h&agrave;ng ngh&igrave;n lượt tương t&aacute;c của cộng đồng mạng.</p>\r\n<p><em>&ldquo;Bức ảnh gi&aacute; trị qu&aacute;&rdquo;, &ldquo;C&oacute; lẽ những khoảnh khắc như thế n&agrave;y rất kh&oacute; để qu&ecirc;n, tuổi học tr&ograve; tr&ocirc;i qua nhanh lắm c&aacute;c em ạ. H&atilde;y cứ tận hưởng khi c&ograve;n c&oacute; thể&rdquo;, &ldquo;H&agrave;nh động &yacute; nghĩa qu&aacute;, cảm ơn c&aacute;c em&rdquo;, &ldquo;Bức ảnh đẹp đ&acirc;u cần qu&aacute; cầu kỳ nhỉ?&rdquo;</em>... l&agrave; một số b&igrave;nh luận của d&acirc;n mạng.</p>', 218, 6, 0, 1, '2022-11-18 19:35:41', '2022-11-22 05:46:41'),
(113, 'Người phụ nữ đi hết 195 quốc gia', 'nguoi-phu-nu-di-het-195-quoc-gia', 'Khi hạ cánh xuống Seychelles ở Đông Phi ngày 6/10/2019, Jessica Nabongo trở thành người phụ nữ da màu đầu tiên đi hết 195 quốc gia được Liên Hợp Quốc công nhận.', '<p class=\"description\">Khi hạ c&aacute;nh xuống Seychelles ở Đ&ocirc;ng Phi ng&agrave;y 6/10/2019, Jessica Nabongo trở th&agrave;nh người phụ nữ da m&agrave;u đầu ti&ecirc;n đi hết 195 quốc gia được Li&ecirc;n Hợp Quốc c&ocirc;ng nhận.</p>\r\n<article class=\"fck_detail \">\r\n<p class=\"Normal\">V&agrave;o thời điểm đ&oacute;, Nabongo, người Mỹ gốc Uganda sinh ra ở Detroit, đ&atilde; bay hơn 450 chuyến, thực hiện hơn một triệu dặm bay. C&ocirc; bay hơn 170 chuyến mỗi năm, su&yacute;t bỏ việc v&agrave;i lần.</p>\r\n<div>\r\n<figure class=\"item_slide_show clearfix\">\r\n<div id=\"video_parent_357178\" class=\"box_embed_video_parent embed_video_new\" data-vcate=\"1003834\" data-vscate=\"1003005\" data-vscaten=\"Thế giới\" data-vid=\"357178\" data-ratio=\"1\" data-articleoriginal=\"4477818\" data-ads=\"0\" data-license=\"0\" data-duration=\"4\" data-brandsafe=\"0\" data-type=\"0\" data-play=\"1\" data-frame=\"\" data-aot=\"\" data-videoclassify=\"None\" data-initdom=\"1\" data-view=\"inview\" data-auto=\"1\" data-status=\"play\">\r\n<div id=\"embed_video_357178\" class=\"box_embed_video\">\r\n<div class=\"bg_overlay_small_unpin\">&nbsp;</div>\r\n</div>\r\n</div>\r\n</figure>\r\n</div>\r\n<p class=\"Normal\">Trong cuốn s&aacute;ch&nbsp;<em>The Catch Me If You Can (H&atilde;y đuổi kịp t&ocirc;i nếu c&oacute; thể)</em>&nbsp;xuất bản h&ocirc;m 14/6 đặt t&ecirc;n theo blog nổi tiếng của m&igrave;nh, Nabongo, 38 tuổi, kể lại h&agrave;nh tr&igrave;nh ph&aacute; kỷ lục, tập trung v&agrave;o 100 trong số 195 nước m&agrave; c&ocirc; đ&atilde; đi qua.</p>\r\n<p class=\"Normal\">\"T&ocirc;i đam m&ecirc; địa l&yacute;\", c&ocirc; n&oacute;i về quyết định chấp nhận thử th&aacute;ch, đồng thời giải th&iacute;ch đ&acirc;y l&agrave; điều c&ocirc; đ&atilde; ấp ủ hơn 10 năm trước khi thực sự bắt đầu. \"Năm 2017, t&ocirc;i quyết định sẽ đi khắp thế giới trước sinh nhật 35 tuổi\".</p>\r\n<p class=\"Normal\">Cuối c&ugrave;ng, Nabongo kh&ocirc;ng thực hiện được mục ti&ecirc;u tr&ecirc;n. \"T&ocirc;i ho&agrave;n th&agrave;nh thử th&aacute;ch sau sinh nhật 5 th&aacute;ng, nhưng n&oacute; rơi đ&uacute;ng v&agrave;o ng&agrave;y sinh nhật bố, người đ&atilde; qua đời hai ng&agrave;y sau sinh nhật 19 tuổi của t&ocirc;i. V&igrave; vậy, cũng tuyệt khi t&ocirc;i c&oacute; thể tưởng nhớ đến bố m&igrave;nh qua thử th&aacute;ch n&agrave;y\".</p>\r\n<p class=\"Normal\">Khoảng 400 người được cho l&agrave; đ&atilde; đến thăm to&agrave;n bộ 195 quốc gia được Li&ecirc;n Hợp Quốc c&ocirc;ng nhận, nhưng trong số đ&oacute; c&oacute; rất &iacute;t người da m&agrave;u. Nabongo cho hay trở th&agrave;nh người phụ nữ da m&agrave;u đầu ti&ecirc;n đi khắp thế giới l&agrave; l&yacute; do th&ocirc;i th&uacute;c c&ocirc; viết s&aacute;ch.</p>\r\n<p class=\"Normal\">\"Ch&uacute;ng ta đ&atilde; qu&aacute; quen nh&igrave;n thế giới qua lăng k&iacute;nh của người da trắng. Trải nghiệm của t&ocirc;i rất kh&aacute;c biệt\", Nabongo n&oacute;i. \"R&otilde; r&agrave;ng mỗi người ch&uacute;ng ta đều c&oacute; những trải nghiệm độc đ&aacute;o\"</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture data-inimage=\"done\"><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/19/httpcdn-cnn-comcnnnextdamasset-6402-5603-1655610960.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=hpdIJqaykCI9hhaaIhoX4Q 1x, https://i1-vnexpress.vnecdn.net/2022/06/19/httpcdn-cnn-comcnnnextdamasset-6402-5603-1655610960.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=pFwYqJHD-yaFcEAoleL8Xg 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/19/httpcdn-cnn-comcnnnextdamasset-6402-5603-1655610960.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=gLxNwvsVG7F_c914bAF25Q 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/19/httpcdn-cnn-comcnnnextdamasset-6402-5603-1655610960.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=hpdIJqaykCI9hhaaIhoX4Q 1x, https://i1-vnexpress.vnecdn.net/2022/06/19/httpcdn-cnn-comcnnnextdamasset-6402-5603-1655610960.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=pFwYqJHD-yaFcEAoleL8Xg 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/19/httpcdn-cnn-comcnnnextdamasset-6402-5603-1655610960.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=gLxNwvsVG7F_c914bAF25Q 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/06/19/httpcdn-cnn-comcnnnextdamasset-6402-5603-1655610960.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=hpdIJqaykCI9hhaaIhoX4Q\" alt=\"Nabongo trong chuyến đi Bali, Indonesia, năm 2017. Ảnh: Instagram/Jessicanabongo\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/06/19/httpcdn-cnn-comcnnnextdamasset-6402-5603-1655610960.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=hpdIJqaykCI9hhaaIhoX4Q\" data-ll-status=\"loaded\" data-adbro-processed=\"true\"></picture>\r\n<div class=\"embed-container-ads\">\r\n<div id=\"sis_inimage\" data-google-query-id=\"CJ2jlL3zuPgCFQxelgodb-IBvQ\">\r\n<div id=\"google_ads_iframe_/27973503/Vnexpress/Desktop/Inimage/Thegioi/Thegioi.cuocsongdoday.detail_0__container__\"><iframe id=\"google_ads_iframe_/27973503/Vnexpress/Desktop/Inimage/Thegioi/Thegioi.cuocsongdoday.detail_0\" tabindex=\"0\" title=\"3rd party ad content\" role=\"region\" name=\"google_ads_iframe_/27973503/Vnexpress/Desktop/Inimage/Thegioi/Thegioi.cuocsongdoday.detail_0\" width=\"1\" height=\"1\" frameborder=\"0\" marginwidth=\"0\" marginheight=\"0\" scrolling=\"no\" aria-label=\"Advertisement\" data-google-container-id=\"4\" data-load-complete=\"true\"></iframe></div>\r\n</div>\r\n</div>\r\n</div>\r\n<figcaption>\r\n<p class=\"Image\">Nabongo trong chuyến đi Bali, Indonesia, năm 2017. Ảnh:&nbsp;<em>Instagram/Jessicanabongo</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Nabongo kể về Afghanistan, nơi c&ocirc; đ&atilde; đến đền Hazart Ali (Nh&agrave; thời Hồi gi&aacute;o Xanh) ở th&agrave;nh phố Mazar-i-Sharif, kể về Pakistan, nơi c&ocirc; m&ecirc; mệt c&aacute;c m&oacute;n ăn đường phố, v&agrave; Iran, nơi c&ocirc; tới thăm th&agrave;nh cổ Yazd.</p>\r\n<p class=\"Normal\">C&ocirc; cảm thấy kh&ocirc;ng th&uacute; vị khi ai cũng tới những nơi nổi tiếng như Bali hay Morocco, bởi \"mọi người đều tới những nơi giống nhau, l&agrave;m những điều giống nhau\".</p>\r\n<p class=\"Normal\">\"Nhưng h&atilde;y nghĩ đến Yemen, Afghanistan v&agrave; Nam Sudan. C&oacute; rất nhiều nơi m&agrave; kh&ocirc;ng ai nghĩ rằng n&ecirc;n đi du lịch th&igrave; t&ocirc;i lại trải qua qu&atilde;ng thời gian cực kỳ tuyệt vời. T&ocirc;i hy vọng th&ocirc;ng qua cuốn s&aacute;ch, người ta sẽ giảm bớt th&agrave;nh kiến về những quốc gia da đen v&agrave; da v&agrave;ng\", Nanbongo b&agrave;y tỏ.</p>\r\n<p class=\"Normal\">Sở hữu hộ chiếu Mỹ mang lại cho c&ocirc; nhiều đặc quyền hơn những du kh&aacute;ch mang quốc tịch kh&aacute;c, trong khi hộ chiếu Uganda lại gi&uacute;p c&ocirc; đi tới những quốc gia như Iran hay Triều Ti&ecirc;n.</p>\r\n<p class=\"Normal\">\"Đ&acirc;y l&agrave; vũ kh&iacute; b&iacute; mật của t&ocirc;i. Nếu chỉ c&oacute; hộ chiếu Mỹ, c&oacute; lẽ t&ocirc;i sẽ kh&ocirc;ng thể ho&agrave;n th&agrave;nh mục ti&ecirc;u\", c&ocirc; n&oacute;i.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/19/header-1655619814-2876-1655619866.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=YOCrRL9-hZAC8qUfQUNf4w 1x, https://i1-vnexpress.vnecdn.net/2022/06/19/header-1655619814-2876-1655619866.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=xsVBbISEiOZ7Kc-CWS_3lA 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/19/header-1655619814-2876-1655619866.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=XxPhexrwMdj5-sV6f9nQzA 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/19/header-1655619814-2876-1655619866.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=YOCrRL9-hZAC8qUfQUNf4w 1x, https://i1-vnexpress.vnecdn.net/2022/06/19/header-1655619814-2876-1655619866.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=xsVBbISEiOZ7Kc-CWS_3lA 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/19/header-1655619814-2876-1655619866.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=XxPhexrwMdj5-sV6f9nQzA 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/06/19/header-1655619814-2876-1655619866.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=YOCrRL9-hZAC8qUfQUNf4w\" alt=\"Nabongo tại sa mạc Wadi Rum, Jordan, th&aacute;ng 9/2018. Ảnh: Instagram/Jessicanabongo\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/06/19/header-1655619814-2876-1655619866.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=YOCrRL9-hZAC8qUfQUNf4w\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Nabongo tại sa mạc Wadi Rum, Jordan, th&aacute;ng 9/2018. Ảnh:&nbsp;<em>Instagram/Jessicanabongo</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Trước khi bắt đầu mỗi thử th&aacute;ch, Nabongo nhấn mạnh ch&uacute;ng ta n&ecirc;n đặt c&acirc;u hỏi tại sao muốn bắt tay l&agrave;m n&oacute; bởi \"đ&oacute; l&agrave; động lực đưa ta đến đ&iacute;ch\". C&ocirc; hy vọng c&acirc;u chuyện của m&igrave;nh sẽ khuyến kh&iacute;ch mọi người theo đuổi ước mơ, bất kể đ&oacute; l&agrave; ước mơ g&igrave;.</p>\r\n<p class=\"Normal\">\"T&ocirc;i cho rằng kh&ocirc;ng phải ai cũng muốn đặt ch&acirc;n l&ecirc;n mọi quốc gia tr&ecirc;n thế giới\", c&ocirc; n&oacute;i. \"Nhưng c&aacute;i m&agrave; t&ocirc;i muốn mọi người biết l&agrave; b&ecirc;n trong họ sở hữu đủ năng lực để l&agrave;m bất kỳ điều g&igrave; họ muốn trong đời\".</p>\r\n<p class=\"Normal\">Nabongo đang chuẩn bị tới quốc gia T&acirc;y Phi Senegal, nơi c&ocirc; m&ocirc; tả l&agrave; \"địa điểm khiến t&ocirc;i cực kỳ vui vẻ\". C&ocirc; cũng l&ecirc;n một kế hoạch kh&aacute;c l&agrave; đi thăm mọi bang của Mỹ.</p>\r\n<p class=\"Normal\">\"T&ocirc;i c&ograve;n 6 bang nữa chưa đi\", c&ocirc; giải th&iacute;ch, nhấn mạnh kh&ocirc;ng hề vội v&agrave;ng v&agrave; sẽ ho&agrave;n th&agrave;nh nhiệm vụ n&agrave;y \"khi sẵn s&agrave;ng\".</p>\r\n</article>', 218, 1, 1, 1, '2022-11-18 19:35:42', '2022-11-21 23:36:52'),
(114, 'Mưa lũ càn quét miền nam Trung Quốc', 'mua-lu-can-quet-mien-nam-Trung-Quoc', 'Nhiều vùng ở miền nam Trung Quốc hứng chịu bão lớn, gây ngập lụt nghiêm trọng ở nhiều thành phố và lở đất ở vùng nông thôn', '<p class=\"description\">Nhi&ecirc;̀u vùng ở mi&ecirc;̀n nam Trung Qu&ocirc;́c hứng chịu bão lớn, g&acirc;y ng&acirc;̣p lụt nghi&ecirc;m trọng ở nhi&ecirc;̀u thành ph&ocirc;́ và lở đ&acirc;́t ở vùng n&ocirc;ng th&ocirc;n.</p>\r\n<article class=\"fck_detail \">\r\n<div id=\"divfirst\">\r\n<p class=\"Normal\">Đường phố biến th&agrave;nh s&ocirc;ng v&agrave; một căn nh&agrave; một tầng bị cuốn tr&ocirc;i trong d&ograve;ng nước lũ ở tỉnh Qu&yacute; Ch&acirc;u, miền nam&nbsp;<a href=\"https://vnexpress.net/chu-de/trung-quoc-451\" rel=\"dofollow\">Trung Quốc</a>&nbsp;h&ocirc;m 18/6. Tại khu trự trị Quảng T&acirc;y gần đ&oacute;, 5 người d&acirc;n thiệt mạng khi một căn nh&agrave; bằng gỗ bị ph&aacute; hủy v&igrave; mưa lớn h&ocirc;m qua. C&aacute;c vụ lở đất v&agrave; sập đường cũng được b&aacute;o c&aacute;o.</p>\r\n<p class=\"Normal\">C&aacute;c tỉnh Qu&yacute; Ch&acirc;u, Giang t&acirc;y, An Huy, Chiết Giang v&agrave; Quảng T&acirc;y dự kiến tiếp tục hứng chịu lượng mưa lớn k&eacute;o d&agrave;i tới đầu tuần tới.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/19/mau-trung-quoc-xinhua-jpg-1655-3375-2349-1655615246.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=XH-x2Ns1y93MUGxuB5menQ 1x, https://i1-vnexpress.vnecdn.net/2022/06/19/mau-trung-quoc-xinhua-jpg-1655-3375-2349-1655615246.png?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=6XPwstlE9EbMtdsfEhpPGw 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/19/mau-trung-quoc-xinhua-jpg-1655-3375-2349-1655615246.png?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=edcLWQDpsB9TWQWtqRwz0A 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/19/mau-trung-quoc-xinhua-jpg-1655-3375-2349-1655615246.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=XH-x2Ns1y93MUGxuB5menQ 1x, https://i1-vnexpress.vnecdn.net/2022/06/19/mau-trung-quoc-xinhua-jpg-1655-3375-2349-1655615246.png?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=6XPwstlE9EbMtdsfEhpPGw 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/19/mau-trung-quoc-xinhua-jpg-1655-3375-2349-1655615246.png?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=edcLWQDpsB9TWQWtqRwz0A 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/06/19/mau-trung-quoc-xinhua-jpg-1655-3375-2349-1655615246.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=XH-x2Ns1y93MUGxuB5menQ\" alt=\"Nh&acirc;n vi&ecirc;n cứu hộ giải cứu người bị mắc kẹt trong nước lũ Nam B&igrave;nh, tỉnh Ph&uacute;c Kiến ng&agrave;y 18/6. Ảnh: Xinhua.\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/06/19/mau-trung-quoc-xinhua-jpg-1655-3375-2349-1655615246.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=XH-x2Ns1y93MUGxuB5menQ\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Nh&acirc;n vi&ecirc;n cứu hộ giải cứu người bị mắc kẹt trong nước lũ ở Nam B&igrave;nh, tỉnh Ph&uacute;c Kiến ng&agrave;y 18/6. Ảnh:&nbsp;<em>Xinhua</em>.</p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Ở miền nam Trung Quốc, những tuần trước v&agrave; sau lễ hội thuyền rồng v&agrave;o th&aacute;ng 6 h&agrave;ng năm thường xuất hiện mưa v&agrave; thời tiết bất ổn, do kh&ocirc;ng kh&iacute; n&oacute;ng ẩm ở ph&iacute;a nam va chạm với khối kh&ocirc;ng kh&iacute; lạnh từ ph&iacute;a bắc.</p>\r\n</div>\r\n<div id=\"admbackground\" data-set=\"dfp\"></div>\r\n<div id=\"divend\">\r\n<p class=\"Normal\">C&aacute;c cơn b&atilde;o m&ugrave;a h&egrave; c&oacute; cường độ lớn v&agrave; k&eacute;o d&agrave;i hơn b&igrave;nh thường trong năm nay, với lượng mưa ở Quảng T&acirc;y, Quảng Đ&ocirc;ng v&agrave; Ph&uacute;c Kiến ở mức cao nhất kể từ năm 1961.</p>\r\n<p class=\"Normal\">Trung Quốc thường dễ xảy ra lũ lụt v&agrave;o m&ugrave;a h&egrave;, nhưng những năm gần đ&acirc;y, nguy cơ ng&agrave;y c&agrave;ng lớn do nạn ph&aacute; rừng, cải tạo đất ngập nước v&agrave; t&iacute;ch trữ nước cho sản xuất điện, tưới ti&ecirc;u. Biến đổi kh&iacute; hậu cũng được xem l&agrave; một trong những nguy&ecirc;n nh&acirc;n g&acirc;y ra t&igrave;nh trạng n&agrave;y.</p>\r\n<p class=\"Normal\">Hồi th&aacute;ng 7/2021, h&agrave;ng trăm người đ&atilde; thiệt mạng v&igrave; lũ lụt nghi&ecirc;m trọng ở Trịnh Ch&acirc;u, th&agrave;nh phố 12 triệu d&acirc;n ở miền trung Trung Quốc.</p>\r\n</div>\r\n</article>', 218, 2, 5, 1, '2022-11-18 19:35:42', '2023-02-08 01:12:12'),
(115, '\'Bè bong bóng\' có thể giúp Trái Đất hạ nhiệt', 'be-bong-bong-co-the-giup-trai-dat-ha-nhiet', 'Cụm \"bong bóng\" trôi nổi phía trên Trái Đất để phản chiếu ánh sáng Mặt Trời có thể giúp đảo ngược hiện tượng ấm lên toàn cầu.', '<p class=\"description\">Cụm \"bong b&oacute;ng\" tr&ocirc;i nổi ph&iacute;a tr&ecirc;n Tr&aacute;i Đất để phản chiếu &aacute;nh s&aacute;ng Mặt Trời c&oacute; thể gi&uacute;p đảo ngược hiện tượng ấm l&ecirc;n to&agrave;n cầu.</p>\r\n<article class=\"fck_detail \">\r\n<div id=\"divfirst\">\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/19/VNE-Bubble-1650-1655615480.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=t-gASg272Dgb6ADRtX-ioQ 1x, https://i1-vnexpress.vnecdn.net/2022/06/19/VNE-Bubble-1650-1655615480.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=cUNs8NnTa8VjD_grpmOzdA 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/19/VNE-Bubble-1650-1655615480.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=zDZcmv0ikOL7gh7kHQBSYQ 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/19/VNE-Bubble-1650-1655615480.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=t-gASg272Dgb6ADRtX-ioQ 1x, https://i1-vnexpress.vnecdn.net/2022/06/19/VNE-Bubble-1650-1655615480.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=cUNs8NnTa8VjD_grpmOzdA 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/19/VNE-Bubble-1650-1655615480.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=zDZcmv0ikOL7gh7kHQBSYQ 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/06/19/VNE-Bubble-1650-1655615480.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=t-gASg272Dgb6ADRtX-ioQ\" alt=\"Khi kết hợp với nhau b&egrave; bong b&oacute;ng sẽ rộng bằng nước Brazil. Ảnh: MIT\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/06/19/VNE-Bubble-1650-1655615480.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=t-gASg272Dgb6ADRtX-ioQ\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Khi kết hợp với nhau b&egrave; bong b&oacute;ng sẽ rộng bằng nước Brazil. Ảnh:&nbsp;<em>MIT</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Dự &aacute;n nghi&ecirc;n cứu Space Bubbles của kiến tr&uacute;c sư Carlo Ratti c&ugrave;ng với c&aacute;c nh&agrave; nghi&ecirc;n cứu kh&aacute;c ở Viện c&ocirc;ng nghệ Massachusetts (MIT) đề xuất để một \"tấm b&egrave;\" kết từ nhiều bong b&oacute;ng đ&ocirc;ng lạnh ở điểm Lagrange L1 nằm giữa Tr&aacute;i Đất v&agrave; Mặt Trời. C&aacute;c bong b&oacute;ng được l&agrave;m từ vật liệu dạng m&agrave;ng mỏng v&agrave; sản xuất trong kh&ocirc;ng gian. Khi nối liền với nhau, ch&uacute;ng sẽ bao phủ diện t&iacute;ch bằng nước Brazil (8,5 triệu km2).</p>\r\n<p class=\"Normal\">Space Bubbles l&agrave; một biến thể của &yacute; tưởng l&agrave;m m&aacute;t Tr&aacute;i Đất bằng tấm chắn Mặt Trời, hoạt động th&ocirc;ng qua ngăn chặn một phần bức xạ từ ng&ocirc;i sao, qua đ&oacute; giảm bớt t&aacute;c động của hiện tượng ấm l&ecirc;n to&agrave;n cầu. Tuy nhi&ecirc;n, điểm kh&aacute;c biệt ch&iacute;nh l&agrave; tấm chắn Mặt Trời trong kh&ocirc;ng gian kh&ocirc;ng c&oacute; nguy cơ ảnh hưởng đến sinh quyển Tr&aacute;i Đất. Những dự &aacute;n triển khai ở tầng b&igrave;nh lưu của Tr&aacute;i Đất đều đi k&egrave;m nguy cơ n&agrave;y.</p>\r\n<p class=\"Normal\">Nh&oacute;m nghi&ecirc;n cứu MIT nhấn mạnh Space Bubbles được thiết kế để hỗ trợ nỗ lực giảm thiểu xu hướng biến đổi kh&iacute; hậu hiện nay. \"Địa kỹ thuật l&agrave; phương &aacute;n duy nhất của ch&uacute;ng ta\", Ratti, người đứng đầu ph&ograve;ng th&iacute; nghiệm Senseable City của MIT, cho biết. \"Tuy nhi&ecirc;n, c&aacute;c đề xuất địa kỹ thuật đều nằm tr&ecirc;n Tr&aacute;i Đất, đặt ra nguy cơ khổng lồ đối với hệ sinh th&aacute;i. Giải ph&aacute;p trong kh&ocirc;ng gian sẽ an to&agrave;n hơn. V&iacute; dụ, nếu l&agrave;m chệch hướng 1,8% bức xạ mặt trời trước khi tới Tr&aacute;i Đất, ch&uacute;ng ta c&oacute; thể đảo ngược ho&agrave;n to&agrave;n hiện tượng ấm l&ecirc;n to&agrave;n cầu\".</p>\r\n</div>\r\n<div id=\"admbackground\" data-set=\"dfp\"></div>\r\n<div id=\"divend\">\r\n<p class=\"Normal\">Một lợi thế kh&aacute;c của tấm chắn Mặt Trời kiểu n&agrave;y l&agrave; c&aacute;c bong b&oacute;ng c&oacute; thể xẹp đi v&agrave; thu hồi từ vị tr&iacute;. Khối cầu được l&agrave;m từ vật liệu như silicon, vận chuyển v&agrave;o kh&ocirc;ng gian ở dạng nung chảy hoặc chất lỏng ion gia cố bằng graphene. Nh&oacute;m nghi&ecirc;n cứu MIT đ&atilde; tiến h&agrave;nh th&agrave;nh c&ocirc;ng một th&iacute; nghiệm sơ bộ bằng c&aacute;ch bơm phồng lớp vỏ khối cầu trong điều kiện ngo&agrave;i kh&ocirc;ng gian. Họ cho rằng đ&acirc;y c&oacute; thể l&agrave; một trong những cấu tr&uacute;c m&agrave;ng mỏng hiệu quả nhất để l&agrave;m chệch hướng bức xạ Mặt Trời.</p>\r\n<p class=\"Normal\">Dự &aacute;n nghi&ecirc;n cứu Space Bubbles được ph&aacute;t triển từ &yacute; tưởng của nh&agrave; khoa học James Early, người đầu ti&ecirc;n đề xuất triển khai vật thể l&agrave;m chệch hướng bức xạ ở điểm Lagrange v&agrave; nh&agrave; thi&ecirc;n văn học Roger Angel, người n&ecirc;u ra thiết kế b&egrave; bong b&oacute;ng. Nh&oacute;m nghi&ecirc;n cứu li&ecirc;n ng&agrave;nh hy vọng c&oacute; thể chứng minh t&iacute;nh khả thi của dự &aacute;n th&ocirc;ng qua những th&iacute; nghiệm v&agrave; ph&acirc;n t&iacute;ch s&acirc;u hơn. Ngo&agrave;i vật liệu, nghi&ecirc;n cứu sẽ t&igrave;m hiểu c&aacute;c phương &aacute;n vận chuyển vật liệu từ Tr&aacute;i Đất, vị tr&iacute; v&agrave; t&iacute;nh ổn định của b&egrave; bong b&oacute;ng, t&iacute;nh hiệu quả về mặt chi ph&iacute;, c&aacute;ch bảo dưỡng, t&aacute;c động tới kh&iacute; hậu v&agrave; hệ sinh th&aacute;i.</p>\r\n</div>\r\n</article>', 218, 11, 0, 1, '2022-11-18 19:35:42', '2022-12-08 09:18:58'),
(118, 'Triều Tiên cử đội y tế tới tỉnh bùng dịch đường ruột', 'trieu-tien-cu-doi-y-te-toi-tinh-bung-dich-duong-ruot', 'Triều Tiên cử nhóm chuyên gia y tế và điều tra dịch tễ học tới tỉnh Nam Hwanghae để đối phó với dịch bệnh đường ruột..', '<p class=\"description\">Tri&ecirc;̀u Ti&ecirc;n cử nhóm chuy&ecirc;n gia y t&ecirc;́ và điều tra dịch t&ecirc;̃ học tới tỉnh Nam Hwanghae đ&ecirc;̉ đ&ocirc;́i phó với dịch bệnh đường ru&ocirc;̣t.</p>\r\n<article class=\"fck_detail \">\r\n<p class=\"Normal\">H&atilde;ng th&ocirc;ng tấn Triều Ti&ecirc;n&nbsp;<em>KCNA&nbsp;</em>cho biết một Đội Chẩn đo&aacute;n v&agrave; Điều trị nhanh đang l&agrave;m việc c&ugrave;ng giới chức y tế tỉnh Nam Hwanghae, đồng thời nhiều biện ph&aacute;p kh&aacute;c được thực hiện để đảm bảo ngăn chặn dịch v&agrave; gi&uacute;p khu vực n&ocirc;ng nghiệp trọng điểm kh&ocirc;ng bị gi&aacute;n đoạn canh t&aacute;c.</p>\r\n<p class=\"Normal\">C&aacute;c biện ph&aacute;p nhằm nỗ lực kiểm so&aacute;t dịch gồm c&aacute;ch ly, s&agrave;ng lọc chuy&ecirc;n s&acirc;u với tất cả người d&acirc;n, điều trị v&agrave; gi&aacute;m s&aacute;t đặc biệt những người dễ bị tổn thương như người gi&agrave; v&agrave; trẻ em.</p>\r\n<p class=\"Normal\">C&ocirc;ng t&aacute;c khử tr&ugrave;ng cũng đang được tiến h&agrave;nh, gồm cả nước thải v&agrave; c&aacute;c chất thải kh&aacute;c, để đảm bảo an to&agrave;n cho nguồn nước uống v&agrave; nước sinh hoạt.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture data-inimage=\"done\"><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/19/afp-trieu-tien-13-6-8813-1655610268.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=3Xek66zj6FH4U_gmArgCLg 1x, https://i1-vnexpress.vnecdn.net/2022/06/19/afp-trieu-tien-13-6-8813-1655610268.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=dUBE5-UYYgss92x0UDLlEQ 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/19/afp-trieu-tien-13-6-8813-1655610268.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=k5xhw0UYINUtfvmy929d5Q 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/19/afp-trieu-tien-13-6-8813-1655610268.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=3Xek66zj6FH4U_gmArgCLg 1x, https://i1-vnexpress.vnecdn.net/2022/06/19/afp-trieu-tien-13-6-8813-1655610268.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=dUBE5-UYYgss92x0UDLlEQ 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/19/afp-trieu-tien-13-6-8813-1655610268.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=k5xhw0UYINUtfvmy929d5Q 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/06/19/afp-trieu-tien-13-6-8813-1655610268.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=3Xek66zj6FH4U_gmArgCLg\" alt=\"Nh&acirc;n vi&ecirc;n phun khử tr&ugrave;ng một nh&agrave; m&aacute;y ở B&igrave;nh Nhưỡng h&ocirc;m 13/6. Ảnh: AFP.\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/06/19/afp-trieu-tien-13-6-8813-1655610268.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=3Xek66zj6FH4U_gmArgCLg\" data-ll-status=\"loaded\" data-adbro-processed=\"true\"></picture>\r\n<div class=\"embed-container-ads\">\r\n<div id=\"sis_inimage\" data-google-query-id=\"CLGursfruPgCFYPClgodNXkMeQ\">\r\n<div id=\"google_ads_iframe_/27973503/Vnexpress/Desktop/Inimage/Thegioi/Thegioi.detail_0__container__\"><iframe id=\"google_ads_iframe_/27973503/Vnexpress/Desktop/Inimage/Thegioi/Thegioi.detail_0\" tabindex=\"0\" title=\"3rd party ad content\" role=\"region\" name=\"google_ads_iframe_/27973503/Vnexpress/Desktop/Inimage/Thegioi/Thegioi.detail_0\" width=\"1\" height=\"1\" frameborder=\"0\" marginwidth=\"0\" marginheight=\"0\" scrolling=\"no\" aria-label=\"Advertisement\" data-google-container-id=\"4\" data-load-complete=\"true\"></iframe></div>\r\n</div>\r\n</div>\r\n</div>\r\n<figcaption>\r\n<p class=\"Image\">Nh&acirc;n vi&ecirc;n phun khử tr&ugrave;ng một nh&agrave; m&aacute;y ở B&igrave;nh Nhưỡng h&ocirc;m 13/6. Ảnh:&nbsp;<em>AFP</em>.</p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Triều Ti&ecirc;n kh&ocirc;ng n&oacute;i r&otilde; căn bệnh đang diễn ra, chỉ gọi đ&acirc;y l&agrave; dịch đường ruột cấp t&iacute;nh. H&agrave;n Quốc suy đo&aacute;n đ&acirc;y l&agrave; dịch tả hoặc thương h&agrave;n. &Iacute;t nhất 800 hộ gia đ&igrave;nh bị ảnh hưởng đ&atilde; nhận được hỗ trợ ở tỉnh Nam Hwanghae, theo KCNA.</p>\r\n<p class=\"Normal\">Dịch đường ruột cấp t&iacute;nh b&ugrave;ng ph&aacute;t ở Triều Ti&ecirc;n từ ng&agrave;y 16/6, trong bối cảnh quốc gia n&agrave;y đang đối ph&oacute; Covid-19. B&igrave;nh Nhưỡng th&aacute;ng trước ban bố t&igrave;nh trạng khẩn cấp trong bối cảnh lo ngại thiếu vaccine v&agrave; vật tư y tế.</p>\r\n<p class=\"Normal\">Tỉnh Nam Hwanghae, v&ugrave;ng n&ocirc;ng nghiệp trọng điểm của Triều Ti&ecirc;n, cũng đang hứng chịu đợt hạn h&aacute;n, l&agrave;m dấy l&ecirc;n lo ngại về t&aacute;c động đối với t&igrave;nh h&igrave;nh lương thực trong nước vốn đ&atilde; nghi&ecirc;m trọng.</p>\r\n</article>', 218, 2, 4, 1, '2022-11-18 19:35:42', '2023-02-08 01:12:18');
INSERT INTO `posts` (`id`, `title`, `slug`, `excerpt`, `body`, `user_id`, `category_id`, `views`, `approved`, `created_at`, `updated_at`) VALUES
(119, 'Minh Hằng nhảy, hát trong tiệc cưới', 'Minh-Hang-nhay-hat-trong-tiec-cuoi', 'BÀ RỊA - VŨNG TÀU-Ca sĩ Minh Hằng hát tặng chồng và nhảy cùng hội bạn thân bên hồ bơi, ở tiệc cưới tối 18/6.', '<p class=\"description\"><span class=\"location-stamp\">B&Agrave; RỊA - VŨNG T&Agrave;U</span>Ca sĩ Minh Hằng h&aacute;t tặng chồng v&agrave; nhảy c&ugrave;ng hội bạn th&acirc;n b&ecirc;n hồ bơi, ở tiệc cưới tối 18/6.</p>\r\n<article class=\"fck_detail \">\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture data-inimage=\"done\"><source srcset=\"https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-3-9641-1655592876.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=aJse_07SaWWATec_loP2fw 1x, https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-3-9641-1655592876.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=_JLGGGbzeD-8i0ONl-vEQQ 1.5x, https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-3-9641-1655592876.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=dvLUB2K_Qp8MpcL0gqhRCg 2x\" data-srcset=\"https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-3-9641-1655592876.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=aJse_07SaWWATec_loP2fw 1x, https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-3-9641-1655592876.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=_JLGGGbzeD-8i0ONl-vEQQ 1.5x, https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-3-9641-1655592876.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=dvLUB2K_Qp8MpcL0gqhRCg 2x\"><img class=\"lazy lazied\" src=\"https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-3-9641-1655592876.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=aJse_07SaWWATec_loP2fw\" alt=\"Minh Hằng thể hiện ca kh&uacute;c Em cưới rồi nha (Duy Anh) d&agrave;nh tặng chồng. C&ocirc; d&acirc;u v&agrave; ch&uacute; rể c&ugrave;ng c&oacute; sở th&iacute;ch đ&aacute;nh tennis v&igrave; vậy họ d&ugrave;ng m&agrave;n đ&aacute;nh b&oacute;ng để kick off (bắt đầu) buổi tiệc. Chiều c&ugrave;ng ng&agrave;y, Minh Hằng v&agrave; ch&uacute; rể - doanh nh&acirc;n Quốc Bảo - thực hiện nghi lễ truyền thống tr&ecirc;n b&atilde;i biển của một resort ở Hồ Tr&agrave;m. Ca sĩ d&ugrave;ng cỏ lau trang tr&iacute; kh&ocirc;ng gian h&ocirc;n lễ v&igrave; muốn l&agrave; b&ocirc;ng hoa duy nhất trong ng&agrave;y vui. C&ocirc; cũng dặn kh&aacute;ch mời kh&ocirc;ng đăng h&igrave;nh ch&uacute; rể tr&ecirc;n mạng x&atilde; hội giữ sự ri&ecirc;ng tư. D&agrave;n sao g&oacute;p mặt mừng hạnh ph&uacute;c của Minh Hằng.\" loading=\"lazy\" data-src=\"https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-3-9641-1655592876.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=aJse_07SaWWATec_loP2fw\" data-ll-status=\"loaded\"></picture>\r\n<div class=\"embed-container-ads\">&nbsp;</div>\r\n</div>\r\n<figcaption>\r\n<p class=\"Image\">C&ocirc; thể hiện ca kh&uacute;c \"Em cưới rồi nha\" (Duy Anh). C&ocirc; d&acirc;u v&agrave; ch&uacute; rể c&ugrave;ng sở th&iacute;ch đ&aacute;nh tennis, v&igrave; vậy, họ d&ugrave;ng m&agrave;n đ&aacute;nh b&oacute;ng để \"kick off\" (bắt đầu) buổi tiệc.<br><br>Chiều c&ugrave;ng ng&agrave;y, Minh Hằng v&agrave; ch&uacute; rể - doanh nh&acirc;n Quốc Bảo - thực hiện nghi lễ truyền thống tr&ecirc;n b&atilde;i biển của một resort ở Hồ Tr&agrave;m. Ca sĩ d&ugrave;ng&nbsp;<a href=\"https://vnexpress.net/minh-hang-trang-tri-tiec-cuoi-bang-co-lau-4477659.html\" target=\"_blank\" rel=\"dofollow noopener\">cỏ lau</a>&nbsp;trang tr&iacute; kh&ocirc;ng gian h&ocirc;n lễ v&igrave; muốn l&agrave; \"b&ocirc;ng hoa duy nhất\" trong ng&agrave;y vui. C&ocirc; dặn kh&aacute;ch mời kh&ocirc;ng đăng h&igrave;nh&nbsp;<a href=\"https://vnexpress.net/minh-hang-dan-khach-moi-khong-dang-hinh-chu-re-4477684.html\" target=\"_blank\" rel=\"dofollow noopener\">ch&uacute; rể</a>&nbsp;tr&ecirc;n mạng x&atilde; hội để giữ sự ri&ecirc;ng tư.<a href=\"https://vnexpress.net/dan-sao-du-tiec-cuoi-minh-hang-4477665.html\" target=\"_blank\" rel=\"dofollow noopener\">&nbsp;D&agrave;n sao&nbsp;</a>g&oacute;p mặt mừng hạnh ph&uacute;c của Minh Hằng.</p>\r\n</figcaption>\r\n</figure>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-5-6891-1655592876.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=3Ro0F45ZFNbKu7dfU-QDAQ 1x, https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-5-6891-1655592876.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=JE7b1Uu03gml6hBtDSVKxQ 1.5x, https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-5-6891-1655592876.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=7JyvDrZxkHdVdvnOC8NcJg 2x\" data-srcset=\"https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-5-6891-1655592876.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=3Ro0F45ZFNbKu7dfU-QDAQ 1x, https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-5-6891-1655592876.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=JE7b1Uu03gml6hBtDSVKxQ 1.5x, https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-5-6891-1655592876.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=7JyvDrZxkHdVdvnOC8NcJg 2x\"><img class=\"lazy lazied\" src=\"https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-5-6891-1655592876.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=3Ro0F45ZFNbKu7dfU-QDAQ\" alt=\"Ca sĩ diện thiết kế ren xuy&ecirc;n thấu của Chung Thanh Phong, thuận tiện nhảy c&ugrave;ng mọi người. Trấn Th&agrave;nh (phải) l&ecirc;n s&acirc;n khấu hưởng ứng m&agrave;n biểu diễn của c&ocirc; d&acirc;u.\" loading=\"lazy\" data-src=\"https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-5-6891-1655592876.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=3Ro0F45ZFNbKu7dfU-QDAQ\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Ca sĩ diện thiết kế ren xuy&ecirc;n thấu của Chung Thanh Phong, nhảy c&ugrave;ng mọi người. Trấn Th&agrave;nh l&ecirc;n s&acirc;n khấu hưởng ứng m&agrave;n biểu diễn của c&ocirc; d&acirc;u.</p>\r\n</figcaption>\r\n</figure>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-11-2254-1655592876.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=dWuxtXCc-kPCkYjppLWmBQ 1x, https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-11-2254-1655592876.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=txh1sf_uDEtBhRpV3xMJFA 1.5x, https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-11-2254-1655592876.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=YB2luzw1qB9oLRD7wbRXZw 2x\" data-srcset=\"https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-11-2254-1655592876.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=dWuxtXCc-kPCkYjppLWmBQ 1x, https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-11-2254-1655592876.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=txh1sf_uDEtBhRpV3xMJFA 1.5x, https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-11-2254-1655592876.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=YB2luzw1qB9oLRD7wbRXZw 2x\"><img class=\"lazy lazied\" src=\"https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-11-2254-1655592876.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=dWuxtXCc-kPCkYjppLWmBQ\" alt=\"Ho&agrave;ng Th&ugrave;y Linh (tr&aacute;i), Đ&ocirc;ng Nhi v&agrave; Khả Ng&acirc;n (phải) tr&igrave;nh diễn tiết mục c&ugrave;ng Minh Hằng. Họ c&ugrave;ng bi&ecirc;n đạo Lan Nhi luyện tập kỹ c&agrave;ng nhiều tuần qua.\" loading=\"lazy\" data-src=\"https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-11-2254-1655592876.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=dWuxtXCc-kPCkYjppLWmBQ\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Ho&agrave;ng Th&ugrave;y Linh (tr&aacute;i), Đ&ocirc;ng Nhi v&agrave; Khả Ng&acirc;n (phải) tr&igrave;nh diễn tiết mục c&ugrave;ng Minh Hằng. Họ c&ugrave;ng bi&ecirc;n đạo Lan Nhi luyện tập nhiều tuần qua.</p>\r\n</figcaption>\r\n</figure>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-1-7973-1655592876.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=1iNhHr1pyG3_TsiF5PGXIQ 1x, https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-1-7973-1655592876.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ydF8SwguvaU_U3Bkgp7b_A 1.5x, https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-1-7973-1655592876.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=RcY00LE2XAlZnrBsWWLoEA 2x\" data-srcset=\"https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-1-7973-1655592876.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=1iNhHr1pyG3_TsiF5PGXIQ 1x, https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-1-7973-1655592876.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ydF8SwguvaU_U3Bkgp7b_A 1.5x, https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-1-7973-1655592876.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=RcY00LE2XAlZnrBsWWLoEA 2x\"><img class=\"lazy lazied\" src=\"https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-1-7973-1655592876.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=1iNhHr1pyG3_TsiF5PGXIQ\" alt=\"C&aacute;c nghệ sĩ quậy tưng bừng trong buổi tiệc. Họ tham gia tr&ograve; chơi nhảy xuống hồ t&igrave;m gi&agrave;y, d&eacute;p\" loading=\"lazy\" data-src=\"https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-1-7973-1655592876.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=1iNhHr1pyG3_TsiF5PGXIQ\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Đ&ocirc;ng Nhi tham gia tr&ograve; chơi nhảy xuống hồ t&igrave;m gi&agrave;y, d&eacute;p</p>\r\n</figcaption>\r\n</figure>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-4-2151-1655592876.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=RY8EjQVbihruUUbxlyDpaQ 1x, https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-4-2151-1655592876.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=uI_CQfx7JYI6HFFPskUvog 1.5x, https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-4-2151-1655592876.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=PBb1ykYz-LbyByUCjvAN9w 2x\" data-srcset=\"https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-4-2151-1655592876.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=RY8EjQVbihruUUbxlyDpaQ 1x, https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-4-2151-1655592876.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=uI_CQfx7JYI6HFFPskUvog 1.5x, https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-4-2151-1655592876.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=PBb1ykYz-LbyByUCjvAN9w 2x\"><img class=\"lazy lazied\" src=\"https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-4-2151-1655592876.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=RY8EjQVbihruUUbxlyDpaQ\" alt=\"Đ&ocirc;ng Nhi, Khả Như (h&igrave;nh tr&ecirc;n) ướt sủng người.\" loading=\"lazy\" data-src=\"https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-4-2151-1655592876.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=RY8EjQVbihruUUbxlyDpaQ\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Diễn vi&ecirc;n Khả Như.</p>\r\n</figcaption>\r\n</figure>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"action_thumb flexbox\">&nbsp;</div>\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-5649-1655600667.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=w3D66nOe13EGRJwtpIIpSw 1x, https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-5649-1655600667.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=p5tlfel8wyeV-NaQgHlHJQ 1.5x, https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-5649-1655600667.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=ON_T99H5K1VV0zgwPybebw 2x\" data-srcset=\"https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-5649-1655600667.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=w3D66nOe13EGRJwtpIIpSw 1x, https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-5649-1655600667.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=p5tlfel8wyeV-NaQgHlHJQ 1.5x, https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-5649-1655600667.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=ON_T99H5K1VV0zgwPybebw 2x\"><img class=\"lazy lazied\" src=\"https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-5649-1655600667.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=w3D66nOe13EGRJwtpIIpSw\" alt=\"&Ocirc;ng x&atilde; Minh Hằng hơn c&ocirc; 10 tuổi, l&agrave;m trong lĩnh vực dệt may. Họ quen nhau hơn s&aacute;u năm mới quyết định về chung một nh&agrave;. Ca sĩ sinh năm 1987, hoạt động ở vai tr&ograve; ca sĩ, diễn vi&ecirc;n. Ở phim truyền h&igrave;nh, c&ocirc; từng g&acirc;y ch&uacute; &yacute; qua c&aacute;c dự &aacute;n Mộng ph&ugrave; du, Gọi giấc mơ về, Ng&atilde; rẽ cuộc đời... Vai Đ&ocirc;ng Dương trong phim Vừa đi vừa kh&oacute;c từng gi&uacute;p Minh Hằng đoạt giải Mai V&agrave;ng hạng mục Nữ diễn vi&ecirc;n điện ảnh - phim truyền h&igrave;nh được y&ecirc;u th&iacute;ch năm 2015. Ở mảng điện ảnh, c&ocirc; từng tham gia Giải cứu thần chết, Những nụ h&ocirc;n rực rỡ, Sắc đẹp ng&agrave;n c&acirc;n...\" loading=\"lazy\" data-src=\"https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-5649-1655600667.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=w3D66nOe13EGRJwtpIIpSw\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\"><br>&Ocirc;ng x&atilde; Minh Hằng hơn c&ocirc; 10 tuổi, l&agrave;m trong lĩnh vực dệt may. Họ quen nhau hơn s&aacute;u năm mới quyết định \"về chung một nh&agrave;\". Ca sĩ sinh năm 1987, hoạt động ở vai tr&ograve; ca sĩ, diễn vi&ecirc;n. Ở phim truyền h&igrave;nh, c&ocirc; từng g&acirc;y ch&uacute; &yacute; qua c&aacute;c dự &aacute;n \"Mộng ph&ugrave; du\", \"Gọi giấc mơ về\", \"Ng&atilde; rẽ cuộc đời\"... Vai Đ&ocirc;ng Dương trong phim \"Vừa đi vừa kh&oacute;c\" từng gi&uacute;p Minh Hằng đoạt giải Mai V&agrave;ng hạng mục \"Nữ diễn vi&ecirc;n điện ảnh - phim truyền h&igrave;nh được y&ecirc;u th&iacute;ch\" năm 2015. Ở mảng điện ảnh, c&ocirc; từng tham gia&nbsp;&nbsp;</p>\r\n</figcaption>\r\n</figure>\r\n</article>', 218, 8, 0, 1, '2022-11-18 19:35:42', '2022-12-08 08:55:25'),
(124, 'Phát hiện ung thư máu từ triệu chứng đau họng, khó nuốt', 'phat-hien-ung-thu-mau-tu-trieu-chung-dau-hong-kho-nuot', 'HÀ NỘI người đàn ông 24 tuổi, đến Khoa Tai Mũi Họng, Bệnh viện Đại học Y vì đau họng, nuốt khó, xét nghiệm phát hiện ung thư máu', '<p class=\"description\"><span class=\"location-stamp\">H&Agrave; NỘI n</span>gười đ&agrave;n &ocirc;ng 24 tuổi, đến Khoa Tai Mũi Họng, Bệnh viện Đại học Y v&igrave; đau họng, nuốt kh&oacute;, x&eacute;t nghiệm ph&aacute;t hiện ung thư m&aacute;u.</p>\r\n<article class=\"fck_detail \">\r\n<p class=\"Normal\">Người bệnh sốt cao, đau họng k&eacute;o d&agrave;i, đ&atilde; d&ugrave;ng thuốc kh&aacute;ng sinh v&agrave; kh&aacute;ng vi&ecirc;m li&ecirc;n tục m&agrave; kh&ocirc;ng đỡ. Anh nghĩ m&igrave;nh bị amidan, chỉ cần cắt l&agrave; khỏi.</p>\r\n<p class=\"Normal\">PGS.TS Phạm Thị B&iacute;ch Đ&agrave;o, Bộ m&ocirc;n Tai Mũi Họng, Bệnh viện Đại học Y H&agrave; Nội, ng&agrave;y 18/6 cho biết ni&ecirc;m mạc họng bệnh nh&acirc;n sưng nề nhưng kh&ocirc;ng đỏ. V&ugrave;ng họng mũi mềm v&agrave; lưỡi g&agrave; c&oacute; những đ&aacute;m tụ m&aacute;u. Amidan kh&ocirc;ng đỏ, kh&ocirc;ng qu&aacute; ph&aacute;t, kh&ocirc;ng c&oacute; giả mạc b&aacute;m song x&eacute;t nghiệm m&aacute;u thấy số lượng giảm.</p>\r\n<p class=\"Normal\">\"Ban đầu, ch&uacute;ng t&ocirc;i nghĩ đến bệnh nh&acirc;n bị dị ứng đồ uống g&acirc;y ph&ugrave; n&ecirc;n chỉ định sử dụng chống dị ứng v&agrave; corticoid\", b&aacute;c sĩ n&oacute;i.</p>\r\n<p class=\"Normal\">Trong ba ng&agrave;y đầu, c&aacute;c biểu hiện đau họng v&agrave; nuốt vướng của người bệnh giảm. Đến ng&agrave;y thứ 4, c&aacute;c triệu chứng n&agrave;y lại gia tăng. Ngo&agrave;i ra, da bệnh nh&acirc;n lạnh v&agrave; trắng, người mệt mỏi, bị s&uacute;t c&acirc;n, x&eacute;t nghiệm chuy&ecirc;n s&acirc;u c&oacute; hiện tượng đ&ocirc;ng m&aacute;u nội mạch lan tỏa (DIC). B&aacute;c sĩ chẩn đo&aacute;n bệnh nh&acirc;n mắc bệnh bạch cầu cấp.</p>\r\n<p class=\"Normal\">Bạch cầu cấp l&agrave; một bệnh l&yacute; ung thư m&aacute;u, do c&aacute;c tế b&agrave;o m&aacute;u bị ung thư h&oacute;a trong qu&aacute; tr&igrave;nh tạo ra tế b&agrave;o. Những tế b&agrave;o ung thư n&agrave;y nh&acirc;n l&ecirc;n rất nhanh v&agrave; nếu kh&ocirc;ng được điều trị sẽ ứ đọng trong tủy xương v&agrave; cản trở qu&aacute; tr&igrave;nh tạo ra c&aacute;c tế b&agrave;o m&aacute;u b&igrave;nh thường tiếp theo, li&ecirc;n quan đến suy tủy xương tiến triển.</p>\r\n<p class=\"Normal\">Bệnh c&oacute; thể xảy ra ở nhiều lứa tuổi, nhiều trường hợp c&oacute; triệu chứng mơ hồ khiến ph&aacute;t hiện bệnh đ&atilde; ở giai đoạn muộn. V&iacute; dụ như đổ mồ h&ocirc;i đ&ecirc;m, bầm t&iacute;m v&agrave; chảy m&aacute;u qu&aacute; mức, đau bụng, đau xương khớp, nhức đầu, sốt kh&ocirc;ng r&otilde; nguy&ecirc;n nh&acirc;n, k&eacute;o d&agrave;i tr&ecirc;n 23 tuần...</p>\r\n<p class=\"Normal\">Theo b&aacute;c sĩ Đ&agrave;o, nhiều bệnh nh&acirc;n ung thư m&aacute;u c&oacute; biểu hiện tại họng. Bệnh nh&acirc;n dễ bị vi&ecirc;m họng v&agrave; đ&aacute;p ứng k&eacute;m với kh&aacute;ng sinh do chức năng của bạch cầu l&agrave; bảo vệ cơ thể chống lại c&aacute;c t&aacute;c nh&acirc;n g&acirc;y bệnh từ b&ecirc;n ngo&agrave;i như vi khuẩn. Khi lượng bạch cầu bị giảm s&uacute;t dẫn đến kh&ocirc;ng đủ khả năng chống đỡ.</p>\r\n<p class=\"Normal\">B&aacute;c sĩ khuyến c&aacute;o, mọi người c&oacute; bất thường cơ thể cần đi kh&aacute;m sớm để kiểm tra, điều trị triệt để, tr&aacute;nh biến chứng sau n&agrave;y.</p>\r\n</article>', 218, 5, 3, 1, '2022-11-18 19:35:42', '2022-11-22 03:52:51'),
(126, 'Xe container cháy ngùn ngụt', 'xe-container-chay-ngun-ngut', 'ĐỒNG NAIPhát hiện khói bốc lên ở đầu cabin, tài xế dùng bình chữa cháy mini dập lửa, song bất thành, lửa bùng cháy dữ dội.', '<p class=\"description\"><span class=\"location-stamp\">ĐỒNG NAI</span>Ph&aacute;t hiện kh&oacute;i bốc l&ecirc;n ở đầu cabin, t&agrave;i xế d&ugrave;ng b&igrave;nh chữa ch&aacute;y mini dập lửa, song bất th&agrave;nh, lửa b&ugrave;ng ch&aacute;y dữ dội.</p>\r\n<article class=\"fck_detail \">\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/19/288765178-595079798714572-5293-3396-2235-1655615406.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=2bzD9cU9JAvmfa-KPneOMw 1x, https://i1-vnexpress.vnecdn.net/2022/06/19/288765178-595079798714572-5293-3396-2235-1655615406.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=v-18K72W_MJb0LhZOyzXAg 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/19/288765178-595079798714572-5293-3396-2235-1655615406.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=0dwHu5F8JejxImdXiDbO5A 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/19/288765178-595079798714572-5293-3396-2235-1655615406.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=2bzD9cU9JAvmfa-KPneOMw 1x, https://i1-vnexpress.vnecdn.net/2022/06/19/288765178-595079798714572-5293-3396-2235-1655615406.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=v-18K72W_MJb0LhZOyzXAg 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/19/288765178-595079798714572-5293-3396-2235-1655615406.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=0dwHu5F8JejxImdXiDbO5A 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/06/19/288765178-595079798714572-5293-3396-2235-1655615406.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=2bzD9cU9JAvmfa-KPneOMw\" alt=\"Đầu xe container ch&aacute;y ng&ugrave;n ngụt tr&ecirc;n quốc lộ 51. Ảnh: Th&aacute;i H&agrave;\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/06/19/288765178-595079798714572-5293-3396-2235-1655615406.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=2bzD9cU9JAvmfa-KPneOMw\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Đầu xe container ch&aacute;y ng&ugrave;n ngụt tr&ecirc;n quốc lộ 51. Ảnh:&nbsp;<em>Th&aacute;i H&agrave;</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">S&aacute;ng 19/6, t&agrave;i xế Nguyễn Ngọc Tuyền chạy xe container tr&ecirc;n quốc lộ 51 từ TP HCM - Vũng T&agrave;u. Khi đến phường An H&ograve;a, TP Bi&ecirc;n H&ograve;a, l&aacute;i xe ph&aacute;t hiện kh&oacute;i bốc ra ở cabin liền dừng lại, c&ugrave;ng người d&acirc;n d&ugrave;ng b&igrave;nh chữa ch&aacute;y mini dập lửa song bất th&agrave;nh.</p>\r\n<p class=\"Normal\">Chỉ 15 ph&uacute;t sau, lửa ch&aacute;y ng&ugrave;n ngụt bao tr&ugrave;m đầu k&eacute;o, kh&oacute;i bốc cao h&agrave;ng chục m&eacute;t. Lực lượng PCCC tỉnh Đồng Nai điều động nhiều xe c&ugrave;ng chiến sĩ đến dập lửa, khống chế.</p>\r\n<p class=\"Normal\">Vụ ch&aacute;y kh&ocirc;ng g&acirc;y thương vong, song to&agrave;n bộ đầu k&eacute;o bị thi&ecirc;u rụi, số h&agrave;ng ph&iacute;a sau xe kh&ocirc;ng bị ảnh hưởng. Quốc lộ 51 qua khu vực bị kẹt xe nhiều giờ liền.</p>\r\n</article>', 218, 3, 3, 1, '2022-11-18 19:35:42', '2022-11-22 05:25:05'),
(127, 'Phát hiện hơn 5.000 loài virus bí ẩn dưới đại dương', 'phat-hien-hon-5000-loai-virus-bi-an-duoi-dai-duong', 'Một nhóm các nhà nghiên cứu quốc tế đã có một phát hiện lớn sau khi xác định được khoảng 5.500 loại virus trong các đại dương mà các nhà khoa học chưa từng biết đến trước đây., Tạp chí Doanh Nghiệp Việt Nam', '<p>C&aacute;c nh&agrave; khoa học ph&aacute;t hiện hơn 5.500 loai virus ARN mới ẩn nấp dưới biển s&acirc;u nhờ một cuộc khảo s&aacute;t đại dương quy m&ocirc; lớn. Nghi&ecirc;n cứu n&agrave;y xuất bản tr&ecirc;n tạp ch&iacute;Sciencehồi th&aacute;ng 4. Trong nghi&ecirc;n cứu mới c&ocirc;ng bố tr&ecirc;n tạp ch&iacute;Scienceh&ocirc;m 9/6 của c&ugrave;ng nh&oacute;m nh&agrave; khoa học, họ tập trung hơn v&agrave;o ảnh hưởng của c&aacute;c virus ARN đến lượng carbon to&agrave;n cầu.</p>\r\n<p>Giai đoạn năm 2008 - 2011, nh&oacute;m dự &aacute;n Tara Ocean Expedition đi khắp c&aacute;c đại dương tr&ecirc;n thế giới v&agrave; thu thập nhiều mẫu nước biển. Họ ph&acirc;n t&iacute;ch nước v&agrave; t&igrave;m thấy h&agrave;ng trăm ngh&igrave;n lo&agrave;i virus ADN.</p>\r\n<div><img src=\"https://static.tin.moi/uploads/news/photo/wH/wH5/wH5S7cH95l-680x408.jpg\"></div>\r\n<p>Trong chuyến th&aacute;m hiểm k&eacute;o d&agrave;i nhiều năm, c&aacute;c nh&agrave; khoa học đ&atilde; thu thập mẫu nước từ mọi đại dương tr&ecirc;n thế giới. Ảnh:Sacha Bollet/Fondation Tara Ocean.</p>\r\n<p>Nh&oacute;m nghi&ecirc;n cứu đ&atilde; ph&acirc;n t&iacute;ch 35.000 mẫu nước được thu thập từ 121 địa điểm tr&ecirc;n 5 đại dương của Tara Oceans Consortium, một nghi&ecirc;n cứu to&agrave;n cầu đang diễn ra nhằm kiểm tra t&aacute;c động của biến đổi kh&iacute; hậu đối với c&aacute;c đại dương.</p>\r\n<p>Những mẫu nước n&agrave;y chứa đầy sinh vật ph&ugrave; du - những sinh vật nhỏ b&eacute; tr&ocirc;i theo d&ograve;ng chảy v&agrave; thường đ&oacute;ng vai tr&ograve; l&agrave; vật chủ cho virus RNA.</p>\r\n<p>Để ph&aacute;t hiện c&aacute;c virus trong c&aacute;c sinh vật ph&ugrave; du n&agrave;y, c&aacute;c nh&agrave; nghi&ecirc;n cứu đ&atilde; s&agrave;ng lọc tất cả RNA trong tế b&agrave;o của sinh vật ph&ugrave; du để t&igrave;m ra một đoạn m&atilde; di truyền cụ thể, được gọi l&agrave; gene RdRp.</p>\r\n<p>C&aacute;c nh&agrave; khoa học x&aacute;c định c&aacute;c cộng đồng virus c&oacute; thể được sắp xếp th&agrave;nh 4 khu vực ch&iacute;nh: Bắc cực, Nam cực, &Ocirc;n đới v&agrave; Nhiệt đới. Điều th&uacute; vị l&agrave; sự đa dạng của virus dường như cao nhất ở c&aacute;c v&ugrave;ng cực, mặc d&ugrave; c&oacute; nhiều loại vật chủ hơn để l&acirc;y nhiễm ở c&aacute;c v&ugrave;ng nước ấm hơn.</p>\r\n<p>H&igrave;nh ảnh virus RNA. Ảnh: ALPES HOLIDAY.</p>\r\n<p>C&aacute;c nh&agrave; nghi&ecirc;n cứu sau đ&oacute; tiếp tục ph&acirc;n t&iacute;ch c&aacute;c mẫu n&agrave;y để khảo s&aacute;t virus ARN dưới biển. ARN l&agrave; \"họ h&agrave;ng\" ph&acirc;n tử của ADN. Virus ARN hiện diện trong nhiều bệnh ở người, v&iacute; dụ, virus corona v&agrave; virus c&uacute;m. Kết quả, nh&oacute;m nghi&ecirc;n cứu t&igrave;m thấy hơn 5.500 loại virus ARN mới.</p>\r\n<p>Trong nghi&ecirc;n cứu mới xuất bản h&ocirc;m 9/6, nh&oacute;m chuy&ecirc;n gia tập trung v&agrave;o ảnh hưởng của virus trong việc thu giữ carbon. Thực tế, đại dương thu giữ một lượng carbon khổng lồ, c&oacute; thể l&ecirc;n tới khoảng 12 tỷ tấn mỗi năm, tương đương 1/3 lượng kh&iacute; thải CO2 của nh&acirc;n loại.</p>\r\n<p>Điều n&agrave;y do một lượng lớn c&aacute;c sinh vật cực nhỏ - sinh vật ph&ugrave; du. Khi chết, cơ thể chứa đầy carbon của ch&uacute;ng ch&igrave;m xuống đ&aacute;y đại dương. Sau đ&oacute;, ch&uacute;ng bị ch&ocirc;n v&ugrave;i suốt h&agrave;ng ngh&igrave;n hoặc h&agrave;ng triệu năm. Đ&acirc;y được gọi l&agrave; m&aacute;y bơm carbon sinh học.</p>\r\n<p>Từ l&acirc;u giới chuy&ecirc;n gia đ&atilde; biết virus đ&oacute;ng vai tr&ograve; quan trọng trong qu&aacute; tr&igrave;nh thu giữ carbon n&agrave;y v&agrave; virus c&oacute; thể ảnh hưởng đến khối lượng carbon được lưu trữ dưới đại dương. Tuy nhi&ecirc;n, c&aacute;c loại virus trong nghi&ecirc;n cứu mới chưa từng được biết đến trước đ&acirc;y. Nh&oacute;m nghi&ecirc;n cứu cho biết, họ đ&atilde; x&aacute;c định được &iacute;t nhất 11 virus ARN trong số hơn hơn 5.500 virus mới đ&oacute;ng vai tr&ograve; quan trọng với m&aacute;y bơm carbon.</p>\r\n<p>Nh&oacute;m nh&agrave; khoa học cũng ph&aacute;t triển một m&ocirc; h&igrave;nh m&aacute;y t&iacute;nh gi&uacute;p dự đo&aacute;n hoạt động của m&aacute;y bơm carbon trong một v&ugrave;ng biển nhất định, t&ugrave;y thuộc v&agrave;o số lượng virus trong khu vực l&acirc;n cận. M&ocirc; h&igrave;nh n&agrave;y c&oacute; thể cung cấp th&ocirc;ng tin cho c&aacute;c m&ocirc; h&igrave;nh kh&iacute; hậu, d&ugrave; hiện tại c&aacute;c nh&agrave; khoa học chưa r&otilde; ch&iacute;nh x&aacute;c virus gi&uacute;p tăng tốc hay cản trở m&aacute;y bơm như thế n&agrave;o.</p>\r\n<p>\"Khi thải nhiều carbon hơn v&agrave;o kh&iacute; quyển, con người phụ thuộc v&agrave;o khả năng hỗ trợ to lớn của đại dương để l&agrave;m chậm qu&aacute; tr&igrave;nh biến đổi kh&iacute; hậu. Ch&uacute;ng t&ocirc;i ng&agrave;y c&agrave;ng nhận thức r&otilde; hơn rằng c&oacute; thể con người cần điều chỉnh m&aacute;y bơm ở quy m&ocirc; đại dương\", Matthew Sullivan, đồng t&aacute;c giả nghi&ecirc;n cứu, cho biết.</p>\r\n<p>Một kết quả th&uacute; vị kh&aacute;c từ nghi&ecirc;n cứu mới l&agrave; một số virus ARN dưới biển đ&atilde; \"ăn trộm\" gene từ những sinh vật nhiễm virus. Điều n&agrave;y c&oacute; thể gi&uacute;p giới khoa học nhận diện c&aacute;c vật chủ của virus v&agrave; vai tr&ograve; của ch&uacute;ng trong những qu&aacute; tr&igrave;nh diễn ra dưới biển.</p>\r\n<div class=\"ddict_btn\" style=\"top: 575px; left: 754px;\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\"></div>', 218, 11, 3, 1, '2022-11-18 19:35:42', '2022-12-08 09:01:23'),
(128, 'Phát hiện 33 viên ngọc lạ giúp \"quay ngược thời gian\" Trái Đất', 'phat-hien-33-vien-ngoc-la-giup-quay-nguoc-thoi-gian-trai-dat', '33 tinh thể zircon quý giá, trông như những viên hồng ngọc cực kỳ sẫm màu', '<p>Nh&oacute;m nghi&ecirc;n cứu dẫn đầu bởi nh&agrave; địa chất Nadja Drabon từ Đại học Harvard (Mỹ) cho biết đ&oacute; l&agrave; những tinh thể kho&aacute;ng chất cực hiếm, được h&igrave;nh th&agrave;nh trong điều kiện h&uacute;t ch&igrave;m - khi m&eacute;p của một mảng kiến tạo trượt xuống b&ecirc;n dưới m&eacute;p của mảng b&ecirc;n cạnh.</p>\r\n<p>Mảng kiến tạo c&oacute; thể hiểu n&ocirc;m na l&agrave; những mảnh vỏ của Tr&aacute;i Đất. C&aacute;c mảng n&agrave;y li&ecirc;n tục di chuyển, trượt l&ecirc;n nhau, khiến c&aacute;c lục địa v&agrave; đại dương ch&uacute;ng c&otilde;ng tr&ecirc;n lưng cũng li&ecirc;n tục thay đổi h&igrave;nh dạng. Ch&iacute;nh qu&aacute; tr&igrave;nh kiến tạo mảng đ&atilde; khiến Tr&aacute;i Đất nhiều lần xuất hiện si&ecirc;u lục địa, rồi lại tan r&atilde; th&agrave;nh nhiều ch&acirc;u lục như ng&agrave;y nay.</p>\r\n<div>\r\n<div>\r\n<div><img src=\"https://static.tin.moi/uploads/news/photo/MV/MVD/MVDDycsGZQ-684x429.jpg\"></div>\r\n</div>\r\n<div>\r\n<p>Một tinh thể zircon lớn được gắn v&agrave;o \"đế\" l&agrave;m bằng canxit - Ảnh: SCIENCE ALERT</p>\r\n</div>\r\n</div>\r\n<p>Kiến tạo mảng v&agrave; c&aacute;c hoạt động địa chất kh&aacute;c c&oacute; vai tr&ograve; quan trọng trong việc tạo ra c&aacute;c điều kiện ph&ugrave; hợp với sự sống tr&ecirc;n Tr&aacute;i Đất, l&agrave;m thay đổi th&agrave;nh phần đại dương v&agrave; kh&iacute; quyển, n&ecirc;n việc hiểu r&otilde; thời điểm v&agrave; c&aacute;ch thức kiến tạo mảng được khởi động c&oacute; thể gi&uacute;p t&igrave;m c&acirc;u trả lời cho vất đề lớn: điều g&igrave; khiến Tr&aacute;i Đất sống được.</p>\r\n<p>Theo Science Alert, vệc ph&acirc;n t&iacute;ch những hạt zircon n&agrave;y c&oacute; thể đem lại điều mong đợi đ&oacute;. C&aacute;c đồng vị hafnium kim loại v&agrave; c&aacute;c nguy&ecirc;n tố vi lượng trong ch&uacute;ng c&oacute; thể được sử dụng để suy luận ra loại đ&aacute; m&agrave; từ đ&oacute; ch&uacute;ng kết tinh, được đ&aacute;nh dấu bằng những mốc thời gian cụ thể.</p>\r\n<p>Những tinh thể n&agrave;y gần như \"bất tử\", nguy&ecirc;n vẹn từ khi ch&uacute;ng ra đời, nhưng chỉ c&oacute; thể được khai quật tại 12 địa điểm tr&ecirc;n khắp địa cầu.</p>\r\n<p>Kho b&aacute;u n&agrave;y được khai quật ở V&agrave;nh đai Barberton Greenstone ở Nam Phi, sẽ mang đến cơ hội hiếm c&oacute; để \"quay ngược thời gian\" đến tận Li&ecirc;n đại Hỏa Th&agrave;nh (Th&aacute;i Viễn Cổ, từ khi Tr&aacute;i Đất h&igrave;nh th&agrave;nh đến 3,8 tỉ năm trước) v&agrave; v&agrave;i trăm triệu năm đầu của Li&ecirc;n Đại Th&aacute;i Cổ.</p>\r\n<p>Những hạt cổ xưa nhất c&ograve;n gi&uacute;p gợi &yacute; về th&agrave;nh phần của lớp vỏ \"Tr&aacute;i Đất địa ngục\" trong giả thuyết. C&aacute;c nh&agrave; khoa học lu&ocirc;n tin rằng trong Li&ecirc;n đại Hỏa Th&agrave;nh, to&agrave;n bộ lớp ngo&agrave;i h&agrave;nh tinh l&agrave; quả cầu lửa n&oacute;ng bỏng với biển dung nham, nhưng bằng chứng x&aacute;c thực về th&agrave;nh phần của lớp vỏ sơ khai n&agrave;y th&igrave; cực kỳ hiếm hoi.</p>', 218, 11, 0, 1, '2022-11-18 19:35:42', '2022-11-22 06:20:52'),
(129, 'Phim về Marilyn Monroe hé lộ bi kịch hậu trường', 'Phim-ve-Marilyn-Monroe-he-lo-bi-kich-hau-truong', 'Trong \"Blonde\", huyền thoại điện ảnh Marilyn Monroe (Ana de Armas đóng) khóc sau cánh gà dù được truyền thông săn đón.', '<p class=\"description\">Trong \"Blonde\", huyền thoại điện ảnh Marilyn Monroe (Ana de Armas đ&oacute;ng) kh&oacute;c sau c&aacute;nh g&agrave; d&ugrave; được truyền th&ocirc;ng săn đ&oacute;n.</p>\r\n<article class=\"fck_detail \">\r\n<div id=\"divfirst\">\r\n<div>\r\n<figure class=\"item_slide_show clearfix\">\r\n<div id=\"video_parent_357183\" class=\"box_embed_video_parent embed_video_new\" data-vcate=\"1003834\" data-vscate=\"1003007\" data-vscaten=\"Giải tr&iacute;\" data-vid=\"357183\" data-ratio=\"1\" data-articleoriginal=\"4477845\" data-ads=\"0\" data-license=\"0\" data-duration=\"48\" data-brandsafe=\"0\" data-type=\"0\" data-play=\"1\" data-frame=\"\" data-aot=\"\" data-videoclassify=\"None\" data-initdom=\"1\" data-view=\"inview\" data-auto=\"1\" data-status=\"play\">\r\n<div id=\"embed_video_357183\" class=\"box_embed_video\">\r\n<div class=\"bg_overlay_small_unpin\">&nbsp;</div>\r\n</div>\r\n</div>\r\n</figure>\r\n</div>\r\n<p class=\"Normal\">Teaser mở m&agrave;n với cảnh Marilyn Monroe kh&oacute;c trong hậu trường với lời cầu xin ai đ&oacute; đừng bỏ rơi c&ocirc;. Nhiều khoảnh khắc kinh điển của minh tinh được t&aacute;i hiện, như cảnh h&agrave;ng trăm k&yacute; giả săn đ&oacute;n Marilyn Monroe, m&agrave;n tung v&aacute;y lấy cảm hứng từ phim&nbsp;<em>The Seven Year Itch</em>... Video kh&eacute;p lại khi nh&acirc;n vật ch&iacute;nh gượng cười trước b&agrave;n trang điểm như che giấu nỗi đau.</p>\r\n<p class=\"Normal\">Theo đơn vị sản xuất, phim t&aacute;i hiện cuộc đời Marilyn Monroe từ thời thơ ấu - l&uacute;c c&ograve;n l&agrave; c&ocirc; b&eacute; Norma Jeane - đến khi trưởng th&agrave;nh, đạt đỉnh cao, trải qua nhiều mối t&igrave;nh. Phim do đạo diễn Andrew Dominik thực hiện, ph&aacute;t h&agrave;nh trực tuyến, d&aacute;n nh&atilde;n NC-17 (cấm kh&aacute;n giả dưới 17 tuổi).</p>\r\n<p class=\"Normal\">Theo&nbsp;<em>Variety</em>, Ana de Armas hạnh ph&uacute;c khi được đạo diễn chọn đ&oacute;ng vai huyền thoại Hollywood. C&ocirc; mất ch&iacute;n th&aacute;ng tập luyện với huấn luyện vi&ecirc;n giọng n&oacute;i, kết hợp th&ecirc;m kỹ thuật ADR (lồng tiếng hậu kỳ) để c&oacute; chất giọng như cố minh tinh. C&ocirc; được tiểu thuyết gốc, xem h&agrave;ng trăm cuộn băng, ghi &acirc;m tư liệu về Marilyn Monroe. \"T&ocirc;i suy nghĩ nhiều về số phận phụ nữ trong ng&agrave;nh c&ocirc;ng nghiệp n&agrave;y. Qua bộ phim, t&ocirc;i thấy nhiều vấn đề trong c&aacute;c thập ni&ecirc;n 1930, 1950 vẫn c&ograve;n tồn tại tới ng&agrave;y nay. Nếu kh&ocirc;ng c&oacute; nền tảng vững chắc như sự ủng hộ của gia đ&igrave;nh, bạn kh&oacute; sống được ở Hollywood\", c&ocirc; n&oacute;i với&nbsp;<em>The Times of London</em>.</p>\r\n</div>\r\n<div id=\"admbackground\" data-set=\"dfp\"></div>\r\n<div id=\"divend\">\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture data-inimage=\"done\"><source srcset=\"https://i1-giaitri.vnecdn.net/2022/06/19/blonde-2-jpeg-8017-1655615729.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=80644Fm2poG0l3mP-0CCgg 1x, https://i1-giaitri.vnecdn.net/2022/06/19/blonde-2-jpeg-8017-1655615729.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=1SuRGynzVnDs2KYJ73Ve1w 1.5x, https://i1-giaitri.vnecdn.net/2022/06/19/blonde-2-jpeg-8017-1655615729.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=_En7rSfX_21yrGTp0w2s-A 2x\" data-srcset=\"https://i1-giaitri.vnecdn.net/2022/06/19/blonde-2-jpeg-8017-1655615729.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=80644Fm2poG0l3mP-0CCgg 1x, https://i1-giaitri.vnecdn.net/2022/06/19/blonde-2-jpeg-8017-1655615729.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=1SuRGynzVnDs2KYJ73Ve1w 1.5x, https://i1-giaitri.vnecdn.net/2022/06/19/blonde-2-jpeg-8017-1655615729.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=_En7rSfX_21yrGTp0w2s-A 2x\"><img class=\"lazy lazied\" src=\"https://i1-giaitri.vnecdn.net/2022/06/19/blonde-2-jpeg-8017-1655615729.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=80644Fm2poG0l3mP-0CCgg\" alt=\"Ana de Armas trong tạo h&igrave;nh Marilyn Monroe. Ảnh: Screenrant\" loading=\"lazy\" data-src=\"https://i1-giaitri.vnecdn.net/2022/06/19/blonde-2-jpeg-8017-1655615729.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=80644Fm2poG0l3mP-0CCgg\" data-ll-status=\"loaded\"></picture>\r\n<div class=\"embed-container-ads\">&nbsp;</div>\r\n</div>\r\n<figcaption>\r\n<p class=\"Image\">Ana de Armas trong tạo h&igrave;nh Marilyn Monroe. Ảnh:<em>&nbsp;Screenrant</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Những năm gần đ&acirc;y,&nbsp;<a href=\"https://vnexpress.net/chu-de/ana-de-armas-3822\" rel=\"dofollow\">Ana de Armas</a>&nbsp;nổi l&ecirc;n th&agrave;nh gương mặt triển vọng tại Hollywood. Diễn vi&ecirc;n Cuba g&acirc;y ch&uacute; &yacute; với vai phụ trong&nbsp;<em>Blade Runner 2049&nbsp;</em>(2017) trước khi nhận đề cử Quả Cầu V&agrave;ng cho&nbsp;<em>Knives Out&nbsp;</em>(2019). Năm 2021, Armas g&acirc;y ấn tượng với vai \"Bond girl\" trong&nbsp;<em><a href=\"https://vnexpress.net/chu-de/no-time-to-die-4185\" rel=\"dofollow\">No Time To Die</a></em>, phần phim thứ 25 của thương hiệu 007, t&aacute;c phẩm đạt doanh thu 774 triệu USD.<br><br><em>Blonde&nbsp;</em>dựa tr&ecirc;n tiểu thuyết c&ugrave;ng t&ecirc;n của nh&agrave; văn Joyce Carol Oates, lần đầu ph&aacute;t h&agrave;nh năm 2000. Nội dung s&aacute;ch v&agrave; phim đều thuộc thể loại giả tưởng, lấy cảm hứng từ cuộc đời minh tinh Marilyn Monroe thay v&igrave; đi theo hướng t&aacute;c phẩm tiểu sử. Phim dự kiến ra mắt v&agrave;o th&aacute;ng 9, được dự đo&aacute;n l&agrave; ứng vi&ecirc;n cho m&ugrave;a giải thưởng sắp tới.</p>\r\n<p class=\"Normal\"><a href=\"https://vnexpress.net/giai-tri/phim/thu-vien-phim/the-mystery-of-marilyn-monroe-the-unheard-tapes-438\" rel=\"dofollow\">Marilyn Monroe</a>&nbsp;sinh năm 1926 tại Mỹ, l&agrave; diễn vi&ecirc;n, ca sĩ, biểu tượng nhan sắc mọi thời. C&ocirc; nổi tiếng với c&aacute;c phim&nbsp;<em>All About Eve, Monkey Business, Gentlemen Prefer Blondes, How to Marry a Millionaire, The Seven Year Itch</em>... Năm 1999, Viện phim Mỹ xếp Monroe ở vị tr&iacute; thứ s&aacute;u trong danh s&aacute;ch Những minh tinh m&agrave;n ảnh vĩ đại nhất Hollywood thời kỳ ho&agrave;ng kim (thập ni&ecirc;n 1940-1960). Marilyn Monroe đột ngột qua đời rạng s&aacute;ng 5/8/1962 trong biệt thự ở California (Mỹ), b&ecirc;n cạnh nhiều chai thuốc ngủ rỗng. C&aacute;i chết của c&ocirc; vẫn l&agrave; điều b&iacute; ẩn đến ng&agrave;y nay.</p>\r\n</div>\r\n</article>', 218, 8, 0, 1, '2022-11-18 19:35:42', '2023-02-06 20:34:01'),
(130, 'Nhật Bản thắng đậm Australia để giành HC đồng U23 châu Á', 'Nhat-Ban-thang-dam-Australia-de-gianh-HC-dong-u23-chau-A', 'UZBEKISTAN-Nhật Bản giành HC đồng U23 châu Á, sau khi thắng Australia 3-0 ở trận tranh giải ba hôm 18/6.', '<p class=\"description\"><span class=\"location-stamp\">UZBEKISTAN</span>Nhật Bản gi&agrave;nh HC đồng U23 ch&acirc;u &Aacute;, sau khi thắng Australia 3-0 ở trận tranh giải ba h&ocirc;m 18/6.</p>\r\n<article class=\"fck_detail \">\r\n<p class=\"Normal\"><em>*B&agrave;n thắng: Sato 7\', Trewin 39\' (phản lưới), Fujio 63\'.</em></p>\r\n<p class=\"Normal\">Australia chuyền nhiều hơn, với thời lượng giữ b&oacute;ng 55%. Nhưng cứ đến gần cấm địa, những đường chuyền của họ lại thiếu ch&iacute;nh x&aacute;c. C&ograve;n Nhật Bản d&ugrave; chỉ đem lứa U21 tham dự, cho thấy khả năng dứt điểm tốt hơn hẳn. Đ&oacute; l&agrave; ch&igrave;a kho&aacute; gi&uacute;p Nhật Bản thắng lớn để l&agrave;m qu&agrave; rời giải.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture data-inimage=\"done\"><source srcset=\"https://i1-thethao.vnecdn.net/2022/06/18/nha-t-ba-n-jpeg-1655563529-3908-1655563676.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=g5worjy8I9b3YKiNtJiYpA 1x, https://i1-thethao.vnecdn.net/2022/06/18/nha-t-ba-n-jpeg-1655563529-3908-1655563676.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=myMR5lFIos3hn4evMCHfDA 1.5x, https://i1-thethao.vnecdn.net/2022/06/18/nha-t-ba-n-jpeg-1655563529-3908-1655563676.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=K_4uYK69ZHy9knG3X8K8KA 2x\" data-srcset=\"https://i1-thethao.vnecdn.net/2022/06/18/nha-t-ba-n-jpeg-1655563529-3908-1655563676.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=g5worjy8I9b3YKiNtJiYpA 1x, https://i1-thethao.vnecdn.net/2022/06/18/nha-t-ba-n-jpeg-1655563529-3908-1655563676.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=myMR5lFIos3hn4evMCHfDA 1.5x, https://i1-thethao.vnecdn.net/2022/06/18/nha-t-ba-n-jpeg-1655563529-3908-1655563676.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=K_4uYK69ZHy9knG3X8K8KA 2x\"><img class=\"lazy lazied\" src=\"https://i1-thethao.vnecdn.net/2022/06/18/nha-t-ba-n-jpeg-1655563529-3908-1655563676.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=g5worjy8I9b3YKiNtJiYpA\" alt=\"Cầu thủ Nhật Bản mừng b&agrave;n mở tỷ số của Kein Sato, tr&ecirc;n s&acirc;n Pakhtakor, Tashkent, Uzbekistan h&ocirc;m 18/6. Ảnh: AFC\" loading=\"lazy\" data-src=\"https://i1-thethao.vnecdn.net/2022/06/18/nha-t-ba-n-jpeg-1655563529-3908-1655563676.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=g5worjy8I9b3YKiNtJiYpA\" data-ll-status=\"loaded\" data-adbro-processed=\"true\"></picture>\r\n<div class=\"embed-container-ads\">\r\n<div id=\"sis_inimage\" data-google-query-id=\"CO_1wpuAufgCFXbKFgUdKt4EtQ\">\r\n<div id=\"google_ads_iframe_/27973503/Vnexpress/Desktop/Inimage/Thethao/Thethao.affcup.detail_0__container__\"><iframe id=\"google_ads_iframe_/27973503/Vnexpress/Desktop/Inimage/Thethao/Thethao.affcup.detail_0\" tabindex=\"0\" title=\"3rd party ad content\" role=\"region\" name=\"google_ads_iframe_/27973503/Vnexpress/Desktop/Inimage/Thethao/Thethao.affcup.detail_0\" width=\"1\" height=\"1\" frameborder=\"0\" marginwidth=\"0\" marginheight=\"0\" scrolling=\"no\" aria-label=\"Advertisement\" data-google-container-id=\"6\" data-load-complete=\"true\"></iframe></div>\r\n</div>\r\n</div>\r\n</div>\r\n<figcaption>\r\n<p class=\"Image\">Cầu thủ Nhật Bản mừng b&agrave;n mở tỷ số của Kein Sato, tr&ecirc;n s&acirc;n Pakhtakor, Tashkent, Uzbekistan h&ocirc;m 18/6. Ảnh:&nbsp;<em>AFC</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Ngay ph&uacute;t thứ 7, Nhật Bản đ&atilde; ghi b&agrave;n từ c&uacute; đ&aacute;nh đầu ph&aacute; b&oacute;ng kh&ocirc;ng tốt của trung vệ Australia. B&oacute;ng tới ch&acirc;n Kein Sato trong cấm địa, chếch b&ecirc;n tr&aacute;i. Anh gạt b&oacute;ng xuống ch&acirc;n tr&aacute;i rồi s&uacute;t căng ở g&oacute;c gần tr&uacute;ng m&eacute;p dưới x&agrave; ngang bay v&agrave;o lưới. Thủ m&ocirc;n Nicholas Bilokapic kh&ocirc;ng c&oacute; cơ hội n&agrave;o cản ph&aacute;.</p>\r\n<p class=\"Normal\">Nhật Bản tiếp tục lấn lướt v&agrave; su&yacute;t ghi th&ecirc;m b&agrave;n ở ph&uacute;t 24. Tiền vệ Shunsuke Mito gẩy b&oacute;ng qua trung vệ Australia rồi đối mặt thủ m&ocirc;n. Anh s&uacute;t căng bằng ch&acirc;n tr&aacute;i đưa b&oacute;ng đi liệng tr&uacute;ng x&agrave; ngang, nhưng lần n&agrave;y bật ra ngo&agrave;i.</p>\r\n<p class=\"Normal\">Nhưng thầy tr&ograve; Go Oiwa kh&ocirc;ng phải chờ l&acirc;u để mừng b&agrave;n thứ hai. Từ quả phạt g&oacute;c b&ecirc;n phải của Nhật Bản, hậu vệ Kai Trewin l&uacute;ng t&uacute;ng đ&aacute; phản lưới. Australia chỉ một lần dứt điểm về cầu m&ocirc;n Nhật Bản trận n&agrave;y, v&agrave; một lần dứt điểm về lưới nh&agrave;.</p>\r\n<p class=\"Normal\">Với lợi thế hai b&agrave;n dẫn, Nhật Bản nhường thế trận cho Australia trong hiệp hai. Nhưng thầy tr&ograve; Trevor Morgan kh&ocirc;ng những tịt ng&ograve;i, c&ograve;n để đối thủ ghi b&agrave;n thứ ba. Kuryu Matsuki x&ocirc;ng v&agrave;o cấm địa nhận đường chọc khe của đồng đội, trước khi căng ngang cho Shota Fujio đệm b&oacute;ng v&agrave;o lưới ấn định thắng lợi 3-0. Một cầu thủ Australia bực tức đến nỗi đ&aacute; mạnh tr&aacute;i b&oacute;ng đi.</p>\r\n<div>\r\n<div>\r\n<div id=\"video_parent_357160\" class=\"box_embed_video_parent embed_video_new\" data-vcate=\"1003834\" data-vscate=\"1003008\" data-vscaten=\"Thể thao\" data-vid=\"357160\" data-ratio=\"1\" data-articleoriginal=\"4477723\" data-ads=\"1\" data-license=\"0\" data-duration=\"183\" data-brandsafe=\"0\" data-type=\"0\" data-play=\"1\" data-frame=\"\" data-aot=\"\" data-videoclassify=\"None\" data-initdom=\"1\" data-view=\"inview\" data-auto=\"1\" data-status=\"play\">\r\n<div id=\"embed_video_357160\" class=\"box_embed_video\">\r\n<div class=\"bg_overlay_small_unpin\">&nbsp;</div>\r\n<div class=\"bg_overlay_small\">&nbsp;</div>\r\n<div class=\"embed-container\">\r\n<div id=\"video-357160\">\r\n<div id=\"parser_player_357160\" class=\"media_content\">\r\n<div id=\"videoContainter_357160\" class=\"videoContainter\">\r\n<div id=\"media-video-357160\" class=\"video-js vjs-default-skin vjs-controls-enabled vjs-workinghover vjs-v7 media-video-357160-dimensions vjs-playing vjs-has-started vjs-user-inactive\" lang=\"vi\" role=\"region\" data-ex=\"st=0&amp;bs=0&amp;pt=0\" data-subtitle=\"0\" data-mode=\"240|360|480|720\" aria-label=\"Video Player\">\r\n<div class=\"vjs-text-track-display\" aria-live=\"off\" aria-atomic=\"true\">&nbsp;</div>\r\n<div class=\"vjs-loading-spinner\" dir=\"ltr\">&nbsp;</div>\r\n<div id=\"357160_soundTopRight\" class=\"fp-sound-status-container\"></div>\r\n<div class=\"share-hover\">&nbsp;</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n<p class=\"Normal\">Đ&acirc;y l&agrave; lần thứ hai Nhật Bản đoạt huy chương U23 ch&acirc;u &Aacute;, sau chức v&ocirc; địch năm 2016. C&ograve;n Australia đ&aacute;nh mất cơ hội gi&agrave;nh HC đồng kỳ thứ hai li&ecirc;n tiếp.</p>\r\n</article>', 218, 7, 1, 1, '2022-11-18 19:35:42', '2022-11-22 05:53:16');
INSERT INTO `posts` (`id`, `title`, `slug`, `excerpt`, `body`, `user_id`, `category_id`, `views`, `approved`, `created_at`, `updated_at`) VALUES
(132, 'Bảng giá điều hoà Panasonic, giảm tới 1,6 triệu', 'bang-gia-dieu-hoa-panasonic-giam-toi-16-trieu', 'Trong tháng 6, nhiều máy lạnh/ điều hoà Panasonic đang được đồng giảm giá 1,3 triệu đồng, đi kèm nhiều ưu đãi khi mua online.', '<p>Thị trường điều ho&agrave;, m&aacute;y lạnh tại Việt Nam kh&aacute; đa dạng với nhiều thương hiệu nổi tiếng: LG,&nbsp;<a class=\"TextlinkBaiviet\" title=\"Samsung\" href=\"https://www.24h.com.vn/samsung-c407e3988.html\">Samsung</a>, Casper, Daikin,&hellip;ngo&agrave;i ra c&ograve;n c&oacute; Panasonic. Đ&acirc;y l&agrave; thương hiệu m&aacute;y lạnh đến từ Nhật Bản.</p>\r\n<p align=\"center\">&nbsp;</p>\r\n<div id=\"container-24h-banner-in-image\">\r\n<div>\r\n<div><img class=\"news-image initial loading\" src=\"https://icdn.24h.com.vn/upload/2-2022/images/2022-06-16/casper-1655365624-735-width740height494.jpg\" alt=\"Bảng gi&aacute; điều ho&agrave; Panasonic, giảm tới 1,6 triệu - 1\" data-was-processed=\"true\"></div>\r\n<div id=\"24h-banner-in-image\">\r\n<div id=\"OBpBKkAmjb_139_15s\" class=\"txtCent\">\r\n<div class=\"text_adver_right\">&nbsp;</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n<p>&nbsp;</p>\r\n<p class=\"img_chu_thich_0407\">Ảnh minh hoạ.</p>\r\n<p>Đa số c&aacute;c sản phẩm m&aacute;y lạnh Panasonic c&oacute; ph&acirc;n kh&uacute;c gi&aacute; tầm trung, từ 8&nbsp;&ndash; 15 triệu đồng, ph&ugrave; hợp cho nhiều hộ gia đ&igrave;nh. Th&ecirc;m v&agrave;o đ&oacute;, ch&uacute;ng cũng được t&iacute;ch hợp c&ocirc;ng nghệ Inverter như nhiều thương hiệu kh&aacute;c, gi&uacute;p tiết kiệm điện khi sử dụng trong thời gian d&agrave;i.</p>\r\n<p align=\"center\"><img class=\"news-image loaded\" src=\"https://icdn.24h.com.vn/upload/2-2022/images/2022-06-16/Cap-nhat-bang-gia-dieu-hoa-Panasonic-chi-tu-62-trieu-giam-kich-toi-16-trieu-untitled-1655366237-618-width740height283.jpg\" alt=\"Bảng gi&aacute; điều ho&agrave; Panasonic, giảm tới 1,6 triệu - 3\" data-original=\"https://icdn.24h.com.vn/upload/2-2022/images/2022-06-16/Cap-nhat-bang-gia-dieu-hoa-Panasonic-chi-tu-62-trieu-giam-kich-toi-16-trieu-untitled-1655366237-618-width740height283.jpg\" data-was-processed=\"true\"></p>\r\n<p class=\"img_chu_thich_0407\">M&aacute;y lạnh Panasonic 1 HP CU/CS-N9WKH-8M.</p>\r\n<p>Khi mua m&aacute;y lạnh/ điều ho&agrave; Panasonic trong th&aacute;ng n&agrave;y, người d&ugrave;ng sẽ được nhận c&aacute;c g&oacute;i ưu đ&atilde;i lắp đặt trị gi&aacute; 1,3 triệu đồng. Thiết bị c&oacute; gi&aacute; thấp nhất của h&atilde;ng l&agrave; M&aacute;y lạnh Panasonic 1 HP CU/CS-N9WKH-8M, c&oacute; gi&aacute; chỉ từ 8,54 triệu đồng. M&aacute;y c&oacute; hệ thống l&agrave;m m&aacute;t 1 chiều, c&oacute; phạm vi l&agrave;m m&aacute;t dưới 15 m&eacute;t vu&ocirc;ng, ph&ugrave; hợp cho c&aacute;c gia đ&igrave;nh nhỏ, ph&ugrave; hợp lắp đặt trong ph&ograve;ng ngủ.</p>\r\n<p><strong>Bảng gi&aacute; điều ho&agrave;/ m&aacute;y lạnh&nbsp;Panasonic th&aacute;ng 6:</strong></p>\r\n<table class=\"tblCustom\" border=\"1\" cellspacing=\"1\" cellpadding=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><strong>T&ecirc;n sản phẩm</strong></td>\r\n<td><strong>Phạm vi l&agrave;m m&aacute;t</strong></td>\r\n<td>\r\n<p><strong>Gi&aacute; ni&ecirc;m yết (VNĐ)</strong></p>\r\n<p><strong>Bao gồm lắp đặt</strong></p>\r\n</td>\r\n<td><strong>Gi&aacute; ưu đ&atilde;i (VNĐ)</strong></td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>M&aacute;y lạnh Panasonic 1 HP CU/CS-N9WKH-8M</strong></p>\r\n</td>\r\n<td>\r\n<p>Dưới 15m&sup2;</p>\r\n</td>\r\n<td>\r\n<p>9.190.000</p>\r\n</td>\r\n<td>8.540.000</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>M&aacute;y lạnh Panasonic Inverter 1 HP CU/CS-PU9XKH-8M</strong></p>\r\n</td>\r\n<td>\r\n<p>Dưới 15m&sup2;</p>\r\n</td>\r\n<td>\r\n<p>10.990.000</p>\r\n</td>\r\n<td>10.390.000</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>M&aacute;y lạnh Panasonic 1.5 HP CU/CS-N12WKH-8M</strong></p>\r\n</td>\r\n<td>\r\n<p>Từ 15 - 20m&sup2;</p>\r\n</td>\r\n<td>\r\n<p>11.590.000</p>\r\n</td>\r\n<td>10.940.000</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>M&aacute;y lạnh Panasonic Inverter 1 HP CU/CS-WPU9XKH-8</strong></p>\r\n</td>\r\n<td>\r\n<p>Dưới 15m&sup2;</p>\r\n</td>\r\n<td>\r\n<p>11.990.000</p>\r\n</td>\r\n<td>11.340.000</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>M&aacute;y lạnh Panasonic Inverter 1 HP CU/CS-XU9UKH-8</strong></p>\r\n</td>\r\n<td>\r\n<p>Dưới 15m&sup2;&nbsp;</p>\r\n</td>\r\n<td>\r\n<p>12.190.000</p>\r\n</td>\r\n<td>11.540.000</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>M&aacute;y lạnh Panasonic Inverter 1.5 HP CU/CS-PU12WKH-8M</strong></p>\r\n</td>\r\n<td>\r\n<p>Từ 15 - 20m&sup2;</p>\r\n</td>\r\n<td>\r\n<p>12.890.000</p>\r\n</td>\r\n<td>12.240.000</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>M&aacute;y lạnh Panasonic Inverter 1 HP CU/CS-XU9XKH-8</strong></p>\r\n</td>\r\n<td>\r\n<p>Dưới 15m&sup2;</p>\r\n</td>\r\n<td>\r\n<p>12.990.000</p>\r\n</td>\r\n<td>12.340.000</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>M&aacute;y lạnh 2 chiều Panasonic Inverter 1 HP CU/CS-YZ9WKH-8</strong></p>\r\n</td>\r\n<td>\r\n<p>Dưới 15m&sup2;</p>\r\n</td>\r\n<td>\r\n<p>12.990.000</p>\r\n</td>\r\n<td>\r\n<p>12.340.000</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>M&aacute;y lạnh Panasonic Inverter 1.5 HP CU/CS-PU12XKH-8M</strong></p>\r\n</td>\r\n<td>\r\n<p>Từ 15 - 20m&sup2;</p>\r\n</td>\r\n<td>\r\n<p>13.490.000</p>\r\n</td>\r\n<td>12.840.000</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>M&aacute;y lạnh Panasonic Inverter 1.5 HP CU/CS-WPU12XKH-8</strong></p>\r\n</td>\r\n<td>\r\n<p>Từ 15 - 20m&sup2;</p>\r\n</td>\r\n<td>\r\n<p>14.490.000</p>\r\n<p>đặt trước</p>\r\n</td>\r\n<td>-</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>M&aacute;y lạnh Panasonic Inverter 1.5 HP CU/CS-XU12UKH-8</strong></p>\r\n</td>\r\n<td>\r\n<p>Từ 15 - 20m&sup2;</p>\r\n</td>\r\n<td>\r\n<p>14.890.000</p>\r\n</td>\r\n<td>14.240.000</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>M&aacute;y lạnh 2 chiều Panasonic Inverter 1.5 HP CU/CS-YZ12WKH-8</strong></p>\r\n</td>\r\n<td>\r\n<p>Từ 15 - 20m&sup2;</p>\r\n</td>\r\n<td>\r\n<p>15.890.000</p>\r\n</td>\r\n<td>15.240.000</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>M&aacute;y lạnh Panasonic Inverter 1.5 HP CU/CS-XU12XKH-8</strong></p>\r\n</td>\r\n<td>\r\n<p>Từ 15 - 20m&sup2;</p>\r\n</td>\r\n<td>\r\n<p>15.990.000</p>\r\n</td>\r\n<td>15.340.000</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>M&aacute;y lạnh Panasonic Inverter 1 HP CU/CS-VU9UKH-8</strong></p>\r\n</td>\r\n<td>\r\n<p>Dưới 15m&sup2;</p>\r\n</td>\r\n<td>\r\n<p>17.490.000</p>\r\n</td>\r\n<td>16.840.000</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>M&aacute;y lạnh Panasonic Inverter 2 HP CU/CS-PU18WKH-8M</strong></p>\r\n</td>\r\n<td>\r\n<p>Từ 20 - 30m&sup2;</p>\r\n</td>\r\n<td>\r\n<p>19.390.000</p>\r\n</td>\r\n<td>18.740.000</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>M&aacute;y lạnh Panasonic Inverter 2 HP CU/CS-PU18XKH-8M</strong></p>\r\n</td>\r\n<td>\r\n<p>Từ 20 - 30m&sup2;</p>\r\n</td>\r\n<td>\r\n<p>19.990.000</p>\r\n</td>\r\n<td>19.340.000</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>M&aacute;y lạnh Panasonic Inverter 1.5 HP CU/CS-VU12UKH-8</strong></p>\r\n</td>\r\n<td>\r\n<p>Từ 15 - 20m&sup2;</p>\r\n</td>\r\n<td>\r\n<p>21.090.000</p>\r\n</td>\r\n<td>20.440.000</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>M&aacute;y lạnh Panasonic Inverter 2 HP CU/CS-WPU18XKH-8</strong></p>\r\n</td>\r\n<td>\r\n<p>Từ 20&nbsp;- 30m&sup2;</p>\r\n</td>\r\n<td>\r\n<p>21.490.000</p>\r\n<p>đặt trước</p>\r\n</td>\r\n<td>-</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>M&aacute;y lạnh Panasonic Inverter 2 HP CU/CS-XU18XKH-8</strong></p>\r\n</td>\r\n<td>\r\n<p>Từ 20 - 30m&sup2;</p>\r\n</td>\r\n<td>\r\n<p>23.990.000</p>\r\n</td>\r\n<td>23.340.000</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>M&aacute;y lạnh 2 chiều Panasonic 2 HP CU/CS-YZ18XKH-8</strong></p>\r\n</td>\r\n<td>\r\n<p>Từ 20 - 30m&sup2;</p>\r\n</td>\r\n<td>\r\n<p>24.290.000</p>\r\n</td>\r\n<td>23.640.000</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>M&aacute;y lạnh 2 chiều Panasonic 2 HP CU/CS-XZ18XKH-8</strong></p>\r\n</td>\r\n<td>\r\n<p>Từ 20 - 30m&sup2;</p>\r\n</td>\r\n<td>\r\n<p>27.590.000</p>\r\n</td>\r\n<td>26.940.000</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>M&aacute;y lạnh Panasonic Inverter 2 HP CU/CS-VU18UKH-8</strong></p>\r\n</td>\r\n<td>\r\n<p>Từ 20 - 30m&sup2;</p>\r\n</td>\r\n<td>\r\n<p>32.090.000</p>\r\n</td>\r\n<td>31.440.000</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>M&aacute;y lạnh Panasonic Inverter 2.5 HP CU/CS-XU24XKH-8</strong></p>\r\n</td>\r\n<td>\r\n<p>Từ 30 - 40m&sup2;&nbsp;</p>\r\n</td>\r\n<td>\r\n<p>32.990.000</p>\r\n</td>\r\n<td>32.340.000</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p><strong>M&aacute;y lạnh 2 chiều Panasonic 2.5 HP CU/CS-XZ24XKH-8</strong></p>\r\n</td>\r\n<td>\r\n<p>Từ 30 - 40m&sup2;</p>\r\n</td>\r\n<td>\r\n<p>37.890.000</p>\r\n<p>đặt trước</p>\r\n</td>\r\n<td>-</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><u>*Lưu &yacute;:&nbsp;</u>Bảng gi&aacute; được cập nhật v&agrave;o ng&agrave;y 16/6, tham khảo tại Điện M&aacute;y Xanh.</p>\r\n<p>K&yacute; tự \"-\" l&agrave; kh&ocirc;ng giảm gi&aacute;.</p>', 218, 10, 1, 1, '2022-11-18 19:35:42', '2022-11-22 06:12:09'),
(133, 'Thí sinh chống nạng đi thi', 'thi-sinh-chong-nang-di-thi', 'HÀ NỘI-Bị tai nạn dẫn đến gãy chân ngay trước kỳ tuyển sinh lớp 10, một số thí sinh chống nạng đến trường thi.', '<p class=\"description\"><span class=\"location-stamp\">H&Agrave; NỘI</span>Bị tai nạn dẫn đến g&atilde;y ch&acirc;n ngay trước kỳ tuyển sinh lớp 10, một số th&iacute; sinh chống nạng đến trường thi.</p>\r\n<article class=\"fck_detail \">\r\n<p class=\"Normal\">S&aacute;ng 19/6, L&ecirc; Thanh Mai, cựu học sinh lớp 9A1, THCS Ngọc Thụy (quận Long Bi&ecirc;n), được mẹ chở bằng xe m&aacute;y đến điểm thi ở trường THCS Gia Thụy. Ngo&agrave;i vật dụng như b&uacute;t, thước kẻ, m&aacute;y t&iacute;nh, giấy dự thi, em mang theo đ&ocirc;i nạng. Nữ sinh mới bị g&atilde;y ch&acirc;n tr&aacute;i sau vụ tai nạn v&agrave;o tuần trước.</p>\r\n<p class=\"Normal\">Vừa xuống xe, Mai được c&aacute;c t&igrave;nh nguyện vi&ecirc;n d&igrave;u l&ecirc;n tận ph&ograve;ng thi. \"Em gặp đ&ocirc;i ch&uacute;t kh&oacute; khăn khi di chuyển v&agrave; cũng hoang mang hơn khi gặp tai nạn ngay trước kỳ thi quan trọng. Tuy nhi&ecirc;n, được bạn b&egrave;, thầy c&ocirc; v&agrave; c&aacute;c anh chị t&igrave;nh nguyện vi&ecirc;n hết l&ograve;ng quan t&acirc;m, gi&uacute;p đỡ, em c&oacute; động lực hơn để ho&agrave;n th&agrave;nh kỳ thi\", Mai n&oacute;i.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture data-inimage=\"done\"><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/19/thi-sinh-1-jpeg-1655614563-4311-1655615706.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=OK51xud6kjWlJgkoi7TZuA 1x, https://i1-vnexpress.vnecdn.net/2022/06/19/thi-sinh-1-jpeg-1655614563-4311-1655615706.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=A8nrpXbVl0Dy9Uc-WAouog 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/19/thi-sinh-1-jpeg-1655614563-4311-1655615706.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=JqHoInTUUVQSzmnmkmio2g 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/19/thi-sinh-1-jpeg-1655614563-4311-1655615706.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=OK51xud6kjWlJgkoi7TZuA 1x, https://i1-vnexpress.vnecdn.net/2022/06/19/thi-sinh-1-jpeg-1655614563-4311-1655615706.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=A8nrpXbVl0Dy9Uc-WAouog 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/19/thi-sinh-1-jpeg-1655614563-4311-1655615706.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=JqHoInTUUVQSzmnmkmio2g 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/06/19/thi-sinh-1-jpeg-1655614563-4311-1655615706.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=OK51xud6kjWlJgkoi7TZuA\" alt=\"Thanh Mai đến điểm thi s&aacute;ng 19/6. Ảnh: Giang Huy\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/06/19/thi-sinh-1-jpeg-1655614563-4311-1655615706.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=OK51xud6kjWlJgkoi7TZuA\" data-ll-status=\"loaded\" data-adbro-processed=\"true\"></picture>\r\n<div class=\"embed-container-ads\">\r\n<div id=\"sis_inimage\" data-google-query-id=\"CMf8vab9uPgCFQxelgodb-IBvQ\">\r\n<div id=\"google_ads_iframe_/27973503/Vnexpress/Desktop/Inimage/Giaoduc/Giaoduc.tuyensinh.detail_0__container__\"><iframe id=\"google_ads_iframe_/27973503/Vnexpress/Desktop/Inimage/Giaoduc/Giaoduc.tuyensinh.detail_0\" tabindex=\"0\" title=\"3rd party ad content\" role=\"region\" name=\"google_ads_iframe_/27973503/Vnexpress/Desktop/Inimage/Giaoduc/Giaoduc.tuyensinh.detail_0\" width=\"1\" height=\"1\" frameborder=\"0\" marginwidth=\"0\" marginheight=\"0\" scrolling=\"no\" aria-label=\"Advertisement\" data-google-container-id=\"3\" data-load-complete=\"true\"></iframe></div>\r\n</div>\r\n</div>\r\n</div>\r\n<figcaption>\r\n<p class=\"Image\">Thanh Mai đến điểm thi s&aacute;ng 19/6. Ảnh:&nbsp;<em>Giang Huy</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">C&ugrave;ng điểm thi với Mai, Trương Ngọc Minh, cựu học sinh THCS Ngọc L&acirc;m, cũng đi nạng v&agrave; ch&acirc;n tr&aacute;i c&ograve;n b&oacute; bột. Di chuyển vất vả nhưng Minh được an ủi phần n&agrave;o khi gia đ&igrave;nh, c&aacute;c t&igrave;nh nguyện vi&ecirc;n tại điểm thi gi&uacute;p đỡ nhiệt t&igrave;nh. Minh tự tin ho&agrave;n th&agrave;nh c&aacute;c b&agrave;i thi, đặc biệt l&agrave; To&aacute;n, m&ocirc;n tủ của em.</p>\r\n<p class=\"Normal\">Một số điểm thi kh&aacute;c cũng c&oacute; những th&iacute; sinh rơi v&agrave;o trường hợp tương tự. Tại điểm thi THCS T&acirc;n Mai (quận Ho&agrave;ng Mai), một nữ sinh b&oacute; bột ch&acirc;n phải cũng nhờ đến sự hỗ trợ. Điểm thi THPT Đan Phượng c&oacute; một nam sinh g&atilde;y ch&acirc;n, phải chống nạng hơn hai th&aacute;ng nay, vẫn quyết t&acirc;m ho&agrave;n th&agrave;nh kỳ thi.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/19/thi-sinh-2-jpeg-1655614672-3968-1655615706.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=5aZcmIiHBi3tZGGDTralCQ 1x, https://i1-vnexpress.vnecdn.net/2022/06/19/thi-sinh-2-jpeg-1655614672-3968-1655615706.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=NfjEK7KdP-_NGk__U7WLzQ 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/19/thi-sinh-2-jpeg-1655614672-3968-1655615706.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=XOzvSNFsgeBo0WthYWOtWg 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/19/thi-sinh-2-jpeg-1655614672-3968-1655615706.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=5aZcmIiHBi3tZGGDTralCQ 1x, https://i1-vnexpress.vnecdn.net/2022/06/19/thi-sinh-2-jpeg-1655614672-3968-1655615706.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=NfjEK7KdP-_NGk__U7WLzQ 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/19/thi-sinh-2-jpeg-1655614672-3968-1655615706.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=XOzvSNFsgeBo0WthYWOtWg 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/06/19/thi-sinh-2-jpeg-1655614672-3968-1655615706.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=5aZcmIiHBi3tZGGDTralCQ\" alt=\"Trương Ngọc Minh được t&igrave;nh nguyện vi&ecirc;n hỗ trợ l&ecirc;n ph&ograve;ng thi. Ảnh: Giang Huy\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/06/19/thi-sinh-2-jpeg-1655614672-3968-1655615706.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=5aZcmIiHBi3tZGGDTralCQ\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Trương Ngọc Minh được t&igrave;nh nguyện vi&ecirc;n hỗ trợ l&ecirc;n ph&ograve;ng thi. Ảnh:&nbsp;<em>Giang Huy</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">To&agrave;n th&agrave;nh phố c&oacute; 186 th&iacute; sinh đang mắc Covid-19, thuộc diện kh&ocirc;ng may bị ốm đau ở thời điểm kỳ thi diễn ra. Theo quy định, nếu c&oacute; giấy x&aacute;c nhận của cơ sở y tế hoặc của địa phương, nh&oacute;m n&agrave;y được x&eacute;t tuyển v&agrave;o c&aacute;c trường THPT c&ocirc;ng lập kh&ocirc;ng chuy&ecirc;n bằng học bạ THCS. Tuy nhi&ecirc;n, nếu đủ sức khoẻ v&agrave; c&oacute; nguyện vọng, c&aacute;c em c&oacute; thể l&agrave;m đơn xin dự thi. C&aacute;c điểm thi sẽ bố tr&iacute; ph&ograve;ng ri&ecirc;ng, đảm bảo an to&agrave;n ph&ograve;ng, chống dịch.</p>\r\n<p class=\"Normal\">&Ocirc;ng Phạm Quốc Toản, Trưởng ph&ograve;ng Quản l&yacute; thi v&agrave; Kiểm định chất lượng gi&aacute;o dục (Sở Gi&aacute;o dục v&agrave; Đ&agrave;o tạo H&agrave; Nội) cho biết, trong 186 th&iacute; sinh mắc Covid-19, 41 em đăng k&yacute; dự thi tại 37 ph&ograve;ng thi ri&ecirc;ng ở c&aacute;c điểm thi, 145 em c&ograve;n lại c&oacute; nguyện vọng x&eacute;t tuyển theo điểm học bạ.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/19/thi-sinh-F0-5758-1655615706.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=JkW7aQUo4R7eTy5tRhGzhg 1x, https://i1-vnexpress.vnecdn.net/2022/06/19/thi-sinh-F0-5758-1655615706.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=guZpxpS8uxx9ys9ttVwCsg 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/19/thi-sinh-F0-5758-1655615706.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=6N-Yqbzn1hls_FDN9VXL1w 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/19/thi-sinh-F0-5758-1655615706.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=JkW7aQUo4R7eTy5tRhGzhg 1x, https://i1-vnexpress.vnecdn.net/2022/06/19/thi-sinh-F0-5758-1655615706.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=guZpxpS8uxx9ys9ttVwCsg 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/19/thi-sinh-F0-5758-1655615706.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=6N-Yqbzn1hls_FDN9VXL1w 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/06/19/thi-sinh-F0-5758-1655615706.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=JkW7aQUo4R7eTy5tRhGzhg\" alt=\"Hai th&iacute; sinh diện F0 thi tại ph&ograve;ng dự ph&ograve;ng ở điểm thi THCS T&acirc;n Mai, quận Ho&agrave;ng Mai. Ảnh: Thanh Thanh\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/06/19/thi-sinh-F0-5758-1655615706.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=JkW7aQUo4R7eTy5tRhGzhg\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Hai th&iacute; sinh diện F0 thi tại ph&ograve;ng dự ph&ograve;ng ở điểm thi THCS T&acirc;n Mai, quận Ho&agrave;ng Mai. Ảnh:&nbsp;<em>Thanh Thanh</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Kỳ thi tuyển sinh v&agrave;o lớp 10 THPT c&ocirc;ng lập của H&agrave; Nội diễn ra trong hai ng&agrave;y 18-19/6 với sự tham dự của gần 107.000 th&iacute; sinh tại hơn 4.550 ph&ograve;ng thi ở 203 điểm thi.</p>\r\n<p class=\"Normal\">Trong buổi thi m&ocirc;n Ngữ văn s&aacute;ng 18/6, c&oacute; 981 em vắng mặt. Với m&ocirc;n Ngoại ngữ chiều 18/6, số vắng mặt l&agrave; 994. Nh&oacute;m n&agrave;y kh&ocirc;ng đủ điều kiện x&eacute;t tuyển do kh&ocirc;ng dự thi đủ ba m&ocirc;n To&aacute;n, Ngữ văn, Ngoại ngữ. Hiện Sở Gi&aacute;o dục v&agrave; Đ&agrave;o tạo chưa c&ocirc;ng bố thống k&ecirc; chung cho to&agrave;n kỳ thi</p>\r\n<p class=\"Normal\">Ng&agrave;y 20/6, những th&iacute; sinh c&oacute; nguyện vọng v&agrave;o c&aacute;c trường THPT chuy&ecirc;n H&agrave; Nội - Amsterdam, Nguyễn Huệ hay lớp chuy&ecirc;n của THPT Chu Văn An, Sơn T&acirc;y sẽ l&agrave;m th&ecirc;m b&agrave;i thi m&ocirc;n chuy&ecirc;n.</p>\r\n<p class=\"Normal\">Năm học 2021-2022, H&agrave; Nội c&oacute; khoảng 129.000 học sinh x&eacute;t tốt nghiệp THCS nhưng chỉ gần 107.000 em đăng k&yacute; thi v&agrave;o lớp 10. Trong số n&agrave;y, khoảng 64,7% sẽ tr&uacute;ng tuyển v&agrave;o c&aacute;c trường THPT c&ocirc;ng lập. Số c&ograve;n lại học tại c&aacute;c trường tư thục hoặc cơ sở gi&aacute;o dục nghề nghiệp - gi&aacute;o dục thường xuy&ecirc;n.</p>\r\n</article>', 218, 6, 3, 1, '2022-11-21 19:35:42', '2022-11-22 05:45:32'),
(135, 'Ba công nhân tử vong khi chữa cháy', 'ba-cong-nhan-tu-vong-hi-chua-chay', 'BÌNH DƯƠNG-Sau khi đám cháy được khống chế, lửa còn âm ỉ, nhiều công nhân vào hiện trường dọn dẹp, trần gỗ đổ sập khiến ba người tử vong, một bị thương.', '<p class=\"description\"><span class=\"location-stamp\">B&Igrave;NH DƯƠNG</span>Sau khi đ&aacute;m ch&aacute;y được khống chế, lửa c&ograve;n &acirc;m ỉ, nhiều c&ocirc;ng nh&acirc;n v&agrave;o hiện trường dọn dẹp, trần gỗ đổ sập khiến ba người tử vong, một bị thương.</p>\r\n<article class=\"fck_detail \">\r\n<p class=\"Normal\">Ng&agrave;y 19/6, C&ocirc;ng an B&igrave;nh Dương đang điều tra vụ tai nạn lao động xảy ra tại c&ocirc;ng ty C&ocirc;ng ty TNHH Scancom Việt Nam, ở Khu c&ocirc;ng nghiệp S&oacute;ng Thần 1, TP Dĩ An.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/19/z3503480132137-fb69f3faa85f3ae-7751-2491-1655610123.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=GaV3rAEc1DNR2EvMjL1ydA 1x, https://i1-vnexpress.vnecdn.net/2022/06/19/z3503480132137-fb69f3faa85f3ae-7751-2491-1655610123.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=YmphyQME5fWw-zte6Drpmg 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/19/z3503480132137-fb69f3faa85f3ae-7751-2491-1655610123.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=Jj0m9ldHfsQdKInyz9otZA 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/19/z3503480132137-fb69f3faa85f3ae-7751-2491-1655610123.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=GaV3rAEc1DNR2EvMjL1ydA 1x, https://i1-vnexpress.vnecdn.net/2022/06/19/z3503480132137-fb69f3faa85f3ae-7751-2491-1655610123.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=YmphyQME5fWw-zte6Drpmg 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/19/z3503480132137-fb69f3faa85f3ae-7751-2491-1655610123.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=Jj0m9ldHfsQdKInyz9otZA 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/06/19/z3503480132137-fb69f3faa85f3ae-7751-2491-1655610123.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=GaV3rAEc1DNR2EvMjL1ydA\" alt=\"Hiện trường vụ tai nạn lao động. Ảnh: Th&aacute;i h&agrave;\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/06/19/z3503480132137-fb69f3faa85f3ae-7751-2491-1655610123.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=GaV3rAEc1DNR2EvMjL1ydA\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Hiện trường vụ tai nạn lao động. Ảnh:&nbsp;<em>Th&aacute;i H&agrave;</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Chiều hai h&ocirc;m trước, đ&aacute;m ch&aacute;y xảy ra tại C&ocirc;ng ty TNHH Scancom Việt Nam. Khu vực hoả hoạn l&agrave; nơi chứa bụi gỗ, m&ugrave;n cưa. Cảnh s&aacute;t PCCC tỉnh điều động bốn xe chuy&ecirc;n dụng c&ugrave;ng nhiều chiến sĩ đến ứng cứu. Khi đ&aacute;m ch&aacute;y được dập tắt, lực lượng chức năng thu hồi phương tiện chuẩn bị ra về, nhiều c&ocirc;ng nh&acirc;n v&agrave;o hiện trường dọn dẹp.</p>\r\n<p class=\"Normal\">Khi c&ocirc;ng nh&acirc;n đang vệ sinh bồn h&uacute;t bụi, dập những chỗ m&ugrave;n cưa ch&aacute;y &acirc;m ỉ, bất ngờ bụi gỗ trong bồn chứa tr&ecirc;n cao xả ập xuống đ&egrave; l&ecirc;n bốn người. Họ được đưa cấp cứu tại Bệnh viện Qu&acirc;n đo&agrave;n 4 nằm gần khu c&ocirc;ng nghiệp. Ba người bị bỏng nặng sau đ&oacute; được chuyển tới Bệnh viện Chợ Rẫy (TP HCM), song hai người tử vong ng&agrave;y h&ocirc;m qua. Nạn nh&acirc;n c&ograve;n lại s&aacute;ng nay kh&ocirc;ng qua khỏi.</p>\r\n</article>', 218, 3, 0, 1, '2022-11-21 19:35:42', '2022-11-22 05:25:49'),
(136, 'Tương quan trước trận chung kết U23 châu Á', 'tuong-quan-truoc-tran-chung-ket-u23-chau-A', 'UZBEKISTAN-Chủ nhà Uzbekistan ít kinh nghiệm hơn Saudi Arabia, nhưng có thể hình và khả năng tấn công tốt hơn khi vào đá chung kết U23 châu Á 2022 hôm nay 19/6.', '<div class=\"width-detail-photo\">\r\n<p class=\"description\"><span class=\"location-stamp\">UZBEKISTAN</span>Chủ nh&agrave; Uzbekistan &iacute;t kinh nghiệm hơn Saudi Arabia, nhưng c&oacute; thể h&igrave;nh v&agrave; khả năng tấn c&ocirc;ng tốt hơn khi v&agrave;o đ&aacute; chung kết U23 ch&acirc;u &Aacute; 2022 h&ocirc;m nay 19/6.</p>\r\n<div class=\"width_common social-com\">\r\n<div class=\"myvne_save_for_later border\" title=\"Lưu b&agrave;i viết\" data-token=\"c5bb607d09c0cfd402cb96ed6a1e51ed\" data-article-id=\"4477721\">&nbsp;</div>\r\n<a class=\"count_com_hoz flexbox\" title=\"B&igrave;nh luận\" href=\"https://vnexpress.net/tuong-quan-truoc-tran-chung-ket-u23-chau-a-4477721.html#box_comment_vne\"><span class=\"number_cmt num_cmt_detail widget-comment-4477721-4\">55</span></a></div>\r\n</div>\r\n<article class=\"fck_detail\">\r\n<div class=\"item_slide_show clearfix\">\r\n<div class=\"block_thumb_slide_show\" data-size-image=\"true\">\r\n<div class=\"block_thumb_picture\"><picture><source srcset=\"https://i1-thethao.vnecdn.net/2022/06/19/Web-Saudi-Arabia-Uzbekistan-CC-1655607822.jpg?w=0&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=tYfiToiGQ_PpnYfytUOeTA 1x, https://i1-thethao.vnecdn.net/2022/06/19/Web-Saudi-Arabia-Uzbekistan-CC-1655607822.jpg?w=0&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=8HxLq6UOHN4sGynnRca8aA 2x\" data-srcset=\"https://i1-thethao.vnecdn.net/2022/06/19/Web-Saudi-Arabia-Uzbekistan-CC-1655607822.jpg?w=0&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=tYfiToiGQ_PpnYfytUOeTA 1x, https://i1-thethao.vnecdn.net/2022/06/19/Web-Saudi-Arabia-Uzbekistan-CC-1655607822.jpg?w=0&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=8HxLq6UOHN4sGynnRca8aA 2x\"><img class=\"lazy lazy-blur lazy-unblur lazied\" src=\"https://i1-thethao.vnecdn.net/2022/06/19/Web-Saudi-Arabia-Uzbekistan-CC-1655607822.jpg?w=0&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=tYfiToiGQ_PpnYfytUOeTA\" alt=\"\" loading=\"lazy\" data-src=\"https://i1-thethao.vnecdn.net/2022/06/19/Web-Saudi-Arabia-Uzbekistan-CC-1655607822.jpg?w=0&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=tYfiToiGQ_PpnYfytUOeTA\" data-ll-status=\"loaded\"></picture></div>\r\n<div class=\"action_thumb flexbox\">&nbsp;</div>\r\n</div>\r\n</div>\r\n<div class=\"width-detail-photo\">&nbsp;</div>\r\n</article>', 218, 7, 0, 1, '2022-11-21 19:35:42', '2022-11-22 05:53:29'),
(137, 'Bắt Giám đốc cùng nhiều cán bộ CDC Đà Nẵng do liên quan Công ty Việt Á', 'bat-giam-doc-cung-nhieu-can-bo-cdc-da-nang-do-lien-quan-cong-ty-viet-a', 'Công an TP.Đà Nẵng vừa tống đạt quyết định khởi tố, bắt tạm giam Giám đốc CDC Đà Nẵng vì tham ô tài sản, liên quan Công ty Việt Á.', '<p>Chiều 20/6, Cơ quan CSĐT C&ocirc;ng an TP.Đ&agrave; Nẵng tống đạt quyết định khởi tố vụ &aacute;n, khởi tố bị can v&agrave; thực hiện lệnh bắt tạm giam 4 th&aacute;ng đối với &ocirc;ng T&ocirc;n Thất Thạnh (58 tuổi, tr&uacute; phường Thạch Thang, quận Hải Ch&acirc;u, TP.Đ&agrave; Nẵng), Gi&aacute;m đốc Trung t&acirc;m Kiểm so&aacute;t bệnh tật (CDC) Đ&agrave; Nẵng.</p>\r\n<div>\r\n<p class=\"image\"><img class=\"width100 initial loaded\" src=\"https://cdn.baogiaothong.vn/upload/images/2022-2/article_img/2022-06-20/img-bgt-2021-cdc-1655721827-width1280height720.jpg\" alt=\"Bắt Gi&aacute;m đốc c&ugrave;ng nhiều c&aacute;n bộ CDC Đ&agrave; Nẵng do li&ecirc;n quan C&ocirc;ng ty Việt &Aacute; 1\" data-original=\"https://cdn.baogiaothong.vn/upload/images/2022-2/article_img/2022-06-20/img-bgt-2021-cdc-1655721827-width1280height720.jpg\" data-was-processed=\"true\"></p>\r\n<p class=\"img_chu_thich\">Cơ quan CSĐT C&ocirc;ng an TP.Đ&agrave; Nẵng tống đạt quyết định khởi tố bị can đối với &ocirc;ng T&ocirc;n Thất Thạnh</p>\r\n</div>\r\n<p>&Ocirc;ng Thạnh bị khởi tố, bắt tạm giam để điều tra h&agrave;nh vi &ldquo;Tham &ocirc; t&agrave;i sản&rdquo; theo quy định tại khoản 4, Điều 353 BLHS.</p>\r\n<p>Cơ quan CSĐT C&ocirc;ng an TP.Đ&agrave; Nẵng cũng tống đạt quyết định khởi tố bị can v&agrave; thi h&agrave;nh lệnh bắt tạm giam 4 th&aacute;ng đối với b&agrave; Nguyễn Thị Thanh Nh&agrave;n (40 tuổi, tr&uacute; phường Thạch Thang, quận Hải Ch&acirc;u, TP.Đ&agrave; Nẵng), Trưởng Khoa X&eacute;t nghiệm CDC Đ&agrave; Nẵng.</p>\r\n<p>Khởi tố bị can v&agrave; &aacute;p dụng biện ph&aacute;p ngăn chặn \"Cấm đi khỏi nơi cư tr&uacute;\" đối với b&agrave; L&ecirc; Thị Kim Chi (36 tuổi, tr&uacute; quận Thanh kh&ecirc;, TP.Đ&agrave; Nẵng), l&agrave; nh&acirc;n vi&ecirc;n Khoa X&eacute;t nghiệm CDC Đ&agrave; Nẵng c&ugrave;ng về tội danh tr&ecirc;n.</p>\r\n<p>Cơ quan CSĐT C&ocirc;ng an TP.Đ&agrave; Nẵng cũng tiến h&agrave;nh kh&aacute;m x&eacute;t, thu giữ t&agrave;i liệu, chứng cứ li&ecirc;n quan đến vụ &aacute;n tại nơi ở v&agrave; nơi l&agrave;m việc của &ocirc;ng Thạnh v&agrave; c&aacute;c bị can n&oacute;i tr&ecirc;n.</p>\r\n<div id=\"v-24hContainer_video_player_21815917\" class=\"v-24hContainer_video_player_21815917_loaded\">\r\n<div id=\"my-video-multiple1_1655739250331-minableMon\"></div>\r\n</div>\r\n<p>Theo Cơ quan CSĐT C&ocirc;ng an TP.Đ&agrave; Nẵng, từ năm 2020 - 2021, &ocirc;ng T&ocirc;n Thất Thạnh c&ugrave;ng c&aacute;c đồng phạm đ&atilde; m&oacute;c nối với C&ocirc;ng ty Việt &Aacute; nhằm mục đ&iacute;ch tham &ocirc; số vật tư x&eacute;t nghiệm&nbsp;<a class=\"seokwl\" title=\"Covid-19\" href=\"https://www.baogiaothong.vn/tin-tuc-covid-19-moi-nhat-ngay-hom-nay-channel405/\">Covid-19</a>&nbsp;đ&atilde; được TP.Đ&agrave; Nẵng mua để phục vụ c&ocirc;ng t&aacute;c ph&ograve;ng chống dịch.</p>\r\n<p>&Ocirc;ng Thạnh đ&atilde; chỉ đạo Nguyễn Thị Thanh Nh&agrave;n v&agrave; L&ecirc; Thị Kim Chi l&agrave;m giả sổ s&aacute;ch, chứng từ để n&acirc;ng khống, chuyển h&oacute;a h&agrave;ng chục ng&agrave;n mẫu gộp x&eacute;t nghiệm Covid-19 th&agrave;nh mẫu đơn, đồng thời biến h&agrave;ng chục ng&agrave;n bộ kit được t&agrave;i trợ th&agrave;nh bộ kit mua của C&ocirc;ng ty Việt &Aacute;.</p>\r\n<p>Số vật tư d&ocirc;i dư sau sau khi được \"ph&ugrave; ph&eacute;p\", &ocirc;ng Thạnh c&ugrave;ng c&aacute;c đồng phạm đ&atilde; chuyển lại cho C&ocirc;ng ty Việt &Aacute; để chiếm đoạt số tiền với gi&aacute; trị thỏa thuận.</p>\r\n<p>Theo x&aacute;c định ban đầu của Cơ quan điều tra, gi&aacute; trị t&agrave;i sản bị chiếm đoạt l&agrave; hơn 4 tỷ đồng.</p>', 218, 1, 0, 1, '2022-11-21 19:35:42', '2022-11-22 08:35:04'),
(138, 'Tranh cãi vụ ông Liên Khui Thìn đòi nhiều tài sản', 'tranh-cai-vu-ong-lien-khui-thin-doi-nhieu-tai-san', 'TP HCM-Cựu tử tù Liên Khui Thìn đòi 50% tài sản trong công ty góp vốn 26 năm trước, song bị đơn cho rằng đã cấn trừ bằng mảnh đất, dù không đưa ra chứng cứ.', '<p class=\"description\"><span class=\"location-stamp\">TP HCM</span>Cựu tử t&ugrave; Li&ecirc;n Khui Th&igrave;n đ&ograve;i 50% t&agrave;i sản trong c&ocirc;ng ty g&oacute;p vốn 26 năm trước, song bị đơn cho rằng đ&atilde; cấn trừ bằng mảnh đất, d&ugrave; kh&ocirc;ng đưa ra chứng cứ.</p>\r\n<article class=\"fck_detail \">\r\n<p class=\"Normal\">Vụ tranh chấp t&agrave;i sản g&oacute;p vốn tại C&ocirc;ng ty TNHH Sản xuất Thương mại T&acirc;y Sơn giữa nguy&ecirc;n đơn l&agrave; &ocirc;ng&nbsp;<a href=\"https://vnexpress.net/ong-lien-khui-thin-co-the-duoc-nhan-lai-nhieu-tai-san-4381064.html\" rel=\"dofollow\">Li&ecirc;n Khui Th&igrave;n</a>&nbsp;v&agrave; bị đơn Phạm Minh Đạo, Phạm Nguyễn Minh Đức được TAND Cấp cao tại TP HCM x&eacute;t xử ph&uacute;c thẩm ng&agrave;y 17/6.</p>\r\n<p class=\"Normal\">Năm ngo&aacute;i, t&ograve;a cấp sơ thẩm đ&atilde; tuy&ecirc;n chấp nhận y&ecirc;u cầu của &ocirc;ng Th&igrave;n - được nhận lại 50% cổ phần tại C&ocirc;ng ty T&acirc;y Sơn (từng g&oacute;p vốn 26 năm trước) gồm nhiều t&agrave;i sản v&agrave; nh&agrave; đất tại S&agrave;i G&ograve;n, Vũng T&agrave;u. Tuy nhi&ecirc;n, bị đơn đơn kh&aacute;ng c&aacute;o, kh&ocirc;ng chấp nhận.</p>\r\n<p class=\"Normal\">Năm 1996, &ocirc;ng Th&igrave;n v&agrave; b&agrave; Nguyễn Thị Tuyết Mai c&ugrave;ng th&agrave;nh lập C&ocirc;ng ty TNHH Sản xuất Thương mại T&acirc;y Sơn kinh doanh đa ng&agrave;nh nghề, vốn điều lệ 3 tỷ đồng, mỗi b&ecirc;n g&oacute;p 50%. &Ocirc;ng Th&igrave;n l&agrave;m Chủ tịch Hội đồng th&agrave;nh vi&ecirc;n c&ograve;n b&agrave; Mai l&agrave; Gi&aacute;m đốc c&ocirc;ng ty.</p>\r\n<p class=\"Normal\">Một năm sau, &ocirc;ng Th&igrave;n bị bắt trong&nbsp;<a href=\"https://vnexpress.net/topic/thi-hanh-an-vu-epco-minh-phung-va-tan-truong-sanh-13185\" rel=\"dofollow\">vụ &aacute;n Epco</a>&nbsp;c&ugrave;ng Tăng Minh Phụng với c&aacute;o buộc chiếm đoạt h&agrave;ng ngh&igrave;n tỷ đồng của c&aacute;c ng&acirc;n h&agrave;ng. Hai người đều bị tuy&ecirc;n &aacute;n tử h&igrave;nh về tội&nbsp;<em>Lừa đảo chiếm đoạt t&agrave;i sản XHCN.</em></p>\r\n<p class=\"Normal\">&Ocirc;ng Th&igrave;n sau đ&oacute; được Chủ tịch nước chấp nhận đơn xin &acirc;n giảm từ tử h&igrave;nh xuống t&ugrave; chung th&acirc;n. Hơn 10 năm cải tạo tốt, thi h&agrave;nh &aacute;n tr&ecirc;n 500 tỷ đồng... &ocirc;ng tiếp tục được giảm &aacute;n rồi được đặc x&aacute; năm 2009.</p>\r\n<p class=\"Normal\">&Ocirc;ng Th&igrave;n cho rằng, trong thời gian m&igrave;nh chấp h&agrave;nh &aacute;n, b&agrave; Mai đ&atilde; chuyển to&agrave;n bộ vốn v&agrave; t&agrave;i sản của C&ocirc;ng ty T&acirc;y Sơn cho &ocirc;ng Phạm Minh Đạo, Phạm Nguyễn Minh Đức (chồng, con b&agrave; Mai) v&agrave; &ocirc;ng Đỗ Thế Minh (em b&agrave; Mai) m&agrave; kh&ocirc;ng hỏi &yacute; kiến của m&igrave;nh. &Ocirc;ng nhiều lần li&ecirc;n hệ b&agrave; Mai v&agrave; người th&acirc;n của b&agrave; để giải quyết nhưng họ kh&ocirc;ng hợp t&aacute;c.</p>\r\n<p class=\"Normal\">Năm 2018, &ocirc;ng Th&igrave;n kiện chồng con b&agrave; Mai ra TAND TP HCM, y&ecirc;u cầu t&ograve;a tuy&ecirc;n hủy giao dịch gian dối, phục hồi quyền sở hữu 50% t&agrave;i sản v&agrave; vốn g&oacute;p của m&igrave;nh trong C&ocirc;ng ty T&acirc;y Sơn bao gồm nhiều bất động sản tại TP HCM, Vũng T&agrave;u... Ngo&agrave;i ra, &ocirc;ng Th&igrave;n cũng đề nghị t&ograve;a tuy&ecirc;n v&ocirc; hiệu c&aacute;c giao dịch chuyển nhượng vốn g&oacute;p giữa những người n&agrave;y, hủy c&aacute;c Giấy chứng nhận đăng k&yacute; doanh nghiệp từ lần 1 đến lần 8 của C&ocirc;ng ty T&acirc;y Sơn.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/18/Lien-Khui-Thin-1-4755-1655555836.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=-YSQWygV3WVviLXmlDTrfg 1x, https://i1-vnexpress.vnecdn.net/2022/06/18/Lien-Khui-Thin-1-4755-1655555836.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=OMka-iuUtewE1oBa63rdPw 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/18/Lien-Khui-Thin-1-4755-1655555836.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=Z064fgzfVpAD3JK-7AxvRw 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/18/Lien-Khui-Thin-1-4755-1655555836.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=-YSQWygV3WVviLXmlDTrfg 1x, https://i1-vnexpress.vnecdn.net/2022/06/18/Lien-Khui-Thin-1-4755-1655555836.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=OMka-iuUtewE1oBa63rdPw 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/18/Lien-Khui-Thin-1-4755-1655555836.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=Z064fgzfVpAD3JK-7AxvRw 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/06/18/Lien-Khui-Thin-1-4755-1655555836.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=-YSQWygV3WVviLXmlDTrfg\" alt=\"&Ocirc;ng Li&ecirc;n Khui Th&igrave;n tại c&ocirc;ng ty gần đ&acirc;y. Ảnh: Minh Kiều\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/06/18/Lien-Khui-Thin-1-4755-1655555836.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=-YSQWygV3WVviLXmlDTrfg\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">&Ocirc;ng Li&ecirc;n Khui Th&igrave;n tại c&ocirc;ng ty gần đ&acirc;y. Ảnh:&nbsp;<em>Minh Kiều</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">C&ocirc;ng ty T&acirc;y Sơn hiện do &ocirc;ng Đức điều h&agrave;nh.&nbsp;<strong>Tr&igrave;nh b&agrave;y tại phi&ecirc;n t&ograve;a ph&uacute;c thẩm</strong>, bị đơn giữ nguy&ecirc;n kh&aacute;ng c&aacute;o kh&ocirc;ng chấp nhận y&ecirc;u cầu khởi kiện của &ocirc;ng Th&igrave;n. Họ cho rằng đ&atilde; l&agrave;m đ&uacute;ng quy định của ph&aacute;p luật, việc nhận chuyển nhượng phần vốn của b&agrave; Mai đ&atilde; xin &yacute; kiến c&aacute;c cơ quan chức năng v&agrave; được đồng &yacute;.</p>\r\n<p class=\"Normal\">Trong đ&oacute; c&oacute; C&ocirc;ng văn ng&agrave;y 22/8/2000 của TAND TP HCM trả lời về phần h&ugrave;n vốn của &ocirc;ng Th&igrave;n. Cụ thể, c&ocirc;ng văn n&agrave;y giải th&iacute;ch: \"Li&ecirc;n Khui Th&igrave;n tuy c&oacute; g&oacute;p vốn điều lệ trong C&ocirc;ng ty TNHH T&acirc;y Sơn l&agrave; 1,5 tỷ đồng, nhưng sau đ&oacute; lại lấy t&agrave;i sản của ch&iacute;nh C&ocirc;ng ty T&acirc;y sơn để thế chấp nơi kh&aacute;c th&igrave; coi như kh&ocirc;ng c&ograve;n vốn điều lệ trong c&ocirc;ng ty nữa. C&aacute;c th&agrave;nh vi&ecirc;n c&ograve;n lại c&oacute; thể đăng k&yacute; theo Luật Doanh nghiệp để ổn định hoạt động\".</p>\r\n<p class=\"Normal\">L&agrave; người&nbsp;<em>c&oacute; quyền v&agrave; nghĩa vụ li&ecirc;n quan</em>, b&agrave; Mai cho rằng phần vốn g&oacute;p 50% trị gi&aacute; 1,5 tỷ của &ocirc;ng Th&igrave;n đ&atilde; được b&agrave; cấn trừ v&agrave;o một thửa đất ở B&igrave;nh Dương, n&ecirc;n đề nghị t&ograve;a x&aacute;c định phần vốn g&oacute;p của &ocirc;ng Th&igrave;n kh&ocirc;ng c&ograve;n. \"Mảnh đất n&agrave;y t&ocirc;i bỏ tiền mua sau đ&oacute; sang t&ecirc;n cho &ocirc;ng Th&igrave;n để &ocirc;ng ấy đem thế chấp vay tiền ng&acirc;n h&agrave;ng. Do đ&oacute; t&ocirc;i mới gạt t&ecirc;n &ocirc;ng Th&igrave;n ra khỏi c&ocirc;ng ty v&agrave; chỉ chuyển nhượng phần 1,5 tỷ vốn g&oacute;p của m&igrave;nh cho người th&acirc;n\", b&agrave; Mai n&oacute;i. Tuy nhi&ecirc;n, b&agrave; cũng thừa nhận chứng từ ph&aacute;p l&yacute; li&ecirc;n quan đến việc cấn trừ kh&ocirc;ng c&ograve;n giữ.</p>\r\n<p class=\"Normal\">Cũng theo b&agrave; Mai, khi to&agrave; x&eacute;t xử vụ &aacute;n Epco, b&agrave; đ&atilde; hỏi &ocirc;ng Th&igrave;n: \"Phần 1,5 tỷ của anh tại T&acirc;y Sơn coi như kh&ocirc;ng c&ograve;n nha? Ảnh đồng &yacute; khi chưa bị bắt nhưng hỏi tại t&ograve;a ảnh kh&ocirc;ng trả lời\".</p>\r\n<p class=\"Normal\">Trả lời HĐXX về l&yacute; do c&oacute; c&ocirc;ng văn của TAND TP HCM x&aacute;c định &ocirc;ng Th&igrave;n kh&ocirc;ng c&ograve;n vốn tại c&ocirc;ng ty, b&agrave; Mai cho biết sau khi t&ograve;a kết &aacute;n &ocirc;ng Th&igrave;n b&agrave; c&oacute; hỏi về t&agrave;i sản của &ocirc;ng tại C&ocirc;ng ty T&acirc;y Sơn th&igrave; được thư k&yacute; hướng dẫn l&agrave;m đơn. Sau khi được thẩm ph&aacute;n ra văn bản giải th&iacute;ch b&agrave; mới gạch t&ecirc;n &ocirc;ng Th&igrave;n v&agrave; l&agrave;m thủ tục chuyển nhượng g&oacute;p vốn hơn 1,5 tỷ đồng cho người th&acirc;n.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/18/Lien-Khui-Thin-4843-1655555836.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=tfo7a-lWcsk8Pyyu6Sg9QA 1x, https://i1-vnexpress.vnecdn.net/2022/06/18/Lien-Khui-Thin-4843-1655555836.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=g9Zaly4hxIVkzm5ANlVR1A 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/18/Lien-Khui-Thin-4843-1655555836.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=uUIjpaLRaYpS9BvZtyCV4A 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/18/Lien-Khui-Thin-4843-1655555836.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=tfo7a-lWcsk8Pyyu6Sg9QA 1x, https://i1-vnexpress.vnecdn.net/2022/06/18/Lien-Khui-Thin-4843-1655555836.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=g9Zaly4hxIVkzm5ANlVR1A 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/18/Lien-Khui-Thin-4843-1655555836.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=uUIjpaLRaYpS9BvZtyCV4A 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/06/18/Lien-Khui-Thin-4843-1655555836.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=tfo7a-lWcsk8Pyyu6Sg9QA\" alt=\"Luật gia Đặng Đ&igrave;nh Thịnh - đại diện theo ủy quyền của &ocirc;ng Th&igrave;n tại t&ograve;a. Ảnh: Ng&acirc;n Nga\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/06/18/Lien-Khui-Thin-4843-1655555836.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=tfo7a-lWcsk8Pyyu6Sg9QA\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Luật gia Đặng Đ&igrave;nh Thịnh - đại diện theo ủy quyền của &ocirc;ng Th&igrave;n tại t&ograve;a. Ảnh:&nbsp;<em>Ng&acirc;n Nga</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">N&ecirc;u quan điểm, người đại diện theo ủy quyền của &ocirc;ng Th&igrave;n đề nghị t&ograve;a b&aacute;c kh&aacute;ng c&aacute;o của bị đơn, giữ nguy&ecirc;n bản &aacute;n sơ thẩm. \"C&ocirc;ng ty T&acirc;y Sơn bản chất l&agrave; của &ocirc;ng Th&igrave;n, b&agrave; Mai chỉ l&agrave; nh&acirc;n vi&ecirc;n v&agrave; ho&agrave;n to&agrave;n kh&ocirc;ng c&oacute; chuyện cấn trừ nợ như b&agrave; Mai n&oacute;i\", đại diện nguy&ecirc;n đơn n&oacute;i, th&ecirc;m rằng tr&igrave;nh b&agrave;y của bị đơn kh&ocirc;ng c&oacute; căn cứ v&agrave; cũng kh&ocirc;ng đưa ra được chứng cứ chứng minh. Nguyện vọng của &ocirc;ng Th&igrave;n muốn lấy lại phần g&oacute;p vốn l&agrave; để c&ugrave;ng b&agrave; Mai phối hợp kh&ocirc;i phục c&ocirc;ng ty hoạt động lại tốt hơn.</p>\r\n<p class=\"Normal\">Theo nguy&ecirc;n đơn, c&ocirc;ng văn của t&ograve;a th&agrave;nh phố trả lời b&agrave; Mai kh&ocirc;ng li&ecirc;n quan đến vụ &aacute;n n&agrave;y m&agrave; li&ecirc;n quan đến vụ &aacute;n h&igrave;nh sự. Cũng từ c&ocirc;ng văn đ&oacute; m&agrave; Sở Kế hoạch - Đầu tư giảm cổ phần doanh nghiệp xuống. C&ograve;n theo quy định, muốn đưa &ocirc;ng Th&igrave;n ra khỏi phần g&oacute;p vốn th&igrave; phải c&oacute; quyết định họp của Hội đồng th&agrave;nh vi&ecirc;n.</p>\r\n<p class=\"Normal\">Ph&aacute;t biểu quan điểm giải quyết vụ &aacute;n, đại diện VKS cho rằng việc x&oacute;a t&ecirc;n &ocirc;ng Th&igrave;n m&agrave; kh&ocirc;ng c&oacute; quyết định của Hội đồng th&agrave;nh vi&ecirc;n l&agrave; vi phạm Luật Doanh nghiệp, x&acirc;m phạm quyền lợi của &ocirc;ng Th&igrave;n. T&ograve;a tuy&ecirc;n c&aacute;c hợp đồng chuyển nhượng giữa bị đơn v&agrave; người li&ecirc;n quan trong vụ &aacute;n v&ocirc; hiệu l&agrave; c&oacute; căn cứ.</p>\r\n<p class=\"Normal\">Ph&iacute;a bị đơn viện dẫn c&ocirc;ng văn năm 2000 của t&ograve;a th&agrave;nh phố, cho rằng &ocirc;ng Th&igrave;n kh&ocirc;ng c&ograve;n l&agrave; th&agrave;nh vi&ecirc;n C&ocirc;ng ty T&acirc;y Sơn l&agrave; chưa đ&uacute;ng bởi c&ocirc;ng văn n&agrave;y kh&ocirc;ng phải l&agrave; bản &aacute;n hoặc quyết định c&oacute; hiệu lực ph&aacute;p luật của t&ograve;a. \"Bị đơn kh&aacute;ng c&aacute;o nhưng kh&ocirc;ng đưa ra được căn cứ chứng minh n&ecirc;n VKS đề nghị kh&ocirc;ng chấp nhận đơn kh&aacute;ng c&aacute;o, giữ nguy&ecirc;n bản &aacute;n sơ thẩm\", đại diện VKS n&oacute;i.</p>\r\n<p class=\"Normal\">HĐXX nghị &aacute;n k&eacute;o d&agrave;i, sẽ ra ph&aacute;n quyết v&agrave;o ng&agrave;y 24/6.</p>\r\n</article>', 218, 9, 2, 1, '2022-11-21 19:35:42', '2023-03-12 08:36:21');
INSERT INTO `posts` (`id`, `title`, `slug`, `excerpt`, `body`, `user_id`, `category_id`, `views`, `approved`, `created_at`, `updated_at`) VALUES
(139, 'Dấu hiệu của một lời tỏ tình vội vàng', 'dau-hieu-cua-mot-loi-to-tinh-voi-vang', 'Nói lời yêu là một dấu mốc đặc biệt trong mối quan hệ tình cảm nên việc tỏ tình sớm hay muộn sẽ ảnh hưởng rất nhiều đến tương lai của hai người.', '<p class=\"Normal\">N&oacute;i lời y&ecirc;u ở thời điểm n&agrave;o l&agrave; một lựa chọn kh&ocirc;ng dễ d&agrave;ng. C&aacute;c chuy&ecirc;n gia t&acirc;m l&yacute; v&agrave; quan hệ đều nhất tr&iacute; rằng kh&ocirc;ng c&oacute; mốc thời gian cụ thể n&agrave;o được coi l&agrave; thời điểm th&iacute;ch hợp cho việc n&oacute;i lời y&ecirc;u. Khoảng thời gian cần thiết để đ&aacute;nh gi&aacute; mối quan hệ đ&atilde; ch&iacute;n muồi chưa t&ugrave;y thuộc v&agrave;o lượng thời gian hai ph&iacute;a d&agrave;nh cho nhau cũng như c&aacute;c t&igrave;nh huống họ c&ugrave;ng nhau trải qua, gi&uacute;p mối quan hệ tiến triển.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture data-inimage=\"done\"><source srcset=\"https://i1-giadinh.vnecdn.net/2022/06/17/To-tinh-2907-1655457179.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=lQgvP-bV5XGPkjYXkrWVdA 1x, https://i1-giadinh.vnecdn.net/2022/06/17/To-tinh-2907-1655457179.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=qfVtwQ0Qg0kqGTFhpY52tw 1.5x, https://i1-giadinh.vnecdn.net/2022/06/17/To-tinh-2907-1655457179.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=TQNq36LeAw31wl8J4N0uyA 2x\" data-srcset=\"https://i1-giadinh.vnecdn.net/2022/06/17/To-tinh-2907-1655457179.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=lQgvP-bV5XGPkjYXkrWVdA 1x, https://i1-giadinh.vnecdn.net/2022/06/17/To-tinh-2907-1655457179.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=qfVtwQ0Qg0kqGTFhpY52tw 1.5x, https://i1-giadinh.vnecdn.net/2022/06/17/To-tinh-2907-1655457179.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=TQNq36LeAw31wl8J4N0uyA 2x\"><img class=\"lazy lazied\" src=\"https://i1-giadinh.vnecdn.net/2022/06/17/To-tinh-2907-1655457179.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=lQgvP-bV5XGPkjYXkrWVdA\" alt=\"Ảnh minh họa: Yahoo News.\" loading=\"lazy\" data-src=\"https://i1-giadinh.vnecdn.net/2022/06/17/To-tinh-2907-1655457179.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=lQgvP-bV5XGPkjYXkrWVdA\" data-ll-status=\"loaded\"></picture>\r\n<div class=\"embed-container-ads\">&nbsp;</div>\r\n</div>\r\n<figcaption>\r\n<p class=\"Image\">Ảnh minh họa:&nbsp;<em>Yahoo News.</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Tuy nhi&ecirc;n, cũng c&oacute; một số dấu hiệu cho thấy rằng c&oacute; thể c&ograve;n qu&aacute; sớm để thốt ra lời y&ecirc;u.</p>\r\n<p class=\"Normal\"><em>Bạn chưa biết đối phương ứng xử thế n&agrave;o với bạn b&egrave;, gia đ&igrave;nh, người th&acirc;n... của c&ocirc; ấy/anh ấy</em></p>\r\n<p class=\"Normal\">Bạn c&oacute; thể biết được c&aacute;ch đối phương sẽ đối xử với m&igrave;nh thế n&agrave;o trong tương lai bằng c&aacute;ch quan s&aacute;t c&aacute;ch họ đối xử với gia đ&igrave;nh, bạn b&egrave; th&acirc;n thiết v&agrave; cả những người xa lạ. Nếu họ c&oacute; ứng xử kh&oacute; chịu, thường m&acirc;u thuẫn, căng thẳng, điều tương tự c&oacute; thể xảy ra trong mối quan hệ l&atilde;ng mạn của bạn.</p>\r\n<p class=\"Normal\"><em>Mối quan hệ của hai bạn chưa đủ s&acirc;u sắc</em></p>\r\n<p class=\"Normal\">Đương nhi&ecirc;n c&oacute; thể bạn chỉ muốn c&oacute; những ph&uacute;t gi&acirc;y vui vẻ b&ecirc;n đối t&aacute;c, tuy nhi&ecirc;n, một mối quan hệ l&acirc;u d&agrave;i sẽ kh&ocirc;ng chỉ c&oacute; thế. Y&ecirc;u thực sự l&agrave; c&ugrave;ng nhau trải qua những cung bậc kh&aacute;c nhau của cảm x&uacute;c, c&ugrave;ng đồng h&agrave;nh trong những c&ocirc;ng việc b&igrave;nh thường, c&ugrave;ng ho&agrave;n th&agrave;nh c&aacute;c tr&aacute;ch nhiệm v&agrave; mục ti&ecirc;u, cũng như c&ugrave;ng trải qua những gi&acirc;y ph&uacute;t kh&oacute; khăn v&agrave; xem phản ứng của đối t&aacute;c ra sao. V&igrave; thế, nếu bạn chỉ trải qua những ph&uacute;t vui vẻ tho&aacute;ng qua với đối t&aacute;c, đ&oacute; chưa phải l&agrave; l&uacute;c n&oacute;i y&ecirc;u.</p>\r\n<p class=\"Normal\">Jenn Mann, nh&agrave; trị liệu t&acirc;m l&yacute; người Mỹ cho rằng bạn n&ecirc;n \"đo nhiệt độ của mối quan hệ\" trước khi nghĩ đến việc n&oacute;i \"Anh y&ecirc;u em\". Cụ thể hơn, bạn n&ecirc;n x&aacute;c định xem những ph&uacute;t gi&acirc;y hai bạn d&agrave;nh cho nhau c&oacute; lu&ocirc;n l&agrave; một cục than hồng hay c&oacute; l&uacute;c n&oacute;ng, l&uacute;c lạnh. Jenn n&oacute;i: \"Nếu ai đ&oacute; lu&ocirc;n sẵn s&agrave;ng đồng h&agrave;nh với bạn hoặc &iacute;t nhất coi bạn l&agrave; đối t&aacute;c ch&iacute;nh trong mối quan hệ, đ&oacute; l&agrave; một t&iacute;n hiệu tốt\".</p>\r\n<p class=\"Normal\">Chuy&ecirc;n gia cũng khuy&ecirc;n bạn n&ecirc;n ngồi lại với những cảm x&uacute;c của m&igrave;nh để nhận thức được những kỳ vọng của bản th&acirc;n về mối quan hệ. Đừng cho rằng khi bạn n&oacute;i y&ecirc;u đối t&aacute;c, điều đ&oacute; đồng nghĩa với việc cả hai sẽ lập tức bước v&agrave;o một chương mới trong mối quan hệ. H&atilde;y đặt m&igrave;nh v&agrave;o t&igrave;nh huống nếu t&igrave;nh cảm kh&ocirc;ng được đ&aacute;p lại.</p>\r\n<p class=\"Normal\">Điều quan trọng nữa được đề cập tới l&agrave; việc bạn n&ecirc;n tự hỏi bản th&acirc;n: Đ&oacute; c&oacute; chắc l&agrave; t&igrave;nh y&ecirc;u?</p>\r\n<p class=\"Normal\">Cảm gi&aacute;c hồi hộp của một mối quan hệ mới bắt đầu c&ugrave;ng niềm vui n&oacute; mang lại c&oacute; thể khiến bạn trở n&ecirc;n m&ecirc; đắm, lẫn lộn v&agrave; kh&ocirc;ng định nghĩa được c&oacute; thật l&agrave; m&igrave;nh đang y&ecirc;u hay kh&ocirc;ng.</p>\r\n<p class=\"Normal\">\"Trong giai đoạn n&agrave;y, nhiều người bị mờ mắt bởi sự phấn kh&iacute;ch. Đương nhi&ecirc;n điều n&agrave;y kh&ocirc;ng xấu, bạn c&oacute; thể thoải m&aacute;i tận hưởng n&oacute;, nhưng đừng vội v&agrave;ng, khi mối quan hệ chưa đủ để tiến đến mức l&agrave; t&igrave;nh y&ecirc;u\", Jenn n&oacute;i.</p>\r\n<p class=\"Normal\">T&igrave;nh y&ecirc;u kh&ocirc;ng phải l&uacute;c n&agrave;o cũng giống như trong điện ảnh. N&oacute; l&agrave; một qu&aacute; tr&igrave;nh bao gồm x&acirc;y dựng kết nối, sự gắn kết v&agrave; l&ograve;ng tin theo thời gian. Đ&oacute; kh&ocirc;ng chỉ l&agrave; lời n&oacute;i đầu m&ocirc;i, m&agrave; l&agrave; việc họ đến chăm s&oacute;c bạn khi bạn ốm, c&ugrave;ng bạn quan t&acirc;m tới những người th&acirc;n đau ốm trong nh&agrave;. Mann n&oacute;i: \"T&igrave;nh y&ecirc;u ch&acirc;n th&agrave;nh bao gồm cả sự hy sinh, thay v&igrave; t&igrave;nh dục hay những cảm x&uacute;c n&oacute;ng h&ocirc;i hổi ban đầu\".</p>\r\n<p class=\"Normal\">Monica Berg, một gi&aacute;m đốc truyền th&ocirc;ng về h&ocirc;n nh&acirc;n, gia đ&igrave;nh n&oacute;i: \"Điều quan trọng nhất, bạn cần xem lại mọi cảm x&uacute;c của m&igrave;nh, trước khi bạn n&oacute;i với ai đ&oacute; rằng bạn y&ecirc;u họ\". N&ecirc;n th&agrave;nh thật với bản th&acirc;n, đừng để m&igrave;nh b&agrave;y tỏ t&igrave;nh y&ecirc;u chỉ v&igrave; bạn đang rơi v&agrave;o cảm gi&aacute;c c&ocirc; đơn hay tuyệt vọng v&agrave; muốn t&igrave;m một chỗ dựa tinh thần.</p>\r\n<p class=\"Normal\">Ngo&agrave;i ra, n&oacute;i lời y&ecirc;u qu&aacute; sớm c&oacute; thể ảnh hưởng đến mối quan hệ. Chuy&ecirc;n gia cho rằng n&oacute;i lời y&ecirc;u qu&aacute; sớm c&oacute; thể khiến mối quan hệ đang tr&ecirc;n đ&agrave; tiến triển trở n&ecirc;n xấu đi, bởi n&oacute; khiến đối t&aacute;c sợ h&atilde;i.</p>', 218, 12, 0, 1, '2022-11-21 19:35:42', '2023-02-05 07:31:36'),
(141, 'Ba bộ váy cưới trăm triệu của Minh Hằng', 'ba-bo-vay-cuoi-tram-trieu-cua-Minh-Hang', 'Minh Hằng chọn váy thương hiệu nước ngoài khi làm lễ, sau đó diện thiết kế của Chung Thanh Phong nhảy cùng bạn bè.', '<p class=\"description\">Minh Hằng chọn v&aacute;y thương hiệu nước ngo&agrave;i khi l&agrave;m lễ, sau đ&oacute; diện thiết kế của Chung Thanh Phong nhảy c&ugrave;ng bạn b&egrave;.</p>\r\n<article class=\"fck_detail \">\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-giaitri.vnecdn.net/2022/06/19/vay-minh-hang-6-5851-1655610916.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=kiWS-G1empSv-Bnczrv2zg 1x, https://i1-giaitri.vnecdn.net/2022/06/19/vay-minh-hang-6-5851-1655610916.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=PMukvtfdVQaC3FZzP5ufcQ 1.5x, https://i1-giaitri.vnecdn.net/2022/06/19/vay-minh-hang-6-5851-1655610916.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=HN7837o4bfvCPQtwBZW51g 2x\" data-srcset=\"https://i1-giaitri.vnecdn.net/2022/06/19/vay-minh-hang-6-5851-1655610916.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=kiWS-G1empSv-Bnczrv2zg 1x, https://i1-giaitri.vnecdn.net/2022/06/19/vay-minh-hang-6-5851-1655610916.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=PMukvtfdVQaC3FZzP5ufcQ 1.5x, https://i1-giaitri.vnecdn.net/2022/06/19/vay-minh-hang-6-5851-1655610916.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=HN7837o4bfvCPQtwBZW51g 2x\"><img class=\"lazy lazied\" src=\"https://i1-giaitri.vnecdn.net/2022/06/19/vay-minh-hang-6-5851-1655610916.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=kiWS-G1empSv-Bnczrv2zg\" alt=\"Trong thời khắc l&ecirc;n lễ đường, Minh Hằng diện chiếc v&aacute;y Lena của h&atilde;ng Marchesa Couture. Thiết kế d&aacute;ng ball gown với họa tiết th&ecirc;u 3D tỉ mỉ thuộc bộ sưu tập m&ugrave;a Xu&acirc;n 2020, c&oacute; gi&aacute; 16.000 USD (hơn 371 triệu đồng).\" loading=\"lazy\" data-src=\"https://i1-giaitri.vnecdn.net/2022/06/19/vay-minh-hang-6-5851-1655610916.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=kiWS-G1empSv-Bnczrv2zg\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Khi l&ecirc;n lễ đường, Minh Hằng diện v&aacute;y Lena của h&atilde;ng Marchesa Couture. Thiết kế d&aacute;ng ball gown với họa tiết th&ecirc;u 3D tỉ mỉ thuộc bộ sưu tập m&ugrave;a Xu&acirc;n 2020, c&oacute; gi&aacute; 16.000 USD (hơn 371 triệu đồng).</p>\r\n</figcaption>\r\n</figure>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-giaitri.vnecdn.net/2022/06/19/vay-minh-hang-3-1132-1655610916.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=9PI_223UrU7iu0zSIWxNcA 1x, https://i1-giaitri.vnecdn.net/2022/06/19/vay-minh-hang-3-1132-1655610916.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=NQwT65y2btanJFe3pMNKcg 1.5x, https://i1-giaitri.vnecdn.net/2022/06/19/vay-minh-hang-3-1132-1655610916.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=T66zW8O2Frm-nL1vd45X6g 2x\" data-srcset=\"https://i1-giaitri.vnecdn.net/2022/06/19/vay-minh-hang-3-1132-1655610916.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=9PI_223UrU7iu0zSIWxNcA 1x, https://i1-giaitri.vnecdn.net/2022/06/19/vay-minh-hang-3-1132-1655610916.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=NQwT65y2btanJFe3pMNKcg 1.5x, https://i1-giaitri.vnecdn.net/2022/06/19/vay-minh-hang-3-1132-1655610916.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=T66zW8O2Frm-nL1vd45X6g 2x\"><img class=\"lazy lazied\" src=\"https://i1-giaitri.vnecdn.net/2022/06/19/vay-minh-hang-3-1132-1655610916.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=9PI_223UrU7iu0zSIWxNcA\" alt=\"Đi c&ugrave;ng bộ c&aacute;nh l&agrave; chiếc mạng che mặt Lena Veil c&oacute; gi&aacute; 4.900 USD (khoảng 114 triệu đồng) v&agrave; găng tay của Rosa Clara (T&acirc;y Ban Nha) c&oacute; gi&aacute; 300 USD (7 triệu đồng). Trước Minh Hằng, nhiều sao Hollywood như Paris Hilton, Nicole Richie, Julianne Hough... cũng chọn v&aacute;y cưới của Marchesa Couture cho h&ocirc;n lễ của họ.\" loading=\"lazy\" data-src=\"https://i1-giaitri.vnecdn.net/2022/06/19/vay-minh-hang-3-1132-1655610916.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=9PI_223UrU7iu0zSIWxNcA\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Đi c&ugrave;ng bộ c&aacute;nh l&agrave; mạng che mặt Lena Veil c&oacute; gi&aacute; 4.900 USD (khoảng 114 triệu đồng) v&agrave; găng tay của Rosa Clara (T&acirc;y Ban Nha) c&oacute; gi&aacute; 300 USD (bảy triệu đồng). Nhiều sao Hollywood như Paris Hilton, Nicole Richie, Julianne Hough... cũng chọn v&aacute;y cưới của Marchesa Couture cho h&ocirc;n lễ của họ.</p>\r\n</figcaption>\r\n</figure>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-111-5368-1655610916.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=oNVuUeylwNbLIGfQgm0xrQ 1x, https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-111-5368-1655610916.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=js3AMnVviEb_BDj5Mp0vOQ 1.5x, https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-111-5368-1655610916.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=ITEJnlhhlBuZedidbQDIbg 2x\" data-srcset=\"https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-111-5368-1655610916.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=oNVuUeylwNbLIGfQgm0xrQ 1x, https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-111-5368-1655610916.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=js3AMnVviEb_BDj5Mp0vOQ 1.5x, https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-111-5368-1655610916.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=ITEJnlhhlBuZedidbQDIbg 2x\"><img class=\"lazy lazied\" src=\"https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-111-5368-1655610916.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=oNVuUeylwNbLIGfQgm0xrQ\" alt=\"Bộ thứ hai được nghệ sĩ d&ugrave;ng đ&oacute;n tiếp kh&aacute;ch thuộc bộ sưu tập Xu&acirc;n - H&egrave; 2020 đến từ thương hiệu Anne Barge, gi&aacute; 4.830 USD (112 triệu đồng). V&aacute;y đu&ocirc;i c&aacute; kết hợp corset được may theo số đo ri&ecirc;ng, t&ocirc;n d&aacute;ng  c&ocirc; d&acirc;u. C&aacute;c lớp vải voan đan xen ngẫu hứng tạo n&ecirc;n hiệu ứng thị gi&aacute;c bồng bềnh theo mỗi chuyển động.\" loading=\"lazy\" data-src=\"https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-111-5368-1655610916.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=oNVuUeylwNbLIGfQgm0xrQ\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Bộ thứ hai được nghệ sĩ d&ugrave;ng đ&oacute;n tiếp kh&aacute;ch thuộc bộ sưu tập Xu&acirc;n - H&egrave; 2020 đến từ thương hiệu Anne Barge, gi&aacute; 4.830 USD (112 triệu đồng). V&aacute;y đu&ocirc;i c&aacute; kết hợp corset được may theo số đo ri&ecirc;ng, t&ocirc;n d&aacute;ng c&ocirc; d&acirc;u. C&aacute;c lớp vải voan đan xen ngẫu hứng tạo n&ecirc;n hiệu ứng thị gi&aacute;c bồng bềnh theo mỗi chuyển động.</p>\r\n</figcaption>\r\n</figure>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-giaitri.vnecdn.net/2022/06/19/vay-minh-hang-2-1891-1655610916.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=nCi3L6yAjNj-3VKPVd4vRw 1x, https://i1-giaitri.vnecdn.net/2022/06/19/vay-minh-hang-2-1891-1655610916.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=c-KBA5z2wMV7Gy2OqU7vvg 1.5x, https://i1-giaitri.vnecdn.net/2022/06/19/vay-minh-hang-2-1891-1655610916.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=zxcNd3XEuK3f_u0054d-FA 2x\" data-srcset=\"https://i1-giaitri.vnecdn.net/2022/06/19/vay-minh-hang-2-1891-1655610916.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=nCi3L6yAjNj-3VKPVd4vRw 1x, https://i1-giaitri.vnecdn.net/2022/06/19/vay-minh-hang-2-1891-1655610916.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=c-KBA5z2wMV7Gy2OqU7vvg 1.5x, https://i1-giaitri.vnecdn.net/2022/06/19/vay-minh-hang-2-1891-1655610916.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=zxcNd3XEuK3f_u0054d-FA 2x\"><img class=\"lazy lazied\" src=\"https://i1-giaitri.vnecdn.net/2022/06/19/vay-minh-hang-2-1891-1655610916.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=nCi3L6yAjNj-3VKPVd4vRw\" alt=\"Minh Hằng c&ograve;n kết hợp với tay &aacute;o voan th&ecirc;u hoa gi&aacute; 590 USD (14 triệu đồng) gi&uacute;p tổng thể th&ecirc;m bắt mắt. Anne Barge th&agrave;nh lập năm 1999, l&agrave; thương hiệu thời trang cưới l&acirc;u đời v&agrave; nổi tiếng tại Mỹ. Thương hiệu n&agrave;y từng đồng h&agrave;nh trong lễ cưới của nhiều ng&ocirc;i sao như Laura Benanti, Johanna Braddy, Kristin Cavallari, Yvonne Orji...\" loading=\"lazy\" data-src=\"https://i1-giaitri.vnecdn.net/2022/06/19/vay-minh-hang-2-1891-1655610916.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=nCi3L6yAjNj-3VKPVd4vRw\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Minh Hằng c&ograve;n kết hợp với tay &aacute;o voan th&ecirc;u hoa gi&aacute; 590 USD (14 triệu đồng) gi&uacute;p tổng thể th&ecirc;m bắt mắt. Anne Barge th&agrave;nh lập năm 1999, l&agrave; thương hiệu thời trang cưới l&acirc;u đời v&agrave; nổi tiếng tại Mỹ. Thương hiệu n&agrave;y từng đồng h&agrave;nh trong lễ cưới của nhiều ng&ocirc;i sao như Laura Benanti, Johanna Braddy, Kristin Cavallari, Yvonne Orji...</p>\r\n</figcaption>\r\n</figure>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-16-4670-1655610916.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=C3dHiY5sr8lQ9t2P9oKmlA 1x, https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-16-4670-1655610916.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=qJ2--_rCf01AccjxWil2IA 1.5x, https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-16-4670-1655610916.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=BgeMPY8BNkhKLDPmPUbI4w 2x\" data-srcset=\"https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-16-4670-1655610916.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=C3dHiY5sr8lQ9t2P9oKmlA 1x, https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-16-4670-1655610916.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=qJ2--_rCf01AccjxWil2IA 1.5x, https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-16-4670-1655610916.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=BgeMPY8BNkhKLDPmPUbI4w 2x\"><img class=\"lazy lazied\" src=\"https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-16-4670-1655610916.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=C3dHiY5sr8lQ9t2P9oKmlA\" alt=\"Chung Thanh Phong thiết kế cho c&ocirc; d&acirc;u chiếc đầm đa năng, c&oacute; thể biến h&oacute;a th&agrave;nh hai bộ kh&aacute;c nhau, ph&ugrave; hợp với tiệc ngo&agrave;i trời. Chiếc v&aacute;y được c&ocirc; d&acirc;u mặc nhảy mở m&agrave;n v&agrave; tiệc đ&atilde;i kh&aacute;ch. C&ocirc; giữ b&iacute; mật với chồng về trang phục, g&acirc;y bất ngờ cho anh trong m&agrave;n first dance. Điểm ph&aacute; c&aacute;ch của thiết kế nằm ở t&ugrave;ng v&aacute;y mullet l&agrave;m từ vải satin, nhấn bằng nơ to bản, Minh Hằng phối bộ đầm c&ugrave;ng găng tay đ&iacute;nh kết pha l&ecirc; v&agrave; l&ocirc;ng vũ, tạo n&ecirc;n tổng thể h&agrave;i h&ograve;a đậm chất cổ điển. Thiết kế mất một th&aacute;ng ho&agrave;n thiện với s&aacute;u người thợ l&agrave;nh nghề.\" loading=\"lazy\" data-src=\"https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-16-4670-1655610916.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=C3dHiY5sr8lQ9t2P9oKmlA\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Chung Thanh Phong thiết kế cho c&ocirc; d&acirc;u chiếc đầm đa năng, c&oacute; thể biến h&oacute;a th&agrave;nh hai bộ kh&aacute;c nhau, ph&ugrave; hợp với tiệc ngo&agrave;i trời. Chiếc v&aacute;y được c&ocirc; d&acirc;u mặc nhảy mở m&agrave;n v&agrave; tiệc đ&atilde;i kh&aacute;ch. C&ocirc; giữ b&iacute; mật với chồng về trang phục, g&acirc;y bất ngờ cho anh trong m&agrave;n \"first dance\". Điểm ph&aacute; c&aacute;ch của thiết kế nằm ở t&ugrave;ng v&aacute;y mullet l&agrave;m từ vải satin, nhấn bằng nơ to bản, Minh Hằng phối bộ đầm c&ugrave;ng găng tay đ&iacute;nh kết pha l&ecirc; v&agrave; l&ocirc;ng vũ, tạo tổng thể h&agrave;i h&ograve;a đậm chất cổ điển. Thiết kế được s&aacute;u người thợ ho&agrave;n thiện trong một th&aacute;ng.</p>\r\n</figcaption>\r\n</figure>\r\n<div>\r\n<figure class=\"item_slide_show clearfix\">\r\n<div id=\"video_parent_357171\" class=\"box_embed_video_parent embed_video_new\" data-vcate=\"1003834\" data-vscate=\"1003007\" data-vscaten=\"Giải tr&iacute;\" data-vid=\"357171\" data-ratio=\"1\" data-articleoriginal=\"4477724\" data-ads=\"1\" data-license=\"0\" data-duration=\"103\" data-brandsafe=\"0\" data-type=\"0\" data-play=\"1\" data-frame=\"\" data-aot=\"\" data-videoclassify=\"None\" data-initdom=\"1\" data-view=\"inview\" data-auto=\"1\" data-status=\"play\">\r\n<div id=\"embed_video_357171\" class=\"box_embed_video\">\r\n<div class=\"bg_overlay_small_unpin\">&nbsp;</div>\r\n</div>\r\n</div>\r\n</figure>\r\n</div>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"action_thumb flexbox\">&nbsp;</div>\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-11-5084-1655610916.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=4Rv0f9-1_u6B6SyPWJ14jw 1x, https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-11-5084-1655610916.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=dgE9qOhEaw3sKM4C9X76sQ 1.5x, https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-11-5084-1655610916.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=zLJzyz92hNpL-lsjRvdxwA 2x\" data-srcset=\"https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-11-5084-1655610916.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=4Rv0f9-1_u6B6SyPWJ14jw 1x, https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-11-5084-1655610916.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=dgE9qOhEaw3sKM4C9X76sQ 1.5x, https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-11-5084-1655610916.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=zLJzyz92hNpL-lsjRvdxwA 2x\"><img class=\"lazy lazied\" src=\"https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-11-5084-1655610916.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=4Rv0f9-1_u6B6SyPWJ14jw\" alt=\"Ban đầu, c&ocirc; d&acirc;u diện bộ đầm d&agrave;i, l&ecirc;n s&acirc;n khấu biểu diễn trước kh&aacute;ch mời. Sau đ&oacute;, người đẹp th&aacute;o ch&acirc;n v&aacute;y, để lộ thiết kế xuy&ecirc;n thấu b&ecirc;n trong. Chung Thanh Phong tự s&aacute;ng tạo l&ecirc;n hoa văn v&agrave; vải ren bằng c&aacute;ch th&ecirc;u thủ c&ocirc;ng tr&ecirc;n nền vải lưới m&agrave;u nude. Bộ đầm th&ecirc;m lộng lẫy với phần đ&iacute;nh kết hạt đ&aacute; v&agrave; pha l&ecirc; trong suốt tr&ecirc;n họa tiết. Khung corset b&ecirc;n trong v&aacute;y nhằm n&acirc;ng ngực, siết eo, gi&uacute;p t&ocirc;n đường cong của c&ocirc; d&acirc;u. V&aacute;y d&aacute;ng ngắn cũng khiến Minh Hằng thuận tiện trong di chuyển.\" loading=\"lazy\" data-src=\"https://i1-giaitri.vnecdn.net/2022/06/19/minh-hang-11-5084-1655610916.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=4Rv0f9-1_u6B6SyPWJ14jw\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Ban đầu, c&ocirc; d&acirc;u diện bộ đầm d&agrave;i, l&ecirc;n s&acirc;n khấu biểu diễn trước kh&aacute;ch mời. Sau đ&oacute;, người đẹp th&aacute;o ch&acirc;n v&aacute;y, để lộ thiết kế xuy&ecirc;n thấu b&ecirc;n trong. Chung Thanh Phong s&aacute;ng tạo hoa văn v&agrave; vải ren bằng c&aacute;ch th&ecirc;u thủ c&ocirc;ng tr&ecirc;n nền vải lưới m&agrave;u nude. Bộ đầm th&ecirc;m lộng lẫy với phần đ&iacute;nh kết hạt đ&aacute; v&agrave; pha l&ecirc; trong suốt. Khung corset b&ecirc;n trong v&aacute;y nhằm n&acirc;ng ngực, siết eo, gi&uacute;p t&ocirc;n đường cong của c&ocirc; d&acirc;u. V&aacute;y d&aacute;ng ngắn cũng khiến Minh Hằng thuận tiện trong di chuyển.</p>\r\n</figcaption>\r\n</figure>\r\n</article>', 218, 8, 0, 1, '2022-11-21 19:35:42', '2022-12-08 08:57:22'),
(145, 'Những con chip có thể khiến iPhone sợ xanh mặt', 'nhung-con-chip-co-the-khien-iphone-so-xanh-mat', 'Theo các báo cáo mới nhất, các chip 3nm của TSMC sẽ ra mắt vào năm 2023, chip quy trình 2nm xuất hiện vào năm 2025.', '<p>Mới đ&acirc;y, TSMC đ&atilde; tr&igrave;nh b&agrave;y chi tiết lịch tr&igrave;nh ph&aacute;t triển chip của m&igrave;nh tại Hội nghị chuy&ecirc;n đề c&ocirc;ng nghệ TSMC năm 2022. Nh&agrave; sản xuất chip c&oacute; trụ sở tại Đ&agrave;i Loan sẽ giới thiệu chip 3nm v&agrave;o nửa cuối năm nay v&agrave; sẽ đưa c&ocirc;ng nghệ 2nm l&ecirc;n s&acirc;n khấu thế giới v&agrave;o năm 2025.</p>\r\n<p align=\"center\">&nbsp;</p>\r\n<div id=\"container-24h-banner-in-image\">\r\n<div>\r\n<div><img class=\"news-image initial loaded\" src=\"https://icdn.24h.com.vn/upload/2-2022/images/2022-06-18/gsmarena_001-1655547381-224-width740height415.jpg\" alt=\"Những con chip c&oacute; thể khiến iPhone sợ xanh mặt - 1\" data-was-processed=\"true\"></div>\r\n<div id=\"24h-banner-in-image\">\r\n<div id=\"OeKXyRDkSW_139_15s\" class=\"txtCent\">\r\n<div class=\"text_adver_right\">&nbsp;</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n<p>&nbsp;</p>\r\n<p class=\"img_chu_thich_0407\">5 cấp độ của chip quy tr&igrave;nh 3nm.</p>\r\n<p>Quy tr&igrave;nh 3nm sẽ c&oacute; năm cấp, c&aacute;c cấp sẽ dần mạnh hơn, mật độ b&oacute;ng b&aacute;n dẫn cao hơn v&agrave; hiệu quả hơn - N3, N3E (N&acirc;ng cao), N3P (Tăng cường hiệu suất), N3S (Tăng cường mật độ) v&agrave; N3X (Hiệu suất cực cao).</p>\r\n<p align=\"center\"><img class=\"news-image initial loaded\" src=\"https://icdn.24h.com.vn/upload/2-2022/images/2022-06-18/gsmarena_002-1655547381-368-width740height416.jpg\" alt=\"Những con chip c&oacute; thể khiến iPhone sợ xanh mặt - 3\" data-original=\"https://icdn.24h.com.vn/upload/2-2022/images/2022-06-18/gsmarena_002-1655547381-368-width740height416.jpg\" data-was-processed=\"true\"></p>\r\n<p class=\"img_chu_thich_0407\">Chip N2 sẽ mạnh hơn đ&aacute;ng kể so với thế hệ N3.</p>\r\n<p>Đối với quy tr&igrave;nh 2nm, chip sẽ tăng hiệu suất từ ​​10 - 15% ở c&ugrave;ng một mức ti&ecirc;u thụ điện năng v&agrave; mang lại mức ti&ecirc;u thụ thấp hơn 25 - 30% ở c&ugrave;ng tần số v&agrave; số lượng b&oacute;ng b&aacute;n dẫn so với chip N3E. N2 sẽ tăng mật độ chip so với chip N3E l&ecirc;n 1,1 lần.</p>\r\n<p align=\"center\"><img class=\"news-image initial loaded\" src=\"https://icdn.24h.com.vn/upload/2-2022/images/2022-06-18/gsmarena_003-1655547381-217-width740height415.jpg\" alt=\"Những con chip c&oacute; thể khiến iPhone sợ xanh mặt - 4\" data-original=\"https://icdn.24h.com.vn/upload/2-2022/images/2022-06-18/gsmarena_003-1655547381-217-width740height415.jpg\" data-was-processed=\"true\"></p>\r\n<p class=\"img_chu_thich_0407\">Tốc độ sẽ tăng dần theo từng thế hệ chip.</p>\r\n<p>TSMC đ&atilde; giới thiệu GAAFETs (gate-all-around field-effect transistors). C&aacute;c b&oacute;ng b&aacute;n dẫn nano mới sẽ tăng hiệu suất tr&ecirc;n mỗi watt bằng c&aacute;ch giảm điện trở.</p>\r\n<p align=\"center\"><img class=\"news-image initial loaded\" src=\"https://icdn.24h.com.vn/upload/2-2022/images/2022-06-18/samsung-galaxy-s23-ultra-concept-face-1655547381-204-width740height416.jpg\" alt=\"Những con chip c&oacute; thể khiến iPhone sợ xanh mặt - 5\" data-original=\"https://icdn.24h.com.vn/upload/2-2022/images/2022-06-18/samsung-galaxy-s23-ultra-concept-face-1655547381-204-width740height416.jpg\" data-was-processed=\"true\"></p>\r\n<p class=\"img_chu_thich_0407\">Chip 3nm sẽ được t&iacute;ch hợp v&agrave;o loạt Galaxy S23.</p>\r\n<p>Trong khi đ&oacute;,&nbsp;<a class=\"TextlinkBaiviet\" title=\"Samsung\" href=\"https://www.24h.com.vn/samsung-c407e3988.html\">Samsung</a>&nbsp;Foundry cũng sẽ bắt đầu sản xuất h&agrave;ng loạt chip 3nm v&agrave;o năm 2022 &ndash; dự kiến t&iacute;ch hợp v&agrave;o Galaxy S23 Series, kế hoạch sản xuất chip 2nm sẽ được triển khai v&agrave;o năm 2025 &ndash; c&oacute; thể trang bị tr&ecirc;n d&ograve;ng&nbsp;<a class=\"TextlinkBaiviet\" title=\"smartphone\" href=\"https://www.24h.com.vn/smartphone-c407e4053.html\">smartphone</a>&nbsp;cao cấp Galaxy S25.</p>', 218, 10, 8, 1, '2022-11-21 19:35:42', '2023-02-06 20:44:59'),
(148, 'Cảnh sát phong tỏa cây xăng ở Bà Rịa – Vũng Tàu', 'canh-sat-phong-toa-cay-xang-o-ba-ria-vung-tau', 'Nhiều cảnh sát phong tỏa cây xăng Gia Khiêm ở thị xã Phú Mỹ, để đồng nghiệp khám xét, tìm tài liệu, chứng cứ liên quan nghi án sản xuất xăng giả, sáng 19/6.', '<p class=\"description\">Nhiều cảnh s&aacute;t phong tỏa c&acirc;y xăng Gia Khi&ecirc;m ở thị x&atilde; Ph&uacute; Mỹ, để đồng nghiệp kh&aacute;m x&eacute;t, t&igrave;m t&agrave;i liệu, chứng cứ li&ecirc;n quan nghi &aacute;n sản xuất xăng giả, s&aacute;ng 19/6.</p>\r\n<article class=\"fck_detail \">\r\n<p class=\"Normal\">Cửa h&agrave;ng xăng dầu Gia Khi&ecirc;m thuộc C&ocirc;ng ty TNHH Thương mại Vận tải Gia Khi&ecirc;m, bị ngừng hoạt động. Cảnh s&aacute;t căng d&acirc;y che chắn, kh&ocirc;ng cho người ngo&agrave;i lại gần. B&ecirc;n trong, nhiều c&aacute;n bộ l&agrave;m việc với nh&acirc;n vi&ecirc;n; đồng thời lấy mẫu, đo đạc, c&aacute;c cột xăng bị ni&ecirc;m phong...</p>\r\n<p class=\"Normal\">Đến hơn 10h, Ph&oacute; gi&aacute;m đốc C&ocirc;ng an tỉnh B&agrave; Rịa &ndash; Vũng T&agrave;u đến hiện trường, chỉ đạo c&aacute;c c&ocirc;ng việc, thu thập t&agrave;i liệu, chứng cứ.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/19/xang-gia-2443-1655612021.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=tl3FlixNhtQb1QuavcHe1A 1x, https://i1-vnexpress.vnecdn.net/2022/06/19/xang-gia-2443-1655612021.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=kbDBHSoOpPf_GezWeuW1wQ 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/19/xang-gia-2443-1655612021.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=4fGB9piz-jfib27UpRtACQ 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/19/xang-gia-2443-1655612021.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=tl3FlixNhtQb1QuavcHe1A 1x, https://i1-vnexpress.vnecdn.net/2022/06/19/xang-gia-2443-1655612021.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=kbDBHSoOpPf_GezWeuW1wQ 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/19/xang-gia-2443-1655612021.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=4fGB9piz-jfib27UpRtACQ 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/06/19/xang-gia-2443-1655612021.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=tl3FlixNhtQb1QuavcHe1A\" alt=\"Cảnh s&aacute;t t&uacute;c trực tại c&acirc;y xăng Gia Khi&ecirc;m s&aacute;ng 19/6. Ảnh: Trường H&agrave;\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/06/19/xang-gia-2443-1655612021.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=tl3FlixNhtQb1QuavcHe1A\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Cảnh s&aacute;t phong toả c&acirc;y xăng Gia Khi&ecirc;m s&aacute;ng 19/6. Ảnh:&nbsp;<em>Trường H&agrave;</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Người d&acirc;n sống tại khu vực cho biết, từ 20h h&ocirc;m qua, cảnh s&aacute;t đ&atilde; đến l&agrave;m việc tại cửa h&agrave;ng xăng dầu. Họ đến kh&aacute; đ&ocirc;ng, l&agrave;m việc đến khuya.</p>\r\n<p class=\"Normal\">L&atilde;nh đạo C&ocirc;ng an B&agrave; Rịa &ndash; Vũng T&agrave;u cho biết, C&ocirc;ng ty Gia Khi&ecirc;m bị nghi sản xuất xăng giả số lượng lớn. Lực lượng chức năng đang l&agrave;m việc với những người li&ecirc;n quan, chưa thể cung cấp th&ocirc;ng tin cụ thể.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/19/lau-mau-4271-1655612021.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=TFdTdXF3rwHjmTDLuAsCdg 1x, https://i1-vnexpress.vnecdn.net/2022/06/19/lau-mau-4271-1655612021.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=LM1zynuXCsRNp_4y49blag 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/19/lau-mau-4271-1655612021.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=WmA-CVezODygnRVflCAcEQ 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/19/lau-mau-4271-1655612021.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=TFdTdXF3rwHjmTDLuAsCdg 1x, https://i1-vnexpress.vnecdn.net/2022/06/19/lau-mau-4271-1655612021.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=LM1zynuXCsRNp_4y49blag 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/19/lau-mau-4271-1655612021.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=WmA-CVezODygnRVflCAcEQ 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/06/19/lau-mau-4271-1655612021.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=TFdTdXF3rwHjmTDLuAsCdg\" alt=\"Cảnh s&aacute;t lấy mẫu xe bồn, tối 18/6. Ảnh: Người d&acirc;n cung cấp\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/06/19/lau-mau-4271-1655612021.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=TFdTdXF3rwHjmTDLuAsCdg\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Cảnh s&aacute;t lấy mẫu xăng trong xe bồn, tối 18/6. Ảnh:&nbsp;<em>Người d&acirc;n cung cấp</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">C&ocirc;ng an tỉnh B&agrave; Rịa &ndash; Vũng T&agrave;u đ&atilde; bắt một số nghi can, theo b&aacute;o&nbsp;<em>B&agrave; Rịa - Vũng T&agrave;u.&nbsp;</em>Họ bị c&aacute;o buộc sản xuất xăng giả bằng c&aacute;ch mua h&oacute;a chất pha trộn với dung m&ocirc;i th&agrave;nh xăng A95 để b&aacute;n ra thị trường.</p>\r\n<p class=\"Normal\">Vụ &aacute;n đang được mở rộng điều tra.</p>\r\n</article>\r\n<p>Ut alias et saepe aperiam laboriosam sit. Impedit hic voluptatum amet facilis quae nisi. Animi iure occaecati tempore architecto porro aut consequatur. Natus et qui corporis minima sunt asperiores praesentium vel.</p>', 218, 9, 1, 1, '2022-11-21 19:35:42', '2023-02-08 00:36:20'),
(149, 'Phát hiện sóng bí ẩn trong Mặt trời với tốc độ khủng khiếp', 'phat-hien-song-bi-an-trong-mat-troi-voi-toc-do-khung-khiep', 'Các nhà nghiên cứu phát hiện ra một loại sóng bí ẩn trong mặt trời với tốc độ không giải thích được.', '<div>\r\n<div>\r\n<p>C&aacute;c nh&agrave; nghi&ecirc;n cứu từ Trung t&acirc;m Khoa học Kh&ocirc;ng gian của NYU Abu Dhabi (NYUAD) đ&atilde; ph&aacute;t hiện ra một tập hợp&nbsp;<strong>s&oacute;ng mới tr&ecirc;n mặt trời</strong>, bất ngờ thay, n&oacute; dường như di chuyển nhanh hơn nhiều so với dự đo&aacute;n của l&yacute; thuyết.</p>\r\n</div>\r\n<div>\r\n<p>Trong nghi&ecirc;n cứu \"Kh&aacute;m ph&aacute; s&oacute;ng xo&aacute;y nghịch lưu tần số cao trong mặt trời\", được c&ocirc;ng bố tr&ecirc;n tạp ch&iacute; Nature Astronomy, c&aacute;c nh&agrave; nghi&ecirc;n cứu - do Ph&oacute; nghi&ecirc;n cứu Chris S. Hanson dẫn đầu đ&atilde; tr&igrave;nh b&agrave;y chi tiết c&aacute;ch họ ph&acirc;n t&iacute;ch dữ liệu tr&ecirc;n mặt đất v&agrave; kh&ocirc;ng gian trong 25 năm để ph&aacute;t hiện c&aacute;c s&oacute;ng n&agrave;y.</p>\r\n</div>\r\n<div>\r\n<p>C&aacute;c s&oacute;ng ngược d&ograve;ng tần số cao (HFR), di chuyển theo hướng ngược lại với chiều quay của mặt trời, xuất hiện như một dạng xo&aacute;y (chuyển động xo&aacute;y) tr&ecirc;n bề mặt của mặt trời, v&agrave; di chuyển với tốc độ gấp ba lần tốc độ được thiết lập bởi l&yacute; thuyết hiện tại.</p>\r\n</div>\r\n<div>\r\n<div>&nbsp;</div>\r\n<p>Kh&ocirc;ng thể chụp ảnh b&ecirc;n trong của mặt trời v&agrave; c&aacute;c ng&ocirc;i sao bằng thi&ecirc;n văn học th&ocirc;ng thường (v&iacute; dụ như quang học, tia X, v.v.), v&agrave; c&aacute;c nh&agrave; khoa học dựa v&agrave;o việc giải th&iacute;ch c&aacute;c dấu hiệu bề mặt của nhiều loại s&oacute;ng để x&aacute;c định đặc điểm b&ecirc;n trong. Những s&oacute;ng HFR mới n&agrave;y c&oacute; thể vẫn l&agrave; một mảnh gh&eacute;p quan trọng trong hiểu biết của ch&uacute;ng ta về c&aacute;c v&igrave; sao cũng như Hệ mặt trời.</p>\r\n</div>\r\n<div>\r\n<div><img src=\"https://tin.moi/uploads/news/photo/Q8/Q8X/Q8XukVZCWL-1500x864.jpg\" height=\"500\"></div>\r\n<p>Sự tương t&aacute;c phức tạp giữa c&aacute;c s&oacute;ng nổi tiếng kh&aacute;c v&agrave; từ t&iacute;nh, lực hấp dẫn hoặc đối lưu c&oacute; thể th&uacute;c đẩy s&oacute;ng HFR di chuyển ở tốc độ n&agrave;y. Hanson cho biết: &ldquo;Nếu s&oacute;ng HFR c&oacute; thể l&agrave; do bất kỳ qu&aacute; tr&igrave;nh n&agrave;o trong ba qu&aacute; tr&igrave;nh n&agrave;y, th&igrave; ph&aacute;t hiện n&agrave;y sẽ giải đ&aacute;p được một số c&acirc;u hỏi c&ograve;n bỏ ngỏ m&agrave; ch&uacute;ng ta vẫn c&ograve;n thắc mắc về&nbsp;<em>mặt trời</em>\".</p>\r\n</div>\r\n<div>\r\n<div>&nbsp;</div>\r\n<p>Tuy nhi&ecirc;n, những l&agrave;n s&oacute;ng HFR mới n&agrave;y dường như kh&ocirc;ng phải l&agrave; kết quả của những qu&aacute; tr&igrave;nh tr&ecirc;n, v&agrave; điều đ&oacute; thật th&uacute; vị v&igrave; n&oacute; sẽ dẫn đến một loạt c&acirc;u hỏi ho&agrave;n to&agrave;n mới.</p>\r\n</div>\r\n<div>\r\n<div>&nbsp;</div>\r\n<p>Bằng c&aacute;ch nghi&ecirc;n cứu động lực học b&ecirc;n trong của mặt trời th&ocirc;ng qua việc sử dụng s&oacute;ng HFR, c&aacute;c nh&agrave; khoa học c&oacute; thể đ&aacute;nh gi&aacute; tốt hơn t&aacute;c động tiềm t&agrave;ng của mặt trời đối với Tr&aacute;i đất, v&agrave; c&aacute;c h&agrave;nh tinh kh&aacute;c trong&nbsp;hệ mặt trời&nbsp;của ch&uacute;ng ta.</p>\r\n</div>\r\n<div>\r\n<div>&nbsp;</div>\r\n<p>Shravan Hanasoge, đồng t&aacute;c giả của b&agrave;i b&aacute;o cho biết: &ldquo;Sự tồn tại của c&aacute;c chế độ s&oacute;ng HFR v&agrave; nguồn gốc của ch&uacute;ng l&agrave; một b&iacute; ẩn thực sự v&agrave; c&oacute; thể &aacute;m chỉ đến vật l&yacute; th&uacute; vị khi nghi&ecirc;n cứu. N&oacute; c&oacute; khả năng l&agrave;m s&aacute;ng tỏ c&aacute;i nh&igrave;n s&acirc;u sắc về b&ecirc;n trong mặt trời kh&ocirc;ng thể quan s&aacute;t được&rdquo;.</p>\r\n</div>\r\n</div>', 218, 11, 1, 1, '2022-11-21 19:35:42', '2023-02-08 00:36:17'),
(150, 'Trợ lý ảo, trí tuệ nhân tạo lần đầu tiên được sử dụng tại Tòa án nhân dân tối cao', 'tro-ly-ao-tri-tue-nhan-tao-lan-dau-tien-duoc-su-dung-tai-toa-an-nhan-dan-toi-cao', 'Trợ lý ảo, trí tuệ nhân tạo lần đầu tiên được sử dụng tại Tòa án nhân dân tối cao', '<p>Tập đo&agrave;n C&ocirc;ng nghiệp -&nbsp;Viễn th&ocirc;ng Qu&acirc;n đội (Viettel) vừa đưa v&agrave;o vận h&agrave;nh Trung t&acirc;m gi&aacute;m s&aacute;t v&agrave; điều h&agrave;nh hoạt động T&ograve;a &aacute;n nh&acirc;n d&acirc;n cho T&ograve;a &aacute;n nh&acirc;n d&acirc;n tối cao.&nbsp;Đ&acirc;y l&agrave; một trong những hệ thống trọng t&acirc;m nhằm th&uacute;c đẩy chuyển đổi số T&ograve;a &aacute;n, hướng đến x&acirc;y dựng T&ograve;a &aacute;n điện tử.</p>\r\n<p>C&ugrave;ng với đ&oacute;, phần mềm Trợ l&yacute; ảo hỗ trợ cho Thẩm ph&aacute;n v&agrave; Nền tảng x&eacute;t xử trực tuyến cũng đ&atilde; được&nbsp;<a class=\"TextlinkBaiviet\" title=\"Viettel\" href=\"https://www.24h.com.vn/viettel-c55e5697.html\">Viettel</a>&nbsp;c&ugrave;ng T&ograve;a &aacute;n nh&acirc;n d&acirc;n tối cao đưa v&agrave;o triển khai phục vụ hoạt động nghiệp vụ.&nbsp;Dự &aacute;n do Viettel phối hợp với T&ograve;a &aacute;n nh&acirc;n d&acirc;n tối cao thiết kế, x&acirc;y dựng, t&iacute;ch hợp v&agrave; triển khai, gi&uacute;p theo d&otilde;i, gi&aacute;m s&aacute;t, điều h&agrave;nh hoạt động của gần 770 t&ograve;a &aacute;n nh&acirc;n d&acirc;n tr&ecirc;n to&agrave;n quốc.</p>\r\n<p>&nbsp;</p>\r\n<div id=\"container-24h-banner-in-image\">\r\n<div>\r\n<div><img class=\"news-image initial loading\" src=\"https://icdn.24h.com.vn/upload/1-2022/images/2022-01-10/anh-2-1641802434-640-width660height440.jpg\" alt=\"Trợ l&yacute; ảo, tr&iacute; tuệ nh&acirc;n tạo lần đầu ti&ecirc;n được sử dụng tại T&ograve;a &aacute;n nh&acirc;n d&acirc;n tối cao - 1\" data-was-processed=\"true\"></div>\r\n<div id=\"24h-banner-in-image\">\r\n<div id=\"XgHoOqkwCh_139_15s\" class=\"txtCent\">\r\n<div class=\"text_adver_right\">&nbsp;</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n<p>&nbsp;</p>\r\n<p class=\"img_chu_thich_0407\">C&aacute;c đại biểu thăm v&agrave; l&agrave;m việc tại Trung t&acirc;m Gi&aacute;m s&aacute;t v&agrave; điều h&agrave;nh hoạt động TAND.</p>\r\n<p>Theo Viettel, Trung t&acirc;m gi&aacute;m s&aacute;t v&agrave; điều h&agrave;nh hoạt động T&ograve;a &aacute;n nh&acirc;n d&acirc;n l&agrave; bộ n&atilde;o số của t&ograve;a &aacute;n. Nền tảng n&agrave;y t&iacute;ch hợp dữ liệu v&agrave; c&aacute;c hệ thống sẵn c&oacute; trong to&agrave;n bộ ng&agrave;nh t&ograve;a &aacute;n với c&aacute;c phần mềm điều khiển trung t&acirc;m, đảm bảo vận h&agrave;nh th&ocirc;ng suốt, c&ocirc;ng khai, minh bạch, dễ d&agrave;ng theo d&otilde;i, gi&aacute;m s&aacute;t v&agrave; đ&aacute;nh gi&aacute; hoạt động của c&aacute;c t&ograve;a &aacute;n nh&acirc;n d&acirc;n.</p>\r\n<p>\"Nền tảng x&eacute;t xử trực tuyến l&agrave; một trong những giải ph&aacute;p hiệu quả gi&uacute;p khắc phục những kh&oacute; khăn trong bối cảnh đại dịch COVID-19 diễn biến phức tạp. Th&ocirc;ng qua Hệ thống x&eacute;t xử trực tuyến, c&oacute; thể gi&uacute;p giảm thiểu thời gian cho người d&acirc;n, cơ quan, tổ chức, từ đ&oacute; tiết kiệm chi ph&iacute; cho x&atilde; hội\", Viettel cho biết.</p>\r\n<p>Hệ thống phần mềm trợ l&yacute; ảo c&oacute; khả năng thực hiện c&aacute;c y&ecirc;u cầu t&igrave;m kiếm th&ocirc;ng tin dựa tr&ecirc;n c&acirc;u hỏi, khẩu lệnh của thẩm ph&aacute;n nhằm hỗ trợ c&ocirc;ng t&aacute;c tra cứu v&agrave; ra quyết định , r&uacute;t ngắn thời gian xử l&yacute; vụ việc. Hơn 100 thẩm ph&aacute;n, chuy&ecirc;n gia đ&atilde; tham gia v&agrave;o qu&aacute; tr&igrave;nh thẩm định nội dung, th&ocirc;ng minh h&oacute;a hệ thống nhờ c&ocirc;ng nghệ AI. Trong thời gian tới, trợ l&yacute; ảo được kỳ vọng c&oacute; khả năng hỗ trợ đo&aacute;n định tư ph&aacute;p, c&oacute; thể trả lời về tội danh hoặc dạng tranh chấp d&acirc;n sự dựa tr&ecirc;n dữ liệu về h&agrave;nh vi, t&igrave;nh huống ph&aacute;p l&yacute;...</p>\r\n<p>&Ocirc;ng Nguyễn Mạnh Hổ -&nbsp;Tổng gi&aacute;m đốc Tổng c&ocirc;ng ty Giải ph&aacute;p Doanh nghiệp Viettel cho biết th&ecirc;m,&nbsp;để triển khai c&aacute;c giải gi&aacute;p cho To&agrave; &aacute;n, Viettel đ&atilde; &aacute;p dụng những c&ocirc;ng nghệ hiện đại nhất như ph&acirc;n t&iacute;ch dữ liệu, tr&iacute; tuệ nh&acirc;n tạo v&agrave; c&aacute;c c&ocirc;ng nghệ 4.0, với mục ti&ecirc;u tạo ra một trợ l&yacute; th&ocirc;ng minh, đưa ra c&aacute;c ph&acirc;n t&iacute;ch v&agrave; c&aacute;c hệ tư duy cho từng trường hợp cụ thể, hỗ trợ c&aacute;c Thẩm ph&aacute;n trong qu&aacute; tr&igrave;nh ra quyết định cuối c&ugrave;ng.</p>', 218, 10, 1, 1, '2022-11-21 19:35:42', '2023-02-06 20:43:51'),
(151, 'Cảnh sát mất việc vì hình xăm gây \'thù địch\'', 'canh-sat-mat-viec-vi-hinh-xam-gay-thu-dich', 'MỸSĩ quan Eric Weyda, 50 tuổi, bị sa thải vì xăm các hình ảnh, thông điệp \"sắc thái thù địch\" lên đầu, cổ và bàn tay.', '<p class=\"description\"><span class=\"location-stamp\">MỸ</span>Sĩ quan Eric Weyda, 50 tuổi, bị sa thải v&igrave; xăm c&aacute;c h&igrave;nh ảnh, th&ocirc;ng điệp \"sắc th&aacute;i th&ugrave; địch\" l&ecirc;n đầu, cổ v&agrave; b&agrave;n tay.</p>\r\n<article class=\"fck_detail \">\r\n<p class=\"Normal\">Ph&aacute;t ng&ocirc;n vi&ecirc;n của Sở Cảnh s&aacute;t Cincinnati x&aacute;c nhận h&ocirc;m 15/6 Weyda đ&atilde; bị sa thải sau một cuộc điều tra nội bộ. Cựu sĩ quan đ&atilde; nộp đơn khiếu nại l&ecirc;n c&ocirc;ng đo&agrave;n cảnh s&aacute;t Cincinnati.</p>\r\n<p class=\"Normal\">Hồ sơ cho thấy, Weyda v&agrave;o ng&agrave;nh năm 2006. Thời điểm đ&oacute;, Weyda c&oacute; kh&aacute; nhiều h&igrave;nh xăm, nhưng đều tu&acirc;n thủ quy định của Sở Cảnh s&aacute;t Cincinnati.</p>\r\n<p class=\"Normal\">Đến năm 2013, Sở cập nhật th&ecirc;m một số y&ecirc;u cầu trong bộ quy tắc ứng xử, trong đ&oacute; cấm sĩ quan c&oacute; h&igrave;nh xăm ở mặt, đầu, cổ, g&aacute;y hoặc tay. Weyda bị c&aacute;o buộc đ&atilde; biết việc cấm xăm h&igrave;nh, nhưng phớt lờ.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/18/3af61f5a-d2da-4960-ba1e-66fc73-9564-9355-1655563736.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=S8TPljHbd3D5BK-64ZL_zw 1x, https://i1-vnexpress.vnecdn.net/2022/06/18/3af61f5a-d2da-4960-ba1e-66fc73-9564-9355-1655563736.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=AX8MLPN4es5fkfi8BJe8nA 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/18/3af61f5a-d2da-4960-ba1e-66fc73-9564-9355-1655563736.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=Wq0J37-YsOugcfP88oL2RA 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/18/3af61f5a-d2da-4960-ba1e-66fc73-9564-9355-1655563736.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=S8TPljHbd3D5BK-64ZL_zw 1x, https://i1-vnexpress.vnecdn.net/2022/06/18/3af61f5a-d2da-4960-ba1e-66fc73-9564-9355-1655563736.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=AX8MLPN4es5fkfi8BJe8nA 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/18/3af61f5a-d2da-4960-ba1e-66fc73-9564-9355-1655563736.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=Wq0J37-YsOugcfP88oL2RA 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/06/18/3af61f5a-d2da-4960-ba1e-66fc73-9564-9355-1655563736.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=S8TPljHbd3D5BK-64ZL_zw\" alt=\"H&igrave;nh xưm tr&ecirc;n b&agrave;n tay sĩ quan cảnh s&aacute;t Eric Weyda. Ảnh: Cincinnati Police Department\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/06/18/3af61f5a-d2da-4960-ba1e-66fc73-9564-9355-1655563736.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=S8TPljHbd3D5BK-64ZL_zw\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">H&igrave;nh xưm tr&ecirc;n b&agrave;n tay sĩ quan cảnh s&aacute;t Eric Weyda. Ảnh:&nbsp;<em>Cincinnati Police Department</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Weyda đ&atilde; xăm tr&ecirc;n cổ, đầu, ban tay \"theo kiểu h&igrave;nh học bất thường\" n&ecirc;n bị chuyển đến đơn vị tạm giữ như một biện ph&aacute;p tạm thời hạn chế tiếp x&uacute;c với c&ocirc;ng ch&uacute;ng.</p>\r\n<p class=\"Normal\">Kh&ocirc;ng l&acirc;u sau, Sở Cảnh s&aacute;t nhận được đơn khiếu nại từ một người b&aacute;n h&agrave;ng trong th&agrave;nh phố về những h&igrave;nh xăm của Weyda, y&ecirc;u cầu xem x&eacute;t lại tư c&aacute;ch của sĩ quan n&agrave;y.</p>\r\n<p class=\"Normal\">Trong buổi điều trần v&agrave;o th&aacute;ng 4, Weyda n&oacute;i \"kh&ocirc;ng hối hận\", từ chối giải th&iacute;ch &yacute; nghĩa những h&igrave;nh xăm. Anh chỉ n&oacute;i với c&aacute;c nh&agrave; điều tra những từ được xăm tr&ecirc;n tay thể hiện \"cuộc đấu tranh giữa thiện v&agrave; &aacute;c\".</p>\r\n<p class=\"Normal\">Tuy nhi&ecirc;n, l&atilde;nh đạo Sở Cảnh s&aacute;t cho rằng th&ocirc;ng điệp của những h&igrave;nh xăm c&oacute; thể bị c&ocirc;ng ch&uacute;ng xem l&agrave; th&ugrave; địch. \"Rất đơn giản, b&agrave;n tay của một sĩ quan cảnh s&aacute;t n&ecirc;n được coi l&agrave; biểu tượng của sự an to&agrave;n v&agrave; an ninh hơn l&agrave; th&ocirc;ng điệp khi&ecirc;u kh&iacute;ch về sự sa đọa v&agrave; tổn hại\", l&atilde;nh đạo Sở n&oacute;i.</p>\r\n<p class=\"Normal\">Chủ tịch C&ocirc;ng đo&agrave;n cảnh s&aacute;t Cincinnati chưa hồi &acirc;m về khiếu nại của Weyda.</p>\r\n</article>', 218, 9, 0, 1, '2022-11-21 19:35:42', '2022-11-22 06:00:01');
INSERT INTO `posts` (`id`, `title`, `slug`, `excerpt`, `body`, `user_id`, `category_id`, `views`, `approved`, `created_at`, `updated_at`) VALUES
(154, 'Nhâm Mạnh Dũng bất ngờ vì đột nhiên nổi tiếng', 'Nham-Manh-Dung-bat-ngo-vi-dot-nhien-noi-tieng', 'Trên VnExpress chiều 18/6, tiền đạo Nhâm Mạnh Dũng thừa nhận ngỡ ngàng vì sự nổi tiếng của bản thân sau thành công ở giải U23 châu Á và SEA Games.', '<div class=\"section-inner inset-column special-column\">\r\n<h2>Tr&ecirc;n VnExpress chiều 18/6, tiền đạo Nh&acirc;m Mạnh Dũng thừa nhận ngỡ ng&agrave;ng v&igrave; sự nổi tiếng của bản th&acirc;n sau th&agrave;nh c&ocirc;ng ở giải U23 ch&acirc;u &Aacute; v&agrave; SEA Games.</h2>\r\n</div>\r\n<div class=\"section-inner inset-column\">\r\n<p><em>- Mạnh Dũng l&agrave;m g&igrave; sau khi trở về từ v&ograve;ng chung kết U23 ch&acirc;u &Aacute;?</em></p>\r\n</div>\r\n<div class=\"section-inner inset-column\">\r\n<p>- Hạ c&aacute;nh xuống H&agrave; Nội s&aacute;ng 16/6, t&ocirc;i lập tức l&ecirc;n đường về Th&aacute;i B&igrave;nh để thăm nh&agrave; một ng&agrave;y. Đ&acirc;y mới l&agrave; lần thứ hai t&ocirc;i được về nh&agrave; kể từ Tết, v&igrave; li&ecirc;n tục phải tập trung cho SEA Games rồi U23 ch&acirc;u &Aacute;. T&ocirc;i rất vui. T&ocirc;i chỉ ở nh&agrave; ăn cơm với gia đ&igrave;nh. Bố mẹ biết t&ocirc;i th&iacute;ch ăn thịt g&agrave; n&ecirc;n chi&ecirc;u đ&atilde;i m&oacute;n rất truyền thống v&agrave; đơn giản n&agrave;y. Tối h&ocirc;m ấy, t&ocirc;i ngủ c&ugrave;ng anh trai, nhưng v&igrave; qu&aacute; mệt sau chuyến bay d&agrave;i từ Uzbekistan, t&ocirc;i ngủ lu&ocirc;n, kh&ocirc;ng kịp t&acirc;m sự g&igrave; nhiều với anh hay c&aacute;c th&agrave;nh vi&ecirc;n kh&aacute;c trong gia đ&igrave;nh.</p>\r\n<p>H&ocirc;m qua (17/6), t&ocirc;i l&ecirc;n lại H&agrave; Nội để tập trung c&ugrave;ng CLB Viettel chuẩn bị đ&aacute; AFC Cup.</p>\r\n</div>\r\n<div class=\"section-inner outset-column\">\r\n<div class=\"medium-insert-images medium-insert-images-large\">\r\n<figure><img src=\"https://i1-thethao.vnecdn.net/2022/06/18/Dung-2-jpeg-9083-1655543613.jpg?w=0&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Ync2i5WG7pa2-O6__U7fIg\" alt=\"\">\r\n<figcaption>Mạnh Dũng (giữa) trong bữa cơm gia đ&igrave;nh tại Th&aacute;i B&igrave;nh ng&agrave;y 16/6. Ảnh:&nbsp;<em>FBNV</em></figcaption>\r\n</figure>\r\n</div>\r\n</div>\r\n<div class=\"section-inner inset-column\">\r\n<p><em>- Cuộc sống của Dũng c&oacute; g&igrave; thay đổi sau th&agrave;nh c&ocirc;ng ở SEA Games 31 rồi v&ograve;ng chung kết U23 ch&acirc;u &Aacute; vừa qua?</em></p>\r\n<p>- Mọi người biết đến t&ocirc;i nhiều hơn, n&ecirc;n khi gặp, ai cũng xin chữ k&yacute; v&agrave; chụp ảnh c&ugrave;ng. Thật l&ograve;ng, t&ocirc;i cũng c&oacute; phần ngỡ ng&agrave;ng v&igrave; sự nổi tiếng n&agrave;y. T&ocirc;i kh&ocirc;ng nghĩ mọi người quan t&acirc;m đến m&igrave;nh nhiều như vậy. Khi về qu&ecirc;, dọc đường ở x&atilde;, t&ocirc;i thấy mọi người c&ograve;n in ảnh t&ocirc;i l&ecirc;n băng-r&ocirc;n treo dọc hai b&ecirc;n đường, v&agrave; đ&oacute;n ch&agrave;o t&ocirc;i nồng nhiệt.</p>\r\n<p><em>- Ở trận tứ kết U23 ch&acirc;u &Aacute; vừa rồi, gặp Saudi Arabia, Dũng được điều xuống l&agrave;m thủ m&ocirc;n khi đội mất Quan Văn Chuẩn v&agrave; đ&atilde; hết quyền thay người. Cảm x&uacute;c của anh thế n&agrave;o khi lần đầu thử sức ở vị tr&iacute; mới n&agrave;y?</em></p>\r\n<p>- T&ocirc;i bất ngờ khi được HLV Gong Oh-kyun chỉ định l&agrave;m thủ m&ocirc;n trong t&igrave;nh huống đ&oacute;. Đội cũng c&oacute; nhiều cầu thủ, nhưng kh&ocirc;ng hiểu sao thầy lại chọn t&ocirc;i. T&ocirc;i phải thay &aacute;o đến ba lần v&agrave; thật sự cũng hơi run khi đứng trong khung gỗ. T&ocirc;i sở trường đ&aacute; tiền đạo v&agrave; cũng v&agrave;i năm chơi trung vệ khi ho&agrave;n cảnh bắt buộc, nhưng cảm gi&aacute;c l&agrave;m một thủ m&ocirc;n thật sự rất kh&aacute;c lạ. C&oacute; một số t&igrave;nh huống, t&ocirc;i d&ugrave;ng ch&acirc;n theo bản năng, thay v&igrave; d&ugrave;ng tay. V&agrave; sau trận, khi ngẫm lại, t&ocirc;i nghĩ hẳn sẽ tốt hơn nhiều nếu trong những t&igrave;nh huống đ&oacute;, t&ocirc;i d&ugrave;ng tay.</p>\r\n<p><em>- Sau m&agrave;n đ&oacute;ng thế cuối trận tứ kết với Saudi Arabia đ&oacute;, nhiều người n&oacute;i Dũng l&agrave; thủ m&ocirc;n lịch sử v&igrave; chưa thủng lưới b&agrave;n n&agrave;o. Anh nghĩ sao về điều đ&oacute;?</em></p>\r\n<p>- Đồng đội ở Viettel, Qu&agrave;ng Thế T&agrave;i, c&ograve;n n&oacute;i đ&ugrave;a rằng anh ấy c&oacute; nguy cơ mất vị tr&iacute; v&igrave; t&ocirc;i. Nhưng t&ocirc;i nghĩ anh ấy v&agrave; mọi người chỉ n&oacute;i vui, động vi&ecirc;n cho m&agrave;n đ&oacute;ng thế trong khung th&agrave;nh của t&ocirc;i h&ocirc;m đ&oacute;. Đ&aacute; được nhiều vị tr&iacute; th&igrave; cũng l&agrave; một lợi thế, nhưng thật sự t&ocirc;i kh&ocirc;ng c&oacute; khả năng l&agrave;m thủ m&ocirc;n đ&acirc;u. T&ocirc;i kh&ocirc;ng muốn trải nghiệm th&ecirc;m trong vai tr&ograve; ấy lần n&agrave;o nữa.</p>\r\n<div class=\"medium-insert-embeds medium-insert-embeds-normal\" contenteditable=\"false\">\r\n<figure>\r\n<div class=\"medium-insert-embed\">\r\n<div class=\"video video-vne video-youtube\">\r\n<div id=\"video_parent_356808\" class=\"box_embed_video_parent embed_video_new\" data-vcate=\"1003834\" data-vscate=\"1003008\" data-vscaten=\"Thể thao\" data-vid=\"356808\" data-ratio=\"1\" data-articleoriginal=\"4475404\" data-ads=\"1\" data-license=\"0\" data-duration=\"100\" data-brandsafe=\"0\" data-type=\"0\" data-play=\"1\" data-frame=\"\" data-aot=\"Nh&acirc;m Mạnh Dũng: \'T&igrave;nh thế buộc t&ocirc;i l&agrave;m thủ m&ocirc;n\'\" data-videoclassify=\"Ban Video\" data-initdom=\"1\" data-view=\"inview\" data-auto=\"1\" data-status=\"play\">\r\n<div id=\"embed_video_356808\" class=\"box_embed_video\">\r\n<div class=\"bg_overlay_small_unpin\">&nbsp;</div>\r\n<div class=\"bg_overlay_small\">&nbsp;</div>\r\n<div class=\"embed-container\">\r\n<div id=\"video-356808\">\r\n<div id=\"parser_player_356808\" class=\"media_content\">\r\n<div id=\"videoContainter_356808\" class=\"videoContainter\">\r\n<div id=\"media-video-356808\" class=\"video-js vjs-default-skin vjs-workinghover vjs-v7 media-video-356808-dimensions vjs-has-started vjs-user-inactive vjs-controls-enabled vjs-playing\" lang=\"vi\" role=\"region\" data-ex=\"st=0&amp;bs=0&amp;pt=0\" data-subtitle=\"0\" data-mode=\"240|360|480|720\" aria-label=\"Video Player\">\r\n<div class=\"vjs-text-track-display\" aria-live=\"off\" aria-atomic=\"true\">&nbsp;</div>\r\n<div class=\"vjs-loading-spinner\" dir=\"ltr\"><span class=\"vjs-control-text\">Video Player is loading.</span></div>\r\n<button class=\"vjs-big-play-button\" title=\"\" type=\"button\" aria-disabled=\"false\"></button>\r\n<div class=\"vjs-control-bar\" dir=\"ltr\"><button class=\"vjs-play-control vjs-control vjs-button vjs-playing\" title=\"Dừng\" type=\"button\" aria-disabled=\"false\"><span class=\"vjs-control-text\" aria-live=\"polite\">Dừng</span></button>\r\n<div class=\"vjs-current-time vjs-time-control vjs-control\"><span class=\"vjs-control-text\">Hiện tại&nbsp;</span><span class=\"vjs-current-time-display\" aria-live=\"off\">0:18</span></div>\r\n<div class=\"vjs-time-control vjs-time-divider\">\r\n<div>/</div>\r\n</div>\r\n<div class=\"vjs-duration vjs-time-control vjs-control\"><span class=\"vjs-control-text\">Thời lượng&nbsp;</span><span class=\"vjs-duration-display\" aria-live=\"off\">1:40</span></div>\r\n<div class=\"vjs-progress-control vjs-control\">\r\n<div class=\"vjs-progress-holder vjs-slider vjs-slider-horizontal\" tabindex=\"0\" role=\"slider\" aria-valuenow=\"19.01\" aria-valuemin=\"0\" aria-valuemax=\"100\" aria-label=\"Progress Bar\" aria-valuetext=\"0:19 of 1:40\">\r\n<div class=\"vjs-load-progress\"><span class=\"vjs-control-text\">Đ&atilde; tải: 0%</span>\r\n<div>&nbsp;</div>\r\n</div>\r\n<div class=\"vjs-play-progress vjs-slider-bar\">\r\n<div class=\"vjs-time-tooltip\">&nbsp;</div>\r\n<span class=\"vjs-control-text\">Tiến tr&igrave;nh: 0%</span></div>\r\n</div>\r\n</div>\r\n<div class=\"vjs-volume-panel vjs-control vjs-volume-panel-vertical\"><button class=\"vjs-mute-control vjs-control vjs-button vjs-vol-0\" title=\"Bỏ tắt tiếng\" type=\"button\" aria-disabled=\"false\"><span class=\"vjs-control-text\" aria-live=\"polite\">Bỏ tắt tiếng</span></button>\r\n<div class=\"vjs-volume-control vjs-control vjs-volume-vertical\">\r\n<div class=\"vjs-volume-bar vjs-slider-bar vjs-slider vjs-slider-vertical\" tabindex=\"0\" role=\"slider\" aria-valuenow=\"0\" aria-valuemin=\"0\" aria-valuemax=\"100\" aria-label=\"Volume Level\" aria-live=\"polite\" aria-valuetext=\"0%\">\r\n<div class=\"vjs-volume-level\">&nbsp;</div>\r\n</div>\r\n</div>\r\n</div>\r\n<button class=\"vjs-fullscreen-control vjs-control vjs-button\" title=\"To&agrave;n m&agrave;n h&igrave;nh\" type=\"button\" aria-disabled=\"false\"><span class=\"vjs-control-text\" aria-live=\"polite\">To&agrave;n m&agrave;n h&igrave;nh</span></button></div>\r\n<div id=\"356808_soundTopRight\" class=\"fp-sound-status-container\"></div>\r\n<div class=\"share-hover\">&nbsp;</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n<figcaption>Nh&acirc;m Mạnh Dũng l&agrave;m thủ m&ocirc;n trong &iacute;t ph&uacute;t cuối trận Việt Nam - Saudi Arabia ở tứ kết U23 ch&acirc;u &Aacute; ng&agrave;y 12/6.</figcaption>\r\n</figure>\r\n</div>\r\n<p><em>- Mạnh Dũng đ&aacute; hậu vệ một thời gian d&agrave;i ở đội U23 dưới thời HLV Park Hang-seo. Anh được đưa l&ecirc;n đ&aacute; tiền đạo từ khi n&agrave;o?</em></p>\r\n<p>- Thật ra, sở trường của t&ocirc;i l&agrave; tiền đạo, nhưng khi l&ecirc;n tuyển, thầy Park k&eacute;o t&ocirc;i về đ&aacute; trung vệ v&agrave; chỉ bảo tận t&igrave;nh phải đ&aacute; thế n&agrave;o ở vị tr&iacute; n&agrave;y n&ecirc;n t&ocirc;i cũng dần th&iacute;ch nghi. Thế rồi trước SEA Games, trong một buổi đ&aacute; tập nội bộ, thầy Park lại đưa t&ocirc;i trở lại đ&aacute; tiền đạo. H&ocirc;m đ&oacute;, t&ocirc;i cũng bất ngờ. Nhưng c&oacute; lẽ nhờ g&acirc;y ấn tượng tốt h&ocirc;m đ&oacute;, rồi cả trận ra qu&acirc;n SEA Games thắng Indonesia 3-0, n&ecirc;n t&ocirc;i được thầy tiếp tục tin d&ugrave;ng. T&ocirc;i vẫn th&iacute;ch đ&aacute; tiền đạo nhất v&agrave; muốn tiếp tục ph&aacute;t triển sự nghiệp ở vị tr&iacute; n&agrave;y.</p>\r\n<p><em>- Anh tiếc nuối nhất điều g&igrave; tại giải U23 ch&acirc;u &Aacute; vừa qua?</em></p>\r\n<p>- Thật sự, t&ocirc;i rất tiếc khi đội dừng ch&acirc;n ở tứ kết. Trận đ&oacute;, đội đ&aacute; rất cố gắng, nhưng thiếu kinh nghiệm dẫn đến thất bại. Ở b&agrave;n thua đầu ti&ecirc;n, một phần c&oacute; lẽ ch&uacute;ng t&ocirc;i chơi qu&aacute; fair-play, nhưng t&igrave;nh huống đ&oacute; cũng xuất ph&aacute;t từ việc thiếu kinh nghiệm. Nhưng ở kh&iacute;a cạnh kh&aacute;c, t&ocirc;i nghĩ thua Saudi Arabia cũng l&agrave; một b&agrave;i học qu&yacute; b&aacute;u cho cả đội.</p>\r\n<p>Trước đ&oacute;, t&ocirc;i cũng tiếc v&igrave; đội kh&ocirc;ng thể thắng Th&aacute;i Lan ở trận ra qu&acirc;n. Ch&uacute;ng t&ocirc;i rất muốn đ&aacute;nh bại Th&aacute;i Lan để tặng người h&acirc;m mộ cũng như những anh em gặp sự cố sức khoẻ kh&ocirc;ng thể thi đấu h&ocirc;m đ&oacute;. Nhưng về cuối trận, đội c&oacute; phần xuống sức, n&ecirc;n kh&ocirc;ng theo kịp đối thủ v&agrave; bị gỡ ho&agrave;. B&oacute;ng đ&aacute; lu&ocirc;n c&oacute; những khoảnh khắc như vậy, v&agrave; ch&uacute;ng t&ocirc;i đ&agrave;nh phải chấp nhận kết quả.</p>\r\n</div>\r\n<div class=\"section-inner outset-column\">\r\n<div class=\"medium-insert-images medium-insert-images-large\">\r\n<figure><img src=\"https://i1-thethao.vnecdn.net/2022/06/18/MD-2-b-jpeg-2453-1655543613.jpg?w=0&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ImGvg63AZXyrg6Px6XxXMg\" alt=\"\">\r\n<figcaption>Mạnh Dũng trả lời VnExpress tại Trung t&acirc;m B&oacute;ng đ&aacute; Viettel, Ho&agrave; Lạc, H&agrave; Nội chiều 18/6. Ảnh:&nbsp;<em>Thi&ecirc;n Anh</em></figcaption>\r\n</figure>\r\n</div>\r\n</div>\r\n<div class=\"section-inner inset-column\">\r\n<p><em>- Anh đ&aacute;nh gi&aacute; sao về HLV Gong?</em></p>\r\n<p>- Thầy Gong l&agrave; người vui t&iacute;nh, theo đuổi lối đ&aacute; tấn c&ocirc;ng. T&ocirc;i nghĩ thầy đ&atilde; thổi một l&agrave;n gi&oacute; mới cho lối chơi của đội, gi&uacute;p U23 Việt Nam đ&aacute; cởi mở hơn trước c&aacute;c đối thủ mạnh. Trước đ&acirc;y, Việt Nam thường chỉ ph&ograve;ng ngự, chứ kh&ocirc;ng đ&aacute; high-presssing trước những đội mạnh.</p>\r\n<p><em>- Quen đ&aacute; ph&ograve;ng ngự một thời gian d&agrave;i, vậy khi mới tiếp thu lối chơi mới của HLV Gong, anh v&agrave; c&aacute;c đồng đội cảm gi&aacute;c thế n&agrave;o?</em></p>\r\n<p>- N&oacute;i thật l&agrave; ban đầu ch&uacute;ng t&ocirc;i cũng c&oacute; phần nghi ngờ khi thầy truyền đạt chiến thuật mới. Đ&aacute; high pressing ti&ecirc;u tốn thể lực, đ&ograve;i hỏi đội chuẩn bị sức khỏe tốt v&agrave; bung sức đ&uacute;ng thời điểm. Nhưng sau đ&oacute;, ch&iacute;nh t&ocirc;i v&agrave; c&aacute;c đồng đội cũng th&iacute;ch lối đ&aacute; n&agrave;y, cố gắng tiếp thu những chỉ dẫn của thầy v&agrave; kh&aacute;m ph&aacute; những khả năng mới của bản th&acirc;n trong lối chơi mới. Ngay cả khi đội thua UAE 0-3 trong trận giao hữu trước thềm giải U23, cả đội vẫn tự tin v&agrave; chơi đ&uacute;ng &yacute; đồ của thầy Gong. Sau trận đ&oacute;, ch&uacute;ng t&ocirc;i như được th&aacute;o bỏ n&uacute;t thắt t&acirc;m l&yacute;. Thầy Gong cũng động vi&ecirc;n cả đội rất nhiều. Trước mỗi trận đấu, thầy rất lạc quan, lu&ocirc;n truyền cho ch&uacute;ng t&ocirc;i niềm tin rằng đội c&oacute; thể thắng, ngay cả khi gặp c&aacute;c đối thủ mạnh. Ch&uacute;ng t&ocirc;i nhờ đ&oacute; cũng th&ecirc;m tự tin khi s&acirc;n v&agrave; thể hiện được hết khả năng.</p>\r\n<p><em>- Ai l&agrave; người ảnh hưởng lớn nhất đến Dũng từ đầu sự nghiệp?</em></p>\r\n<p>- Người đ&oacute; l&agrave; HLV Đinh Thế Nam. Năm 14 tuổi, t&ocirc;i c&oacute; suy nghĩ hơi lệch lạc, theo hướng nghi ngờ năng lực của bản th&acirc;n, rồi xao nh&atilde;ng bởi nhiều yếu tố b&ecirc;n ngo&agrave;i. Nhưng nhờ b&aacute;c Nam chỉ bảo, uốn nắn, t&ocirc;i đi đ&uacute;ng hướng trở lại, để tập trung cho sự nghiệp.</p>\r\n<p><em>- Trả lời VnExpress ng&agrave;y 16/6, HLV Gong c&oacute; n&oacute;i th&agrave;nh t&iacute;ch của đội ở giải U23 ch&acirc;u &Aacute; kh&ocirc;ng quan trọng bằng việc để c&aacute;c cầu thủ ra s&acirc;n, v&igrave; d&ugrave; chỉ một ph&uacute;t thi đấu ở giải tầm cỡ ch&acirc;u lục như thế n&agrave;y vẫn l&agrave; trải nghiệm qu&yacute; b&aacute;u cho tương lai. Dũng được ra s&acirc;n kh&aacute; nhiều tại Uzbekistan vừa qua. Anh nghĩ tương lai của bản th&acirc;n ở cả cấp CLB lẫn ĐTQG sẽ ra sao?</em></p>\r\n<p>- Việc thầy chủ trương tạo điều kiện tối đa cho c&aacute;c cầu thủ ra s&acirc;n ở U23 ch&acirc;u &Aacute; qu&yacute; b&aacute;u thật sự. Giờ về lại CLB v&agrave; V-League, &aacute;p lực cạnh tranh l&agrave; rất lớn, n&ecirc;n t&ocirc;i cũng kh&ocirc;ng d&aacute;m n&oacute;i trước về khả năng được thi đấu hay đ&aacute; ch&iacute;nh. Việc cạnh tranh suất ở ĐTQG th&igrave; t&ocirc;i c&ograve;n chưa nghĩ đến, v&igrave; tr&ecirc;n đ&oacute; vẫn c&ograve;n nhiều đ&agrave;n anh giỏi, tr&igrave;nh độ cao, v&agrave; vẫn c&ograve;n tương đối trẻ d&ugrave; đ&atilde; rất d&agrave;y dạn kinh nghiệm. T&ocirc;i nghĩ mục ti&ecirc;u ph&ugrave; hợp với m&igrave;nh l&uacute;c n&agrave;y phải l&agrave; cố gắng được gọi v&agrave;o ĐTQG.</p>\r\n</div>\r\n<div class=\"section-inner outset-column\">\r\n<div class=\"medium-insert-images medium-insert-images-large\">\r\n<figure><img src=\"https://i1-thethao.vnecdn.net/2022/06/18/Dung-jpeg-5684-1655543613.jpg?w=0&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=eYG41BACUBBja-V-RorM4w\" alt=\"\">\r\n<figcaption>\r\n<p>Nh&acirc;m Mạnh Dũng bật cao đ&aacute;nh đầu v&agrave;o lưới Malaysia ở lượt cuối v&ograve;ng bảng ng&agrave;y 8/6. Ảnh:&nbsp;<em>L&acirc;m Thoả</em></p>\r\n</figcaption>\r\n</figure>\r\n</div>\r\n</div>\r\n<div class=\"section-inner inset-column\">\r\n<p><em>- Dũng tự thấy đ&acirc;u l&agrave; điểm mạnh v&agrave; đ&acirc;u l&agrave; hạn chế cần cải thiện của bản th&acirc;n ở vị tr&iacute; tiền đạo?</em></p>\r\n<p>- T&ocirc;i nghĩ m&igrave;nh di chuyển, phối hợp ổn với đồng đội, c&agrave;i đ&egrave; v&agrave; l&agrave;m tường cũng tốt. Nhưng t&ocirc;i kh&ocirc;ng c&oacute; tốc độ cao, n&ecirc;n lu&ocirc;n cố gắng hướng đến phong c&aacute;ch chơi b&oacute;ng đơn giản, với h&igrave;nh mẫu l&agrave; đ&agrave;n anh Nguyễn Anh Đức.</p>\r\n<p>Với tiền đạo, ghi b&agrave;n l&agrave; ưu ti&ecirc;n h&agrave;ng đầu, nhưng đ&oacute;ng g&oacute;p cho lối chơi chung to&agrave;n đội cũng rất quan trọng. T&ocirc;i vẫn đang cố gắng cải thiện khoản bứt tốc, r&ecirc; b&oacute;ng. Trong mỗi buổi tập ở CLB Viettel, t&ocirc;i lu&ocirc;n chủ động ở lại tập ri&ecirc;ng để cải thiện bản th&acirc;n.</p>\r\n<p><em>- Anh ngại đối đầu với hậu vệ n&agrave;o nhất ở V-League?</em></p>\r\n<p>- T&ocirc;i chưa đối đầu trực tiếp nhiều, nhưng thấy ngại nhất khi đấu anh Quế Ngọc Hải, v&igrave; anh ấy chơi rất th&ocirc;ng minh. Trong c&aacute;c buổi tập ở Viettel, t&ocirc;i rất kh&oacute; vượt qua anh ấy. Nhưng t&ocirc;i lu&ocirc;n t&acirc;m niệm phải đối đầu những hậu vệ giỏi như thế, m&igrave;nh mới c&oacute; cơ hội v&agrave; động lực để cố gắng tự ho&agrave;n hiện bản th&acirc;n.</p>\r\n<p><em>- Anh nghĩ sao về khả năng ra nước ngo&agrave;i thi đấu, như nhiều cầu thủ Việt Nam gần đ&acirc;y?</em></p>\r\n<p>- Đ&atilde; l&agrave; cầu thủ chuy&ecirc;n nghiệp, t&ocirc;i nghĩ ai cũng sẽ muốn c&oacute; cơ hội ra nước ngo&agrave;i thi đấu, t&igrave;m kiếm trải nghiệm b&oacute;ng đ&aacute; mới ở những nền b&oacute;ng đ&aacute; c&oacute; tr&igrave;nh độ cao hơn. Nhưng với t&ocirc;i l&uacute;c n&agrave;y, đ&oacute; vẫn l&agrave; việc kh&oacute; v&agrave; c&oacute; phần xa vời. T&ocirc;i mới 22 tuổi, cũng chỉ ở chặng đầu sự nghiệp. T&ocirc;i nghĩ trước mắt, m&igrave;nh vẫn phải chứng minh khả năng ở V-League v&agrave; c&aacute;c đội tuyển quốc gia.</p>\r\n</div>', 218, 7, 0, 1, '2022-11-21 19:35:42', '2022-11-22 05:53:44'),
(158, 'Cầu hôn giữa siêu thị', 'cau-hon-giua-sieu-thi', 'ITALY-Một người đàn ông gây bất ngờ với màn cầu hôn bạn gái qua micro ở quầy thanh toán của siêu thị tại Venice.', '<p class=\"description\"><span class=\"location-stamp\">ITALY</span>M&ocirc;̣t người đàn &ocirc;ng g&acirc;y b&acirc;́t ngờ với màn c&acirc;̀u h&ocirc;n bạn gái qua micro ở qu&acirc;̀y thanh toán của si&ecirc;u thị tại Venice.</p>\r\n<article class=\"fck_detail \">\r\n<div id=\"divfirst\">\r\n<p class=\"Normal\">\"T&igrave;nh y&ecirc;u của anh, anh muốn hỏi em một điều trước mặt tất cả mọi người\", Salvatore Costarelli n&oacute;i trước micro tại si&ecirc;u thị Despar ở Mira, ngoại &ocirc; Venice. \"Anh y&ecirc;u em. Em l&agrave; cuộc sống của anh. Anh muốn hỏi l&agrave; em c&oacute; đồng &yacute; lấy anh kh&ocirc;ng?\".</p>\r\n<p class=\"Normal\">Video đăng tr&ecirc;n b&aacute;o&nbsp;<em>Corriere del Veneto</em>&nbsp;ng&agrave;y 18/6 cho thấy những tiếng vỗ tay vang l&ecirc;n v&agrave; m&aacute;y quay sau đ&oacute; hướng đến một gian h&agrave;ng chất tẩy rửa, nơi Tiziana Famao đang đứng.</p>\r\n<figure class=\"item_slide_show clearfix\">\r\n<div id=\"video_parent_357175\" class=\"box_embed_video_parent embed_video_new\" data-vcate=\"1003834\" data-vscate=\"1003005\" data-vscaten=\"Thế giới\" data-vid=\"357175\" data-ratio=\"1\" data-articleoriginal=\"4477769\" data-ads=\"1\" data-license=\"0\" data-duration=\"88\" data-brandsafe=\"0\" data-type=\"0\" data-play=\"1\" data-frame=\"\" data-aot=\"\" data-videoclassify=\"None\" data-initdom=\"1\" data-view=\"inview\" data-auto=\"1\" data-status=\"play\">\r\n<div id=\"embed_video_357175\" class=\"box_embed_video\">\r\n<div class=\"embed-container\">\r\n<div id=\"video-357175\">\r\n<div id=\"parser_player_357175\" class=\"media_content\">\r\n<div id=\"videoContainter_357175\" class=\"videoContainter\">\r\n<div id=\"media-video-357175\" class=\"video-js vjs-default-skin vjs-workinghover vjs-v7 media-video-357175-dimensions vjs-has-started vjs-controls-enabled vjs-playing vjs-user-inactive\" lang=\"vi\" role=\"region\" data-ex=\"st=0&amp;bs=0&amp;pt=0\" data-subtitle=\"0\" data-mode=\"240|360|480|720\" aria-label=\"Video Player\">\r\n<div class=\"share-hover\">&nbsp;</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n<figcaption class=\"desc_cation\">\r\n<div class=\"inner_caption\">\r\n<p class=\"Image\">M&agrave;n cầu h&ocirc;n giữa si&ecirc;u thị của Salvatore Costarelli. Video:&nbsp;<em>Corriere del Veneto.</em></p>\r\n</div>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Costarelli v&agrave; Famao &ocirc;m nhau ở khu rau quả đ&ocirc;ng lạnh trước khi anh lấy chiếc nhẫn từ trong t&uacute;i v&agrave; trao cho người y&ecirc;u. Hai người trao nhau nụ h&ocirc;n trong tiếng reo h&ograve; v&agrave; vỗ tay của những người xung quanh.</p>\r\n<p class=\"Normal\">\"Khi nghe những lời đ&oacute;, t&ocirc;i đ&atilde; qu&aacute; bất ngờ\", Famao n&oacute;i, cho biết h&ocirc;n phu của c&ocirc; vốn l&agrave; người rất nh&uacute;t nh&aacute;t.</p>\r\n</div>\r\n<div id=\"admbackground\" data-set=\"dfp\"></div>\r\n<div id=\"divend\">\r\n<p class=\"Normal\">Sau đ&oacute;, tất cả những người c&oacute; mặt đ&atilde; mở một chai Prosecco được chuẩn bị sẵn để c&ugrave;ng n&acirc;ng ly ch&uacute;c mừng cặp đ&ocirc;i. Costarelli cho biết anh đ&atilde; l&ecirc;n kế hoạch cầu h&ocirc;n từ nhiều ng&agrave;y trước.</p>\r\n<p class=\"Normal\">\"T&ocirc;i từng nghĩ đến m&agrave;n cầu h&ocirc;n kinh điển tr&ecirc;n một chiếc thuyền gondola\", anh n&oacute;i, nhắc đến loại thuyền truyền thống của Venice. \"Nhưng sau đ&oacute; t&ocirc;i chọn si&ecirc;u thị, để c&ocirc; ấy hiểu t&ocirc;i quan t&acirc;m đến c&ocirc; ấy như thế n&agrave;o. T&ocirc;i nghĩ c&ocirc; ấy sẽ nhớ m&atilde;i m&agrave;n cầu h&ocirc;n n&agrave;y\".</p>\r\n<p class=\"Normal\">Đ&aacute;m cưới của hai người dự kiến tổ chức tại đảo Sicily v&agrave;o th&aacute;ng 12 tới.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"action_thumb flexbox\">&nbsp;</div>\r\n<div class=\"fig-picture\"><picture data-inimage=\"done\"><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/19/proposta-kh0G-U46100225562870M-4251-7593-1655606129.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=E5hk2m0uRJ74OJ0O2PnchA 1x, https://i1-vnexpress.vnecdn.net/2022/06/19/proposta-kh0G-U46100225562870M-4251-7593-1655606129.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=a924rCVWHNOFkjwgHpAqMQ 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/19/proposta-kh0G-U46100225562870M-4251-7593-1655606129.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=IYJxId_CfzpUcET3p_NGuA 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/19/proposta-kh0G-U46100225562870M-4251-7593-1655606129.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=E5hk2m0uRJ74OJ0O2PnchA 1x, https://i1-vnexpress.vnecdn.net/2022/06/19/proposta-kh0G-U46100225562870M-4251-7593-1655606129.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=a924rCVWHNOFkjwgHpAqMQ 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/19/proposta-kh0G-U46100225562870M-4251-7593-1655606129.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=IYJxId_CfzpUcET3p_NGuA 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/06/19/proposta-kh0G-U46100225562870M-4251-7593-1655606129.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=E5hk2m0uRJ74OJ0O2PnchA\" alt=\"Salvatore Costarelli v&agrave; Tiziana Famao h&ocirc;n nhau tại si&ecirc;u thị si&ecirc;u thị Despar ở Mira, ngoại &ocirc; Venice. Ảnh: Corriere del Veneto.\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/06/19/proposta-kh0G-U46100225562870M-4251-7593-1655606129.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=E5hk2m0uRJ74OJ0O2PnchA\" data-ll-status=\"loaded\" data-adbro-processed=\"true\"></picture>\r\n<div class=\"embed-container-ads\">\r\n<div id=\"sis_inimage\" data-google-query-id=\"COOig_3ruPgCFUQmlgod0VQAFA\">\r\n<div id=\"google_ads_iframe_/27973503/Vnexpress/Desktop/Inimage/Thegioi/Thegioi.cuocsongdoday.detail_0__container__\"><iframe id=\"google_ads_iframe_/27973503/Vnexpress/Desktop/Inimage/Thegioi/Thegioi.cuocsongdoday.detail_0\" tabindex=\"0\" title=\"3rd party ad content\" role=\"region\" name=\"google_ads_iframe_/27973503/Vnexpress/Desktop/Inimage/Thegioi/Thegioi.cuocsongdoday.detail_0\" width=\"1\" height=\"1\" frameborder=\"0\" marginwidth=\"0\" marginheight=\"0\" scrolling=\"no\" aria-label=\"Advertisement\" data-google-container-id=\"4\" data-load-complete=\"true\"></iframe></div>\r\n</div>\r\n</div>\r\n</div>\r\n<figcaption>\r\n<p class=\"Image\">Salvatore Costarelli v&agrave; Tiziana Famao h&ocirc;n nhau tại si&ecirc;u thị si&ecirc;u thị Despar ở Mira, ngoại &ocirc; Venice. Ảnh:&nbsp;<em>Corriere del Veneto</em>.</p>\r\n</figcaption>\r\n</figure>\r\n</div>\r\n</article>', 218, 2, 2, 1, '2022-11-21 19:35:42', '2023-02-08 01:11:43'),
(160, '11 cách tự nhiên chữa ho tại nhà', '11-cach-tu-nhien-chua-ho-tai-nha', 'Người bệnh hô hấp bị ho có thể ăn dứa, uống đủ nước, xông hơi, dùng các thực phẩm tự nhiên giúp giảm cơn ho khan, ho có đờm.', '<p class=\"description\">Người bệnh h&ocirc; hấp bị ho c&oacute; thể ăn dứa, uống đủ nước, x&ocirc;ng hơi, d&ugrave;ng c&aacute;c thực phẩm tự nhi&ecirc;n gi&uacute;p giảm cơn ho khan, ho c&oacute; đờm.</p>\r\n<article class=\"fck_detail \">\r\n<p class=\"Normal\">Ho c&oacute; thể g&acirc;y kh&oacute; chịu cho người bệnh, d&ugrave; đ&acirc;y l&agrave; phản ứng cơ thể đang đẩy c&aacute;c vi khuẩn v&agrave; virus bệnh ra khỏi đường h&ocirc; hấp. Người bệnh c&oacute; thể bị ho khan hay ho c&oacute; đờm. Bạn n&ecirc;n thăm kh&aacute;m sớm với b&aacute;c sĩ nếu cơn ho k&eacute;o d&agrave;i qu&aacute; l&acirc;u. 11 phương ph&aacute;p sau đ&acirc;y c&oacute; thể gi&uacute;p dịu cơn ho ở cả người ho c&oacute; đờm v&agrave; ho khan.</p>\r\n<p class=\"Normal\"><strong>D&ugrave;ng mật ong</strong></p>\r\n<p class=\"Normal\">C&aacute;c nghi&ecirc;n cứu c&ocirc;ng bố bởi Hiệp hội Y khoa Mỹ, cho thấy mật ong gi&uacute;p dịu cơn ho ở người trưởng th&agrave;nh v&agrave; trẻ em từ 2 tuổi. Bạn c&oacute; thể kết hợp 2 th&igrave;a mật ong với tr&agrave; thảo mộc hoặc pha với nước ấm, uống một đến hai lần một ng&agrave;y c&oacute; thể chữa ho hiệu quả.</p>\r\n<p class=\"Normal\"><strong>D&ugrave;ng gừng</strong></p>\r\n<p class=\"Normal\">Gừng l&agrave; gia vị tự nhi&ecirc;n gi&uacute;p l&agrave;m dịu chứng ho khan v&agrave; cơn suyễn nhờ đặc t&iacute;nh chống vi&ecirc;m cao. B&aacute;o c&aacute;o nghi&ecirc;n cứu tr&ecirc;n Thư viện Y tế Quốc gia (Mỹ) cho thấy, một số hợp chất chống vi&ecirc;m trong gừng c&oacute; thể gi&uacute;p thư gi&atilde;n đường h&ocirc; hấp, gi&uacute;p giảm ho. Bạn c&oacute; thể pha 20-40 gừng tươi với nước n&oacute;ng, giữ ấm trước khi uống. Uống nhiều tr&agrave; gừng c&oacute; thể g&acirc;y đau bụng hoặc ợ chua.</p>\r\n<p class=\"Normal\"><strong>Uống đủ nước</strong></p>\r\n<p class=\"Normal\">Giữ cơ thể lu&ocirc;n đủ nước bằng c&aacute;ch uống c&aacute;c thức uống ở nhiệt độ ph&ograve;ng c&oacute; thể gi&uacute;p giảm ho v&agrave; giảm sổ mũi hiệu quả. Bạn c&oacute; thể uống tr&agrave; thảo mộc, nước ấm, nước &eacute;p tr&aacute;i c&acirc;y ấm. Nước sẽ gi&uacute;p l&agrave;m lo&atilde;ng c&aacute;c chất nhầy ở đường h&ocirc; hấp, dễ d&agrave;ng đẩy c&aacute;c dịch thừa ra khỏi cơ thể hơn.</p>\r\n<p class=\"Normal\"><strong>Ăn dứa</strong></p>\r\n<p class=\"Normal\">Bromelain l&agrave; một enzyme c&oacute; từ dứa được chứng minh c&oacute; đặc t&iacute;nh chống vi&ecirc;m cao v&agrave; ph&acirc;n giải chất nhầy tốt. Bạn c&oacute; thể ăn hoặc uống nước &eacute;p quả dứa để giảm dần dịch nhầy trong họng, gi&uacute;p giảm ho. Bạn n&ecirc;n tham khảo &yacute; kiến b&aacute;c sĩ để biết th&ecirc;m về lượng bromelain cần nạp trong những ng&agrave;y bệnh trước khi d&ugrave;ng, bởi một số t&aacute;c dụng phụ đối với người đang d&ugrave;ng thuốc l&agrave;m lo&atilde;ng m&aacute;u hoặc c&aacute;c thuốc kh&aacute;ng sinh. Thuốc bổ sung bromelain cũng c&oacute; thể mang lại hiệu quả giảm ho với liều lượng được khuyến nghị.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-suckhoe.vnecdn.net/2022/06/19/image001-1714-1655601016.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Sm48piYl4aPqLKjRDzlJUA 1x, https://i1-suckhoe.vnecdn.net/2022/06/19/image001-1714-1655601016.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=eO9QN7qc2OUPBFPZF6y5TA 1.5x, https://i1-suckhoe.vnecdn.net/2022/06/19/image001-1714-1655601016.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=y0520uhcMNKrrp8gU2BlxA 2x\" data-srcset=\"https://i1-suckhoe.vnecdn.net/2022/06/19/image001-1714-1655601016.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Sm48piYl4aPqLKjRDzlJUA 1x, https://i1-suckhoe.vnecdn.net/2022/06/19/image001-1714-1655601016.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=eO9QN7qc2OUPBFPZF6y5TA 1.5x, https://i1-suckhoe.vnecdn.net/2022/06/19/image001-1714-1655601016.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=y0520uhcMNKrrp8gU2BlxA 2x\"><img class=\"lazy lazied\" src=\"https://i1-suckhoe.vnecdn.net/2022/06/19/image001-1714-1655601016.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Sm48piYl4aPqLKjRDzlJUA\" alt=\"Dứa gi&uacute;p giảm cơn ho nhờ hợp chất bromelain c&oacute; đặc t&iacute;nh kh&aacute;ng vi&ecirc;m v&agrave; ph&acirc;n giải chất nhầy trong họng hiệu quả. Ảnh: Freepik\" loading=\"lazy\" data-src=\"https://i1-suckhoe.vnecdn.net/2022/06/19/image001-1714-1655601016.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Sm48piYl4aPqLKjRDzlJUA\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Dứa gi&uacute;p giảm cơn ho nhờ hợp chất bromelain c&oacute; đặc t&iacute;nh kh&aacute;ng vi&ecirc;m v&agrave; ph&acirc;n giải chất nhầy trong họng hiệu quả. Ảnh: Freepik</p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\"><strong>D&ugrave;ng rễ cam thảo</strong></p>\r\n<p class=\"Normal\">Rễ c&acirc;y cam thảo - thảo dược thuộc họ đậu c&oacute; t&ecirc;n khoa học Glycyrrhiza cũng thường được d&ugrave;ng chữa ho, dạng d&ugrave;ng rễ tươi, vi&ecirc;n n&eacute;n, kẹo trị ho hay chiết xuất tinh dầu dạng siro trị ho. Bộ Y tế v&agrave; dịch vụ Nh&acirc;n sinh (Mỹ) cho biết, cam thảo tuy l&agrave; vị thuốc hỗ trợ long đờm, l&agrave;m dịu m&agrave;ng nhầy, gi&uacute;p dịu phổi, n&ecirc;n được d&ugrave;ng với lượng khuyến nghị bởi b&aacute;c sĩ. Bệnh nh&acirc;n bị bệnh tim mạch, bệnh gan v&agrave; phụ nữ đang mang thai n&ecirc;n c&acirc;n nhắc &yacute; kiến b&aacute;c sĩ trước khi d&ugrave;ng rễ c&acirc;y cam thảo trị ho để tr&aacute;nh c&aacute;c t&aacute;c dụng phụ.</p>\r\n<p class=\"Normal\"><strong>D&ugrave;ng l&aacute; bạc h&agrave;</strong></p>\r\n<p class=\"Normal\">D&ugrave;ng bạc h&agrave; tươi, tinh dầu bạc h&agrave; l&agrave; liệu ph&aacute;p th&ocirc;ng mũi phổ biến gi&uacute;p ph&acirc;n hủy chất nhầy ngăn tr&agrave;n xuống cổ họng, phục hồi sức khỏe đường h&ocirc; hấp. Bạn c&oacute; thể ngậm vi&ecirc;n ngậm bạc h&agrave;, uống tr&agrave; bạc h&agrave; hoặc x&ocirc;ng hơi tinh dầu bạc h&agrave; để giảm c&aacute;c triệu chứng ho khan v&agrave; ho c&oacute; đờm hiệu quả.</p>\r\n<p class=\"Normal\"><strong>D&ugrave;ng l&aacute; bạch đ&agrave;n</strong></p>\r\n<p class=\"Normal\">Một liệu ph&aacute;p chữa ho hữu &iacute;ch v&agrave; phổ biến kh&aacute;c l&agrave; sử dụng l&aacute; bạch đ&agrave;n tươi x&ocirc;ng hơi, uống tr&agrave; bạch đ&agrave;n. Tinh dầu tự nhi&ecirc;n trong c&agrave;nh v&agrave; l&aacute; c&acirc;y bạch đ&agrave;n hỗ trợ th&ocirc;ng mũi, gi&atilde;n nở c&aacute;c tiểu phế quản, giảm cơn ho.</p>\r\n<p class=\"Normal\"><strong>D&ugrave;ng tinh dầu thyme (cỏ xạ hương)</strong></p>\r\n<p class=\"Normal\">Cỏ xạ hương được chuộng d&ugrave;ng trong ẩm thực v&agrave; y học, l&agrave; vị thuốc phổ biến chữa ho, vi&ecirc;m họng, vi&ecirc;m phế quản v&agrave; c&aacute;c vấn đề ti&ecirc;u h&oacute;a. Kết quả nghi&ecirc;n cứu tại Đức tr&ecirc;n gần 400 bệnh nh&acirc;n vi&ecirc;m phế quản cấp t&iacute;nh v&agrave; vi&ecirc;m đường h&ocirc; hấp cho thấy, họ giảm triệu chứng bệnh đ&aacute;ng kể khi uống siro ho cỏ xạ hương v&agrave; thường xu&acirc;n. Bạn cũng c&oacute; thể pha tr&agrave; cỏ xạ hương uống trị ho. Bạn cho 2 th&igrave;a c&agrave; ph&ecirc; cỏ xạ hương kh&ocirc; v&agrave;o một cốc nước n&oacute;ng, h&atilde;m tr&agrave; trong khoảng 10 ph&uacute;t, lọc v&agrave; uống ấm ở nhiệt độ dưới 70 độ C.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-suckhoe.vnecdn.net/2022/06/19/image003-2397-1655601016.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=qyTxnGc0bunRud0p27MfqA 1x, https://i1-suckhoe.vnecdn.net/2022/06/19/image003-2397-1655601016.png?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=yclsODrC12pImTqs7CiLyQ 1.5x, https://i1-suckhoe.vnecdn.net/2022/06/19/image003-2397-1655601016.png?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=CGgHpAddgGB-RYDLIteHDg 2x\" data-srcset=\"https://i1-suckhoe.vnecdn.net/2022/06/19/image003-2397-1655601016.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=qyTxnGc0bunRud0p27MfqA 1x, https://i1-suckhoe.vnecdn.net/2022/06/19/image003-2397-1655601016.png?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=yclsODrC12pImTqs7CiLyQ 1.5x, https://i1-suckhoe.vnecdn.net/2022/06/19/image003-2397-1655601016.png?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=CGgHpAddgGB-RYDLIteHDg 2x\"><img class=\"lazy lazied\" src=\"https://i1-suckhoe.vnecdn.net/2022/06/19/image003-2397-1655601016.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=qyTxnGc0bunRud0p27MfqA\" alt=\"Cỏ xạ hương (l&aacute; thyme) l&agrave; c&acirc;y gia vị v&agrave; vị thuốc c&oacute; thể gi&uacute;p chữa ho hiệu quả. Ảnh: Freepik\" loading=\"lazy\" data-src=\"https://i1-suckhoe.vnecdn.net/2022/06/19/image003-2397-1655601016.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=qyTxnGc0bunRud0p27MfqA\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Cỏ xạ hương (l&aacute; thyme) l&agrave; c&acirc;y gia vị v&agrave; vị thuốc c&oacute; thể gi&uacute;p chữa ho hiệu quả. Ảnh: Freepik</p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\"><strong>X&ocirc;ng hơi</strong></p>\r\n<p class=\"Normal\">Người ho c&oacute; đờm c&oacute; thể cải thiện triệu chứng bằng liệu ph&aacute;p hơi nước. Bạn c&oacute; thể tắm v&ograve;i sen hoặc tắm bồn nước n&oacute;ng, giữ cơ thể trong buồng hơi n&oacute;ng n&agrave;y v&agrave;i ph&uacute;t, bạn c&oacute; thể cảm nhận được triệu chứng ho giảm dần. Sau đ&oacute;, bạn b&ugrave; nước để ngăn mất nước. Viện Tim, Phổi, M&aacute;u Quốc gia (Mỹ) khuyến nghị bệnh nh&acirc;n ho c&oacute; đờm, c&oacute; dấu hiệu thở kh&ograve; kh&egrave; c&oacute; thể d&ugrave;ng m&aacute;y tạo độ ẩm, m&aacute;y tạo hơi c&aacute; nh&acirc;n trong kh&ocirc;ng gian sinh hoạt để giảm triệu chứng bệnh.</p>\r\n<p class=\"Normal\"><strong>S&uacute;c miệng bằng nước muối</strong></p>\r\n<p class=\"Normal\">Nước muối lo&atilde;ng gi&uacute;p l&agrave;m giảm đờm v&agrave; chất nhầy ở cổ họng, giảm cơn ho. Bạn c&oacute; thể cho nửa th&igrave;a c&agrave; ph&ecirc; muối v&agrave;o cốc nước ấm, khuấy tan v&agrave; để nguội rồi s&uacute;c miệng, s&uacute;c họng. Tr&aacute;nh s&uacute;c nước muối qu&aacute; mặn c&oacute; thể ảnh hưởng đến huyết &aacute;p ở người c&oacute; tiền sử cao huyết &aacute;p.</p>\r\n<p class=\"Normal\"><strong>D&ugrave;ng probiotics</strong></p>\r\n<p class=\"Normal\">Theo một nghi&ecirc;n cứu cho thấy ảnh hưởng của probiotics trong điều trị cảm ho tại H&agrave;n Quốc, men vi sinh probiotics c&oacute; thể hỗ trợ hệ miễn dịch, bằng c&aacute;ch gi&uacute;p tăng lợi khuẩn trong đường ruột. Cơ thể c&oacute; hệ miễn dịch tốt c&oacute; thể kh&ocirc;ng bị nhiễm tr&ugrave;ng bởi c&aacute;c t&aacute;c nh&acirc;n dị ứng g&acirc;y ho. Bạn c&oacute; thể d&ugrave;ng c&aacute;c chế phẩm sinh học c&oacute; chứa lợi khuẩn lactobacillus tại c&aacute;c cửa h&agrave;ng dược y tế. Một số thực phẩm gi&agrave;u probiotics tự nhi&ecirc;n phổ biến như s&uacute;p miso, yogurt, dưa cải bắp Việt Nam, kim chi... cũng được nghi&ecirc;n cứu cho biết c&oacute; &iacute;ch cho hệ miễn dịch.</p>\r\n</article>', 218, 5, 1, 1, '2022-11-21 19:35:42', '2022-11-22 05:39:25');
INSERT INTO `posts` (`id`, `title`, `slug`, `excerpt`, `body`, `user_id`, `category_id`, `views`, `approved`, `created_at`, `updated_at`) VALUES
(161, 'Chiếc áo ngực tố cáo kẻ giết người', 'chiec-ao-nguc-to-cao-ke-giet-nguoi', 'TRUNG QUỐCNhờ chiếc áo ngực dính ADN của hung thủ, cảnh sát lần tìm ra kẻ sát hại thiếu nữ Trần Mộng Đình sau 15 năm vụ án rơi vào bế tắc.', '<p class=\"description\"><span class=\"location-stamp\">TRUNG QUỐC</span>Nhờ chiếc &aacute;o ngực d&iacute;nh ADN của hung thủ, cảnh s&aacute;t lần t&igrave;m ra kẻ s&aacute;t hại thiếu nữ Trần Mộng Đ&igrave;nh sau 15 năm vụ &aacute;n rơi v&agrave;o bế tắc.</p>\r\n<article class=\"fck_detail \">\r\n<p class=\"Normal\">Ng&agrave;y 24/9/2005 l&agrave; thứ bảy, thiếu nữ 16 tuổi Trần Mộng Đ&igrave;nh ở TP Quảng Nguy&ecirc;n, tỉnh Tứ Xuy&ecirc;n, được nghỉ học n&ecirc;n c&ugrave;ng c&aacute;c bạn ra ngo&agrave;i chơi. Tối h&ocirc;m đ&oacute;, thấy con g&aacute;i vẫn chưa về, cha mẹ Đ&igrave;nh an ủi lẫn nhau, n&oacute;i con g&aacute;i đ&atilde; lớn, đi chơi về muộn một ch&uacute;t cũng b&igrave;nh thường. Đến 2h50 s&aacute;ng h&ocirc;m sau, mẹ của Đ&igrave;nh sốt ruột gọi điện cho con g&aacute;i, nhưng điện thoại đ&atilde; tắt m&aacute;y.</p>\r\n<p class=\"Normal\">L&uacute;c n&agrave;y b&agrave; mới lo lắng, gọi điện cho bạn học của Đ&igrave;nh. C&aacute;c học sinh n&agrave;y đ&atilde; về nh&agrave;, chỉ một bạn nữ n&oacute;i m&igrave;nh v&agrave; Đ&igrave;nh đến qu&aacute;n net, ngồi đến gần 2h s&aacute;ng, sau đ&oacute; mỗi người một đường đi bộ về nh&agrave;. Sợ con g&aacute;i bị bắt c&oacute;c hay tai nạn, mẹ của Đ&igrave;nh vội gọi điện thoại cầu cứu cảnh s&aacute;t.</p>\r\n<p class=\"Normal\">Điều tra vụ &aacute;n, cảnh s&aacute;t lại ph&aacute;t hiện Đ&igrave;nh từng bỏ nh&agrave; đi, hai ng&agrave;y trước khi xảy ra chuyện c&ograve;n c&atilde;i nhau với cha mẹ. V&igrave; vậy ban đầu cảnh s&aacute;t cho rằng Đ&igrave;nh lại bỏ nh&agrave; đi. Nhưng trước khi ra ngo&agrave;i, c&ocirc; c&ograve;n thoải m&aacute;i tr&ograve; chuyện với cha mẹ về việc học h&agrave;nh, kh&ocirc;ng giống một người muốn bỏ nh&agrave;.</p>\r\n<p class=\"Normal\">H&ocirc;m đ&oacute; Đ&igrave;nh đi chơi c&ugrave;ng bốn bạn học, buổi tối đến qu&aacute;n karaoke, Đ&igrave;nh c&ograve;n h&aacute;t rất vui vẻ. Sau khi h&aacute;t, ba trong bốn bạn học về nh&agrave;, một người c&ugrave;ng Đ&igrave;nh đến qu&aacute;n net.</p>\r\n<p class=\"Normal\">Căn cứ lời khai của nh&acirc;n vi&ecirc;n qu&aacute;n karaoke v&agrave; qu&aacute;n net, cảnh s&aacute;t x&aacute;c định bốn bạn học của Đ&igrave;nh đều n&oacute;i thật. Cha mẹ Đ&igrave;nh c&ugrave;ng cảnh s&aacute;t đi hỏi thăm c&aacute;c cửa h&agrave;ng, t&agrave;i xế taxi tr&ecirc;n qu&atilde;ng đường từ qu&aacute;n net về nh&agrave;, nhưng kh&ocirc;ng ai ấn tượng g&igrave; với Đ&igrave;nh.</p>\r\n<p class=\"Normal\">Ng&agrave;y 4/10/2005, một c&ocirc;ng nh&acirc;n l&agrave;m đường ph&aacute;t hiện bao tải đựng thi thể phụ nữ khi đang thi c&ocirc;ng. Cảnh s&aacute;t lập tức nghĩ đến vụ mất t&iacute;ch của Đ&igrave;nh, mời cha mẹ em c&ugrave;ng đến hiện trường. Nh&igrave;n thấy bộ quần &aacute;o quen thuộc, tia hy vọng cuối c&ugrave;ng của cha mẹ Đ&igrave;nh vụt tắt.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/18/ec574f2e6b084375bd2547d049e697-6598-6142-1655561092.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=VMKgGfCwJgzXH_ktKVhkOA 1x, https://i1-vnexpress.vnecdn.net/2022/06/18/ec574f2e6b084375bd2547d049e697-6598-6142-1655561092.png?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=JQuszUreI0QtXuJA9gy5Jw 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/18/ec574f2e6b084375bd2547d049e697-6598-6142-1655561092.png?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=cJyAtyQm82SGF-WNhhnM2A 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/18/ec574f2e6b084375bd2547d049e697-6598-6142-1655561092.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=VMKgGfCwJgzXH_ktKVhkOA 1x, https://i1-vnexpress.vnecdn.net/2022/06/18/ec574f2e6b084375bd2547d049e697-6598-6142-1655561092.png?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=JQuszUreI0QtXuJA9gy5Jw 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/18/ec574f2e6b084375bd2547d049e697-6598-6142-1655561092.png?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=cJyAtyQm82SGF-WNhhnM2A 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/06/18/ec574f2e6b084375bd2547d049e697-6598-6142-1655561092.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=VMKgGfCwJgzXH_ktKVhkOA\" alt=\"C&ocirc;ng trường x&acirc;y dựng nơi Cử giấu thi thể Trần Mộng Đ&igrave;nh. Ảnh: Toutiao\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/06/18/ec574f2e6b084375bd2547d049e697-6598-6142-1655561092.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=VMKgGfCwJgzXH_ktKVhkOA\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">C&ocirc;ng trường x&acirc;y dựng nơi Cử giấu thi thể Trần Mộng Đ&igrave;nh. Ảnh:&nbsp;<em>Toutiao</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Theo b&aacute;o c&aacute;o kh&aacute;m nghiệm tử thi, nguy&ecirc;n nh&acirc;n dẫn đến c&aacute;i chết của Đ&igrave;nh l&agrave; ngạt thở do t&aacute;c động cơ học, tức l&agrave; vụ &aacute;n mạng. Kết quả kh&aacute;m nghiệm hiện trường kh&ocirc;ng t&igrave;m được manh mối n&agrave;o c&oacute; thể gi&uacute;p cảnh s&aacute;t t&igrave;m ra hung thủ, nhưng nhận định hung thủ c&oacute; thể ở th&agrave;nh phố n&agrave;y.</p>\r\n<p class=\"Normal\">Ngo&agrave;i ra, nơi ch&ocirc;n thi thể l&agrave; c&ocirc;ng trường l&agrave;m đường đ&atilde; gần ho&agrave;n thiện, nếu c&ocirc;ng nh&acirc;n kh&ocirc;ng t&igrave;nh cờ ph&aacute;t hiện, chỉ v&agrave;i ng&agrave;y nữa thi thể sẽ vĩnh viễn nằm s&acirc;u dưới mặt đường. Điều n&agrave;y chứng tỏ hung thủ rất am hiểu khu vực n&agrave;y. Tuy nhi&ecirc;n, điều n&agrave;y cũng kh&ocirc;ng thể gi&uacute;p cảnh s&aacute;t t&igrave;m ra hung thủ.</p>\r\n<p class=\"Normal\">15 năm tr&ocirc;i qua, c&ocirc;ng nghệ ADN ng&agrave;y c&agrave;ng ph&aacute;t triển, thậm ch&iacute; c&oacute; thể thu được ADN từ c&aacute;c tế b&agrave;o da chết tr&ecirc;n quần &aacute;o. To&agrave;n bộ quần &aacute;o của nạn nh&acirc;n vẫn bảo quản nguy&ecirc;n vẹn, được cảnh s&aacute;t đưa đến ph&ograve;ng th&iacute; nghiệm một lần nữa.</p>\r\n<p class=\"Normal\">Để kh&ocirc;ng bỏ s&oacute;t một chi tiết n&agrave;o, nh&acirc;n vi&ecirc;n ph&ograve;ng th&iacute; nghiệm cắt quần &aacute;o th&agrave;nh nhiều mảnh nhỏ để x&eacute;t nghiệm, ri&ecirc;ng &aacute;o l&oacute;t đ&atilde; được cắt th&agrave;nh 600 phần. Sau v&agrave;i ng&agrave;y l&agrave;m việc li&ecirc;n tục, cuối c&ugrave;ng cảnh s&aacute;t thu được mẫu ADN của một người lạ tr&ecirc;n mảnh &aacute;o l&oacute;t của Đ&igrave;nh.</p>\r\n<p class=\"Normal\">Đối chiếu với th&ocirc;ng tin trong kho dữ liệu, mẫu ADN n&agrave;y thuộc về người đ&agrave;n &ocirc;ng 47 tuổi t&ecirc;n l&agrave; Qu&aacute;ch Thi&ecirc;n Cử. Mặc d&ugrave; Cử kh&ocirc;ng quen biết Đ&igrave;nh hay cha mẹ Đ&igrave;nh, cảnh s&aacute;t vẫn c&oacute; cơ sở nghi ngờ. Thứ nhất, khoảng th&aacute;ng 10/2005, Cử đang kinh doanh một cửa h&agrave;ng tạp h&oacute;a rất tốt, đột nhi&ecirc;n lại quyết định đến Quảng Đ&ocirc;ng l&agrave;m thu&ecirc;, bất kể người nh&agrave; khuy&ecirc;n bảo thế n&agrave;o cũng kh&ocirc;ng nghe. Thứ hai, địa chỉ cửa h&agrave;ng tạp h&oacute;a của Cử chỉ c&aacute;ch nh&agrave; Đ&igrave;nh hơn 100 m.</p>\r\n<p class=\"Normal\">Nhiều dấu hiệu cho thấy Cử rất c&oacute; thể l&agrave; hung thủ s&aacute;t hại Đ&igrave;nh, nhưng chứng cứ chỉ l&agrave; mẫu ADN của Cử tr&ecirc;n mảnh &aacute;o l&oacute;t của Đ&igrave;nh, ho&agrave;n to&agrave;n kh&ocirc;ng đủ để buộc tội. Khi được hỏi về chuyện 15 năm trước, Cử n&oacute;i đ&atilde; qu&aacute; l&acirc;u n&ecirc;n kh&ocirc;ng nhớ nổi.</p>\r\n<p class=\"Normal\">Ban chuy&ecirc;n &aacute;n quyết định cử những điều tra vi&ecirc;n d&agrave;y dạn kinh nghiệm nhất phụ tr&aacute;ch thẩm vấn, mời chuy&ecirc;n gia t&acirc;m l&yacute; học tội phạm đến hỗ trợ. Sau nhiều ng&agrave;y thẩm vấn, cuối c&ugrave;ng ph&ograve;ng tuyến t&acirc;m l&yacute; của Cử cũng sụp đổ. Cử thừa nhận tội &aacute;c g&acirc;y ra năm 2005, b&agrave;y tỏ c&oacute; lỗi với cha mẹ của Đ&igrave;nh.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/18/77d2bed69b8f47e7803acc3f088857-7695-5851-1655561092.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=YYL-I4jQaX7KxDHb9JqlpQ 1x, https://i1-vnexpress.vnecdn.net/2022/06/18/77d2bed69b8f47e7803acc3f088857-7695-5851-1655561092.png?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=xuhjBVkgFTPdQcQS7oP3qQ 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/18/77d2bed69b8f47e7803acc3f088857-7695-5851-1655561092.png?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=Ycnc0euj7DIO59509xYBgg 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/18/77d2bed69b8f47e7803acc3f088857-7695-5851-1655561092.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=YYL-I4jQaX7KxDHb9JqlpQ 1x, https://i1-vnexpress.vnecdn.net/2022/06/18/77d2bed69b8f47e7803acc3f088857-7695-5851-1655561092.png?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=xuhjBVkgFTPdQcQS7oP3qQ 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/18/77d2bed69b8f47e7803acc3f088857-7695-5851-1655561092.png?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=Ycnc0euj7DIO59509xYBgg 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/06/18/77d2bed69b8f47e7803acc3f088857-7695-5851-1655561092.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=YYL-I4jQaX7KxDHb9JqlpQ\" alt=\"Cảnh s&aacute;t tr&iacute;ch xuất DNA của kẻ s&aacute;t nh&acirc;n tr&ecirc;n &aacute;o ngực của nạn nh&acirc;n. Ảnh: Toutiao\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/06/18/77d2bed69b8f47e7803acc3f088857-7695-5851-1655561092.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=YYL-I4jQaX7KxDHb9JqlpQ\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Cảnh s&aacute;t tr&iacute;ch xuất ADN của kẻ s&aacute;t nh&acirc;n tr&ecirc;n &aacute;o ngực của nạn nh&acirc;n. Ảnh:&nbsp;<em>Toutiao</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Cử khai nhận, hơn 2h ng&agrave;y 25/9/2005, Cử vừa chơi điện thoại di động vừa tr&ocirc;ng cửa h&agrave;ng tạp h&oacute;a. L&uacute;c n&agrave;y, một nữ sinh cấp ba đi v&agrave;o cửa h&agrave;ng. Thời điểm đ&oacute;, vợ chồng Cử đang c&atilde;i nhau đ&ograve;i ly h&ocirc;n, Cử kh&ocirc;ng để &yacute; đến kh&aacute;ch. Đến l&uacute;c Đ&igrave;nh mang m&igrave; ăn liền, x&uacute;c x&iacute;ch, nước lọc v&agrave; một tờ 50 tệ đặt l&ecirc;n tr&ecirc;n quầy, Cử mới ngồi dậy t&iacute;nh tiền, tiện tay n&eacute;m hơn 30 tệ tiền trả lại l&ecirc;n mặt quầy.</p>\r\n<p class=\"Normal\">Thấy chủ cửa h&agrave;ng bất lịch sự, trong số tiền trả lại c&oacute; một đồng cũ n&aacute;t, Đ&igrave;nh tức giận qu&aacute;t Cử, bắt đổi đồng tiền mới. Cử cho rằng Đ&igrave;nh đang cố t&igrave;nh g&acirc;y sự n&ecirc;n từ chối đổi tiền v&agrave; bị chửi mắng thậm tệ. Đang bực sẵn, Cử cho Đ&igrave;nh một c&aacute;i bạt tai. Hai người lao v&agrave;o đ&aacute;nh nhau, đến khi Cử bu&ocirc;ng tay ra th&igrave; Đ&igrave;nh đ&atilde; nằm rũ dưới đất kh&ocirc;ng hề nh&uacute;c nh&iacute;ch.</p>\r\n<p class=\"Normal\">Thấy thiếu nữ kh&ocirc;ng c&ograve;n thở, suy nghĩ đầu ti&ecirc;n trong đầu Cử l&agrave; đi tự th&uacute;. Nhưng thấy đang đ&ecirc;m h&ocirc;m khuya khoắt, tr&ecirc;n đường kh&ocirc;ng c&oacute; người, Cử lại nghĩ kh&ocirc;ng người n&agrave;o biết th&igrave; việc g&igrave; phải tự th&uacute;. Cử mang thi thể đi ch&ocirc;n để che giấu tội &aacute;c.</p>\r\n<p class=\"Normal\">Trong v&agrave;i ng&agrave;y sau đ&oacute;, Cử sống trong sợ h&atilde;i. Thiếu nữ đến cửa h&agrave;ng mua đồ l&uacute;c qu&aacute; nửa đ&ecirc;m, chắc chắn l&agrave; người gần đ&acirc;y, cảnh s&aacute;t sẽ t&igrave;m đến bắt hắn. Cử lấy cớ đi kiếm tiền, rời bỏ qu&ecirc; qu&aacute;n đến tỉnh Quảng Đ&ocirc;ng l&agrave;m thu&ecirc;, đến năm 2014 mới quay về qu&ecirc; cũ. Thấy kh&ocirc;ng ai t&igrave;m m&igrave;nh, Cử cho rằng đ&atilde; tho&aacute;t tội, kh&ocirc;ng ngờ cuối c&ugrave;ng vẫn sa lưới ph&aacute;p luật.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/18/4d6a042419744ac1b4198748a7b495-2959-7568-1655561092.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=VB3RrnaQXaZkMCkBRS-qWQ 1x, https://i1-vnexpress.vnecdn.net/2022/06/18/4d6a042419744ac1b4198748a7b495-2959-7568-1655561092.png?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=BtvX_hqYjlIYJp-HDK7ybA 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/18/4d6a042419744ac1b4198748a7b495-2959-7568-1655561092.png?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=A9pJgcyXgPgBjBbhuQD0uw 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/18/4d6a042419744ac1b4198748a7b495-2959-7568-1655561092.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=VB3RrnaQXaZkMCkBRS-qWQ 1x, https://i1-vnexpress.vnecdn.net/2022/06/18/4d6a042419744ac1b4198748a7b495-2959-7568-1655561092.png?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=BtvX_hqYjlIYJp-HDK7ybA 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/18/4d6a042419744ac1b4198748a7b495-2959-7568-1655561092.png?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=A9pJgcyXgPgBjBbhuQD0uw 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/06/18/4d6a042419744ac1b4198748a7b495-2959-7568-1655561092.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=VB3RrnaQXaZkMCkBRS-qWQ\" alt=\"Qu&aacute;ch Thi&ecirc;n Cử khi bị bắt. Ảnh: Toutiao\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/06/18/4d6a042419744ac1b4198748a7b495-2959-7568-1655561092.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=VB3RrnaQXaZkMCkBRS-qWQ\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Qu&aacute;ch Thi&ecirc;n Cử khi bị bắt. Ảnh:&nbsp;<em>Toutiao</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Theo luật tố tụng h&igrave;nh sự Trung Quốc, to&agrave;n bộ chứng cứ trong một vụ &aacute;n phải tạo th&agrave;nh chuỗi chứng cứ kh&ocirc;ng m&acirc;u thuẫn với nhau, c&oacute; thể chứng thực lẫn nhau, chứng minh t&iacute;nh ch&acirc;n thực của vụ &aacute;n.</p>\r\n<p class=\"Normal\">Trong lời khai, Cử mi&ecirc;u tả kiểu t&oacute;c, quần &aacute;o nạn nh&acirc;n ph&ugrave; hợp với Đ&igrave;nh, loại bao tải d&ugrave;ng để chứa thi thể cũng ch&iacute;nh x&aacute;c. C&ugrave;ng với việc Cử dẫn cảnh s&aacute;t đến chỉ ch&iacute;nh x&aacute;c vị tr&iacute; ch&ocirc;n thi thể năm đ&oacute;, chuỗi chứng cứ đ&atilde; được h&igrave;nh th&agrave;nh.</p>\r\n<p class=\"Normal\">Hiện vụ &aacute;n tiếp tục được điều tra để ho&agrave;n thiện chuỗi chứng cứ trước khi đưa ra x&eacute;t xử.</p>\r\n</article>', 218, 9, 0, 1, '2022-11-21 19:35:42', '2022-11-22 06:00:17'),
(162, 'Trung Quốc xôn xao vì trường hợp anh em họ lấy nhau', 'Trung-quoc-xon-xao-vi-truong-hop-anh-em-ho-lay-nhau', 'Bệnh nhân nữ không thể mang thai vì kết hôn với anh họ làm dấy lên cuộc tranh luận về hôn nhân cận huyết.', '<p class=\"description\">Bệnh nh&acirc;n nữ kh&ocirc;ng thể mang thai v&igrave; kết h&ocirc;n với anh họ l&agrave;m dấy l&ecirc;n cuộc tranh luận về h&ocirc;n nh&acirc;n cận huyết.</p>\r\n<article class=\"fck_detail \">\r\n<div id=\"divfirst\">\r\n<p class=\"Normal\">Người d&ugrave;ng mạng x&atilde; hội Trung Quốc đang b&agrave;n luận s&ocirc;i nổi về chủ đề kết h&ocirc;n cận huyết sau khi xuất hiện video b&aacute;c sĩ khuy&ecirc;n bệnh nh&acirc;n t&igrave;m người hiến tinh tr&ugrave;ng h&ocirc;m 1/6.</p>\r\n<p class=\"Normal\">Trong video, b&aacute;c sĩ tư vấn cho bệnh nh&acirc;n nữ ở tỉnh Cam T&uacute;c, t&acirc;y bắc Trung Quốc từng sảy thai 4 lần, ba lần thụ tinh ống nghiệm kh&ocirc;ng th&agrave;nh. B&aacute;c sĩ ngạc nhi&ecirc;n khi biết người chồng l&agrave; con của b&aacute;c g&aacute;i, chị ruột bố của bệnh nh&acirc;n.</p>\r\n<p class=\"Normal\">\"Theo t&ocirc;i được biết th&igrave; chỉ người &iacute;t học mới lấy họ h&agrave;ng gần\", b&aacute;c sĩ n&oacute;i. \"Cả c&ocirc; v&agrave; chồng đều l&agrave; cử nh&acirc;n đại học, tại sao c&ocirc; vẫn lấy chồng l&agrave; họ h&agrave;ng gần?\".</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture data-inimage=\"done\"><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/19/image-1-1655603420-6078-1655603986.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=PyrdhalNi8Z97UaA8bd9tQ 1x, https://i1-vnexpress.vnecdn.net/2022/06/19/image-1-1655603420-6078-1655603986.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Rp72IiuztrGAx9zKMs7iWQ 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/19/image-1-1655603420-6078-1655603986.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=PdQdtejgwe_nRPLts0PCbg 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/19/image-1-1655603420-6078-1655603986.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=PyrdhalNi8Z97UaA8bd9tQ 1x, https://i1-vnexpress.vnecdn.net/2022/06/19/image-1-1655603420-6078-1655603986.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Rp72IiuztrGAx9zKMs7iWQ 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/19/image-1-1655603420-6078-1655603986.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=PdQdtejgwe_nRPLts0PCbg 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/06/19/image-1-1655603420-6078-1655603986.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=PyrdhalNi8Z97UaA8bd9tQ\" alt=\"B&aacute;c sĩ tư vấn cho bệnh nh&acirc;n kết h&ocirc;n cận huyết ở tỉnh Cam T&uacute;c, Trung Quốc. Ảnh: SCMP\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/06/19/image-1-1655603420-6078-1655603986.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=PyrdhalNi8Z97UaA8bd9tQ\" data-ll-status=\"loaded\" data-adbro-processed=\"true\"></picture>\r\n<div class=\"embed-container-ads\">\r\n<div id=\"sis_inimage\" data-google-query-id=\"COOgseXsuPgCFYeTlgod8ZkNXw\">\r\n<div id=\"google_ads_iframe_/27973503/Vnexpress/Desktop/Inimage/Thegioi/Thegioi.cuocsongdoday.detail_0__container__\"><iframe id=\"google_ads_iframe_/27973503/Vnexpress/Desktop/Inimage/Thegioi/Thegioi.cuocsongdoday.detail_0\" tabindex=\"0\" title=\"3rd party ad content\" role=\"region\" name=\"google_ads_iframe_/27973503/Vnexpress/Desktop/Inimage/Thegioi/Thegioi.cuocsongdoday.detail_0\" width=\"1\" height=\"1\" frameborder=\"0\" marginwidth=\"0\" marginheight=\"0\" scrolling=\"no\" aria-label=\"Advertisement\" data-google-container-id=\"4\" data-load-complete=\"true\"></iframe></div>\r\n</div>\r\n</div>\r\n</div>\r\n<figcaption>\r\n<p class=\"Image\">B&aacute;c sĩ tư vấn cho bệnh nh&acirc;n kết h&ocirc;n cận huyết ở tỉnh Cam T&uacute;c, Trung Quốc. Ảnh:&nbsp;<em>SCMP</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Người phụ nữ tỏ ra ngượng ng&ugrave;ng. B&aacute;c sĩ hỏi tiếp: \"C&ocirc; đ&atilde; bao giờ t&iacute;nh chuyện chia tay để t&igrave;m người kh&aacute;c th&iacute;ch hợp kh&ocirc;ng?\" Bệnh nh&acirc;n trả lời: \"Vậy th&igrave; ch&uacute;ng t&ocirc;i th&agrave; kh&ocirc;ng c&oacute; con\", sau đ&oacute; giải th&iacute;ch quan hệ giữa hai vợ chồng rất tốt.</p>\r\n</div>\r\n<div id=\"admbackground\" data-set=\"dfp\"></div>\r\n<div id=\"divend\">\r\n<p class=\"Normal\">Cuối c&ugrave;ng, b&aacute;c sĩ đề nghị t&igrave;m người hiến tinh tr&ugrave;ng bởi đ&oacute; l&agrave; c&aacute;ch tốt nhất để sinh con khỏe mạnh. \"V&acirc;ng, đ&uacute;ng l&agrave; thế\", bệnh nh&acirc;n nữ đồng &yacute;, n&oacute;i th&ecirc;m \"cả chồng t&ocirc;i v&agrave; gia đ&igrave;nh đều đồng &yacute; phương &aacute;n n&agrave;y\".</p>\r\n<p class=\"Normal\">Từ thời cổ đại, Trung Quốc đ&atilde; cấm h&ocirc;n nh&acirc;n giữa anh em c&ugrave;ng d&ograve;ng tộc về đằng nh&agrave; nội, nghĩa l&agrave; con c&aacute;i của hai người đ&agrave;n &ocirc;ng l&agrave; anh em ruột, nhưng cho ph&eacute;p h&ocirc;n nh&acirc;n kh&aacute;c họ, tới khi luật cấm h&ocirc;n nh&acirc;n cận huyết hiện h&agrave;nh ra đời năm 1981.</p>\r\n<p class=\"Normal\">Tuy nhi&ecirc;n, một nghi&ecirc;n cứu cho thấy h&ocirc;n nh&acirc;n cận huyết vẫn tồn tại bất chấp luật ph&aacute;p, đặc biệt ở khu vực miền n&uacute;i v&agrave; hải đảo do mạng lưới giao th&ocirc;ng k&eacute;m ph&aacute;t triển. Nhiều người tr&ecirc;n mạng x&atilde; hội ngạc nhi&ecirc;n khi biết ở thời hiện đại vẫn tồn tại trường hợp người c&oacute; học vấn cao kết h&ocirc;n cận huyết.</p>\r\n<p class=\"Normal\">\"Tại sao kh&ocirc;ng học kỹ kiến thức trước khi y&ecirc;u đương với họ h&agrave;ng gần?\", một người b&igrave;nh luận.</p>\r\n<p class=\"Normal\">\"T&ocirc;i kh&ocirc;ng phản đối kiểu quan hệ n&agrave;y nhưng t&ocirc;i nghĩ kh&ocirc;ng n&ecirc;n sinh con v&igrave; đ&oacute; l&agrave; thiếu t&ocirc;n trọng mạng sống của trẻ em bởi khả năng mang dị tật cao\", một người kh&aacute;c b&agrave;y tỏ.</p>\r\n</div>\r\n</article>', 218, 2, 4, 1, '2022-11-21 19:35:42', '2023-02-08 01:12:01'),
(163, 'Người dùng ngân hàng lại điêu đứng vì phần mềm độc hại này', 'nguoi-dung-ngan-hang-lai-dieu-dung-vi-phan-mem-doc-hai-nay', 'Vừa qua, các nhà nghiên cứu bảo mật tại F5 Labs đã phát hiện một phần mềm độc hại Android mới, nhắm mục tiêu vào người dùng ví điện tử và ngân hàng trực tuyến.', '<p>Phần mềm độc hại được gọi l&agrave; MaliBot (m&aacute;y chủ điều khiển đặt tại Nga), được thiết kế nhằm đ&aacute;nh cắp th&ocirc;ng tin đăng nhập v&agrave; cookie, bỏ qua m&atilde; x&aacute;c thực đa yếu tố (MFA) v&agrave; lạm dụng dịch vụ trợ năng tr&ecirc;n&nbsp;<a class=\"TextlinkBaiviet\" title=\"Android\" href=\"https://www.24h.com.vn/android-c407e4051.html\">Android</a>&nbsp;để theo d&otilde;i m&agrave;n h&igrave;nh thiết bị của nạn nh&acirc;n.</p>\r\n<p>Dịch vụ trợ năng l&agrave; một dịch vụ nền chạy tr&ecirc;n c&aacute;c thiết bị Android nhằm hỗ trợ người d&ugrave;ng khuyết tật. Tuy nhi&ecirc;n, dịch vụ n&agrave;y từ l&acirc;u đ&atilde; bị phần mềm gi&aacute;n điệp v&agrave; Trojan tận dụng để lấy nội dung v&agrave; đ&aacute;nh cắp th&ocirc;ng tin đăng nhập.</p>\r\n<p align=\"center\">&nbsp;</p>\r\n<div id=\"container-24h-banner-in-image\">\r\n<div>\r\n<div><img class=\"news-image initial loading\" src=\"https://icdn.24h.com.vn/upload/2-2022/images/2022-06-19//1655600731-1f75f3906cfed6551384af765f301e8f-width850height445.jpg\" alt=\"Người d&ugrave;ng ng&acirc;n h&agrave;ng lại đi&ecirc;u đứng v&igrave; phần mềm độc hại n&agrave;y - 1\" width=\"660\" data-was-processed=\"true\"></div>\r\n<div id=\"24h-banner-in-image\">\r\n<div id=\"JYWBKmfrYf_139_15s\" class=\"txtCent\">\r\n<div class=\"text_adver_right\">&nbsp;</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n<p>&nbsp;</p>\r\n<p>MaliBot l&agrave; phi&ecirc;n bản được sửa đổi lại từ phần mềm độc hại SOVA (c&oacute; nghĩa l&agrave; &ldquo;c&uacute;&rdquo; trong tiếng Nga). SOVA được ph&aacute;t hiện lần đầu ti&ecirc;n v&agrave;o th&aacute;ng 8 năm 2021, nổi bật với khả năng tấn c&ocirc;ng bằng lớp phủ, giả mạo giao diện đăng nhập ng&acirc;n h&agrave;ng v&agrave; đ&aacute;nh cắp th&ocirc;ng tin của nạn nh&acirc;n.</p>\r\n<p>Theo nh&agrave; nghi&ecirc;n cứu bảo mật Dor Nizar của F5 Labs, MaliBot chủ yếu ngụy trang th&agrave;nh c&aacute;c ứng dụng khai th&aacute;c tiền điện tử như Mining X hoặc The CryptoApp, v&agrave; được ph&acirc;n phối th&ocirc;ng qua c&aacute;c trang web lừa đảo.</p>\r\n<p>Một số ng&acirc;n h&agrave;ng được MaliBot nhắm mục ti&ecirc;u bao gồm UniCredit, Santander, CaixaBank v&agrave; CartaBCC.</p>\r\n<p align=\"center\"><img class=\"news-image loaded\" src=\"https://icdn.24h.com.vn/upload/2-2022/images/2022-06-19//1655600731-2f984ea4107a374f8fbd66c455cf7959-width850height532.jpeg\" alt=\"Người d&ugrave;ng ng&acirc;n h&agrave;ng lại đi&ecirc;u đứng v&igrave; phần mềm độc hại n&agrave;y - 3\" width=\"660\" data-original=\"https://icdn.24h.com.vn/upload/2-2022/images/2022-06-19//1655600731-2f984ea4107a374f8fbd66c455cf7959-width850height532.jpeg\" data-was-processed=\"true\"></p>\r\n<p>B&ecirc;n cạnh việc đ&aacute;nh cắp mật khẩu v&agrave; cookie của nạn nh&acirc;n, phần mềm độc hại c&ograve;n c&oacute; khả năng qu&eacute;t m&atilde; 2FA từ ứng dụng Google Authenticator, lọc th&ocirc;ng tin nhạy cảm như tổng số dư tr&ecirc;n ứng dụng Binance v&agrave; Trust Wallet.</p>\r\n<p>C&aacute;c nh&agrave; nghi&ecirc;n cứu cho biết: &ldquo;T&iacute;nh linh hoạt v&agrave; khả năng kiểm so&aacute;t của phần mềm độc hại cho ph&eacute;p kẻ gian c&oacute; thể thực hiện h&agrave;ng loạt c&aacute;c cuộc tấn c&ocirc;ng, thay v&igrave; chỉ đơn thuần đ&aacute;nh cắp th&ocirc;ng tin c&aacute; nh&acirc;n&rdquo;.</p>\r\n<p>Tr&ecirc;n thực tế, bất kỳ ứng dụng n&agrave;o sử dụng WebView đều c&oacute; thể bị đ&aacute;nh cắp th&ocirc;ng tin đăng nhập v&agrave; cookie.</p>\r\n<p>Trước đ&oacute; kh&ocirc;ng l&acirc;u, c&aacute;c nh&agrave; nghi&ecirc;n cứu bảo mật tại Avast cũng đ&atilde; ph&aacute;t hiện một phần mềm độc hại mới c&oacute; t&ecirc;n &ldquo;FakeCrack&rdquo;, được thiết kế để đ&aacute;nh cắp mật khẩu v&agrave; th&ocirc;ng tin đăng nhập ng&acirc;n h&agrave;ng. Phần lớn nạn nh&acirc;n bị tấn c&ocirc;ng đều ở Ph&aacute;p, Brazil, Indonesia v&agrave; Ấn Độ.</p>\r\n<p>Theo BleepingComputer, bằng c&aacute;ch sử dụng c&aacute;c phương ph&aacute;p SEO mũ đen, kẻ gian đ&atilde; đưa những li&ecirc;n kết c&oacute; chứa phần mềm độc hại FakeCrack l&ecirc;n đầu kết quả t&igrave;m kiếm tr&ecirc;n Google.</p>\r\n<p>Phần mềm độc hại ẩn m&igrave;nh b&ecirc;n trong c&aacute;c phi&ecirc;n bản &ldquo;bẻ kh&oacute;a&rdquo; của những c&ocirc;ng cụ phổ biến, đơn cử như CCleaner Professional, Movavi Video Editor v&agrave; Microsoft Office.</p>\r\n<p align=\"center\"><img class=\"news-image loaded\" src=\"https://icdn.24h.com.vn/upload/2-2022/images/2022-06-19//1655600731-a169d1cdd74739f5e7e3537fb9826967-width850height885.jpeg\" alt=\"Người d&ugrave;ng ng&acirc;n h&agrave;ng lại đi&ecirc;u đứng v&igrave; phần mềm độc hại n&agrave;y - 4\" width=\"660\" data-original=\"https://icdn.24h.com.vn/upload/2-2022/images/2022-06-19//1655600731-a169d1cdd74739f5e7e3537fb9826967-width850height885.jpeg\" data-was-processed=\"true\"></p>\r\n<p>Khi nhấp v&agrave;o li&ecirc;n kết, kẻ gian sẽ y&ecirc;u cầu bạn tải xuống một tệp tin n&eacute;n (ZIP) c&oacute; mật khẩu bảo vệ, điều n&agrave;y nhằm qua mặt c&aacute;c phần mềm chống virus. Nếu l&agrave;m theo c&aacute;c bước hướng dẫn v&agrave; nhấp v&agrave;o tệp tin c&agrave;i đặt (cracketup.exe hoặc setup.exe), FakeCrack sẽ ngay lập tức x&acirc;m nhập m&aacute;y t&iacute;nh.</p>\r\n<p>Thống k&ecirc; cho thấy mỗi ng&agrave;y c&oacute; trung b&igrave;nh khoảng 10.000 lần l&acirc;y nhiễm phần mềm độc hại.</p>\r\n<p>Ngo&agrave;i việc lấy cắp tất cả th&ocirc;ng tin t&agrave;i ch&iacute;nh của bạn, phần mềm độc hại n&agrave;y c&ograve;n c&oacute; khả năng sao ch&eacute;p nội dung trong bộ nhớ tạm.</p>', 218, 10, 0, 1, '2022-11-21 19:35:42', '2022-11-22 06:11:40'),
(166, 'Phát hiện “Siêu Trái Đất” gần chúng ta: Có thể tồn tại sự sống?', 'phat-hien-sieu-trai-dat-gan-chung-ta-co-the-ton-tai-su-song', 'Các nhà khoa học vừa phát hiện một hành tinh đặc biệt được gọi là \"bản sao phóng to của Trái đất\" ở khá gần chúng ta và được hy vọng có thể nuôi dưỡng sự sống.', '<blockquote>C&aacute;c nh&agrave; khoa học vừa ph&aacute;t hiện một h&agrave;nh tinh đặc biệt được gọi l&agrave; \"bản sao ph&oacute;ng to của Tr&aacute;i đất\" ở kh&aacute; gần ch&uacute;ng ta v&agrave; được hy vọng c&oacute; thể nu&ocirc;i dưỡng sự sống.</blockquote>\r\n<div class=\"clearfix\">&nbsp;</div>\r\n<div>\r\n<div>\r\n<div><img src=\"https://static.tin.moi/uploads/news/photo/a8/a8P/a8PFH3FGdu-1200x900.jpg\" height=\"500\"></div>\r\n<p>Quanh một ng&ocirc;i sao l&ugrave;n đỏ mờ c&oacute; t&ecirc;n Ross 508 c&aacute;ch ch&uacute;ng ta chỉ 36,5 &aacute;nh s&aacute;ng, c&aacute;c nh&agrave; khoa học vừa t&igrave;m thấy thứ c&oacute; thể l&agrave; bản sao ph&oacute;ng to của Tr&aacute;i đất:&nbsp;<strong>Si&ecirc;u tr&aacute;i đất</strong>&nbsp;Ross 508 b.</p>\r\n</div>\r\n<div>\r\n<div><img src=\"https://static.tin.moi/uploads/news/photo/uO/uOg/uOgVZvFGU8-550x339.jpg\" height=\"500\"></div>\r\n<p>Nghi&ecirc;n cứu mới của nh&agrave; thi&ecirc;n văn học Hiroki Harakawa từ k&iacute;nh viễn vọng Subaru (đặt tại Hawaii - Mỹ) v&agrave; Đ&agrave;i quan s&aacute;t thi&ecirc;n văn Quốc gia Nhật Bản (NAOJ) cho thấy Ross 508 b l&agrave; một h&agrave;nh tinh đầy hứa hẹn cho cuộc săn t&igrave;m sự sống.</p>\r\n</div>\r\n<div>\r\n<div><img src=\"https://static.tin.moi/uploads/news/photo/E0/E0Q/E0Qz3tU9ka-960x540.jpg\" height=\"500\"></div>\r\n<p>H&agrave;nh tinh n&agrave;y c&oacute; khối lượng gấp 4 lần Tr&aacute;i Đất, quay quanh ng&ocirc;i&nbsp;<em>sao l&ugrave;n mẹ</em>&nbsp;m&agrave;u đỏ thẫm 10,75 ng&agrave;y/lần.</p>\r\n</div>\r\n<div>\r\n<div><img src=\"https://static.tin.moi/uploads/news/photo/9q/9qe/9qeXbD6E3f-607x404.jpg\" height=\"500\"></div>\r\n<p>Đ&oacute; l&agrave; một quỹ đạo v&ocirc; c&ugrave;ng hẹp so với Tr&aacute;i Đất, nhưng v&igrave; Ross 508 l&agrave; ng&ocirc;i sao l&ugrave;n đỏ m&aacute;t hơn Mặt trời nhiều n&ecirc;n v&ocirc; t&igrave;nh khoảng c&aacute;ch gần đ&oacute; khiến h&agrave;nh tinh nằm ngay cạnh r&igrave;a của \"v&ugrave;ng sự sống\".</p>\r\n</div>\r\n<div>\r\n<div><img src=\"https://static.tin.moi/uploads/news/photo/8u/8uj/8uj263Qzld-580x505.jpg\" height=\"500\"></div>\r\n<p>\"V&ugrave;ng sự sống\" - Goldilocks - của một ng&ocirc;i sao l&agrave; nơi c&oacute; c&aacute;c điều kiện nhiệt độ, bức xạ ph&ugrave; hợp để nước ở trạng th&aacute;i lỏng v&agrave; sự sống c&oacute; cơ hội ph&aacute;t sinh. Nhưng v&ugrave;ng n&agrave;y cũng mang t&iacute;nh chất tương đối.</p>\r\n</div>\r\n<div>\r\n<div><img src=\"https://static.tin.moi/uploads/news/photo/DT/DTa/DTaGJ4ajHN-1200x675.jpg\" height=\"500\"></div>\r\n<p>V&ugrave;ng sự sống của hệ Mặt trời c&oacute; sao Kim, Tr&aacute;i đất v&agrave; sao Hỏa, nhưng 2 h&agrave;nh tinh gần ch&uacute;ng ta c&oacute; vẻ kh&oacute; c&oacute; sự sống hiện đại, trong khi một số mặt trăng của sao Mộc, sao Thổ như Europa, Enceladus nằm ngo&agrave;i v&ugrave;ng sự sống th&igrave; lại c&oacute; thể... đang c&oacute; sự sống, theo c&aacute;c nghi&ecirc;n cứu từ NASA.</p>\r\n</div>\r\n<p>Nghi&ecirc;n cứu mới cho thấy Ross 508 b l&agrave; một ứng cử vi&ecirc;n tốt, nhất l&agrave; khi c&aacute;c ph&eacute;p đo cho thấy bức xạ n&oacute; nhận được từ sao mẹ chỉ khoảng 1,4 lần bức xạ m&agrave; Tr&aacute;i đất nhận từ Mặt Trời.</p>\r\n<div>\r\n<div><img src=\"https://static.tin.moi/uploads/news/photo/Fi/Fi6/Fi66EblAGr-750x450.jpg\" height=\"500\"></div>\r\n<p>Đ&acirc;y l&agrave; mức bức xạ chấp nhận được với sự sống, ngay cả c&aacute;c dạng sống th&ocirc;ng thường tr&ecirc;n Tr&aacute;i đất, nhất l&agrave; nếu n&oacute; được bảo vệ bởi một bầu kh&iacute; quyển ph&ugrave; hợp.</p>\r\n</div>\r\n<div>\r\n<div><img src=\"https://static.tin.moi/uploads/news/photo/wC/wCZ/wCZeJdws5b-580x326.jpg\" height=\"500\"></div>\r\n<p>C&aacute;ch đ&acirc;y v&agrave;i năm, Ross 508 b đ&atilde; được quan s&aacute;t lần đầu cũng bởi k&iacute;nh viễn vọng Subaru, với c&aacute;c dấu hiệu cho thấy n&oacute; l&agrave; một si&ecirc;u Tr&aacute;i đất.</p>\r\n</div>\r\n<div>\r\n<div><img src=\"https://static.tin.moi/uploads/news/photo/Kf/KfI/KfIpNQgLkH-600x597.jpg\" height=\"500\"></div>\r\n<p>Tuy nhi&ecirc;n l&uacute;c đ&oacute; khả năng c&oacute; sự sống của n&oacute; bị bỏ qua, bởi c&aacute;c nh&agrave; khoa học nghĩ rằng một thế giới nhỏ hơn, k&iacute;ch cỡ như Tr&aacute;i đất sẽ dễ sống hơn.</p>\r\n</div>\r\n</div>', 218, 11, 1, 1, '2022-11-21 19:35:42', '2022-12-08 09:01:18'),
(168, 'Lo ngại bất công bằng do quy định giảm điểm ưu tiên', 'lo-ngai-bat-cong-bang-do-quy-dinh-giam-diem-uu-tien', 'Quy chế mới, yêu cầu giảm điểm cộng ưu tiên cho thí sinh 22,5 điểm trở lên, được dự đoán sẽ tạo tác động lớn, thậm chí gây ra bất công mới trong xét tuyển đại học.', '<p class=\"description\">Quy chế mới, y&ecirc;u cầu giảm điểm cộng ưu ti&ecirc;n cho th&iacute; sinh 22,5 điểm trở l&ecirc;n, được dự đo&aacute;n sẽ tạo t&aacute;c động lớn, thậm ch&iacute; g&acirc;y ra bất c&ocirc;ng mới trong x&eacute;t tuyển đại học.</p>\r\n<article class=\"fck_detail \">\r\n<div id=\"divfirst\">\r\n<p class=\"Normal\">Theo quy chế tuyển sinh của Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo, từ năm 2023, th&iacute; sinh đạt 22,5 điểm trở l&ecirc;n khi x&eacute;t tuyển đại học&nbsp;<a href=\"https://vnexpress.net/giam-muc-cong-uu-tien-voi-thi-sinh-dat-tu-22-5-diem-4474387.html\" rel=\"dofollow\">kh&ocirc;ng được cộng tối đa</a>&nbsp;điểm ưu ti&ecirc;n; đạt 30 điểm sẽ kh&ocirc;ng được cộng.</p>\r\n<p class=\"Normal\">Quy định n&agrave;y kh&ocirc;ng chỉ &aacute;p dụng với th&iacute; sinh x&eacute;t kết quả thi tốt nghiệp THPT, m&agrave; c&ograve;n với c&aacute;c phương thức kh&aacute;c, bằng c&aacute;ch quy ra thang điểm tương đương để x&aacute;c định mức ưu ti&ecirc;n ph&ugrave; hợp.</p>\r\n<p class=\"Normal\">C&ocirc;ng thức t&iacute;nh l&agrave;:&nbsp;<em>Mức điểm ưu ti&ecirc;n th&iacute; sinh được hưởng = [(30 - Tổng điểm đạt được của th&iacute; sinh)/7,5] x Tổng điểm ưu ti&ecirc;n được x&aacute;c định th&ocirc;ng thường</em></p>\r\n<p class=\"Normal\">Với c&aacute;ch t&iacute;nh n&agrave;y, mức điểm ưu ti&ecirc;n th&iacute; sinh được hưởng sẽ giảm dần theo tổng điểm họ đạt được, theo biểu đồ sau:</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/18/bieu-do-5663-1655517128.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Bg4rn5TW5kHpUyTWFIZWjg 1x, https://i1-vnexpress.vnecdn.net/2022/06/18/bieu-do-5663-1655517128.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=LUSW8eyp1JUggkWN01fPug 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/18/bieu-do-5663-1655517128.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=O0i7YRcAQpxKj5Uhm-EeSg 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/18/bieu-do-5663-1655517128.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Bg4rn5TW5kHpUyTWFIZWjg 1x, https://i1-vnexpress.vnecdn.net/2022/06/18/bieu-do-5663-1655517128.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=LUSW8eyp1JUggkWN01fPug 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/18/bieu-do-5663-1655517128.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=O0i7YRcAQpxKj5Uhm-EeSg 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/06/18/bieu-do-5663-1655517128.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Bg4rn5TW5kHpUyTWFIZWjg\" alt=\"Lo ngại bất c&ocirc;ng bằng do quy định giảm điểm ưu ti&ecirc;n\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/06/18/bieu-do-5663-1655517128.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Bg4rn5TW5kHpUyTWFIZWjg\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption></figcaption>\r\n</figure>\r\n<p class=\"Normal\">Hiện c&oacute; hai diện ưu ti&ecirc;n trong x&eacute;t tuyển đại học: ưu ti&ecirc;n khu vực (cộng th&ecirc;m 0,25-0,75) v&agrave; ưu ti&ecirc;n đối tượng ch&iacute;nh s&aacute;ch (1-2 điểm). Một th&iacute; sinh c&oacute; thể được cộng tối đa 2,75 điểm ưu ti&ecirc;n.</p>\r\n<p class=\"Normal\">Chuy&ecirc;n gia tuyển sinh của một đại học tại H&agrave; Nội nhận định, điều chỉnh cộng điểm ưu ti&ecirc;n sẽ&nbsp;<strong>tạo ra t&aacute;c động lớn với x&eacute;t tuyển đại học</strong>. Trong x&eacute;t tuyển đại học, ch&ecirc;nh nhau 0,1 điểm cộng cũng đ&atilde; l&agrave;m tăng hoặc giảm h&agrave;ng trăm em tr&uacute;ng tuyển.</p>\r\n<p class=\"Normal\">Thay đổi n&agrave;y, theo &ocirc;ng, sẽ g&acirc;y ra bức x&uacute;c cho phụ huynh v&agrave; th&iacute; sinh v&igrave; n&oacute; tạo ra bất cập mới; chẳng hạn hai học sinh c&ugrave;ng học tập ở v&ugrave;ng kh&oacute; khăn nhưng một em &iacute;t được ưu ti&ecirc;n hơn chỉ v&igrave; nỗ lực hơn hoặc c&oacute; tố chất tốt hơn.</p>\r\n<p class=\"Normal\">Tuy nhi&ecirc;n, chuy&ecirc;n gia n&agrave;y vẫn cho rằng, ch&iacute;nh s&aacute;ch mới l&agrave; ph&ugrave; hợp, đảm bảo c&ocirc;ng bằng cho c&aacute;c nh&oacute;m th&iacute; sinh điểm cao, muốn v&agrave;o c&aacute;c trường top đầu, tr&aacute;nh t&igrave;nh trạng 29-30 điểm vẫn trượt, do phải \"nhường chỗ\" cho những bạn được cộng điểm. Với một số ng&agrave;nh c&oacute; điểm chuẩn cao như Y, Dược, số lượng th&iacute; sinh tr&uacute;ng tuyển nhờ điểm ưu ti&ecirc;n sẽ giảm.</p>\r\n<p class=\"Normal\">B&agrave; Nguyễn Thu Thuỷ, Vụ trưởng Gi&aacute;o dục đại học, cũng cho biết điều chỉnh n&agrave;y gi&uacute;p tr&aacute;nh hiện tượng tr&ecirc;n; tạo c&ocirc;ng bằng ở nh&oacute;m th&iacute; sinh điểm cao khi cạnh tranh v&agrave;o c&aacute;c ng&agrave;nh/ trường h&agrave;ng đầu.</p>\r\n<p class=\"Normal\">Qua ph&acirc;n t&iacute;ch c&aacute;c năm trước, điểm x&eacute;t tuyển của th&iacute; sinh trước v&agrave; sau khi được cộng điểm ưu ti&ecirc;n c&oacute; sự thay đổi r&otilde; rệt, đặc biệt từ mức tr&ecirc;n 22,5. Từ mức n&agrave;y, khi chưa được cộng ưu ti&ecirc;n, nh&oacute;m th&iacute; sinh ở khu vực 1, 2 v&agrave; 2 - n&ocirc;ng th&ocirc;n c&oacute; điểm trung b&igrave;nh thấp hơn nh&oacute;m kh&ocirc;ng được cộng (khu vực 3). Nhưng khi được cộng, điểm trung b&igrave;nh của nh&oacute;m n&agrave;y lại cao hơn hẳn. Ở nhiều ng&agrave;nh cạnh tranh cao, tỷ lệ tr&uacute;ng tuyển của những th&iacute; sinh kh&ocirc;ng được cộng điểm ưu ti&ecirc;n rất thấp, trong khi nh&oacute;m n&agrave;y học tốt hơn. Như vậy, theo b&agrave;, nh&oacute;m n&agrave;y bị bất lợi nhiều nhất.</p>\r\n</div>\r\n<div id=\"admbackground\" data-set=\"dfp\"></div>\r\n<div id=\"divend\">\r\n<p class=\"Normal\">Số th&iacute; sinh thuộc diện hưởng c&aacute;c ưu ti&ecirc;n kh&aacute;c nhau l&agrave; rất lớn, chiếm 75% số lượng tốt nghiệp h&agrave;ng năm. &Aacute;p dụng ch&iacute;nh s&aacute;ch ưu ti&ecirc;n nhằm gi&uacute;p tăng tiếp cận gi&aacute;o dục v&agrave; đ&agrave;o tạo bậc cao với th&iacute; sinh v&ugrave;ng kh&oacute; khăn v&agrave; thuộc đối tượng yếu thế. Tuy nhi&ecirc;n, theo b&agrave; Thủy, cũng cần đảm bảo c&ocirc;ng bằng, tr&aacute;nh để sự hỗ trợ n&agrave;y lại l&agrave;m cho nh&oacute;m th&iacute; sinh kh&aacute;c rơi v&agrave;o bất lợi v&agrave; yếu thế.</p>\r\n<p class=\"Normal\">\"Chế độ cộng điểm ưu ti&ecirc;n cũng tương tự c&aacute;c chế độ ch&iacute;nh s&aacute;ch hỗ trợ kh&aacute;c, kh&ocirc;ng thể c&agrave;o bằng theo khu vực, m&agrave; c&ograve;n phải căn cứ mức độ kh&oacute; khăn cần hỗ trợ của từng đối tượng\", b&agrave; Thủy chia sẻ.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/18/thi-thpt-2021HuuKhoa-30-of-31-3293-1655517128.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=_PCdlC0rDd_aqgxieS7EaQ 1x, https://i1-vnexpress.vnecdn.net/2022/06/18/thi-thpt-2021HuuKhoa-30-of-31-3293-1655517128.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=jMD796KHq234Uu0m4KJn9Q 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/18/thi-thpt-2021HuuKhoa-30-of-31-3293-1655517128.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=2ZrmdzqWJHX4nBjnFVCJ3g 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/18/thi-thpt-2021HuuKhoa-30-of-31-3293-1655517128.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=_PCdlC0rDd_aqgxieS7EaQ 1x, https://i1-vnexpress.vnecdn.net/2022/06/18/thi-thpt-2021HuuKhoa-30-of-31-3293-1655517128.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=jMD796KHq234Uu0m4KJn9Q 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/18/thi-thpt-2021HuuKhoa-30-of-31-3293-1655517128.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=2ZrmdzqWJHX4nBjnFVCJ3g 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/06/18/thi-thpt-2021HuuKhoa-30-of-31-3293-1655517128.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=_PCdlC0rDd_aqgxieS7EaQ\" alt=\"Th&iacute; sinh dự thi tốt nghiệp THPT tại TP HCM năm 2021. Ảnh: Hữu Khoa\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/06/18/thi-thpt-2021HuuKhoa-30-of-31-3293-1655517128.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=_PCdlC0rDd_aqgxieS7EaQ\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Th&iacute; sinh dự thi tốt nghiệp THPT tại TP HCM năm 2021. Ảnh:&nbsp;<em>Hữu Khoa</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Ở g&oacute;c độ trường phổ th&ocirc;ng, nhiều gi&aacute;o vi&ecirc;n cho rằng quy định mới l&agrave;&nbsp;<strong>bất hợp l&yacute;, tạo th&ecirc;m bất c&ocirc;ng trong gi&aacute;o dục.</strong></p>\r\n<p class=\"Normal\">Thầy Phan Thế Ho&agrave;i, gi&aacute;o vi&ecirc;n Ngữ văn, THPT B&igrave;nh Hưng H&ograve;a, cho rằng, căn cứ lấy 22,5 điểm để giảm điểm ưu ti&ecirc;n l&agrave; chưa thuyết phục.</p>\r\n<p class=\"Normal\">\"Ngưỡng điểm thi sẽ thay đổi theo từng năm t&ugrave;y thuộc v&agrave;o độ kh&oacute; của đề. V&iacute; dụ, đề thi năm 2018 rất kh&oacute;, c&ograve;n đề thi năm 2021 th&igrave; xuất hiện \'mưa điểm 10\' ở nhiều m&ocirc;n, vậy lấy ngưỡng n&agrave;o để l&agrave;m chuẩn?\", thầy Ho&agrave;i đặt c&acirc;u hỏi.</p>\r\n<p class=\"Normal\">Cũng theo thầy Ho&agrave;i, quy định mới sẽ tạo th&ecirc;m bất c&ocirc;ng mới giữa c&aacute;c th&iacute; sinh. T&igrave;nh trạng th&iacute; sinh đạt 30 điểm nhưng kh&ocirc;ng tr&uacute;ng tuyển đại học chủ yếu l&agrave; do đề thi thiếu ph&acirc;n h&oacute;a chứ kh&ocirc;ng phải do điểm ưu ti&ecirc;n.</p>\r\n<p class=\"Normal\">\"T&ocirc;i nghĩ, giao cho địa phương tổ chức thi v&agrave; x&eacute;t c&ocirc;ng nhận tốt nghiệp; c&ograve;n trường đại học c&oacute; phương &aacute;n thi tuyển ri&ecirc;ng th&igrave; sẽ chấm dứt việc th&iacute; sinh 30, 30,5 điểm vẫn kh&ocirc;ng đỗ đại học\", thầy Ho&agrave;i n&oacute;i.</p>\r\n<p class=\"Normal\">Gi&aacute;o vi&ecirc;n một trường THPT ở huyện Cần Giờ cho rằng, quy định tr&ecirc;n g&acirc;y thiệt th&ograve;i lớn cho học sinh ở v&ugrave;ng s&acirc;u, v&ugrave;ng xa, kh&oacute; khăn về kinh tế.</p>\r\n<p class=\"Normal\">\"Điểm ưu ti&ecirc;n l&agrave; để b&ugrave; đắp cho học sinh kh&oacute; khăn hoặc thiệt th&ograve;i hơn về điều kiện học tập. Nếu giảm bớt điểm ưu ti&ecirc;n chỉ v&igrave; c&aacute;c em đ&oacute; đạt điểm cao sẽ ảnh hưởng lớn đến c&aacute;c em v&agrave; cả nh&agrave; trường, nền gi&aacute;o dục ở khu vực đ&oacute;\", thầy cho biết.</p>\r\n<p class=\"Normal\">Ho&agrave;ng Hạnh, học sinh lớp 11 ở Ph&uacute; Thọ, bất ngờ với điều chỉnh n&agrave;y. Đặt mục ti&ecirc;u v&agrave;o ng&agrave;nh Y khoa, Đại học Y Dược, Đại học Th&aacute;i Nguy&ecirc;n với mức điểm chuẩn thường tr&ecirc;n 26, Hạnh x&aacute;c định 0,5 điểm cộng ưu ti&ecirc;n khu vực 2 - n&ocirc;ng th&ocirc;n c&oacute; thể trở th&agrave;nh yếu tố quyết định em đỗ hay trượt.</p>\r\n<p class=\"Normal\">Giả sử nếu đạt 26 điểm, theo quy chế mới, mức cộng ưu ti&ecirc;n của nữ sinh sẽ giảm từ 0,5 xuống c&ograve;n khoảng 0,27. Nếu trường lấy điểm tr&uacute;ng tuyển 26,4 như năm 2020, em sẽ trượt. \"Điều kiện học tập của em so với c&aacute;c bạn đạt 22,5 điểm trở xuống l&agrave; như nhau, vậy tại sao em lại được cộng &iacute;t điểm hơn\", Hạnh thắc mắc.</p>\r\n<p class=\"Normal\">D&ugrave; vậy, nữ sinh cũng x&aacute;c định, quy chế mới đ&atilde; được ban h&agrave;nh, nhiều th&iacute; sinh bị t&aacute;c động chứ kh&ocirc;ng chỉ ri&ecirc;ng em. Điều n&agrave;y cũng c&oacute; thể khiến điểm chuẩn của c&aacute;c trường giảm nhẹ tương ứng. Thời gian n&agrave;y, Hạnh tập trung học tập, kh&ocirc;ng c&ograve;n tr&ocirc;ng cậy v&agrave;o &yacute; nghĩ tr&uacute;ng tuyển nhờ điểm ưu ti&ecirc;n.</p>\r\n</div>\r\n</article>', 218, 6, 0, 1, '2022-11-21 19:35:42', '2022-11-22 05:45:46'),
(173, 'Diễn viên hài Hữu Tín bị cáo buộc tổ chức chơi ma tuý', 'dien-vien-hai-huu-tin-bi-cao-buoc-to-chuc-choi-ma-tuy', 'TP HCM-Trần Hữu Tín, 35 tuổi, diễn viên hài, cùng một người khác bị cảnh sát xác định có hành vi tổ chức sử dụng trái phép chất ma tuý.', '<p class=\"Normal\">Ng&agrave;y 17/6, Hữu T&iacute;n v&agrave; Nguyễn Ho&agrave;ng Phi, 32 tuổi, bị C&ocirc;ng an quận 8 khởi tố, bắt tạm giam về h&agrave;nh vi&nbsp;<em>T&agrave;ng trữ tr&aacute;i ph&eacute;p chất ma tu&yacute;&nbsp;</em>v&agrave;&nbsp;<em>Tổ chức sử dụng tr&aacute;i ph&eacute;p chất ma t&uacute;y.</em></p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/17/huutin-9412-1655471996.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=0RGgasKazrDbbrx9DQueDQ 1x, https://i1-vnexpress.vnecdn.net/2022/06/17/huutin-9412-1655471996.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=5m6ZsvrEaFmHWdIO1gvLuA 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/17/huutin-9412-1655471996.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=bUmLuU89sTdtx7ZzcNvtsg 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/17/huutin-9412-1655471996.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=0RGgasKazrDbbrx9DQueDQ 1x, https://i1-vnexpress.vnecdn.net/2022/06/17/huutin-9412-1655471996.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=5m6ZsvrEaFmHWdIO1gvLuA 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/17/huutin-9412-1655471996.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=bUmLuU89sTdtx7ZzcNvtsg 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/06/17/huutin-9412-1655471996.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=0RGgasKazrDbbrx9DQueDQ\" alt=\"Hữu T&iacute;n tại cơ quan điều tra. Ảnh: C&ocirc;ng an cung cấp\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/06/17/huutin-9412-1655471996.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=0RGgasKazrDbbrx9DQueDQ\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Hữu T&iacute;n tại cơ quan điều tra. Ảnh:&nbsp;<em>C&ocirc;ng an cung cấp</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Theo điều tra, Hữu T&iacute;n c&ugrave;ng nh&oacute;m bạn đến qu&aacute;n bar ở quận 1 chơi, mua ma t&uacute;y sử dụng. C&ograve;n thừa, nam diễn vi&ecirc;n đem về chờ c&oacute; dịp mang ra sử dụng.</p>\r\n<p class=\"Normal\">Đ&ecirc;m 11/6, Hữu T&iacute;n, Phi c&ugrave;ng 4 người kh&aacute;c tổ chức sử dụng ma t&uacute;y tại căn hộ ở quận 8 th&igrave; bị cảnh s&aacute;t ập v&agrave;o bắt quả tang. Hữu T&iacute;n v&agrave; c&ugrave;ng 2 người dương t&iacute;nh với chất ma t&uacute;y.</p>\r\n<p class=\"Normal\">Kh&aacute;m x&eacute;t căn hộ, cảnh s&aacute;t t&igrave;m thấy hai g&oacute;i nylon c&oacute; 11 vi&ecirc;n ma t&uacute;y dạng n&eacute;n, đĩa s&agrave;nh đựng ma tu&yacute; đ&aacute; v&agrave; một số tang vật kh&aacute;c...</p>\r\n<p class=\"Normal\">Hữu T&iacute;n l&agrave; trưởng nh&oacute;m h&agrave;i XPro - từng đạt giải Qu&aacute;n qu&acirc;n Cười xuy&ecirc;n Việt - Tiếu l&acirc;m hội 2016.</p>', 218, 9, 0, 1, '2022-11-21 19:35:42', '2022-11-22 06:00:30');
INSERT INTO `posts` (`id`, `title`, `slug`, `excerpt`, `body`, `user_id`, `category_id`, `views`, `approved`, `created_at`, `updated_at`) VALUES
(174, 'Google tiếp tục phá kỷ lục, chinh phục 100 nghìn tỉ chữ số của Pi', 'google-tiep-tuc-pha-ky-luc-chinh-phuc-100-nghin-ti-chu-so-cua-pi', 'Google tiếp tục phá kỷ lục, chinh phục 100 nghìn tỉ chữ số của Pi', '<p>Theo TechRadar, những ph&eacute;p t&iacute;nh trong to&aacute;n học Pi một lần nữa đ&atilde; được k&eacute;o d&agrave;i hơn nhờ&nbsp;<a class=\"TextlinkBaiviet\" title=\"Google\" href=\"https://www.24h.com.vn/google-c407e4341.html\">Google</a>&nbsp;Cloud.</p>\r\n<p>Một nh&agrave; ph&aacute;t triển của Google c&oacute; t&ecirc;n Emma Haruka Iwao, đ&atilde; t&iacute;nh to&aacute;n th&agrave;nh c&ocirc;ng số Pi đến 100 ngh&igrave;n tỉ chữ số bằng c&aacute;ch sử dụng nền tảng đ&aacute;m m&acirc;y của c&ocirc;ng ty. Đ&aacute;ng ch&uacute; &yacute; hơn, đ&acirc;y l&agrave; lần thứ hai chỉ sau 3 năm Iwao ph&aacute; kỷ lục.</p>\r\n<p><strong>Điều n&agrave;y c&oacute; &yacute; nghĩa thế n&agrave;o?</strong></p>\r\n<p>C&aacute;c nh&agrave; to&aacute;n học đ&atilde; bắt đầu t&iacute;nh to&aacute;n đến giới hạn của số pi kể từ thời Ai Cập cổ đại, Hy Lạp v&agrave; Babylon.</p>\r\n<p>Google c&ocirc;ng khai thừa nhận rằng việc t&iacute;nh to&aacute;n l&ecirc;n đến 100 ngh&igrave;n tỉ con số tuy kh&ocirc;ng thực sự cần thiết, nhưng ph&eacute;p t&iacute;nh lớn n&agrave;y đ&atilde; chứng minh c&aacute;ch m&agrave; cơ sở hạ tầng linh hoạt của Google Cloud cho ph&eacute;p c&aacute;c đội ngũ tr&ecirc;n khắp thế giới vượt qua ranh giới của thử nghiệm khoa học.</p>\r\n<p>C&aacute;c ph&eacute;p t&iacute;nh li&ecirc;n quan đến số Pi xuất hiện trong mọi thứ, từ l&yacute; thuyết tương đối đến c&aacute;c vấn đề kỹ thuật v&agrave; lập bản đồ GPS. C&aacute;c loại t&iacute;nh to&aacute;n n&agrave;y thường được c&aacute;c nh&agrave; khoa học m&aacute;y t&iacute;nh sử dụng l&agrave;m c&ocirc;ng cụ đo điểm chuẩn để chứng minh v&agrave; đ&aacute;nh gi&aacute; sức mạnh của phần cứng của họ.</p>\r\n<p><strong>Google Cloud đ&atilde; l&agrave;m điều đ&oacute; ra sao?</strong></p>\r\n<p>Google Cloud cho biết họ đ&atilde; sử dụng dịch vụ Compute Engine c&oacute; sẵn để thực hiện ph&eacute;p t&iacute;nh kỷ lục n&agrave;y.</p>\r\n<p>G&atilde; khổng lồ c&ocirc;ng nghệ cho rằng, kết quả cải thiện của n&oacute; so với lần cuối c&ugrave;ng họ nỗ lực t&iacute;nh to&aacute;n v&agrave;o năm 2019 sẽ gi&uacute;p cải thiện mạng v&agrave; lưu trữ. Dự &aacute;n đ&atilde; c&oacute; thể đạt được băng th&ocirc;ng đầu ra 100Gbps, một sự cải thiện đ&aacute;ng kể so với băng th&ocirc;ng đầu ra 16Gbps khi họ thực hiện ph&eacute;p t&iacute;nh 31,4 ngh&igrave;n tỷ chữ số v&agrave;o năm 2019.</p>\r\n<p>Dự &aacute;n đ&atilde; sử dụng tr&igrave;nh điều khiển mạng mới Google Virtual NIC (gVNIC), được t&iacute;ch hợp với stack mạng ảo Andromeda của Google.</p>\r\n<p align=\"center\">&nbsp;</p>\r\n<div id=\"container-24h-banner-in-image\">\r\n<div>\r\n<div><img class=\"news-image loading\" src=\"https://icdn.24h.com.vn/upload/2-2022/images/2022-06-15/picture-2-1655280321-252-width740height416.jpg\" alt=\"Google tiếp tục ph&aacute; kỷ lục, chinh phục 100 ngh&igrave;n tỉ chữ số của Pi - 1\" width=\"740\" data-was-processed=\"true\"></div>\r\n<div id=\"24h-banner-in-image\">\r\n<div id=\"EzWLElzOyO_139_15s\" class=\"txtCent\">\r\n<div class=\"text_adver_right\">&nbsp;</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n<p>&nbsp;</p>\r\n<p class=\"img_chu_thich_0407\">Google Cloud c&oacute; thể t&iacute;nh to&aacute;n số Pi l&ecirc;n đ&oacute; 100 ngh&igrave;n tỉ chữ số.</p>\r\n<p>Google cũng cho rằng sự th&agrave;nh c&ocirc;ng của dự &aacute;n một phần lớn l&agrave; do cải thiện khả năng lưu trữ.&nbsp;Đội ngũ đ&atilde; sử dụng Balanced Persistent Disc, một loại đĩa cứng bền bỉ mới m&agrave; Google cho biết cung cấp tốc độ đọc v&agrave; ghi l&ecirc;n tới 1.200MB/s v&agrave; 15 - 80.000&nbsp;IOPS.</p>\r\n<p>Google sẽ tổ chức hội thảo trực tiếp tr&ecirc;n web v&agrave;o ng&agrave;y 15/6 để chia sẻ th&ecirc;m về qu&aacute; tr&igrave;nh thử nghiệm v&agrave; kết quả tại địa chỉ&nbsp;<em>https://cloudonair.withgoogle.com/events/pi-time</em>.</p>', 218, 10, 1, 1, '2022-11-21 19:35:42', '2022-12-08 08:52:39'),
(175, 'Thủ môn U23 Việt Nam: \'Không hối hận khi nhận thẻ đỏ\'', 'thu-mon-u23-Viet-Nam-khong-hoi-han-khi-nhan-the-do', 'HÀ NỘI-Quan Văn Chuẩn cho rằng bản thân quyết định đúng trong pha cản phá khiến anh bị đuổi ở tứ kết U23 châu Á.', '<p class=\"description\"><span class=\"location-stamp\">H&Agrave; NỘI</span>Quan Văn Chuẩn cho rằng bản th&acirc;n quyết định đ&uacute;ng trong pha cản ph&aacute; khiến anh bị đuổi ở tứ kết U23 ch&acirc;u &Aacute;.</p>\r\n<article class=\"fck_detail \">\r\n<p class=\"Normal\">\"T&igrave;nh huống diễn ra rất nhanh v&agrave; l&agrave; một biến cố trong trận đấu m&agrave; t&ocirc;i kh&ocirc;ng thể lường trước. L&uacute;c trọng t&agrave;i r&uacute;t thẻ đỏ, HLV Gong n&oacute;i t&ocirc;i đợi xem kết quả kiểm tra VAR thế n&agrave;o nhưng trọng t&agrave;i kh&ocirc;ng thay đổi quyết định. Khi về nh&agrave;, t&ocirc;i xem lại t&igrave;nh huống đ&oacute; v&agrave; nghĩ: \'Nếu cho l&agrave;m lại, m&igrave;nh vẫn sẽ xử l&yacute; như vậy\'. L&uacute;c ấy, do đội bị dẫn n&ecirc;n phải d&acirc;ng cao. T&ocirc;i phải bọc l&oacute;t cho c&aacute;c trung vệ. T&ocirc;i kh&ocirc;ng hề c&oacute; &yacute; định phạm lỗi m&agrave; chỉ tập trung ph&aacute; b&oacute;ng. Tiếc l&agrave; cầu thủ đối phương vẫn rướn l&ecirc;n v&agrave; vấp v&agrave;o ch&acirc;n t&ocirc;i\", Văn Chuẩn chia sẻ về pha b&oacute;ng dẫn tới thẻ đỏ của anh trong trận thua Saudi Arabia 0-2.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture data-inimage=\"done\"><source srcset=\"https://i1-thethao.vnecdn.net/2022/06/18/Untitled-1847-1655528102.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=nKJvL2Ja6KmlS_C-rSwzIw 1x, https://i1-thethao.vnecdn.net/2022/06/18/Untitled-1847-1655528102.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=LWQBT_5A4paESaF10ASKfg 1.5x, https://i1-thethao.vnecdn.net/2022/06/18/Untitled-1847-1655528102.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=V9dDRCJJNQMtW6R1gURuLw 2x\" data-srcset=\"https://i1-thethao.vnecdn.net/2022/06/18/Untitled-1847-1655528102.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=nKJvL2Ja6KmlS_C-rSwzIw 1x, https://i1-thethao.vnecdn.net/2022/06/18/Untitled-1847-1655528102.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=LWQBT_5A4paESaF10ASKfg 1.5x, https://i1-thethao.vnecdn.net/2022/06/18/Untitled-1847-1655528102.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=V9dDRCJJNQMtW6R1gURuLw 2x\"><img class=\"lazy lazied\" src=\"https://i1-thethao.vnecdn.net/2022/06/18/Untitled-1847-1655528102.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=nKJvL2Ja6KmlS_C-rSwzIw\" alt=\"Văn Chuẩn phản ứng sau khi bị trọng t&agrave;i phạt thẻ đỏ ở trận thua Saudi Arabia. Ảnh: L&acirc;m Thỏa.\" loading=\"lazy\" data-src=\"https://i1-thethao.vnecdn.net/2022/06/18/Untitled-1847-1655528102.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=nKJvL2Ja6KmlS_C-rSwzIw\" data-ll-status=\"loaded\" data-adbro-processed=\"true\"></picture>\r\n<div class=\"embed-container-ads\">\r\n<div id=\"sis_inimage\" data-google-query-id=\"CLSD6oOCufgCFRQ_lgod1U0Dug\">\r\n<div id=\"google_ads_iframe_/27973503/Vnexpress/Desktop/Inimage/Thethao/Thethao.affcup.detail_0__container__\"><iframe id=\"google_ads_iframe_/27973503/Vnexpress/Desktop/Inimage/Thethao/Thethao.affcup.detail_0\" tabindex=\"0\" title=\"3rd party ad content\" role=\"region\" name=\"google_ads_iframe_/27973503/Vnexpress/Desktop/Inimage/Thethao/Thethao.affcup.detail_0\" width=\"1\" height=\"1\" frameborder=\"0\" marginwidth=\"0\" marginheight=\"0\" scrolling=\"no\" aria-label=\"Advertisement\" data-google-container-id=\"6\" data-load-complete=\"true\"></iframe></div>\r\n</div>\r\n</div>\r\n</div>\r\n<figcaption>\r\n<p class=\"Image\">Văn Chuẩn phản ứng sau khi bị trọng t&agrave;i phạt thẻ đỏ ở trận thua Saudi Arabia. Ảnh:<em>&nbsp;L&acirc;m Thỏa</em>.</p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Ph&uacute;t 78, khi Việt Nam bị dẫn 0-2, Văn Chuẩn băng ra c&aacute;ch khung th&agrave;nh 40m để cản ph&aacute; Buraikan sau khi tiền đạo Saudi Arabia vượt qua trung vệ Việt Anh. Thủ m&ocirc;n Việt Nam nhận thẻ đỏ trực tiếp sau pha b&oacute;ng n&agrave;y d&ugrave; chạm b&oacute;ng trước. Chơi thiếu người, Việt Nam kh&ocirc;ng thể ngược d&ograve;ng trong hơn 10 ph&uacute;t c&ograve;n lại v&agrave; phải dừng bước. Sau khi Văn Chuẩn rời s&acirc;n, tiền đạo Nh&acirc;m Mạnh Dũng phải thay anh giữ khung th&agrave;nh. Thẻ đỏ của Văn Chuẩn g&acirc;y tranh c&atilde;i v&agrave; tiếc nuối. Thủ m&ocirc;n đang kho&aacute;c &aacute;o CLB H&agrave; Nội tiết lộ mẹ gọi điện cho anh v&agrave;o h&ocirc;m sau để động vi&ecirc;n.</p>\r\n<p class=\"Normal\">Văn Chuẩn dự U23 ch&acirc;u &Aacute; 2022 tr&ecirc;n tư c&aacute;ch thủ m&ocirc;n số hai của Việt Nam. Anh bắt ch&iacute;nh ba trận, trước H&agrave;n Quốc, Malaysia v&agrave; Saudi Arabia sau khi Nguyễn Văn Toản d&iacute;nh chấn thương trước Th&aacute;i Lan. Thủ th&agrave;nh sinh năm 2001 để lại ấn tượng ở khả năng chơi ch&acirc;n tốt v&agrave; những phản xạ xuất thần, trong đ&oacute; c&oacute; pha cản ph&aacute; c&uacute; s&uacute;t của tiền vệ H&agrave;n Quốc Eom Ji-sung, gi&uacute;p anh được vinh danh trong top năm pha cứu thua v&ograve;ng bảng.</p>\r\n<p class=\"Normal\">\"T&ocirc;i kh&aacute; h&agrave;i l&ograve;ng với m&agrave;n tr&igrave;nh diễn của m&igrave;nh ở U23 ch&acirc;u &Aacute;. Nhưng giờ l&agrave; l&uacute;c trở lại với c&ocirc;ng việc v&agrave; phải l&agrave;m tốt hơn nữa. Do được nghỉ &iacute;t n&ecirc;n t&ocirc;i kh&ocirc;ng về qu&ecirc; (Tuy&ecirc;n Quang) m&agrave; ở lại tự tập trước khi hội qu&acirc;n c&ugrave;ng CLB. T&ocirc;i muốn nỗ lực hơn nữa để được cống hiến cho H&agrave; Nội\", Văn Chuẩn chia sẻ.</p>\r\n<p class=\"Normal\">Việt Nam từng v&agrave;o chung kết U23 ch&acirc;u &Aacute; 2018 với lứa cầu thủ Quang Hải, C&ocirc;ng Phượng, Duy Mạnh, Văn Hậu..., những người hiện l&agrave; trụ cột tại đội tuyển. Văn Chuẩn cho rằng so với lứa cầu thủ năm đ&oacute;, anh v&agrave; đồng đội c&ograve;n thua k&eacute;m nhiều về kinh nghiệm. \"Đa phần ch&uacute;ng t&ocirc;i chưa được ra s&acirc;n nhiều ở V-League. Nếu được trao cơ hội ở CLB hay đội tuyển, t&ocirc;i nghĩ bản th&acirc;n sẽ biết c&aacute;ch nắm bắt\".</p>\r\n<p class=\"Normal\">Nhận x&eacute;t về HLV Gong Oh-kyun, Văn Chuẩn n&oacute;i: \"&Ocirc;ng ấy l&agrave; người t&igrave;nh cảm, lu&ocirc;n cố gắng khiến cầu thủ ra s&acirc;n với t&acirc;m l&yacute; thoải m&aacute;i nhất. HLV Gong lu&ocirc;n n&oacute;i ch&uacute;ng t&ocirc;i cứ tập trung l&agrave;m theo chỉ đạo, mọi tr&aacute;ch nhiệm &ocirc;ng chịu. Trong giờ nghỉ trận h&ograve;a H&agrave;n Quốc, HLV dự đo&aacute;n đối thủ sẽ ch&ugrave;ng xuống trong hiệp hai v&agrave; đ&oacute; l&agrave; thời cơ cho ch&uacute;ng t&ocirc;i. Mọi chuyện xảy ra đ&uacute;ng như vậy\".</p>\r\n<p class=\"Normal\">Văn Chuẩn tiết lộ anh th&iacute;ch phong c&aacute;ch của thủ th&agrave;nh đội tuyển Trần Nguy&ecirc;n Mạnh v&agrave; lu&ocirc;n nỗ lực để đạt khả năng chơi ch&acirc;n tốt như đ&agrave;n anh.</p>\r\n</article>', 218, 7, 0, 1, '2022-11-21 19:35:42', '2022-11-22 05:53:59'),
(176, 'Đường bị cày nát vì xe quá tải', 'duong-bi-cay-nat-vi-xe-qua-tai', 'Nhiều tỉnh lộ, quốc lộ từ Bắc vào Nam hư hỏng nghiêm trọng do xe chở quá tải; người dân gần các mỏ đá sống trong cảnh bụi bặm đất đá, lo sợ tai nạn.', '<p class=\"description\">Nhiều tỉnh lộ, quốc lộ từ Bắc v&agrave;o Nam hư hỏng nghi&ecirc;m trọng do xe chở qu&aacute; tải; người d&acirc;n gần c&aacute;c mỏ đ&aacute; sống trong cảnh bụi bặm đất đ&aacute;, lo sợ tai nạn.</p>\r\n<article class=\"fck_detail \">\r\n<p class=\"Normal\">Đường Hồ Ch&iacute; Minh đoạn từ huyện Lương Sơn (Ho&agrave; B&igrave;nh) đến huyện Chương Mỹ (H&agrave; Nội) d&agrave;i hơn 10 km, nhưng c&oacute; hơn 20 mỏ đ&aacute; đang khai th&aacute;c, biến cung đường n&agrave;y th&agrave;nh điểm n&oacute;ng của xe qu&aacute; tải, cơi nới. Nhiều năm nay, người d&acirc;n ở đ&acirc;y khổ sở khi trời nắng tuyến đường bụi m&ugrave; mịt, l&uacute;c mưa nhiều đoạn s&igrave;nh lầy ngập nước, chưa kể những vị tr&iacute; nứt to&aacute;c, hố sụt.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/18/a-qua-tai-Ha-Noi-IMG-9994-copy-5576-1655540147.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=4uyChjS9-9TCVzfTmZd_8w 1x, https://i1-vnexpress.vnecdn.net/2022/06/18/a-qua-tai-Ha-Noi-IMG-9994-copy-5576-1655540147.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=aOVLTawWm5pZg54k99Pf0g 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/18/a-qua-tai-Ha-Noi-IMG-9994-copy-5576-1655540147.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=5jRkN4qh98k9uJflDPiG9A 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/18/a-qua-tai-Ha-Noi-IMG-9994-copy-5576-1655540147.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=4uyChjS9-9TCVzfTmZd_8w 1x, https://i1-vnexpress.vnecdn.net/2022/06/18/a-qua-tai-Ha-Noi-IMG-9994-copy-5576-1655540147.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=aOVLTawWm5pZg54k99Pf0g 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/18/a-qua-tai-Ha-Noi-IMG-9994-copy-5576-1655540147.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=5jRkN4qh98k9uJflDPiG9A 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/06/18/a-qua-tai-Ha-Noi-IMG-9994-copy-5576-1655540147.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=4uyChjS9-9TCVzfTmZd_8w\" alt=\"Đường Hồ Ch&iacute; Minh qua huyện Chương Mỹ bụi m&ugrave; mịt do xe qu&aacute; tải. Ảnh: Gia Ch&iacute;nh\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/06/18/a-qua-tai-Ha-Noi-IMG-9994-copy-5576-1655540147.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=4uyChjS9-9TCVzfTmZd_8w\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Đường Hồ Ch&iacute; Minh qua huyện Chương Mỹ, TP H&agrave; Nội, bụi m&ugrave; mịt do xe qu&aacute; tải. Ảnh:&nbsp;<em>Gia Ch&iacute;nh</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Xuống cấp nặng nhất l&agrave; lối v&agrave;o c&aacute;c mỏ đ&aacute; tại x&atilde; Thuỷ Xu&acirc;n Ti&ecirc;n (huyện Chương Mỹ), mặt đường biến dạng ho&agrave;n to&agrave;n. Con đường được nhiều lần sửa chữa nhưng sau v&agrave;i ng&agrave;y lại hư hỏng do lượng xe chở đ&aacute; chạy qu&aacute; nhiều. Đơn vị bảo tr&igrave; phải đổ đất đ&aacute; mạt cao hơn so với mặt đường, song biện ph&aacute;p n&agrave;y khiến vị tr&iacute; sửa mấp m&ocirc;, nhiều chỗ tạo th&agrave;nh ổ g&agrave; s&acirc;u hơn 10 cm.</p>\r\n<p class=\"Normal\">Anh Trần Đức Anh, 28 tuổi, nh&agrave; tại huyện Lương Sơn, h&agrave;ng ng&agrave;y đi l&agrave;m tại thị trấn Xu&acirc;n Mai, huyện Chương Mỹ, kh&ocirc;ng &iacute;t lần &aacute;m ảnh v&igrave; xe tải lớn ph&oacute;ng nhanh s&aacute;t sạt, khuấy bụi m&ugrave;. C&aacute;ch đ&acirc;y hơn một th&aacute;ng tr&ecirc;n đường chở vợ đi l&agrave;m, &ocirc;t&ocirc; tải vượt nhanh qua l&agrave;m anh Đức loạng choạng tay l&aacute;i, lại đ&uacute;ng đoạn đường c&oacute; sỏi rơi v&atilde;i n&ecirc;n hai vợ chồng ng&atilde; ra đường. May mắn khi đ&oacute; kh&ocirc;ng c&oacute; &ocirc;t&ocirc; kh&aacute;c đi từ ph&iacute;a sau n&ecirc;n hai người chỉ bị x&acirc;y x&aacute;t nhẹ.</p>\r\n<p class=\"Normal\">C&aacute;ch H&agrave; Nội hơn 1.700 km, người d&acirc;n sống b&ecirc;n tỉnh lộ 768, x&atilde; Thiện T&acirc;n, TP Bi&ecirc;n Ho&agrave;,&nbsp;<strong>Đồng Nai,</strong>&nbsp;h&agrave;ng ng&agrave;y cũng khốn khổ bởi h&agrave;ng ngh&igrave;n xe ben đi từ mỏ khai th&aacute;c đ&aacute; Thiện T&acirc;n. Qu&aacute;n tạp h&oacute;a của b&agrave; Phạm Thị Mến, 55 tuổi, kề tỉnh lộ, mỗi ng&agrave;y phải tưới nước trước tiệm gần 10 lần chống bụi do xe chở đ&aacute; g&acirc;y ra.</p>\r\n<p class=\"Normal\">B&agrave; Mến cho biết t&igrave;nh trạng xe ben chạy d&agrave;y đặc tr&ecirc;n tuyến tồn tại hơn 5 năm nay. Nhiều xe kh&ocirc;ng che chắn, chở qu&aacute; tải l&agrave;m đất đ&aacute; rơi v&atilde;i, bụi bay m&ugrave; mịt, b&aacute;m bẩn đồ đạc khắp nh&agrave;. \"Nhiều xe ben chạy bạt mạng, ph&oacute;ng nhanh khiến &ocirc; nhiễm, bụi bẩn c&agrave;ng bay xa, tưới nước li&ecirc;n tục kh&ocirc;ng hết\", b&agrave; Mến n&oacute;i.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/19/DSC05994-JPG-3283-1655575293.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=LLvRCliVEHZEzwCgwsXFcQ 1x, https://i1-vnexpress.vnecdn.net/2022/06/19/DSC05994-JPG-3283-1655575293.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Wx4rxjdqU1-ZsE10GwKDMQ 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/19/DSC05994-JPG-3283-1655575293.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=sCHjsAJ-Rkca1ll4bnpQwQ 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/19/DSC05994-JPG-3283-1655575293.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=LLvRCliVEHZEzwCgwsXFcQ 1x, https://i1-vnexpress.vnecdn.net/2022/06/19/DSC05994-JPG-3283-1655575293.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Wx4rxjdqU1-ZsE10GwKDMQ 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/19/DSC05994-JPG-3283-1655575293.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=sCHjsAJ-Rkca1ll4bnpQwQ 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/06/19/DSC05994-JPG-3283-1655575293.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=LLvRCliVEHZEzwCgwsXFcQ\" alt=\"D&ograve;ng xe ben chạy với tốc độ cao ở tỉnh lộ 768, TP Bi&ecirc;n Ho&agrave;, Đồng Nai. Ảnh: Phước Tuấn\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/06/19/DSC05994-JPG-3283-1655575293.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=LLvRCliVEHZEzwCgwsXFcQ\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">D&ograve;ng xe ben chạy với tốc độ cao ở đường chuy&ecirc;n d&ugrave;ng ở mỏ đ&aacute; T&acirc;n Cang, TP Bi&ecirc;n Ho&agrave;, Đồng Nai. Ảnh:&nbsp;<em>Phước Tuấn</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">C&aacute;ch mỏ đ&aacute; x&atilde; Thiện T&acirc;n v&agrave;i c&acirc;y số, khu vực mỏ đ&aacute; T&acirc;n Cang (TP Bi&ecirc;n H&ograve;a) c&oacute; x&acirc;y dựng ri&ecirc;ng con đường chuy&ecirc;n d&ugrave;ng d&agrave;nh cho xe ben chở đ&aacute; từ c&aacute;c mỏ ra quốc lộ 51. Tuy nhi&ecirc;n, thời gian gần đ&acirc;y, con đường n&agrave;y cũng xuống cấp trầm trọng, \"ổ voi, ổ g&agrave;\" d&agrave;y đặc. Mỗi ng&agrave;y trời mưa, nước ngập l&ecirc;nh l&aacute;ng, xe ben chạy qua, nước bắn tung to&eacute; hai b&ecirc;n đường.</p>\r\n<p class=\"Normal\">Khu vực quốc lộ 51, đường Đinh Quan &Acirc;n, Nam Cao... thường xuy&ecirc;n xảy ra tai nạn giao th&ocirc;ng li&ecirc;n quan xe ben chở đ&aacute;, l&agrave;m chết nhiều người. Người d&acirc;n phường Phước T&acirc;n phải dựng chốt chặn xe, kh&ocirc;ng cho v&agrave;o khu d&acirc;n cư. \"Xe ben ở đ&acirc;y kh&ocirc;ng những chạy ẩu, chở qu&aacute; tải m&agrave; thường xuy&ecirc;n vượt đ&egrave;n đỏ, rất nguy hiểm cho người đi đường\", b&agrave; Hằng ở phường Phước T&acirc;n n&oacute;i.</p>\r\n<p class=\"Normal\">T&igrave;nh trạng đường xuống cấp, hư hỏng do xe qu&aacute; tải cũng xảy ra tại nhiều tuyến đường ở&nbsp;<strong>TP HCM&nbsp;</strong>như: Ho&agrave;ng Hữu Nam, Nguyễn Văn B&aacute; (TP Thủ Đức), Lưu Trọng Lư, Huỳnh Tấn Ph&aacute;t (quận 7)...</p>\r\n<p class=\"Normal\">22h ng&agrave;y 16/6, h&agrave;ng chục xe ben cơi nới th&ugrave;ng từ c&ocirc;ng tr&igrave;nh nằm tr&ecirc;n đường Hai B&agrave; Trưng, quận 1, nối đu&ocirc;i nhau chạy về TP Thủ Đức. Tr&ecirc;n th&ugrave;ng xe đầy đất đ&aacute; nhưng kh&ocirc;ng được che đậy khiến b&ugrave;n s&igrave;nh, nước bẩn rơi v&atilde;i dọc đường. D&ograve;ng xe bấm c&ograve;i inh ỏi, chạy tốc độ cao khi đến c&aacute;c giao lộ l&agrave;m người đi xe m&aacute;y lo sợ.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/19/3-JPG-1781-1655604656.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=YQmos_UCQoyjt1OouD_YUw 1x, https://i1-vnexpress.vnecdn.net/2022/06/19/3-JPG-1781-1655604656.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=-24lbMlwS4_oN92lBgJBAg 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/19/3-JPG-1781-1655604656.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=9Vw8-r8Z3usIzRG9RxtpGQ 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/19/3-JPG-1781-1655604656.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=YQmos_UCQoyjt1OouD_YUw 1x, https://i1-vnexpress.vnecdn.net/2022/06/19/3-JPG-1781-1655604656.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=-24lbMlwS4_oN92lBgJBAg 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/19/3-JPG-1781-1655604656.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=9Vw8-r8Z3usIzRG9RxtpGQ 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/06/19/3-JPG-1781-1655604656.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=YQmos_UCQoyjt1OouD_YUw\" alt=\"Đất, s&igrave;nh tr&agrave;n ra khe th&ugrave;ng xe chạy v&agrave;o trung t&acirc;m TP HCM. Ảnh: Đ&igrave;nh Văn \" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/06/19/3-JPG-1781-1655604656.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=YQmos_UCQoyjt1OouD_YUw\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Đất, s&igrave;nh tr&agrave;n ra khe th&ugrave;ng xe chạy v&agrave;o trung t&acirc;m TP HCM. Ảnh:&nbsp;<em>Đ&igrave;nh Văn</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Đến đường Mai Ch&iacute; Thọ, phường An Ph&uacute;, đo&agrave;n xe n&oacute;i tr&ecirc;n bị Đội CSGT C&aacute;t L&aacute;i ra hiệu kiểm tra. Cảnh s&aacute;t d&ugrave;ng thước đo, ph&aacute;t hiện nhiều xe cơi nới th&ugrave;ng đến 1,8 m, gấp ba lần độ cao th&ugrave;ng xe cho ph&eacute;p. Trong đ&oacute;, một số t&agrave;i xế vừa bị xử phạt về lỗi cơi nới d&ugrave; chưa khắc phục nhưng vẫn hoạt động.</p>\r\n<p class=\"Normal\">C&aacute;c tỉnh miền Trung như&nbsp;<strong>H&agrave; Tĩnh, Quảng B&igrave;nh, Thừa Thi&ecirc;n Huế</strong>, nhiều năm nay người d&acirc;n ở gần c&aacute;c mỏ kho&aacute;ng sản cũng sống trong cảnh sợ h&atilde;i, đường xuống cấp nghi&ecirc;m trọng do xe chở đ&aacute;.</p>\r\n<p class=\"Normal\">Đ&aacute;ng ch&uacute; &yacute;, tuyến đường li&ecirc;n x&atilde; nối đường Hồ Ch&iacute; Minh với đường 10, đi giữa l&ograve;ng thị trấn Lệ Ninh (huyện Lệ Thủy, Quảng B&igrave;nh), đầy \"ổ g&agrave;, ổ tr&acirc;u\" do xe chở đ&aacute;. To&agrave;n tuyến d&agrave;i khoảng 7 km, nhưng hơn 5 km hư hỏng. Để hạn chế tốc độ của &ocirc;t&ocirc; chở đ&aacute;, một số hộ d&acirc;n đặt vật cản trước cổng nh&agrave;, s&aacute;t lề đường.</p>\r\n<p class=\"Normal\">Theo CSGT tỉnh Quảng B&igrave;nh, t&igrave;nh trạng xe qu&aacute; tải xuất hiện ở hầu hết c&aacute;c tuyến đường, từ quốc lộ 1A, quốc lộ 12A, đường Hồ Ch&iacute; Minh đến c&aacute;c tỉnh lộ, đường li&ecirc;n x&atilde;... Chủ yếu xe qu&aacute; tải l&agrave; &ocirc;t&ocirc; chở vật liệu x&acirc;y dựng, đất c&aacute;t.</p>\r\n<p class=\"Normal\">Tương tự, tỉnh lộ 16 đi qua phường Hương Văn, Hương V&acirc;n v&agrave; Tứ Hạ (thị x&atilde; Hương Tr&agrave;, Thừa Thi&ecirc;n Huế) được xem l&agrave; \"con đường đau khổ\" khi mỗi ng&agrave;y h&agrave;ng trăm lượt xe tải, xe ben chở đ&aacute;, đất đi qua. Mỗi lần &ocirc;t&ocirc; tải chạy qua lại cuốn bụi bay m&ugrave; mịt cả tuyến đường, nhiều nh&agrave; d&acirc;n nơi đ&acirc;y phải đ&oacute;ng cửa cả ng&agrave;y. Những h&agrave;ng c&acirc;y ven hai b&ecirc;n đường d&agrave;y đặc lớp đất đỏ.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/19/IMG-7318-JPG-6181-1655604657.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=uSxp52iJti1IAbGcL_REiQ 1x, https://i1-vnexpress.vnecdn.net/2022/06/19/IMG-7318-JPG-6181-1655604657.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=K8AgCp-I7kjJBftUaIUh-A 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/19/IMG-7318-JPG-6181-1655604657.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=RnR4vOEFCxHD1Bdl3Udhbg 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/19/IMG-7318-JPG-6181-1655604657.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=uSxp52iJti1IAbGcL_REiQ 1x, https://i1-vnexpress.vnecdn.net/2022/06/19/IMG-7318-JPG-6181-1655604657.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=K8AgCp-I7kjJBftUaIUh-A 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/19/IMG-7318-JPG-6181-1655604657.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=RnR4vOEFCxHD1Bdl3Udhbg 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/06/19/IMG-7318-JPG-6181-1655604657.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=uSxp52iJti1IAbGcL_REiQ\" alt=\"Xe tải chở đất đ&aacute; tr&ecirc;n tỉnh lộ 16 đi qua phường Hương Văn, Thị x&atilde; Hương Tr&agrave;. Ảnh: V&otilde; Thạnh\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/06/19/IMG-7318-JPG-6181-1655604657.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=uSxp52iJti1IAbGcL_REiQ\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Xe tải chở đất đ&aacute; tr&ecirc;n tỉnh lộ 16 đi qua phường Hương Văn, Thị x&atilde; Hương Tr&agrave;. Ảnh:&nbsp;<em>V&otilde; Thạnh</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">CSGT thị x&atilde; Hương Tr&agrave; cho biết, tỉnh lộ 16 từ trung t&acirc;m phường Tứ Hạ l&ecirc;n x&atilde; Hương B&igrave;nh l&agrave; nơi tập trung nhiều mỏ đất, đ&aacute; n&ecirc;n số lượng xe ben, xe tải đi qua rất lớn. Từ đầu năm đến nay, đơn vị đ&atilde; xử l&yacute; 21 trường hợp xe chở qu&aacute; tải tr&ecirc;n tuyến đường n&agrave;y.</p>\r\n<p class=\"Normal\">T&igrave;nh trạng &ocirc;t&ocirc; qu&aacute; tải g&acirc;y hư hỏng đường x&aacute;, ảnh hưởng đời sống người d&acirc;n tồn tại nhiều năm v&agrave; chưa c&oacute; dấu hiệu thuy&ecirc;n giảm. Mới đ&acirc;y, h&ocirc;m 4/6 xe tải cơi nới th&ugrave;ng cao hơn 2,5 lần cho ph&eacute;p, chở đất, chạy tr&ecirc;n đường Hồ Ch&iacute; Minh (x&atilde; Bảo Hiệu, huyện Y&ecirc;n Thủy, tỉnh Ho&agrave; B&igrave;nh) bị lật, đ&egrave; &ocirc;t&ocirc; 4 chỗ l&agrave;m&nbsp;<a href=\"https://vnexpress.net/xe-bon-cho-bi-de-bep-ba-nguoi-chet-4472127.html\" rel=\"dofollow\">ba người chết</a>.</p>\r\n<p class=\"Normal\">Họp Quốc hội ng&agrave;y 9/6, đại biểu L&ecirc; Tất Hiếu (đo&agrave;n Vĩnh Ph&uacute;c) n&ecirc;u xe qu&aacute; tải đ&atilde; khiến nhiều c&ocirc;ng tr&igrave;nh đường x&aacute; xuống cấp rất nghi&ecirc;m trọng, thiệt hại nhiều tỷ đồng, song quy định kh&oacute; xử l&yacute; h&igrave;nh sự, n&ecirc;n t&iacute;nh răn đe kh&ocirc;ng cao.</p>\r\n<p class=\"Normal\">Bộ trưởng Giao th&ocirc;ng Vận tải Nguyễn Văn Thể cho biết Nghị định 100 tạo sự đột ph&aacute; trong kiểm so&aacute;t an to&agrave;n giao th&ocirc;ng, nhưng Bộ đ&aacute;nh gi&aacute; vẫn c&ograve;n một số điểm hạn chế, nhất l&agrave; trong xử phạt xe qu&aacute; tải. Hiện quy định cho xe vượt tải 50-60%. Song sắp tới, Bộ sẽ kiến nghị theo hướng&nbsp;<a href=\"https://vnexpress.net/bo-truong-nguyen-van-the-xem-xet-tich-thu-xe-qua-tai-20-4473957.html\" rel=\"dofollow\">phạt nặng</a>&nbsp;&ocirc;t&ocirc; vượt tải 10%; xe vượt đến 20% sẽ bị tịch thu.</p>\r\n<p class=\"Normal\">\"Ch&uacute;ng ta kh&ocirc;ng xử l&yacute; h&igrave;nh sự m&agrave; bằng biện ph&aacute;p h&agrave;nh ch&iacute;nh để đảm bảo răn đe v&agrave; an to&agrave;n cho c&aacute;c c&ocirc;ng tr&igrave;nh giao th&ocirc;ng\", &ocirc;ng Thể n&oacute;i v&agrave; cho biết việc xử phạt nặng sẽ ngăn chặn c&aacute;c chủ xe sau khi đăng kiểm lắp gh&eacute;p, cơi nới th&ugrave;ng.</p>\r\n<p class=\"Normal\"><strong>Nh&oacute;m ph&oacute;ng vi&ecirc;n</strong></p>\r\n</article>', 218, 3, 0, 1, '2022-11-21 19:35:42', '2022-11-22 05:28:03'),
(183, 'Nhiều cá heo xuất hiện ở vịnh Vân Phong', 'nhieu-ca-heo-xuat-hien-o-vinh-van-phong', 'KHÁNH HÒA-Đàn cá heo bơi lội trước tàu ngư dân tại biển Hòn Gầm, thuộc vịnh Vân Phong, huyện Vạn Ninh, ngày 18/6.', '<div>\r\n<p>L&uacute;c 9h20, anh Đ&agrave;o Nhật Duy, ngư d&acirc;n ở huyện Vạn Ninh, l&aacute;i t&agrave;u ra khu vực biển H&ograve;n Gầm, thuộc vịnh V&acirc;n Phong, thấy đ&agrave;n c&aacute; heo mỗi con d&agrave;i chừng 3 m, bơi lượn, \"nhảy m&uacute;a\" tr&ecirc;n mặt nước.</p>\r\n</div>\r\n<p class=\"Normal\">T&agrave;u anh Duy chạy đến đ&acirc;u, đ&agrave;n c&aacute; bơi theo đến đ&oacute;. Cảnh c&aacute; heo b&aacute;m t&agrave;u được anh quay lại. Anh Duy cho biết thỉnh thoảng ra khơi gặp cũng gặp đ&agrave;n c&aacute; heo chừng chục con theo t&agrave;u. Song đ&acirc;y l&agrave; lần đầu ti&ecirc;n ngư d&acirc;n thấy đ&agrave;n nhiều c&aacute; heo như vậy. Ch&uacute;ng xuất hiện chừng 20 ph&uacute;t rồi lặn xuống biển.</p>\r\n<p class=\"Normal\">C&aacute; heo thỉnh thoảng xuất hiện tại v&ugrave;ng biển Kh&aacute;nh Ho&agrave;. Hai năm trước, người d&acirc;n v&agrave; du kh&aacute;ch th&iacute;ch th&uacute; ghi lại cảnh c&aacute; heo xuất hiện ở biển khu vực B&atilde;i D&agrave;i (TP Cam Ranh), vịnh Nha Trang.</p>\r\n<p class=\"Normal\">C&aacute; heo l&agrave; động vật c&oacute; v&uacute;, d&agrave;i 1,2 - 9,5 m, nặng từ 40 kg đến 10 tấn, thường cư ngụ ở c&aacute;c v&ugrave;ng biển n&ocirc;ng th&agrave;nh đ&agrave;n từ h&agrave;ng chục tới h&agrave;ng trăm con. Đ&acirc;y l&agrave; một trong số lo&agrave;i c&aacute; th&acirc;n thiện với con người, th&ocirc;ng minh c&oacute; thể huấn luyện l&agrave;m xiếc.</p>', 218, 3, 0, 1, '2022-11-21 19:35:42', '2022-11-22 05:27:27'),
(187, 'Báo chí luôn đồng hành, hỗ trợ ngành GTVT phát triển', 'bao-chi-luon-dong-hanh-ho-tro-nganh-gtvt-phat-trien', 'Báo chí luôn đồng hành, hỗ trợ ngành GTVT phát triển', '<p>S&aacute;ng nay (20/6), Bộ trưởng Bộ GTVT Nguyễn Văn Thể chủ tr&igrave; buổi gặp mặt b&aacute;o ch&iacute; nh&acirc;n dịp kỷ niệm 97 năm ng&agrave;y B&aacute;o ch&iacute; C&aacute;ch mạng Việt Nam (21/6/1925 - 21/6/2022).</p>\r\n<p>Tại buổi gặp mặt,&nbsp;<a class=\"seokwl\" title=\"Bộ trưởng Nguyễn Văn Thể\" href=\"https://www.baogiaothong.vn/bo-truong-gtvt-nguyen-van-the-channel418/\">Bộ trưởng Nguyễn Văn Thể</a>&nbsp;cảm ơn đội ngũ ph&oacute;ng vi&ecirc;n, bi&ecirc;n tập vi&ecirc;n c&aacute;c cơ quan b&aacute;o ch&iacute; Trung ương v&agrave; địa phương thời gian qua đ&atilde; lu&ocirc;n t&iacute;ch cực th&ocirc;ng tin, tuy&ecirc;n truyền về ng&agrave;nh GTVT, l&agrave; cầu nối th&ocirc;ng tin giữa ng&agrave;nh GTVT v&agrave; người d&acirc;n.</p>\r\n<div>\r\n<p class=\"image\"><img class=\"width100 initial loaded\" src=\"https://cdn.baogiaothong.vn/upload/images/2022-2/article_img/2022-06-20/img-bgt-2021-bo-truong-nguyen-van-the-gap-mat-phong-vien-copy-1655711632-width1614height1080.jpg\" alt=\"B&aacute;o ch&iacute; lu&ocirc;n đồng h&agrave;nh, hỗ trợ ng&agrave;nh GTVT ph&aacute;t triển 1\" data-original=\"https://cdn.baogiaothong.vn/upload/images/2022-2/article_img/2022-06-20/img-bgt-2021-bo-truong-nguyen-van-the-gap-mat-phong-vien-copy-1655711632-width1614height1080.jpg\" data-was-processed=\"true\"></p>\r\n<p class=\"img_chu_thich\">Bộ trưởng Nguyễn Văn Thể đ&aacute;nh gi&aacute; cao v&agrave; cảm ơn sự quan t&acirc;m, chia sẻ, ủng hộ của c&aacute;c cơ quan b&aacute;o ch&iacute;, ph&oacute;ng vi&ecirc;n, bi&ecirc;n tập vi&ecirc;n đối với c&aacute;c hoạt động ng&agrave;nh GTVT</p>\r\n</div>\r\n<p>Theo Bộ trưởng, c&aacute;c cơ quan b&aacute;o ch&iacute;, ph&oacute;ng vi&ecirc;n, bi&ecirc;n tập vi&ecirc;n, những người l&agrave;m b&aacute;o n&oacute;i chung ch&iacute;nh l&agrave; những chiến sĩ tr&ecirc;n mặt trận văn h&oacute;a, truyền tải c&aacute;c chủ trương, định hướng, chiến lược của Đảng, Nh&agrave; nước đến với người d&acirc;n cũng như phản &aacute;nh &yacute; kiến của người d&acirc;n với Đảng, Nh&agrave; nước.</p>\r\n<p>Ng&agrave;nh GTVT l&agrave; ng&agrave;nh &ldquo;n&oacute;ng&rdquo; lu&ocirc;n nhận được sự quan t&acirc;m của x&atilde; hội, nhất l&agrave; đối với c&aacute;c dự &aacute;n giao th&ocirc;ng. Từ l&uacute;c h&igrave;nh th&agrave;nh dự &aacute;n, x&acirc;y dựng đến khi ho&agrave;n th&agrave;nh, khai th&aacute;c vận tải, đảm bảo an to&agrave;n giao th&ocirc;ng v&agrave; duy tu, bảo tr&igrave; sẽ kh&ocirc;ng tr&aacute;nh khỏi nảy sinh c&aacute;c vấn đề. Tuy nhi&ecirc;n, ng&agrave;nh GTVT lu&ocirc;n nhận được sự quan t&acirc;m, chia sẻ, đồng h&agrave;nh của c&aacute;c cơ quan b&aacute;o ch&iacute;.</p>\r\n<p>&ldquo;C&aacute;c ph&oacute;ng vi&ecirc;n th&ocirc;ng qua c&aacute;c b&agrave;i viết của m&igrave;nh đ&atilde; kịp thời phản &aacute;nh, g&oacute;p &yacute; với ng&agrave;nh GTVT những vấn đề c&ograve;n bất cập để kịp thời sửa đổi, điều chỉnh c&aacute;c văn bản quy phạm ph&aacute;p luật, cơ chế ch&iacute;nh s&aacute;ch, sao cho hoạt động GTVT được tốt hơn, hiệu quả cao&rdquo;, Bộ trưởng Thể n&oacute;i.</p>\r\n<p>Bộ trưởng cũng mong muốn, giai đoạn sắp tới sẽ tiếp tục nhận được sự quan t&acirc;m, ủng hộ, đồng h&agrave;nh của c&aacute;c cơ quan b&aacute;o ch&iacute;, đồng thời cam kết sẽ kịp thời th&ocirc;ng tin đến b&aacute;o ch&iacute; c&aacute;c hoạt động của ng&agrave;nh GTVT th&ocirc;ng qua bộ phận truyền th&ocirc;ng của Bộ, qua c&aacute;c cuộc họp b&aacute;o định kỳ...</p>\r\n<p>Cũng tại buổi gặp mặt, Bộ trưởng Nguyễn Văn Thể th&ocirc;ng tin về một số nhiệm vụ, c&ocirc;ng việc trọng t&acirc;m m&agrave; Bộ GTVT đang triển khai như: dự &aacute;n đường bộ cao tốc, vấn đề bảo đảm nguồn cung v&agrave; gi&aacute; vật liệu dự &aacute;n đường bộ cao tốc, vấn đề t&aacute;ch Luật Giao th&ocirc;ng đường bộ, quản l&yacute; hoạt động vận tải giai đoạn hậu&nbsp;<a class=\"seokwl\" title=\"Covid-19\" href=\"https://www.baogiaothong.vn/tin-tuc-covid-19-moi-nhat-ngay-hom-nay-channel405/\">Covid-19</a>, bảo đảm trật tự ATGT, nghi&ecirc;n cứu phương &aacute;n tổ chức Tổng cục Đường bộ VN sau quy định của Ch&iacute;nh phủ&hellip;</p>', 218, 1, 0, 1, '2022-11-21 19:35:42', '2022-11-22 08:39:18'),
(188, 'Giảm mức cộng ưu tiên với thí sinh đạt từ 22,5 điểm', 'giam-muc-cong-uu-tien-voi-thi-sinh-dat-tu-225-diem', 'Từ năm 2023, thí sinh đạt 22,5 điểm trở lên khi xét tuyển đại học không được cộng tối đa điểm ưu tiên, những em đạt 30 điểm thậm chí không được cộng.', '<p class=\"description\">Từ năm 2023, th&iacute; sinh đạt 22,5 điểm trở l&ecirc;n khi x&eacute;t tuyển đại học kh&ocirc;ng được cộng tối đa điểm ưu ti&ecirc;n, những em đạt 30 điểm thậm ch&iacute; kh&ocirc;ng được cộng.</p>\r\n<article class=\"fck_detail \">\r\n<div id=\"divfirst\">\r\n<p class=\"Normal\">Quy chế tuyển sinh đại học năm 2022 lần thứ hai được Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo c&ocirc;ng bố dự thảo đ&ecirc;m 9/6 v&agrave; k&yacute; ng&agrave;y 10/6, đặt ra một số quy định mới li&ecirc;n quan đến cộng điểm ưu ti&ecirc;n cho th&iacute; sinh tự do v&agrave; c&aacute;ch t&iacute;nh điểm ưu ti&ecirc;n cho nh&oacute;m th&iacute; sinh đạt điểm cao khi x&eacute;t tuyển đại học.</p>\r\n<p class=\"Normal\">Trong&nbsp;<a href=\"https://vnexpress.net/ba-thay-doi-lon-trong-tuyen-sinh-dai-hoc-2022-4451152.html\" rel=\"dofollow\">dự thảo lần thứ nhất</a>, Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo dự kiến kh&ocirc;ng cộng điểm ưu ti&ecirc;n khu vực cho th&iacute; sinh tự do kể từ năm 2022. Dự kiến n&agrave;y nhận nhiều &yacute; kiến tr&aacute;i chiều, đặc biệt từ nh&oacute;m&nbsp;<a href=\"https://vnexpress.net/hut-hang-vi-mat-diem-cong-uu-tien-vao-dai-hoc-4452428.html\" rel=\"dofollow\">th&iacute; sinh</a>&nbsp;x&eacute;t tuyển lại v&agrave;o năm nay. V&igrave; vậy, Bộ c&oacute; điều chỉnh để đảm bảo quyền lợi của th&iacute; sinh.</p>\r\n<p class=\"Normal\">Theo đ&oacute;, từ năm 2023, th&iacute; sinh sẽ được hưởng ch&iacute;nh s&aacute;ch ưu ti&ecirc;n khu vực trong năm tốt nghiệp THPT v&agrave; một năm kế tiếp khi x&eacute;t tuyển đại học, cao đẳng. Như vậy, nếu thi lại một năm, th&iacute; sinh vẫn được hưởng ch&iacute;nh s&aacute;ch ưu ti&ecirc;n.</p>\r\n<p class=\"Normal\">C&ugrave;ng với thay đổi tr&ecirc;n,&nbsp;<strong>việc t&iacute;nh mức điểm ưu ti&ecirc;n với từng nh&oacute;m th&iacute; sinh cũng được điều chỉnh theo mức điểm c&aacute;c em đạt được</strong>.</p>\r\n<p class=\"Normal\">Bộ cho biết qua thống k&ecirc; điểm thi tốt nghiệp THPT ba năm qua, nh&oacute;m th&iacute; sinh kh&ocirc;ng được cộng điểm ưu ti&ecirc;n (chiếm 25% tổng số th&iacute; sinh tốt nghiệp) lu&ocirc;n c&oacute; phổ điểm tổng ba m&ocirc;n cao hơn hẳn c&aacute;c nh&oacute;m c&ograve;n lại.</p>\r\n<p class=\"Normal\">Trong số 75% th&iacute; sinh c&ograve;n lại (tức nh&oacute;m được cộng điểm ưu ti&ecirc;n ở c&aacute;c mức độ kh&aacute;c nhau), số liệu của Bộ cho thấy một điểm đ&aacute;ng ch&uacute; &yacute; li&ecirc;n quan tới mốc 22,5 điểm. Theo đ&oacute;, với c&aacute;c th&iacute; sinh c&oacute; tổng điểm ba m&ocirc;n dưới 22,5, chỉ khi được cộng điểm ưu ti&ecirc;n, c&aacute;c em mới c&oacute; khả năng tiệm cận về mức điểm với nh&oacute;m 25% th&iacute; sinh kh&ocirc;ng được cộng.</p>\r\n<p class=\"Normal\">Bộ đ&aacute;nh gi&aacute;, điều n&agrave;y chứng tỏ cộng điểm ưu ti&ecirc;n đ&atilde; gia tăng cơ hội tiếp cận gi&aacute;o dục đại học cho nh&oacute;m c&oacute; điều kiện kh&oacute; khăn hơn.</p>\r\n<p class=\"Normal\">Tuy nhi&ecirc;n, dữ liệu cũng cho thấy c&oacute; sự bất hợp l&yacute; l&agrave; tỷ lệ th&iacute; sinh đạt điểm từ 22,5 trở l&ecirc;n của nh&oacute;m được ưu ti&ecirc;n lại tăng vọt. Điều n&agrave;y dẫn tới sự bất c&ocirc;ng khi th&iacute; sinh ứng tuyển v&agrave;o c&aacute;c ng&agrave;nh, trường c&oacute; mức độ cạnh tranh cao; đẩy điểm chuẩn một số ng&agrave;nh l&ecirc;n tới 30.</p>\r\n</div>\r\n<div id=\"admbackground\" data-set=\"dfp\"></div>\r\n<div id=\"divend\">\r\n<p class=\"Normal\">Để khắc phục, Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo quy định từ năm 2023, mức điểm ưu ti&ecirc;n khu vực v&agrave; đối tượng với th&iacute; sinh đạt từ 22,5 điểm (tương đương 7,5 điểm mỗi m&ocirc;n trong tổ hợp ba m&ocirc;n) được giảm tuyến t&iacute;nh theo c&ocirc;ng thức:</p>\r\n<p class=\"Normal\"><strong>Mức điểm ưu ti&ecirc;n th&iacute; sinh được hưởng = [(30 - Tổng điểm đạt được của th&iacute; sinh)/7,5] x Tổng điểm ưu ti&ecirc;n được x&aacute;c định th&ocirc;ng thường.</strong></p>\r\n<p class=\"Normal\">\"&Aacute;p dụng ch&iacute;nh s&aacute;ch ưu ti&ecirc;n l&agrave; nhằm gi&uacute;p tăng tiếp cận gi&aacute;o dục v&agrave; đ&agrave;o tạo bậc cao đối với c&aacute;c th&iacute; sinh ở v&ugrave;ng kh&oacute; khăn v&agrave; thuộc đối tượng yếu thế, tuy nhi&ecirc;n cũng cần đảm bảo sự c&ocirc;ng bằng, tr&aacute;nh để sự hỗ trợ đ&oacute; lại l&agrave;m nh&oacute;m th&iacute; sinh kh&aacute;c bị bất lợi v&agrave; yếu thế\", Bộ n&oacute;i l&yacute; do đưa ra thay đổi n&agrave;y.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" align=\"center\" data-widget=\"obj\">\r\n<tbody>\r\n<tr>\r\n<td align=\"center\">\r\n<div id=\"vote-37257\" data-component=\"true\" data-component-value=\"37257\" data-component-siteid=\"1000000\">\r\n<div id=\"boxthamdoykien\" class=\"width_common box_category vote_rating_box thamdo box_100 _noresult box-vote-pmi\">\r\n<div class=\"width_common title-box-category title-box-small\">\r\n<h4 class=\"inner-title\">Thăm d&ograve;</h4>\r\n<span class=\"count-vote\"><strong>1.721</strong>&nbsp;biểu quyết</span></div>\r\n<div class=\"content_box_category box_bg_gray width_common\">\r\n<div class=\"title_bx width_common\">Theo bạn, dự kiến giảm mức điểm cộng ưu ti&ecirc;n với th&iacute; sinh từ 22,5 điểm l&agrave;...</div>\r\n<div class=\"item_row_bx width_common\"><label class=\"label_check\" for=\"_i132687\"><input id=\"_i132687\" class=\"radio_check\" name=\"answer_37257\" type=\"radio\" value=\"132687\">Hợp l&yacute;</label></div>\r\n<div class=\"item_row_bx width_common\"><label class=\"label_check\" for=\"_i132688\"><input id=\"_i132688\" class=\"radio_check\" name=\"answer_37257\" type=\"radio\" value=\"132688\">Bất hợp l&yacute;, tạo ra sự bất c&ocirc;ng kh&aacute;c</label></div>\r\n<div class=\"box_button width_common\"><button id=\"btn_add_vote_37257\" class=\"btn_vne btn_disable btn_normal btn_vote_rate\">Biểu quyết</button>&nbsp;<a id=\"_btnResult\" class=\"btn_vne btn_normal btn_view_cancel btn_vote_rate btn_ketqua\" rel=\"nofollow\"></a>Xem kết quả</div>\r\n</div>\r\n<p class=\"width_common time-vote\">Thời gian từ: 10/6</p>\r\n</div>\r\n</div>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture data-inimage=\"done\"><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/10/thi-sinh9a-9076-1654828891.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=OwlmSR2ipvN8oTbhNIWuCQ 1x, https://i1-vnexpress.vnecdn.net/2022/06/10/thi-sinh9a-9076-1654828891.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=JMEVCnP1kp1rdxBLPwHLJg 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/10/thi-sinh9a-9076-1654828891.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=JzFo0yWl0nUnoRnq4N0flw 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/10/thi-sinh9a-9076-1654828891.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=OwlmSR2ipvN8oTbhNIWuCQ 1x, https://i1-vnexpress.vnecdn.net/2022/06/10/thi-sinh9a-9076-1654828891.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=JMEVCnP1kp1rdxBLPwHLJg 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/10/thi-sinh9a-9076-1654828891.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=JzFo0yWl0nUnoRnq4N0flw 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/06/10/thi-sinh9a-9076-1654828891.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=OwlmSR2ipvN8oTbhNIWuCQ\" alt=\"Th&iacute; sinh dự thi tốt nghiệp 2021 tại trường THPT Trưng Vương (quận 1). Ảnh: Quỳnh Trần\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/06/10/thi-sinh9a-9076-1654828891.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=OwlmSR2ipvN8oTbhNIWuCQ\" data-ll-status=\"loaded\" data-adbro-processed=\"true\"></picture>\r\n<div class=\"embed-container-ads\">\r\n<div id=\"sis_inimage\" data-google-query-id=\"CJzj8o7_uPgCFZ3HFgUd3ScERA\">\r\n<div id=\"google_ads_iframe_/27973503/Vnexpress/Desktop/Inimage/Giaoduc/Giaoduc.tuyensinh.detail_0__container__\"><iframe id=\"google_ads_iframe_/27973503/Vnexpress/Desktop/Inimage/Giaoduc/Giaoduc.tuyensinh.detail_0\" tabindex=\"0\" title=\"3rd party ad content\" role=\"region\" name=\"google_ads_iframe_/27973503/Vnexpress/Desktop/Inimage/Giaoduc/Giaoduc.tuyensinh.detail_0\" width=\"1\" height=\"1\" frameborder=\"0\" marginwidth=\"0\" marginheight=\"0\" scrolling=\"no\" aria-label=\"Advertisement\" data-google-container-id=\"4\" data-load-complete=\"true\"></iframe></div>\r\n</div>\r\n</div>\r\n</div>\r\n<figcaption>\r\n<p class=\"Image\">Th&iacute; sinh dự thi tốt nghiệp 2021 tại trường THPT Trưng Vương (quận 1). Ảnh:<em>&nbsp;Quỳnh Trần</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\"><strong>Về quy chế tuyển sinh năm 2022, Bộ cho biết cơ bản giữ ổn định</strong>. Một số quy định được điều chỉnh, đ&atilde; được Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo c&ocirc;ng bố ở dự thảo lần một.</p>\r\n<p class=\"Normal\">Cụ thể, th&iacute; sinh đăng k&yacute; dự thi tốt nghiệp THPT v&agrave; x&eacute;t tuyển đại học theo h&igrave;nh thức trực tuyến tr&ecirc;n Cổng th&ocirc;ng tin của Bộ hoặc Cổng dịch vụ c&ocirc;ng quốc gia. Tất cả nguyện vọng x&eacute;t tuyển đại học của th&iacute; sinh được ghi nhận v&agrave;o hệ thống từ sau khi kết th&uacute;c kỳ thi tốt nghiệp THPT cho tới khi c&ocirc;ng bố kết quả thi v&agrave; kết quả điểm ph&uacute;c khảo (nếu c&oacute;).</p>\r\n<p class=\"Normal\">Tất cả nguyện vọng của th&iacute; sinh được từng trường x&eacute;t tuyển, đưa l&ecirc;n phần mềm xử l&yacute; v&agrave; cho kết quả th&iacute; sinh tr&uacute;ng tuyển ở nguyện vọng cao nhất. Th&iacute; sinh kh&ocirc;ng phải x&aacute;c nhận nhập học trước đối với c&aacute;c phương thức x&eacute;t tuyển sớm.</p>\r\n<p class=\"Normal\">Một điểm mới nữa trong năm nay l&agrave; th&iacute; sinh kh&ocirc;ng cần c&ocirc;ng chứng hoặc y&ecirc;u cầu trường THPT x&aacute;c nhận kết quả học tập khi đăng k&yacute; x&eacute;t tuyển v&agrave;o nhiều đại học bởi c&aacute;c trường THPT sẽ cập nhật kết quả l&ecirc;n cơ sở dữ liệu ng&agrave;nh rồi đồng bộ sang hệ thống hỗ trợ tuyển sinh.</p>\r\n</div>\r\n</article>', 218, 6, 0, 1, '2022-11-21 19:35:42', '2022-11-22 05:46:01');
INSERT INTO `posts` (`id`, `title`, `slug`, `excerpt`, `body`, `user_id`, `category_id`, `views`, `approved`, `created_at`, `updated_at`) VALUES
(189, 'Giám đốc CDC Khánh Hòa bị khởi tố', 'giam-doc-cdc-khanh-hoa-bi-khoi-to', 'Ông Huỳnh Văn Dõng, 55 tuổi, Giám đốc CDC Khánh Hòa, cùng một số cấp dưới bị cáo buộc sai phạm trong việc mua kit xét nghiệm Covid-19 của Công ty Việt Á.', '<p class=\"Normal\">S&aacute;ng 17/6, &ocirc;ng D&otilde;ng, Gi&aacute;m đốc Trung t&acirc;m Kiểm so&aacute;t bệnh tật (CDC) bị C&ocirc;ng an tỉnh Kh&aacute;nh H&ograve;a khởi tố, cho tại ngoại (do đang bị bệnh) để điều tra về h&agrave;nh vi&nbsp;<em>Vi phạm quy định đấu thầu g&acirc;y hậu quả nghi&ecirc;m trọng.</em></p>\r\n<p class=\"Normal\">Bị c&aacute;o buộc vai tr&ograve; đồng phạm, &ocirc;ng Trần Quốc Huy (41 tuổi, Trưởng ph&ograve;ng Tổ chức - H&agrave;nh ch&iacute;nh CDC Kh&aacute;nh H&ograve;a) bị bắt tạm giam; c&ograve;n Nguyễn Thị Th&uacute;y (Gi&aacute;m đốc Ph&ograve;ng dự &aacute;n C&ocirc;ng ty C&ocirc;ng ty TNHH Ph&aacute;t triển, ứng dụng c&ocirc;ng nghệ Việt Nam - VNDT) đ&atilde; bị bắt trong một vụ &aacute;n kh&aacute;c.</p>\r\n<p class=\"Normal\">Trong khoảng 2 tiếng, tổ c&ocirc;ng t&aacute;c gồm cảnh s&aacute;t v&agrave; đại diện VKS đ&atilde; tống đạt c&aacute;c quyết định khởi tố bị can, kh&aacute;m x&eacute;t CDC Kh&aacute;nh H&ograve;a - nơi l&agrave;m việc của &ocirc;ng D&otilde;ng v&agrave; Huy, thu giữ nhiều th&ugrave;ng t&agrave;i liệu.</p>\r\n<figure class=\"item_slide_show clearfix\">\r\n<div id=\"video_parent_357078\" class=\"box_embed_video_parent embed_video_new\" data-vcate=\"1003834\" data-vscate=\"1003869\" data-vscaten=\"Ph&aacute;p luật\" data-vid=\"357078\" data-ratio=\"1\" data-articleoriginal=\"4477103\" data-ads=\"0\" data-license=\"1\" data-duration=\"42\" data-brandsafe=\"0\" data-type=\"0\" data-play=\"1\" data-frame=\"\" data-aot=\"\" data-videoclassify=\"None\" data-initdom=\"1\" data-view=\"inview\" data-auto=\"1\" data-status=\"play\">\r\n<div id=\"embed_video_357078\" class=\"box_embed_video\"></div>\r\n</div>\r\n</figure>\r\n<p class=\"Normal\">Theo cơ quan điều tra, năm 2020-2021, CDC Kh&aacute;nh H&ograve;a được giao sử dụng ng&acirc;n s&aacute;ch v&agrave; nguồn thu dịch vụ để đấu thầu mua sắm trang thiết bị y tế ph&ograve;ng chống Covid-19. &Ocirc;ng D&otilde;ng giao Huy tổ chức v&agrave; thực hiện to&agrave;n bộ hồ sơ đấu thầu kit test PCR.</p>\r\n<p class=\"Normal\">Nh&agrave; chức tr&aacute;ch c&aacute;o buộc c&aacute;c bị can đ&atilde; th&ocirc;ng đồng với Nguyễn Thị Th&uacute;y thực hiện tr&aacute;i ph&aacute;p luật 5 g&oacute;i thầu mua sắm bộ h&oacute;a chất x&eacute;t nghiệm phục vụ x&eacute;t nghiệm Covid-19 với tổng trị gi&aacute; 14,215 tỷ đồng, g&acirc;y thiệt hại cho Nh&agrave; nước 9,86 tỷ đồng. &Ocirc;ng D&otilde;ng c&ugrave;ng một số nh&acirc;n vi&ecirc;n đ&atilde; nhận hoa hồng, hưởng lợi từ c&ocirc;ng ty tr&uacute;ng thầu, trong đ&oacute; c&oacute; C&ocirc;ng ty Việt &Aacute;.</p>\r\n<p class=\"Normal\">Hồi th&aacute;ng 2, cơ quan điều tra đ&atilde; đề nghị Sở Tư ph&aacute;p Kh&aacute;nh H&ograve;a th&ocirc;ng b&aacute;o cho tất cả văn ph&ograve;ng c&ocirc;ng chứng trong tỉnh \"kh&ocirc;ng thực hiện c&aacute;c thủ tục mua b&aacute;n, chuyển nhượng, tặng cho, thế chấp t&agrave;i sản\" của &ocirc;ng D&otilde;ng v&agrave; 9 người li&ecirc;n quan.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/17/CDC-Khanh-Hoa-6274-1655440265.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=zYw0O815DB4DjgQUb9-KKw 1x, https://i1-vnexpress.vnecdn.net/2022/06/17/CDC-Khanh-Hoa-6274-1655440265.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=XJGank85mHYI6h5l3zLFYQ 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/17/CDC-Khanh-Hoa-6274-1655440265.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=WVOlw1h_MoWe9Dnm2ByVnA 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/17/CDC-Khanh-Hoa-6274-1655440265.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=zYw0O815DB4DjgQUb9-KKw 1x, https://i1-vnexpress.vnecdn.net/2022/06/17/CDC-Khanh-Hoa-6274-1655440265.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=XJGank85mHYI6h5l3zLFYQ 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/17/CDC-Khanh-Hoa-6274-1655440265.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=WVOlw1h_MoWe9Dnm2ByVnA 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/06/17/CDC-Khanh-Hoa-6274-1655440265.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=zYw0O815DB4DjgQUb9-KKw\" alt=\"&Ocirc;ng Trần Quốc Huy bị cảnh s&aacute;t bắt, s&aacute;ng 17/6. Ảnh: B&ugrave;i To&agrave;n\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/06/17/CDC-Khanh-Hoa-6274-1655440265.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=zYw0O815DB4DjgQUb9-KKw\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">&Ocirc;ng Trần Quốc Huy bị cảnh s&aacute;t bắt, s&aacute;ng 17/6. Ảnh:&nbsp;<em>B&ugrave;i To&agrave;n</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Theo Sở Y tế Kh&aacute;nh H&ograve;a, CDC đ&atilde; mua 63.280 kit x&eacute;t nghiệm do C&ocirc;ng ty Việt &Aacute; sản xuất với số tiền hơn 26,2 tỷ đồng. Ri&ecirc;ng năm 2021, mua 58.580 kit hết 23,823 tỷ đồng. Việc mua kit x&eacute;t nghiệm được th&ocirc;ng qua C&ocirc;ng ty TNHH Khoa học Hợp Nhất bằng h&igrave;nh thức đấu thầu qua mạng v&agrave; ch&agrave;o h&agrave;ng cạnh tranh, gi&aacute; mua từ 367.500 đồng đến 509.250 đồng một kit.</p>\r\n<p class=\"Normal\">Sau 6 th&aacute;ng nh&agrave; chức tr&aacute;ch điều tra h&agrave;ng loạt sai phạm li&ecirc;n quan C&ocirc;ng ty Việt &Aacute;, Bộ C&ocirc;ng an v&agrave; c&ocirc;ng an c&aacute;c địa phương đ&atilde; khởi tố hơn 60 người. Trong đ&oacute; c&oacute; cựu bộ trưởng Y tế Nguyễn Thanh Long, cựu chủ tịch H&agrave; Nội Chu Ngọc Anh c&ugrave;ng h&agrave;ng loạt gi&aacute;m đốc CDC, c&aacute;n bộ Sở Y tế c&aacute;c tỉnh. Cơ quan điều tra Bộ Quốc ph&ograve;ng cũng khởi tố một vụ &aacute;n kh&aacute;c, bắt&nbsp;<a href=\"https://vnexpress.net/hai-sy-quan-nghien-cuu-kit-test-viet-a-bi-bat-4436150.html\" rel=\"dofollow\">hai sĩ quan</a>.</p>\r\n<p class=\"Normal\">Kết quả điều tra ban đầu x&aacute;c định, để thu lợi bất ch&iacute;nh v&agrave; chi tiền ngo&agrave;i hợp đồng, Việt &Aacute; đ&atilde; n&acirc;ng khống gi&aacute; thiết bị, chi ph&iacute; nguy&ecirc;n vật liệu đầu v&agrave;o, x&aacute;c định gi&aacute; b&aacute;n l&agrave; 470.000 đồng/kit. Ngo&agrave;i \"bắt tay\" với c&aacute;n bộ, l&atilde;nh đạo c&aacute;c CDC để th&ocirc;ng thầu, l&atilde;nh đạo Việt &Aacute; c&ograve;n chi % ngo&agrave;i hợp đồng gần 800 tỷ đồng cho nhiều c&aacute; nh&acirc;n, đơn vị.</p>', 218, 9, 0, 1, '2022-11-21 19:35:42', '2022-11-22 06:00:40'),
(190, 'Hoàng Nam và Quốc Khánh giành ngôi á quân đôi nam giải M15 Hải Đăng Cup tuần 3', 'hoang-nam-va-quoc-khanh-gianh-ngoi-a-quan-doi-nam-giai-m15-hai-dang-cup-tuan-3', 'Ở trận chung kết nội dung đôi nam giải M15 Hải Đăng Cup tuần 3, cặp Lý Hoàng Nam và Lê Quốc Khánh đã để thua sau 2 set đấu và giành ngôi á quân.', '<p>Trải qua gần 3 tuần thi đấu, giải M15 Hải Đăng Cup 2022 sắp sửa kh&eacute;p lại. Hiện tại, c&aacute;c tay vợt đang ở tuần đấu thứ ba ở T&acirc;y Ninh. Tại đ&acirc;y, c&aacute;c tay vợt Việt Nam đều c&oacute; những m&agrave;n tr&igrave;nh diễn xuất sắc.</p>\r\n<figure class=\"expNoEdit\"><img src=\"https://media.thethao.vn//uploads/2022/06/18/hoang-nam-va-quoc-khanh-gianh-ngoi-a-quan-m15-hai-dang-cup-tuan-3_98464.jpg\" alt=\"Ho&agrave;ng Nam v&agrave; Quốc Kh&aacute;nh gi&agrave;nh ng&ocirc;i &aacute; qu&acirc;n M15 Hải Đăng Cup tuần 3 - Ảnh 1\">\r\n<figcaption>Ho&agrave;ng Nam v&agrave; Quốc Kh&aacute;nh gi&agrave;nh ng&ocirc;i &aacute; qu&acirc;n M15 Hải Đăng Cup tuần 3 - Ảnh: CLB Hải Đăng</figcaption>\r\n</figure>\r\n<p>Chiều nay 18/6, một trong những trận đấu quan trọng nhất tại M15 Hải Đăng Cup tuần 3 l&agrave; trận chung kết nội dung đ&ocirc;i nam. Đ&acirc;y l&agrave; m&agrave;n so t&agrave;i giữa cặp L&yacute; Ho&agrave;ng Nam / L&ecirc; Quốc Kh&aacute;nh của Việt Nam v&agrave; Imamura Masamichi / Chikami Shohei của Nhật Bản.&nbsp;</p>\r\n<p>Bước v&agrave;o set 1, hai b&ecirc;n tạo ra m&agrave;n rượt đuổi tỷ số kh&aacute; kịch t&iacute;nh. Bước ngoặt chỉ đến ở game 11 khi Ho&agrave;ng Nam v&agrave; Quốc Kh&aacute;nh để mất break. Sang game 12, cặp đ&ocirc;i của Nhật Bản kh&ocirc;ng mắc sai lầm khi bảo vệ th&agrave;nh c&ocirc;ng game giao b&oacute;ng, qua đ&oacute; kh&eacute;p lại set 1 với chiến thắng 7-5.</p>\r\n<p><ins class=\"adsbygoogle\" data-ad-client=\"ca-pub-9305697404646295\" data-ad-slot=\"1910971528\" data-ad-format=\"auto\" data-full-width-responsive=\"true\" data-adsbygoogle-status=\"done\" data-ad-status=\"filled\"><ins id=\"aswift_2_expand\" tabindex=\"0\" title=\"Advertisement\" aria-label=\"Advertisement\"><ins id=\"aswift_2_anchor\"><iframe id=\"aswift_2\" src=\"https://googleads.g.doubleclick.net/pagead/ads?client=ca-pub-9305697404646295&amp;output=html&amp;h=280&amp;slotname=1910971528&amp;adk=3754884813&amp;adf=2377751787&amp;pi=t.ma~as.1910971528&amp;w=734&amp;fwrn=4&amp;fwrnh=100&amp;lmt=1655643269&amp;rafmt=1&amp;psa=1&amp;format=734x280&amp;url=https%3A%2F%2Fthethao.vn%2Ftennis%2Ftennis-viet-nam%2Fhoang-nam-va-quoc-khanh-gianh-ngoi-a-quan-doi-nam-giai-m15-hai-dang-cup-tuan-3-tt46855.html&amp;fwr=0&amp;fwrattr=true&amp;rpe=1&amp;resp_fmts=3&amp;wgl=1&amp;adsid=ChEI8JK7lQYQ_4qclevw5_XKARI5AB9zjp6JVNRSwyyOsz43UWLO3q121_XLv_9hyZ8SztOBEOlQg0MXiI-xteA9MgL-4onkFe9w9XpA&amp;uach=WyJXaW5kb3dzIiwiMTAuMC4wIiwieDg2IiwiIiwiMTAyLjAuMTI0NS40NCIsW10sbnVsbCxudWxsLCI2NCIsW1siIE5vdCBBO0JyYW5kIiwiOTkuMC4wLjAiXSxbIkNocm9taXVtIiwiMTAyLjAuNTAwNS4xMjQiXSxbIk1pY3Jvc29mdCBFZGdlIiwiMTAyLjAuMTI0NS40NCJdXSxmYWxzZV0.&amp;dt=1655643251083&amp;bpp=4&amp;bdt=521&amp;idt=1232&amp;shv=r20220615&amp;mjsv=m202206140101&amp;ptt=9&amp;saldr=aa&amp;abxe=1&amp;cookie=ID%3D9bb3664207b3bedd-2242b14c98d40045%3AT%3D1655641745%3ART%3D1655641745%3AS%3DALNI_MYuTpZj7IemO9tAXFLyIt5U0aVx5A&amp;gpic=UID%3D000006c78d72afe2%3AT%3D1655641745%3ART%3D1655641745%3AS%3DALNI_MaQ6PEC9U-mi3Z5Dc_qNZ6Z3jjFgQ&amp;prev_fmts=0x0%2C734x280&amp;nras=1&amp;correlator=4585843841623&amp;frm=20&amp;pv=1&amp;ga_vid=1404101269.1643716517&amp;ga_sid=1655643252&amp;ga_hid=1510087037&amp;ga_fc=1&amp;ga_cid=144720403.1655641750&amp;u_tz=420&amp;u_his=13&amp;u_h=720&amp;u_w=1280&amp;u_ah=680&amp;u_aw=1280&amp;u_cd=24&amp;u_sd=1.5&amp;dmc=4&amp;adx=78&amp;ady=1717&amp;biw=1263&amp;bih=609&amp;scr_x=0&amp;scr_y=0&amp;eid=44759875%2C44759926%2C44759842%2C44763507%2C42531556%2C44761044%2C21066435%2C42531608&amp;oid=2&amp;psts=AGkb-H8XoxAehCLWzXdTbN_86T8S30nAObM0tWs1xr01l3I4dfh9iVgbzLs32CSYXdqimokaQ8r6srGjuSkT1g&amp;pvsid=3805527031273192&amp;tmod=1713050512&amp;wsm=1&amp;uas=0&amp;nvt=1&amp;ref=https%3A%2F%2Fthethao.vn%2Ftennis%2Fwimbledon%2Fnaomi-osaka-khong-du-wimbledon-2022-tt46886.html&amp;eae=0&amp;fc=1920&amp;brdim=0%2C0%2C0%2C0%2C1280%2C0%2C1280%2C680%2C1280%2C609&amp;vis=1&amp;rsz=%7C%7CeEbr%7C&amp;abl=CS&amp;pfx=0&amp;fu=128&amp;bc=31&amp;jar=2022-06-19-01&amp;ifi=3&amp;uci=a!3&amp;btvi=1&amp;fsb=1&amp;xpc=O6rh3RosaN&amp;p=https%3A//thethao.vn&amp;dtd=18647\" name=\"aswift_2\" width=\"734\" height=\"280\" frameborder=\"0\" marginwidth=\"0\" marginheight=\"0\" scrolling=\"no\" sandbox=\"allow-forms allow-popups allow-popups-to-escape-sandbox allow-same-origin allow-scripts allow-top-navigation-by-user-activation\" data-google-container-id=\"a!3\" data-google-query-id=\"CILQt-XHufgCFVvDFgUdeGUDCw\" data-load-complete=\"true\"></iframe></ins></ins></ins></p>\r\n<p>Sang set 2, Imamura Masamichi / Chikami Shohei tỏ ra vượt trội ho&agrave;n to&agrave;n. Họ c&oacute; khởi đầu tốt khi li&ecirc;n tục gi&agrave;nh break v&agrave; vươn l&ecirc;n dẫn 4-0. M&atilde;i đến game 5, Ho&agrave;ng Nam v&agrave; Quốc Kh&aacute;nh mới c&oacute; game thắng đầu ti&ecirc;n. Trong khoảng thời gian c&ograve;n lại, bộ đ&ocirc;i của Việt Nam kh&ocirc;ng thể lội ngược d&ograve;ng, qua đ&oacute; chấp nhận g&aacute;c vợt với thất bại 1-6.&nbsp;</p>\r\n<figure class=\"expNoEdit\"><img src=\"https://media.thethao.vn//uploads/2022/06/18/hoang-nam-va-quoc-khanh-gianh-ngoi-a-quan-m15-hai-dang-cup-tuan-3_98465.jpg\" alt=\"Ho&agrave;ng Nam v&agrave; Quốc Kh&aacute;nh gi&agrave;nh ng&ocirc;i &aacute; qu&acirc;n M15 Hải Đăng Cup tuần 3 - Ảnh 2\">\r\n<figcaption>Cặp đ&ocirc;i của Nhật Bản v&ocirc; địch nội dung đ&ocirc;i nam M15 Hải Đăng Cup tuần 3 - Ảnh: CLB Hải Đăng</figcaption>\r\n</figure>\r\n<p>Chung cuộc, Quốc Kh&aacute;nh v&agrave; Ho&agrave;ng Nam để thua 0-2 v&agrave; gi&agrave;nh ng&ocirc;i &aacute; qu&acirc;n giải M15 Hải Đăng Cup tuần 3. Trong khi đ&oacute;, chức v&ocirc; địch thuộc về Imamura Masamichi / Chikami Shohei của Nhật Bản.&nbsp;</p>\r\n<p>Ng&agrave;y mai, ITF M15 Hải Đăng Cup sẽ bước v&agrave;o ng&agrave;y thi đấu cuối c&ugrave;ng. Theo đ&oacute;, L&yacute; Ho&agrave;ng Nam sẽ bước v&agrave;o trận chung kết đơn nam. Đối thủ của anh sẽ l&agrave; tay vợt người Ấn Độ - Dalwinder Singh.</p>', 218, 7, 1, 1, '2022-11-21 19:35:42', '2022-12-08 09:01:33'),
(192, 'Đã tìm ra nhà vô địch Giải bóng đá Cúp Tạp chí GTVT 2022', 'da-tim-ra-nha-vo-dich-giai-bong-da-cup-tap-chi-gtvt-2022', 'Đội bóng Nam Anh xuất sắc đánh bại CIENCO 4 trong trận chung kết, qua đó giành chức vô địch Giải bóng đá Cúp Tạp chí GTVT 2022.', '<p>Chiều 19/6, trận chung kết Giải b&oacute;ng đ&aacute; C&uacute;p Tạp ch&iacute; GTVT lần thứ nhất năm 2022 giữa hai đội b&oacute;ng Nam Anh v&agrave; CIENCO 4 đ&atilde; diễn ra tại s&acirc;n b&oacute;ng Tuấn Phong (Đại Lộ Chu Văn An, H&agrave; Nội).</p>\r\n<div>\r\n<p class=\"image\"><img class=\"width100 initial loaded\" src=\"https://cdn.baogiaothong.vn/upload/images/2022-2/article_img/2022-06-19/img-bgt-2021-a7-03800-2005-1655657551-width1280height720.jpg\" alt=\"Đ&atilde; t&igrave;m ra nh&agrave; v&ocirc; địch giải b&oacute;ng đ&aacute; c&uacute;p tạp ch&iacute; gtvt 2022\" data-original=\"https://cdn.baogiaothong.vn/upload/images/2022-2/article_img/2022-06-19/img-bgt-2021-a7-03800-2005-1655657551-width1280height720.jpg\" data-was-processed=\"true\"></p>\r\n<p class=\"img_chu_thich\">BTC trao chức v&ocirc; địch cho đội b&oacute;ng Nam Anh. Ảnh Th&agrave;nh Vũ</p>\r\n</div>\r\n<p>D&ugrave; được đ&aacute;nh gi&aacute; thấp hơn khi từng thua Nam Anh 1-4 ở v&ograve;ng bảng nhưng CIENCO 4 nhập cuộc đầy tự tin.</p>\r\n<p>Đội b&oacute;ng &aacute;o xanh chủ động chơi chắc chắn b&ecirc;n phần s&acirc;n nh&agrave; v&agrave; chờ cơ hội tung ra những đường phản c&ocirc;ng chất lượng.</p>\r\n<p>C&aacute;ch chơi n&agrave;y của CIENCO 4 khiến Nam Anh gặp nhiều kh&oacute; khăn v&agrave; khung th&agrave;nh của họ mới l&agrave; nơi bị uy hiếp thường xuy&ecirc;n trong hiệp 1.</p>\r\n<section class=\"bnrBt txtCent\"></section>\r\n<p>Sau 25 ph&uacute;t đầu ti&ecirc;n kh&ocirc;ng c&oacute; b&agrave;n thắng, Nam Anh đ&atilde; đẩy cao tốc độ khi bước v&agrave;o hiệp 2.</p>\r\n<p>Với nền tảng thể lực vượt trội, c&aacute;c cầu thủ &aacute;o trắng li&ecirc;n tục h&atilde;m th&agrave;nh đối phương từ nhiều hướng.</p>\r\n<p>Cuối c&ugrave;ng, những nỗ lực của đội b&oacute;ng mạnh hơn cũng đem đến kết quả với b&agrave;n thắng ở cuối trận.</p>\r\n<p>Chung cuộc, Nam Anh đ&aacute;nh bại CIENCO 4 1-0 để gi&agrave;nh ng&ocirc;i v&ocirc; địch Giải b&oacute;ng đ&aacute; C&uacute;p Tạp ch&iacute; GTVT lần thứ nhất năm 2022.</p>\r\n<div>\r\n<p class=\"image\"><img class=\"width100 loaded\" src=\"https://cdn.baogiaothong.vn/upload/images/2022-2/article_img/2022-06-19/img-bgt-2021-a6-01397-2001-1655657588-width1920height1080.jpg\" alt=\"Đ&atilde; t&igrave;m ra nh&agrave; v&ocirc; địch giải b&oacute;ng đ&aacute; c&uacute;p tạp ch&iacute; gtvt 2022\" data-original=\"https://cdn.baogiaothong.vn/upload/images/2022-2/article_img/2022-06-19/img-bgt-2021-a6-01397-2001-1655657588-width1920height1080.jpg\" data-was-processed=\"true\"></p>\r\n<p class=\"img_chu_thich\">Trận chung kết diễn ra hấp dẫn. Ảnh Th&agrave;nh Vũ</p>\r\n</div>\r\n<p>Trước đ&oacute;, ở trận tranh hạng Ba, đội b&oacute;ng Cơ quan Bộ GTVT đ&atilde; gi&agrave;nh chiến thắng với tỷ số 3-2 trước đội b&oacute;ng Thuận An.</p>\r\n<p>Ngay sau trận chung kết, BTC đ&atilde; tiến h&agrave;nh lễ bế mạc, trao giải cho c&aacute;c tập thể, c&aacute; nh&acirc;n c&oacute; th&agrave;nh t&iacute;ch xuất sắc.</p>\r\n<p>Đội v&ocirc; địch Nam Anh nhận c&uacute;p, cờ lưu niệm v&agrave; phần thưởng 10 triệu đồng. CIENCO 4 về nh&igrave; nhận giải thưởng 8 triệu đồng.</p>\r\n<p>Đội b&oacute;ng Cơ quan Bộ GTVT nhận giải ba với phần thưởng 5 triệu đồng c&ograve;n đội hạng tư Thuận An nhận 2 triệu đồng.</p>\r\n<p>Ngo&agrave;i ra, BTC c&ograve;n trao giải phong c&aacute;ch cho đội Đ&egrave;o Cả, danh hiệu Vua ph&aacute; lưới cho cầu thủ Cao Văn Trung của đội Thuận An, mỗi giải trị gi&aacute; 2 triệu đồng.</p>\r\n<div>\r\n<p class=\"image\"><img class=\"width100 loaded\" src=\"https://cdn.baogiaothong.vn/upload/images/2022-2/article_img/2022-06-19/img-bgt-2021-a7-03752-2008-1655657632-width1280height720.jpg\" alt=\"Đ&atilde; t&igrave;m ra nh&agrave; v&ocirc; địch giải b&oacute;ng đ&aacute; c&uacute;p tạp ch&iacute; gtvt 2022\" data-original=\"https://cdn.baogiaothong.vn/upload/images/2022-2/article_img/2022-06-19/img-bgt-2021-a7-03752-2008-1655657632-width1280height720.jpg\" data-was-processed=\"true\"></p>\r\n<p class=\"img_chu_thich\">&Ocirc;ng Nguyễn Văn Hường, Q.Tổng Bi&ecirc;n tập Tạp ch&iacute; GTVT, Trưởng BTC ph&aacute;t biểu bế mạc Giải. Ảnh Th&agrave;nh Vũ</p>\r\n</div>\r\n<p>Giải b&oacute;ng đ&aacute; Cup Tạp ch&iacute; GTVT lần thứ nhất năm 2022 do Tạp ch&iacute; GTVT tổ chức nhằm hướng đến kỷ niệm 97 năm Ng&agrave;y B&aacute;o ch&iacute; C&aacute;ch mạng Việt Nam (21/6/1925 - 21/6/2022), kỷ niệm 77 năm Ng&agrave;y Truyền thống Ng&agrave;nh GTVT (28/8/1945 - 28/8/2022) v&agrave; ch&agrave;o mừng Đại hội Đo&agrave;n TNCS Hồ Ch&iacute; Minh Bộ GTVT.</p>\r\n<p>Trong lần đầu ti&ecirc;n tổ chức, giải đ&atilde; quy tụ 9 đội b&oacute;ng tham dự, trong đ&oacute; c&oacute; 1 đội chủ nh&agrave; l&agrave; Tạp ch&iacute; GTVT v&agrave; 8 đội kh&aacute;ch mời.</p>\r\n<p>C&aacute;c đội b&oacute;ng đ&atilde; cống hiến cho kh&aacute;n giả nhiều pha b&oacute;ng hay, b&agrave;n thắng đẹp v&agrave; tr&ecirc;n tất cả l&agrave; tinh thần&nbsp;<a class=\"seokwl\" title=\"thể thao\" href=\"https://www.baogiaothong.vn/the-thao/\">thể thao</a>&nbsp;cao thượng.</p>\r\n<p>&ldquo;Giải b&oacute;ng đ&aacute; C&uacute;p Tạp ch&iacute; GTVT lần thứ nhất năm 2022 đ&atilde; kh&eacute;p lại với những dấu ấn đậm n&eacute;t về tinh thần r&egrave;n luyện thể thao n&acirc;ng cao sức khỏe, đo&agrave;n kết, cao thượng của c&aacute;c cầu thủ v&agrave; c&aacute;c đội b&oacute;ng.</p>\r\n<p>T&ocirc;i tin tưởng rằng giải đấu n&agrave;y sẽ được tiếp tục duy tr&igrave; v&agrave; tổ chức th&agrave;nh c&ocirc;ng hơn nữa ở những m&ugrave;a giải năm sau&rdquo;, &ocirc;ng Nguyễn Văn Hường, Q.Tổng Bi&ecirc;n tập Tạp ch&iacute; GTVT, Trưởng BTC giải ph&aacute;t biểu.</p>', 218, 1, 0, 0, '2022-11-21 19:35:42', '2022-11-22 08:41:45'),
(195, 'Tăng tốc trình duyệt Google Chrome chỉ với \"1 click chuột\"', 'tang-toc-trinh-duyet-google-chrome-chi-voi-1-click-chuot', 'Nếu cảm thấy trình duyệt Chrome của bạn đang hoạt động với tốc độ quá “ì ạch”, hãy thử cách sau để tăng tốc cho nó.', '<p>Với hơn 48% thị trường to&agrave;n cầu t&iacute;nh đến th&aacute;ng 5/2022, tr&igrave;nh duyệt Chrome của&nbsp;<a class=\"TextlinkBaiviet\" title=\"Google\" href=\"https://www.24h.com.vn/google-c407e4341.html\">Google</a>&nbsp;cho đến nay l&agrave; tr&igrave;nh duyệt web m&aacute;y t&iacute;nh phổ biến nhất v&agrave; c&oacute; lợi nhuận rất cao. Nh&igrave;n chung, Chrome đang thống trị&nbsp;<a class=\"TextlinkBaiviet\" title=\"đế chế\" href=\"https://www.24h.com.vn/de-che-aoe-viet-nam-c55e3523.html\">đế chế</a>&nbsp;của tr&igrave;nh duyệt web. Thậm ch&iacute; tr&ecirc;n nền tảng di động khi Safari đang dẫn đầu, Chrome cũng c&oacute; một thị phần đ&aacute;ng kể cho m&igrave;nh.</p>\r\n<p>Chrome được ưa chuộng v&igrave; nhiều l&yacute; do, trong đ&oacute; c&oacute; sự đơn giản v&agrave; tốc độ. Tuy nhi&ecirc;n, khi n&oacute;i đến tốc độ, mọi thứ kh&ocirc;ng phải l&uacute;c n&agrave;o cũng ho&agrave;n hảo. Chrome thường tỏ ra &ldquo;nhanh như chớp&rdquo; khi tải c&aacute;c trang web, nhưng tốc độ hoạt động lại bị ảnh hưởng khi mở số lượng lớn c&aacute;c tab.</p>\r\n<p>Trong những năm qua, Google đ&atilde; thực hiện nhiều tối ưu h&oacute;a nhằm mục đ&iacute;ch cải thiện hiệu suất v&agrave; giảm việc sử dụng t&agrave;i nguy&ecirc;n của c&aacute;c tab chạy nền. Tuy nhi&ecirc;n, mọi thứ dường như vẫn kh&ocirc;ng mấy khả quan - đặc biệt l&agrave; tr&ecirc;n c&aacute;c PC cũ - khi mở qu&aacute; nhiều tab. Nhưng c&oacute; một tiện &iacute;ch mở rộng miễn ph&iacute; cho ph&eacute;p bạn tăng tốc độ cực nhanh cho Chrome chỉ với 1 c&uacute; nhấp chuột.</p>\r\n<p>Tiện &iacute;ch c&oacute; t&ecirc;n OneTab, n&oacute; sẽ gi&uacute;p bạn tiết kiệm tới 95% bộ nhớ RAM v&agrave; giảm ho&agrave;n to&agrave;n sự lộn xộn của c&aacute;c tab đang mở, từ đ&oacute; tốc độ của tr&igrave;nh duyệt Chrome cũng tăng l&ecirc;n đ&aacute;ng kể. Tải tiện &iacute;ch từ Chrome Web Store tại&nbsp;<a href=\"https://chrome.google.com/webstore/detail/onetab/chphlpgkkbolifaimnlloiipkdnihall\" target=\"_blank\" rel=\"nofollow noopener\">địa chỉ n&agrave;y</a>.</p>\r\n<p align=\"center\">&nbsp;</p>\r\n<div id=\"container-24h-banner-in-image\">\r\n<div>\r\n<div><img class=\"news-image initial loading\" src=\"https://icdn.24h.com.vn/upload/2-2022/images/2022-06-02/picture-1-1654162430-649-width740height462.jpg\" alt=\"Tăng tốc tr&igrave;nh duyệt Google Chrome chỉ với &amp;#34;1 click chuột&amp;#34; - 1\" width=\"740\" data-was-processed=\"true\"></div>\r\n<div id=\"24h-banner-in-image\">\r\n<div id=\"ZQieARTVqB_139_15s\" class=\"txtCent\">\r\n<div class=\"text_adver_right\">&nbsp;</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n<p>&nbsp;</p>\r\n<p class=\"img_chu_thich_0407\">Dọn dẹp tr&igrave;nh duyệt Chrome chỉ với 1 c&uacute; nhấp chuột.</p>\r\n<p>Bất cứ khi n&agrave;o cảm thấy &ldquo;cho&aacute;ng&rdquo; v&igrave; số lượng tab đang mở hoặc hệ thống bắt đầu trở n&ecirc;n &ldquo;&igrave; ạch&rdquo;, chỉ cần bấm v&agrave;o biểu tượng của OneTab, l&uacute;c n&agrave;y to&agrave;n bộ c&aacute;c tab sẽ bị đ&oacute;ng lại.</p>\r\n<p>Đừng lo lắng, mọi thứ b&acirc;y giờ đ&atilde; được &ldquo;g&oacute;i gọn&rdquo; v&agrave;o một tab duy nhất trong một danh s&aacute;ch dưới dạng c&aacute;c li&ecirc;n kết, mỗi li&ecirc;n kết sẽ tương ứng với một tab đ&atilde; được mở trước đ&oacute;.</p>\r\n<p>Khi cần quay lại một trang web n&agrave;o đ&oacute;, chỉ cần mở n&oacute; từ danh s&aacute;ch. Đ&oacute; l&agrave; một giải ph&aacute;p tuyệt vời đơn giản v&agrave; hiệu quả cho t&igrave;nh trạng ngốn t&agrave;i nguy&ecirc;n của Chrome.</p>', 218, 10, 0, 1, '2022-11-21 19:35:42', '2022-11-22 06:12:03'),
(206, 'Pin sinh học tạo ra điện nhờ 3 loại vi khuẩn', 'pin-sinh-hoc-tao-ra-dien-nho-3-loai-vi-khuan', 'MỸ-Các khối pin của Đại học Binghamton mô phỏng quá trình tạo năng lượng trong tế bào sinh học và có thể dễ dàng ráp với nhau để sử dụng.', '<div id=\"divfirst\">\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/06/24/biobatteries-1920-resize-md-3500-1656046545.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=qHOPuvJQTvEO6cfPaSPAZA\" alt=\"Pin sinh học c&oacute; thể tạo ra năng lượng suốt nhiều tuần. Ảnh: Đại học Binghamton\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/06/24/biobatteries-1920-resize-md-3500-1656046545.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=qHOPuvJQTvEO6cfPaSPAZA\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Pin sinh học c&oacute; thể tạo ra năng lượng suốt nhiều tuần. Ảnh:&nbsp;<em>Đại học Binghamton</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Nh&oacute;m nh&agrave; nghi&ecirc;n cứu tại Đại học Binghamton, New York, t&igrave;m ra c&aacute;ch khiến pin sinh học c&oacute; thể sinh ra năng lượng suốt nhiều tuần với ba loại vi khuẩn kh&aacute;c nhau,<em>&nbsp;Interesting Engineering&nbsp;</em>h&ocirc;m 23/6 đưa tin. Nghi&ecirc;n cứu mới xuất bản tr&ecirc;n tạp ch&iacute;&nbsp;<em>Power Sources.</em></p>\r\n<p class=\"Normal\">Pin sinh học l&agrave; giải ph&aacute;p mới để cung cấp năng lượng cho c&aacute;c thiết bị bằng c&aacute;ch m&ocirc; phỏng qu&aacute; tr&igrave;nh tạo ra năng lượng trong tế b&agrave;o sinh học. Glucose l&agrave; nguồn năng lượng phổ biến nhất m&agrave; sinh vật sống sử dụng. Khi enzyme trong tế b&agrave;o ph&acirc;n giải glucose, n&oacute; cũng giải ph&oacute;ng c&aacute;c electron c&oacute; thể d&ugrave;ng để chạy c&aacute;c thiết bị điện.</p>\r\n<p class=\"Normal\">Seokheun Choi, gi&aacute;o sư tại Trường Kỹ thuật v&agrave; Khoa học Ứng dụng Thomas J. Watson thuộc Đại học Binghamton, nghi&ecirc;n cứu về pin sinh học nhiều năm v&agrave; nhận thấy, sự tương t&aacute;c của vi khuẩn c&oacute; thể tạo ra đủ năng lượng để vận h&agrave;nh c&aacute;c thiết bị trong v&agrave;i giờ.</p>\r\n<p class=\"Normal\">D&ugrave; khả năng n&agrave;y rất hữu &iacute;ch trong một số trường hợp, Choi c&ugrave;ng đồng nghiệp vẫn t&igrave;m c&aacute;ch tăng tuổi thọ cho pin. Trước đ&acirc;y, họ sử dụng hệ thống hai vi khuẩn để tạo ra điện cho pin sinh học. Trong nghi&ecirc;n cứu mới, họ sử dụng hệ thống ba vi khuẩn v&agrave; đặt ch&uacute;ng v&agrave;o những khoang ri&ecirc;ng biệt.</p>\r\n</div>\r\n<div id=\"admbackground\" data-set=\"dfp\"></div>\r\n<div id=\"divend\">\r\n<p class=\"Normal\">\"Một loại vi khuẩn quang hợp tạo ra thức ăn hữu cơ d&ugrave;ng l&agrave;m chất dinh dưỡng cho c&aacute;c tế b&agrave;o vi khuẩn kh&aacute;c b&ecirc;n dưới. Dưới c&ugrave;ng l&agrave; vi khuẩn sản xuất điện, c&ograve;n vi khuẩn ở giữa tạo ra một số h&oacute;a chất để cải thiện việc truyền electron\", Choi giải th&iacute;ch.</p>\r\n<p class=\"Normal\">Pin sinh học mới cũng c&oacute; c&aacute;ch lắp r&aacute;p mới. Đặt trong c&aacute;c khối hộp 3 cm x 3 cm, loại pin n&agrave;y giống như khối Lego, c&oacute; thể kết hợp v&agrave; sắp xếp lại một c&aacute;ch dễ d&agrave;ng. T&ugrave;y thuộc v&agrave;o thiết bị cần lắp pin, người ta c&oacute; thể sắp xếp những khối pin để tạo ra điện &aacute;p v&agrave; d&ograve;ng điện ph&ugrave; hợp.</p>\r\n<p class=\"Normal\">Choi tin rằng 6G sẽ được triển khai to&agrave;n cầu trong thập kỷ tới v&agrave; một lượng lớn c&aacute;c thiết bị nhỏ, th&ocirc;ng minh v&agrave; độc lập sẽ được sử dụng trong tương lai, c&oacute; thể ở cả những nơi xa x&ocirc;i v&agrave; khắc nghiệt. Khi đ&oacute;, pin sinh học sẽ rất hữu &iacute;ch.</p>\r\n<p class=\"Normal\">Trong tương lai, Choi mong muốn chế tạo loại pin c&oacute; thể tự phục hồi khi chịu hư hại trong m&ocirc;i trường khắc nghiệt. Tuy nhi&ecirc;n, mục ti&ecirc;u cuối c&ugrave;ng của &ocirc;ng l&agrave; khiến pin trở n&ecirc;n thật nhỏ gọn. \"Ch&uacute;ng t&ocirc;i gọi đ&oacute; l&agrave; \'bụi th&ocirc;ng minh\' v&agrave; chỉ v&agrave;i tế b&agrave;o vi khuẩn cũng c&oacute; thể tạo ra đủ năng lượng cho ch&uacute;ng. Sau đ&oacute;, ch&uacute;ng t&ocirc;i c&oacute; thể rắc ch&uacute;ng ở những nơi cần thiết\", &ocirc;ng bổ sung.</p>\r\n</div>', 218, 11, 0, 1, '0000-00-00 00:00:00', '2022-11-22 06:19:57'),
(210, 'Ronaldo được tìm kiếm nhiều nhất thế giới thể thao', 'ronaldo-duoc-tim-kiem-nhieu-nhat-the-gioi-the-thao', 'Tiền đạo Man Utd Cristiano Ronaldo là ngôi sao thể thao được tìm kiếm nhiều nhất trên toàn cầu, bỏ xa Lionel Messi, LeBron James hay Tom Brady.', '<div id=\"divfirst\">\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture data-inimage=\"done\"><img class=\"lazy lazied\" src=\"https://i1-thethao.vnecdn.net/2022/06/25/DD-COMP-MOST-INFLUENTIAL-SPORT-9133-5030-1656122640.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=hgMLmGfA8L9ueGHTJIxASw\" alt=\"Ronaldo, ở tuổi 37 v&agrave; đ&atilde; qua thời đỉnh cao, vẫn c&oacute; sức h&uacute;t lớn nhất tr&ecirc;n mạng x&atilde; hội.\" loading=\"lazy\" data-src=\"https://i1-thethao.vnecdn.net/2022/06/25/DD-COMP-MOST-INFLUENTIAL-SPORT-9133-5030-1656122640.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=hgMLmGfA8L9ueGHTJIxASw\" data-ll-status=\"loaded\" data-adbro-processed=\"true\"></picture>\r\n<div class=\"embed-container-ads\">\r\n<div id=\"sis_inimage\" data-google-query-id=\"CMjm-bvHx_gCFYKBlgodSzYOCw\">\r\n<div id=\"google_ads_iframe_/27973503/Vnexpress/Desktop/Inimage/Thethao/Thethao.hautruong.detail_0__container__\"><iframe id=\"google_ads_iframe_/27973503/Vnexpress/Desktop/Inimage/Thethao/Thethao.hautruong.detail_0\" tabindex=\"0\" title=\"3rd party ad content\" role=\"region\" name=\"google_ads_iframe_/27973503/Vnexpress/Desktop/Inimage/Thethao/Thethao.hautruong.detail_0\" width=\"1\" height=\"1\" frameborder=\"0\" marginwidth=\"0\" marginheight=\"0\" scrolling=\"no\" aria-label=\"Advertisement\" data-google-container-id=\"5\" data-load-complete=\"true\"></iframe></div>\r\n</div>\r\n</div>\r\n</div>\r\n<figcaption>\r\n<p class=\"Image\">Ronaldo, ở tuổi 37 v&agrave; đ&atilde; qua thời đỉnh cao, vẫn c&oacute; sức h&uacute;t lớn nhất tr&ecirc;n mạng x&atilde; hội.</p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Theo&nbsp;<em>betting.com</em>,&nbsp;<a href=\"https://vnexpress.net/chu-de/cristiano-ronaldo-194\" rel=\"dofollow\">Ronaldo</a>&nbsp;được t&igrave;m kiếm trung b&igrave;nh 11 triệu lần mỗi th&aacute;ng, cao gấp đ&ocirc;i người đứng sau l&agrave; VĐV b&oacute;ng bầu dục người Mỹ Ryan Ramczyk (6,1 triệu).&nbsp;<a href=\"https://vnexpress.net/chu-de/neymar-335\" rel=\"dofollow\">Neymar</a>&nbsp;v&agrave; huyền thoại NBA&nbsp;<a href=\"https://vnexpress.net/chu-de/lebron-james-292\" rel=\"dofollow\">LeBron James</a>&nbsp;l&agrave; hai ng&ocirc;i sao thể thao kh&aacute;c c&oacute; tr&ecirc;n 5 triệu lượt t&igrave;m kiếm mỗi th&aacute;ng, với lần lượt 5,8 v&agrave; 5,3 triệu. K&igrave;nh địch của Ronaldo,&nbsp;<a href=\"https://vnexpress.net/chu-de/lionel-messi-300\" rel=\"dofollow\">Messi</a>&nbsp;chỉ đứng thứ năm với 4,8 triệu.</p>\r\n<p class=\"Normal\">Xếp sau lần lượt l&agrave; Tom Brady - người đoạt Super Bowl bảy lần - với 4,3 triệu v&agrave; hai ng&ocirc;i sao F1 Lewis Hamilton (3,9) v&agrave; Max Verstappen (3,8). Aaron Rodgers, một cầu thủ của NFL kh&aacute;c, cũng v&agrave;o top 10 ng&ocirc;i sao thể thao được t&igrave;m kiếm nhiều nhất.</p>\r\n<p class=\"Normal\">Si&ecirc;u sao UFC&nbsp;<a href=\"https://vnexpress.net/chu-de/conor-mcgregor-193\" rel=\"dofollow\">Conor McGregor</a>&nbsp;chốt top 10 với 3,1 triệu lượt t&igrave;m kiếm mỗi th&aacute;ng. McGregor kh&ocirc;ng thi đấu từ khi lật cổ ch&acirc;n ở trận gặp Dustin Poirier hồi th&aacute;ng 7/2021 v&agrave; đang đ&agrave;m ph&aacute;n thượng đ&agrave;i với \"Độc c&ocirc; cầu bại\" Floyd Mayweather.</p>\r\n</div>\r\n<div id=\"admbackground\" data-set=\"dfp\"></div>\r\n<div id=\"divend\">\r\n<p class=\"Normal\">Kh&ocirc;ng c&oacute; tầm ảnh hưởng lớn nhất, nhưng Messi lại gi&uacute;p&nbsp;<a href=\"https://vnexpress.net/the-thao/du-lieu-bong-da/doi-bong/psg-85\" rel=\"dofollow\">PSG</a>&nbsp;lập kỷ lục b&aacute;n &aacute;o. Theo&nbsp;<em>Sportmail</em>, &aacute;o số 30 của Messi chiếm hơn 60% tr&ecirc;n tổng số 1 triệu &aacute;o đấu được PSG b&aacute;n m&ugrave;a trước. Chủ s&acirc;n Parc des Princes lần đầu trong lịch sử c&aacute;n mốc n&agrave;y để so&aacute;n ng&ocirc;i Man Utd th&agrave;nh CLB b&aacute;n nhiều &aacute;o đấu nhất.</p>\r\n<p class=\"Normal\">Sau khi c&oacute; Messi, PSG phải mở rộng cửa h&agrave;ng tr&ecirc;n đại lộ Champs-Elysses, nhằm đ&aacute;p ứng nhu cầu ng&agrave;y c&agrave;ng lớn của người h&acirc;m mộ. Marc Armstrong, Gi&aacute;m đốc đối t&aacute;c của PSG, n&oacute;i với&nbsp;<em>Marca</em>: \"Nhu cầu đ&atilde; tăng 30 đến 40%, v&agrave; điều c&oacute; thể ngăn xu thế n&agrave;y chậm lại chỉ l&agrave; nguồn cung. Ch&uacute;ng t&ocirc;i kh&ocirc;ng thể đ&aacute;p ứng nhu cầu về lượng &aacute;o của Messi, với số &aacute;o b&aacute;n ra đ&atilde; đạt mức cao nhất. Kh&ocirc;ng ai c&oacute; thể đ&aacute;p ứng nhu cầu đ&oacute;. PSG b&aacute;n &aacute;o đấu nhiều nhất thế giới, v&agrave; điều đ&oacute; đưa ch&uacute;ng t&ocirc;i l&ecirc;n tầm cao mới. Doanh thu với thương hiệu Jordan l&agrave; rất lớn, nhưng nhu cầu c&ograve;n tăng l&ecirc;n khi bạn k&yacute; hợp đồng với một cầu thủ như Messi\".</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-thethao.vnecdn.net/2022/06/25/np-file-105794-jpeg-2155-1656122640.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=lXjulSNcefyIrHN63TW0Xw 1x, https://i1-thethao.vnecdn.net/2022/06/25/np-file-105794-jpeg-2155-1656122640.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Q6U1mBgteMCwjyFx-Tfzuw 1.5x, https://i1-thethao.vnecdn.net/2022/06/25/np-file-105794-jpeg-2155-1656122640.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=DW1FFMtG1-73-jXuEQJCwQ 2x\" data-srcset=\"https://i1-thethao.vnecdn.net/2022/06/25/np-file-105794-jpeg-2155-1656122640.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=lXjulSNcefyIrHN63TW0Xw 1x, https://i1-thethao.vnecdn.net/2022/06/25/np-file-105794-jpeg-2155-1656122640.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=Q6U1mBgteMCwjyFx-Tfzuw 1.5x, https://i1-thethao.vnecdn.net/2022/06/25/np-file-105794-jpeg-2155-1656122640.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=DW1FFMtG1-73-jXuEQJCwQ 2x\"><img class=\"lazy lazied\" src=\"https://i1-thethao.vnecdn.net/2022/06/25/np-file-105794-jpeg-2155-1656122640.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=lXjulSNcefyIrHN63TW0Xw\" alt=\"&Aacute;o đấu Messi thường xuy&ecirc;n trong t&igrave;nh trạng ch&aacute;y h&agrave;ng ở c&aacute;c cửa h&agrave;ng lưu niệm của PSG. Ảnh: Reuters\" loading=\"lazy\" data-src=\"https://i1-thethao.vnecdn.net/2022/06/25/np-file-105794-jpeg-2155-1656122640.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=lXjulSNcefyIrHN63TW0Xw\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">&Aacute;o đấu Messi thường xuy&ecirc;n trong t&igrave;nh trạng ch&aacute;y h&agrave;ng ở c&aacute;c cửa h&agrave;ng lưu niệm của PSG. Ảnh:&nbsp;<em>Reuters</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Theo Armstrong, doanh thu của PSG tăng 13% sau khi chi&ecirc;u mộ Messi, v&agrave; họ đ&atilde; k&yacute; nhiều hợp đồng t&agrave;i trợ với c&aacute;c thương hiệu nổi tiếng như Dior, Gorillas, Crypto.com, Autohero, GOAT, Smart Good Things, Infinity Sports Water, Geekvape, PlayBetR, Volt hay Big Cola. Ngo&agrave;i ra, số người theo d&otilde;i tr&ecirc;n mạng x&atilde; hội của PSG tăng 1,4 triệu mỗi tuần, c&ugrave;ng sự gia tăng đ&aacute;ng kể về doanh số b&aacute;n v&eacute;. Tổng cộng, PSG cho rằng họ thu về 735 triệu USD sau khi k&yacute; hợp đồng với Messi.</p>\r\n<p class=\"Normal\">Ronaldo tạo hiệu ứng tương tự khi trở lại s&acirc;n Old Trafford h&egrave; năm ngo&aacute;i. &Aacute;o đấu của si&ecirc;u sao Bồ Đ&agrave;o Nha thu về cho Man Utd 230 triệu USD từ th&aacute;ng 9/2021, nhưng vẫn xếp sau Messi.</p>\r\n</div>', 218, 7, 0, 1, '2022-11-21 19:29:56', '2022-11-22 05:52:56'),
(212, 'Virus bị xóa sổ 40 năm \'trỗi dậy\' ở Anh', 'virus-bi-xoa-so-40-nam-troi-day-o-anh', 'ANH-Giới chức y tế cảnh báo bệnh bại liệt tái xuất sau khi ghi nhận dấu vết của virus trong các mẫu nước thải tại London.', '<p class=\"Normal\">D&ugrave; chưa ch&iacute;nh thức ph&aacute;t hiện ca nhiễm, giới chức gọi đ&acirc;y l&agrave; \"sự cố cấp quốc gia\", k&ecirc;u gọi người d&acirc;n ti&ecirc;m ph&ograve;ng để ngăn ngừa virus l&acirc;y lan.</p>\r\n<p class=\"Normal\">C&aacute;c nh&agrave; khoa học đ&atilde; ph&aacute;t hiện một chủng virus bại liệt trong nước thải sinh hoạt tại Bắc v&agrave; Đ&ocirc;ng London hồi th&aacute;ng 2. Đ&acirc;y l&agrave; nguồn nước thải từ 4 triệu người.</p>\r\n<p class=\"Normal\">Ban đầu, họ kh&ocirc;ng qu&aacute; lo ngại. Tuy nhi&ecirc;n, đến th&aacute;ng 4 v&agrave; th&aacute;ng 5, c&aacute;c chuy&ecirc;n gia ghi nhận phi&ecirc;n bản đột biến c&ugrave;ng d&ograve;ng của virus, cho thấy c&oacute; sự l&acirc;y lan li&ecirc;n tục giữa nhiều c&aacute; thể trong v&agrave;i th&aacute;ng. Điều n&agrave;y cho ph&eacute;p virus tiến h&oacute;a. Giới chức đ&atilde; thực hiện nhiều cuộc x&eacute;t nghiệm cục bộ tại 6 khu vực ở thủ đ&ocirc; để x&aacute;c định ổ dịch.</p>\r\n<p class=\"Normal\">Tại Anh, người d&acirc;n được chủng ngừa bằng loại vaccine bất hoạt đường ti&ecirc;m, kh&ocirc;ng thể thải ra ngo&agrave;i qua ph&acirc;n hoặc nước tiểu. Tuy nhi&ecirc;n, một số quốc gia tr&ecirc;n thế giới sử dụng vaccine bại liệt đường uống chứa ba chủng virus sống đ&atilde; suy yếu. Họ c&oacute; thể thải virus qua ph&acirc;n trong thời gian ngắn.</p>\r\n<p class=\"Normal\">C&aacute;c nh&agrave; khoa học suy đo&aacute;n một du kh&aacute;ch hoặc người nhập cảnh từ nước ngo&agrave;i đ&atilde; uống vaccine bại liệt trước khi đến Anh. V&igrave; vậy, virus xuất hiện trong hệ thống nước thải sinh hoạt. Người n&agrave;y c&oacute; thể sinh sống tại Nigeria, Pakistan hoặc Afghanistan, nơi dịch bệnh c&ograve;n lưu h&agrave;nh.</p>\r\n<p class=\"Normal\">Theo Vanessa Saliba, chuy&ecirc;n gia tư vấn Dịch tễ học tại Cơ quan An ninh Y tế Anh (UKHSA), virus bại liệt nguồn gốc từ vaccine c&oacute; khả năng l&acirc;y lan, đặc biệt tại c&aacute;c cộng đồng nơi mức độ ti&ecirc;m chủng thấp.</p>\r\n<p class=\"Normal\">\"Trong một số trường hợp hiếm hoi, n&oacute; c&oacute; thể g&acirc;y bệnh ở người chưa được ti&ecirc;m chủng. V&igrave; vậy, nếu con bạn chưa ti&ecirc;m vaccine bại liệt, h&atilde;y li&ecirc;n hệ ngay với b&aacute;c sĩ đa khoa để l&agrave;m điều n&agrave;y\", b&agrave; cho biết.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-suckhoe.vnecdn.net/2022/06/24/telemmglpict000300442623-trans-5136-9316-1656038070.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=mvGzJzDPU4ESOjWhgzvJyw 1x, https://i1-suckhoe.vnecdn.net/2022/06/24/telemmglpict000300442623-trans-5136-9316-1656038070.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=5_t0TVT4ILcEH6MgB6bPgw 1.5x, https://i1-suckhoe.vnecdn.net/2022/06/24/telemmglpict000300442623-trans-5136-9316-1656038070.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=l_xcT-WrlHJjf7mAjrmQag 2x\" data-srcset=\"https://i1-suckhoe.vnecdn.net/2022/06/24/telemmglpict000300442623-trans-5136-9316-1656038070.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=mvGzJzDPU4ESOjWhgzvJyw 1x, https://i1-suckhoe.vnecdn.net/2022/06/24/telemmglpict000300442623-trans-5136-9316-1656038070.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=5_t0TVT4ILcEH6MgB6bPgw 1.5x, https://i1-suckhoe.vnecdn.net/2022/06/24/telemmglpict000300442623-trans-5136-9316-1656038070.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=l_xcT-WrlHJjf7mAjrmQag 2x\"><img class=\"lazy lazied\" src=\"https://i1-suckhoe.vnecdn.net/2022/06/24/telemmglpict000300442623-trans-5136-9316-1656038070.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=mvGzJzDPU4ESOjWhgzvJyw\" alt=\"Bệnh nh&acirc;n bại liệt được lắp m&aacute;y thở ở ngực để c&oacute; thể h&ocirc; hấp b&igrave;nh thường. Ảnh: Hulton Archive\" loading=\"lazy\" data-src=\"https://i1-suckhoe.vnecdn.net/2022/06/24/telemmglpict000300442623-trans-5136-9316-1656038070.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=mvGzJzDPU4ESOjWhgzvJyw\" data-ll-status=\"loaded\"></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Bệnh nh&acirc;n bại liệt được lắp m&aacute;y thở ở ngực để c&oacute; thể h&ocirc; hấp b&igrave;nh thường. Ảnh:&nbsp;<em>Hulton Archive</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">C&aacute;c chuy&ecirc;n gia y tế cũng lo ngại c&aacute;c b&aacute;c sĩ kh&ocirc;ng c&ograve;n nhận ra những triệu chứng của bệnh bại liệt, bởi n&oacute; đ&atilde; kh&ocirc;ng c&ograve;n lưu h&agrave;nh trong nhiều thập kỷ, nghĩa l&agrave; c&aacute;c trường hợp c&oacute; thể bị chẩn đo&aacute;n sai. Giới chức y&ecirc;u cầu b&aacute;c sĩ đa khoa lưu &yacute; biểu hiện bất thường v&agrave; b&aacute;o c&aacute;o những ca nghi nhiễm virus kịp thời.</p>\r\n<p class=\"Normal\">Bệnh bại liệt l&acirc;y lan chủ yếu do người nhiễm virus rửa tay kh&ocirc;ng đ&uacute;ng c&aacute;ch, sau đ&oacute; chạm v&agrave;o đồ ăn hoặc thức uống của người kh&aacute;c. Virus ph&aacute;t triển mạnh ở đường ruột v&agrave; xuất hiện trong ph&acirc;n. Ở khoảng 1% c&aacute;c bệnh nh&acirc;n, virus c&oacute; thể l&acirc;y nhiễm v&agrave;o cột sống v&agrave; g&acirc;y t&ecirc; liệt.</p>\r\n<p class=\"Normal\">Tiến sĩ David Heymann, chuy&ecirc;n gia về bệnh truyền nhiễm tại Trường Vệ sinh v&agrave; Y học Nhiệt đới London, cho biết hầu hết c&aacute;c bệnh nh&acirc;n kh&ocirc;ng c&oacute; triệu chứng.</p>\r\n<p class=\"Normal\">UKHSA cho biết nếu ph&aacute;t hiện ổ dịch cụ thể, giới chức sẽ tổ chức đợt ti&ecirc;m chủng h&agrave;ng loạt để bảo vệ cộng đồng.</p>\r\n<p class=\"Normal\">Jane Clegg, y t&aacute; trưởng của Dịch vụ Y tế Quốc gia (NHS) cho biết: \"Phần lớn người d&acirc;n London an to&agrave;n trước bệnh bại liệt, kh&ocirc;ng cần thực hiện th&ecirc;m bất cứ biện ph&aacute;p n&agrave;o. Tuy nhi&ecirc;n, NHS sẽ bắt đầu li&ecirc;n hệ với c&aacute;c bậc phụ huynh c&oacute; con em dưới 5 tuổi tại London để cập nhật về chương tr&igrave;nh ti&ecirc;m ph&ograve;ng\".</p>\r\n<p class=\"Normal\">Virus bại liệt đ&atilde; bị loại trừ ở mọi quốc gia tr&ecirc;n thế giới, ngoại trừ Afghanistan v&agrave; Pakistan. Tuy nhi&ecirc;n, bệnh bại liệt c&oacute; nguồn gốc từ vaccine tiếp tục g&acirc;y ra c&aacute;c đợt dịch nhỏ, đặc biệt tại những cộng đồng hoặc nh&oacute;m d&acirc;n số c&oacute; tỷ lệ ti&ecirc;m chủng thấp, chẳng hạn trẻ em.</p>\r\n<p class=\"Normal\">Theo c&aacute;c chuy&ecirc;n gia Anh, đợt l&acirc;y truyền cộng đồng rất c&oacute; thể l&agrave; ở trẻ nhỏ. Khả năng &iacute;t xảy ra hơn l&agrave; từ một người suy giảm miễn dịch đ&atilde; nhiễm virus trong nhiều th&aacute;ng.</p>', 218, 5, 3, 1, '2022-11-21 19:34:58', '2022-11-21 21:20:47'),
(213, 'Giá vàng hôm nay 25/6: Tiếp đà suy giảm', 'gia-vang-hom-nay-256-tiep-da-suy-giam', '(VTC News) - Giá vàng hôm nay 25/6 sụt giảm trong bối cảnh thị trường chứng khoán toàn cầu tăng điểm trong phiên cuối tuần.', '<p>6h s&aacute;ng nay 25/6 (giờ Việt Nam),&nbsp;gi&aacute; v&agrave;ng thế giới&nbsp;tr&ecirc;n s&agrave;n Kitco giao dịch ở mức 1.826 USD/ounce, kh&ocirc;ng đổi so với đầu giờ s&aacute;ng qua.&nbsp;</p>\r\n<p>V&agrave;ng sụt giảm trong bối cảnh&nbsp;<a class=\"inlink-remake\" title=\"thị trường chứng kho&aacute;n\" href=\"https://vtc.vn/tieu-diem/thi-truong-chung-khoan.html\">thị trường chứng kho&aacute;n</a> to&agrave;n cầu tăng điểm trong phi&ecirc;n cuối tuần.</p>\r\n<p>B&ecirc;n cạnh đ&oacute;, kim loại m&agrave;u cũng kh&ocirc;ng nhận được sự hậu thuẫn từ thị trường h&agrave;ng h&oacute;a, nhất l&agrave; dầu th&ocirc;.</p>\r\n<p>Diễn biến đ&aacute;ng ch&uacute; &yacute; kể từ đầu tuần n&agrave;y l&agrave; gi&aacute; h&agrave;ng h&oacute;a th&ocirc; kỳ hạn sụt giảm mạnh, dẫn đầu l&agrave; gi&aacute; dầu th&ocirc;. Đ&acirc;y được coi l&agrave; một manh mối cho thấy &aacute;p lực lạm ph&aacute;t c&oacute; thể đ&atilde; l&ecirc;n đến đỉnh điểm.</p>\r\n<figure class=\"expNoEdit\"><img class=\"lazy loaded\" src=\"https://image.vtc.vn/resize/th/upload/2022/06/25/vang-23-07054272.jpeg\" alt=\"Gi&aacute; v&agrave;ng h&ocirc;m nay 25/6: Tiếp đ&agrave; suy giảm - 1\" height=\"422\" data-id=\"1841259\" data-detail=\"1\" data-width=\"660\" data-height=\"422\" data-src=\"https://image.vtc.vn/resize/th/upload/2022/06/25/vang-23-07054272.jpeg\" data-index=\"0\" data-adbro-processed=\"true\" data-was-processed=\"true\">\r\n<div id=\"adbro\" class=\"adbro-animated adbro-sm adbro-xs adbro-md\">\r\n<div>\r\n<div class=\"adbro-fristi\">\r\n<div class=\"adbro-fristi__block adbro-takeover-replay\">\r\n<div class=\"adbro-fristi__video-container\">&nbsp;</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</figure>\r\n<p>Khả năng suy tho&aacute;i ng&agrave;y c&agrave;ng tăng tại c&aacute;c nền&nbsp;<a class=\"inlink-remake\" title=\"kinh tế\" href=\"https://vtc.vn/kinh-te-29.html\">kinh tế</a>&nbsp;lớn tr&ecirc;n to&agrave;n cầu đ&atilde; khiến c&aacute;c nh&agrave; kinh doanh h&agrave;ng h&oacute;a cho rằng nhu cầu h&agrave;ng h&oacute;a sẽ suy yếu trong những th&aacute;ng tới.</p>\r\n<p>Lo lắng về duy tho&aacute;i kinh tế vẫn tăng l&ecirc;n bất chấp chủ tịch Cục Dự trữ li&ecirc;n bang Mỹ (Fed) Jerome Powell xoa dịu c&aacute;c nh&agrave; lập ph&aacute;p v&agrave; nh&agrave; đầu tư tr&ecirc;n thị trường trong b&agrave;i ph&aacute;t biểu trước Quốc hội Mỹ h&ocirc;m thứ 4 v&agrave; 5 vừa qua.</p>\r\n<p><strong>Gi&aacute; v&agrave;ng trong nước</strong></p>\r\n<p>Kết th&uacute;c phi&ecirc;n giao dịch ng&agrave;y 24/6, C&ocirc;ng ty V&agrave;ng bạc đ&aacute; qu&yacute; S&agrave;i G&ograve;n ni&ecirc;m yết gi&aacute; v&agrave;ng SJC ở mức 67,8 triệu đồng/lượng (mua v&agrave;o) v&agrave; 68,6 triệu đồng/lượng (b&aacute;n ra).</p>\r\n<p>Tập đo&agrave;n DOJI ni&ecirc;m yết gi&aacute; v&agrave;ng ở mức 67,75 triệu đồng/lượng (mua v&agrave;o) v&agrave; 68,55 triệu đồng/lượng (b&aacute;n ra).</p>\r\n<p><strong>Dự b&aacute;o gi&aacute; v&agrave;ng</strong></p>\r\n<p>&Ocirc;ng Jim Wyckoff, chuy&ecirc;n gia ph&acirc;n t&iacute;ch cấp cao của c&ocirc;ng ty Kitco Metals, c&oacute; trụ sở tại Canada cho rằng, thị trường v&agrave;ng v&agrave; bạc c&ograve;n đang gặp lực cản từ những đồn đo&aacute;n rằng sự giảm tốc của nền kinh tế n&oacute;i chung c&oacute; thể l&agrave;m giảm nhu cầu đối với c&aacute;c kim loại qu&yacute; n&agrave;y, d&ugrave; vị thế l&agrave; k&ecirc;nh tr&uacute; ẩn an to&agrave;n của v&agrave;ng đang hạn chế đ&agrave; giảm n&agrave;y.&nbsp;</p>\r\n<p>Nicky Shiels, chiến lược gia về kim loại cho rằng, d&ugrave; độ biến động của v&agrave;ng tương đối thấp so với c&aacute;c t&agrave;i sản kh&aacute;c v&agrave; giao dịch thoải m&aacute;i trong khoảng 1.800 - 1.900 USD/ounce, nhưng đừng mong đợi một đợt tăng gi&aacute; cho đến khi một số niềm tin quay trở lại thị trường.&nbsp;</p>\r\n<p>Khi lo ngại lạm ph&aacute;t nhường chỗ cho sự hoảng loạn suy tho&aacute;i, c&aacute;c nh&agrave; đầu tư cổ phiếu sẽ chọn tiền mặt thay v&igrave; những nơi tr&uacute; ẩn an to&agrave;n như v&agrave;ng.</p>', 218, 4, 3, 1, '2022-11-21 19:38:04', '2022-11-29 20:19:24');
INSERT INTO `posts` (`id`, `title`, `slug`, `excerpt`, `body`, `user_id`, `category_id`, `views`, `approved`, `created_at`, `updated_at`) VALUES
(214, 'Việt Nam - Campuchia: Mối quan hệ mãi mãi xanh tươi, đời đời bền vững', 'viet-nam---campuchia-moi-quan-he-mai-mai-xanh-tuoi-doi-doi-ben-vung', 'Sáng 24/6, tại Nhà hát Lớn, thành phố Hà Nội long trọng tổ chức Lễ kỷ niệm 55 năm Ngày thiết lập quan hệ ngoại giao Việt Nam - Campuchia (24/6/1967 - 24/6/2022).', '<p><img class=\"medium-zoom-image\" src=\"https://hanoi.gov.vn/image/image_gallery?img_id=2003102852693\" alt=\"\"><em>Chương tr&igrave;nh biểu diễn nghệ thuật kỷ niệm 55 năm Ng&agrave;y thiết lập quan hệ ngoại giao Việt Nam - Campuchia</em></p>\r\n<p>Dự buổi lễ, về ph&iacute;a Việt Nam c&oacute; c&aacute;c đồng ch&iacute;: Ủy vi&ecirc;n Bộ Ch&iacute;nh trị, Thường trực Ban B&iacute; thư, Trưởng ban Chỉ đạo quốc gia năm hữu nghị Việt Nam - Campuchia V&otilde; Văn Thưởng; nguy&ecirc;n Ủy vi&ecirc;n Bộ Ch&iacute;nh trị, nguy&ecirc;n Thủ tướng Ch&iacute;nh phủ Nguyễn Tấn Dũng; nguy&ecirc;n Ủy vi&ecirc;n Bộ Ch&iacute;nh trị, nguy&ecirc;n Bộ trưởng Bộ Quốc ph&ograve;ng, Anh h&ugrave;ng Lực lượng Vũ trang Nh&acirc;n d&acirc;n, Đại tướng Phạm Văn Tr&agrave;; B&iacute; thư Trung ương Đảng; Chủ tịch Ủy ban MTTQ Việt Nam Đỗ Văn Chiến; B&iacute; thư Trung ương Đảng, Trưởng Ban Tuy&ecirc;n gi&aacute;o Trung ương Nguyễn Trọng Nghĩa; B&iacute; thư Trung ương Đảng, Ph&oacute; Thủ tướng Ch&iacute;nh phủ L&ecirc; Minh Kh&aacute;i. Về ph&iacute;a H&agrave; Nội, Ủy vi&ecirc;n Bộ Ch&iacute;nh trị, B&iacute; thư Th&agrave;nh ủy, Trưởng đo&agrave;n Đại biểu Quốc hội th&agrave;nh phố H&agrave; Nội Đinh Tiến Dũng v&agrave; Ủy vi&ecirc;n Trung ương Đảng, Ph&oacute; B&iacute; thư Thường trực Th&agrave;nh ủy Nguyễn Thị Tuyến dự sự kiện.</p>\r\n<div>\r\n<div class=\"trv-player-container\">\r\n<div class=\"trv-wrapper\">\r\n<div id=\"br_video_player_72275\" class=\"trvd_video_player\">\r\n<div>\r\n<div>\r\n<div class=\"close-video\">✕</div>\r\n</div>\r\n</div>\r\n<div class=\"media-player\">\r\n<div class=\"trv-video\">&nbsp;</div>\r\n<div class=\"trv-overlay\">&nbsp;</div>\r\n<div class=\"trv-media-controls\">\r\n<div class=\"trv-progress-bar\">\r\n<div class=\"trv-hover-progress\">&nbsp;</div>\r\n<div class=\"button-holder\">\r\n<div class=\"trv-progressBtn\">&nbsp;</div>\r\n</div>\r\n</div>\r\n<button class=\"togglePlayPause brpBtn sprite-background\" title=\"Play\" type=\"button\">Play</button><button class=\"volume brpBtn sprite-background\" title=\"volume\" type=\"button\">volume</button><span class=\"trv-currentTime\">00:00</span><span class=\"timeDivider\">/</span><span class=\"trv-duration\">01:01</span><span class=\"videoTitle\" title=\"VIETNAM-TEMPLES HLS\">VIETNAM-TEMPLES HLS</span><button class=\"BR brpBtn sprite-background\" title=\"Truvid.com\" type=\"button\">Truvid</button><button class=\"fullScreen brpBtn sprite-background\" title=\"Full Screen\" type=\"button\">fullScreen</button></div>\r\n<div class=\"trv-floating-menu\">&nbsp;</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n<p>Đại biểu Campuchia c&oacute;: Ph&oacute; Thủ tướng Campuchia, Bộ trưởng Quan hệ Quốc hội, Thượng viện v&agrave; Thanh tra, Chủ tịch Hội hữu nghị Campuchia - Việt Nam Men Sam An c&ugrave;ng c&aacute;c th&agrave;nh vi&ecirc;n Đo&agrave;n đại biểu cấp cao Vương quốc Campuchia đang thăm v&agrave; l&agrave;m việc tại Việt Nam, Đại sứ qu&aacute;n Campuchia tại Việt Nam.</p>\r\n<p>&nbsp;</p>\r\n<p><img class=\"medium-zoom-image\" src=\"https://hanoi.gov.vn/documents/2605401/40870649/a6fdab05a313604d3902.jpg?t=1656049781429\" alt=\"\"></p>\r\n<p><em>Đại biểu tham dự chương tr&igrave;nh</em></p>\r\n<p>Tham dự lễ kỷ niệm c&ograve;n c&oacute; c&aacute;c đồng ch&iacute; nguy&ecirc;n l&atilde;nh đạo Đảng, Nh&agrave; nước Việt Nam qua c&aacute;c thời kỳ; l&atilde;nh đạo c&aacute;c bộ, ban, ng&agrave;nh Trung ương; Ban li&ecirc;n lạc cựu chuy&ecirc;n gia - qu&acirc;n t&igrave;nh nguyện Việt Nam gi&uacute;p c&aacute;ch mạng Campuchia, th&agrave;nh vi&ecirc;n Ban Chỉ đạo quốc gia năm hữu nghị Việt Nam - Campuchia 2022; l&atilde;nh đạo Th&agrave;nh ủy, HĐND, UBND, Ủy ban MTTQ Việt Nam th&agrave;nh phố H&agrave; Nội; l&atilde;nh đạo c&aacute;c sở, ban, ng&agrave;nh, đo&agrave;n thể, Đảng bộ trực thuộc TP H&agrave; Nội; nh&acirc;n sĩ, tr&iacute; thức v&agrave; đại diện c&aacute;c tầng lớp Nh&acirc;n d&acirc;n Thủ đ&ocirc;.</p>\r\n<p>Lễ kỷ niệm 55 năm ng&agrave;y thiết lập quan hệ ngoại giao Việt Nam - Campuchia diễn ra trong kh&ocirc;ng kh&iacute; trang trọng v&agrave; thắm t&igrave;nh hữu nghị.</p>\r\n<p>Ph&aacute;t biểu tại lễ kỷ niệm, B&iacute; thư Trung ương Đảng, Ph&oacute; Thủ tướng Ch&iacute;nh phủ L&ecirc; Minh Kh&aacute;i n&ecirc;u r&otilde;, sự gần gũi về địa l&yacute; v&agrave; những n&eacute;t tương đồng về văn ho&aacute;, lịch sử l&agrave; nền tảng vững chắc để Việt Nam v&agrave; Campuchia vun đắp v&agrave; ph&aacute;t triển một trong những mối quan hệ c&oacute; truyền thống l&acirc;u đời ở khu vực Đ&ocirc;ng Nam &Aacute;.&nbsp; Điểm lại những dấu mốc đ&aacute;ng nhớ trong mối quan hệ hai nước, Ph&oacute; Thủ tướng L&ecirc; Minh Kh&aacute;i nhắc tới sự kiện ng&agrave;y 23/6/1967, trong bức điện gửi Quốc trưởng Campuchia Norodom Sihanouk, Chủ tịch Hồ Ch&iacute; Minh n&ecirc;u r&otilde;: \"Việc đặt quan hệ ngoại giao giữa hai nước ch&uacute;ng ta l&agrave; biểu hiện rực rỡ của mối t&igrave;nh hữu nghị th&acirc;n thiết v&agrave; đo&agrave;n kết chiến đấu. Đ&oacute; l&agrave; một sự kiện lịch sử trong quan hệ giữa Việt Nam v&agrave; Campuchia, một nh&acirc;n tố t&iacute;ch cực trong việc giữ g&igrave;n h&ograve;a b&igrave;nh ở Đ&ocirc;ng Dương v&agrave; Đ&ocirc;ng Nam &Aacute;\". Chỉ sau đ&oacute; một ng&agrave;y, ng&agrave;y 24/6/1967, Việt Nam v&agrave; Campuchia đ&atilde; ch&iacute;nh thức thiết lập quan hệ ngoại giao, mở ra một chương mới trong lịch sử hai nước.</p>\r\n<p>Ph&oacute; Thủ tướng L&ecirc; Minh Kh&aacute;i nhấn mạnh, sau khi thiết lập quan hệ ngoại giao, Việt Nam v&agrave; Campuchia đ&atilde; s&aacute;t c&aacute;nh b&ecirc;n nhau, gi&agrave;nh nhiều thắng lợi tr&ecirc;n cả mặt trận qu&acirc;n sự v&agrave; mặt trận ngoại giao, c&ugrave;ng gi&agrave;nh chiến thắng lịch sử v&agrave;o m&ugrave;a Xu&acirc;n năm 1975. T&igrave;nh đo&agrave;n kết, tương trợ lẫn nhau giữa hai d&acirc;n tộc c&agrave;ng được thể hiện r&otilde; n&eacute;t v&agrave; s&acirc;u sắc hơn khi hai nước kề vai s&aacute;t c&aacute;nh, chung tay lật đổ chế độ diệt chủng Pol Pot t&agrave;n bạo, gi&uacute;p hồi sinh đất nước Ch&ugrave;a Th&aacute;p. Năm 2005, l&atilde;nh đạo cao cấp hai nước đ&atilde; nhất tr&iacute; đề ra phương ch&acirc;m mới trong ph&aacute;t triển quan hệ song phương l&agrave; \"l&aacute;ng giềng tốt đẹp, hữu nghị truyền thống, hợp t&aacute;c to&agrave;n diện, bền vững l&acirc;u d&agrave;i\". Với định hướng đ&oacute;, quan hệ hai nước đ&atilde; kh&ocirc;ng ngừng được củng cố v&agrave; ph&aacute;t triển tr&ecirc;n mọi lĩnh vực, mang lại lợi &iacute;ch thiết thực cho nh&acirc;n d&acirc;n hai nước, đ&oacute;ng g&oacute;p t&iacute;ch cực v&agrave;o h&ograve;a b&igrave;nh, ổn định v&agrave; hợp t&aacute;c ở khu vực v&agrave; tr&ecirc;n thế giới. Dấu ấn gần nhất l&agrave; s&aacute;ng 20/6 vừa qua, tại khu vực bi&ecirc;n giới giữa huyện Lộc Ninh (B&igrave;nh Phước) v&agrave; huyện Memot (Tbong Khmum) đ&atilde; diễn ra c&aacute;c hoạt động trong khu&ocirc;n khổ Lễ kỷ niệm 45 năm \"H&agrave;nh tr&igrave;nh hướng tới lật đổ chế độ diệt chủng Pol Pot\" dưới sự đồng chủ tr&igrave; của Thủ tướng Ch&iacute;nh phủ Việt Nam Phạm Minh Ch&iacute;nh v&agrave; Thủ tướng Ch&iacute;nh phủ Campuchia Samdech Techo Hun Sen.&nbsp;&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img class=\"medium-zoom-image\" src=\"https://hanoi.gov.vn/documents/2605401/40870649/cb7ccd64c572062c5f63.jpg?t=1656049822454\" alt=\"\"></p>\r\n<p><em>Ph&oacute; Thủ tướng Ch&iacute;nh phủ L&ecirc; Minh Kh&aacute;i ph&aacute;t biểu tại Lễ kỷ niệm</em></p>\r\n<p>Theo Ph&oacute; Thủ tướng L&ecirc; Minh Kh&aacute;i, quan hệ ch&iacute;nh trị giữa hai nước tiếp tục ph&aacute;t triển tốt đẹp. Quan hệ hợp t&aacute;c giữa c&aacute;c bộ, ng&agrave;nh, địa phương hai nước c&agrave;ng chặt chẽ v&agrave; thực chất. C&aacute;c hoạt động ngoại giao nh&acirc;n d&acirc;n, nhất l&agrave; tại c&aacute;c tỉnh bi&ecirc;n giới, diễn ra s&ocirc;i nổi v&agrave; rộng khắp. C&ugrave;ng với đ&oacute;, hợp t&aacute;c kinh tế, thương mại, đầu tư ph&aacute;t triển nhanh ch&oacute;ng, đạt được những kết quả đ&aacute;ng kh&iacute;ch lệ. C&aacute;c lĩnh vực hợp t&aacute;c kh&aacute;c như gi&aacute;o dục - đ&agrave;o tạo, y tế, văn ho&aacute; - thể thao... tiếp tục được quan t&acirc;m th&uacute;c đẩy.</p>\r\n<p>&nbsp;</p>\r\n<div id=\"vtzone63\" class=\"vmcadszone\">\r\n<div id=\"yomedia-passback-c9f647d5c88e46c39004b88badfad27c-1656124720533\"></div>\r\n</div>\r\n<p>B&ecirc;n cạnh đ&oacute;, hợp t&aacute;c tr&ecirc;n lĩnh vực an ninh, quốc ph&ograve;ng tiếp tục được tăng cường, l&agrave; trụ cột trong quan hệ hai nước. Hai b&ecirc;n ki&ecirc;n định nguy&ecirc;n tắc kh&ocirc;ng cho ph&eacute;p bất kỳ lực lượng th&ugrave; địch n&agrave;o sử dụng l&atilde;nh thổ của nước n&agrave;y g&acirc;y phương hại cho an ninh v&agrave; lợi &iacute;ch của nước kia. Tr&ecirc;n cơ sở c&aacute;c hiệp ước, hiệp định v&agrave; thỏa thuận của l&atilde;nh đạo cấp cao hai nước, c&aacute;c cơ quan chức năng, ch&iacute;nh quyền v&agrave; nh&acirc;n d&acirc;n địa phương bi&ecirc;n giới hai b&ecirc;n nỗ lực x&acirc;y dựng đường bi&ecirc;n giới chung ho&agrave; b&igrave;nh, hữu nghị, hợp t&aacute;c v&agrave; ph&aacute;t triển. L&atilde;nh đạo hai nước cũng cam kết tiếp tục tạo điều kiện thuận lợi cho kiều d&acirc;n hai nước sinh sống tr&ecirc;n l&atilde;nh thổ của nhau, ph&ugrave; hợp với luật ph&aacute;p mỗi nước v&agrave; tinh thần quan hệ l&aacute;ng giềng hữu nghị. Trong qu&aacute; tr&igrave;nh triển khai ch&iacute;nh s&aacute;ch đối ngoại v&agrave; hội nhập quốc tế, hai nước cũng đ&atilde; phối hợp tốt tại c&aacute;c diễn đ&agrave;n quốc tế, khu vực v&agrave; tiểu v&ugrave;ng, c&ugrave;ng nhau đ&oacute;ng g&oacute;p t&iacute;ch cực v&agrave;o ho&agrave; b&igrave;nh, ổn định, hợp t&aacute;c v&agrave; ph&aacute;t triển của Cộng đồng ASEAN cũng như ở khu vực v&agrave; tr&ecirc;n thế giới.</p>\r\n<p>Ph&oacute; Thủ tướng n&ecirc;u r&otilde;, l&agrave; nước l&aacute;ng giềng của Campuchia, Việt Nam vui mừng chứng kiến những th&agrave;nh tựu rực rỡ m&agrave; đất nước Ch&ugrave;a Th&aacute;p đ&atilde; đạt được, đồng thời, tin tưởng vững chắc rằng, nh&acirc;n d&acirc;n Campuchia anh em sẽ tiếp tục gi&agrave;nh được những thắng lợi mới, to lớn hơn nữa trong sự nghiệp x&acirc;y dựng th&agrave;nh c&ocirc;ng Vương quốc Campuchia ho&agrave; b&igrave;nh, ổn định, ph&aacute;t triển thịnh vượng, c&oacute; vai tr&ograve; v&agrave; vị thế ng&agrave;y c&agrave;ng cao trong khu vực v&agrave; tr&ecirc;n trường quốc tế.&nbsp;</p>\r\n<p>Ph&oacute; Thủ tướng tin tưởng, d&ugrave; bối cảnh t&igrave;nh h&igrave;nh thế giới v&agrave; khu vực c&oacute; nhiều chuyển biến, nhưng hai nước, hai d&acirc;n tộc Việt Nam - Campuchia vẫn sẽ tiếp tục tăng cường hơn nữa hợp t&aacute;c to&agrave;n diện, vun đắp mối quan hệ đặc biệt ng&agrave;y c&agrave;ng ph&aacute;t triển tốt đẹp, m&atilde;i m&atilde;i xanh tươi, đời đời bền vững, v&igrave; lợi &iacute;ch của nh&acirc;n d&acirc;n Campuchia v&agrave; Việt Nam. Ph&oacute; Thủ tướng nhấn mạnh, đ&acirc;y kh&ocirc;ng chỉ l&agrave; tr&aacute;ch nhiệm cao cả, bổn phận của thế hệ h&ocirc;m nay m&agrave; c&ograve;n l&agrave; của c&aacute;c thế hệ l&atilde;nh đạo v&agrave; nh&acirc;n d&acirc;n hai nước mai sau.</p>\r\n<p>&nbsp;</p>\r\n<p>Chủ trương nhất qu&aacute;n của Việt Nam l&agrave; ủng hộ một nước Campuchia độc lập, h&ograve;a b&igrave;nh, trung lập v&agrave; ph&aacute;t triển, Ph&oacute; Thủ tướng khẳng định, Đảng, Nh&agrave; nước v&agrave; Nh&acirc;n d&acirc;n Việt Nam lu&ocirc;n hết sức coi trọng v&agrave; gi&agrave;nh ưu ti&ecirc;n cao cho mối quan hệ \"l&aacute;ng giềng tốt đẹp, hữu nghị truyền thống, hợp t&aacute;c to&agrave;n diện, bền vững l&acirc;u d&agrave;i\" với Campuchia. Ph&oacute; Thủ tướng nhấn mạnh, đ&acirc;y cũng l&agrave; mong muốn v&agrave; quyết t&acirc;m của l&atilde;nh đạo cấp cao hai nước, Ph&oacute; Thủ tướng tr&iacute;ch dẫn lời Tổng B&iacute; thư Nguyễn Ph&uacute; Trọng ph&aacute;t biểu nh&acirc;n chuyến thăm cấp Nh&agrave; nước tới Campuchia th&aacute;ng 7-2017: \"Cho d&ugrave; t&igrave;nh h&igrave;nh thế giới v&agrave; khu vực c&oacute; những đổi thay, song trước sau như một, Việt Nam lu&ocirc;n coi trọng v&agrave; sẽ l&agrave;m hết sức m&igrave;nh c&ugrave;ng với Campuchia giữ g&igrave;n, vun đắp mối quan hệ Việt Nam - Campuchia m&atilde;i m&atilde;i xanh tươi, đời đời bền vững\".</p>\r\n<p><img class=\"medium-zoom-image\" src=\"https://hanoi.gov.vn/documents/2605401/40870649/688d63026b14a84af105.jpg?t=1656049826529\" alt=\"\"></p>\r\n<p><em>Ph&oacute; Thủ tướng Campuchia Men Sam An ph&aacute;t biểu tại buổi lễ</em></p>\r\n<p>Tại Lễ kỷ niệm, Ph&oacute; Thủ tướng, Bộ trưởng Quan hệ Quốc hội, Thượng viện v&agrave; Thanh tra, Chủ tịch Hội hữu nghị Campuchia - Việt Nam Men Sam An b&agrave;y tỏ vinh dự tham dự Lễ kỷ niệm 55 năm Ng&agrave;y thiết lập quan hệ ngoại giao Việt Nam - Campuchia, nhấn mạnh 55 năm qua, hai nước lu&ocirc;n lu&ocirc;n l&agrave; những người bạn th&acirc;n thiết, người l&aacute;ng giềng tốt, ch&acirc;n th&agrave;nh, đồng thời, b&agrave;y tỏ tin tưởng mối quan hệ hữu nghị đo&agrave;n kết hợp t&aacute;c giữa hai nước sẽ ng&agrave;y c&agrave;ng bền vững, gắn b&oacute; v&agrave; s&acirc;u sắc hơn nữa.&nbsp;</p>\r\n<p>Ph&oacute; Thủ tướng Campuchia Men Sam An cho biết, ng&agrave;y nay, hai nước đ&atilde; bước sang trang sử mới nhờ gi&agrave;nh được ho&agrave; b&igrave;nh, to&agrave;n vẹn l&atilde;nh thổ, tiến bộ v&agrave; ph&aacute;t triển tr&ecirc;n to&agrave;n bộ c&aacute;c lĩnh vực, nhất l&agrave; trong lĩnh vực ngoại giao, quốc ph&ograve;ng, thương mại, đầu tư v&agrave; du lịch. Ph&oacute; Thủ tướng Campuchia nhấn mạnh, một trong những th&agrave;nh quả lịch sử l&agrave; hai nước đ&atilde; giải quyết vấn đề bi&ecirc;n giới đất liền, khi cơ bản ho&agrave;n th&agrave;nh c&ocirc;ng t&aacute;c ph&acirc;n giới, cắm mốc, c&ugrave;ng nhau g&igrave;n giữ v&agrave; x&acirc;y dựng đường bi&ecirc;n giới ho&agrave; b&igrave;nh, hữu nghị, hợp t&aacute;c v&agrave; ph&aacute;t triển, mang lại lợi &iacute;ch thiết thực. Ph&oacute; Thủ tướng Campuchia Men Sam An khẳng định, Campuchia lu&ocirc;n ghi nhớ v&agrave; biết ơn Đảng, Nh&agrave; nước, Qu&acirc;n đội v&agrave; Nh&acirc;n d&acirc;n Việt Nam đ&atilde; lu&ocirc;n lu&ocirc;n gi&uacute;p đỡ, đồng cam cộng khổ với Campuchia, nhấn mạnh sự ủng hộ v&agrave; gi&uacute;p đỡ của Việt Nam đ&atilde; g&oacute;p phần đưa Campuchia ph&aacute;t triển từ một d&acirc;n tộc đau khổ v&agrave; chia ly do chiến tranh, trở th&agrave;nh một quốc gia thống nhất, với ho&agrave; b&igrave;nh trọn vẹn v&agrave; ph&aacute;t triển tr&ecirc;n mọi lĩnh vực.&nbsp;</p>\r\n<p>Ph&oacute; Thủ tướng Campuchia Men Sam An khẳng định, sự kiện kỷ niệm lần n&agrave;y ch&iacute;nh l&agrave; minh chứng cho sự củng cố v&agrave; vun đắp mối quan hệ đo&agrave;n kết, hữu nghị, hợp t&aacute;c tốt đẹp giữa nh&acirc;n d&acirc;n hai nước Việt Nam - Campuchia, ng&agrave;y c&agrave;ng mở rộng, bền vững, v&agrave; m&atilde;i m&atilde;i trường tồn.&nbsp;</p>\r\n<p>Đại biểu hai nước đ&atilde; thưởng thức c&aacute;c tiết mục văn nghệ mang đậm bản sắc văn h&oacute;a của hai d&acirc;n tộc; c&ugrave;ng lắng nghe những chia sẻ của cựu qu&acirc;n t&igrave;nh nguyện Việt Nam tại Campuchia v&agrave; đại diện thế hệ trẻ hai nước. C&aacute;c ph&aacute;t biểu đều khẳng định, sẽ tiếp tục vun đắp t&igrave;nh hữu nghị giữa hai d&acirc;n tộc ng&agrave;y c&agrave;ng đơm hoa kết tr&aacute;i, mang lại lợi &iacute;ch thiết thực cho nh&acirc;n d&acirc;n hai nước, v&igrave; h&ograve;a b&igrave;nh, ổn định v&agrave; ph&aacute;t triển ở khu vực v&agrave; thế giới.</p>', 218, 3, 7, 1, '2022-11-22 19:39:57', '2023-02-08 00:36:13'),
(215, 'Nga chỉ trích EU trao tư cách ứng viên cho Ukraine', 'nga-chi-trich-eu-trao-tu-cach-ung-vien-cho-ukraine', 'Nga cảnh báo quyết định trao tư cách ứng viên cho Ukraine của EU sẽ gây ra những hậu quả tiêu cực và \"nô dịch\" các nước láng giềng.', '<div id=\"divfirst\">\r\n<p class=\"Normal\">\"Với quyết định trao cho&nbsp;<a href=\"https://vnexpress.net/chu-de/ukraine-692\" rel=\"dofollow\">Ukraine</a>&nbsp;v&agrave; Moldova tư c&aacute;ch l&agrave; c&aacute;c nước ứng cử vi&ecirc;n, Li&ecirc;n minh ch&acirc;u &Acirc;u (<a href=\"https://vnexpress.net/chu-de/lien-minh-chau-au-eu-4558\" rel=\"dofollow\">EU)</a>&nbsp;khẳng định rằng họ tiếp tục lợi dụng c&aacute;c nước SNG tr&ecirc;n phương diện địa ch&iacute;nh trị để kiềm chế&nbsp;<a href=\"https://vnexpress.net/chu-de/nga-710\" rel=\"dofollow\">Nga</a>. Họ kh&ocirc;ng t&iacute;nh tới hậu quả ti&ecirc;u cực của động th&aacute;i như vậy\", ph&aacute;t ng&ocirc;n vi&ecirc;n Bộ Ngoại giao Nga Maria Zakharova cho biết h&ocirc;m 24/6. SNG l&agrave; Cộng đồng c&aacute;c Quốc gia Độc lập gồm c&aacute;c nước từng thuộc Li&ecirc;n X&ocirc;.</p>\r\n<p class=\"Normal\">Ph&aacute;t ng&ocirc;n vi&ecirc;n Bộ Ngoại giao Nga c&aacute;o buộc bằng c&aacute;c mở rộng sang Ukraine v&agrave; Moldova, EU đ&atilde; \"hy sinh những l&yacute; tưởng d&acirc;n chủ của m&igrave;nh\" cho \"sự b&agrave;nh trướng kh&ocirc;ng giới hạn v&agrave; n&ocirc; dịch về kinh tế, ch&iacute;nh trị với c&aacute;c nước l&aacute;ng giềng\".</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture data-inimage=\"done\"><source srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/25/afp-com-20220615-partners-033-9684-3349-1656117440.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=LNE6kMDhsi1L8oTJm7dSLg 1x, https://i1-vnexpress.vnecdn.net/2022/06/25/afp-com-20220615-partners-033-9684-3349-1656117440.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=GRA1qpZuRddDocjh453Zgg 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/25/afp-com-20220615-partners-033-9684-3349-1656117440.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=IZt2uHAMJAedBWYbwsa7ZA 2x\" data-srcset=\"https://i1-vnexpress.vnecdn.net/2022/06/25/afp-com-20220615-partners-033-9684-3349-1656117440.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=LNE6kMDhsi1L8oTJm7dSLg 1x, https://i1-vnexpress.vnecdn.net/2022/06/25/afp-com-20220615-partners-033-9684-3349-1656117440.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=GRA1qpZuRddDocjh453Zgg 1.5x, https://i1-vnexpress.vnecdn.net/2022/06/25/afp-com-20220615-partners-033-9684-3349-1656117440.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=IZt2uHAMJAedBWYbwsa7ZA 2x\"><img class=\"lazy lazied\" src=\"https://i1-vnexpress.vnecdn.net/2022/06/25/afp-com-20220615-partners-033-9684-3349-1656117440.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=LNE6kMDhsi1L8oTJm7dSLg\" alt=\"Người ph&aacute;t ng&ocirc;n Bộ Ngoại giao Nga Maria Zakharova tại St.Petersburg h&ocirc;m 15/6. Ảnh: AFP.\" loading=\"lazy\" data-src=\"https://i1-vnexpress.vnecdn.net/2022/06/25/afp-com-20220615-partners-033-9684-3349-1656117440.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=LNE6kMDhsi1L8oTJm7dSLg\" data-ll-status=\"loaded\" data-adbro-processed=\"true\"></picture>\r\n<div class=\"embed-container-ads\">\r\n<div id=\"sis_inimage\" data-google-query-id=\"CJLVodjJx_gCFU-ClgodawALuA\">\r\n<div id=\"google_ads_iframe_/27973503/Vnexpress/Desktop/Inimage/Thegioi/Thegioi.detail_0__container__\"><iframe id=\"google_ads_iframe_/27973503/Vnexpress/Desktop/Inimage/Thegioi/Thegioi.detail_0\" tabindex=\"0\" title=\"3rd party ad content\" role=\"region\" name=\"google_ads_iframe_/27973503/Vnexpress/Desktop/Inimage/Thegioi/Thegioi.detail_0\" width=\"1\" height=\"1\" frameborder=\"0\" marginwidth=\"0\" marginheight=\"0\" scrolling=\"no\" aria-label=\"Advertisement\" data-google-container-id=\"4\" data-load-complete=\"true\"></iframe></div>\r\n</div>\r\n</div>\r\n</div>\r\n<figcaption>\r\n<p class=\"Image\">Người ph&aacute;t ng&ocirc;n Bộ Ngoại giao Nga Maria Zakharova tại St.Petersburg h&ocirc;m 15/6. Ảnh:&nbsp;<em>AFP.</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Người ph&aacute;t ng&ocirc;n Điện Kremlin Dmitry Peskov trước đ&oacute; cũng b&igrave;nh luận về việc EU trao tư c&aacute;ch ứng vi&ecirc;n cho Ukraine, gọi đ&acirc;y l&agrave; \"vấn đề nội bộ của ch&acirc;u &Acirc;u\". \"Điều quan trọng l&agrave; tất cả những qu&aacute; tr&igrave;nh n&agrave;y kh&ocirc;ng g&acirc;y th&ecirc;m vấn đề cho ch&uacute;ng t&ocirc;i v&agrave; cho quan hệ của ch&uacute;ng t&ocirc;i với c&aacute;c quốc gia n&agrave;y\", &ocirc;ng Peskov n&oacute;i.</p>\r\n</div>\r\n<div id=\"admbackground\" data-set=\"dfp\"></div>\r\n<div id=\"divend\">\r\n<p class=\"Normal\">Bộ trưởng Ngoại giao Nga Sergey Lavrov cũng nhận định việc Ukraine v&agrave; Moldova muốn gia nhập EU kh&ocirc;ng g&acirc;y ra rủi ro n&agrave;o cho Moskva v&igrave; khối n&agrave;y kh&ocirc;ng phải li&ecirc;n minh qu&acirc;n sự. Tuy nhi&ecirc;n, &ocirc;ng Lavrov c&aacute;o buộc \"EU v&agrave; NATO đang muốn g&acirc;y chiến\" với Nga.</p>\r\n<p class=\"Normal\">EU h&ocirc;m 23/6 quyết định cấp tư c&aacute;ch ứng vi&ecirc;n cho Ukraine v&agrave; nước l&aacute;ng giềng Moldova trong hội nghị thượng đỉnh tại Brussels, Bỉ. Tổng thống Ukraine Zelensky ca ngợi đ&acirc;y l&agrave; \"thời khắc lịch sử c&oacute; một kh&ocirc;ng hai trong quan hệ Ukraine - EU, khởi đầu cho trang sử mới của ch&acirc;u &Acirc;u\".</p>\r\n<p class=\"Normal\">Sau khi được ph&ecirc; duyệt tư c&aacute;ch ứng vi&ecirc;n, Ukraine sẽ phải nỗ lực để đ&aacute;p ứng Ti&ecirc;u ch&iacute; Copenhagen được EU đề ra đối với c&aacute;c nước xin gia nhập, về nền kinh tế thị trường tự do cũng như c&aacute;c gi&aacute; trị về d&acirc;n chủ, nh&acirc;n quyền.</p>\r\n<p class=\"Normal\">Tổng thống Ukraine đ&atilde; nhiều lần th&uacute;c giục EU cho ph&eacute;p Ukraine gia nhập ngay lập tức theo c&aacute;i &ocirc;ng m&ocirc; tả l&agrave; \"thủ tục đặc biệt mới\", song kh&ocirc;ng n&ecirc;u chi tiết. L&atilde;nh đạo Ph&aacute;p, Đức, T&acirc;y Ban Nha v&agrave; H&agrave; Lan hồi th&aacute;ng 3 đ&atilde; b&aacute;c bỏ lời k&ecirc;u gọi về nhanh ch&oacute;ng kết nạp.</p>\r\n</div>', 218, 2, 16, 1, '2022-11-24 19:41:32', '2023-02-08 01:33:19'),
(216, 'Alcaraz tự nhận không có cửa vô địch Wimbledon', 'alcaraz-tu-nhan-khong-co-cua-vo-dich-wimbledon', 'ANH-Là hạt giống số 5 của Wimbledon năm nay, nhưng Carlos Alcaraz thấy bản thân thiếu nhiều kỹ năng để chinh phục mặt sân cỏ.', '<p class=\"Normal\">\"Nhiều người n&oacute;i rằng t&ocirc;i l&agrave; ứng vi&ecirc;n v&ocirc; địch Wimbledon, nhưng kh&ocirc;ng phải vậy\",&nbsp;<a href=\"https://vnexpress.net/the-thao/djokovic-alcaraz-gia-truoc-tuoi-4460687.html\" rel=\"dofollow\">Alcaraz</a>&nbsp;n&oacute;i sau trận thua 4-6, 2-6 dưới tay Frances Tiafoe ở giải biểu diễn Hurlingham ng&agrave;y 23/6. \"T&ocirc;i vẫn đang học c&aacute;ch di chuyển ở s&acirc;n cỏ. C&oacute; rất nhiều tay vợt chơi bề mặt n&agrave;y tốt hơn t&ocirc;i, như Djokovic, Nadal, Berrettini... Kinh nghiệm của t&ocirc;i ở Wimbledon rất &iacute;t. T&ocirc;i đang cố gắng cải thiện bản th&acirc;n ở đ&acirc;y v&agrave; điều n&agrave;y cần th&ecirc;m thời gian\".</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture data-inimage=\"done\"><source srcset=\"https://i1-thethao.vnecdn.net/2022/06/24/1-8637-1656081397.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=FjIfaUBwOOwGFKeoZ5PIAg 1x, https://i1-thethao.vnecdn.net/2022/06/24/1-8637-1656081397.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=MABS_iLhRT1Lyzj4I9hBLg 1.5x, https://i1-thethao.vnecdn.net/2022/06/24/1-8637-1656081397.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=r1vn0YQvS7FsS9sf5OY-rA 2x\" data-srcset=\"https://i1-thethao.vnecdn.net/2022/06/24/1-8637-1656081397.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=FjIfaUBwOOwGFKeoZ5PIAg 1x, https://i1-thethao.vnecdn.net/2022/06/24/1-8637-1656081397.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=MABS_iLhRT1Lyzj4I9hBLg 1.5x, https://i1-thethao.vnecdn.net/2022/06/24/1-8637-1656081397.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=r1vn0YQvS7FsS9sf5OY-rA 2x\"><img class=\"lazy lazied\" src=\"https://i1-thethao.vnecdn.net/2022/06/24/1-8637-1656081397.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=FjIfaUBwOOwGFKeoZ5PIAg\" alt=\"Alcaraz v&agrave;o tứ kết Mỹ Mở rộng v&agrave; Roland Garros trong ba Grand Slam gần nhất. Ảnh: Metro\" loading=\"lazy\" data-src=\"https://i1-thethao.vnecdn.net/2022/06/24/1-8637-1656081397.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=FjIfaUBwOOwGFKeoZ5PIAg\" data-ll-status=\"loaded\" data-adbro-processed=\"true\"></picture>\r\n<div class=\"embed-container-ads\">\r\n<div id=\"sis_inimage\" data-google-query-id=\"CMTCnMjKx_gCFY2DlgodYOwB3g\">\r\n<div id=\"google_ads_iframe_/27973503/Vnexpress/Desktop/Inimage/Thethao/Thethao.tennis.detail_0__container__\"><iframe id=\"google_ads_iframe_/27973503/Vnexpress/Desktop/Inimage/Thethao/Thethao.tennis.detail_0\" tabindex=\"0\" title=\"3rd party ad content\" role=\"region\" name=\"google_ads_iframe_/27973503/Vnexpress/Desktop/Inimage/Thethao/Thethao.tennis.detail_0\" width=\"1\" height=\"1\" frameborder=\"0\" marginwidth=\"0\" marginheight=\"0\" scrolling=\"no\" aria-label=\"Advertisement\" data-google-container-id=\"4\" data-load-complete=\"true\"></iframe></div>\r\n</div>\r\n</div>\r\n</div>\r\n<figcaption>\r\n<p class=\"Image\">Alcaraz v&agrave;o tứ kết Mỹ Mở rộng v&agrave; Roland Garros trong ba Grand Slam gần nhất. Ảnh:&nbsp;<em>Metro</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Alcaraz tới Wimbledon với chấn thương nhẹ ở khuỷu tay phải. Anh đ&atilde; bỏ giải đấu ở Queen&rsquo;s Club v&igrave; chấn thương n&agrave;y v&agrave; mới tập luyện trở lại tuần trước. Tay vợt 19 tuổi khởi đầu m&ugrave;a giải ấn tượng, đoạt hai Masters 1000 ở Miami v&agrave; Madrid, c&ugrave;ng danh hiệu tại Barcelona v&agrave; Rio de Janiero. Th&aacute;ng trước anh v&agrave;o tứ kết Roland Garros.</p>\r\n<p class=\"Normal\">\"T&ocirc;i cảm thấy ổn sau khi b&igrave;nh phục chấn thương\", Alcaraz n&oacute;i th&ecirc;m về qu&aacute; tr&igrave;nh chuẩn bị cho Wimbledon. \"Điều quan trọng nhất để chơi tốt ở Wimbledon l&agrave; di chuyển. T&ocirc;i đ&atilde; cố gắng chơi quyết liệt khi đấu Tiafoe, cảm gi&aacute;c di chuyển tốt hơn. Nhưng để tiến s&acirc;u ở giải đấu n&agrave;y rất kh&oacute; v&igrave; c&oacute; nhiều đối thủ xuất sắc ở mặt s&acirc;n n&agrave;y\".</p>\r\n<p class=\"Normal\">Alcaraz sẽ chơi th&ecirc;m một trận ở Hurlingham, gặp Casper Ruud, trước khi chờ ng&agrave;y ra qu&acirc;n Wimbledon với tư c&aacute;ch hạt giống số năm. Theo kết quả ph&acirc;n nh&aacute;nh, Alcaraz c&oacute; thể gặp&nbsp;<a href=\"https://vnexpress.net/chu-de/novak-djokovic-352\" rel=\"dofollow\">Novak Djokovic</a>&nbsp;ở tứ kết. Một năm trước Alcaraz dự Grand Slam s&acirc;n cỏ nhờ v&eacute; đặc c&aacute;ch v&agrave; dừng bước ở v&ograve;ng hai, thua trắng&nbsp;<a href=\"https://vnexpress.net/chu-de/daniil-medvedev-198\" rel=\"dofollow\">Daniil Medvedev</a>&nbsp;ba set.</p>\r\n<p class=\"Normal\">Medvedev vừa thất bại trong chiến dịch bảo vệ danh hiệu Mallorca Open, thua Roberto Bautista Agut 3-6, 2-6 ở tứ kết. Tay vợt 26 tuổi trước đ&oacute; thua hai trận chung kết li&ecirc;n tiếp tại Libema Open v&agrave; Halle Open. Medvedev đang đứng số một thế giới nhưng bị&nbsp;<a href=\"https://vnexpress.net/the-thao/wimbledon-cam-cac-tay-vot-nga-4453882.html\" rel=\"dofollow\">cấm dự</a>&nbsp;Wimbledon.</p>\r\n<p class=\"Normal\">\"T&ocirc;i sẽ t&igrave;m chỗ n&agrave;o đ&oacute; nghỉ ngơi sau nhiều tuần thi đấu li&ecirc;n tiếp\", Medvedev n&oacute;i về qu&atilde;ng nghỉ Wimbledon trong cuộc họp b&aacute;o ở Mallorca. \"Với t&ocirc;i, Djokovic l&agrave; ứng vi&ecirc;n v&ocirc; địch số một của Wimbledon. Anh ấy lu&ocirc;n chơi tốt ở s&acirc;n cỏ v&agrave; thực tế đ&atilde; thống trị Wimbledon nhiều năm qua. T&ocirc;i chưa xem Sampras hay Borg thi đấu nhưng c&oacute; lẽ Djokovic l&agrave; người hay nhất ở s&acirc;n cỏ. Nadal th&igrave; c&oacute; thể v&ocirc; địch bất cứ giải n&agrave;o, c&ograve;n Berrettini cũng đang chơi ở đẳng cấp cao\".</p>\r\n<p class=\"Normal\">B&ecirc;n cạnh Medvedev v&agrave; c&aacute;c tay vợt Nga, Belarus kh&aacute;c, Wimbledon c&ograve;n thiếu&nbsp;<a href=\"https://vnexpress.net/chu-de/alexander-zverev-165\" rel=\"dofollow\">Alexander Zverev&nbsp;</a>v&agrave; Gael Monfils v&igrave; chấn thương.&nbsp;<a href=\"https://vnexpress.net/chu-de/novak-djokovic-352\" rel=\"dofollow\">Djokovic&nbsp;</a>v&agrave;&nbsp;<a href=\"https://vnexpress.net/chu-de/rafael-nadal-370\" rel=\"dofollow\">Nadal&nbsp;</a>v&igrave; thế l&agrave; hai hạt giống h&agrave;ng đầu, tr&aacute;nh được nhau cho tới chung kết.</p>', 218, 1, 0, 1, '2022-11-21 19:45:17', '2022-11-21 19:45:17'),
(217, 'Ngày mới với tin tức sức khỏe', 'ngay-moi-voi-tin-tuc-suc-khoe', 'Giảm nhanh mức cholesterol cao bằng thực phẩm này', '<div id=\"chapeau\" class=\"sapo cms-desc\">\r\n<p>\'Chuy&ecirc;n gia cho biết c&oacute; loại gia vị chứa chất chống oxy h&oacute;a \'mạnh mẽ\' c&oacute; thể gi&uacute;p giảm mức cholesterol trong m&aacute;u\'.&nbsp;<a title=\"Ng&agrave;y mới với tin tức sức khỏe: L&uacute;c n&agrave;o n&ecirc;n uống nước lạnh?\" href=\"https://thanhnien.vn/ngay-moi-voi-tin-tuc-suc-khoe-luc-nao-nen-uong-nuoc-lanh-post1544686.html\" target=\"_blank\" rel=\"noopener\">H&atilde;y bắt đầu ng&agrave;y mới với tin tức sức khỏe</a><em>&nbsp;để xem th&ecirc;m nội dung b&agrave;i viết n&agrave;y!</em></p>\r\n</div>\r\n<div id=\"abdf\" class=\"details__content\">\r\n<div id=\"abody\" class=\"cms-body detail\">\r\n<p><a title=\"Ng&agrave;y mới với tin tức sức khỏe: Ăn uống thế n&agrave;o để mức cholesterol kh&ocirc;ng tăng cao?\" href=\"https://thanhnien.vn/ngay-moi-voi-tin-tuc-suc-khoe-an-uong-the-nao-de-muc-cholesterol-khong-tang-cao-post1544423.html\" target=\"_blank\" rel=\"noopener\">Bắt đầu ng&agrave;y mới với tin tức sức khỏe</a>, bạn đọc c&ograve;n c&oacute; thể xem th&ecirc;m c&aacute;c b&agrave;i viết:&nbsp;<em><strong>L&agrave;m sao để biết số c&acirc;n giảm được l&agrave; mỡ thừa hay nước?;&nbsp;</strong>C&aacute;ch ph&acirc;n biệt cơn&nbsp;<a href=\"https://thanhnien.vn/chuyen-gia-giai-thich-uong-nuoc-am-hay-nuoc-lanh-tot-hon-post1544453.html\">sốt</a>&nbsp;l&agrave; do vi khuẩn hay virus<strong>; Người phụ nữ bị&nbsp;<a title=\"sư tử\" href=\"https://thanhnien.vn/su-tu/\" target=\"_blank\" rel=\"noopener noreferrer\">sư tử</a>&nbsp;vồ do leo r&agrave;o v&agrave;o vườn th&uacute; l&uacute;c say rượu...</strong></em></p>\r\n<h2>Bị&nbsp;<a title=\"C&aacute;ch ăn đặc biệt giảm đến 52% mức cholesterol xấu chỉ sau v&agrave;i tuần\" href=\"https://thanhnien.vn/cach-an-dac-biet-giam-den-52-muc-cholesterol-xau-chi-sau-vai-tuan-post1543029.html\" target=\"_blank\" rel=\"noopener\">cholesterol</a>&nbsp;cao, h&atilde;y th&ecirc;m thứ n&agrave;y v&agrave;o bữa ăn của bạn</h2>\r\n<p>Chuy&ecirc;n gia cho biết c&oacute; loại gia vị chứa chất chống oxy h&oacute;a \'mạnh mẽ\' c&oacute; thể gi&uacute;p giảm mức cholesterol trong m&aacute;u.</p>\r\n<p>Mức cholesterol cao, nếu kh&ocirc;ng được điều trị, c&oacute; thể g&acirc;y hậu quả nghi&ecirc;m trọng, bởi cholesterol c&oacute; thể t&iacute;ch tụ trong mạch m&aacute;u, dẫn đến nguy cơ mắc bệnh tim v&agrave; đột quỵ. Do đ&oacute;, duy tr&igrave; cholesterol ở mức thấp l&agrave; cực kỳ quan trọng.</p>\r\n<p>C&oacute; những thực phẩm g&acirc;y ra mức cholesterol cao, nhưng cũng c&oacute; những thực phẩm c&oacute; t&aacute;c dụng ngược lại.</p>\r\n<p>B&aacute;c sĩ Deborah Lee, l&agrave;m việc tại nh&agrave; thuốc trực tuyến Dr Fox (Anh), giải th&iacute;ch: Tỏi c&oacute; chứa allicin, một chất chống oxy h&oacute;a mạnh c&oacute; đặc t&iacute;nh hạ lipid m&aacute;u. Ăn 0,5 - 1 t&eacute;p tỏi mỗi ng&agrave;y gi&uacute;p giảm đến 10% mức cholesterol.&nbsp;<em>Bạn đọc c&oacute; thể xem th&ecirc;m nội dung</em>&nbsp;<em>b&agrave;i viết n&agrave;y tr&ecirc;n&nbsp;<a href=\"https://thanhnien.vn/suc-khoe/\" target=\"_blank\" rel=\"noopener\"><strong>trang sức khỏe</strong></a><strong>&nbsp;</strong>ng&agrave;y 27.1</em>.<em>2023.</em></p>\r\n<h2>L&agrave;m sao để biết số&nbsp;<a title=\"Ng&agrave;y mới với tin tức sức khỏe: Giờ ăn tối gi&uacute;p giảm nguy cơ bệnh tiểu đường\" href=\"https://thanhnien.vn/ngay-moi-voi-tin-tuc-suc-khoe-gio-an-toi-giup-giam-nguy-co-benh-tieu-duong-post1535633.html\" target=\"_blank\" rel=\"noopener\">c&acirc;n giảm</a>&nbsp;được l&agrave; mỡ thừa hay nước?</h2>\r\n<p>C&oacute; nhiều nguy&ecirc;n nh&acirc;n khiến mọi người muốn giảm c&acirc;n, từ tăng cường sức khỏe, cải thiện v&oacute;c d&aacute;ng hay mặc vừa v&agrave;i bộ đồ đẹp. Nhưng khi giảm c&acirc;n th&agrave;nh c&ocirc;ng, kh&ocirc;ng phải l&uacute;c n&agrave;o trọng lượng giảm được cũng l&agrave; mỡ thừa.</p>\r\n<p>Trung t&acirc;m Kiểm so&aacute;t v&agrave; Ph&ograve;ng ngừa Dịch bệnh (CDC) Mỹ cho rằng chỉ cần giảm từ 5 đến 10% tổng trọng lượng cơ thể th&igrave; sẽ mang lại rất nhiều lợi &iacute;ch sức khỏe, từ giảm nồng độ cholesterol, hạ huyết &aacute;p đến cải thiện đường huyết.</p>\r\n<table class=\"picture\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td class=\"pic\"><img class=\"cms-photo lazyloaded\" src=\"https://image.thanhnien.vn/w2048/Uploaded/2023/unvjxbf/2023_01_20/6-giam-can-shutterstock-9068.jpg\" alt=\"Ng&agrave;y mới với tin tức sức khỏe: Giảm nhanh mức cholesterol cao bằng thực phẩm n&agrave;y - ảnh 2\" data-image-id=\"4123272\" data-width=\"1024\" data-height=\"768\" data-src=\"https://image.thanhnien.vn/w2048/Uploaded/2023/unvjxbf/2023_01_20/6-giam-can-shutterstock-9068.jpg\"></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>\r\n</div>', 218, 5, 6, 1, '2023-01-27 02:29:57', '2023-03-26 03:04:16');
